INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C18765A9E0801F49A7587DE76DFF3B67', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 0, NULL, NULL, 2, NULL, 0, 'Split Oreo Cookies in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C291F9BE0637A74A9F76BC3BCA9CF28D', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 1, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of white chocolate on the inside of both halves of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16BEB1F11D79C743A6ACB5F1A0649068', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 2, NULL, NULL, 2, NULL, 0, 'Immediate press a lollipop stick on one half and press the halves back together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4816F8804EDBE74E89E863A6B2B1D3AD', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 3, NULL, NULL, 2, NULL, 0, 'Allow the white chocolate to harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'624CAD98F3B4E444A453FE5223FC98BD', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes and one pink heart sprinkle nose to each of 6 black candy melt wafers using the melted white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3A51075DFD9814DAFA673942503D51F', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 5, NULL, NULL, 2, NULL, 0, 'Cut two small tear drop shapes out of each of the remaining 12 black candy melts creating 12 lamb ears and 12 lamb legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73FC858CC641F64DB4C30AE113096614', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 6, NULL, NULL, 2, NULL, 0, 'Dip an Oreo lollipop in white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'523CD3AB77F2204BBCA77CB910151722', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 7, NULL, NULL, 2, NULL, 0, 'Press one of the chocolate wafer lamb faces in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D054C8668B274B4E8DE97E5B8F6234B8', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle tiny marshmallows all over the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9310BA465FF6140ACB08FC12EDD0A89', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 9, NULL, NULL, 2, NULL, 0, 'Let dry completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA1592D655F9224F89766A667B467E52', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 10, NULL, NULL, 2, NULL, 0, 'Repeat, creating 6 Oreo Lamb Pops.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B8B3EDE77C77D44941E04051969055A', X'12F99633152F7B4293786D327D998469', X'12F99633152F7B4293786D327D998469', 11, NULL, NULL, 2, NULL, 0, 'Use melted white chocolate to attach two chocolate ears and two chocolate legs to each lamb pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DED340CE49616C4C8E289A25334A3AA1', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 0, NULL, NULL, 2, NULL, 0, 'CRUST:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B62D37D73A09540BD9BD7AC4A35B6EC', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1404CE4AA49BD94682BDB60F3C239C98', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 2, NULL, NULL, 2, NULL, 0, 'Spray a 9 inch springform pan with cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'327E575CD4F6314C9D8B1AAC8BAA763A', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 3, NULL, NULL, 2, NULL, 0, 'Combine graham cracker crumbs, 1 tablespoon sugar, and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C975CD4CE9A10246A49C39F4B9A331A1', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 4, NULL, NULL, 2, NULL, 0, 'Press it in the bottom of the springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36E57A5BE16B524E9B3284A0D3704BFD', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 5, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until fragrant and the edges begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'422D661838B39049B84802AE60385B32', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F8F450BF261A54FA18FDC15DF21E750', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 7, NULL, NULL, 2, NULL, 0, 'Set a large roasting pan in the oven and pour in 1 1/2 inches of hot water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDA9D104E7724C48B9F8F1B84E28D05A', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 8, NULL, NULL, 2, NULL, 0, 'PEACH COBBLER CHEESECAKE FILLING:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D72EACA601791D45A8DF92FD3741CA7C', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 9, NULL, NULL, 2, NULL, 0, 'Combine cream cheese and sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC8BECA6D627DC44B5A5DE9E57D32C71', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 10, NULL, NULL, 2, NULL, 0, 'Add eggs and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'511DD9D7ED69FF4C9537830023BF6AF2', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 11, NULL, NULL, 2, NULL, 0, 'Add sour cream and the 3 flavorings and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B69A25EFBEC6CE449312966A96BCBB89', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 12, NULL, NULL, 2, NULL, 0, 'Add desired color. (1 drop orange for light peach skin tone)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA5803C1B335204CBA93FBF4B4BD2870', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 13, NULL, NULL, 2, NULL, 0, 'Scoop out 1 1/2 tablespoons of filing and place in a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'323AB19B4521BC4AA02FD5678F5F6D64', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 14, NULL, NULL, 2, NULL, 0, 'Color using several drops of black coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38ABD6ED9B5C5445B65843FF0DD0F7FB', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 15, NULL, NULL, 2, NULL, 0, 'Spoon into a small zip top bag or pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EC7808D16E8E54D9D0AA5027C0BD09D', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 16, NULL, NULL, 2, NULL, 0, 'If making a baby girl, also color 1 1/2 tablespoons of filling pink.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'746CB56E8AAA924398F8B06B45277F7D', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 17, NULL, NULL, 2, NULL, 0, 'Spoon into a bag also.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24864F8B46226F48849C5341CCF1EB48', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 18, NULL, NULL, 2, NULL, 0, 'Pour the remaining cheesecake filing over crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'870E9023141DFD4D8AE902FE8F40D169', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 19, NULL, NULL, 2, NULL, 0, 'Pipe on two eyes, two eyebrows, a wisp of hair and a smile using the black filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32EA8782090C6B489C177BD6B44FCD77', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 20, NULL, NULL, 2, NULL, 0, 'If making a girl, you can add rosy cheeks and a pink bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87DA8435266F4F47A159C43AB7E0A804', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 21, NULL, NULL, 2, NULL, 0, 'Set cheesecake in roasting pan filled with water and bake for 45-50 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3144291AB927D4087DCED5E2A9983D9', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 22, NULL, NULL, 2, NULL, 0, 'The top of the cheesecake should look set but still wiggle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDD052C40802114AA12BDBB48E6186FD', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 23, NULL, NULL, 2, NULL, 0, 'Turn off oven, crack open door, and let cheesecake sit for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA623D75CF3F6342987EA79317C51854', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 24, NULL, NULL, 2, NULL, 0, 'Remove cheesecake from water bath and allow to cool for 1 hour at room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6D5E3E3965D474E9E6377E7A123A713', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 25, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 6 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DED62A5EE52DB345874177DCA6249F9E', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 26, NULL, NULL, 2, NULL, 0, 'DECORATIONS:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B12E53FC24A7E449D6CDACD14866A7A', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 27, NULL, NULL, 2, NULL, 0, 'Roll out blue or pink fondant and cut out one 1 1/2 inch round circle. Cut out a pacifier handle. Attach the handle to the circle using a dab of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77CC940245CAB945A8B5F1403E2E360E', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 28, NULL, NULL, 2, NULL, 0, 'Roll out skin toned fondant and cut out two ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DD26212E7738B4AA2A36E5FD569185D', X'9F050F455D4C3547B4A14EAA7121A32F', X'9F050F455D4C3547B4A14EAA7121A32F', 29, NULL, NULL, 2, NULL, 0, 'Just before serving, attach the two ears to the side of the cheesecake and add a pacifier in the baby''s mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'319E505A13087C4FBAC8AA8F776FF067', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 0, NULL, NULL, 2, NULL, 0, 'Bring one inch of water in a large pot to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'604A7E13EC86B54A97A62086A2AC6DDC', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 1, NULL, NULL, 2, NULL, 0, 'Set eggs in a steamer basket and lower into pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C262F959B586B4CA5A0C81046D2FAB1', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 2, NULL, NULL, 2, NULL, 0, 'Cover pot with lid and set timer for 13 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'082F14C199124E4B8E4A18CD49FFC918', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 3, NULL, NULL, 2, NULL, 0, 'Remove lid and take steamer basket out of the pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94DB4D15FEC64A4791E5986E6552183D', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 4, NULL, NULL, 2, NULL, 0, 'Immediately place eggs in a bowl of ice water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC5479CC6EA97B44A5EA23E4EF26815D', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 5, NULL, NULL, 2, NULL, 0, 'Allow to cool for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07B91F86992B714686D07A99F8589FF3', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile cut carrots into slices and sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B76259FA0E006409F07A3FC1288E564', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 7, NULL, NULL, 2, NULL, 0, 'Cut the slices using a small `E` cookie cutter (2 per egg).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBDA775D3688034184CF738023480645', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 8, NULL, NULL, 2, NULL, 0, 'Cut the sticks so that each end is pointed ( 2 per egg).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5CFDD0B1EC47840AAA631189B1AF4F8', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 9, NULL, NULL, 2, NULL, 0, 'Push a skewer into the center of each `E` creating a hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'721ABBC9F67AC246BD78C890A52B48BE', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 10, NULL, NULL, 2, NULL, 0, 'Poke a carrot stick into each `E` creating a chicken leg and foot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0C93EDED735DA44A5353E683A67627A', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 11, NULL, NULL, 2, NULL, 0, 'Remove eggs from ice water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89C992ADF446A34AA14B0EAEDC39FADD', X'C1AA5C294D81C04D83FED4415C98D129', X'C1AA5C294D81C04D83FED4415C98D129', 12, NULL, NULL, 2, NULL, 0, 'Poke two carrot chicken legs into each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D6FCB674981AB4C861071CD3103523D', X'1FE0CB87BA137342B425AB6480B88E81', X'1FE0CB87BA137342B425AB6480B88E81', 0, NULL, NULL, 2, NULL, 0, 'Stir together the cream cheese and cheddar cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B8E01A5C74D0946B3320162BCAA2EDA', X'1FE0CB87BA137342B425AB6480B88E81', X'1FE0CB87BA137342B425AB6480B88E81', 1, NULL, NULL, 2, NULL, 0, 'Reserve 2 tablespoons of this mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E29144BF7AFE574096DA2969B914B22C', X'1FE0CB87BA137342B425AB6480B88E81', X'1FE0CB87BA137342B425AB6480B88E81', 2, NULL, NULL, 2, NULL, 0, 'Stir the bacon into the remaining cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59BF42F2B154474F87FD29D411178A40', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 0, NULL, NULL, 2, NULL, 0, 'Scoop out 1/2 tablespoon size scoops of the cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BBFFFB03348FA43AAC744868B747D3F', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 1, NULL, NULL, 2, NULL, 0, 'Roll into the small pieces of finely shredded cheddar cheese shaping into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1B160768CFA4345A2F945A9E10CFD97', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 2, NULL, NULL, 2, NULL, 0, 'Cut 2 tear drop shapes, 2 flower shapes, and one tiny triangle from the cheddar cheese for each cheese ball chick you have made.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02171052B4A91F4F9F463BE85D56F95A', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 3, NULL, NULL, 2, NULL, 0, 'Brush orange food coloring over the triangles and flowers (or brush the coloring over the slice of cheese, allow to dry, then cut out the shapes).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42D1C7C24E45784D81363B0C8BD3C66A', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 4, NULL, NULL, 2, NULL, 0, 'Cut thin slivers off the edges of the black olives and cut into tiny circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0A458CAAE0A644AA1F150B1B15AAA6E', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 5, NULL, NULL, 2, NULL, 0, 'Attach two tear drop shaped wings to the sides of each cheese ball using the reserved cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E60092AF6733244DB62E0F9D685E68EB', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 6, NULL, NULL, 2, NULL, 0, 'If needed, warm the reserved cheese ball mixture in the microwave on the defrost setting for about 10 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EB27582DA12AB47BDCACBFB4C1B76CB', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 7, NULL, NULL, 2, NULL, 0, 'Attach a triangle beak and two flower shaped feet to each cheese ball then attach two black olive eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DD3A045BCEACC43B98278BDDAF481C8', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 8, NULL, NULL, 2, NULL, 0, 'Store in the refrigerator in an airtight container until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2980E9FABB794B4EABB55EA8E2E21B16', X'A770C0E1E212534AAE026FE04335767C', X'A770C0E1E212534AAE026FE04335767C', 9, NULL, NULL, 2, NULL, 0, 'Set cheese balls on crackers just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23A1B560B3851246AB456FEAFB3821FB', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 0, NULL, NULL, 2, NULL, 0, 'Coat one bag of popped popcorn with 18 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B9D26439C3BA847AA9BC291E2C9149A', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 1, NULL, NULL, 2, NULL, 0, 'Press into the bottom and up the sides of a large, plastic, football shaped bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9CF9C14BADA4F4B9551820B64FAB1A8', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 2, NULL, NULL, 2, NULL, 0, 'Coat one more bag of popped popcorn with 18 ounces of chocolate and continue to press the popcorn up the sides of the football bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0352FBA72F07FA468BC0C4B1E33EBE48', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 3, NULL, NULL, 2, NULL, 0, 'Brush the inside of the football shaped popcorn bowl with 4 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F322874CEAEBE498912A0ADED5026D0', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes then freeze for 10.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AF5A36147221643882402CDDD8F1D42', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 5, NULL, NULL, 2, NULL, 0, 'Remove popcorn bowl from plastic bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A35EEC8252EF444DB11F68270C68B0C9', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 6, NULL, NULL, 2, NULL, 0, 'Coat the remaining bag of popcorn, peanuts, and toffee bits with 18 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F127911571CF248863CCDAB40B95780', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 7, NULL, NULL, 2, NULL, 0, 'Spread onto a parchment paper lined baking pan and refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D5B897C7EB0844F96CDCF6FE131E95C', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 8, NULL, NULL, 2, NULL, 0, 'Break the popcorn into pieces and place in the football shaped popcorn bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7FB2F035E090946B6C79FF0D0D5AB39', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 9, NULL, NULL, 2, NULL, 0, 'Decorate the football with white modeling chocolate laces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A5F4312957EE34CB15A7DB86C9A3CD5', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 10, NULL, NULL, 2, NULL, 0, 'Roll out the white modeling chocolate to about 1/8th inch thickness. Cut one 5 inch by 1/4 inch strip and attach it to the popcorn bowl using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FD53C6B6AC1434BBFD73AD6E7D1F856', X'A3E55C875D9FEC488A37A20FBFB56CF1', X'A3E55C875D9FEC488A37A20FBFB56CF1', 11, NULL, NULL, 2, NULL, 0, 'Cut eight 3/4 inch by 1/8th inch strips and attach them using piping gel going vertically across the 5 inch strip creating the laces of the football.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4960CE10213AD42A3F5970A820FCA93', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', 0, NULL, NULL, 2, NULL, 0, 'Cut 4 grahams in half lengthwise to create four (5` x 1 1/16`) tops and four bottoms.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48E5EC09B4949447B1D9E21DCFB00141', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', 1, NULL, NULL, 2, NULL, 0, 'Cut 8 long (5` x 9/16`) side panels out of 2 graham crackers and 8 short (3/4`) end panels out of the remaining cracker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1848D536556634EBA8DAD1783DF0467', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', 2, NULL, NULL, 2, NULL, 0, 'Use the melted peanut butter chips to attach two long side panels and 2 short end panels to the 4 bottom crackers, creating open boxes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B5E1DAA1BDC9D4E8690CC7C1E78C075', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', 3, NULL, NULL, 2, NULL, 0, 'Draw ruler lines and numbers on the top pieces using a food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3165BF24CEA3EE4DB7AF3728B08B52A5', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', X'8A8CB214E2606B4D9DCBFAD06A0F9C7F', 4, NULL, NULL, 2, NULL, 0, 'Fill boxes with Welch''s Fruit ''n Yogurt Snacks and cover with the graham cracker ruler.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'997FF849E8C12D4EBD05B49113158B8C', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 0, NULL, NULL, 2, NULL, 0, 'Heat Donut Holes according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'195795ACF60C5D4E9F9CCD1A6C16B065', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile combine the granulated sugar with 1 tablespoon of the cinnamon sugar that comes in the box of Donut Holes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'690753402E46F54A9E7C111B8C5316DE', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 2, NULL, NULL, 2, NULL, 0, 'Pour into a zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33DF0DE855B65A4D811DCBBDA50AF73F', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 3, NULL, NULL, 2, NULL, 0, 'Squeeze in about 10 drops of red food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EC7D7C5CA7DA3419270954D9DEA0ACB', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 4, NULL, NULL, 2, NULL, 0, 'Shake the bag several times then massage the red coloring into the sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85DCDA42457FA24CA12418A9F922E1F1', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 5, NULL, NULL, 2, NULL, 0, 'Pour the sugar into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8D720D0F70EBC428BA64AC9A631D23F', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 6, NULL, NULL, 2, NULL, 0, 'Roll the hot Donut Holes in the red sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C245E96896B12746987C390319E31FB9', X'2AFD4DEFDD769B4AB115A89E62ACE255', X'2AFD4DEFDD769B4AB115A89E62ACE255', 7, NULL, NULL, 2, NULL, 0, 'Insert one half of a pretzel stick into each Donut Hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A33DEC0C9DE29439CD94BC5B789F089', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 0, NULL, NULL, 2, NULL, 0, 'Cut one slice of orange cheese into a book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46DBB9467D14FA4EAA53D1F276EE9870', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 1, NULL, NULL, 2, NULL, 0, 'Use the back of a knife to score the book to create the illusion of a spine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'302FEB01AE4AC04499F49AAAB03961A8', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 2, NULL, NULL, 2, NULL, 0, 'Cut two long thin strips from the white cheese slice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF9C84EF7D6DEA4D8B71CC230E8AD2A4', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 3, NULL, NULL, 2, NULL, 0, 'Set one strip of white cheese across the cheese book about 1/4 inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0ADD8418A7AE9E40ADFC97402F0EFEEF', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 4, NULL, NULL, 2, NULL, 0, 'Set the other strip near the bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F798EFDA57E7464D84A6A0F666DEFA53', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 5, NULL, NULL, 2, NULL, 0, 'Cut 2 small circles out of the white cheese, for the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F0C9312B4A39F479CAB67933DC313F8', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 6, NULL, NULL, 2, NULL, 0, 'Cut 1 slice off the edge of an olive and cut that into two very small circles for the pupils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22E2A319E0238B4EBCE4CAFC385FD50C', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 7, NULL, NULL, 2, NULL, 0, 'Cut two slices from the middle of the olive.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54B02D46BB975443B0F580B6AF033CA1', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 8, NULL, NULL, 2, NULL, 0, 'Use a round cutter to cut the centers of the slices out creating the rims of the bookworm''s glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'196C646746AAA94CB938BFAF39B6143C', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 9, NULL, NULL, 2, NULL, 0, 'Cut another slice from the olive and cut it in half. Half will become the smile. Cut the other piece in half to form the nose piece of the glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'019E7DC77E493B4795818CD86774CB6D', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 10, NULL, NULL, 2, NULL, 0, 'Heat the Farm Rich Mozzarella Bites according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC8B978BDC067F458DB3BDDDDA328790', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 11, NULL, NULL, 2, NULL, 0, 'Arrange 6 of the Mozzarella Bites on a plate forming a worm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C918518F97DCE9449A0470CB35D085E5', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 12, NULL, NULL, 2, NULL, 0, 'Cut one thin slice off the remaining Mozzarella Bite, then cut two small half moon shapes out for the worm''s hands.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1829054BF9F937438B299AD4606BD577', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 13, NULL, NULL, 2, NULL, 0, 'Arrange the olive glasses, cheese eyes, and olive smile on the top Mozzarella Bite (head.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF0972C94A243D47BE84236E5C40B128', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 14, NULL, NULL, 2, NULL, 0, 'Set the book between the two Mozzarella Bites that are just below the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A4ED95B9D8CF04996D43D6D8DB686B0', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 15, NULL, NULL, 2, NULL, 0, 'Set one hand on either side of the cheese book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEB5EBC38019154A831EBA3091ED50FA', X'A230A7D74A7F6B49AC29F2F25049AD4F', X'A230A7D74A7F6B49AC29F2F25049AD4F', 16, NULL, NULL, 2, NULL, 0, 'Heat the marinara sauce and serve on the side.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35CB1D118B7412409E8EA2B30E6127C7', X'193980E47C43AA419C37A11AD6D3B9EE', X'193980E47C43AA419C37A11AD6D3B9EE', 0, NULL, NULL, 2, NULL, 0, 'Cut soft flour tortillas into 4 1/4 inch by 2 3/4 inch rectangles. (You can change these dimensions if youd like larger sandwiches.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43FFACDD813231408262406D0773403C', X'193980E47C43AA419C37A11AD6D3B9EE', X'193980E47C43AA419C37A11AD6D3B9EE', 1, NULL, NULL, 2, NULL, 0, 'Then cut slices of white cheese and deli meat into 1 3/4 inch by 2 1/2 inch rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DDA8764057CEF44894EA17BEA105028', X'193980E47C43AA419C37A11AD6D3B9EE', X'193980E47C43AA419C37A11AD6D3B9EE', 2, NULL, NULL, 2, NULL, 0, 'Stack slices of cheese and deli meat, alternating the two until you have 5 slices or pages for your book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8BC859FA3D94249A202FA32F4CA7F14', X'193980E47C43AA419C37A11AD6D3B9EE', X'193980E47C43AA419C37A11AD6D3B9EE', 3, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of mayonnaise on one side of the tortilla rectangles, keeping it 1/4 inch from the edge then set the cheese and meat pages on one edge of the tortilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EA80C91B6968844B8B681422C728CA2', X'193980E47C43AA419C37A11AD6D3B9EE', X'193980E47C43AA419C37A11AD6D3B9EE', 4, NULL, NULL, 2, NULL, 0, 'Fold the tortilla over the `pages` forming a book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9F2E6883C649B4C99FFED6053D9472B', X'193980E47C43AA419C37A11AD6D3B9EE', X'193980E47C43AA419C37A11AD6D3B9EE', 5, NULL, NULL, 2, NULL, 0, 'Use a food coloring marker to write the title of the book on the front of each sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77E2F524122F50499125EB9A4A5A825A', X'193980E47C43AA419C37A11AD6D3B9EE', X'193980E47C43AA419C37A11AD6D3B9EE', 6, NULL, NULL, 2, NULL, 0, 'Add some lines and repeat the name on the binding (edge) of the book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BED99D66BB0BC4A9FBF8ECC4304C9FA', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350ºF.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EF0226ADB489E4EB6A2F9356B20FDDE', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 1, NULL, NULL, 2, NULL, 0, 'Make one Betty Crocker SuperMoist white cake mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC84E88776B16E43BED7CE6DBF1DCD51', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 2, NULL, NULL, 2, NULL, 0, 'Scoop out 3/4 cup of batter and color it using gold or yellow food coloring. Equally divide the remaining batter and color one half red, the other green.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F35589323ABAEB4D99FAF1BE801787D9', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 3, NULL, NULL, 2, NULL, 0, 'Spray one 6-inch square pan and three 4-inch round springform pans with baking spray. Pour gold batter into square pan and bake for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7328D347E9001E4CA6362A6809A570F0', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 4, NULL, NULL, 2, NULL, 0, 'Pour some red batter in one half of a 4-inch springform pan and green in the other half. Use a skewer or toothpick to swirl the colors slightly. Repeat in the other two pans. Go back and add layers of color, swirling, until you use up all the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D13C4069ACDF9244ACAC5763F642AAE3', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 5, NULL, NULL, 2, NULL, 0, 'Bake cakes for 26-30 minutes. The tops will look moist but should be baked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBBC73BE6F91AC42BE90DF738A59D53E', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 6, NULL, NULL, 2, NULL, 0, 'Cool cakes for 10 minutes, then remove from pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B01AB379BA41FE48BFAECCF89381724B', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 7, NULL, NULL, 2, NULL, 0, 'Cut the gold cake into three 3/4-inch by 2-inch strips. Cut the domes off each of the red and green cakes, making them 2 inches thick. Reserve the cake scraps for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29C84CA23A8B584B97C18948BBFB6BED', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 8, NULL, NULL, 2, NULL, 0, 'Spray three 6-inch square baking pans with baking spray. Set one round cake in the bottom of each pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DA9B1378541E34F87EE4CB9C03E2975', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 9, NULL, NULL, 2, NULL, 0, 'Make another Betty Crocker SuperMoist white cake mix.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F51E80135F8E4B4B8303B1E9F90E57A8', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 10, NULL, NULL, 2, NULL, 0, 'Spread just a tiny bit of batter along one edge of a gold cake strip and press that up against the top of one of the round cakes, creating the top of the ornament. Repeat, then equally divide the batter among the three pans, pouring it around the ornaments.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6F9093D82529D4D84661DF0166F076A', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 11, NULL, NULL, 2, NULL, 0, 'Bake the cakes for 30-35 minutes until they are golden brown on top and when you press down around the edge with your finger, the cake bounces back.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73F61C19885B5444B1483C31861653E8', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 12, NULL, NULL, 2, NULL, 0, 'Allow cakes to cool completely, then freeze for one hour. Once the cakes are firm, carve off all the browned cake, making three level cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EFAC1CBC724844B8EF2F4DEE07A46D2', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 13, NULL, NULL, 2, NULL, 0, 'Stack the cakes together, with the ornaments lined up. Carve off more cake if needed to make straight sides. Press the cakes to make sure they stick and freeze for another hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1774B53B52638446814573E63BB0DBE8', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 14, NULL, NULL, 2, NULL, 0, 'Stand the cake upright, and frost. Smooth out the frosting to create the look of a box.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF14898C963F844BB532649C01941EE7', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 15, NULL, NULL, 2, NULL, 0, 'Roll 2 ounces of red and 2 ounces of green fondant into logs, brush one side lightly with vodka, and stick the two ropes together. Twist, flatten, and roll out. Cut into two 1 1/2-inch by 15-inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA5578435FE68D48B0BD1B69DF9EC054', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 16, NULL, NULL, 2, NULL, 0, 'Drape one piece from front to back over the center of the cake and the other from side to side. Cut the excess fondant at the bottom edges of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1DE84B04EACB846BDD80AC26D78332B', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 17, NULL, NULL, 2, NULL, 0, 'Mix the remaining red fondant and green with a pinch of Tylose powder. Repeat above, cutting two 2-inch by 4-inch strips. Fold one strip over, into a loop, attaching the ends together with a bit of vodka. Roll up a small amount of paper towels and insert it into the loop. Pinch the ends and allow the loop to dry until it holds its shape. Repeat. Once dry, remove the paper towels. Cut the folded ends and stick the two loops together, forming a bow. Cut another small piece of fondant and wrap it around the center of the bow. Also cut two 5-inch long pieces for the ribbon. Attach pieces to cake using more vodka.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C266A443243A04AA92CB4BD5A802269', X'BAE6721A710AB7499992647E88AF295D', X'BAE6721A710AB7499992647E88AF295D', 18, NULL, NULL, 2, NULL, 0, 'To serve the cake, cut across the cake to reveal the Christmas ornament hiding inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9041923E5DB13F4FB0DD85EAC632E90E', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8011E017F06689479188E401B39963BB', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 1, NULL, NULL, 2, NULL, 0, 'Spray a 9-inch by 13-inch cake pan with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95BC8E985B60074F8181473B670BFFBF', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 2, NULL, NULL, 2, NULL, 0, 'Optionally, line the pan with parchment paper and spray the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69AE65CCAF199443B01BCB365C3110C3', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 3, NULL, NULL, 2, NULL, 0, 'Divide white cake batter into 6 bowls (about 3/4 cup in each).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F3A5D3175026D49B57ECB70F4E97EE8', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 4, NULL, NULL, 2, NULL, 0, 'Color one bowl red, another orange, yellow, green, blue and purple.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73767B2F7DF6F445957EA63C84647D58', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 5, NULL, NULL, 2, NULL, 0, 'Pour batter into individual disposable pastry bags or large zip top bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78719AB5AE1CBF489473BD5633CB1514', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 6, NULL, NULL, 2, NULL, 0, 'Snip off the tip of the bag and pipe squiggles of one color batter into the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39A45E06B02AC54A82B611B5E0E7A464', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 7, NULL, NULL, 2, NULL, 0, 'Use about 1/3rd of the batter for the first layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBD1ECFC209EA94180AC0A2DCFE4DE01', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 8, NULL, NULL, 2, NULL, 0, 'Continue piping squiggles of colors, layering the 6 colors a total of three times.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A88FCB67CE98374188A53138850F4E66', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 9, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter a few times to level the cake batter and to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFDB762872859D40B0239688971B6B94', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 10, NULL, NULL, 2, NULL, 0, 'Bake according to instructions on the cake mix box.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAB075D9166B764980C9BC9D3456B845', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 11, NULL, NULL, 2, NULL, 0, 'Remove cake and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6183103BB0E4E2489469AD214E9A6649', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 12, NULL, NULL, 2, NULL, 0, 'Invert cake onto a cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F318F30AA7C2D4C9B6793ED5D1AA93C', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 13, NULL, NULL, 2, NULL, 0, 'Remove parchment paper if necessary.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'719489E79FF2D54A81EEC261E9D59726', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 14, NULL, NULL, 2, NULL, 0, 'Cut cake in half so you have two 9 by 6 1/2-inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09AFAB5EC70C4A479F8CCBD31CA12DCE', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 15, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CAD9F5A6DC5FC47BE843391E5308B9E', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 16, NULL, NULL, 2, NULL, 0, 'Cut the other piece in half so you have two 6 1/2 by 4 1/2-inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'223088B84C30B943AFCA5FDBFDF61741', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 17, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B9090B7E8825245971FA1E096C5EE8B', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 18, NULL, NULL, 2, NULL, 0, 'Cut two 2 by 3-inch pieces for the unicorn''s legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81F6F0394BDA7F498A40026C63DE695D', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 19, NULL, NULL, 2, NULL, 0, 'Discard or eat the remaining pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25ABC9F06F0D9541A977084D9C90C39C', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 20, NULL, NULL, 2, NULL, 0, 'Assemble the cake by placing the 9-inch by 6 1/2-inch cake in the lower half of your cake board forming the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6F8F5909FDA584B957F781AE7E27179', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 21, NULL, NULL, 2, NULL, 0, 'Set the 6 1/2-inch by 4 1/2-inch piece about 2 1/2 inches from the top edge of the larger cake forming the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA3CE3EA300DC447AE548BFE0744DAC1', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 22, NULL, NULL, 2, NULL, 0, 'Place the two leg pieces under the body piece about 3/4 of an inch from either edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C2D55DEFB4A294388142B2AC581768A', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 23, NULL, NULL, 2, NULL, 0, 'Spoon frosting into pastry bags fitted with #21 star tips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88609098433A744B96184147D39DFB24', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 24, NULL, NULL, 2, NULL, 0, 'Pipe white stars over the large cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40146C8D0D836840B6AF9E1D40DA5643', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 25, NULL, NULL, 2, NULL, 0, 'Pipe stars over 3/4''s of the head and legs, leaving room to pipe on pink hooves and a pink snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'521CBEC858347248BDAC9BB599E22C64', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 26, NULL, NULL, 2, NULL, 0, 'Pinch off about 3 ounces of white modeling chocolate and form into a twisted unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A16F99CAD21350468771F0593C80B2E7', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 27, NULL, NULL, 2, NULL, 0, 'Brush with edible gold luster dust and attach to the cake near the right side of the top edge of the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C8B2E55B2CD874BB7491DF7A773027D', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 28, NULL, NULL, 2, NULL, 0, 'Roll the remaining white candy clay to about 3/16-inch thickness. Cut out an ear using a 1 1/2-inch wide by 2 1/2-inch long teardrop cookie cutter or a knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'288A423AA9CF02408F871D591983EB40', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 29, NULL, NULL, 2, NULL, 0, 'Roll out the pink candy clay to about 1/16th-inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F351EA20EF2A8143A5E28451668EDD89', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 30, NULL, NULL, 2, NULL, 0, 'Cut a smaller teardrop shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E02B66758D5CA24BA5C327E15BD34ACD', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 31, NULL, NULL, 2, NULL, 0, 'Use a small dab of water to attach the pink teardrop to the center near the lower edge of the white teardrop to form the unicorn''s ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD217A3A25D7734086CA9FF23D3052AF', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 32, NULL, NULL, 2, NULL, 0, 'Set the ear aside to dry for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6B2153326F360478FE3088D9866E61C', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 33, NULL, NULL, 2, NULL, 0, 'Roll out the black candy clay and cut out a 2 1/4-inch circle then cut just a thin edge from around the circle to create a thin moon-shaped eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE543698DB787848B2AA3A61099B6CAB', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 34, NULL, NULL, 2, NULL, 0, 'Cut a small 3/4-inch round out of the black and do the same creating a nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F205495368E76F41BC4C77B1643326C4', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 35, NULL, NULL, 2, NULL, 0, 'Cut four small rectangles then shape them into teardrop shaped eyelashes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3CD750D3E3EA24AA756247ECA4796F0', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 36, NULL, NULL, 2, NULL, 0, 'Roll the remaining black candy clay into a long thin log for the unicorn''s mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AFC252D35619B40AC21E29E62625AE4', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 37, NULL, NULL, 2, NULL, 0, 'Use a dab of frosting to attach the candy ear to the upper right corner of the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'753528A385342244ABBB4E2CC2281F45', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 38, NULL, NULL, 2, NULL, 0, 'Press the eye onto the cake to the left below the ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12A0E952660C834DBD275437A007F0B5', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 39, NULL, NULL, 2, NULL, 0, 'Then press the four eyelashes above the eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D061EDAC9722E4AB2D87CE6A37D1440', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 40, NULL, NULL, 2, NULL, 0, 'Press the black log onto the pink frosting creating the unicorn''s smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'147AA9A55A5C5740BB1181941B864D0F', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 41, NULL, NULL, 2, NULL, 0, 'Cut off any excess candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CED2FA8F5FD644B8A41B1662B22B849', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 42, NULL, NULL, 2, NULL, 0, 'Press the nose above the smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D065A44333F7F34CA869A749D0CDCADA', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 43, NULL, NULL, 2, NULL, 0, 'Roll all the other colored candy clay pieces into 8 inch long logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0597CD38C8D2E6439BF7595C8BBE934B', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 44, NULL, NULL, 2, NULL, 0, 'Press them all together in rainbow order: purple, blue, green, yellow, orange, and red.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BE542759EC500499DD14D967F1AFDFA', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 45, NULL, NULL, 2, NULL, 0, 'Cut the logs into one 3 inch side and one 5 inch side.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9740A31D9640E447B2B3916225FD2080', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 46, NULL, NULL, 2, NULL, 0, 'Flatten the logs and roll them out to about 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EF237CF79B38644B1E2508158CD3B9F', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 47, NULL, NULL, 2, NULL, 0, 'Shape the longer piece into a curved tail pinching together the left side.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AE95BF7FD7444438B4F32D8EA7B395A', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 48, NULL, NULL, 2, NULL, 0, 'On the right side, cut the ends so that each color has a nice pointed shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D09569EA289BE4685866AFF3C7DC1AA', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 49, NULL, NULL, 2, NULL, 0, 'Cut the left edge of the other piece so that it''s straight then cut the opposite side so that each color has a nice pointed shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C76E85C995F0F746BE06FB04118B7C8F', X'F72261AEA22A95448108B1237B110D21', X'F72261AEA22A95448108B1237B110D21', 50, NULL, NULL, 2, NULL, 0, 'Attach the tail to the top corner on the right side of the unicorn''s body and the mane to the right edge of the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69E2F87A357ACA45BDAC9E6453630DA5', X'05D81AA3D7E08D45B801196BB0ED3DA2', X'05D81AA3D7E08D45B801196BB0ED3DA2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E956B55C03C084D907BA01B64B27C57', X'05D81AA3D7E08D45B801196BB0ED3DA2', X'05D81AA3D7E08D45B801196BB0ED3DA2', 1, NULL, NULL, 2, NULL, 0, 'Spray 10 half-pint mason jars with non-stick baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94538AD7A7738641964D2D820C077E22', X'05D81AA3D7E08D45B801196BB0ED3DA2', X'05D81AA3D7E08D45B801196BB0ED3DA2', 2, NULL, NULL, 2, NULL, 0, 'Make the cake mix using the instructions on the package swapping out root beer for water and adding 2 tablespoons of root beer dessert topper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12386D6FA6C94C4D89384F92AE030FA6', X'05D81AA3D7E08D45B801196BB0ED3DA2', X'05D81AA3D7E08D45B801196BB0ED3DA2', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the batter among the 10 Mason jars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC65DAA08F2CE74BACF5EB379F12FD51', X'05D81AA3D7E08D45B801196BB0ED3DA2', X'05D81AA3D7E08D45B801196BB0ED3DA2', 4, NULL, NULL, 2, NULL, 0, 'Place the jars on a baking sheet and bake for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04F2F77C4815CA4E8C21FA0E677308F4', X'05D81AA3D7E08D45B801196BB0ED3DA2', X'05D81AA3D7E08D45B801196BB0ED3DA2', 5, NULL, NULL, 2, NULL, 0, 'Allow the cakes to cool for 10 minutes, then turn the jars upside down, allowing the cakes to fall out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B648700E390EAB4FB436C0F42EE15FD3', X'05D81AA3D7E08D45B801196BB0ED3DA2', X'05D81AA3D7E08D45B801196BB0ED3DA2', 6, NULL, NULL, 2, NULL, 0, 'Allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D4E2C2CBABEA944B8C9089AB5BB4A7B', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 0, NULL, NULL, 2, NULL, 0, 'Heat the ice cream in the microwave just until it melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'345F1FBF64DB234A85D49F6A5457606D', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 1, NULL, NULL, 2, NULL, 0, 'Measure out 1/3 cup of the melted ice cream (that''s all you''ll need).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'929E2F0A11E50C4790BBF90B705346AD', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 2, NULL, NULL, 2, NULL, 0, 'Pour in the white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70CAFA659360B0498AFE98C232B344F5', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 3, NULL, NULL, 2, NULL, 0, 'Place the bowl back in the microwave and heat on high for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2F248D3E6D7D5428AFF4000B1203DD0', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 4, NULL, NULL, 2, NULL, 0, 'Allow the bowl to sit in the microwave for about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7A92F7138DD4C4CBBDD95ED10B6EAAE', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 5, NULL, NULL, 2, NULL, 0, 'Remove it from the microwave and stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6356C737960D542A62D4C33DB988350', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 6, NULL, NULL, 2, NULL, 0, 'Set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F3BAF289EC62B45ADA1C6F7198FB0F7', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 7, NULL, NULL, 2, NULL, 0, 'Cut the domed tops off of each cake so that they are all 3 inches tall.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DC829C25E6C484794D7D6D0FE59E734', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 8, NULL, NULL, 2, NULL, 0, 'Use a cupcake plunger/corer, apple corer, or a knife to remove some of the center of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FED1C2CC50D526409131C2680EF0766B', X'F234D1F3C942144C9F8F622DC2286E1C', X'F234D1F3C942144C9F8F622DC2286E1C', 9, NULL, NULL, 2, NULL, 0, 'Fill each well with vanilla ice cream ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73175DD48385CA4FBA0290CC264031D0', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 0, NULL, NULL, 2, NULL, 0, 'Melt 24 ounces of the white confectionery coating/candy melts in the microwave or in a double boiler.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9C3D47F13576D4BB2443649F318399D', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 1, NULL, NULL, 2, NULL, 0, 'If using the microwave, heat on high for 25 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C624ADE16AC7064C92F6C3AB3C8AFCCB', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 2, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds, then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D66A2FE1762424AA7E5DC6C058F8DE6', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 3, NULL, NULL, 2, NULL, 0, 'Heat for 15 seconds then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C127EB4E8B7ECC43B02A7D927C2E49B5', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10-second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D2D30834A676D47BB481BC6178F1903', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 5, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool to about 91 degrees Fahrenheit, stirring and scraping the sides of the bowl often.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFEAD32F236C8D4D911FE5001207B3A5', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 6, NULL, NULL, 2, NULL, 0, 'Pour in the A&W Root Beer Float Dessert Topper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9583E5B548EEDA40BD684260F3A2FF20', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 7, NULL, NULL, 2, NULL, 0, 'Stir until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC5DEED43FDF00498F9B6B7CB52F009F', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 8, NULL, NULL, 2, NULL, 0, 'The mixture will become thick very quickly, so scrape the bottom of the bowl to incorporate all of the melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCEE2CC0C535BF468EAF1F4E6FF37258', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 9, NULL, NULL, 2, NULL, 0, 'Wrap it in a zip-top bag or plastic wrap and allow it to sit at room temperature for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C6B4C3DC456694EA2D5BEB78B88C03B', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 10, NULL, NULL, 2, NULL, 0, 'When you are ready to use it, knead it until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D93CC9B825117345903114BFF9733F34', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 11, NULL, NULL, 2, NULL, 0, 'Roll out the root beer modeling chocolate on a non-stick baking mat (or on a cutting board lightly dusted with powdered sugar or cornstarch) to about 3/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E0A44FAF9506A459AE7CF078BBB5CA4', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 12, NULL, NULL, 2, NULL, 0, 'Cut ten rectangles that are 3 1/4 inches wide by about 8 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E38709565E07FD41900B6D8B61D48E13', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 13, NULL, NULL, 2, NULL, 0, 'Use a wooden dowel or straw to press indentations about 3/4 inch apart all across the brown strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EEB064DBC555F469B44EC66FC98B607', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 14, NULL, NULL, 2, NULL, 0, 'Leave about a 1/2 border all along one edge. That will become the bottom of your root beer mug.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'882F5ED3AF8208479B7ED1347D1A07F3', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 15, NULL, NULL, 2, NULL, 0, 'Flip the modeling chocolate rectangles over and spread on a thin layer of vanilla ganache, leaving 1/2 inch border all around.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D895FFE883DF0141B0BCC2FA084DB592', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 16, NULL, NULL, 2, NULL, 0, 'Then wrap that, ganache side against the cake, around a cake, cutting off excess modeling chocolate so the two seams match up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4C862F321C6BC4CB190272A88512A70', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 17, NULL, NULL, 2, NULL, 0, 'Rub some water along both ends and press the two seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BF900E817BFEE489F957A6AB8C77E2D', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 18, NULL, NULL, 2, NULL, 0, 'Let the wrapped cakes sit for about 30 minutes, then spoon some vanilla ice cream ganache over the cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCB56D47C0D6F644AAE76AEA2CA8F214', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 19, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of the white modeling chocolate, roll into logs and bend to form handles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'618FA209156E0A4087DD3337E2BB2261', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 20, NULL, NULL, 2, NULL, 0, 'Allow the handles to dry for at least 15 minutes, then use some clear piping gel, water, or melted white chocolate to attach the handles to the mugs and set something underneath to prop them up until they dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB1895DC2C14DE47807E3ADFB830B092', X'0126F396A2E0814ABB3E81B803A9D5F6', X'0126F396A2E0814ABB3E81B803A9D5F6', 21, NULL, NULL, 2, NULL, 0, 'Just before serving, spoon whipped topping over the cakes, allowing some to drip down the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'775FAD475885BE4183B7820DA6999145', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2800DA4DE861A4286192137EF10035F', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 1, NULL, NULL, 2, NULL, 0, 'Make one cake mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AAD88BDD6838540ADDE027BA9F77FC0', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 2, NULL, NULL, 2, NULL, 0, 'Divide batter in half. Color one-half red, the other blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'825F64DF18955E4D9D0AB058003E2D79', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 3, NULL, NULL, 2, NULL, 0, 'Line an 18x13 inch baking pan or two 9x13 inch baking pans with non-stick foil or parchment paper.');
