INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA4283FD7AF62543B00F3E654E8DC763', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 5, NULL, NULL, 2, NULL, 0, 'Bake cake/s for 20 minutes.');
