INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F888CD6C0E54047A2BD109F0E988808', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 9, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
