INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AF1D07A2ACDC5488D381344D418524D', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 11, NULL, NULL, 2, NULL, 0, 'Prep two 6-inch round by 3-inch tall pans and one 8-inch round by 3-inch tall pans by greasing them with shortening and');
