INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'383E74C6B22B694CA123420FDA16F314', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 16, NULL, NULL, 2, NULL, 0, 'Set the 8-inch pan to the back of your oven on the middle rack then set the two smaller pans on either side in the front of the oven. Bake cakes for 58-64 minutes until a toothpick poked in the center of each cake comes out clean.');
