INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'963635CB1ADF884C8AAA2F8B3D962881', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 18, NULL, NULL, 2, NULL, 0, 'Trim off domed tops on each cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'543B8180D67F2540A8F75F3C87517F52', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 19, NULL, NULL, 2, NULL, 0, 'Set one 6 inch round cake on a cake board, top with frosting, add another 6-inch round, and more frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B551B50A478964E860B8D041A0E307C', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 20, NULL, NULL, 2, NULL, 0, 'Carve the 8 inch round cake around the circumference at an angle from about 6 inches in the center of the cake to the outer 8-inch edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96C95A733EAC594784772EF6EFCC82C3', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 21, NULL, NULL, 2, NULL, 0, 'Set on top of stacked cakes and carve to shape into Uncle Sam''s hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'297F952D3181CB4885E4D1A126BA6E4F', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 22, NULL, NULL, 2, NULL, 0, 'Frost entire cake, smoothing the frosting well.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1175F22C28A7154A9495FD1788C4C82A', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 23, NULL, NULL, 2, NULL, 0, 'Measure the height of the cake. Roll out red fondant. Cut 7 strips of fondant that are the height of the cake by 1 3/8 inch wide.');
