INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C808235AA70E2340A1BFCA87B6489502', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 25, NULL, NULL, 2, NULL, 0, 'Attach red fondant stripes to frosted cake, placing them vertically on the cake. You want to space them so they are about');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7F01A7042BC344C90BF81287BB5A34B', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 26, NULL, NULL, 2, NULL, 0, '1 3/8 inches apart.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D7FD1E73DE95D40BC14D2A8FEC1E11C', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 27, NULL, NULL, 2, NULL, 0, 'Roll out more red fondant, cut out an 8 inch round, set on top of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B50054D357AB684689E8CFD90FCED513', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 28, NULL, NULL, 2, NULL, 0, 'Roll more red fondant out and cut a 10 inch round. Set it on a cake board or serving platter. Add a dab of frosting to the center of the red fondant circle. Pick up cake and place it on the fondant circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AE656D15B41C7438AE26978E3D4C87C', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 29, NULL, NULL, 2, NULL, 0, 'Roll out the blue fondant into a long strip. Cut a 2-inch wide strip that is long enough to circle the base of the cake. Wrap it around the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'577349D675F3A84A98821B2CC0DDA3B2', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 30, NULL, NULL, 2, NULL, 0, 'Roll out the white fondant, cut 7 stars using the 1-inch star cookie cutter. Attach the white stars to the blue hatband, spacing them so the stars line up with the red stripes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D17C04FE4D91D348B9AAB122437E9EC5', X'795250A22BA3F847A7FF2D81A5D571E2', X'795250A22BA3F847A7FF2D81A5D571E2', 31, NULL, NULL, 2, NULL, 0, 'Store cake at room temperature for up to 3 days. Cut to reveal the red and blue swirled stars inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34E94D25BE6E1F4BB81DB294D6D5A7CE', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
