INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33EA759753648A42AD13476EC22D638E', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 5, NULL, NULL, 2, NULL, 0, 'Stir int eh finely chopped coconut.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB539E192066D34B816FE296690F5A29', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 6, NULL, NULL, 2, NULL, 0, 'Divide batter in half. One half will remain white.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89345FC0AEA43443A9F7FC5AD9D256D4', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 7, NULL, NULL, 2, NULL, 0, 'Divide the other half into half again and color one part red an the other blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3971801B473F5A4BBE81B8D1866D25AA', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 8, NULL, NULL, 2, NULL, 0, 'Pour batter into individual pastry bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94984623C492C54FB491D6A797BA8B44', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 9, NULL, NULL, 2, NULL, 0, 'Pipe squiggles of batter, layering the colors red, white, blue, white, red, white, etc.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FEE1E99C97A334C9544A92E3804F180', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 10, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter a few times to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74699D9F3E81D8489C01E60D712E6BBB', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 11, NULL, NULL, 2, NULL, 0, 'Bake cake for 45-55 minutes until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'528EDCBE2E31554C9DB2C75DD05B38B3', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 12, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool in the pan for 10 minutes.');
