INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77B7AD908D3B2745AD3157B0F6DB3AD4', X'DD9EB6851BB6524E8607C105B5F4258D', X'DD9EB6851BB6524E8607C105B5F4258D', 14, NULL, NULL, 2, NULL, 0, 'Carve off the domed top and sides of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB1C7E174FED744E8CB4ADB5A17A6FFD', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', 0, NULL, NULL, 2, NULL, 0, 'Melt candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51E2B948D9117649A363C7E4331CD793', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', 1, NULL, NULL, 2, NULL, 0, 'Beat powdered sugar and butter together until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FB70F49C57F9A4D8AD162E8CE4F2211', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', 2, NULL, NULL, 2, NULL, 0, 'Add coconut milk, melted candy melts, and extract if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46ABF1E487749341B0FC4F3AD7403ACF', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', 3, NULL, NULL, 2, NULL, 0, 'Beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3775A07CB75F2B46B9AA7DC1C9B83865', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', X'1E9F7BBB6B8D0A46B26BAA27BF5AC5EF', 4, NULL, NULL, 2, NULL, 0, 'Spread frosting over cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59119F07E919C14BA81205AD0E38086C', X'B6FC4BCC9C9EAA4B88CAD9D410D9F7E6', X'B6FC4BCC9C9EAA4B88CAD9D410D9F7E6', 0, NULL, NULL, 2, NULL, 0, 'Heat the red candy melts, whipping cream, and corn syrup in a microwave safe bowl on high power for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A370C69F4B2A843A60C81053F473C64', X'B6FC4BCC9C9EAA4B88CAD9D410D9F7E6', X'B6FC4BCC9C9EAA4B88CAD9D410D9F7E6', 1, NULL, NULL, 2, NULL, 0, 'Stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31B78CBB0389514E821ED2CF8E8A5BAE', X'E91D8545185F1F41811BEB31DA095D34', X'E91D8545185F1F41811BEB31DA095D34', 0, NULL, NULL, 2, NULL, 0, 'Repeat steps above to make blue ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50D87CE5404536498B858258A46F90ED', X'E91D8545185F1F41811BEB31DA095D34', X'E91D8545185F1F41811BEB31DA095D34', 1, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFE5AC1B7ABE9A4C90A61A928BE467E4', X'E91D8545185F1F41811BEB31DA095D34', X'E91D8545185F1F41811BEB31DA095D34', 2, NULL, NULL, 2, NULL, 0, 'Drip ganache over the edges and top of the cake alternating red and blue as you go around the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F43CF251AF15BA46878DE0CE548C9FE8', X'22FE428CA47D09488DA8BFC358292064', X'22FE428CA47D09488DA8BFC358292064', 0, NULL, NULL, 2, NULL, 0, 'Either pipe on white frosting stars or roll out and cut stars from white modeling chocolate (candy clay).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51081C363053F848877D179CB4D5FBE6', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03F48FF6C5BE1D46964D688950258A4B', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 1, NULL, NULL, 2, NULL, 0, 'Make cake batter using the package instructions and ingredients (oil, water and egg whites) adding 3 tablespoons of instant pudding mix and 3 tablespoons sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA146E78F476A542A3A5AD9AF439A71D', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 2, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with non-stick tin foil or parchment paper OR grease and flour 3 (6-inch) heart pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD6B71A81AFE754BBBB5857560DA8320', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 3, NULL, NULL, 2, NULL, 0, 'Spread cake batter in an even layer in pan/s.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36585F672585CD4D8C7FBFF28EFA6186', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 4, NULL, NULL, 2, NULL, 0, 'Bake sheet cake for 18-22 minutes OR bake heart cakes for 24-30 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC618C85C7551C4FB0757B57F28BBCF7', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 5, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely then remove cake/s from pan/s.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'849AC51D2510EB41B06AB26C07DDCC88', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 6, NULL, NULL, 2, NULL, 0, 'If using a sheet cake cut 6 hearts out using a 5 or 6-inch heart-shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CF3A7A556CF924BA69DA4E8151FC817', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 7, NULL, NULL, 2, NULL, 0, 'If you baked heart-shaped cakes, cut off the domed tops of the cakes, then cut the cake into two equally thick layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA579111746F9C4E9D8FFC974AFABAE8', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 8, NULL, NULL, 2, NULL, 0, 'Use the smaller cookie cutter to cut the centers out of 4 of the hearts, leaving 2 heart cakes intact.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8567F42237AD7E488622ECC979F52B75', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 9, NULL, NULL, 2, NULL, 0, 'Cut a small cake board into a heart shape, that is at least 1/4-inch larger than your heart cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1996351C1C77B4D8B27FE9CEB93511E', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 10, NULL, NULL, 2, NULL, 0, 'Set one full cake on the cake board.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFF8D7027D13494FAA2A9B3142D63BC8', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 11, NULL, NULL, 2, NULL, 0, 'Pipe some icing around the edge of the cake, then stack a cake with a heart-shaped hole in the center on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34BB28CAB92DD44FA909DEB914E260DA', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 12, NULL, NULL, 2, NULL, 0, 'Repeat, frosting and stacking the remaining cut-out cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'340E03D03DF937428B13A1E0E86E5811', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 13, NULL, NULL, 2, NULL, 0, 'Pour conversation heart candies into the center of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8385DD9FB84F3241AD0D0C9A4234BDC4', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 14, NULL, NULL, 2, NULL, 0, 'Pipe frosting around the edge of the top cut-out cake and set the remaining whole cake on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A2523CC5692394DAAAA79106FAEB88E', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 15, NULL, NULL, 2, NULL, 0, 'Color the remaining white frosting using pink coloring, or whatever color you choose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE89598055729B48B031AD396040FEF5', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 16, NULL, NULL, 2, NULL, 0, 'If desired, crumb coat the cake then frost the cake with a thick layer of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60649E3D691D4D4E9E19DCC83C0FB9B6', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 17, NULL, NULL, 2, NULL, 0, 'To add a phrase to the top of your cake, you can cut letters out of red modeling chocolate or fondant, and place them on top of the cake or use red frosting to pipe them on the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B3A380388F2204D96BAF884CC865A52', X'006A0E33BE036946B5B9CAB9FA336A05', X'006A0E33BE036946B5B9CAB9FA336A05', 18, NULL, NULL, 2, NULL, 0, 'Just before serving, cut into the cake and allow the candy to fall out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A91062F61DD9E54E8437EDCCB047D9D1', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC3B552E74A1404B80AD8ECC7316B423', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 1, NULL, NULL, 2, NULL, 0, 'Grease and flour a Mickey Mouse cake ring and set on a parchment paper-lined baking pan. (Or use a 6-inch pan and 2 jumbo muffin cups.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BC663DFEE8BE849A6D774BD29EF0473', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 2, NULL, NULL, 2, NULL, 0, 'Combine all the cake ingredients in a mixing bowl and beat, using an electric mixer, on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5344CDCAFEF4A44A86D0455906C43FA8', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 3, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3732661B0F495E43835DE9A480CD51F9', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 4, NULL, NULL, 2, NULL, 0, 'Pour batter into pan, filling it 2/3 full. Use the remaining batter to make cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB7ED4FE1ADD114AB9CFEE20BB20F566', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 5, NULL, NULL, 2, NULL, 0, 'Bake for 26-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AC3708F5CB3EB468F0445177589A52F', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 6, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C258D2B7CFF08842A8E3B75122E59AD2', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 7, NULL, NULL, 2, NULL, 0, 'Carefully flip the cake upside down and unmold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B47C0A9159131A45AD8ABD340302864B', X'E316F0B6FC719F4999FE4DE2B6287488', X'E316F0B6FC719F4999FE4DE2B6287488', 8, NULL, NULL, 2, NULL, 0, 'Cut the cake in half creating two even layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7258EF0306A1A64CBA61AEAAD0F88D8F', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', 0, NULL, NULL, 2, NULL, 0, 'Spread about 1 cup of white frosting in between the cake layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97A0CEF01B2928419676CC00B82CDAB6', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', 1, NULL, NULL, 2, NULL, 0, 'Color about 2 tablespoons of white frosting using orange food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B71A766701724442B74EB637609B348C', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', 2, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a small #14 or #15 star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21A9BEEABFE6284EA7A8F5689097BB11', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', X'DB6FD4B7570EEF4F9323C366DBC6AAD0', 3, NULL, NULL, 2, NULL, 0, 'Color the chocolate frosting using black food coloring and spoon into a pastry bag fitted with a coupler and a #3 round tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9C9AFF4A16FAC4FBBF582D82A2AB8BD', X'BB7CAFE4D82402448F4DFDCCF5886659', X'BB7CAFE4D82402448F4DFDCCF5886659', 0, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag fitted with a #3 round pastry tip with the clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97611C1F6442CD4F8E812F82F8557EAD', X'BB7CAFE4D82402448F4DFDCCF5886659', X'BB7CAFE4D82402448F4DFDCCF5886659', 1, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment paper over an image of Olaf (that has been printed in reverse) and pipe the gel over the outline of the mouth, eyes, carrot nose, and eyebrows.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F360A2F486EC684FB8590121E0F4C4B6', X'BB7CAFE4D82402448F4DFDCCF5886659', X'BB7CAFE4D82402448F4DFDCCF5886659', 2, NULL, NULL, 2, NULL, 0, 'Turn the template upside down and set it on the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF9F061A15E99D4BB2FCDF72DE5E6088', X'BB7CAFE4D82402448F4DFDCCF5886659', X'BB7CAFE4D82402448F4DFDCCF5886659', 3, NULL, NULL, 2, NULL, 0, 'Gently press to adhere the piping gel to the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7140C44073175B4EB2B04D3CFBF445DE', X'BB7CAFE4D82402448F4DFDCCF5886659', X'BB7CAFE4D82402448F4DFDCCF5886659', 4, NULL, NULL, 2, NULL, 0, 'Remove the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AD4FE723107D14ABB73A92FCB363240', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', 0, NULL, NULL, 2, NULL, 0, 'Pipe black frosting directly over top of the piping gel outline.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'694AF07D5C4725459411DE75D4F34F50', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', 1, NULL, NULL, 2, NULL, 0, 'Remove the round tip and replace it with a (#18) star tip.');
