INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F7B2A5242F984DBD274F3C8B912B54', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', 3, NULL, NULL, 2, NULL, 0, 'Pipe orange frosting stars into the carrot nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07861AD8178308459713C769A398627C', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', 4, NULL, NULL, 2, NULL, 0, 'Spoon white frosting into a pastry bag fitted with a (#18) star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'713C0EB3546B184DB95B20A92A93071C', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', 5, NULL, NULL, 2, NULL, 0, 'Pipe stars into the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4978C8EE7A2CB441B1CC51CDA620FCCD', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', 6, NULL, NULL, 2, NULL, 0, 'Then pipe white stars all over the rest of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9ABE46A879DA7469B9DD33C03FD1A9D', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', X'34DB15CE4EFF4B4C95DC36EC8C49D1DB', 7, NULL, NULL, 2, NULL, 0, 'If needed, replace the star tip with the round tip on the black frosting and pipe more black frosting over the pupils, eyebrows, and parts of the smile to make them show better.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D75C782F7C0CE47A2CB0F7320393248', X'ACD741BA4B4C374595128833A86A8DDE', X'ACD741BA4B4C374595128833A86A8DDE', 0, NULL, NULL, 2, NULL, 0, 'Either use chocolate frosting to pipe some twigs onto your cake board or pipe twigs out of candy melts and allow them to harden before inserting them into the cake between the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4ACA6D7947A2C4B935FC177F8EC91D7', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 0, NULL, NULL, 2, NULL, 0, 'Allow the cake ingredients to sit at room temperature for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B132B4FF1B82E042858CFE3B7876DF24', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E9CEE1307D2644CB001DDA2715B606F', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 2, NULL, NULL, 2, NULL, 0, 'Brush two 6-inch cake pans with butter then dust with flour and line with a round of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D2C8FB6E8E84249AC029AAE9188C1C6', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 3, NULL, NULL, 2, NULL, 0, 'Brush 4 cupcake cavities in a muffin tin with butter then dust with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D418509110446A4BB9EB14CCDC202DB8', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 4, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, water, oil, egg whites, sour cream, and pudding mix together in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'267EEB0B48DB0E47A885FA13342EB012', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 5, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat on low for 30 seconds then increase speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8474AE2519BA674A852BDC756575F691', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 6, NULL, NULL, 2, NULL, 0, 'Fill 4 cupcake cavities about 2/3''s full with batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'527FA81714590343918B2C3D40D42BBB', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 7, NULL, NULL, 2, NULL, 0, 'Divide the remaining batter among the two 6-inch cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F836F3340C42804893496A9C5F349A0E', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 8, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes and cakes for 18-26 minutes removing the cupcakes as soon as they are done at around 18-20 minutes. Check the cakes at around the 22 minute mark.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A8CDEC47A47F24CA21DBE0B6F01430C', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 9, NULL, NULL, 2, NULL, 0, 'A toothpick inserted into the cakes should come out with just a few moist crumbs. Or the cakes should spring back when your press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A9C962E5A2C194388C8037E9F4CBCB4', X'4B37DCD114F02F4B864A4EE6396791C7', X'4B37DCD114F02F4B864A4EE6396791C7', 10, NULL, NULL, 2, NULL, 0, 'Allow the cakes and cupcakes to cool for 10 minutes in their pans then remove them and set them on a cooling rack. Peel off the parchment paper once the cakes are cooled completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CB0ACBEF23EC14794B31A02B7CD608E', X'93E204547A203B48B6C37CB5179A687A', X'93E204547A203B48B6C37CB5179A687A', 0, NULL, NULL, 2, NULL, 0, 'Beat the shortening and butter together until well combined using an electric mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D75C1179D2C7034C85D89961771B20EF', X'93E204547A203B48B6C37CB5179A687A', X'93E204547A203B48B6C37CB5179A687A', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C6CFA7AB30CCD48B4CC21E9D84E0118', X'93E204547A203B48B6C37CB5179A687A', X'93E204547A203B48B6C37CB5179A687A', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla and 3 tablespoons of the milk and beat until fully incorporated and the frosting is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00068ECDA3A1A44FB73A595ADBF9D930', X'93E204547A203B48B6C37CB5179A687A', X'93E204547A203B48B6C37CB5179A687A', 3, NULL, NULL, 2, NULL, 0, 'Add more milk as needed to make the frosting soft enough to pipe through a star tip yet hold it''s shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2FACA4C02D8154E92BBACACE1F6E759', X'93E204547A203B48B6C37CB5179A687A', X'93E204547A203B48B6C37CB5179A687A', 4, NULL, NULL, 2, NULL, 0, 'Spoon 2/3 cup of the frosting into another bowl.');
