INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97D5B4BAF2FF284EA8C5633B9CEBBADD', X'A1A2FDB02D7B694AAE8D73763BE19797', X'A1A2FDB02D7B694AAE8D73763BE19797', 0, NULL, NULL, 2, NULL, 0, 'Roll out the green, black, white, and light blue modeling chocolate to about 1/8th inch thickness on a silicone mat using a silicone rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'814FB98C01C06C4DBE563C7CFF74C126', X'A1A2FDB02D7B694AAE8D73763BE19797', X'A1A2FDB02D7B694AAE8D73763BE19797', 1, NULL, NULL, 2, NULL, 0, 'Cut out 2 green and 2 blue tear drops, 4 black ovals, and 4 tiny white circles then attach them together forming two green eyes and 2 blue eyes with pupils and white highlights.');
