INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A51FFDCD56C734DB21584011809A7C2', X'CE28BAD089A6404C9D2DF3315168EC10', X'CE28BAD089A6404C9D2DF3315168EC10', 4, NULL, NULL, 2, NULL, 0, 'Spoon 2/3 cup of the frosting into another bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3306EC1FBCD468468760B951ED5C97A7', X'CE28BAD089A6404C9D2DF3315168EC10', X'CE28BAD089A6404C9D2DF3315168EC10', 5, NULL, NULL, 2, NULL, 0, 'Stir the cocoa powder into that small bowl of frosting then color using black food coloring and add more milk, if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6756BCDD137074CB59A8587CDA34568', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 0, NULL, NULL, 2, NULL, 0, 'Roll out the black, white, and pink modeling chocolate to about 1/8th inch thickness on a silicone mat using a silicone rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A79CFFC49132F409A9BE419D775D9FA', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 1, NULL, NULL, 2, NULL, 0, 'Cut four 2 1/4 inch by 1 3/4 inch ovals from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1B97A1CA7FCE1418056B68D6E7AB6C3', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 2, NULL, NULL, 2, NULL, 0, 'Cut four 1 1/2 inch circles from the white, four small 1/4 inch hearts from the white and four 1 inch circles from the black.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F88A04A35CD714479842A2C535E96AAF', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 3, NULL, NULL, 2, NULL, 0, 'Attach one white circle to one black oval using a thin layer of water or clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A40F74633373A14ABB24CE6D9A7D1BC5', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 4, NULL, NULL, 2, NULL, 0, 'Attach one black circle on top of the white circle then attach one small white heart along one edge of the black circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'529F2CC5C7DC6642BE3EB65964AAD174', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 eyes with all the hearts pointing up or down in one direction.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A86DFBF4DB7F74AB97F0553D43E7FC4', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 6, NULL, NULL, 2, NULL, 0, 'Cut out two 1 1/2 inch long heart shaped noses from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'308B25FEA8224143A9C3BCB4A05FCFC7', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 7, NULL, NULL, 2, NULL, 0, 'Cut two 1 inch hearts and one 1/2 inch circle from the pink modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA4D7ABC225977439AA90C74DDEC2D94', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', X'7E4CD057D8E2CB4DB62E2EE12A2BF12C', 8, NULL, NULL, 2, NULL, 0, 'Place the two points of the heart together then attach the circle over the top of the tips using water or piping gel to form a cute bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C97A0831DEA9E340893324C09D3BFEA9', X'06F0493C38A64B4C8B37252230C83269', X'06F0493C38A64B4C8B37252230C83269', 0, NULL, NULL, 2, NULL, 0, 'Attach two eyes and a nose using frosting to each round cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22384DCE8BE4324D8CD4FEE8BBD2EE1F', X'06F0493C38A64B4C8B37252230C83269', X'06F0493C38A64B4C8B37252230C83269', 1, NULL, NULL, 2, NULL, 0, 'Pipe white frosting using a star tip over the entire surface of the round cakes around the eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F443E713F4D61C45B0E67E31C333130C', X'06F0493C38A64B4C8B37252230C83269', X'06F0493C38A64B4C8B37252230C83269', 2, NULL, NULL, 2, NULL, 0, 'Pipe black frosting stars over the cupcake ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC4D3FC651BD0C4AA5F636D097E8E910', X'06F0493C38A64B4C8B37252230C83269', X'06F0493C38A64B4C8B37252230C83269', 3, NULL, NULL, 2, NULL, 0, 'Press the pink modeling chocolate bow just below one ear of one of the panda cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64CA0CB1B5FE214988CE01C66F60AAE9', X'06F0493C38A64B4C8B37252230C83269', X'06F0493C38A64B4C8B37252230C83269', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF4F40DAC13D6544AC1022B0EEADA173', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 0, NULL, NULL, 2, NULL, 0, 'Carve a Minion shaped oval out of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46ADED376D937143AB2484610A5AB211', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 1, NULL, NULL, 2, NULL, 0, 'Reserve all the cake scraps for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69EBAD95D654DD4285651C989AB8F09D', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 2, NULL, NULL, 2, NULL, 0, 'Spoon all the frosting colors into individual pastry bags, each fitted with a star tip, reserving about 1/2 cup of the black.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE5443EF15F13C469BC03B6656243B6B', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 3, NULL, NULL, 2, NULL, 0, 'Place the reserved black frosting into a pastry bag fitted with a #4 round tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D8C242A9A6F97458A4CAFEB67808926', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 4, NULL, NULL, 2, NULL, 0, 'Decorate the cake by piping stars all over the surface and sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA5272B97D1D85429D2D322460237D6B', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 5, NULL, NULL, 2, NULL, 0, 'Start by making two silver gray circles for the Minion''s goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D39B77C0DE2FF4DB4D33AA77E562E03', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 6, NULL, NULL, 2, NULL, 0, 'Pipe a small black circle in the middle of those circles then fill in around the black circle using white frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F4E4FFBFE6295459E2598C2E2B90390', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 7, NULL, NULL, 2, NULL, 0, 'Pipe a black band on either side of the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B825A95120F3940A3AA708D88649919', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 8, NULL, NULL, 2, NULL, 0, 'Pipe yellow above and just below the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA6E33430E76A34892777232EB2EAD43', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 9, NULL, NULL, 2, NULL, 0, 'Pipe on the black and white striped prisoner uniform.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14E10DDEE34B8C469C1A03E482C535CE', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 10, NULL, NULL, 2, NULL, 0, 'Pipe a line around the straps to the uniform and pipe on the outline of a pocket in the middle of the uniform.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80C0867CA7D25F49A3D5595B75664286', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 11, NULL, NULL, 2, NULL, 0, 'Pipe white stars inside the pocket border then pipe on a prisoner number over the white stars using black frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'748026DEA3F80B46A48127B91FDDD693', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 12, NULL, NULL, 2, NULL, 0, 'Pipe on a black smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F376ACE7053B4248B1176FCEBE29859C', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 13, NULL, NULL, 2, NULL, 0, 'Glue together the cake scraps using frosting and cut a rectangle prisoner hat out of the scraps.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FE1A2F4DEE2F449970876E89F892815', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 14, NULL, NULL, 2, NULL, 0, 'Attach the hat to the top of the Minion and pipe on black and white stripes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33A888A6DD84D84D9BA6456893747D9A', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 15, NULL, NULL, 2, NULL, 0, 'Crumble all the remaining cake scraps and blend together with a tablespoon or two of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'599B5A1200E6EF4CBBCB98251302EE3E', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 16, NULL, NULL, 2, NULL, 0, 'Use this cake ball mixture to shape two arms, two legs, and two shoes for the Minion.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CD3C5ED85183F43865ED68C205573F9', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 17, NULL, NULL, 2, NULL, 0, 'Pipe yellow frosting over the arm then pipe on black gloves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E78C733B4843E14A83DCA2438523BFE6', X'2F1A5CDE8D985747A8945E8D2504D7B4', X'2F1A5CDE8D985747A8945E8D2504D7B4', 18, NULL, NULL, 2, NULL, 0, 'Pipe white frosting over the legs then black over the shoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C59CAB19481E284288B13C560A479E91', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3750151C83C78E479FB3424BF9952CE8', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 1, NULL, NULL, 2, NULL, 0, 'Line a 9x13 inch baking pan with non-stick tin foil, parchment paper, or baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD366AC7B5175243928C1DA6764E10EE', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 2, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, graham cracker crumbs, water, sour cream, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'116A65836A87E245AD963203E9252C20', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 3, NULL, NULL, 2, NULL, 0, 'Beat on low speed using an electric mixer for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D56A2A82D32DD34D8507D851596CC2CB', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 4, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74B8D4EA261A4C4EAC9BBA54F00917EB', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 5, NULL, NULL, 2, NULL, 0, 'Fold in the chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'818885F96B367F49B03B400E7E0E685C', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 6, NULL, NULL, 2, NULL, 0, 'Pour batter into prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48D20D19BCCD4F45953EAEBCE94FEAD7', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 7, NULL, NULL, 2, NULL, 0, 'Bake for 28-32 minutes until a toothpick inserted into the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA9AEADCAA81804A9165CFBB2A5387EE', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 8, NULL, NULL, 2, NULL, 0, 'Remove from oven, and set pan on a cooling rack and allow cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BF4D9F81245A646B595920306B6AF88', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 9, NULL, NULL, 2, NULL, 0, 'Remove cake from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A69E29C04C8A06438E1979612E3B0D64', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 10, NULL, NULL, 2, NULL, 0, 'Cut cake in half so you have two 9 inch by 6 1/2 inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E116CF2951FA224BA7790E24F5A0CDD6', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 11, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D5E13535313154FA13E76E63713FF2C', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 12, NULL, NULL, 2, NULL, 0, 'Cut the other piece in half so you have two 6 1/2 by 4 1/2 inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AD2DC05D594BF4D90A16AD6C89BD721', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 13, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAD86D54229A18498A954CF9D0134917', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 14, NULL, NULL, 2, NULL, 0, 'Cut two 2 inch by 3 inch pieces for the unicorn''s legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8296944905BD984691C2EB5A737B0A73', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 15, NULL, NULL, 2, NULL, 0, 'Discard or eat the remaining pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E88526D04425BD4BBB16F09AD869ABE9', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 16, NULL, NULL, 2, NULL, 0, 'Assemble the cake by placing the 9 inch by 6 1/2 inch cake in the lower half of your cake board forming the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FEE5FF9E737694191AEDDE26258BD2B', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 17, NULL, NULL, 2, NULL, 0, 'Set the 6 1/2 inch by 4 1/2 inch piece about 2 1/2 inches from the top edge of the larger cake forming the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F47E141F91716488FAA4214A1E5F9AB', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 18, NULL, NULL, 2, NULL, 0, 'Place the two leg pieces under the body piece about 3/4 of an inch from either edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'785DF38EBE6D2C4E9F0137AE5302C081', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 19, NULL, NULL, 2, NULL, 0, 'Mix together the marshmallow frosting ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD58B8352D40774087E146287315FAE7', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 20, NULL, NULL, 2, NULL, 0, 'Spread the white frosting over the body cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55DF5EE442E977419EBCD0B54054D92C', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 21, NULL, NULL, 2, NULL, 0, 'Mix together the black marshmallow frosting ingredients, adding a tiny bit of water, as needed to thin out the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38D7F3660344A847A99B9848C4CF94A1', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 22, NULL, NULL, 2, NULL, 0, 'Frost the legs using the black frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9E4DBF97A2CFB478CBD7F90093C677D', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 23, NULL, NULL, 2, NULL, 0, 'For the head, brush black frosting onto about 3/4 of the head piece, curving the back edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B2843BB9C8F2347B9477A576FDF5AD8', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 24, NULL, NULL, 2, NULL, 0, 'Add white frosting on the back part of the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE7CE291DC08864C8DBFEE1DD6FFFCA8', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 25, NULL, NULL, 2, NULL, 0, 'Pour the remaining white and black frosting into individual pastry or zip top bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5539229FE08234BB0DD1D0CC420E0B3', X'C2ED724750C84342BDF03B890E590168', X'C2ED724750C84342BDF03B890E590168', 26, NULL, NULL, 2, NULL, 0, 'Pipe on a black ear, a white tail, a white eyelid with eye lashes, and a nose and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E8654C9E412004994E4F40C452E5D2A', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350ºF.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47EF2617B1D2BE41BE3C6D23CD16FD9B', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 1, NULL, NULL, 2, NULL, 0, 'Line two 8x3-inch square baking pans with two strips of non-stick tin foil or parchment paper for easy removal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3616CACED616C418AA3E67674D149B5', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 2, NULL, NULL, 2, NULL, 0, 'If using, place one metal cake rod in each pan (under or over the tin foil.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'868A0D93B78222439CE4702613939FBB', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 3, NULL, NULL, 2, NULL, 0, 'Pound Cake Letters:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AD49B5AB855644AA1E6573F65D87844', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 4, NULL, NULL, 2, NULL, 0, 'Combine the pound cake mix with the softened butter and beat on medium low speed to break up the butter into small pieces. Add the milk, sour cream, and eggs and beat on low for 30 seconds then on medium for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'845214EF77837A4FA333E8E24C652C08', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 5, NULL, NULL, 2, NULL, 0, 'Eequally divide the batter among the two 8x3-inch square pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C8F23926A911D4C853CC57890D312B3', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 6, NULL, NULL, 2, NULL, 0, 'Bake for 65-75 minutes, until a thin wood skewer inserted into the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C135FA047B7F4C4EA31077CD27B6DBA4', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 7, NULL, NULL, 2, NULL, 0, 'Cool cakes in pans for 10 minutes, then remove cakes and set on a cooling rack and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A1D403BFC70C4438196B9FF1B83DCC2', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 8, NULL, NULL, 2, NULL, 0, 'Trim off the domed top and about 1/4 inch from the sides of each cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'181251587E9E924887FE08296DD71DC1', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 9, NULL, NULL, 2, NULL, 0, 'Cut cakes into 8 slices, about 3/4 inches thick. Lay the slices flat on non-stick tin foil or parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31706B01B36B1D4EB7E6FB37A286763C', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 10, NULL, NULL, 2, NULL, 0, 'Set in the freezer for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18CB85E00910CE44B0D410083E81BBDE', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 11, NULL, NULL, 2, NULL, 0, 'Remove 2 slices of cake from the freezer at a time and use small alphabet cookie cutters to cut out the letters needed to spell out Eagle Scout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57DEA60D812214428ECB5B6104F0F850', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 12, NULL, NULL, 2, NULL, 0, 'Refreeze letters for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41D125DF6B769A469C23DE6B001138F9', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 13, NULL, NULL, 2, NULL, 0, 'Cake Layers:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE270708A7502A4C9C1F7270BAAFFD14', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 14, NULL, NULL, 2, NULL, 0, 'Preheat one or two ovens to 350ºF.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88CFFB697F8AA94AB7E888192E4ED724', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 15, NULL, NULL, 2, NULL, 0, 'Line two 8x3-inch square baking pans with two strips of non-stick tinfoil or parchment paper for easy removal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE4C308355C2E14A8484ED99EC3E868A', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 16, NULL, NULL, 2, NULL, 0, 'Mark the front of each cake layer by cutting or marking the foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56585184420C5F46AFF1D91690D3F6F2', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 17, NULL, NULL, 2, NULL, 0, 'Red Cake Layer:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D34B93368911E04F92D62E53AD66B9C3', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 18, NULL, NULL, 2, NULL, 0, 'Combine Pillsbury Funfetti red cake mixes with the white chocolate pudding mix, sour cream, egg whites, milk and oil. Mix on low speed for 30 seconds the on medium for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BF82197DAA26B4AA307DE2F135B8A20', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 19, NULL, NULL, 2, NULL, 0, 'Pour some of the cake batter into a piping bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67A6638AE00E9447BEACB73F15648D53', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 20, NULL, NULL, 2, NULL, 0, 'Pipe red batter into the openings of the `EAGLE` cake letters, filling them 2/3 full.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B670CCC477AE943820BEC86F24F627E', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 21, NULL, NULL, 2, NULL, 0, 'Pipe a thin layer of red batter over the bottom of one of the square pans. Set the letters EAGLE in a row about 1/2 inch from the front of the pan. Continue to add letters, piping batter in between as you go, until you have 8 rows of letters in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB755ABFF6651849A778EC088C4F6616', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 22, NULL, NULL, 2, NULL, 0, 'Pipe batter all around and over top of the letters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0986165BB2B404F830BD444376708C6', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 23, NULL, NULL, 2, NULL, 0, 'Bake for about 1 hour 25 minutes, until a toothpick inserted in the center comes out clean. The top of the cake will get very deep brown and may crack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A8800446329864387C36A20AF62EA08', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 24, NULL, NULL, 2, NULL, 0, 'Repeat this process to create a blue cake layer with the word SCOUT inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F82704CEEB376439438D2FD6A9D2B3C', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 25, NULL, NULL, 2, NULL, 0, 'Note: If using only one oven, allow the first cake to bake completely before making the second. It will take about 40 minutes to prepare the second cake and it''s best not to open the oven door at that point in the baking process.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A11E2C88FAE174B8CD7B7E9CFBCB0F9', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 26, NULL, NULL, 2, NULL, 0, 'Allow the cakes to cool for 10 minutes in their pans, then remove to a wire rack and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7246BE710BDF8C44B5AA596D2DCC1327', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 27, NULL, NULL, 2, NULL, 0, 'Cut off the domed part of each cake, being sure you don''t cut into the letters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC1494AB0AC6F14782318378C762CF50', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 28, NULL, NULL, 2, NULL, 0, 'Set the blue cake on an 8-inch square cake board so that the letters face forward. Spread frosting over the layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B3F5F7BD666514C88820095BD288BC9', X'92D9DC5FEB7D8743AF5891A5A2085493', X'92D9DC5FEB7D8743AF5891A5A2085493', 29, NULL, NULL, 2, NULL, 0, 'Set the red cake on top, making sure the letters face forward. Frost and decorate cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF456446855BFD4B8EA07449F44531C2', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 0, NULL, NULL, 2, NULL, 0, 'CRUST:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A12674D12996F4487825F66F1365137', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA2C88B895DBBC4DAEC6F46EDF967EC7', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 2, NULL, NULL, 2, NULL, 0, 'Spray a 9 inch springform pan with cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0660A50A931F8847AE4902884C0FDE84', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 3, NULL, NULL, 2, NULL, 0, 'Combine graham cracker crumbs, 1 1/2 tablespoons sugar, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11714F004C2FAC439B8116FA3E71B1CA', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 4, NULL, NULL, 2, NULL, 0, 'Press it in the bottom and up the sides of the springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F3C28C5D0CA1A49A02E6C767C39F5BE', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 5, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until fragrant and the edges begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A6FB970144E9148BD8DD7C7B7827FFF', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and press into an even layer using a flat bottomed glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA32FA40DDF1DE4BBC6E0DCCD2921563', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 7, NULL, NULL, 2, NULL, 0, 'Allow the crust to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40610084EBE4A44AAE3C152A7994C0C1', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 8, NULL, NULL, 2, NULL, 0, 'Set a large roasting pan in the oven and pour in 1 1/2 inches of hot water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5418E3CE2A1FA847903071332C3CDC32', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 9, NULL, NULL, 2, NULL, 0, 'If using a traditional clamp style sprinform pan, wrap the bottom of the pan in two layers of tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74660FA35D8FC840B812ED09F116577C', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 10, NULL, NULL, 2, NULL, 0, 'CHEESECAKE FILLING:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CE03742F0636E4B9DC718756459DE3C', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 11, NULL, NULL, 2, NULL, 0, 'Combine cream cheese and sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69375579F159C04FAFBB41588DBF3D12', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 12, NULL, NULL, 2, NULL, 0, 'Add eggs and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6292119AE68A794CB9C067A8880BDEC8', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 13, NULL, NULL, 2, NULL, 0, 'Add sour cream and vanilla and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99CE12A9A5D8F24D806C1A864F67CBFB', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 14, NULL, NULL, 2, NULL, 0, 'Scoop out 3 tablespoons of filing and place in a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F3A836E774DA3449C0380D7C4BF72B2', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 15, NULL, NULL, 2, NULL, 0, 'Color using several drops of blue food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63688CA96132344D963F52E34175D5CE', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 16, NULL, NULL, 2, NULL, 0, 'Spoon into a squeeze bottle or pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07BF423F9C1308469EE7F0B2F2BCB4F3', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 17, NULL, NULL, 2, NULL, 0, 'Scoop out 2 tablespoons of filling and place in a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A559306B2C1144CA17CE9B5C569AA29', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 18, NULL, NULL, 2, NULL, 0, 'Color using red food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A1524E86F6F1C4BBB541DA4051B8AFC', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 19, NULL, NULL, 2, NULL, 0, 'Spoon into a squeeze bottle or pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D7DCD6E22B9354E8349048ABDAF3A15', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 20, NULL, NULL, 2, NULL, 0, 'Pour the remaining cheesecake filing over crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAECB739D7FA5D498782C5A8D650B851', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 21, NULL, NULL, 2, NULL, 0, 'Use a piping gel transfer to add the Eagle Scout Emblem to the top of the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31BCDD06A06FA447B8C2A7895B6E07B9', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 22, NULL, NULL, 2, NULL, 0, 'Pipe the red and blue cheesecake filling to create the Eagle Scout Emblem.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7539E44CAFD82E4899A45A1E2C2197B4', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 23, NULL, NULL, 2, NULL, 0, 'Set cheesecake in roasting pan filled with water and bake for 45-50 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBF7C2A65F31DA48A0F04F598DABBD12', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 24, NULL, NULL, 2, NULL, 0, 'The top of the cheesecake should look set but still wiggle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FE89558DD567542BB26B4F003288D60', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 25, NULL, NULL, 2, NULL, 0, 'Turn off oven, crack open door, and let cheesecake sit for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACC60F9881DCE64981BFE69F874E4A22', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 26, NULL, NULL, 2, NULL, 0, 'Remove cheesecake from water bath and allow to cool for 1 hour at room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B5AEECC9EED9D4082B7B445F4564026', X'C09D30FFAF3D8A4BB30D6813FF67F403', X'C09D30FFAF3D8A4BB30D6813FF67F403', 27, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 6 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAFDB24F6867CE4897C2E9D3CCD9F885', X'EA40AA89A292A14FAD33E0A52F13B2F5', X'EA40AA89A292A14FAD33E0A52F13B2F5', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A2390663B17464988AB667445920782', X'EA40AA89A292A14FAD33E0A52F13B2F5', X'EA40AA89A292A14FAD33E0A52F13B2F5', 1, NULL, NULL, 2, NULL, 0, 'Heat the frozen raspberries in a saucepan set over medium heat. As the raspberries get hot, begin smashing them. Once they are broken down, remove them from the heat and pour into a fine mesh sieve set over a glass measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B255884E9941A48880EAB69BBA04D8A', X'EA40AA89A292A14FAD33E0A52F13B2F5', X'EA40AA89A292A14FAD33E0A52F13B2F5', 2, NULL, NULL, 2, NULL, 0, 'Press the raspberries to extract the juice. Discard the pulp and seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6B5B58E5298AD4C9FE7DB74EEABC719', X'EA40AA89A292A14FAD33E0A52F13B2F5', X'EA40AA89A292A14FAD33E0A52F13B2F5', 3, NULL, NULL, 2, NULL, 0, 'You need 1 1/3 cup of the raspberry puree for the pound cake recipe. If you dont have enough, add some water. If you have too much, remove some.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD529C7641F0154C8572186C5119E099', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 0, NULL, NULL, 2, NULL, 0, 'Combine 2 pound cake mixes with whipped topping mix and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F51DC4DDEF363047BDFF3939F6DDA253', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 1, NULL, NULL, 2, NULL, 0, 'Mix on low speed in a stand mixer until the butter is broken up into pea size pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E364855AB74AE46B98C7E883BD170DD', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 2, NULL, NULL, 2, NULL, 0, 'Add the raspberry puree, eggs, egg whites, and raspberry flavored candy oil, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D546A98C057174E98285A2EB7911117', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 3, NULL, NULL, 2, NULL, 0, 'Mix on low speed for 1 minute, then increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9466193DBA60594AB7EDBC55492754F4', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 4, NULL, NULL, 2, NULL, 0, 'Divide the raspberry pound cake batter into 3 bowls and color one red, one bright pink, and one light pink.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48DBF6A1B727D047A9B3D06FED191107', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 5, NULL, NULL, 2, NULL, 0, 'Spray a Wilton Long Loaf pan with Wilton Bake Easy Spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13B6472ED9F6F04B9ADD3B417C1A8AA1', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 6, NULL, NULL, 2, NULL, 0, 'Pour each color batter into a Wilton Disposable Pastry Bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F013E8691B98DA4F8370681C80643F22', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 7, NULL, NULL, 2, NULL, 0, 'Pipe squiggles of batter over the bottom of the pan. Start with red, then light pink, then red, then bright pink. Repeat until you fill the entire pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83391EE92119B145810A97AD9FE4A2F8', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 8, NULL, NULL, 2, NULL, 0, 'Tap the pan a few times on the counter to remove any big air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E4D445F694E5A46840797743197B69A', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 9, NULL, NULL, 2, NULL, 0, 'Bake for 70-80 minutes, until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'436D974A272CF245B424A11A043CFC42', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 10, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool for 10 minutes, then remove it from the pan and allow it to cool for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89A04A91C128F6418D7752654E17BC4C', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 11, NULL, NULL, 2, NULL, 0, 'Trim the cracked top and sides off the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B42E89D005389A418FAF47AADA8308BB', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 12, NULL, NULL, 2, NULL, 0, 'Use a Wilton Cake Level set at the lowest setting to cut 4 even slices out of the length of cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E8A52126EF8A9469EA357E8386A59BD', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 13, NULL, NULL, 2, NULL, 0, 'The slices need to be no thicker than the cookie cutter you will use.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B9453DD3F1ABE49B4F88DCC1624F32E', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 14, NULL, NULL, 2, NULL, 0, 'Cut I Y O U and a heart out of each slice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DD28F01AFA75B44A9E7C1354CBF9B8A', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 15, NULL, NULL, 2, NULL, 0, 'Lay the cake cutouts in a single layer on a cutting board or baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'884EC889075BBF4498D77484CF1CBF5E', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 16, NULL, NULL, 2, NULL, 0, 'Freeze for at least 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CB00DEB7671FC4087C3AEFF0E793787', X'93C4AFEF54D2B043B8A42BB068EC1212', X'93C4AFEF54D2B043B8A42BB068EC1212', 17, NULL, NULL, 2, NULL, 0, 'Once the cut-outs are frozen, you can make the lemon pound cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'430C20767D39EE469CBA7E6BF922CF2F', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 0, NULL, NULL, 2, NULL, 0, 'Combine all the lemon pound cake ingredients in the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B327523E15ED949B01D4DE802AEFD28', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 1, NULL, NULL, 2, NULL, 0, 'Beat on low speed for one minute, then on medium speed for 2 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82F97474DE937A4D88F3038AF535617A', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 2, NULL, NULL, 2, NULL, 0, 'Spray a Wilton Long Loaf pan with Bake Easy Spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C711424D98C7D746B39791DB13D8B3BA', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 3, NULL, NULL, 2, NULL, 0, 'Pour a small amount of cake batter into the pan, filling it about an inch deep.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E5590814A04D64FA585CEC0CD4C96BD', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 4, NULL, NULL, 2, NULL, 0, 'Stack the I cakes together and set them near one end of the pan, pressing them down into the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55FCF065639D3F4C8A1B42CBCD7FA19C', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 5, NULL, NULL, 2, NULL, 0, 'Stack the U cakes together and set at the opposite end of the pan, pressing them down into the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAA1549DF1D15642AF5D7D71953F227C', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 6, NULL, NULL, 2, NULL, 0, 'Stack the O cakes together and set next to the U cakes, pressing them down into the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B239A486C6950747BF9F4912FF7B98FC', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 7, NULL, NULL, 2, NULL, 0, 'Pour some of the cake batter into a 16 inch disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3510CDCEA21B6478281644BC4DA4637', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 8, NULL, NULL, 2, NULL, 0, 'Pipe some batter around the U cakes and in the middle of the U cakes and in the middle of the O cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B205EC27C91FC4E96328A41955DCC90', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 9, NULL, NULL, 2, NULL, 0, 'Pipe some batter in between the I cakes and the O cakes. You want enough to hold the hearts and the Y cakes in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F760E54D40F5243B8C50F028CB95A3F', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 10, NULL, NULL, 2, NULL, 0, 'Stack the heart cakes together and press into the batter next to the I cakes. Do the same for the Y cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99E85A3026D7A34D993F2658E83A8C9D', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 11, NULL, NULL, 2, NULL, 0, 'Cover the letters with the remaining cake batter. Spread it out in an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49B1A31446575247AD33B69FBE79A4BD', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 12, NULL, NULL, 2, NULL, 0, 'Mark the pan so you know which way the letters are facing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3F09DB2FC9E9B43A7B421831C33283B', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 13, NULL, NULL, 2, NULL, 0, 'Bake the cake at 325 degrees Fahrenheit for 75-85 minutes until a cake tester (or skewer) inserted into the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDAC00D2679FDB4FA0ECD0CA85C996D9', X'1D318171317A0B48BEB44466742A11DD', X'1D318171317A0B48BEB44466742A11DD', 14, NULL, NULL, 2, NULL, 0, 'Allow to cool in the pan for 10 minutes, then turn it out onto a cooling rack to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58381DD00F15114BA1687D74037CBF5F', X'752391B7260B5044926465D8E20B0B56', X'752391B7260B5044926465D8E20B0B56', 0, NULL, NULL, 2, NULL, 0, 'Use an offset spatula to frost the cake so that you have a very thick layer of frosting all over the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FB2806E3126C544B245736DC285BBB9', X'752391B7260B5044926465D8E20B0B56', X'752391B7260B5044926465D8E20B0B56', 1, NULL, NULL, 2, NULL, 0, 'Cover the cake in fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CE7C5B4C553464E858E0C9855DCF21E', X'752391B7260B5044926465D8E20B0B56', X'752391B7260B5044926465D8E20B0B56', 2, NULL, NULL, 2, NULL, 0, 'Roll out the red fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63F21318AC4DC349BD06FF03ADA7EE74', X'752391B7260B5044926465D8E20B0B56', X'752391B7260B5044926465D8E20B0B56', 3, NULL, NULL, 2, NULL, 0, 'Cut four different size hearts using cookie cutters and fondant plunger cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3618BA50593D164F87955C3361B081C0', X'752391B7260B5044926465D8E20B0B56', X'752391B7260B5044926465D8E20B0B56', 4, NULL, NULL, 2, NULL, 0, 'Brush the back sides of the fondant hearts with water using Wiltons Water Brush (which is another amazing tool,) and press them onto the fondant covered cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5B7D37169D3574C85147B5DD665AD6E', X'752391B7260B5044926465D8E20B0B56', X'752391B7260B5044926465D8E20B0B56', 5, NULL, NULL, 2, NULL, 0, 'Place a few tiny hearts on the cake board, to mark the front of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6E38F87C7F4FF4496F38AE4C5544CC6', X'8B117065D83EB9479B8351635DA57865', X'8B117065D83EB9479B8351635DA57865', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86A13F1BD83C0E4C92FEF37816A1CB79', X'8B117065D83EB9479B8351635DA57865', X'8B117065D83EB9479B8351635DA57865', 1, NULL, NULL, 2, NULL, 0, 'Spray a 9 inch springform pan with non-stick baking spray. Wipe out with a paper towel, leaving just a thin coating on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'366A42358C54EC4A95A7289F23B12E79', X'8B117065D83EB9479B8351635DA57865', X'8B117065D83EB9479B8351635DA57865', 2, NULL, NULL, 2, NULL, 0, 'Stir together brownie mix, vegetable oil, water and egg. Pour into the 9 inch springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'130024B5EB915C4B9FD8C12F2D4DBFB4', X'8B117065D83EB9479B8351635DA57865', X'8B117065D83EB9479B8351635DA57865', 3, NULL, NULL, 2, NULL, 0, 'Bake for 48-52 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B01542F9014B9E40B5C6BB78500502BF', X'8B117065D83EB9479B8351635DA57865', X'8B117065D83EB9479B8351635DA57865', 4, NULL, NULL, 2, NULL, 0, 'Cool completely in pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2D12FBE86FB4E4F83C534A2EC99EFE6', X'8B117065D83EB9479B8351635DA57865', X'8B117065D83EB9479B8351635DA57865', 5, NULL, NULL, 2, NULL, 0, 'Remove ring, and loosen brownie from the bottom before you top it with mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AF1AE97ECE82242BAC6F2444A1972A5', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 0, NULL, NULL, 2, NULL, 0, 'Whisk whipping cream to soft peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A78D169F463524CB69C4FB36A3A2766', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 1, NULL, NULL, 2, NULL, 0, 'Melt the chocolate. Click here for detailed instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A5EA24B3E6E984090EE78C2E46C55D1', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 2, NULL, NULL, 2, NULL, 0, 'Pour water and sugar in a small saucepan and bring it to a boil over medium heat, stirring occasionally until the sugar dissolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99D63D883C37FD4BB2DF6155FEF0A5F5', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 3, NULL, NULL, 2, NULL, 0, 'Whisk egg yolks in a medium bowl and slowly drizzle in the hot sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DA451E0E3069B4BADBB97D2AC2DAAA2', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 4, NULL, NULL, 2, NULL, 0, 'Combine eggs with melted chocolate, stirring until well incorporated. Heat in microwave on high power for 15 seconds. Stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD9D518E0F16634BA04F7C51A7FA658A', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 5, NULL, NULL, 2, NULL, 0, 'Add one third of the whipped cream to the chocolate and whisk until well incorporated. Gently fold in the remaining whipped cream until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F23F82679321084BB8EA957908C977A4', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 6, NULL, NULL, 2, NULL, 0, 'Pour mousse over the chocolate brownie layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDFE4DEFFE25C44AA50E2E3519280167', X'E6A448E88F51264180B98DE151597AE9', X'E6A448E88F51264180B98DE151597AE9', 7, NULL, NULL, 2, NULL, 0, 'Spread evenly over top then refrigerate the cake for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48471FC4057DA64FBFD449248825E9B5', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over water. Set aside for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D79136459C857542A7391EFC618B608A', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 1, NULL, NULL, 2, NULL, 0, 'Bring 1/2 cup heavy whipping cream just to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9C6CF1AA30B5948B6999663ED62A5F3', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in gelatin until dissolved.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0135F3ADDBFB7F459B110E0E2712475F', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 3, NULL, NULL, 2, NULL, 0, 'Pour over white chocolate. Let sit one minute, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D619FEE4815ED54EBB8536A414E2CF74', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 4, NULL, NULL, 2, NULL, 0, 'Combine remaining 1 cups heavy whipping cream and peppermint extract and whisk to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4967B5C7CBD7641A4B6C933567CC474', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 5, NULL, NULL, 2, NULL, 0, 'Fold whipped cream into white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD5B8A47FC3F454F811B43A19FFC2649', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 6, NULL, NULL, 2, NULL, 0, 'Spread evenly over chocolate mousse layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3297D626E828E2429C3F4C8D5B2A4641', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate cake for at least two hours and up to 3 days.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CB1CF664F443A418E07CD84B100CC41', X'82BC3CC99C929644BA6B6DEA2ED11400', X'82BC3CC99C929644BA6B6DEA2ED11400', 8, NULL, NULL, 2, NULL, 0, 'Can be frozen for up to a month.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71CB715AC511BC498C5EE84873D00B5C', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', 0, NULL, NULL, 2, NULL, 0, 'pipe white chocolate into candy cane shapes');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCB11A19B0A6D54989C73498B1F9B68E', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', 1, NULL, NULL, 2, NULL, 0, 'sprinkle with crushed candy canes');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8690F6898929B4794991BB8FE6D117C', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', 2, NULL, NULL, 2, NULL, 0, 'just before serving set cake on cake plate and press candy canes into the side of the cake');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C178AB19B43EFB40A321DEB05FA6AB24', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', X'3A3F1B95F284FC4A97C33AB7DE0F5DE7', 3, NULL, NULL, 2, NULL, 0, 'sprinkle top of cake with crushed candy canes');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AABE1771339664FB151D26EE6019F24', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'413867227718E84FB020A479D41FEA85', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 1, NULL, NULL, 2, NULL, 0, 'Generously grease the sides and base of a heart shaped springform pan with butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1204780B986CFA46B737BB9DE613951A', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 2, NULL, NULL, 2, NULL, 0, 'Melt chocolate and butter in a double boiler set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3437DE3500909248AA9144C587287435', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool for 5 minutes, then stir in vanilla and egg yolks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED97E8F3E81AEB4E90CFD22DCE64C44B', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 4, NULL, NULL, 2, NULL, 0, 'Whisk egg whites and salt until foam forms.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2508557E67FEE4B84124AB5E23FFE72', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle in about 1/2 of the brown sugar and whisk until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80F4E2CD304EDF4FAB66EC9B4A2E3F4E', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 6, NULL, NULL, 2, NULL, 0, 'Add the rest of the brown sugar and whisk to soft peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'934B5296C524834D9E4002C1B396B983', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 7, NULL, NULL, 2, NULL, 0, 'Fold whipped egg whites into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31BA3168E0F78A428C6BCC2DB1AFBCFD', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 8, NULL, NULL, 2, NULL, 0, 'Pour batter into springform pan and bake for 15-18 minutes until cake has risen, and springs back when touched in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'163BD9D767121C48AE6936457412FA66', X'C99C865740F9234AAAD9222294302719', X'C99C865740F9234AAAD9222294302719', 9, NULL, NULL, 2, NULL, 0, 'Cool completely for one hour. The cake will fall as it cools.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F97EF3F0040C74DA4A040D93CAF27F5', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 0, NULL, NULL, 2, NULL, 0, 'Whisk whipping cream to soft peaks. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEAFBC047332CB4081876520963978B0', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 1, NULL, NULL, 2, NULL, 0, 'Pour water and sugar in a small saucepan and bring it to a boil over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87241333CB808F40AA136F4BCF36FE40', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 2, NULL, NULL, 2, NULL, 0, 'Whisk egg yolks in a medium bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E16B2FA12E7B044A2A399D22159507B', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 3, NULL, NULL, 2, NULL, 0, 'Slowly drizzle the hot sugar syrup (water and sugar) into the eggs, whisking continuously.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ECB459A24E44243B65388F783898575', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 4, NULL, NULL, 2, NULL, 0, 'Stir into melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BD5CF349955AB41AD9689BAF9FD7329', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 5, NULL, NULL, 2, NULL, 0, 'Fold whipped cream into chocolate until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A41175328D17B145949454A47684A2F8', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 6, NULL, NULL, 2, NULL, 0, 'Spread evenly over flourless chocolate cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78F4AC39EE2F2B40BDBDCD98394749B5', X'EB0E0ABCDD160349B0FF8398D08C9788', X'EB0E0ABCDD160349B0FF8398D08C9788', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6ADC8FC4CA32E42B2458665ABDFE95C', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over water. Set aside for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7617C3FCEF2C904F8949AE58D57F8054', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 1, NULL, NULL, 2, NULL, 0, 'Bring 1/2 cup heavy whipping cream just to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6139BA710224F49BD1C92C7712D66E4', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in gelatin until dissolved.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BBF0A87AB742C48B3010962FD634486', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 3, NULL, NULL, 2, NULL, 0, 'Pour over white chocolate. Let sit one minute, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38880470FE2294469D4BF6E0ACA2FF3A', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 4, NULL, NULL, 2, NULL, 0, 'Whisk remaining 1 cups heavy whipping cream to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54B5AA0EFA24F64FBFF2E817AB126915', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 5, NULL, NULL, 2, NULL, 0, 'Fold whipped cream into white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38CF14CF344F1E469DF10EA532A9FED7', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 6, NULL, NULL, 2, NULL, 0, 'Fold maraschino cherries into white chocolate mousse, leaving a few to sprinkle on top of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'481F61C9C71E2F49BB2786F4EFC2F4EE', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 7, NULL, NULL, 2, NULL, 0, 'Spread evenly over chocolate mousse layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2387D6FD756D2348903A8C07B15FDE84', X'B37C52796EDC2E4E9F95B0F7B5B80597', X'B37C52796EDC2E4E9F95B0F7B5B80597', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate cake for at least 2 hours before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABE04C0A444EF2479C2800FED24A6093', X'3428D9D171D1264F926DBDF57D4C45FD', X'3428D9D171D1264F926DBDF57D4C45FD', 0, NULL, NULL, 2, NULL, 0, 'Stir together the water and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BDD564412C0C44789814319AE37A7BA', X'3428D9D171D1264F926DBDF57D4C45FD', X'3428D9D171D1264F926DBDF57D4C45FD', 1, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9C5786FBB564F4C840CF36EFF128C1F', X'3428D9D171D1264F926DBDF57D4C45FD', X'3428D9D171D1264F926DBDF57D4C45FD', 2, NULL, NULL, 2, NULL, 0, 'Stir until the sugar dissolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD8A894689BC314597789E8E3DC29050', X'3428D9D171D1264F926DBDF57D4C45FD', X'3428D9D171D1264F926DBDF57D4C45FD', 3, NULL, NULL, 2, NULL, 0, 'Set aside to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B76E6C47BEA504D919ECC83A1D7AED7', X'C673A1C745650E468092124A8A9AE802', X'C673A1C745650E468092124A8A9AE802', 0, NULL, NULL, 2, NULL, 0, 'Place cream cheese in a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F300D51F60BB2E488381506BDDF8A170', X'C673A1C745650E468092124A8A9AE802', X'C673A1C745650E468092124A8A9AE802', 1, NULL, NULL, 2, NULL, 0, 'Blend until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D70CBFEDA6F07D4A9BD680967539B569', X'C673A1C745650E468092124A8A9AE802', X'C673A1C745650E468092124A8A9AE802', 2, NULL, NULL, 2, NULL, 0, 'Add powdered sugar and pulse until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0760D5AC08717940805783562140CB54', X'C673A1C745650E468092124A8A9AE802', X'C673A1C745650E468092124A8A9AE802', 3, NULL, NULL, 2, NULL, 0, 'Add heavy whipping cream and pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FAC53353B0EEE41A78F398DF8307AA3', X'C673A1C745650E468092124A8A9AE802', X'C673A1C745650E468092124A8A9AE802', 4, NULL, NULL, 2, NULL, 0, 'Pour in 2 tablespoons of the simple syrup and blend until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1FD6F92F5B4C1419C2D54375DD8084F', X'DB711EDFACFEE243AF53CBBDADE59A7E', X'DB711EDFACFEE243AF53CBBDADE59A7E', 0, NULL, NULL, 2, NULL, 0, 'Combine the blueberries with 2 tablespoons of the simple syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74289BAB6D704643B040C4110B34ACDC', X'DB711EDFACFEE243AF53CBBDADE59A7E', X'DB711EDFACFEE243AF53CBBDADE59A7E', 1, NULL, NULL, 2, NULL, 0, 'Pour into the bowl of a food processor along with 1/2 cup of the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'709A47C866A776469333517085D27891', X'DB711EDFACFEE243AF53CBBDADE59A7E', X'DB711EDFACFEE243AF53CBBDADE59A7E', 2, NULL, NULL, 2, NULL, 0, 'Pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C60F945A331E52489325B08DF4FAE335', X'DB711EDFACFEE243AF53CBBDADE59A7E', X'DB711EDFACFEE243AF53CBBDADE59A7E', 3, NULL, NULL, 2, NULL, 0, 'Remove about 1/3 of the blueberries for the purple layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CC4A40B23164741BF028B426FB5CD8D', X'DB711EDFACFEE243AF53CBBDADE59A7E', X'DB711EDFACFEE243AF53CBBDADE59A7E', 4, NULL, NULL, 2, NULL, 0, 'Color the remaining 2/3 blueberries using a few drops of blue food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAD29D6072098B49A1BED51F463585DA', X'8550FB972364AB42B4844947CEEE15F1', X'8550FB972364AB42B4844947CEEE15F1', 0, NULL, NULL, 2, NULL, 0, 'Puree the raspberries with 2 tablespoons of the simple syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E01EDC0C8CC8AA4EAD6A8AC76D37988A', X'8550FB972364AB42B4844947CEEE15F1', X'8550FB972364AB42B4844947CEEE15F1', 1, NULL, NULL, 2, NULL, 0, 'If desired, push the raspberries through a fine mesh sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DD68C9E3B392144850DC413B82CD921', X'8550FB972364AB42B4844947CEEE15F1', X'8550FB972364AB42B4844947CEEE15F1', 2, NULL, NULL, 2, NULL, 0, 'Pour back into the food processor and add 1/2 cup of the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B83BAA6841F05D40838A92AF4A142461', X'8550FB972364AB42B4844947CEEE15F1', X'8550FB972364AB42B4844947CEEE15F1', 3, NULL, NULL, 2, NULL, 0, 'Blend until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D831510B30E6048B7ED23776C363D9D', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 0, NULL, NULL, 2, NULL, 0, 'Drizzle some of the three colors of fruit puree into a popsicle mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C630D8223476E6428FB6D6E439E6616A', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 1, NULL, NULL, 2, NULL, 0, 'Add some of the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'182DD01C2172D942972317F10DDA3952', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 2, NULL, NULL, 2, NULL, 0, 'Swirl the mold and use a skewer to swirl the colors a bit more if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE985891DF85174B8CB22434FE3EC54E', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 3, NULL, NULL, 2, NULL, 0, 'Repeat until you''ve filled the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99DFAA19141AE044A0828ABC5973B500', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 4, NULL, NULL, 2, NULL, 0, 'Add the stick and lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A2DD9184248294C8576BAB5BACA76E3', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 5, NULL, NULL, 2, NULL, 0, 'Repeat filling all 9 popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EA731939BA5094191771D406D96DBB7', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 6, NULL, NULL, 2, NULL, 0, 'Freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'659D28E166FF7C408DFB28CD616D63A7', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 7, NULL, NULL, 2, NULL, 0, 'To remove the pops from the molds, either dip in hot water or run under hot water in your sink for about 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8108DD8CFBD40541B5D2761839F84A51', X'F89F930798D00C4A8BAE54CDE10BB758', X'F89F930798D00C4A8BAE54CDE10BB758', 8, NULL, NULL, 2, NULL, 0, 'Them slide the pops out of the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD590749AB99E141AF7A0BEFED786B5A', X'3169DF789C42A6488F62891992487389', X'3169DF789C42A6488F62891992487389', 0, NULL, NULL, 2, NULL, 0, 'Combine about a 1/2 teaspoon of water with about a teaspoon of silver luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F0F87E27406384FABBE12EDBA71E78E', X'3169DF789C42A6488F62891992487389', X'3169DF789C42A6488F62891992487389', 1, NULL, NULL, 2, NULL, 0, 'You want this to be thin enough to splatter and thick enough to be metallic in color.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A94B6FF8C0AB364CAC88F197E2586D78', X'3169DF789C42A6488F62891992487389', X'3169DF789C42A6488F62891992487389', 2, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush or pastry brush to flick tiny dots of the luster dust onto a popsicle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E074FDA13D32047A42FE00F25078125', X'3169DF789C42A6488F62891992487389', X'3169DF789C42A6488F62891992487389', 3, NULL, NULL, 2, NULL, 0, 'Brush tiny dots of the wet luster dust onto a popsicle and immediately attach a silver star sprinkle. They will stick as the water freezes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3531D0AD160B9D4DA7AFB9C95C393480', X'3169DF789C42A6488F62891992487389', X'3169DF789C42A6488F62891992487389', 4, NULL, NULL, 2, NULL, 0, 'Add some dry luster dust too, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC1249AFD149434496E2205A8D7643FD', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26DC201223B6F64EBF663CDCAF253A1E', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 1, NULL, NULL, 2, NULL, 0, 'Marbleize white modeling chocolate with half of the brown modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21ADA02CCFF5FA4AB95B8CDD9FE2E054', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 2, NULL, NULL, 2, NULL, 0, 'Roll out to about 1/16th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA310AC98113394ABCDC9BEA832FCFC7', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 3, NULL, NULL, 2, NULL, 0, 'Cut 24 small rounds using a 1 inch round (fluted) cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6314940C2349B94992258EF2406C3E59', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 4, NULL, NULL, 2, NULL, 0, 'Use a pick tool to draw on whiskers, feathering the edges of the circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA39475CC5E5FC4BA22D4786BD4F4530', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 5, NULL, NULL, 2, NULL, 0, 'Roll out the remaining brown modeling chocolate to about 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15054E7DD1F7DC499C52DA52475F959A', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 6, NULL, NULL, 2, NULL, 0, 'Cut out 24 small 1/4 inch rounds using a medium round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9E3A7907A982D46B1C20A9BF3DB9D73', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 7, NULL, NULL, 2, NULL, 0, 'Cut each circle into a 3/4 moon, using the plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B00F3582F583E54889B0BDC57A8A0B65', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 8, NULL, NULL, 2, NULL, 0, 'Roll out half of the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B7EFE93E9F9C341910E351E8816C0EF', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 9, NULL, NULL, 2, NULL, 0, 'Cut out 24 small 1/4 inch circles using a medium round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'740B05FA822CFF42B692673BD4EDC7EA', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 10, NULL, NULL, 2, NULL, 0, 'Pinch off jelly bean size pieces of the remaining black modeling chocolate and roll it into oval noses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E99058BAAD100444A584E5C8A3B96CE2', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 11, NULL, NULL, 2, NULL, 0, 'Use piping gel as `glue` to attach two whiskers, a nose, and two eyes to the front of each Reese''s cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1322D0C6EA533448B2A747F1710B70F', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 12, NULL, NULL, 2, NULL, 0, 'Attach two of the brown moon shaped pieces to either side of the Reese''s cup for the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDA99AE5AC15CA4FB36101E6DE26A05E', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 13, NULL, NULL, 2, NULL, 0, 'Set the decorated Reese''s Cup otter on the upper half of the frosting swirl on a cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C47C086390D3FE45994DDD0DC63CC843', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 14, NULL, NULL, 2, NULL, 0, 'Print out a wave cupcake wrapper, cut it out, then wrap it around your cupcake, taping the ends together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81D03B3AD424894E938D080AF6D004C7', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 15, NULL, NULL, 2, NULL, 0, 'Press two Whoppers into the frosting just above the top of the waves and set a candy shell in between.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2C2BA95CE16404C82583CD3C2647202', X'178E03E565934F4AB6BA7BDF57C3410D', X'178E03E565934F4AB6BA7BDF57C3410D', 16, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 12 Sea Otter Cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'107FE75FCABCB049B38BED9B0319B145', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees. Line baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F29952B8CE4BD44B09BF19F28CD4601', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 1, NULL, NULL, 2, NULL, 0, 'Sift the cookie mix into a large mixing bowl then add the flour and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A335570B6FD4248843346AF5B439DC9', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl gently whisk together egg, A & W Root Beer Float Dessert Topper, and red and green food coloring, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'174B8ACBDE6502418B26BF1DB36D4829', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 3, NULL, NULL, 2, NULL, 0, 'Add to cookie mix and stir until a soft dough forms.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0A2FCDE6FAD6B448D2F44B85DCCD6F7', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 4, NULL, NULL, 2, NULL, 0, 'Dust work surface liberally with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BC139277EBD7548B5390BA2B1BDF3F8', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 5, NULL, NULL, 2, NULL, 0, 'Roll dough to 1/8-1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'197FD935DA9E8645A392BE4A121B2C54', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 6, NULL, NULL, 2, NULL, 0, 'Cut dough using a 3` round cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8279CF26198A6459649AC233C8161EC', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 7, NULL, NULL, 2, NULL, 0, 'Re-roll dough as necessary to create 24 cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB86C52C1D59954183F6B9368882DFE3', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 8, NULL, NULL, 2, NULL, 0, 'Place cookies, about an inch apart on the prepared cookie sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'290C2729D8BCD940A682CDF2C069FBAD', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 9, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until the edges are set (slightly firm to the touch.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7E1B5F792C7D248BC4AB917DA51F1C7', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 10, NULL, NULL, 2, NULL, 0, 'Cool completely before proceeding.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34B4021FEE762F489D7BB21AADCCA3A3', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 11, NULL, NULL, 2, NULL, 0, 'Scoop a heaping 1/4 cup ice cream onto a cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E0BEE5576B43D4CA86B3115CD4F2692', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 12, NULL, NULL, 2, NULL, 0, 'Top with another cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'663721B19A440D4F869E9CAB36D0C2DD', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 13, NULL, NULL, 2, NULL, 0, 'Repeat to make 12 Root Beer Float Ice Cream Sandwiches.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7615DED3FA69F240BD6ED9D39641328A', X'9A2DF3C422B0A9479EE5B53241CC17C4', X'9A2DF3C422B0A9479EE5B53241CC17C4', 14, NULL, NULL, 2, NULL, 0, 'Eat immediately, or wrap each sandwich in plastic wrap and freeze until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1449DF299EE1BD46837DE28A54FDE515', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 0, NULL, NULL, 2, NULL, 0, 'Fill a mini ice cream cone with some chocolate ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5A95B64E8AC4E4E836F51BC0353A6B5', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 1, NULL, NULL, 2, NULL, 0, 'Top the cone with a nice rounded scoop of ice cream then freeze for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46CBCA1A9E379149AB43292BD05AFE8B', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 2, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to draw two nostrils and one smile onto a Peanut Butter Candy Melt wafer to create the monkeys snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D18ABAE190E0E438A3C3E9C7E312C8F', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 3, NULL, NULL, 2, NULL, 0, 'Remove the ice cream cone from the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F6FC3D43BC31644B2C06D42DB749A6F', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 4, NULL, NULL, 2, NULL, 0, 'Hold the two plain peanut butter candy melts in opposite hands.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD88B9732E197749BF2506E914362900', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 5, NULL, NULL, 2, NULL, 0, 'Press the candy melts simultaneously into the sides of the ice cream cone, creating the monkeys ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29562A70E2D02D47B8CBE963C1B8E557', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 6, NULL, NULL, 2, NULL, 0, 'Press the peanut butter candy melt snout onto the lower half of the front of the ice cream cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69B2BB89F459EF479ADDA9D7D8FB4220', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 7, NULL, NULL, 2, NULL, 0, 'Press two candy eyes onto the ice cream just above the snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AADA36F4F9A52458EF26EAC53123E05', X'6D87B92ED4EC414AA73E7CA31AB30882', X'6D87B92ED4EC414AA73E7CA31AB30882', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A8269F95B59434097AA45B1824BBCCD', X'53C1DCF71B2F124BA77026B1A69E7520', X'53C1DCF71B2F124BA77026B1A69E7520', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFD88545E818BA4E9995F6279666EA26', X'53C1DCF71B2F124BA77026B1A69E7520', X'53C1DCF71B2F124BA77026B1A69E7520', 1, NULL, NULL, 2, NULL, 0, 'Spray 10 half-pint mason jars with non-stick baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D77DA6E85BC1B49ADBB03767AE44EF8', X'53C1DCF71B2F124BA77026B1A69E7520', X'53C1DCF71B2F124BA77026B1A69E7520', 2, NULL, NULL, 2, NULL, 0, 'Make the cake mix using the instructions on the package swapping out root beer for water and adding 2 tablespoons of root beer dessert topper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC601FF023E9654CBED4BE13700DB699', X'53C1DCF71B2F124BA77026B1A69E7520', X'53C1DCF71B2F124BA77026B1A69E7520', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the batter among the 10 Mason jars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'404D99991233C14CAB0D6DA04C9C69CA', X'53C1DCF71B2F124BA77026B1A69E7520', X'53C1DCF71B2F124BA77026B1A69E7520', 4, NULL, NULL, 2, NULL, 0, 'Place the jars on a baking sheet and bake for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'890CE8AC99D15A4CA698E9AA18E9F3E3', X'53C1DCF71B2F124BA77026B1A69E7520', X'53C1DCF71B2F124BA77026B1A69E7520', 5, NULL, NULL, 2, NULL, 0, 'Allow the cakes to cool for 10 minutes, then turn the jars upside down, allowing the cakes to fall out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'437127C03EE9654DA0A575EF591114C9', X'53C1DCF71B2F124BA77026B1A69E7520', X'53C1DCF71B2F124BA77026B1A69E7520', 6, NULL, NULL, 2, NULL, 0, 'Allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE193EDBD006514B92DB1A2DD367BB69', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 0, NULL, NULL, 2, NULL, 0, 'Heat the ice cream in the microwave just until it melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2ADDDDB252A8F45BFCCEAA586444EC2', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 1, NULL, NULL, 2, NULL, 0, 'Measure out 1/3 cup of the melted ice cream (that''s all you''ll need).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'081DAA8BFF7A174E952E21EA801FA615', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 2, NULL, NULL, 2, NULL, 0, 'Pour in the white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69BE395D8794C349A5E5A589553FDFF7', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 3, NULL, NULL, 2, NULL, 0, 'Place the bowl back in the microwave and heat on high for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D61ED4A3E4053C49AE2193C03EA05742', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 4, NULL, NULL, 2, NULL, 0, 'Allow the bowl to sit in the microwave for about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7ACFCFD4498F3045B593EE8793AD0E26', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 5, NULL, NULL, 2, NULL, 0, 'Remove it from the microwave and stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53FE13DA371FF94482E2427407071182', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 6, NULL, NULL, 2, NULL, 0, 'Set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10BE210E84067349A5B9092DC8B5F8D3', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 7, NULL, NULL, 2, NULL, 0, 'Cut the domed tops off of each cake so that they are all 3 inches tall.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A29F44CB2E13E4598EB040AD1867690', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 8, NULL, NULL, 2, NULL, 0, 'Use a cupcake plunger/corer, apple corer, or a knife to remove some of the center of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38C3059FFB94F443B5565D115AD0DD37', X'E23FC5F1C7F40B43887DB55BF77E68A8', X'E23FC5F1C7F40B43887DB55BF77E68A8', 9, NULL, NULL, 2, NULL, 0, 'Fill each well with vanilla ice cream ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F0A88DB80631D4FA639881F27E0B21E', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 0, NULL, NULL, 2, NULL, 0, 'Melt 24 ounces of the white confectionery coating/candy melts in the microwave or in a double boiler.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC3A0B5873B96B478090535A880C46F7', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 1, NULL, NULL, 2, NULL, 0, 'If using the microwave, heat on high for 25 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D1527F990CF6447BE9CE59763206353', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 2, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds, then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A9EBE1D4F73404089F5976BC8086290', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 3, NULL, NULL, 2, NULL, 0, 'Heat for 15 seconds then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB357CD92EA4EA458FAB95A13ABFCEB1', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10-second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A972D75F14BC7E4E898140A8EB693885', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 5, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool to about 91 degrees Fahrenheit, stirring and scraping the sides of the bowl often.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A04E52E70685544BA556EE6F6C9E9E3', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 6, NULL, NULL, 2, NULL, 0, 'Pour in the A&W Root Beer Float Dessert Topper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED7343EF1A18374698808B2547C1BBA2', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 7, NULL, NULL, 2, NULL, 0, 'Stir until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0286E948F2708440B8EC0017F3DBDBE5', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 8, NULL, NULL, 2, NULL, 0, 'The mixture will become thick very quickly, so scrape the bottom of the bowl to incorporate all of the melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB2636FFD847804EAB9EEAB7C672FAB8', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 9, NULL, NULL, 2, NULL, 0, 'Wrap it in a zip-top bag or plastic wrap and allow it to sit at room temperature for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'880E2DB05A60E84E8137636B76429A4D', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 10, NULL, NULL, 2, NULL, 0, 'When you are ready to use it, knead it until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6C717D37D0D954E96C5AADC8EF31EFD', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 11, NULL, NULL, 2, NULL, 0, 'Roll out the root beer modeling chocolate on a non-stick baking mat (or on a cutting board lightly dusted with powdered sugar or cornstarch) to about 3/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'537DBEB0879F3D4DA1878DC5530B8E2F', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 12, NULL, NULL, 2, NULL, 0, 'Cut ten rectangles that are 3 1/4 inches wide by about 8 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17AFF69728D71C46A393F6DE70EA09E3', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 13, NULL, NULL, 2, NULL, 0, 'Use a wooden dowel or straw to press indentations about 3/4 inch apart all across the brown strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1010918F961487409531EEF0FCD3278D', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 14, NULL, NULL, 2, NULL, 0, 'Leave about a 1/2 border all along one edge. That will become the bottom of your root beer mug.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'507A4A7600CDB54CB9A2642D0A2E1528', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 15, NULL, NULL, 2, NULL, 0, 'Flip the modeling chocolate rectangles over and spread on a thin layer of vanilla ganache, leaving 1/2 inch border all around.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92C6D65F926A154096DA7780DB40E028', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 16, NULL, NULL, 2, NULL, 0, 'Then wrap that, ganache side against the cake, around a cake, cutting off excess modeling chocolate so the two seams match up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E4511A051046D4FA92C04BDE91EBE61', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 17, NULL, NULL, 2, NULL, 0, 'Rub some water along both ends and press the two seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CABF1A4A21D3AB45BCFF74582E727381', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 18, NULL, NULL, 2, NULL, 0, 'Let the wrapped cakes sit for about 30 minutes, then spoon some vanilla ice cream ganache over the cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D387F640620FA44B2CC4F19D6217EA7', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 19, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of the white modeling chocolate, roll into logs and bend to form handles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79C0AC167634AB4CB42AC0DD5B51819E', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 20, NULL, NULL, 2, NULL, 0, 'Allow the handles to dry for at least 15 minutes, then use some clear piping gel, water, or melted white chocolate to attach the handles to the mugs and set something underneath to prop them up until they dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0561C3E24B11643A63AEE4CC26DFBBB', X'3829A365EB45DF4AA8E15CBC0F1771EE', X'3829A365EB45DF4AA8E15CBC0F1771EE', 21, NULL, NULL, 2, NULL, 0, 'Just before serving, spoon whipped topping over the cakes, allowing some to drip down the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4ED4E69B03B73048AFA8FCD94DEBDBE9', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 0, NULL, NULL, 2, NULL, 0, 'Pour egg yolks and sugar into a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E76EEBFB348231439AB20B3E83359520', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 1, NULL, NULL, 2, NULL, 0, 'Beat with an electric mixer on high speed until fluffy and pale yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA4488E028219C4482AE69E27B64AC98', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 2, NULL, NULL, 2, NULL, 0, 'Heat cashew milk in a medium saucepan over medium heat until bubbles form.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F11C0CBF1F63D54C979184171DEE6A3C', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in chocolate until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89AC2991A0162C4E9D12E9C8EC6F873C', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 4, NULL, NULL, 2, NULL, 0, 'Pour 1/4 cup of chocolate into whipped eggs, stirring constantly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76F1C5FDEF23B440A024F3AB19FD0F2C', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 5, NULL, NULL, 2, NULL, 0, 'Continue stirring and slowly pour in the remaining chocolate milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B2C08C2DCB8DE4ABBD6ABDB08218345', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 6, NULL, NULL, 2, NULL, 0, 'Pour back into the saucepan and return to medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50F490B6FD7E2B44A5FE99D7D5C3771A', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 7, NULL, NULL, 2, NULL, 0, 'Stir constantly until thickened and mixture reaches 180 - 185 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F3FB26A2EE64C489A8881CE6CA88ACD', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 8, NULL, NULL, 2, NULL, 0, 'Pour through a fine-mesh sieve into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B24E256A27AD6544B3CD91D330909ED1', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 9, NULL, NULL, 2, NULL, 0, 'Chill until very cold, at least 3 hours, preferably overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A017F645AB37244C8C65D9BEBBA265A3', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 10, NULL, NULL, 2, NULL, 0, 'Pour into prepared ice cream maker and follow manufacturer''s instructions to chill ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A592145659162C43B18ADF04ECE400FB', X'C85E5004AD6A2C45BEB7A5A4024449BD', X'C85E5004AD6A2C45BEB7A5A4024449BD', 11, NULL, NULL, 2, NULL, 0, 'Scoop into a freezer safe container and freeze until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1527A43A73452D40B196184E1638835C', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 0, NULL, NULL, 2, NULL, 0, 'Ears - Flatten fudge rolls, cut into 1 3/4 inch teardrops. Stretch the pointed ends and bend slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'650D3DAE9E22A24997C4040B8E36B8A0', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 1, NULL, NULL, 2, NULL, 0, 'Tongues - Flatten red taffy roll, cut into 3/4 inch teardrops, stretch the pointed ends, and indent center with a toothpick.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'549BB4C6CF831942A6AAEE90D44CF603', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 2, NULL, NULL, 2, NULL, 0, 'Spotted Ears - flatted vanilla rolls, cut into 1 3/4 inch teardrops. Stretch the pointed ends and bend slightly. Flatten chocolate fudge rolls cut small dots, Attach with water to a white teardrop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7FEB08A58A4774F94B68B2C1CA1D47D', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 3, NULL, NULL, 2, NULL, 0, 'Fill ice cream cones with ice cream. Add one large scoop on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B16299176770F348B364F21BA31BD29D', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 4, NULL, NULL, 2, NULL, 0, 'Add a small scoop to the base of the large scoop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B02D1BB6CBD8554FAE6F0E4FA19D0199', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DDF94688A4EBF43BA3C2FA07F4E9B93', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 6, NULL, NULL, 2, NULL, 0, 'Add two candy eyes, one chocolate chip nose, two candy ears and one tongue to each cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A22B9122D55BB4BA68764F8EA8CDCBB', X'FB90BB1F985F4D43B15238491B747BA3', X'FB90BB1F985F4D43B15238491B747BA3', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes, then serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5F8E46C62A83848AA41DA2CA223F238', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 0, NULL, NULL, 2, NULL, 0, 'In a mixing bowl cream together butter and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB3217A6108717418144DFAD3F76387E', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 1, NULL, NULL, 2, NULL, 0, 'Add the egg and mix until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'915E1AA1D388D643A6F57DB9A85E0D76', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla and almond extract and mix until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCA268AD2B88DD429DFE1A6122CB24D9', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 3, NULL, NULL, 2, NULL, 0, 'Mix flour and salt together and add it to the mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70A2395CBCF61F4BAFE9C4F96D0809DC', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 4, NULL, NULL, 2, NULL, 0, 'Mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E117FB01070EB0419BBD4AA521CE31DD', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 5, NULL, NULL, 2, NULL, 0, 'Place a piece of parchment paper on top of your cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EA2C13A54D0124B86B20014AE89AAF1', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 6, NULL, NULL, 2, NULL, 0, 'Roll your dough out to 3/8` thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE5407A5CCDD6A408B4F15760340BB40', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 7, NULL, NULL, 2, NULL, 0, 'Lay the butterfly template over the dough and cut out around it.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F2DF1C4DA699340A2CB4EC888F8CF9F', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 8, NULL, NULL, 2, NULL, 0, 'Remove excess dough and save for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'727A730094C8B1428420702E50E65775', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate butterfly cookie for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAF10B1BE5DADE45BB66D95AF2B58223', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 10, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat your oven to 375 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4611CBD4B6C51C44913FCB126687DC17', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 11, NULL, NULL, 2, NULL, 0, 'Bake for 16-22 minutes until golden brown around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73BC15A773DF8145A654ED5EFDAFC79F', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 12, NULL, NULL, 2, NULL, 0, 'Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BE65DC554AEED4995CDAC85E147D7BC', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 13, NULL, NULL, 2, NULL, 0, 'Spread strawberry cream cheese over the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E9681FC0662C94697E08238C5134406', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 14, NULL, NULL, 2, NULL, 0, 'Top the cookie with diced fruit using green grapes');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10817D297B2BB143A57D709006C44523', X'61312C304136374BAC71531A9307AEFC', X'61312C304136374BAC71531A9307AEFC', 15, NULL, NULL, 2, NULL, 0, 'for the body, bananas for its eyes, pineapple, strawberries, peaches, and kiwi slices for the wings.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36E3E1EE5647D946B2463244744231DB', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A3E680B2D228C49B60A8C08A766989B', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 1, NULL, NULL, 2, NULL, 0, 'Spray two 9 inch springform pans with cooking spray then line bottoms with a circle of parchment paper or non-stick tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D5F56EC6A9E8E4D99E252BE2771B25A', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 2, NULL, NULL, 2, NULL, 0, 'Combine sugar cookie mix, flour and butter and mix until the butter breaks up into small pie size pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15560ED8589CFA48AB92CE87A9F2A414', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 3, NULL, NULL, 2, NULL, 0, 'Add the egg and mix until it forms a dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BCE958C89143145B08255F5A289466E', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 4, NULL, NULL, 2, NULL, 0, 'Divide dough in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0825AB3697E41748AA785B1E8D299C00', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 5, NULL, NULL, 2, NULL, 0, 'Pat one half of dough in the bottom of each pan, spreading dough so that it ends about 1/2 inch from the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E1CE658740F3045AA799C862689EBCB', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 6, NULL, NULL, 2, NULL, 0, 'Bake for 14-18 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C0AC914EDAF6B4F8E6C5F645CC4BC16', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 7, NULL, NULL, 2, NULL, 0, 'Cool in pan for 10 minutes, then remove and set on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15BBC7BABFF8D84EB66B1AF279FCDC76', X'EA950634E4770742BEC3901ECE5BC376', X'EA950634E4770742BEC3901ECE5BC376', 8, NULL, NULL, 2, NULL, 0, 'Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4673E83FB0399F45ACB8342E136D3D26', X'50875F955B4A2F4BA358845AA729F294', X'50875F955B4A2F4BA358845AA729F294', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48312F7BC9F63A478047A50EA0495569', X'50875F955B4A2F4BA358845AA729F294', X'50875F955B4A2F4BA358845AA729F294', 1, NULL, NULL, 2, NULL, 0, 'Add marshmallow fluff and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51F5D471CAC85C42BE7E367014FF3BAF', X'50875F955B4A2F4BA358845AA729F294', X'50875F955B4A2F4BA358845AA729F294', 2, NULL, NULL, 2, NULL, 0, 'Add whipped topping and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4613592D939EA4F9F4B169A0B36BF88', X'50875F955B4A2F4BA358845AA729F294', X'50875F955B4A2F4BA358845AA729F294', 3, NULL, NULL, 2, NULL, 0, 'Equally divide and spread over top of cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E03524146768BB4CA4DFD2B5BB0492D5', X'50875F955B4A2F4BA358845AA729F294', X'50875F955B4A2F4BA358845AA729F294', 4, NULL, NULL, 2, NULL, 0, 'Cut cookies in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FE929E845480B4C95E88A91D6F72096', X'FB9C5281CCAC6D45AC41CE67121FCA9C', X'FB9C5281CCAC6D45AC41CE67121FCA9C', 0, NULL, NULL, 2, NULL, 0, 'Peel and cut kiwi into thin slices. Cut slices in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34BBD82362FDA243A5815EF1E6180465', X'FB9C5281CCAC6D45AC41CE67121FCA9C', X'FB9C5281CCAC6D45AC41CE67121FCA9C', 1, NULL, NULL, 2, NULL, 0, 'Arrange overlapping slices of kiwi along the curved edge of each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB7872E38237154DABE738A4A24A5821', X'FB9C5281CCAC6D45AC41CE67121FCA9C', X'FB9C5281CCAC6D45AC41CE67121FCA9C', 2, NULL, NULL, 2, NULL, 0, 'Wash and dry strawberries, then carve off the outer edge, keeping just the bright red fruit for the pizza.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03F59DD5076041448C53C9E7106995AB', X'FB9C5281CCAC6D45AC41CE67121FCA9C', X'FB9C5281CCAC6D45AC41CE67121FCA9C', 3, NULL, NULL, 2, NULL, 0, 'Cut slices into small pieces and sprinkle over the cream cheese fluff topped cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3304749372133499B64CD6C7513E52D', X'FB9C5281CCAC6D45AC41CE67121FCA9C', X'FB9C5281CCAC6D45AC41CE67121FCA9C', 4, NULL, NULL, 2, NULL, 0, 'Arrange 8 chocolate chips on each cookie to create watermelon seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FE82750C03D0F468C2A31CD31B253FB', X'FB9C5281CCAC6D45AC41CE67121FCA9C', X'FB9C5281CCAC6D45AC41CE67121FCA9C', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73888BB9788A694BA5A463AFA8258FC7', X'B174BA5CCC02A541A2543C5872969246', X'B174BA5CCC02A541A2543C5872969246', 0, NULL, NULL, 2, NULL, 0, 'Bake mini corn dogs on baking sheet according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE06A0FD94E3F24181C39A6B85FB9A79', X'CD610FEBA484EF4BB57175F96A2E41D4', X'CD610FEBA484EF4BB57175F96A2E41D4', 0, NULL, NULL, 2, NULL, 0, 'Line up 9 corn dogs widthwise into a squiggly line for a centipede.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC8BDA5647E9AF48AC650CF062390453', X'CD610FEBA484EF4BB57175F96A2E41D4', X'CD610FEBA484EF4BB57175F96A2E41D4', 1, NULL, NULL, 2, NULL, 0, 'Press two long chow mein noodles (or potato sticks) into the first corn dog to create antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'389764046C017C409689538102B1EDBA', X'CD610FEBA484EF4BB57175F96A2E41D4', X'CD610FEBA484EF4BB57175F96A2E41D4', 2, NULL, NULL, 2, NULL, 0, 'Press two long, but shorter chow mein noodles (or potato sticks) into the last corn dog to create the back legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DE4095FAE90EA45904DB3A043E89772', X'CD610FEBA484EF4BB57175F96A2E41D4', X'CD610FEBA484EF4BB57175F96A2E41D4', 3, NULL, NULL, 2, NULL, 0, 'Then on all remaining corn dogs, press one chow mein noodle (or potato stick) on either side, creating legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92E88323836A9D41B3FB29E02AD6917E', X'CD610FEBA484EF4BB57175F96A2E41D4', X'CD610FEBA484EF4BB57175F96A2E41D4', 4, NULL, NULL, 2, NULL, 0, 'If needed, place in a 200-degree oven to keep warm until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DD976C5C4C3744B95F489DF62AD8C05', X'CD610FEBA484EF4BB57175F96A2E41D4', X'CD610FEBA484EF4BB57175F96A2E41D4', 5, NULL, NULL, 2, NULL, 0, 'Before serving, add two ketchup eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAB0FF122670FE4F82670D15EB4BD16F', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', 0, NULL, NULL, 2, NULL, 0, 'Line up 4 or 5 corn dogs lengthwise so that each section is longer rather than wider.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10863AECED8278459D6AD918294771DF', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', 1, NULL, NULL, 2, NULL, 0, 'Press two long chow mein noodles (or potato sticks) into the first corn dog to create antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47C822E377163C49953320BCF3E10CDB', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', 2, NULL, NULL, 2, NULL, 0, 'Attach three chow mein noodle legs for each body segment and have them curved toward the ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91F82739819A5842B4974879EBF1924F', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', 3, NULL, NULL, 2, NULL, 0, 'If needed, place in a 200-degree oven to keep warm until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67C796F6D0B9C74688BA3B9925553106', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', X'11F798BCEC4E7A4CA05ABFA4F4390ED6', 4, NULL, NULL, 2, NULL, 0, 'Just before serving add ketchup eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21C97B1E5C30DA41A97A9905D643E5D2', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 0, NULL, NULL, 2, NULL, 0, 'Whisk the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8DB9CEE16DCD3438A32E23AA3FC0A67', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 1, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8DF3286B76A7F47AFCC84B89A077B96', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7570BAE8B5F0442B5AFE01CCBDCFE6D', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 3, NULL, NULL, 2, NULL, 0, 'Stir in the egg, vanilla, and almond or lemon extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88CB81D8ACFEAD438E622101E735C400', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 4, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'768728EBDAF03040841AB54CA98DA1CA', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 5, NULL, NULL, 2, NULL, 0, 'Flatten dough into a disc and wrap in plastic wrap or a large zip-top bag and refrigerate for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4B567DA5DB6FD4D80E01C0846607C76', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 6, NULL, NULL, 2, NULL, 0, 'When the dough has chilled, dust a cutting board or lightly dust a silicone baking mat and place 1/2 of the dough on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36B601F38D42774BB3075CF3992058C5', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 7, NULL, NULL, 2, NULL, 0, 'Roll out to 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64B86B79B8E32646ADB8E1A777B45B55', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 8, NULL, NULL, 2, NULL, 0, 'Cut out 5 petals using the 3 1/2 long tear drop cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C60382FB9844BB47B20F3A5BFAFC097F', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 9, NULL, NULL, 2, NULL, 0, 'Cut out one 1 1/2 inch circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BBF93FE96D0994C814F33288ECB0EA2', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 10, NULL, NULL, 2, NULL, 0, 'Then arrange the petals in a flower shape, so that their tips almost touch, and use the round cookie cutter to cut a circle in the middle of the tips to create room for the center to fit inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5AAA7F14CC8304BA4F48A17E3E9BC21', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 11, NULL, NULL, 2, NULL, 0, 'Cut out two leaves and one 6 1/2 inch long stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6BC8A514F7EE244B6BE6522BE1C9E46', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 12, NULL, NULL, 2, NULL, 0, 'Move all the pieces onto a parchment paper or silicone mat lined baking sheet, leaving an inch in between, and place in the refrigerator for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66D46597DA816F4884A205BC10BEE864', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 13, NULL, NULL, 2, NULL, 0, 'Roll out the other chilled piece of dough and cut out the same shapes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22C5E137303A344E812F9745F9ECFCF6', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 14, NULL, NULL, 2, NULL, 0, 'Blend the remaining dough together and roll it out. Cut out a hillside on which to place your flowers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D9C25A0737A8747B573263020986584', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 15, NULL, NULL, 2, NULL, 0, 'Use the excess dough to cut out another daisy or save for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E812014F24017B45A249CDEBACC2BDC3', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 16, NULL, NULL, 2, NULL, 0, 'While the cut-outs are chilling, preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7395C7039E0BC54692D7E8DBD3AF9B10', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 17, NULL, NULL, 2, NULL, 0, 'Bake 1 tray of cookies at a time for 8 minutes then rotate the pan and continue to bake for 3-4 minutes just until the tops of the cookies look dry and the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6C180379D86EC429B968FB8AABDE9B7', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 18, NULL, NULL, 2, NULL, 0, 'Remove and set the pan on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91B5243BC633144AB6DBD17CC44A8593', X'B13A510AA3B142469654288A7A446760', X'B13A510AA3B142469654288A7A446760', 19, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool for 5 minutes then remove them using a metal spatula and place them directly on the cooling rack to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'385665F8FE81EA44A080BADEB862F6E1', X'4FD887B5F7620745B8C1397878583C15', X'4FD887B5F7620745B8C1397878583C15', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese and 1/4 cup of the powdered sugar together until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE1731DCA77D12479EBC9F421FD7AC05', X'4FD887B5F7620745B8C1397878583C15', X'4FD887B5F7620745B8C1397878583C15', 1, NULL, NULL, 2, NULL, 0, 'In a clean bowl with clean beaters, beat the heavy whipping cream, the remaining 1/2 cup of powdered sugar, and the vanilla extract until you get stiff peaks. Do not over whip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAA3A9D89B63C549A53C5C5DD20946A7', X'4FD887B5F7620745B8C1397878583C15', X'4FD887B5F7620745B8C1397878583C15', 2, NULL, NULL, 2, NULL, 0, 'Stir 1/3 of the whipped cream into the cream cheese until the cream cheese is lighter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94537D49222BAF4DB192242CC4051BBD', X'4FD887B5F7620745B8C1397878583C15', X'4FD887B5F7620745B8C1397878583C15', 3, NULL, NULL, 2, NULL, 0, 'Fold in another 1/3 of the whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84526923233FA34988DE8C576F5C218E', X'4FD887B5F7620745B8C1397878583C15', X'4FD887B5F7620745B8C1397878583C15', 4, NULL, NULL, 2, NULL, 0, 'Then fold in the remaining 1/3 of the whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A15CD0F359026047881530CFAFB33A37', X'4FD887B5F7620745B8C1397878583C15', X'4FD887B5F7620745B8C1397878583C15', 5, NULL, NULL, 2, NULL, 0, 'Chill the cheesecake frosting for at least 30 minutes');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88200EBA3B71C645914205D23D34A80B', X'026A1CAF18F2CA4298136F392911A52D', X'026A1CAF18F2CA4298136F392911A52D', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7928B4E6ADB004E824A967F9E0FE1DD', X'026A1CAF18F2CA4298136F392911A52D', X'026A1CAF18F2CA4298136F392911A52D', 1, NULL, NULL, 2, NULL, 0, 'Add the marshmallow fluff and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DADC30F419577E4FB210AC4E3D91813B', X'026A1CAF18F2CA4298136F392911A52D', X'026A1CAF18F2CA4298136F392911A52D', 2, NULL, NULL, 2, NULL, 0, 'Then add the whipped topping and beat until nice and light.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B22895E1D5A034A84CB17BE4A5CCAAE', X'5409835087EF6A41989274D5C129F0FC', X'5409835087EF6A41989274D5C129F0FC', 0, NULL, NULL, 2, NULL, 0, 'Spread or pipe a 1/4 inch layer of cheesecake filling onto each individual cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD843D168C2D36449876470A75AFC3FF', X'5409835087EF6A41989274D5C129F0FC', X'5409835087EF6A41989274D5C129F0FC', 1, NULL, NULL, 2, NULL, 0, 'Arrange the cookies on a platter or cake board.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57AFA673E464164B9E07C0B8095FFFEA', X'5409835087EF6A41989274D5C129F0FC', X'5409835087EF6A41989274D5C129F0FC', 2, NULL, NULL, 2, NULL, 0, 'Then arrange raspberries, blueberries, or strawberry pieces over the petals of your daisies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97B8D0916CA4E146910716C5DA4F7214', X'5409835087EF6A41989274D5C129F0FC', X'5409835087EF6A41989274D5C129F0FC', 3, NULL, NULL, 2, NULL, 0, 'Top the small round cookie with pineapple, mango, or peach pieces');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B35E6E15846FC94497D582B3ECF03A9E', X'5409835087EF6A41989274D5C129F0FC', X'5409835087EF6A41989274D5C129F0FC', 4, NULL, NULL, 2, NULL, 0, 'Top the stem and leaves with green grapes and/or kiwi slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77B95227D3C2A2468E3819579915AE48', X'5409835087EF6A41989274D5C129F0FC', X'5409835087EF6A41989274D5C129F0FC', 5, NULL, NULL, 2, NULL, 0, 'Lay the hillside cookie over the bottom of the flower stems and top with kiwi slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87C794DAAF34F441A162EE4F5C2C8C80', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9837AE1E4FC5B34AA4B5C9B607903263', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 1, NULL, NULL, 2, NULL, 0, 'Butter the inside of 24 pig cavities in 2 silicone molds using melted butter, reserving 2 tablespoons of the melted butter to put into the pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6652561013303643A6C0380D1B2C20EA', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 2, NULL, NULL, 2, NULL, 0, 'In large bowl, whisk together the flour, salt, baking powder, and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30B71F6E53E7F34D84AD51E33E89FDB9', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the egg and buttermilk then add the sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DD8AF1D883C274A8C0655E215626DF6', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 4, NULL, NULL, 2, NULL, 0, 'Pour the liquid mixture into the flour mixture and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EA5638EAB9E7B40AFD48DBDD5588872', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 5, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons melted butter and mix until blended but with lumps remaining.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4AD5BB52AA64B438D3BB4BC8B24499D', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 6, NULL, NULL, 2, NULL, 0, 'Pour batter into a squeeze bottle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C9156D3AB5B5B4B9FA00C7E1F3420D1', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 7, NULL, NULL, 2, NULL, 0, 'Squeeze batter into a pig cavity in the silicone mold filling it about 3/4''s full.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFB8DD32194A3A448BCC4B99F96E2DA3', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 8, NULL, NULL, 2, NULL, 0, 'Press a Little Smokie into the pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A22B1F351470EA4798E0748850A88A73', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 9, NULL, NULL, 2, NULL, 0, 'Cover the little sausage with more pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12CDAB7C1BBE6F4DB8CA37B572D0480F', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 10, NULL, NULL, 2, NULL, 0, 'Repeat, filling up all 24 pig cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CA0A2568834BB4DA38DEAEA4C197E51', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 11, NULL, NULL, 2, NULL, 0, 'Bake for 14-18 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B858FEDF330B48458B7DDED0C90E401D', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 12, NULL, NULL, 2, NULL, 0, 'Remove and allow the pigs to cool for a few minutes before removing from the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8669BF96AE98E4BB6656CD872AD575F', X'DEE127FD5D73DB479B78431346D0054B', X'DEE127FD5D73DB479B78431346D0054B', 13, NULL, NULL, 2, NULL, 0, 'Serve with your favorite dipping sauces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'114F598BE6E5834B8706637D88732F5F', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0781DF5BB1071F418E952DDD3F48CC89', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 1, NULL, NULL, 2, NULL, 0, 'Mix together brownie mix, water, oil, and eggs just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91507C0BF98D7E4D91DFB9B430C5F3EA', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 2, NULL, NULL, 2, NULL, 0, 'Pour into a non-stick tin foil or parchment paper lined 9x13 inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46AB88DFF873784BB89F7916DB66BB29', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 3, NULL, NULL, 2, NULL, 0, 'Bake for 18-22 minutes until the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC45A9C73E72474D85A1BB6DFBB912A7', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and cool completely for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E6935198FC4D142B922C0D12E61007E', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 5, NULL, NULL, 2, NULL, 0, 'Remove brownie from pan and set on a cake board or cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0535DFB86E97004DBB7C8B6FC288CB96', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 6, NULL, NULL, 2, NULL, 0, 'Spread Pillsbury Jif Peanut Butter Frosting over about 1/2 of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4322FF2DE5A7E64DA02168F037ECD7E8', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle cookie crumbs over the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'480CAD61AECD3E4FA1F65F9815ECEFC8', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 8, NULL, NULL, 2, NULL, 0, 'Spread Pillsbury Funfetti Aqua Blue Vanilla Frosting over the remaining cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6460892C217094F8FB0BF7A2BA718E1', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 9, NULL, NULL, 2, NULL, 0, 'Add small dollops of Pillsbury Creamy Supreme Vanilla Frosting over the blue frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB7556759655BF4087BAA6F1C4A65CD6', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 10, NULL, NULL, 2, NULL, 0, 'Use an offset spatula or spoon to swirl the blue and white frosting together making the frosting look like ocean waves with some peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2966D537BA93534D8F92171EBD8C3795', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 11, NULL, NULL, 2, NULL, 0, 'If desired, crafts some candy crabs (see the video and tutorial above for instructions) and set them on the sandy beach.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7872FFA8F1C35343B28DC7DD3634E69B', X'9D8273923CFD2B459E405D7CBC49AFB7', X'9D8273923CFD2B459E405D7CBC49AFB7', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'126251BFE552D942B1F24DAA9BBFE889', X'FBAFE09665ABEF439973F4F477EF16B1', X'FBAFE09665ABEF439973F4F477EF16B1', 0, NULL, NULL, 2, NULL, 0, 'Grease the bottom and sides of a 9` x 13` with butter or cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E2C237DA2080646ADBCE049F895CA3A', X'FBAFE09665ABEF439973F4F477EF16B1', X'FBAFE09665ABEF439973F4F477EF16B1', 1, NULL, NULL, 2, NULL, 0, 'Combine the powdered sugar and corn starch and dust the inside of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54A2FE167D52354F8FCE96822A9B8BBD', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over 1/2 cup of water in the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D4C74BCFA009244833CA0E74870C1A9', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 1, NULL, NULL, 2, NULL, 0, 'Let sit 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F98823100402DD4282F52566305A993F', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 2, NULL, NULL, 2, NULL, 0, 'Stir together remaining water, sugar, corn syrup and salt in a small saucepan set over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14C2154BDF22024CA9E96C9E92442971', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 3, NULL, NULL, 2, NULL, 0, 'Cook, covered for 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B228D7D16DE1324B91072D5F1AD09083', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 4, NULL, NULL, 2, NULL, 0, 'Remove lid and cook, without stirring until the mixture reaches 240 degrees Fahrenheit (about 7-8 minutes.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC42F2ED33D3844F8E143280956C0967', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 5, NULL, NULL, 2, NULL, 0, 'Slowly pour hot liquid into the gelatin with the mixer turned on low speed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB9534F1151D944DBB35B105C1B1D099', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 6, NULL, NULL, 2, NULL, 0, 'Increase mixer speed to high and whip until the marshmallow becomes very thick and cools to a lukewarm temperature, about 12-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FC33E421D4470408CBCD4F70335BF93', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 7, NULL, NULL, 2, NULL, 0, 'Add the vanilla and whip just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF08FD78B499B84EA0D25AC92D2D9BC6', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 8, NULL, NULL, 2, NULL, 0, 'Spread the marshmallow into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E8BE86EE63CDC4C9C50F36E18C2A306', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 9, NULL, NULL, 2, NULL, 0, 'Dust top with powdered sugar/corn starch.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'558BB915C51BE342BBB8E21F8EF00C2A', X'C752B93B73F92E49807B3EDA005A8C8B', X'C752B93B73F92E49807B3EDA005A8C8B', 10, NULL, NULL, 2, NULL, 0, 'Let cool for 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2938C585CE23394BB81680E26C09CC6D', X'A5F3EC0376949A438AD3EC0FC1FF5454', X'A5F3EC0376949A438AD3EC0FC1FF5454', 0, NULL, NULL, 2, NULL, 0, 'Turn out onto a cutting board, cut into star shapes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CDC3A26FF7C6E4EB9F39520D3B9F4BF', X'A5F3EC0376949A438AD3EC0FC1FF5454', X'A5F3EC0376949A438AD3EC0FC1FF5454', 1, NULL, NULL, 2, NULL, 0, 'Dip in melted chocolate, attach two candy eyes, and coat in crushed graham crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE25D8C72AD3004BB23B68A991B8E837', X'A5F3EC0376949A438AD3EC0FC1FF5454', X'A5F3EC0376949A438AD3EC0FC1FF5454', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet and allow the chocolate to harden before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F4FD89ABDD9A14BA02A02A41D900336', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 0, NULL, NULL, 2, NULL, 0, 'Pour the popped popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D749B6A02BD6E4CB7FE1C9C83E3AC50', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 1, NULL, NULL, 2, NULL, 0, 'Stir in creamy peanut butter and a pinch of salt into the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24D4BC43381EE64CB43054DABBC3784B', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 2, NULL, NULL, 2, NULL, 0, 'Reserve 2 tablespoons of peanut butter chocolate mixture for later use.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCBA31EFA9B42041B3F230223426ABC1', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 3, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate over the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D6DB39C0069B540955B65084A481EB6', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 4, NULL, NULL, 2, NULL, 0, 'Toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64478DA8DB98654DB4F0889AD93BD736', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 5, NULL, NULL, 2, NULL, 0, 'Spread into an even layer on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFC2CD6E3A0B9E4991D861568D67AF0C', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle with cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1D402377D1138439BDBDD76DC9987C2', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9567917337FB68459AB2180F1BD6FAE2', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 8, NULL, NULL, 2, NULL, 0, 'Remove and break popcorn into chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'065F81275B69364D942EBA96A97101B8', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 9, NULL, NULL, 2, NULL, 0, 'Reheat the reserved peanut butter chocolate then use it as glue to attach chocolate shells to the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC44E30860A8F24EB301C47294D44D51', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 10, NULL, NULL, 2, NULL, 0, 'Store in an airtight container until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58B9B7B245413F4B87B14BC6DF733CD2', X'6FB22C3D7134AF42B648005B1AD7F9A3', X'6FB22C3D7134AF42B648005B1AD7F9A3', 11, NULL, NULL, 2, NULL, 0, '*See melting instructions on Hungry Happenings'' Chocolate Making Tips Page');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9378657DC447F642A9C774BA7E3BCE9A', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C76A2EBE7F4ABD4A972F793E173DA0A5', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine butter and sugar. Cream until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE67A04B01807A4B9EB4DE0BFA20C078', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 2, NULL, NULL, 2, NULL, 0, 'Mix in vanilla and eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5D20098A3D6CA428080A91B1A9AAC99', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 3, NULL, NULL, 2, NULL, 0, 'Combine cocoa powder, baking powder, salt, and flour. Stir in to wet ingredients and mix just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71B6BE32F64E5745B0A68519634EDF90', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 4, NULL, NULL, 2, NULL, 0, 'Dust silicone mat or parchment paper lightly with cocoa power.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23E111441B4F12489C6E2E51775B5CC3', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 5, NULL, NULL, 2, NULL, 0, 'Roll out dough to about 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B677E3AFD9DA34995E71CCE2D99E73B', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 6, NULL, NULL, 2, NULL, 0, 'Set the paper strawberry template over the tough and use a sharp knife to cut out around the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0D73D998738C84894C80B632C5A7F82', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 7, NULL, NULL, 2, NULL, 0, 'Remove the template and remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B59B8AA31C25454EB55A92504EF0FD68', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 8, NULL, NULL, 2, NULL, 0, 'Re-roll remaining dough and repeat cutting out a second strawberry shaped crust on another silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6A32F690C440542A4D295FD15D2BB88', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 9, NULL, NULL, 2, NULL, 0, 'Freeze strawberry shaped cookie dough for 10 minutes .');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1524E789DF97AA4FB2E9B1C133C638F3', X'B6F14B5ADFE94B41AAB53744A9211F43', X'B6F14B5ADFE94B41AAB53744A9211F43', 10, NULL, NULL, 2, NULL, 0, 'Bake for 18-22 minutes until the top of the cookie no longer looks wet and the edges feel set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D6680B335DB5D439A4C2834D65E9C40', X'F91616BBF979F74FABA3B9825BF04F2A', X'F91616BBF979F74FABA3B9825BF04F2A', 0, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23D737490A93244BB3A50918CE9BCAF7', X'F91616BBF979F74FABA3B9825BF04F2A', X'F91616BBF979F74FABA3B9825BF04F2A', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05ECF1DA4D701D4A91D521BC23A156A3', X'F91616BBF979F74FABA3B9825BF04F2A', X'F91616BBF979F74FABA3B9825BF04F2A', 2, NULL, NULL, 2, NULL, 0, 'Add whipped topping and beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB52A368E2408F438018DF751B0514C9', X'F91616BBF979F74FABA3B9825BF04F2A', X'F91616BBF979F74FABA3B9825BF04F2A', 3, NULL, NULL, 2, NULL, 0, 'Spread half of the filling over top of each of the two strawberry shaped cookie crusts .');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7391F8DA946DD7489642D7DEF5369A91', X'64A6A215BCD627469D19A6B3FA353A0C', X'64A6A215BCD627469D19A6B3FA353A0C', 0, NULL, NULL, 2, NULL, 0, 'Arrange kiwi slices to form the leaves at the top of each strawberry shaped cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2E0ED58C88BEB4DA1D4C2E9DAA752BF', X'64A6A215BCD627469D19A6B3FA353A0C', X'64A6A215BCD627469D19A6B3FA353A0C', 1, NULL, NULL, 2, NULL, 0, 'Arrange the strawberry pieces over the remainder of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'045A0B0B7E58F94EB406F0AF0F37C10C', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 0, NULL, NULL, 2, NULL, 0, 'Pour chocolate and heavy whipping cream into a glass measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48883A6098372E4C83373E58817BCECB', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 1, NULL, NULL, 2, NULL, 0, 'Pop the fruit pizza in the refrigerator for 30 minutes before topping with ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8944910CB689441A2CD285686C700C1', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 2, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high power for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5ED5BE658D781C4C9813DB83E78B1B7B', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 3, NULL, NULL, 2, NULL, 0, 'Let sit in microwave for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE28BEAAD634FE4EBF2B3030FE4BB4D0', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 4, NULL, NULL, 2, NULL, 0, 'Remove and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A68DBCB2F5CAF4FAE4B35917AF73C22', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 5, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AD1395806DF1640A12BB0B53A4263E5', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 6, NULL, NULL, 2, NULL, 0, 'Allow the ganache to cool slightly. You still need it to be pourable but you do not want it hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA8A7E12AB00BA4891F992DEE8997BFD', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 7, NULL, NULL, 2, NULL, 0, 'Pour the ganache over the bottom 3/4''s of the strawberry fruit pizza.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C14244225CA4AB47AEF90A2CAEEF8552', X'2E3855739E25294F95C1762B52E8B7DD', X'2E3855739E25294F95C1762B52E8B7DD', 8, NULL, NULL, 2, NULL, 0, 'Drizzle melted white chocolate over the chocolate covered strawberry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC2F26C1D26B3D41B909A6827050E9AD', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 0, NULL, NULL, 2, NULL, 0, 'Combine chocolate and heavy whipping cream in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97DBA3182A14054E800EF282A0A8706F', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'384EE6D8643B9E47829F2D7ED3B0A272', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 2, NULL, NULL, 2, NULL, 0, 'Let sit in microwave for 2 minutes then remove and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECFEA618A22DCB4AA7ABF8965276B842', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool until it thickens slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A81629D8DDA9B4E837C5C05890509CD', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 4, NULL, NULL, 2, NULL, 0, 'Use a food-use-only paintbrush to paint chocolate spots on the inside of two mason jar drinking glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AA58CC27DE0714B92A1039900ECD2BE', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 5, NULL, NULL, 2, NULL, 0, 'Freeze the glasses for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FDA376D8B058F468CE5F36837B4DF5B', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 6, NULL, NULL, 2, NULL, 0, 'Once the glasses are chilled make the milkshakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0645BE21E2EDBA4DA57A127A6E0B2C9F', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 7, NULL, NULL, 2, NULL, 0, 'Combine vanilla ice cream, COFFEE-MATE® Cookies & Cream Liquid Coffee Creamer and milk in a blender and blend until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AF52A7516340F428CFF74A9B0119664', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 8, NULL, NULL, 2, NULL, 0, 'Pour the milkshake into the two glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EC99FA6A6884744BC104659B7618FDB', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 9, NULL, NULL, 2, NULL, 0, 'Top with a dollop of Reddi-wip® and sprinkle on some cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'240B1BD89BDCD648B951C87CC4D91422', X'799F20D6E5EEBE4E95E215C8AF22B8F2', X'799F20D6E5EEBE4E95E215C8AF22B8F2', 10, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBEC6817E354F146BC559BB71902FFE8', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 0, NULL, NULL, 2, NULL, 0, 'Pour 1 1/3 cups heavy whipping cream into a mixing bowl and whisk, or beat using an electric mixer, until you get stiff peaked whipped cream. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'470D2A0A74590F428B58430AB4B7A344', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 1, NULL, NULL, 2, NULL, 0, 'Combine water and sugar in a saucepan and heat over medium high heat until it comes to a boil, stirring until the sugar completely dissolves. Remove from heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EE0C3A757726343831575E0A344ABEC', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 2, NULL, NULL, 2, NULL, 0, 'Whisk the egg yolks in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41348467E517BD478A5552B8B3AD2B1A', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 3, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the hot sugar water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2C8909186151C4D956D9B462C959D8B', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 4, NULL, NULL, 2, NULL, 0, 'Return this mixture, along with the remaining 2/3 cup heavy whipping cream, to your saucepan and continue to stir constantly while heating over medium low heat until the mixture reaches 160 degrees F and starts to thicken slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55396EEA687E534B869F7ED5713F57E5', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 5, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh strainer set over a bowl of melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5CCEAEE04D16841A4D15E2A5F4C8508', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 6, NULL, NULL, 2, NULL, 0, 'Stir until smooth and glossy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88B1AA3110BFF1408C5D74049EE9D540', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 7, NULL, NULL, 2, NULL, 0, 'Fold in half of the whipped cream until well blended then fold in the remaining whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFB636A0412F2E479386951915614F19', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 8, NULL, NULL, 2, NULL, 0, 'Pour into 12 silicone flower pot baking cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'027CF9C700C35D4682452FC11F4D231C', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle cookie crumbs over top of each pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C6BD8BC70C69643B3CFCF988986439B', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 10, NULL, NULL, 2, NULL, 0, 'Chill for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'897076B41D75BF49B877A96FBD54113A', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 11, NULL, NULL, 2, NULL, 0, 'Remove from refrigerator and let rest at room temperature for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75481B3330C762478EAD7D74F19F93BA', X'BC4045270374FE4A91DA699C225491D1', X'BC4045270374FE4A91DA699C225491D1', 12, NULL, NULL, 2, NULL, 0, 'Insert mint sprigs into the center of each chocolate mousse pot or top with tiny strawberries and strawberry leaves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AB3684BFDD30345BFDCE666695FFD60', X'BC9CFCE39BDA9146BD66804E8FDF790F', X'BC9CFCE39BDA9146BD66804E8FDF790F', 0, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'363C71C227BFF44A88D30E15C5D4B47B', X'BC9CFCE39BDA9146BD66804E8FDF790F', X'BC9CFCE39BDA9146BD66804E8FDF790F', 1, NULL, NULL, 2, NULL, 0, 'Stir just to coat the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F80EEF4AB1CEA2408F059A47C0B1F104', X'BC9CFCE39BDA9146BD66804E8FDF790F', X'BC9CFCE39BDA9146BD66804E8FDF790F', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle Hershey''s S''mores Baking Pieces over popcorn and stir just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDF9BAC79BF6874AB1C97F475D439C1C', X'BC9CFCE39BDA9146BD66804E8FDF790F', X'BC9CFCE39BDA9146BD66804E8FDF790F', 3, NULL, NULL, 2, NULL, 0, 'Pour out onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EED3512AF75A2499C5CB547703F80BB', X'BC9CFCE39BDA9146BD66804E8FDF790F', X'BC9CFCE39BDA9146BD66804E8FDF790F', 4, NULL, NULL, 2, NULL, 0, 'Drizzle with dark chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED07EE00DE33F7468E5866E122C8EC74', X'BC9CFCE39BDA9146BD66804E8FDF790F', X'BC9CFCE39BDA9146BD66804E8FDF790F', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC326815A818E24493A541D0AFB72AE5', X'BC9CFCE39BDA9146BD66804E8FDF790F', X'BC9CFCE39BDA9146BD66804E8FDF790F', 6, NULL, NULL, 2, NULL, 0, 'Break into pieces and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'364A8AB832A3A04D88A5D8ED735C64AB', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 0, NULL, NULL, 2, NULL, 0, 'Dip Nutter Butter Cookies in Yellow Candy Melts and set them on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65CB14787D0910449E344CC518AF3B0B', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 1, NULL, NULL, 2, NULL, 0, 'Freeze for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F5F3034246223458E2E88A0F59302D2', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 2, NULL, NULL, 2, NULL, 0, 'Fit a clay extruder with a ribbon disc (small rectangle whole).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D506D6C35B88649AE9D28A71368B08A', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 3, NULL, NULL, 2, NULL, 0, 'Insert black modeling chocolate in the clay extruder and press out ribbons of the candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'288FACAD94D6C94396748036384ECD40', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 4, NULL, NULL, 2, NULL, 0, 'Brush clear piping gel in a thin line over the upper middle part of the cookie and lay a strip of black modeling chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FFB4618C740344F8C0AB1A254D7FB20', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 5, NULL, NULL, 2, NULL, 0, 'Cut off the excess modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C4DF2381B83E045BCF5FBD00D0BD6F2', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating four black, curved stripes across each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C66D2F44FD9613499E33D676E0440658', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 7, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of black modeling chocolate and roll into a cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A428B0F0DA942948A72CE69C695E526E', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 8, NULL, NULL, 2, NULL, 0, 'Attach along the bottom edge of the cookie to create the bee''s stinger.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7258BDF527F6548A3BEDD2573EA31AC', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 9, NULL, NULL, 2, NULL, 0, 'Press more black modeling chocolate through the clay extruder fitted with a round hole disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20E37B48627D044EBE67E1202BEB19AF', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 10, NULL, NULL, 2, NULL, 0, 'Cut tiny pieces of the modeling chocolate to create a smile then create two antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F35F99509B974447873BC89FDD318798', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 11, NULL, NULL, 2, NULL, 0, 'Attach the smile using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9F35D40C11A124CAFA585931CC65FC3', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 12, NULL, NULL, 2, NULL, 0, 'Attach the antennae using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40AD2BF6468F0A43A06EC186FCB18865', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 13, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65B41C735BB8284C9E7E08FEB1B8AC25', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 14, NULL, NULL, 2, NULL, 0, 'Pipe two wings and set a cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'078D951A0A8F0F4BAC1F157A56B10FE6', X'393C57EDCB249443804C2D3D8870F7EE', X'393C57EDCB249443804C2D3D8870F7EE', 15, NULL, NULL, 2, NULL, 0, 'Allow the wings and antennae to harden before moving the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F1F378F663E2A459A91C76FCFE5CDCF', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 0, NULL, NULL, 2, NULL, 0, 'Pour heavy whipping cream into large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1E9682D23C89F438E3112C52EF64372', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 1, NULL, NULL, 2, NULL, 0, 'Beat on high until the cream holds stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22ECD21103276845858CBBB71BF24BCC', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 2, NULL, NULL, 2, NULL, 0, 'Add the maple syrup and sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69398792F05A624B924FAAEAD144A771', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 3, NULL, NULL, 2, NULL, 0, 'Beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C49C35852BBB2439E382E5B1AC41915', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 4, NULL, NULL, 2, NULL, 0, 'Pour into a plastic container, cover, and freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'549DD27C881C2F4CBA6315FFBC05D05B', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 5, NULL, NULL, 2, NULL, 0, 'Heat pancakes according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F02616E5BC1CF944AADF465ECC435FB5', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 6, NULL, NULL, 2, NULL, 0, 'Allow them to cool slightly then scoop 1/6th of the ice cream onto one pancake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD7D3992B2770245AA0558030A9C7218', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 7, NULL, NULL, 2, NULL, 0, 'Top with another pancake and press down to form an ice cream sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B24F408D90262E42A7B88F66BD6A909D', X'9B7935ABD86953479B8334536B647421', X'9B7935ABD86953479B8334536B647421', 8, NULL, NULL, 2, NULL, 0, 'Optionally, roll the ice cream sandwich in one of the toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFFF0E851670F6498F96B8747416AA1C', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 0, NULL, NULL, 2, NULL, 0, 'Insert wooden popsicle sticks into 4 silicone swirl popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E9C2D2FEB560841BF608588E50B2F66', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 1, NULL, NULL, 2, NULL, 0, 'Combine milk and chocolate spread in a blender or mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5FAFF00FEFB4648B98A8EA204E96066', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 2, NULL, NULL, 2, NULL, 0, 'Blend using an electric blender, a stick blender, or a whisk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0229571B42100F48BD501A9C75F36AA8', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 3, NULL, NULL, 2, NULL, 0, 'Pour into swirl popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'937E184560010243901F367D805E7F3F', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 4, NULL, NULL, 2, NULL, 0, 'Freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A5FDEAF22CAFC4F8FB89D2B8DA6C49A', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 5, NULL, NULL, 2, NULL, 0, 'Before serving remove the popsicles from the freezer and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF3E9E5A2AA93A4F91A0433A785246C6', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 6, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of clear piping gel on the back of a large candy eye then press it onto the popsicle about 1/3rd of the way from the top of the swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D8265D13B0B474AA24BF832BD019BCC', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 7, NULL, NULL, 2, NULL, 0, 'Repeat adding a second eye next to the first.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE3795A19D58F5488242E38B2A1DD8D8', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 8, NULL, NULL, 2, NULL, 0, 'Brush piping gel onto the back of a candy smile and attach it about an inch below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C861BD81038804E88F1AB22804CB6DE', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 9, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 10 minutes so that the piping gel can set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A47B03912721DF49ABBAFAFD74AF4995', X'9433F7289F0AF342BB52BAAEFBD90BDE', X'9433F7289F0AF342BB52BAAEFBD90BDE', 10, NULL, NULL, 2, NULL, 0, 'Remove, serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC45CB685BFDCC47ACCD440656092019', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 0, NULL, NULL, 2, NULL, 0, 'Create a simply syrup by combining the water and sugar in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'120D6BDE6AC4AF40B562FCB8C21D2300', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3782CF8E8EB1054CB5F8C895F2358EA5', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B79F5385437FB644BE98CD58CEDD6F6B', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 3, NULL, NULL, 2, NULL, 0, 'Heat on high for 30-45 seconds until it comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F97B98FA04E189499DD64D38F074793D', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 4, NULL, NULL, 2, NULL, 0, 'Carefully remove from microwave.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D7CAA54B366F14A8F93DC964E4A944A', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 5, NULL, NULL, 2, NULL, 0, 'Place block of cream cheese in large mixing bowl (with a spout, preferably).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49E5AEBF1C70184FA6F9B68C8861F58A', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 6, NULL, NULL, 2, NULL, 0, 'Pour all but 1 tablespoon of the simple syrup over the cream cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4379B27D3ADD884B95A0588180DAB1FD', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 7, NULL, NULL, 2, NULL, 0, 'Beat with electric mixer until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06B9CE9BE1E21142AFB92FEECE2EF2AE', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 8, NULL, NULL, 2, NULL, 0, 'Add heavy whipping cream and vanilla and blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'909C97180A41DC48A0DAAA436194074C', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 9, NULL, NULL, 2, NULL, 0, 'Stir the remaining tablespoon of simple syrup into the raspberry puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3DC075AA56DE240BEFA3214C17582AE', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 10, NULL, NULL, 2, NULL, 0, 'Drizzle raspberry puree into 8 zombie popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8C5C01B85BD0B478878BC5C0083ED15', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 11, NULL, NULL, 2, NULL, 0, 'Fill the molds with the cheesecake mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86E6AA2175B1FD49BF714029F5D6A11C', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 12, NULL, NULL, 2, NULL, 0, 'Insert the legs, making sure they are facing forward, and pop into the freezer for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'633F788B4716964983A0DD3D8B1F6769', X'AC8536A288D7594DA3EBC814776A3B9E', X'AC8536A288D7594DA3EBC814776A3B9E', 13, NULL, NULL, 2, NULL, 0, 'Peel off the silicone mold before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'026A321E4E450B418EB9D859FA468C7A', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F98431657E6DE478501644ADF7CFEDA', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 1, NULL, NULL, 2, NULL, 0, 'Combine butter and cream cheese in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55C3AA46DD976E45ACAA8C3DBC523051', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 2, NULL, NULL, 2, NULL, 0, 'Beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C08EA35E7D87734EB9613FF14D50A941', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 3, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until well mixed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07CAC17F0C1D5545A9C5FC0BD30CA0CE', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 4, NULL, NULL, 2, NULL, 0, 'Pour in egg, vanilla, almond extract, and food coloring and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0612534AF3745C46A3E73797D09E20CB', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 5, NULL, NULL, 2, NULL, 0, 'Whisk together flour, baking powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D954F3BDC5C4F74E94FF704AAA521D27', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 6, NULL, NULL, 2, NULL, 0, 'Stir the dry ingredients into the wet just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB490FAC2D3C04408A7D22AED571651A', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 7, NULL, NULL, 2, NULL, 0, 'Scoop out 40 heaping tablespoons of dough and set on parchment paper lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DC7BEC08EC5A84187818A40735DD3B2', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 8, NULL, NULL, 2, NULL, 0, 'Use a fork to spike the dough to make it look furry, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'268E8A190F87EE43A23FCA23E64BC3DC', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 9, NULL, NULL, 2, NULL, 0, 'Bake one tray at a time for 12-16 minutes until the edges of the cookie feel set and the tops no longer look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63D6D1D3C320F649998AF2DACF755319', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 10, NULL, NULL, 2, NULL, 0, 'Remove cookies and allow to cool completely,');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'945E1B03B197254BA615766467ED8986', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 11, NULL, NULL, 2, NULL, 0, 'Peel cookies off parchment paper and pair them up based on size.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8076DF067950DF44B8A41B2D57AE6C50', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 12, NULL, NULL, 2, NULL, 0, 'Use blue cookie icing to attach two candy eyes to half of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0B2651BBC9E0E4FA81AC78F5AA950A4', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 13, NULL, NULL, 2, NULL, 0, 'Place a scoop of ice cream in between two cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A77631F0B9CFC46898F4B655996BA74', X'D9A50274E4DE784197C563AE4060A249', X'D9A50274E4DE784197C563AE4060A249', 14, NULL, NULL, 2, NULL, 0, 'Freeze until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F10DDD49B3FE6D46ACE7B012C0A8A7E5', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 0, NULL, NULL, 2, NULL, 0, 'Spread some peanut butter on 12 of the butterfly crackers then set another cracker over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'744B1A6ED5584F4B8B689F1293F48C6E', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 1, NULL, NULL, 2, NULL, 0, 'Dip a peanut butter cracker into the melted candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B9BF42018354C44B82442F7B41D0000', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 2, NULL, NULL, 2, NULL, 0, 'Use a chocolate dipping fork to remove the butterfly and allow the excess candy melts to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B6F29E4B7EEEC45A27B9DFAAF40338E', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 3, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1408567A8F20894B84536872391ED71C', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 4, NULL, NULL, 2, NULL, 0, 'Immediately press two regular yellow or orange M&M''s on the upper portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A6FEFC8163CF1488DBC373FF8B350E2', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 5, NULL, NULL, 2, NULL, 0, 'Press two mini yellow or orange M&M''s on the lower portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC29F18CD6C3B04F9DA69891EA984704', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 6, NULL, NULL, 2, NULL, 0, 'Press one regular green M&M at the top of the mid section creating the butterfly''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56CECCB854740841A938B6CF5FEB8DB5', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 7, NULL, NULL, 2, NULL, 0, 'Arrange three mini green M&M''s in a row below the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE5230A0324A174495EC04A6D98352C6', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 8, NULL, NULL, 2, NULL, 0, 'Insert two candy licorice pieces into the top of each cracker creating antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39FF236B8FE9724EAB9AFF695A193108', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 9, NULL, NULL, 2, NULL, 0, 'Pop the cracker into the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'909174F1A5B6F24DACA055830A9ECE6E', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 10, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34E239471C020F4A897D9F5B8E11D279', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 11, NULL, NULL, 2, NULL, 0, 'Remove the crackers from the freezer and let sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE5BBF4C14A01246AA653997BACC3A3E', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 12, NULL, NULL, 2, NULL, 0, 'Draw two eyes and a smile on the green butterfly heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0682F02614ADE247929A886E792C1A7D', X'1B572E6806334D4981C6D630509489DD', X'1B572E6806334D4981C6D630509489DD', 13, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E805681531A5044A8CABED3ED83F0DC', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 0, NULL, NULL, 2, NULL, 0, 'Mix the blue food coloring in the can of white frosting until you get the desired blue color.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1319A27C9E291F4FB039F6FAE4CF5460', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 1, NULL, NULL, 2, NULL, 0, 'Frost the top of the Rice Krispie Treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C3643FA92EAC14D8152EAE81A66518F', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 2, NULL, NULL, 2, NULL, 0, 'Alternate the orange and yellow mini M&Ms around the outside of the Rice Krispie Treat to form the pool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B82F0E4ABC622408995D18B81705F96', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 3, NULL, NULL, 2, NULL, 0, 'Put a Gummy Lifesaver candy in the middle of the pool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98DE7AB8C92BA84FAE32C030C2A9CBBE', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 4, NULL, NULL, 2, NULL, 0, 'Cut the Teddy Graham in half and attach to the top of the Lifesaver with frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45FA13D1D2C8F5488F40FD261739EFD3', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 5, NULL, NULL, 2, NULL, 0, 'Or put 2 Teddy Grahams that have been cut in half in the pool with a gumball.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0208117F456B8342AA0D4609456FA43D', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 6, NULL, NULL, 2, NULL, 0, 'Or place a bite size Rips candy in the pool and lay a Teddy Graham on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18A694A5C3E5B7489D31E51BE7234D20', X'CCCFDDC61567884EA966EA7DCD518FDB', X'CCCFDDC61567884EA966EA7DCD518FDB', 7, NULL, NULL, 2, NULL, 0, 'Push in paper drink umbrellas in the cereal treat pool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C88A5A70CC42CC49973E0BC0030589BF', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 0, NULL, NULL, 2, NULL, 0, 'Melt the white candy melts in the microwave on high power for 30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C72600CA96A79C4791BEAF77D14FE030', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 1, NULL, NULL, 2, NULL, 0, 'Stir in the peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B18E6B4396E2F14CA350A10781F68194', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 2, NULL, NULL, 2, NULL, 0, 'Pour candy into castle lollipops molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCD6C6A6B297A346B3C5722AFC99CF16', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 3, NULL, NULL, 2, NULL, 0, 'Insert lollipop sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7E0F6B3209A99489A9B8C0B2DD0CC82', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 8-10 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A26DD0E09696441A954A76AE003AC42', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 5, NULL, NULL, 2, NULL, 0, 'Remove from freezer and pop candy out of mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC0F1FB2D37FFF4AA4FE13B09356845C', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 6, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D7F517C48D45841BFE4D049170AE33A', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 7, NULL, NULL, 2, NULL, 0, 'Brush a light coating of corn syrup over front of lollipop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D765FA57451D3D4CA7F2AEEFBA928274', X'003F0E6E108E2041905B7DC8AB37562B', X'003F0E6E108E2041905B7DC8AB37562B', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle cookie crumbs over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D6CBBCBF1F2C545B9E7FDDF279AB035', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE4B8D1A56323B4586B0314D4EC9DF4A', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9816C3B097BAF64EB8F232A60F468DDA', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F62915D3C29DB34884F5715CB6463420', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 3, NULL, NULL, 2, NULL, 0, 'Stir in vanilla, and almond extract then stir in the egg until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95C312057A892842B6896AEEE4459382', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D85273A9BC54444B40A8F2F34FC09C8', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 5, NULL, NULL, 2, NULL, 0, 'If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C141B7904514894087C8131AD758AD35', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 6, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface or non-stick mat with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A89E985AD423946826A7ADC2CAFE333', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 7, NULL, NULL, 2, NULL, 0, 'Roll the cookie dough out to between a 1/16 inch and 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5431236B334E0042AB75270BDCAFB428', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 8, NULL, NULL, 2, NULL, 0, 'Cut dough using cookie cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A6B40E80B4D0E43A572BDDE5A0F35D4', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 9, NULL, NULL, 2, NULL, 0, 'Remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F59FD47625854E4CA91A9023BD662079', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 10, NULL, NULL, 2, NULL, 0, 'Freeze cut cookies for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C06403902615ED4A8714EFD2806FFD8D', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 11, NULL, NULL, 2, NULL, 0, 'Peel off cookies and set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03D2F4B89A3D294B8F39CAD872165C9B', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 12, NULL, NULL, 2, NULL, 0, 'Bake cookies for 6-12 minutes, depending on the size, until the edges are set and the tops look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B73429C96A46D4BB24A1A77643BD13E', X'4FAB096F9E570341AD195A11714D2A3F', X'4FAB096F9E570341AD195A11714D2A3F', 13, NULL, NULL, 2, NULL, 0, 'Cool cookies completely then decorate using one of the methods in the tutorial.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C7247E96F5F4A489F3B3ECEA6AC1461', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 0, NULL, NULL, 2, NULL, 0, 'Pour crisp rice cereal in very large heatproof bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AD5DC4A4AC65444936B6A7B86B89908', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 1, NULL, NULL, 2, NULL, 0, 'Cut butter into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C7C943FC742804C99978B2AC4EC6F5F', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 2, NULL, NULL, 2, NULL, 0, 'Place butter, brown sugar, and corn syrup in a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B9CF7E90B310D478C56F350B88DCDB7', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 3, NULL, NULL, 2, NULL, 0, 'Heat on medium, stirring as needed, until butter melts and mixture begins to bubble around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54E59CE9731C8144993F5819DC505683', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 4, NULL, NULL, 2, NULL, 0, 'Place lid on pan for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F061D4572AD96143A785B6CEF4C5EA94', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 5, NULL, NULL, 2, NULL, 0, 'Remove lid and allow to boil for 2 more minutes, without stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27A9D0FA4D258947956E855BD7EA5133', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and add sweetened condensed milk, stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C414CDA95EBF440A143A116E4C9190A', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 7, NULL, NULL, 2, NULL, 0, 'Return to medium heat and allow to boil for three minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D81F611FCE17484A9E2941466CA9BCCD', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 8, NULL, NULL, 2, NULL, 0, 'Pour over cereal and stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89EA665DBE0F4744BD220E3955F3530E', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 9, NULL, NULL, 2, NULL, 0, 'Let cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B64A42760028A44951B626EC00182A9', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 10, NULL, NULL, 2, NULL, 0, 'Line plastic molds with plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D3179FE267988489D6D4ADBFED60930', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 11, NULL, NULL, 2, NULL, 0, 'Fill molds with cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E0EE2BF19D60F49A2C6FD3AE91AA370', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 12, NULL, NULL, 2, NULL, 0, 'Press to compress cereal so that it holds it''s shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80995DEE1617694881166BF67F0644A4', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 13, NULL, NULL, 2, NULL, 0, 'Un-mold treats and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A86A7BC64C421D458952C997B3470723', X'7BB62929C75A894C8F7F42788E75AC56', X'7BB62929C75A894C8F7F42788E75AC56', 14, NULL, NULL, 2, NULL, 0, 'Decorate, if desired by attaching candies using edible `glue.`');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5809E6E53923C348996C543FFD8B43DD', X'E33F55F1D7122C4AA51A702A3D27353D', X'E33F55F1D7122C4AA51A702A3D27353D', 0, NULL, NULL, 2, NULL, 0, 'Heat marshmallows, butter, and candy melts in a large microwave safe bowl on high power for 30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00A5B7F25D6D6442940D22FAA6D69BBB', X'E33F55F1D7122C4AA51A702A3D27353D', X'E33F55F1D7122C4AA51A702A3D27353D', 1, NULL, NULL, 2, NULL, 0, 'Stir in 1 drop of black food coloring, if using, to make the marshmallow mixture light grey.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FA6418646A6294EA3D635B64705CB12', X'E33F55F1D7122C4AA51A702A3D27353D', X'E33F55F1D7122C4AA51A702A3D27353D', 2, NULL, NULL, 2, NULL, 0, 'Stir in Rice Krispies Cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A33DCDD78535B54AA0A44116EA96D621', X'E33F55F1D7122C4AA51A702A3D27353D', X'E33F55F1D7122C4AA51A702A3D27353D', 3, NULL, NULL, 2, NULL, 0, 'Press cereal mixture into your 3-D dolphin mold filling it to the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2FD7CE092B9D94FA9204AAD59B0AAA7', X'E33F55F1D7122C4AA51A702A3D27353D', X'E33F55F1D7122C4AA51A702A3D27353D', 4, NULL, NULL, 2, NULL, 0, 'Turn mold upside down and allow treat to pop out. (Use a thin knife to help it out, if it wont pop out on it''s own.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36B638E18B050D4792AE981417B8581C', X'E33F55F1D7122C4AA51A702A3D27353D', X'E33F55F1D7122C4AA51A702A3D27353D', 5, NULL, NULL, 2, NULL, 0, 'Repeat, creating 8 dolphins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'083E54A48757B241BE3743C8CF037E90', X'E33F55F1D7122C4AA51A702A3D27353D', X'E33F55F1D7122C4AA51A702A3D27353D', 6, NULL, NULL, 2, NULL, 0, 'Insert one mini chocolate chip eye on either side of each dolphin''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67D0B7704C0D4A4FBB0A111E15576B6B', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDC05FF0E0119549A7684BC1DDC8010A', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67FE93053482F543891C3DD535EB0605', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'462569CC55833F43BD1E0B518C396065', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 3, NULL, NULL, 2, NULL, 0, 'Stir in egg, vanilla, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E55407A2B71BB3409A0F91D9A0613EE1', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined. If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'787AA8CF931DE144AE1835AF0CAA9DE4', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 5, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface or non-stick mat with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD6BC8AEB0E8814FA469B9A035444785', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 6, NULL, NULL, 2, NULL, 0, 'Roll the cookie dough out to between a 1/16 inch and 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0181D22ECBBD844595AC5E6813543A14', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 7, NULL, NULL, 2, NULL, 0, 'Cut dough using palm tree cookie cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E23FD14A2970BD46ACD550896FAB7D34', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 8, NULL, NULL, 2, NULL, 0, 'Remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E4BAF4A14E3954BB9B7C7204B5C4987', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 9, NULL, NULL, 2, NULL, 0, 'Freeze cut cookies for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E19E73684512D45A62135431879A31C', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 10, NULL, NULL, 2, NULL, 0, 'Peel off cookies and set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5319624B3423624590562871A3BD99BA', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 11, NULL, NULL, 2, NULL, 0, 'Bake cookies for 7-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDC7239D01FFFD4CA61B646358AA9FDE', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 12, NULL, NULL, 2, NULL, 0, 'Cool cookies completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8210A8098D001A489AE9294751F51A2D', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 13, NULL, NULL, 2, NULL, 0, 'Roll out brown modeling chocolate and cut using the palm tree cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F5CFEF627F1FC4E91B471010FB74C37', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 14, NULL, NULL, 2, NULL, 0, 'Cut so you just have a brown tree trunk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69EA61C8368CD347AF44D098CCBE1C50', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 15, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of corn syrup over the cookie trunk and press the modeling chocolate on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1C2957538A16A428D6ADE0312063D4D', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 16, NULL, NULL, 2, NULL, 0, 'Use the back of a knife or spatula to make X''s across the trunk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCC8035753993841A014F295B03477B3', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 17, NULL, NULL, 2, NULL, 0, 'Roll out green modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D4AF7928982934CBE847A189D85526E', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 18, NULL, NULL, 2, NULL, 0, 'Cut out up to 12 small leaves and 6 large leaves per cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7496503D19C5FB49B816820B1B02AB70', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 19, NULL, NULL, 2, NULL, 0, 'Brush a thin coating of corn syrup on the top of the palm tree cookie and press leaves over top, layering them as you go.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'805A1C9F91B41A408E0C8ADA6AEBD6EC', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 20, NULL, NULL, 2, NULL, 0, 'Pinch off pea sized pieces of the brown modeling chocolate and roll into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4589E5A39C002745AEC473B4A889F7AD', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 21, NULL, NULL, 2, NULL, 0, 'Attach 3 of the balls to the center of the leaves on each palm tree cookie using corn syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C61B2520B0839A4B9A477AA08CA90411', X'CCA7095475AE1E43BEA1496F14BA73A5', X'CCA7095475AE1E43BEA1496F14BA73A5', 22, NULL, NULL, 2, NULL, 0, 'Let cookies dry for at least 1 hour before stacking or packaging them.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37883A882796E448ABC59EC1AC8DB910', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 0, NULL, NULL, 2, NULL, 0, 'Butter the sides of a heavy 1-1/2 or 2 quart saucepan and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD954C6C6BC60F4586294FC137A613BF', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 1, NULL, NULL, 2, NULL, 0, 'In another 2 or 3 quart saucepan combine the water, pectin, and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E79014856443D41ABEC1AC2B4561DCE', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, stirring constantly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA521B0A3B766B4DBB7B21AB0AB06367', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat, cover, and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9F3E368BB5D174AAE8E4DC92697933C', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 4, NULL, NULL, 2, NULL, 0, 'In the buttered saucepan add corn syrup and water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'324BCF8DEA583F4E974A606A1480E6D0', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 5, NULL, NULL, 2, NULL, 0, 'Set over medium-high heat stirring until sugar disolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'856FA6DB815B1742BF831484F4354572', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 6, NULL, NULL, 2, NULL, 0, 'Bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'165188F5CD8B3040ADD2B5C9C8EB3FA2', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 7, NULL, NULL, 2, NULL, 0, 'When candy reaches 260 degrees Fahrenheit, remove the lid and place the pot of pectin back on high heat and bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEB4C9D6344F164E918FFB5A01F20D02', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 8, NULL, NULL, 2, NULL, 0, 'When candy reaches 280 degrees F, remove from heat and slowly pour in the hot pectin mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7CE9063802E0384791FBE75C8E15D3D7', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 9, NULL, NULL, 2, NULL, 0, 'Return to medium-high heat and stir for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'093C75B166D2AF4BBBBCA32283AE1DFC', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 10, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in 1/4 - 1/2 teaspoon flavored oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45C5ED254B90A54E888E2720F1C7CA29', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 11, NULL, NULL, 2, NULL, 0, 'Add coloring, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7035B2DB337D74AB6DFAF091A3D68BA', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 12, NULL, NULL, 2, NULL, 0, 'Carefully pour into silicone candy mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'012D34FDBC948340B325DE57A453C861', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 13, NULL, NULL, 2, NULL, 0, 'Set molds aside for 6 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9ADD53D0DE0CF94EB16A011F519CE9C8', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 14, NULL, NULL, 2, NULL, 0, 'Turn molds upside down and press on the back side of the mold to remove the candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C2375799CF2A44196B1BFA958F9F466', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 15, NULL, NULL, 2, NULL, 0, 'Add details using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85B7475E278A5C4B99A09FC58FBD1AEB', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 16, NULL, NULL, 2, NULL, 0, 'Roll in colored decorating sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C165483D05C794DAB227924F570E8D7', X'811B6B9F561AC24A9E595BC1D22ABF05', X'811B6B9F561AC24A9E595BC1D22ABF05', 17, NULL, NULL, 2, NULL, 0, 'Set aside for 1 hour before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1EB9C2662B7214BA6FD7E544952C1CE', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70ACC82252EF184C9E478E080733827D', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', 1, NULL, NULL, 2, NULL, 0, 'Stir together graham cracker crumbs and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1048DF59E7B6F46A1D6504D66170DAD', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', 2, NULL, NULL, 2, NULL, 0, 'Spoon a heaping tablespoon of crumbs into each of 18 round cavities in 3 silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E9928F0C0CBD84992ECEEAA21C8DDA6', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', 3, NULL, NULL, 2, NULL, 0, 'Press crumbs into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A4DA4DFA48E5F43931C958A4C7F0912', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', 4, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until fragrant and golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E629AFB57DE1AC4791E614820379EF88', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', 5, NULL, NULL, 2, NULL, 0, 'Remove and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDE9C6C8641B0648A6E5914D7ADFFFAA', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', X'2F75B1FADDF4CA4CB0BAF0E88A67136E', 6, NULL, NULL, 2, NULL, 0, 'Set molds on baking pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'825E7B1156D10E4DAD9B23597BDC764E', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C38DF7D35F11EA4AA067F1EE218D1C22', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 1, NULL, NULL, 2, NULL, 0, 'Add sugar, eggs, heavy whipping cream, and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE041EA8F47C9C4D8D8622EA11D651B0', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 2, NULL, NULL, 2, NULL, 0, 'Spoon 1/3 cup of filling into a bowl and color it blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E72D2D12CACF6A43AEAEAED827DC5E4C', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 3, NULL, NULL, 2, NULL, 0, 'Repeat, coloring 1/3 cup red, and another 1/3 cup yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'974C428664721748AB2375BEFB464B03', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 4, NULL, NULL, 2, NULL, 0, 'Color 3 tablespoons of filling orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49C1985D4F04E24A9577D3A3A5736537', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 5, NULL, NULL, 2, NULL, 0, 'Tap the bowls on the counter several times to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'029647C8D0195947901CD2B0F3430A4C', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 6, NULL, NULL, 2, NULL, 0, 'Pour individual colors of filling into squeeze bottles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D115569BF9FC341BC016F2EEA92CBD0', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 7, NULL, NULL, 2, NULL, 0, 'Spoon the plain filling over top of each of the crusts, diving filling equally.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69E7909BAA87B4478E91B7EDD69AD052', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 8, NULL, NULL, 2, NULL, 0, 'Pipe red, blue, and yellow beach ball panels onto the plain filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43E2D9B7943AA84383FAE3C4D4F222ED', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 9, NULL, NULL, 2, NULL, 0, 'Pipe an orange circle in the middle of the cheesecake beach balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F93EE44C8197CC4C964976F682B7726C', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 10, NULL, NULL, 2, NULL, 0, 'Set pans in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E24AD143EB498429B44ED7CE8723F7B', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 11, NULL, NULL, 2, NULL, 0, 'Pour water into the pan, being careful not to pour any onto the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82382C63D602494AB14D5BC516AA7E80', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 12, NULL, NULL, 2, NULL, 0, 'Bake for 16-22 minutes until the edges of the cheesecake look set. The center will still be jiggly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8B45C62EC334A488A15350C8CD06F3F', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 13, NULL, NULL, 2, NULL, 0, 'Remove molds from water bath, set on a cooling rack, and cool for 30 minutes .');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18A4CB76F282524897F6A34C3FA13D3D', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 14, NULL, NULL, 2, NULL, 0, 'Freeze for at least an hour until firm enough to remove from molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBA30D1F9047BE41891D19D6EAEB6B82', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 15, NULL, NULL, 2, NULL, 0, 'Remove and push cheesecakes out of silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57891AA1CE479B4F9C390FDA8A5453CD', X'88DFC1F972F8354CAA6579CC86543CEE', X'88DFC1F972F8354CAA6579CC86543CEE', 16, NULL, NULL, 2, NULL, 0, 'Cover with paper towels and refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FD969D369977A4ABBD257190837BF6B', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81A23E3AD2EB3C41A6B7020D4A441776', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFB15F07C9496A4CB5910C8DADF40322', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 2, NULL, NULL, 2, NULL, 0, 'Pour blue candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B6A2DD1276B3F4893EDAE681B1E414E', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 3, NULL, NULL, 2, NULL, 0, 'Heat for 30 second burst of high power, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF2A67E251C56040A5114C19105C34EA', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 4, NULL, NULL, 2, NULL, 0, 'Pour candy melts over the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95316DB79634554E995087EBF857EDA9', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 5, NULL, NULL, 2, NULL, 0, 'Toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87872772D1E9224ABC9B836ED1749707', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 6, NULL, NULL, 2, NULL, 0, 'Pour out onto a parchment paper lined baking sheets and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6F6C550B7A3AF4AB77D8516BCC9E01E', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 7, NULL, NULL, 2, NULL, 0, 'Immediately press Finding Dory and Nemo marshmallows all over the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'047CEAF7D3A94A45BCBA5D6376E212FB', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle on white sugar pearls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58F0991E98A54247AAF7510DF3DAF0B6', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 9, NULL, NULL, 2, NULL, 0, 'If desired, press some of the cookies onto the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'766B1D717B520245A9AA09535100A800', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate for 8-10 minutes until the candy coating n the popcorn hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'609A713F0F89524488D17605DFE9BD58', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 11, NULL, NULL, 2, NULL, 0, 'Break popcorn into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A7FB59AC7B6314491CE2F09A92A0D24', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 12, NULL, NULL, 2, NULL, 0, 'Package in clear cellophane bags with some of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F59A119E6896F24C9FEC4B0F6E6E6446', X'E28E1788376F8D4EAAFE8769A1039925', X'E28E1788376F8D4EAAFE8769A1039925', 13, NULL, NULL, 2, NULL, 0, 'Top bag with printable label.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44B76169A7FE7F4DA216C0DEBF8B2B4B', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 0, NULL, NULL, 2, NULL, 0, 'Unwrap one of the PB&J bars and put it into a mini food processor or blender along with the chopped dates, dried cherries, and 4 teaspoons of the peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3082A3FFDB84949B8C7D9A1D4761879', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 1, NULL, NULL, 2, NULL, 0, 'Pulse until really well chopped.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D25EE0C06D54134DA7A5F8C3AF8168F3', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 2, NULL, NULL, 2, NULL, 0, 'To test if it''s chopped enough, squeeze about a tablespoon of the mixture in your hand and it should stick together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D161337306FCD44963B7E0915B2A1D1', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 3, NULL, NULL, 2, NULL, 0, 'Dump all the crumbs out onto your work surface and squeeze together into a flat disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A312F38EC6A6B142BD7A786227F96DB9', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 4, NULL, NULL, 2, NULL, 0, 'Flatten the disc of dough out to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D68EBF31B185FA42B79498ADAD7E8382', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 5, NULL, NULL, 2, NULL, 0, 'Cut out 9 small stars using a cookie cutter, reworking the dough as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0742EE17626FB45818938EFE3BB795A', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 6, NULL, NULL, 2, NULL, 0, 'Pinch the points of the stars to look more like a starfish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE65FCC016BCA34998439686F116841B', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 7, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes to each starfish using peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EFB88CCD411DE41834617FC61647CBB', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 8, NULL, NULL, 2, NULL, 0, 'Brush a teaspoon of peanut butter over each of the remaining PB&J Bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D9F5ED010A6754EA673F0944369AADA', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 9, NULL, NULL, 2, NULL, 0, 'Crush the Honey Crunch Cereal into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1953CFBE9B014B4DB51EF37726A6C7FC', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle crumbs over top of the peanut butter coated PB&J bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49193F8020F56E4399D4A5E93A986DF5', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 11, NULL, NULL, 2, NULL, 0, 'Spread a small amount of peanut butter onto the backside of a starfish and press it into the sandy cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7F23F30BA141C4DA7888D74DE33A0C1', X'B227CD295D7B874A81051D6E4B8BF2C9', X'B227CD295D7B874A81051D6E4B8BF2C9', 12, NULL, NULL, 2, NULL, 0, 'Repeat, creating a total of 9 sandy beaches topped with a starfish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF225F233C11E741A232D657C8B018C2', X'D372BA1819079B4BA49F53456742A3CD', X'D372BA1819079B4BA49F53456742A3CD', 0, NULL, NULL, 2, NULL, 0, 'Combine the butterscotch chips and white confectionery coating wafers in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B925F0D138D504390EA7041FF5C8D04', X'D372BA1819079B4BA49F53456742A3CD', X'D372BA1819079B4BA49F53456742A3CD', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 15-30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E04E329593DF3E48A28867E1A9841467', X'D372BA1819079B4BA49F53456742A3CD', X'D372BA1819079B4BA49F53456742A3CD', 2, NULL, NULL, 2, NULL, 0, 'Stir in cereal crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2C4CBEC2A3CC445B7B063C9A71C6B14', X'D372BA1819079B4BA49F53456742A3CD', X'D372BA1819079B4BA49F53456742A3CD', 3, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into starfish candy mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A06C95D1CC490A409E23FABEF35690DC', X'D372BA1819079B4BA49F53456742A3CD', X'D372BA1819079B4BA49F53456742A3CD', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 8-12 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6508A1B2FB225F4DBD336ACFDE636B31', X'D372BA1819079B4BA49F53456742A3CD', X'D372BA1819079B4BA49F53456742A3CD', 5, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A5C1BEA92AE714A8E99D54D88590C2C', X'131D0B3237504744A9CFCE20E2BF27FB', X'131D0B3237504744A9CFCE20E2BF27FB', 0, NULL, NULL, 2, NULL, 0, 'Brush a thin line or two of the colored confectionery coating onto the sides of a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56A275F2FEEAF444A026BA61EB34BE2D', X'131D0B3237504744A9CFCE20E2BF27FB', X'131D0B3237504744A9CFCE20E2BF27FB', 1, NULL, NULL, 2, NULL, 0, 'Fill bag with white confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D634DBB0FA4E746837C5978A59C036C', X'131D0B3237504744A9CFCE20E2BF27FB', X'131D0B3237504744A9CFCE20E2BF27FB', 2, NULL, NULL, 2, NULL, 0, 'Snip tip off bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47C93E21F5285144889387255F0DFF42', X'131D0B3237504744A9CFCE20E2BF27FB', X'131D0B3237504744A9CFCE20E2BF27FB', 3, NULL, NULL, 2, NULL, 0, 'Pipe into sea shell candy molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'372604F1B8CC5B4E8A031F955D5899B7', X'131D0B3237504744A9CFCE20E2BF27FB', X'131D0B3237504744A9CFCE20E2BF27FB', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 5-10 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E04ED93885211B43B256AEBB80E571DB', X'131D0B3237504744A9CFCE20E2BF27FB', X'131D0B3237504744A9CFCE20E2BF27FB', 5, NULL, NULL, 2, NULL, 0, 'Remove and un-mold the candy shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E79CE886270FE408F7977272D7E384A', X'E5DC8E63C005E543A8BBD3D3E3E4EE16', X'E5DC8E63C005E543A8BBD3D3E3E4EE16', 0, NULL, NULL, 2, NULL, 0, 'Color the white chocolate using candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A15800CFEEA4804EAB2B294E4DB6CF48', X'E5DC8E63C005E543A8BBD3D3E3E4EE16', X'E5DC8E63C005E543A8BBD3D3E3E4EE16', 1, NULL, NULL, 2, NULL, 0, 'You''ll use approximately one ounce of chocolate per cup, so color accordingly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'676688374B9E944893C967737C41E80A', X'A5BDCFE6EC369D4C96525E99FF7E7000', X'A5BDCFE6EC369D4C96525E99FF7E7000', 0, NULL, NULL, 2, NULL, 0, 'Fill a small plastic shot glass full of colored white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFD6172B9C88DA4AB6D39AF9FB9A24FE', X'A5BDCFE6EC369D4C96525E99FF7E7000', X'A5BDCFE6EC369D4C96525E99FF7E7000', 1, NULL, NULL, 2, NULL, 0, 'Let the cup sit at room temperature for 1-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEA3E60E8B5A86409068C93A4DF8396E', X'A5BDCFE6EC369D4C96525E99FF7E7000', X'A5BDCFE6EC369D4C96525E99FF7E7000', 2, NULL, NULL, 2, NULL, 0, 'Turn shot glass upside down and allow the excess chocolate to drip out, leaving just a thin shell.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C24AFA9155E6084CB6E42C918D354B39', X'A5BDCFE6EC369D4C96525E99FF7E7000', X'A5BDCFE6EC369D4C96525E99FF7E7000', 3, NULL, NULL, 2, NULL, 0, 'Set aside for 5 minutes then refrigerate for 5-10 minutes if using pure chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD819CC5872AC243B0F5729D7DB5B899', X'A5BDCFE6EC369D4C96525E99FF7E7000', X'A5BDCFE6EC369D4C96525E99FF7E7000', 4, NULL, NULL, 2, NULL, 0, 'If using candy melts, freeze for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1700E82E72EC24B818C19B8A3010FF7', X'A5BDCFE6EC369D4C96525E99FF7E7000', X'A5BDCFE6EC369D4C96525E99FF7E7000', 5, NULL, NULL, 2, NULL, 0, 'Remove and gently pull the chocolate out of the shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E22EEC15BE9B59488F0A1A948117E7D2', X'8CA34F8F8FE4E944876D9280276EA5AA', X'8CA34F8F8FE4E944876D9280276EA5AA', 0, NULL, NULL, 2, NULL, 0, 'Brush chocolate onto the bottom and up the sides of a plastic shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F2475376619BA46810AF241B5E89A64', X'8CA34F8F8FE4E944876D9280276EA5AA', X'8CA34F8F8FE4E944876D9280276EA5AA', 1, NULL, NULL, 2, NULL, 0, 'Chill until hardened, then brush another layer over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8A8C4D9AB3CD54AA31E556B5ED5715E', X'8CA34F8F8FE4E944876D9280276EA5AA', X'8CA34F8F8FE4E944876D9280276EA5AA', 2, NULL, NULL, 2, NULL, 0, 'Chill until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'565D2580C3AA684A9FB7D76629279273', X'8CA34F8F8FE4E944876D9280276EA5AA', X'8CA34F8F8FE4E944876D9280276EA5AA', 3, NULL, NULL, 2, NULL, 0, 'Remove and gently pull the chocolate out of the shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3AFE0079962C14EA678668E53EA79A9', X'654183A2728F0646818F86FDFD32A4CA', X'654183A2728F0646818F86FDFD32A4CA', 0, NULL, NULL, 2, NULL, 0, 'Cut thin 3/16th inch by 3 1/2 inch long strips of white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'776D4B7CAA32584C8861D1D364A6FD0E', X'654183A2728F0646818F86FDFD32A4CA', X'654183A2728F0646818F86FDFD32A4CA', 1, NULL, NULL, 2, NULL, 0, 'Attach one end near the top edge of a chocolate pail.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7B1489B2B0BD342A4F49EE353EA0306', X'654183A2728F0646818F86FDFD32A4CA', X'654183A2728F0646818F86FDFD32A4CA', 2, NULL, NULL, 2, NULL, 0, 'Drape the strip around to the other side of the pail and press it on.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4F84795C497714983A5D8D8D640C1E4', X'654183A2728F0646818F86FDFD32A4CA', X'654183A2728F0646818F86FDFD32A4CA', 3, NULL, NULL, 2, NULL, 0, 'If needed, use piping gel or melted chocolate as glue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A01FC6BFA3EB94EBD2788AC27AE1AB4', X'76EFA648341C3B489994D5B35B3257BD', X'76EFA648341C3B489994D5B35B3257BD', 0, NULL, NULL, 2, NULL, 0, 'Just before serving, pipe about an ounce of mousse or pudding into each chocolate pail.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3B34B8B8AC33649BA82A7C66BB546EC', X'76EFA648341C3B489994D5B35B3257BD', X'76EFA648341C3B489994D5B35B3257BD', 1, NULL, NULL, 2, NULL, 0, 'Spoon on about a teaspoon of toffee bits crumbs or cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D70481A756A78D4DAB9010F949DA2485', X'76EFA648341C3B489994D5B35B3257BD', X'76EFA648341C3B489994D5B35B3257BD', 2, NULL, NULL, 2, NULL, 0, 'Set three chocolate shells on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EC4D099F032EB4B99153C0A162EA2D9', X'A7946DD3A2B18F4BB58A2D6F7510AF46', X'A7946DD3A2B18F4BB58A2D6F7510AF46', 0, NULL, NULL, 2, NULL, 0, 'Combine white confectionery coating and peanut butter chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2ECE689882A6E4ABA2FA0414B8B3C0A', X'A7946DD3A2B18F4BB58A2D6F7510AF46', X'A7946DD3A2B18F4BB58A2D6F7510AF46', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0C9E86C2A01644C99F53F243CED0C83', X'A7946DD3A2B18F4BB58A2D6F7510AF46', X'A7946DD3A2B18F4BB58A2D6F7510AF46', 2, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and pinch of salt until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C69C6906B007CA43905BCB919DE25096', X'A7946DD3A2B18F4BB58A2D6F7510AF46', X'A7946DD3A2B18F4BB58A2D6F7510AF46', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly before pouring into your white chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8BC57357E20C34AB8CF973BCDEB465F', X'7FB5F6128536B34BBC57C6F3DC068B86', X'7FB5F6128536B34BBC57C6F3DC068B86', 0, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoon of white chocolate into each baseball mold and brush it up the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42801CA929F63E4084033A810AEE2E27', X'7FB5F6128536B34BBC57C6F3DC068B86', X'7FB5F6128536B34BBC57C6F3DC068B86', 1, NULL, NULL, 2, NULL, 0, 'Refrigerate (or freeze if using candy melts) for about 5 minutes until it sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B87CD980CB1E014BA809D30810E18A29', X'7FB5F6128536B34BBC57C6F3DC068B86', X'7FB5F6128536B34BBC57C6F3DC068B86', 2, NULL, NULL, 2, NULL, 0, 'Fill each mold with fudge and chill until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7B51D1A17506F429297F0F84499F0DA', X'7FB5F6128536B34BBC57C6F3DC068B86', X'7FB5F6128536B34BBC57C6F3DC068B86', 3, NULL, NULL, 2, NULL, 0, 'Top with a thin coating of white chocolate and chill until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB4251C37C2CD744A5B030CCC4A3887B', X'7FB5F6128536B34BBC57C6F3DC068B86', X'7FB5F6128536B34BBC57C6F3DC068B86', 4, NULL, NULL, 2, NULL, 0, 'Draw on details using a red food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C3E4BA2FC62954A84094E83E1022EE6', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7D4744261EDE246AD88556706597418', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 1, NULL, NULL, 2, NULL, 0, 'Line baking pans with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BD994CCA05B9044A238E259EE78FD00', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 2, NULL, NULL, 2, NULL, 0, 'CAKES:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3441645FDC9FE44835A45B511998C63', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 3, NULL, NULL, 2, NULL, 0, 'In a mixing bowl combine red velvet cake mix, eggs, oil and water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'391311423BBF21468D30C3AFA03A8E97', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 4, NULL, NULL, 2, NULL, 0, 'Mix until thoroughly blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E138159622F6704295E4CD036AC41979', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 5, NULL, NULL, 2, NULL, 0, 'Spoon some batter into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88BEC9135C84824F8C456AFE68C8D49E', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 6, NULL, NULL, 2, NULL, 0, 'Pipe out lobster shapes on baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AABC4F4E2F414A41B14778335783DA76', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 7, NULL, NULL, 2, NULL, 0, 'Bake for 5-6 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B6374376811034BB08E2C7D810E6FA1', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 8, NULL, NULL, 2, NULL, 0, 'Cool for 2 minutes, then remove from pan and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'779280292A0B6047BE167EE99A0D6EA7', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 9, NULL, NULL, 2, NULL, 0, 'FILLING:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92CEAAC5428DAD4F98983C7614A70A0D', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 10, NULL, NULL, 2, NULL, 0, 'Beat shortening, powdered sugar, marshmallow, vanilla, and whipping cream until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'014428E6E7C54D4F950C9004362941C4', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 11, NULL, NULL, 2, NULL, 0, 'Pipe over half of the lobster shaped cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'647C0F444F20A94C8B4DD76452DBCDD0', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', X'F4CB100F72E9AB4ABE03E1ED0D3B2D7F', 12, NULL, NULL, 2, NULL, 0, 'Top with another lobster shaped cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4471B7829065C14A9B848D7CDB1FFCA6', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 0, NULL, NULL, 2, NULL, 0, 'VANILLA CUSTARD NO CHURN ICE CREAM-');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B3DAF8749A45D49845105A7D0C78DDC', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 1, NULL, NULL, 2, NULL, 0, 'Whisk heavy whipping cream and Vanilla Custard flavoring to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E721B0F619E6DD469D9C9E6CA0320635', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 2, NULL, NULL, 2, NULL, 0, 'Stir in sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E475E43D90EEC449A332451FBBA4933', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 3, NULL, NULL, 2, NULL, 0, 'PANDA BEAR DECORATIONS-');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A145273FAA6E204892DD73BDE2B0CC28', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 4, NULL, NULL, 2, NULL, 0, 'Heat candy melts on high in the microwave for 10-20 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C097F3D9432FEB478020D6C23BE49DED', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 5, NULL, NULL, 2, NULL, 0, 'Pour candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE185366B318A940B158BAAA734AB9F7', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 6, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment paper on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C55245B0D2068409AA66A4512B2A1E9', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 7, NULL, NULL, 2, NULL, 0, 'Pipe 10 ears, 10 eye patches, 5 noses, and 5 smiles for each panda bear cone onto the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71BA9AED4D85F943A8A65B92913DA34D', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 8, NULL, NULL, 2, NULL, 0, 'Freeze for about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D39D2AFC1566F74CA6940919FE68F1E3', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 9, NULL, NULL, 2, NULL, 0, 'Let sit for 10 minutes, then peel off paper and turn over.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'324E327D3B517745AC067C489D71FC86', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 10, NULL, NULL, 2, NULL, 0, 'Use melted candy coating to attach a candy eye to each eye patch.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2EB7D33984F2945AB056695FACDFEDE', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 11, NULL, NULL, 2, NULL, 0, 'ICE CREAM CONE ASSEMBLY-');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2949B11F620DFC438FBC882FAC679564', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 12, NULL, NULL, 2, NULL, 0, 'Fill 5 ice cream cones with ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B833C791370694B9B77678495774A40', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 13, NULL, NULL, 2, NULL, 0, 'Top each with a rounded scoop of ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE4F7077CECD984D8A7A5EF4A343D21D', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 14, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E319E057635FEF4AAFD1B00CD9F17C4B', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 15, NULL, NULL, 2, NULL, 0, 'Remove one cone at a time and insert 2 ears, 2 eyes, 1 nose and 1 smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09BEF185A94A764D91656044895A3582', X'DDBAE227CC626C4DAFADFE670E0DD7C1', X'DDBAE227CC626C4DAFADFE670E0DD7C1', 16, NULL, NULL, 2, NULL, 0, 'Freeze until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C967C178A4C0A44AA8404EF932F4D8A8', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 0, NULL, NULL, 2, NULL, 0, 'Line baking sheets with non-stick tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'342589CFE3516E45A9EF6AF5B8568440', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 1, NULL, NULL, 2, NULL, 0, 'Spray a star shaped cookie cutter with cooking spray. Set cookie cutter on baking sheet. Spoon chicken into cutter, press down into a 1/2 inch thick layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F010C2C85937574FA5DBBA07C45374F6', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 2, NULL, NULL, 2, NULL, 0, 'Remove cutter and repeat making about 22 stars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35D7737C355D9D44A12A74B7AEBAAA99', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 3, NULL, NULL, 2, NULL, 0, 'Pinch the pointed ends of each star, reshaping them to look like starfish legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D5F1699976FE04FA937181648263B5B', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 4, NULL, NULL, 2, NULL, 0, 'Freeze for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'685624366538B94DBCDD97E917707FAD', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 5, NULL, NULL, 2, NULL, 0, 'Crush potato chips into fine crumbs, either using a food processor or a rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBB5E8FE5B48DA4A855A073A2F471959', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 6, NULL, NULL, 2, NULL, 0, 'Pour crumbs into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80152516F14CE54AB825F4E08F1E4569', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 7, NULL, NULL, 2, NULL, 0, 'Blend together eggs and French onion dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B408ACB8417A84E975005652923F719', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 8, NULL, NULL, 2, NULL, 0, 'Pour into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0135AA18FA81B4B9D702783D42F6641', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 9, NULL, NULL, 2, NULL, 0, 'Combine flour and salt and pour into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEB56774570DF74EBDC4517AE104AC4A', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 10, NULL, NULL, 2, NULL, 0, 'Dip one frozen starfish chicken patty into the flour then pat off excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'139B414D8756344A8B8BF32E214BF7B8', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 11, NULL, NULL, 2, NULL, 0, 'Dip into egg mixture allowing excess liquid to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'807C5E4F0968C048A241824E66CA0F75', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 12, NULL, NULL, 2, NULL, 0, 'Coat in potato chip crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A1B0F3B30330D46AD5E49ED4227EDF8', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 13, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4149128C85AC446A02B1D6EBCA80693', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 14, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6D7BB5092823F43AE6B221B8A32AD93', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 15, NULL, NULL, 2, NULL, 0, 'Heat oil to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4058FA53E3A3E046B2F6A725ADC53162', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 16, NULL, NULL, 2, NULL, 0, 'Fry starfish until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82AB453FCE0ECA4ABEAE38C0C2B63BC4', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 17, NULL, NULL, 2, NULL, 0, 'Set starfish on a cooling rack set over a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2007A66DB198E341BC28ACE70EE04FB6', X'D89725505DC7AF4ABF1E90585EAA069A', X'D89725505DC7AF4ABF1E90585EAA069A', 18, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until cooked through and a thermometer inserted into the chicken registers 165 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'795C0E97A21BA94DB4AFDDE1B686D4B0', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 0, NULL, NULL, 2, NULL, 0, 'Grease small 1 1/2 to 2 1/2 inch star shaped cookie cutters with butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F607DF189D0094990820DDC57F954E0', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 1, NULL, NULL, 2, NULL, 0, 'Cut marshmallows with star shaped cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A6B8223CABD8F488D67469439AF4D52', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 2, NULL, NULL, 2, NULL, 0, 'Cut stars in half to make 2 thin stars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A229C9D56478549BF87302BD741D3EC', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 3, NULL, NULL, 2, NULL, 0, 'Melt and temper dark chocolate, if using pure chocolate, or melt candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80E7901BE1CCD347831AAEF6D7074782', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 4, NULL, NULL, 2, NULL, 0, 'Dip a marshmallow star in chocolate then coat with graham cracker crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB3938E215529D46BD77D75A5F24CBA4', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 5, NULL, NULL, 2, NULL, 0, 'Set dipped marshmallow on a parchment paper lined baking sheet and allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE445DD3386D9D45B9FD7E6D39FD5CA0', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 6, NULL, NULL, 2, NULL, 0, 'Repeat, creating 60 starfish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0D299015F36754FB687821FE9C31D40', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 7, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes to each using the remaining chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'066AED39258111498433E65DB0743838', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', X'2B42627AAEF0E54CB2DAB8B2DA281EF6', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to a month.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D677C8A763717408E868C031605D697', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside. Cream butter and sugar until light and fluffy. Stir in egg, vanilla, almond extract, and yellow food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C0919C60982D94BB9B2E7EF5603B470', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 1, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined. If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9B2997D60ECF040B95892F795E816BD', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 2, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface or non-stick mat with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C665165F7C444A468B4ACB5D0CCA837E', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 3, NULL, NULL, 2, NULL, 0, 'Roll the cookie dough out to between a 1/16 inch and 1/8th inch thickness, cut 17 or 18 circles using a round cutter, and drape circles of dough over each mound on your pan and smooth with your fingers. Freeze pan for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01339564EB91984AB81DBCABA010F083', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 4, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degree oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13DD263F4942E6468D495B62F226B0A1', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'805F8C620A74084684E8F7835B6626DE', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 6, NULL, NULL, 2, NULL, 0, 'Roll out the remaining dough and cut out 17-18 sunshine shapes. Set them on parchment lined baking sheets, refrigerate for 15 minutes, then bake for 10-14 minutes until the tops look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70926282AB2105488347300F4A634532', X'45CEB0E6613C424A8657379D9C01C50D', X'45CEB0E6613C424A8657379D9C01C50D', 7, NULL, NULL, 2, NULL, 0, 'Cool cookies completely.');
