INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F4EA650103F264981ED0088CDBD11BA', X'B086BC358A8654449DA95707B529839F', X'B086BC358A8654449DA95707B529839F', 4, NULL, NULL, 2, NULL, 0, 'Divide candy into small bowls and add food coloring, mini chocolate chips or cocoa powder to make a variety of `ice cream flavors.`');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'824340BBC723C14DAC33A0B3EEAB5B90', X'B086BC358A8654449DA95707B529839F', X'B086BC358A8654449DA95707B529839F', 5, NULL, NULL, 2, NULL, 0, 'Pour the semi sweet chocolate and heavy whipping cream into a small microwave safe bowl. Heat it in the microwave for 30 seconds. Let it sit in microwave for 2 minutes, then remove and stir. If the chocolate is not completely melted, heat it for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A54CB3A334A0484AB1743992B3B1CDF5', X'B086BC358A8654449DA95707B529839F', X'B086BC358A8654449DA95707B529839F', 6, NULL, NULL, 2, NULL, 0, 'Pour chocolate ganache into a squeeze bottle. Fill 16 mini ice cream cones abut 2/3 full of ganache. Fill the rest of the cone with creamy coconut candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50D2DD83DFCAFD44AC3E7AB4390D331A', X'B086BC358A8654449DA95707B529839F', X'B086BC358A8654449DA95707B529839F', 7, NULL, NULL, 2, NULL, 0, 'Scoop about a heaping tablespoonful of the creamy coconut candy and set it on top of the cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB19D09D2944AC41AF42C9D37FDAC745', X'B086BC358A8654449DA95707B529839F', X'B086BC358A8654449DA95707B529839F', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to a week.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FEE84837EB6964CBDFFDF00260F73F4', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 0, NULL, NULL, 2, NULL, 0, 'Crumble cake and mix in the vanilla and chocolate frosting until mixture holds together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64EE262F61E2ED4395A1EC8BE28EBFB1', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 1, NULL, NULL, 2, NULL, 0, 'Scoop heaping tablespoonfuls of the dough and roll into hot dog shaped logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C37936A4AF9A834BBD8B8E42BFC5BED4', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 2, NULL, NULL, 2, NULL, 0, 'Combine the peanut butter chips and 6 ounces of the white confectionery coating in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05A1BF325C1DFA4C950905CEAF729AA7', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 3, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21E84A0389CDF34B8916D626E23EA5CD', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 4, NULL, NULL, 2, NULL, 0, 'Thin with paramount crystal if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1CB92D07251684CBED09100E243A741', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 5, NULL, NULL, 2, NULL, 0, 'Stir in graham cracker crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'631D198127802B40B174C2956DE7757C', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 6, NULL, NULL, 2, NULL, 0, 'Dip the end of a lollipop stick into the melted candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61E77306AE7A2B46B54BD9F9BE8DD5ED', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 7, NULL, NULL, 2, NULL, 0, 'Insert the stick into one end of a cake pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58BE9CAB727C304FA61FEE23B6BE32B2', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 8, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11356775F8640846B128A86B787BBB12', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 9, NULL, NULL, 2, NULL, 0, 'Freeze the pops for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A3ECADD330E1E49AF8E8965440E3FC4', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 10, NULL, NULL, 2, NULL, 0, 'Pour the peanut butter candy coating over each `hot dog.` then twirl it around a bit over the bowl, allowing the excess candy coating to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'801567320E175C48AC64E026923B980F', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 11, NULL, NULL, 2, NULL, 0, 'Melt your remaining 2 ounces of white confectionery coating then color using yellow candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07E520079D2E29458FE961E0605010BE', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 12, NULL, NULL, 2, NULL, 0, 'Pipe it over the cake pops to resemble mustard.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C34F59EC3E457A43801B645E80E1BEE1', X'AB9D5CE8EAB6F64380FBD8B17193AB81', X'AB9D5CE8EAB6F64380FBD8B17193AB81', 13, NULL, NULL, 2, NULL, 0, 'Freeze for 2-3 minutes until the `mustard` is set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD32950DF61F5D4F86F0484F608ACEDF', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 0, NULL, NULL, 2, NULL, 0, 'Prehaat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74804D10890FBE479089B8B36E06CEEC', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 1, NULL, NULL, 2, NULL, 0, 'Press cookie dough into round pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A209FA971864B840913D716838F458D7', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 2, NULL, NULL, 2, NULL, 0, 'Lightly spray pizza pan with non-stick baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A34ED19B6BC7141BC9B5A5B3838C3D4', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 3, NULL, NULL, 2, NULL, 0, 'Use a paper towel to dab off any excess spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AF74187E8797D4681AE9606943E7C80', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 4, NULL, NULL, 2, NULL, 0, 'Press cookie into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'111FA8E19D5D3543817F6269D2B16214', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 5, NULL, NULL, 2, NULL, 0, 'Bake for 14-16 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F095F74393C98948A3F951A0BB19B052', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 6, NULL, NULL, 2, NULL, 0, 'Allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EE3D734BAB89149A5FF89A85D8BBE1E', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 7, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine Philadelphia Ready to Eat Cheesecake Filling and lemon curd.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B2EDE9005C3AD4384FD35EEC7A83C58', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 8, NULL, NULL, 2, NULL, 0, 'Fold in whipped topping.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBBA73BDB1D1AC46AE798530FDFF9C45', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 9, NULL, NULL, 2, NULL, 0, 'Set the cookie upside down on a large cutting board or serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F7F85DF4F996647891584CEAAD66B77', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 10, NULL, NULL, 2, NULL, 0, 'Spread lemon cheesecake mousse in an even layer over entire surface of cookie using an offset spatula or rubber spatula.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49DB6943515ED548860D261B78E3A89C', X'0E95F7EE28974B4A8EC02E54B1BE9030', X'0E95F7EE28974B4A8EC02E54B1BE9030', 11, NULL, NULL, 2, NULL, 0, 'Arrange fruit around cookie in a beach ball pattern (using the tutorial instructions).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F749D918F3A0C418C11CF6910265AB0', X'C9CCEBE693F45242B438750158F8F448', X'C9CCEBE693F45242B438750158F8F448', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese and sugar until light and fluffy and substitute it for the cream cheese filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB2E3C9290C4334294195855E8484D2E', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 0, NULL, NULL, 2, NULL, 0, 'Pour candy melts into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FF9DE18B3E1DE4FB9276E633E0C3FD5', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E30867341C5C14E89A124FE4C485652', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 2, NULL, NULL, 2, NULL, 0, 'Heat for another 30 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41BDF62A5E30D444BCF3B7ADC1B196DC', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 3, NULL, NULL, 2, NULL, 0, 'Then heat for 20 seconds, then let the candy coating sit in the microwave for 2 minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2F5C6F6BC53664CB277EEAF209C3693', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 4, NULL, NULL, 2, NULL, 0, 'If all the wafers have not melted, heat for 10-second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4110747ADB86348B3C7089D64C3E498', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 5, NULL, NULL, 2, NULL, 0, 'If using white candy melts, stir in some yellow candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6DD6DC9ACE8F64B8927139227D07DAF', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 6, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment paper on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B972D96B66A95341B4179CB870DF2714', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 7, NULL, NULL, 2, NULL, 0, 'Arrange the pretzel rounds on the baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A8B55FA77BAF6459DD5007DE1597CD8', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 8, NULL, NULL, 2, NULL, 0, 'Pour a few tablespoons of the yellow candy coating into a zip-top bag, and snip off one tip making a small hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41AD58FE3A084E44B3D46792F785BA6F', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 9, NULL, NULL, 2, NULL, 0, 'Pipe the yellow candy coating into a few of the pretzels, then immediately, place two mini chocolate chips in the wet coating to create the smiley faces eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC35E4943201E847B65505D82F1B92EC', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 10, NULL, NULL, 2, NULL, 0, 'Continue filling pretzels and adding chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96730298D644DC41BD8E8E6AA5CF1F54', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 11, NULL, NULL, 2, NULL, 0, 'Pop the tray in the freezer for about 3 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD24AF4818A3C04A895ACC6499B1853A', X'1E4D8D9E2A5F72408E873BAE36519BA7', X'1E4D8D9E2A5F72408E873BAE36519BA7', 12, NULL, NULL, 2, NULL, 0, 'After you remove the pan from the freezer, allow the pretzels to come to room temperature for about 10 minutes, then use a black food coloring marker to draw on smiles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5928AD3B558B54FA8FA5AB62A451CB3', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 0, NULL, NULL, 2, NULL, 0, 'Unwrap peanut butter cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'100336F1900A5941975009E84A07BBE5', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 1, NULL, NULL, 2, NULL, 0, 'Unwrap caramels and soften them by heating in the microwave on defrost for 5 second increments, kneading after each, until soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AAD2915AEC5C047AB39D65D50E2BF74', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 2, NULL, NULL, 2, NULL, 0, 'Flatten 6 of the caramels, and cut using a 1 - 1 1/2 inch heart cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E54D916BB07B345BA528928A074D525', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 3, NULL, NULL, 2, NULL, 0, 'Cut 12 small circles using a small round plunger cutter or pinch off pieces and roll into small balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DEB18675C41AC43B3F1F4107B0533DA', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 4, NULL, NULL, 2, NULL, 0, 'Press one red heart sprinkle onto each caramel heart forming the lion''s nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10EC88BCD71CCA4D96D8FD998407EFE2', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 5, NULL, NULL, 2, NULL, 0, 'Break the remaining 3 heart sprinkles in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4076B5B341F1442A83A50D4EE5ADA0C', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 6, NULL, NULL, 2, NULL, 0, 'Attach one piece with the rounded side facing down, under the curved part of the caramel heart, creating the lion''s tongue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'901E4D7B31D7E74DBE30857EC52BC036', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 7, NULL, NULL, 2, NULL, 0, 'Draw eyes, eyebrows, and freckles onto each caramel heart using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04AE455ED9CB314DBEF037830520FD94', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 8, NULL, NULL, 2, NULL, 0, 'Attach one caramel heart lion''s face and two caramel ears to each peanut butter cup using melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E5DFB52EECEF14A923678F75CDFB994', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 9, NULL, NULL, 2, NULL, 0, 'Cut the remaining caramel squares in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'959096E0D328664F8081E9DEEB8C704E', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 10, NULL, NULL, 2, NULL, 0, 'Roll each into a ball then into a teardrop shaped paw.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32094D55B1A1F94E9C1E9462D24920D5', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 11, NULL, NULL, 2, NULL, 0, 'Draw 5 claws onto each paw using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D16BECA64F268447B4A9C360C8FA5BF2', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 12, NULL, NULL, 2, NULL, 0, 'Wrap each cupcake in a printable `You Make My Heart Roar` cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5BF4535F241274498C31E886E3053B4', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 13, NULL, NULL, 2, NULL, 0, 'Spoon frosting into a large pastry bag fitted with a large 1M star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34A96C560D00E04380F93B03F2292B16', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 14, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B96213EE3F2E494D8FCB2FF2B7FA2F7A', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 15, NULL, NULL, 2, NULL, 0, 'Press a Reese''s Cup lion onto the front at the top of each frosting swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFA898276316EE4F904BE7BE992F2CE5', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 16, NULL, NULL, 2, NULL, 0, 'Press a conversation heart onto the frosting just below the lion''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A94D556A023AB144A368F5C5A9154723', X'E159A2F170B3DA47B3C91B0ADF49D188', X'E159A2F170B3DA47B3C91B0ADF49D188', 17, NULL, NULL, 2, NULL, 0, 'Attach one paw on either side of the candy heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57243AD164AD1F459F1A8B327D917941', X'7229CB4D45BDA94C89E4916AC88EC527', X'7229CB4D45BDA94C89E4916AC88EC527', 0, NULL, NULL, 2, NULL, 0, 'Spread blue frosting over the top of your Rice Krispie Treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7464E943A44F0043B6DC259086F41A5E', X'7229CB4D45BDA94C89E4916AC88EC527', X'7229CB4D45BDA94C89E4916AC88EC527', 1, NULL, NULL, 2, NULL, 0, 'Use a fork to spike the frosting to look like fur.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5AAA760DCDEEC46B612EF67A334A507', X'7229CB4D45BDA94C89E4916AC88EC527', X'7229CB4D45BDA94C89E4916AC88EC527', 2, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes at the top of the treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16086440D6E08A4C83C72D7A56D8EF4E', X'7229CB4D45BDA94C89E4916AC88EC527', X'7229CB4D45BDA94C89E4916AC88EC527', 3, NULL, NULL, 2, NULL, 0, 'Flatten the Tootsie Roll Midgees then cut each into two mouths.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'947EBC53ED48604DA5788BBB9995D2E0', X'7229CB4D45BDA94C89E4916AC88EC527', X'7229CB4D45BDA94C89E4916AC88EC527', 4, NULL, NULL, 2, NULL, 0, 'Press one mouth below the candy eyes on each cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5878F573CCF92347A17669C4AF44D943', X'7229CB4D45BDA94C89E4916AC88EC527', X'7229CB4D45BDA94C89E4916AC88EC527', 5, NULL, NULL, 2, NULL, 0, 'If desired, use some icing to attach a mini cookie to the mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35B62596E3BAFD4A85BFFBBDC70B4A2A', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F92B5F01D04B649BDAFBA4C5F6C217E', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 1, NULL, NULL, 2, NULL, 0, 'Either line a 9 inch by 13-inch baking pan with non-stick tin foil or spray it with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB438D286CE8354486A3DA2D545F73D7', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 2, NULL, NULL, 2, NULL, 0, 'Combine cake mix, pudding mix, sour cream, milk, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC08DF7D0DF44144840D297B2F9E6D54', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFABB43561E98A47BF07AD5E58D33196', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 4, NULL, NULL, 2, NULL, 0, 'Increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B15513E0A7FF8141A1E142EA4FD13ED6', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 5, NULL, NULL, 2, NULL, 0, 'Pour into pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'594D4730BB53EB40BFB07930FEB42E42', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 6, NULL, NULL, 2, NULL, 0, 'Bake for 28-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2620217823B1D3408000FA81A42F1021', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 7, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4909BD94B79044296C10D2DA0A78AC6', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 8, NULL, NULL, 2, NULL, 0, 'Crumble the cake into a large bowl, preferably the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE7E652B132AA84DA3BEB0CEE6379671', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 9, NULL, NULL, 2, NULL, 0, 'Add frosting and stir or beat until well blended and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2046D43DE6538E49A8E2068AAC225710', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 10, NULL, NULL, 2, NULL, 0, 'Scoop out 48 tablespoon-size portions of the cake ball dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B4FDF84CA29E3419D7A6515B1F054D2', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 11, NULL, NULL, 2, NULL, 0, 'Roll them into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D089F774EF4504A9976CB93A0D407E9', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 12, NULL, NULL, 2, NULL, 0, 'Set aside to dry for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1772C93AB150D740B0B03B26D2F383E9', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 13, NULL, NULL, 2, NULL, 0, 'Meanwhile, pour the melted candy melts into a squeeze bottle, disposable pastry bag, or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D842ABA3EF039A49A570D51D9732F7FD', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 14, NULL, NULL, 2, NULL, 0, 'Cut the tip off if using a disposable pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7076ADE4BAD44F488CF062235CC17ABE', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 15, NULL, NULL, 2, NULL, 0, 'Set warm fuzzy feet template on a baking sheet and cover with a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA405CBB04A90449B17FD8CEC09147B8', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 16, NULL, NULL, 2, NULL, 0, 'Pipe the melted candy melts over the template creating two feet for each warm fuzzy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D686A42D5854A541A79630553E1540D5', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 17, NULL, NULL, 2, NULL, 0, 'Pop them in the freezer for about 3 minutes just until they harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C56ED9B29E124842973E30F26858FCD2', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 18, NULL, NULL, 2, NULL, 0, 'Pipe antenna onto a piece of parchment paper, adding sprinkles to the tips if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC9EE9F3F7CE4342AF8D32D521CE46A5', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 19, NULL, NULL, 2, NULL, 0, 'Let them harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FDA88B989F6FD4DBE1EB20365CCCD53', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 20, NULL, NULL, 2, NULL, 0, 'Dip the cake balls in melted white candy melts and immediately roll each ball in colored coconut and insert two antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0716019B7116C458BE4F154616C268E', X'1BF08FB79541F94A82150103A4FB7402', X'1BF08FB79541F94A82150103A4FB7402', 21, NULL, NULL, 2, NULL, 0, 'Use melted white candy melts to attach two candy eyes and two feet to each warm fuzzy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F8E0A8AD049564DBBCAA2F0DF9096C8', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 0, NULL, NULL, 2, NULL, 0, 'Melt 12 ounces white chocolate or white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67A729AF89099A4B95979E7EF63E319B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 1, NULL, NULL, 2, NULL, 0, 'Stirring often, allow the candy melts to cool to 91 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE97649693DA7F40A8D5AB4CDFF95C5F', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 2, NULL, NULL, 2, NULL, 0, 'Pour in 1/3 cup of corn syrup and stir just until incorporated. Do not overmix.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39E3D117B08B8145814A48AAF80D26CE', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 3, NULL, NULL, 2, NULL, 0, 'Place the modeling chocolate in a large zip-top bag, flatten it out into a disc, and set it aside for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C1E4AC31F305C4AAFF5EFAD5F2047F2', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 4, NULL, NULL, 2, NULL, 0, 'Break off pieces of the modeling chocolate and knead until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A97E230568A3884C99625B34E359A4BE', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 5, NULL, NULL, 2, NULL, 0, 'Color some white modeling chocolate using pink food coloring and some using black food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'674C83E81E9AD84FB8E88D7E337BE786', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 6, NULL, NULL, 2, NULL, 0, 'Make the pink bright. Then break off about 1/3 of the pink and blend it with more white modeling chocolate to make pastel pink.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AD11A61A411204ABB5449FECC2A99CA', X'82C4EB0A2D6B844181FCCBFFED7BF65B', X'82C4EB0A2D6B844181FCCBFFED7BF65B', 7, NULL, NULL, 2, NULL, 0, 'Place the colored modeling chocolate in a zip-top bag and let it rest for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58152F50054F79488B595F2AC7336A74', X'67FA2F3E6719B84DB8FB76B5E63C6A96', X'67FA2F3E6719B84DB8FB76B5E63C6A96', 0, NULL, NULL, 2, NULL, 0, 'Use a silicone (fondant) rolling pin to roll out some white modeling chocolate to 1/8-inch thickness on a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81D7AAF6ADA4764895E7277465874F92', X'67FA2F3E6719B84DB8FB76B5E63C6A96', X'67FA2F3E6719B84DB8FB76B5E63C6A96', 1, NULL, NULL, 2, NULL, 0, 'Cut out two 7/8-inch circles for each mouse.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E43BD44BCC82ED408C1F15EE14674268', X'67FA2F3E6719B84DB8FB76B5E63C6A96', X'67FA2F3E6719B84DB8FB76B5E63C6A96', 2, NULL, NULL, 2, NULL, 0, 'Roll out the light pink modeling chocolate really thin and cut out two 5/8-inch circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29F618CE92BBD74BACE9089C1B30BA58', X'67FA2F3E6719B84DB8FB76B5E63C6A96', X'67FA2F3E6719B84DB8FB76B5E63C6A96', 3, NULL, NULL, 2, NULL, 0, 'Brush one side of the pink circles lightly with water then press it onto a white circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BC1455BBC2B7D4E82997C51C31787C5', X'67FA2F3E6719B84DB8FB76B5E63C6A96', X'67FA2F3E6719B84DB8FB76B5E63C6A96', 4, NULL, NULL, 2, NULL, 0, 'Press down to flatten the edge of the pink circle into the white circle. This will make the entire ear about 1-inch in diameter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99906E659953B74F858313D4BA941CB8', X'67FA2F3E6719B84DB8FB76B5E63C6A96', X'67FA2F3E6719B84DB8FB76B5E63C6A96', 5, NULL, NULL, 2, NULL, 0, 'Use the same 7/8-inch cutter to cut a small curve off of each ear. This will allow the ear to fit more closely to the edge of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A9809D8AA63ED41AA5AA7DEBA2AA97F', X'6756E26C7B4EEB4183C76B31F88C1486', X'6756E26C7B4EEB4183C76B31F88C1486', 0, NULL, NULL, 2, NULL, 0, 'Roll out the bright pink modeling chocolate to 1/16-inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8357691A192E1C45AA91FA1006012033', X'6756E26C7B4EEB4183C76B31F88C1486', X'6756E26C7B4EEB4183C76B31F88C1486', 1, NULL, NULL, 2, NULL, 0, 'Cut out a 3/8 inch circle and flatten it into a bigger, rounded edge oval. Or use an oval cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95D4DE2ED9209A4482C519EC76E5CC5A', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 0, NULL, NULL, 2, NULL, 0, 'Roll out black modeling chocolate thin and cut out ovals that are about 7/16-inch in length.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F9A12BFFD424D4198D2E23D8A5DE7A7', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 1, NULL, NULL, 2, NULL, 0, 'Reese''s Cup Mice');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4134BFD809245A4ABC07D6EE26008A14', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 2, NULL, NULL, 2, NULL, 0, 'Brush melted white candy melts along the cut edge of one of the mouse ears and press it onto the White Chocolate Reese''s Cup along the rounded edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DC518F4EE7FA24594F1C824445FFA30', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 3, NULL, NULL, 2, NULL, 0, 'Attach the other ear about an inch away along the edge. You want them spaced so that they are at about 10:00 and 2:00 on a clock face.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EDB62EE84921048AF52CAEFA8788297', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 4, NULL, NULL, 2, NULL, 0, 'Use white candy melts to attach a nose and two eyes to each mouse.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E85B19AF3211504193B97A2B086010D9', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 5, NULL, NULL, 2, NULL, 0, 'Then add a dot of white candy melts to each eye, to create highlights.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1B15C477EEAE84F9E72D67602DC1228', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 6, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to draw on whiskers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A62DF8A2B555934D8B231F2B7E71028E', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 7, NULL, NULL, 2, NULL, 0, 'Brush some white candy melts into a 2-inch long triangle (pick) then set a Reese''s Cup mouse over the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7B7665BE25ABF44BD362595D170F0AD', X'87E179C5A14FA244B17061A0F99CEF11', X'87E179C5A14FA244B17061A0F99CEF11', 8, NULL, NULL, 2, NULL, 0, 'Allow the candy melts to harden before picking up your mice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'235E67BA3315EF408EE73087A29EF707', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 0, NULL, NULL, 2, NULL, 0, 'Place a cupcake in a teacup, coffee mug, or paper cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E90B0D1CC7035D4BAFA8B819C2A24544', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 1, NULL, NULL, 2, NULL, 0, 'Heat the Nutella or other chocolate hazelnut spread in the microwave just long enough to make it pourable.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D15CE0377E354F449A8D41A03A588C14', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 2, NULL, NULL, 2, NULL, 0, 'Pour it over the cupcakes and spread it out just to cover the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA104018D1A71C4899660F0307EA5CEA', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on some mini marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9AE0200BE1457459C5D9FA3C3B4B4DC', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 4, NULL, NULL, 2, NULL, 0, 'Pick up a mouse and insert the white chocolate pick into the cupcake allowing the bottom edge of the Reese''s Cup to touch the Nutella spread.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8A4D414DDF9C642B68D7FB080A63109', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 5, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of white modeling chocolate and roll into a teardrop shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5DC3F408847BC498D778869157DE010', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 6, NULL, NULL, 2, NULL, 0, 'Roll over the tip and insert it into the cup, so that it looks like one of the mouse''s paws is holding onto the cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72D41619F61402479D9D037D77833C74', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 7, NULL, NULL, 2, NULL, 0, 'Repeat creating another paw.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E8D1CDD2FA09C43925B32EB9DB44FF8', X'42E7F28115E35C4B88BC63F684CCAA51', X'42E7F28115E35C4B88BC63F684CCAA51', 8, NULL, NULL, 2, NULL, 0, 'Decorate all 12 cupcakes with the Reese''s Cup Mice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF2DC0294E855A49AA600069FFFD835F', X'726BA034AE63C34C85BE3D77E95C8AB2', X'726BA034AE63C34C85BE3D77E95C8AB2', 0, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of the softened butter over a parchment-paper-lined 9-inch by 13-inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E502A750EA839946A44E191B7A2A558D', X'726BA034AE63C34C85BE3D77E95C8AB2', X'726BA034AE63C34C85BE3D77E95C8AB2', 1, NULL, NULL, 2, NULL, 0, 'Pour marshmallows, butter, and red candy melts into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A8E89C0269D4945B2AADBCB2E5A568C', X'726BA034AE63C34C85BE3D77E95C8AB2', X'726BA034AE63C34C85BE3D77E95C8AB2', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for three 30 second bursts of power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7E02B1EA7C52C42AAE1A68DD4A2C558', X'726BA034AE63C34C85BE3D77E95C8AB2', X'726BA034AE63C34C85BE3D77E95C8AB2', 3, NULL, NULL, 2, NULL, 0, 'Stir in some red food coloring so that the marshmallow mixture becomes vibrantly red.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A64B115BEE3D8541A35A91C603E24397', X'726BA034AE63C34C85BE3D77E95C8AB2', X'726BA034AE63C34C85BE3D77E95C8AB2', 4, NULL, NULL, 2, NULL, 0, 'Stir in the cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C94A9191C305F49AC335297B41B444F', X'726BA034AE63C34C85BE3D77E95C8AB2', X'726BA034AE63C34C85BE3D77E95C8AB2', 5, NULL, NULL, 2, NULL, 0, 'Pour out onto the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC617D102A3015469545F895787F22CC', X'726BA034AE63C34C85BE3D77E95C8AB2', X'726BA034AE63C34C85BE3D77E95C8AB2', 6, NULL, NULL, 2, NULL, 0, 'Grease your hands with butter then press the cereal mixture into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09457F19C1C00A4FAACFEFB97F452517', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 0, NULL, NULL, 2, NULL, 0, 'Pour marshmallows, butter, and green candy melts into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB39B1584C86254FAEC7985DC32FE91A', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for three 30 second bursts of power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48081DCB57B45A48B88C6F7E07F5651C', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 2, NULL, NULL, 2, NULL, 0, 'Stir in some green food coloring so that the marshmallows mixture becomes vibrantly green.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'489E56A9CD7E4F4784F71D06EA812EDE', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 3, NULL, NULL, 2, NULL, 0, 'Stir in the cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE3CAD8F8DE79C47B824E4B6B8A2EF42', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 4, NULL, NULL, 2, NULL, 0, 'Use greased hands to press the green cereal into an even layer over top of the red cereal layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'145CD000002EAF4FA81AD37CBA46019E', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 5, NULL, NULL, 2, NULL, 0, 'Turn the pan upside down and allow the cereal treat to fall out onto a silicone mat or a buttered piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D374AC4AFA4744AAB5D72A4217EE5CC', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 6, NULL, NULL, 2, NULL, 0, 'Starting at one of the shorter ends, roll the cereal treat up into a log, pinching together any gaps in the cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2751D498A3C6D14D927E6C4E3B8D26FC', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 5-10 minutes just until the cereal treat has cooled.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1211F8D834B4F74F9D30CAC7E687B2C1', X'444997F1FB7F014292CC0599819EB196', X'444997F1FB7F014292CC0599819EB196', 8, NULL, NULL, 2, NULL, 0, 'Cut into 12-18 slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FD4ABFE43F1A04C8E1FB31D2A75B4EB', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 0, NULL, NULL, 2, NULL, 0, 'Stir all of the cheese ball ingredients together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5301489339638F4E8D25A4A80A737C7E', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 1, NULL, NULL, 2, NULL, 0, 'Place 8 ounces of shredded cheddar cheese in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AA4A1FA4F778A42928A29D58C840564', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 2, NULL, NULL, 2, NULL, 0, 'Scoop out a tablespoonful of the cheese ball mixture and drop it into the bowl of cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B4746ECEE78F944A262FA8E37B28F96', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 3, NULL, NULL, 2, NULL, 0, 'Toss it around in the cheese, pressing the cheese onto it until the entire cheese ball is coated in shreds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0A65F9339DCEB49A57B17E0D71AF63F', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 4, NULL, NULL, 2, NULL, 0, 'Repeat until you just have a tablespoon of filling remaining. You will need this to be the glue to hold the eyes and mouths onto your monsters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD3C19749C8E2247805499AF2EB40B15', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 5, NULL, NULL, 2, NULL, 0, 'Use a small round fondant cutter to cut eyes out of white cheese slices. Draw pupils onto the eyes using a black food coloring marker. For tips on using your markers go here.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'142C3B2241676C4FBF6D2944B5DB1DE2', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 6, NULL, NULL, 2, NULL, 0, 'Break pretzel sticks in half. Stick one, two or three sticks coming out of the top of each mini cheese ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8755D5B9B9DBE488651FAD54A21C5E0', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 7, NULL, NULL, 2, NULL, 0, 'Use some of the remaining cheese ball filling to attach the cheese slice eyes to the pretzel sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B30C8B5D0EDDEE46814C2179365C9A6B', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 8, NULL, NULL, 2, NULL, 0, 'Use the round fondant cutter to cut small little mouths out of the black olive slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A1F47559168DD49AFCB0ABADD8094A9', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 9, NULL, NULL, 2, NULL, 0, 'Use some of the cheese ball filling to attach them to each monster.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AAFC12196C6C047BCD2DAB42E049ADE', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', X'7EA5C20CEEEFF34D9631AC7B0D29FB7A', 10, NULL, NULL, 2, NULL, 0, 'Arrange on a serving platter and share with your guests.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D24F0E56B662C44E80FBB8A6A87C7257', X'68D5DBD29C09B741B0E5695723F5995F', X'68D5DBD29C09B741B0E5695723F5995F', 0, NULL, NULL, 2, NULL, 0, 'Scrape the filling out of 1 Mini Oreo Cookie and discard filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4494FA0809117643A662144742E4960C', X'68D5DBD29C09B741B0E5695723F5995F', X'68D5DBD29C09B741B0E5695723F5995F', 1, NULL, NULL, 2, NULL, 0, 'Insert both mini sandwich cookie halves into the larger Oreo creating Mickey Mouse ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C6AAD89D2F33444BD06DF82878CEAC0', X'68D5DBD29C09B741B0E5695723F5995F', X'68D5DBD29C09B741B0E5695723F5995F', 2, NULL, NULL, 2, NULL, 0, 'Roll out candy clay and cut out decorations.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C40293D01C3AFD4DA148328BF56F06BA', X'68D5DBD29C09B741B0E5695723F5995F', X'68D5DBD29C09B741B0E5695723F5995F', 3, NULL, NULL, 2, NULL, 0, 'Attach candy clay decorations together using piping gel or water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B4CEFA391B4B945B4B2D8E5262B6A6C', X'68D5DBD29C09B741B0E5695723F5995F', X'68D5DBD29C09B741B0E5695723F5995F', 4, NULL, NULL, 2, NULL, 0, 'Attach the candy clay decorations to the Oreo using melted chocolate or candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'750BD58E5410B84CA5420F657DDCA337', X'68D5DBD29C09B741B0E5695723F5995F', X'68D5DBD29C09B741B0E5695723F5995F', 5, NULL, NULL, 2, NULL, 0, 'See Hungry Happenings for step-by-step tutorial for each decorations.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F12F5D6ACB525468FCADC23BEAF150A', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 0, NULL, NULL, 2, NULL, 0, 'Carefully split each cookie and scrape the peanut butter filling out into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CE1166B6C7B264C94EB2CC2522326CA', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 1, NULL, NULL, 2, NULL, 0, 'Combine the creamy peanut butter, butter, cream and salt in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'198B14CBE1C7FA44819DB7F3104F338E', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 2, NULL, NULL, 2, NULL, 0, 'Heat in microwave on high for 30 seconds, stir, heat 20 more seconds, then stir and heat 10-second increments afterward, if needed until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54B57AC6E99D0B4A931101B3D3B0F71E', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 3, NULL, NULL, 2, NULL, 0, 'Stir in the powdered sugar then add the reserved peanut butter filling from the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D1E94C3E4EB4346991031F534978A49', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 30 balls of fudge using a #70 ice cream scoop or spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7223CAAF2949994591830902DFFC214B', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 5, NULL, NULL, 2, NULL, 0, 'Roll into balls and press into a slipper shape on one end of a Nutter Butter Cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDB8125E5FEE2E428DE021E53A116E6A', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 6, NULL, NULL, 2, NULL, 0, 'Create an indentation in each peanut butter fudge ball making it look like a slipper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99FB8CD84AC7B04C8CAB6115C25BB54C', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 7, NULL, NULL, 2, NULL, 0, 'Dip the top party of a cookie in melted pink candy melts and immediately sprinkle with grated pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'296F3D4B5B4C774CB9A37A766D437D1D', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 8, NULL, NULL, 2, NULL, 0, 'Set aside to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DC78BB4127F05468321C957ED45A06D', X'0CCB06A455F50940AA0BFAE2F90F8821', X'0CCB06A455F50940AA0BFAE2F90F8821', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D6C823A6424EA4FAA5491AC45459B92', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E416D1DEA8C48D47826C33DC8F4E95CA', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 1, NULL, NULL, 2, NULL, 0, 'Marbleize white modeling chocolate with half of the brown modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'783F426221BCBD4285DBA46C1FF7A8B0', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 2, NULL, NULL, 2, NULL, 0, 'Roll out to about 1/16th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'283DD56714762E4CA3C7D792880C9BD2', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 3, NULL, NULL, 2, NULL, 0, 'Cut 24 small rounds using a 1 inch round (fluted) cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4377578ACDBF7459B21D8BA65D435EC', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 4, NULL, NULL, 2, NULL, 0, 'Use a pick tool to draw on whiskers, feathering the edges of the circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62FCBDF62CD56540ACD4D6A563FBD734', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 5, NULL, NULL, 2, NULL, 0, 'Roll out the remaining brown modeling chocolate to about 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2152A29370DA484CA907610EB1853C69', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 6, NULL, NULL, 2, NULL, 0, 'Cut out 24 small 1/4 inch rounds using a medium round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'588ECE0839F6E04B8A3446E72825B8AB', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 7, NULL, NULL, 2, NULL, 0, 'Cut each circle into a 3/4 moon, using the plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C72C7A8B5244A24C82FBE5A03406FEB8', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 8, NULL, NULL, 2, NULL, 0, 'Roll out half of the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACF1C8F8FD006D4AA6288F417243D170', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 9, NULL, NULL, 2, NULL, 0, 'Cut out 24 small 1/4 inch circles using a medium round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA93817884D7B044A507D5B84FBB9BFA', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 10, NULL, NULL, 2, NULL, 0, 'Pinch off jelly bean size pieces of the remaining black modeling chocolate and roll it into oval noses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE5EE40B20541A48B60696612B402FA8', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 11, NULL, NULL, 2, NULL, 0, 'Use piping gel as `glue` to attach two whiskers, a nose, and two eyes to the front of each Reese''s cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2607F8A71781804889A85509449A47D6', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 12, NULL, NULL, 2, NULL, 0, 'Attach two of the brown moon shaped pieces to either side of the Reese''s cup for the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91C70DB4814239459F84FE50A435D086', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 13, NULL, NULL, 2, NULL, 0, 'Set the decorated Reese''s Cup otter on the upper half of the frosting swirl on a cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19750F4030482D4695FF3E2ABF0C7DB2', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 14, NULL, NULL, 2, NULL, 0, 'Print out a wave cupcake wrapper, cut it out, then wrap it around your cupcake, taping the ends together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2419017B1FC20344B086869994558679', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 15, NULL, NULL, 2, NULL, 0, 'Press two Whoppers into the frosting just above the top of the waves and set a candy shell in between.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5404495B4E6514AAAE126B53D18BEF2', X'5F3906A4D4BCA44F92700997CFEC0192', X'5F3906A4D4BCA44F92700997CFEC0192', 16, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 12 Sea Otter Cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'040B59454720BD41B8384D9D1BFC7B01', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 0, NULL, NULL, 2, NULL, 0, 'Fill a mini ice cream cone with some chocolate ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF85781689C8464FAB7C36B5112EE1DD', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 1, NULL, NULL, 2, NULL, 0, 'Top the cone with a nice rounded scoop of ice cream then freeze for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBC43DED966DE84BBA0C4055E383E2E3', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 2, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to draw two nostrils and one smile onto a Peanut Butter Candy Melt wafer to create the monkeys snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60A170E0F2A40243B5999A8C1F15A37E', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 3, NULL, NULL, 2, NULL, 0, 'Remove the ice cream cone from the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9E13CB69A7C36468F3036ABB763F848', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 4, NULL, NULL, 2, NULL, 0, 'Hold the two plain peanut butter candy melts in opposite hands.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'796911614927804299650BAC341674AC', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 5, NULL, NULL, 2, NULL, 0, 'Press the candy melts simultaneously into the sides of the ice cream cone, creating the monkeys ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D38559E39956DC4280DB50FC4C426947', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 6, NULL, NULL, 2, NULL, 0, 'Press the peanut butter candy melt snout onto the lower half of the front of the ice cream cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A271A6CD0456CF4D95120E39C6760DAF', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 7, NULL, NULL, 2, NULL, 0, 'Press two candy eyes onto the ice cream just above the snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47D9795B998F0048A690DF1E73482621', X'A17ABF8819CEA8428F9D6BF8DBD3B765', X'A17ABF8819CEA8428F9D6BF8DBD3B765', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D03A45064CA95040B811C5A1B6F2C5E9', X'369D7C963D533D44AAFE073F1B417759', X'369D7C963D533D44AAFE073F1B417759', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84AC41A7DC127C4CAA90D018F3A54A09', X'369D7C963D533D44AAFE073F1B417759', X'369D7C963D533D44AAFE073F1B417759', 1, NULL, NULL, 2, NULL, 0, 'Spray 10 half-pint mason jars with non-stick baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3ADA3BCF844A44BB53C350860D7BFAE', X'369D7C963D533D44AAFE073F1B417759', X'369D7C963D533D44AAFE073F1B417759', 2, NULL, NULL, 2, NULL, 0, 'Make the cake mix using the instructions on the package swapping out root beer for water and adding 2 tablespoons of root beer dessert topper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'361C0863FB034B4CB50E8552C44A47C8', X'369D7C963D533D44AAFE073F1B417759', X'369D7C963D533D44AAFE073F1B417759', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the batter among the 10 Mason jars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0AA2C72CE0CB6498ED2410F7F69C099', X'369D7C963D533D44AAFE073F1B417759', X'369D7C963D533D44AAFE073F1B417759', 4, NULL, NULL, 2, NULL, 0, 'Place the jars on a baking sheet and bake for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98D6247B8231D446819B49A320F98531', X'369D7C963D533D44AAFE073F1B417759', X'369D7C963D533D44AAFE073F1B417759', 5, NULL, NULL, 2, NULL, 0, 'Allow the cakes to cool for 10 minutes, then turn the jars upside down, allowing the cakes to fall out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28EB4D2A398941439536C54EFF23F9C0', X'369D7C963D533D44AAFE073F1B417759', X'369D7C963D533D44AAFE073F1B417759', 6, NULL, NULL, 2, NULL, 0, 'Allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6FACC917890594DBDE44D7C74080A17', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 0, NULL, NULL, 2, NULL, 0, 'Heat the ice cream in the microwave just until it melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20B34212F6C87548B4382A99AF20ED8A', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 1, NULL, NULL, 2, NULL, 0, 'Measure out 1/3 cup of the melted ice cream (that''s all you''ll need).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBBEE7913C23DC4B8961F2735BCC6FA7', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 2, NULL, NULL, 2, NULL, 0, 'Pour in the white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41F635A1AC706D4B87B82087428385B4', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 3, NULL, NULL, 2, NULL, 0, 'Place the bowl back in the microwave and heat on high for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13E5B27A4F45CC4281393BC3B7937D1B', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 4, NULL, NULL, 2, NULL, 0, 'Allow the bowl to sit in the microwave for about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7CAF1A115B1D648934B4868860EB9CA', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 5, NULL, NULL, 2, NULL, 0, 'Remove it from the microwave and stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'992EB832A2D84049B5EAC19E6F375D8B', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 6, NULL, NULL, 2, NULL, 0, 'Set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C5B3ED9BCA7B84BAA1A531620C58D6E', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 7, NULL, NULL, 2, NULL, 0, 'Cut the domed tops off of each cake so that they are all 3 inches tall.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C8A3245D9C9424EA783BD6BB54C8ED9', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 8, NULL, NULL, 2, NULL, 0, 'Use a cupcake plunger/corer, apple corer, or a knife to remove some of the center of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F9D3FA081E70845A45A8A8F652A1B07', X'91E6B7AF7FBBCD469AAC98BE24BF1980', X'91E6B7AF7FBBCD469AAC98BE24BF1980', 9, NULL, NULL, 2, NULL, 0, 'Fill each well with vanilla ice cream ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D24E9698FF96849BFB58403F26794C6', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 0, NULL, NULL, 2, NULL, 0, 'Melt 24 ounces of the white confectionery coating/candy melts in the microwave or in a double boiler.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A1071A324052E448CCAC29DD517D031', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 1, NULL, NULL, 2, NULL, 0, 'If using the microwave, heat on high for 25 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DE52133668F4F45A2866C9371FBA019', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 2, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds, then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBF58B922DAA3644B49F61433AEF4081', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 3, NULL, NULL, 2, NULL, 0, 'Heat for 15 seconds then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'794B7B1430CABF40B1B975A6EB57383E', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10-second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CD75D587AFD8F4E8217E507754090F5', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 5, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool to about 91 degrees Fahrenheit, stirring and scraping the sides of the bowl often.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'610A72C61FB2F746BC5C64484C727643', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 6, NULL, NULL, 2, NULL, 0, 'Pour in the A&W Root Beer Float Dessert Topper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2431836132B20A48BFD6494ACFE2A8E4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 7, NULL, NULL, 2, NULL, 0, 'Stir until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AB9EFEE4B85FB409911E954A248EB0A', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 8, NULL, NULL, 2, NULL, 0, 'The mixture will become thick very quickly, so scrape the bottom of the bowl to incorporate all of the melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98BF394FE14AC2459C1B57AC4480C0E7', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 9, NULL, NULL, 2, NULL, 0, 'Wrap it in a zip-top bag or plastic wrap and allow it to sit at room temperature for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0EC4717B55D194E88BCAECA8CF521B2', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 10, NULL, NULL, 2, NULL, 0, 'When you are ready to use it, knead it until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BC8BEFB42635F449F5CE3A8AFA0705A', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 11, NULL, NULL, 2, NULL, 0, 'Roll out the root beer modeling chocolate on a non-stick baking mat (or on a cutting board lightly dusted with powdered sugar or cornstarch) to about 3/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'849C474AA02077408B6D17CA7DA1A2F0', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 12, NULL, NULL, 2, NULL, 0, 'Cut ten rectangles that are 3 1/4 inches wide by about 8 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37288CDDA9D13B42858D32A4873F9A01', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 13, NULL, NULL, 2, NULL, 0, 'Use a wooden dowel or straw to press indentations about 3/4 inch apart all across the brown strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBB4EBB801CBC74F88B6A0E5D8C49BF2', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 14, NULL, NULL, 2, NULL, 0, 'Leave about a 1/2 border all along one edge. That will become the bottom of your root beer mug.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'746DE0AA9EFC994F910F37B7AA8FB497', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 15, NULL, NULL, 2, NULL, 0, 'Flip the modeling chocolate rectangles over and spread on a thin layer of vanilla ganache, leaving 1/2 inch border all around.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5EBC8A4C510864CA932CE52E2465620', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 16, NULL, NULL, 2, NULL, 0, 'Then wrap that, ganache side against the cake, around a cake, cutting off excess modeling chocolate so the two seams match up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E021295CA45EF347B46FFCEA3A37DA2C', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 17, NULL, NULL, 2, NULL, 0, 'Rub some water along both ends and press the two seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DD797E9CB96C748B20143DEF8E80729', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 18, NULL, NULL, 2, NULL, 0, 'Let the wrapped cakes sit for about 30 minutes, then spoon some vanilla ice cream ganache over the cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EA10BFE4F97D6489A63602429A25F23', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 19, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of the white modeling chocolate, roll into logs and bend to form handles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA9338E17145E742916C8C582CBDDC4F', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 20, NULL, NULL, 2, NULL, 0, 'Allow the handles to dry for at least 15 minutes, then use some clear piping gel, water, or melted white chocolate to attach the handles to the mugs and set something underneath to prop them up until they dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B92D3B19E31BB246BE5BB5D7DA85D765', X'64DC0EA40EF00C439B3A24C1AAE59CD4', X'64DC0EA40EF00C439B3A24C1AAE59CD4', 21, NULL, NULL, 2, NULL, 0, 'Just before serving, spoon whipped topping over the cakes, allowing some to drip down the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE8D853283189C4382686B6016CE62F3', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 0, NULL, NULL, 2, NULL, 0, 'Pour egg yolks and sugar into a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0931AF8EA9E3446AA3EAF8CEFED1B11', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 1, NULL, NULL, 2, NULL, 0, 'Beat with an electric mixer on high speed until fluffy and pale yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F2115A7F4D833418AE65B854B8B935E', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 2, NULL, NULL, 2, NULL, 0, 'Heat cashew milk in a medium saucepan over medium heat until bubbles form.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2BE8E5CC957114E8C36B68332D3EC84', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in chocolate until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'275CEAD1E5D5964DB92BE08DFAC0D9A7', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 4, NULL, NULL, 2, NULL, 0, 'Pour 1/4 cup of chocolate into whipped eggs, stirring constantly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'775B1C89743E5C44B2DC8AD56E5FF42D', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 5, NULL, NULL, 2, NULL, 0, 'Continue stirring and slowly pour in the remaining chocolate milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6E9F42EAD076A4398F13D1C4F3A50B5', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 6, NULL, NULL, 2, NULL, 0, 'Pour back into the saucepan and return to medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C12BB3A7306B664E9F729EE2CF560C99', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 7, NULL, NULL, 2, NULL, 0, 'Stir constantly until thickened and mixture reaches 180 - 185 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1509773A770CF149A350071C0AFDECA2', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 8, NULL, NULL, 2, NULL, 0, 'Pour through a fine-mesh sieve into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A38F4EBA1DA37140992C8E6C3813BAF2', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 9, NULL, NULL, 2, NULL, 0, 'Chill until very cold, at least 3 hours, preferably overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'640CE1E19C779F49A0B631E19C50BD04', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 10, NULL, NULL, 2, NULL, 0, 'Pour into prepared ice cream maker and follow manufacturer''s instructions to chill ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46B3AAE3419FA04DB4C805A578DA1BAD', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', X'943AF4CE3B21C94EBE14F0CB0A3A4A57', 11, NULL, NULL, 2, NULL, 0, 'Scoop into a freezer safe container and freeze until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C98751146C303A42995F3A3B616A9DBC', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 0, NULL, NULL, 2, NULL, 0, 'Ears - Flatten fudge rolls, cut into 1 3/4 inch teardrops. Stretch the pointed ends and bend slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81EB775D62729E4E95DF86CF4DEA4EC0', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 1, NULL, NULL, 2, NULL, 0, 'Tongues - Flatten red taffy roll, cut into 3/4 inch teardrops, stretch the pointed ends, and indent center with a toothpick.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1D1B1D74AB8FB49BDF075BD352649F1', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 2, NULL, NULL, 2, NULL, 0, 'Spotted Ears - flatted vanilla rolls, cut into 1 3/4 inch teardrops. Stretch the pointed ends and bend slightly. Flatten chocolate fudge rolls cut small dots, Attach with water to a white teardrop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0D684625ABC304D81B881E79B8EF847', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 3, NULL, NULL, 2, NULL, 0, 'Fill ice cream cones with ice cream. Add one large scoop on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A842F5787C87A044B4405159B94962BF', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 4, NULL, NULL, 2, NULL, 0, 'Add a small scoop to the base of the large scoop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9056822AE71B1408DE01F51CE3A5D60', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D1DDA8BADCE6B46A52DD7E1C91840B0', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 6, NULL, NULL, 2, NULL, 0, 'Add two candy eyes, one chocolate chip nose, two candy ears and one tongue to each cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BADB231F24F04E449158AB81AA267DAE', X'26C05BEF24C6D946AB3CA73C26075C4C', X'26C05BEF24C6D946AB3CA73C26075C4C', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes, then serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C897BE912C00D419145D8368159B8A4', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 0, NULL, NULL, 2, NULL, 0, 'Mix cake mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D9FE29AC75A6A49B951F99C7911B835', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 1, NULL, NULL, 2, NULL, 0, 'Fold in red, white and blue Jimmies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A3CCF7F66530242A8E036D0D433D246', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 2, NULL, NULL, 2, NULL, 0, 'Bake according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCC67124289427409E8C0190E46E96B7', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 3, NULL, NULL, 2, NULL, 0, 'Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D85743F7E32C442996704546F54D371', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 4, NULL, NULL, 2, NULL, 0, 'Pipe a swirl of white frosting on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A14F1C7F7142645B06ED731541B900C', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle on some stars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D0C69AC826A0B46AE4EC053B463D1A3', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 6, NULL, NULL, 2, NULL, 0, 'Fill wine glasses, goblets, or champagne flutes 3/4 full with M&M''s.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50DAF1A62A42984ABB499B4E7DBC5B6D', X'EBC52EF24BDCC645AB8416264B89BA8E', X'EBC52EF24BDCC645AB8416264B89BA8E', 7, NULL, NULL, 2, NULL, 0, 'Set cupcakes on top of candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C01F40545519049A07DE59D34526D90', X'9ACDCABCD6E5FA479CBCE8E667B6821A', X'9ACDCABCD6E5FA479CBCE8E667B6821A', 0, NULL, NULL, 2, NULL, 0, 'Bake your chicken tenders according to the instructions on the package.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C295CFA73847D94996F957E7BEC3DA50', X'9ACDCABCD6E5FA479CBCE8E667B6821A', X'9ACDCABCD6E5FA479CBCE8E667B6821A', 1, NULL, NULL, 2, NULL, 0, 'You can also make homemade chicken tenders if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'425937EB9448DF439D6806056E0A4090', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', 0, NULL, NULL, 2, NULL, 0, 'Arrange 4 Doritos Nacho Cheese Tortilla Chips like butterfly wings on a plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'060B6EE62074EB4689967424E4F38918', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', 1, NULL, NULL, 2, NULL, 0, 'Add a dab of mustard or dipping sauce in the center, where the points of the chips meet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0468212262AAF46AE888A37D26481C4', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', 2, NULL, NULL, 2, NULL, 0, 'This will help to secure the chicken tender in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEB21450C97A88408D53D6D24DADF841', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', 3, NULL, NULL, 2, NULL, 0, 'Poke two chow mein noodles into one end of the cooked chicken tender forming the antennas.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EFF1EC7F464D04EBCAAA4BAED7FA0E4', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', 4, NULL, NULL, 2, NULL, 0, 'Use the black food coloring marker to draw eyes and a smile on the chicken tender.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'379A829162A83D46800F2F5EEB0C2C32', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', 5, NULL, NULL, 2, NULL, 0, 'Set the chicken tender over the center of the wings with the antennas pointing up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCDEE19DE3E2F24EB40B4EF5BB057438', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', X'FD0C140AFCF58D48A1CC92C18B2DDDEB', 6, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D9FD09EDD8C154AB3C24A345AEDCD58', X'EE8F5F1B7177114FAB5DF0763DD9F75E', X'EE8F5F1B7177114FAB5DF0763DD9F75E', 0, NULL, NULL, 2, NULL, 0, 'Melt the chocolate chips in the microwave at high power for 30-second burst of power, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F8A6AD0664750408CA62CD8A0A5E6A8', X'EE8F5F1B7177114FAB5DF0763DD9F75E', X'EE8F5F1B7177114FAB5DF0763DD9F75E', 1, NULL, NULL, 2, NULL, 0, 'Stir in the almond butter until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96F40B85197C9E49B0B9D00DB2FF9758', X'EE8F5F1B7177114FAB5DF0763DD9F75E', X'EE8F5F1B7177114FAB5DF0763DD9F75E', 2, NULL, NULL, 2, NULL, 0, 'Pour into the lined 8-inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61CCEFA099AB0E49A0CF2FF5281DF731', X'EE8F5F1B7177114FAB5DF0763DD9F75E', X'EE8F5F1B7177114FAB5DF0763DD9F75E', 3, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for about 2 hours until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAA207CD7677B748B8E201AFF9948E1B', X'EE8F5F1B7177114FAB5DF0763DD9F75E', X'EE8F5F1B7177114FAB5DF0763DD9F75E', 4, NULL, NULL, 2, NULL, 0, 'Remove and let the fudge sit at room temperature for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'401EBC2C3007E84A822982820247F25F', X'EE8F5F1B7177114FAB5DF0763DD9F75E', X'EE8F5F1B7177114FAB5DF0763DD9F75E', 5, NULL, NULL, 2, NULL, 0, 'Use a bunny-shaped cookie cutter to cut out fudge bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DD1E65E784A584FAB3698FAA9963E78', X'EE8F5F1B7177114FAB5DF0763DD9F75E', X'EE8F5F1B7177114FAB5DF0763DD9F75E', 6, NULL, NULL, 2, NULL, 0, 'Remelt the chocolate fudge using 10-second bursts of power, stirring after each, then pour the remaining fudge into a smaller pan and repeat the process or pour the fudge directly into some cookie cutters and chill until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F677A55989ED3E48BEAF6D47CA61623A', X'900CFE2ABAA64B4B8B915786F5D89A6F', X'900CFE2ABAA64B4B8B915786F5D89A6F', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CA9F7EF1CAFB84AAE3DB495250451CB', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 0, NULL, NULL, 2, NULL, 0, 'Beat the butter and sugar until light and fluffy using an electric mixer or a wooden spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BC9AACB3C64B04EBC99EA2EF94C0EC6', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 1, NULL, NULL, 2, NULL, 0, 'Mix in the vanilla and egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17D563F2F8A029479A28FE3B21F3CE0F', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 2, NULL, NULL, 2, NULL, 0, 'Then add the dry ingredients and mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8AD2109E954FC469D04AB6C39F6E22A', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 3, NULL, NULL, 2, NULL, 0, 'Dust your work surface with cocoa powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'540A5B6498EA0D4D96BD96561A46A9A5', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 4, NULL, NULL, 2, NULL, 0, 'Roll out some of the cookie dough to about 1/8-inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'761D7680B6AC634199D447E7C15A4BF3', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 5, NULL, NULL, 2, NULL, 0, 'Use your cookie cutter to cut out Mickey Mouse cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'430D4F732C8C754CB9DF74F973D6F3F6', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 6, NULL, NULL, 2, NULL, 0, 'Peel away the excess dough and move the cut-outs to parchment paper or silicone mat-lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB372230AB85B440B9446CB1586F3D0A', X'B0B4E459981E1843BA16B603F733589B', X'B0B4E459981E1843BA16B603F733589B', 7, NULL, NULL, 2, NULL, 0, 'Bake for about 12 minutes until the edges of the cookies feel set and the tops no longer look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3242FDABE1840B4F80E6AA70752CB990', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 0, NULL, NULL, 2, NULL, 0, 'Heat your cream over medium heat to a simmer on the stove, just until bubbles begin to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F447B8E45C22FF49811FE3259F7A4367', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 1, NULL, NULL, 2, NULL, 0, 'Immediately pour the hot cream over the chocolate, making sure it covers all of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5790172EA0F7F94B967FD9A216346BA0', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 2, NULL, NULL, 2, NULL, 0, 'If you have patches of exposed chocolate, gently push it into the cream but do not stir!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3EBDF1849853C4993CF0F8AFA6A2028', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 3, NULL, NULL, 2, NULL, 0, 'Let the chocolate and cream sit, undisturbed for 3 minutes to allow the chocolate to begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCEED8FA3E51174994404E2AA8247FCE', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 4, NULL, NULL, 2, NULL, 0, 'Then begin stirring the mixture in small circles in the center of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'970E60303079FF4D82BF8698AE360869', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 5, NULL, NULL, 2, NULL, 0, 'As the chocolate and cream start to darken and come together you can begin to make your circle bigger.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38F60747B6E5DF4690BA0E68F2D1F98C', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 6, NULL, NULL, 2, NULL, 0, 'Continue stirring until the cream and chocolate come together then widen the circle again and continue until the entire bowl of cream comes together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC7DBAD3106090499509D6D03CEE49AA', X'1F06E90D69BB13428D2C65E3D3672164', X'1F06E90D69BB13428D2C65E3D3672164', 7, NULL, NULL, 2, NULL, 0, 'Add in the corn syrup and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80152F195F692D4F8079499D6950A72F', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', 0, NULL, NULL, 2, NULL, 0, 'Hold one cookie in between your thumb and index finger.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'405F75F45EE24D44A8C51F2379361EBA', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', 1, NULL, NULL, 2, NULL, 0, 'Keeping the cookie flat, dip it into the chocolate ganache coating just the top surface of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C04D1AEFBF995478F2232B2F42B7828', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', 2, NULL, NULL, 2, NULL, 0, 'Lift the cookie out of the ganache and shake it several times until the excess ganache drips back into the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06C737EB5C2933458795644707210191', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', 3, NULL, NULL, 2, NULL, 0, 'Turn the cookie over and set it on your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25F78D6B0865AF4BAC0371A8E827A0AB', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', 4, NULL, NULL, 2, NULL, 0, 'Immediately toss on your galaxy sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0622789F6473C4D9D896DA75A1EAA04', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', X'8DD6CC0085CCCA4A9E5E0F60EE46487C', 5, NULL, NULL, 2, NULL, 0, 'Repeat, decorating all your cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B22E501F111D046B19AE314AEBA7BD2', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 0, NULL, NULL, 2, NULL, 0, 'Dip a clean, food-use-only, fine-tipped paintbrush into the pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C6B80392F5FEE49A97157A5A1BEB982', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 1, NULL, NULL, 2, NULL, 0, 'Dab the candy over the rose cavity in the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96E3E0AA8294CE4BB01AC49421B929F8', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 2, NULL, NULL, 2, NULL, 0, 'Then dip another clean paintbrush into the light pink candy melts and paint on the inner triangle in both of the unicorns ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'380E4450D1CDE84DBE53C3673EE4CC6A', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 3, NULL, NULL, 2, NULL, 0, 'Now begin to paint the mane by dabbing in dark blue or bright purple into the tuft of hair that is closest to the rose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E87CAD55AB251A47A13C4808E0A3FB86', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 4, NULL, NULL, 2, NULL, 0, 'Continue to paint the tufts of hair in this order: light blue, green, yellow, orange, and red.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'593DCBD79D8A8C4AA963EC07AAB1F71E', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 5, NULL, NULL, 2, NULL, 0, 'Use a very fine paintbrush to paint the eyelashes using black or dark cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD63C7101F83A44F8DACFA180DF2771A', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 6, NULL, NULL, 2, NULL, 0, 'Pop the mold into the freezer for about 3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2263E309363D046B4B2A820BD1FF52D', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 7, NULL, NULL, 2, NULL, 0, 'Remove the mold and let it sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96574FF467508340BB560019776B6FF5', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 8, NULL, NULL, 2, NULL, 0, 'Your mold must not be cold when you add in the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A053B3CA809194A999710AC8F57B454', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 9, NULL, NULL, 2, NULL, 0, 'Make sure your white chocolate is not hot to the touch, then fill the entire mold with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA73BC3AA67FEF4F938F398B4BBEFC7C', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 10, NULL, NULL, 2, NULL, 0, 'Carefully invert the mold over your bowl of white chocolate and allow the wet candy to drip out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1135F4DCB4911942BB24B022594768F3', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 11, NULL, NULL, 2, NULL, 0, 'Shake the mold and allow more to drip out until you just have a nice thin coating around the interior of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB5772E2BDD0B6498D8B9CA1C20EAEA2', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 12, NULL, NULL, 2, NULL, 0, 'Use a metal scraper to scrape off the excess so that the edge is nice and clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65F11760868D08408B485D06D81EEDFC', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 13, NULL, NULL, 2, NULL, 0, 'Insert a wooden popsicle stick into the wet white chocolate in the silicone unicorn mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF39DE0803E845489558D3780F075D8B', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 14, NULL, NULL, 2, NULL, 0, 'Immediately insert a wood popsicle stick into the mold while the white chocolate is still wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB1611BEF4BCF249B54DE3892854FCB5', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 15, NULL, NULL, 2, NULL, 0, 'Pop the mold in the freezer for about 5 minutes just until the white candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4294FE00B7B2344687ADF2DC1DCE347F', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 16, NULL, NULL, 2, NULL, 0, 'Step-by-step images showing how to fill a white chocolate unicorn with cake ball filling before covering it in more white chocolate. Then brush edible gold luster dust over the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A72BB0E75B9614DBC01BBCA1099A062', X'1D4ACE9E846E2E40904E9D6D87F05A46', X'1D4ACE9E846E2E40904E9D6D87F05A46', 17, NULL, NULL, 2, NULL, 0, 'Let the mold sit for about 10 minutes to come to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBBAD390AA7AF344AB8F193B7572646F', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 0, NULL, NULL, 2, NULL, 0, 'Cut off the browned edges of the cake then crumble into really small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC0FD34013C4B84BA1080917E50E4973', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 1, NULL, NULL, 2, NULL, 0, 'Stir in at least 1/4 cup of frosting until the cake ball filling is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27E6E61F4C56154AB93513B7A7C52BDD', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 2, NULL, NULL, 2, NULL, 0, 'Press cake ball filling into the mold and, if necessary, under the popsicle stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'764DE45EBED6F94387A534EEA3AFF718', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 3, NULL, NULL, 2, NULL, 0, 'Fill the mold to about 1/8th inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36EB5742F298DB429F9B6663481EDAB2', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 4, NULL, NULL, 2, NULL, 0, 'Spoon white chocolate over top of the cake ball filling and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BD9385886366F4CB07F9542DDD0A105', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 5, NULL, NULL, 2, NULL, 0, 'Scrape over top using a metal scraper to remove all of the excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAF0D2FBD02C4F408FC38CD262223443', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 6, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A06DB7739D03EB468C59C40A39BBC0FF', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 7, NULL, NULL, 2, NULL, 0, 'Then unmold the unicorn and allow it to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7616C08F59C6434085971F70270EC38B', X'21977F9F70E16D45A4DCB11E2F5DB40B', X'21977F9F70E16D45A4DCB11E2F5DB40B', 8, NULL, NULL, 2, NULL, 0, 'Brush a light dusting of edible gold luster dust over the unicorns horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3795790A1A507540BD5EB83E35E07F30', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 0, NULL, NULL, 2, NULL, 0, 'Use a food-use-only paint brush to brush white chocolate in a thin layer over the top of a plastic spork.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79FB8CEDA839A948ADE2DB93A63B1B81', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 1, NULL, NULL, 2, NULL, 0, 'Wipe away any chocolate that drips down the the backside of the spork.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD1EEFE17D537345A7409450A92524A3', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 2, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using tempered pure white chocolate or in the freezer if using confectionery coating (candy melts) for about 3 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF8815115ED7ED43961DA2CF87573981', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 3, NULL, NULL, 2, NULL, 0, 'Allow the spork to come to room temperature then brush on a second layer of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C93A609050654547823FCC440C26DEBD', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 4, NULL, NULL, 2, NULL, 0, 'Chill until the white chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DA042C573C4A949994C05B92F0BFE42', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 5, NULL, NULL, 2, NULL, 0, 'The chocolate should retract from the spork so you can pop it off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2022290C346C084EAFEA37328638A71D', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 6, NULL, NULL, 2, NULL, 0, 'Attach one 1/2 inch candy eye and one 1/4 inch candy eye to each white chocolate spork.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6536688A4FD658409CCCA1656A011F62', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 7, NULL, NULL, 2, NULL, 0, 'Brush rosy red cheeks onto each Forky using edible red luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ADD2CA398F33E44A9548180BD2CD22E', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 8, NULL, NULL, 2, NULL, 0, 'Roll red and blue modeling chocolate into thin ropes and create a mouth, eyebrow and arms for each Forky. See the blog post for detailed instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEDA2CF19772774D9F4380EF6A6B859E', X'C62CA7A28221E14D96C2017FE274E3A3', X'C62CA7A28221E14D96C2017FE274E3A3', 9, NULL, NULL, 2, NULL, 0, 'Attach the mouth and eyebrow to the white chocolate spork using water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B866061D1F3AB4184A29F859106AD18', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 0, NULL, NULL, 2, NULL, 0, 'If desired, fill the bottom of each garbage can with candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D1B508F520CF54BA7A22A19BAFDF274', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 1, NULL, NULL, 2, NULL, 0, 'Place one jumbo cupcake into each 9-ounce disposable cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7261CF9DA4A2EA4B935B2B9E1644B69E', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 2, NULL, NULL, 2, NULL, 0, 'Set the cupcakes down into the garbage cans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACB063AFEBBED74E9FFCAAAABAF1F3A1', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 3, NULL, NULL, 2, NULL, 0, 'Add a scoop of frosting onto each cupcake and spread over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4BEB61596552844A4AD471259E274D0', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 4, NULL, NULL, 2, NULL, 0, 'Break off about 1/3rd of each Pocky Stick and insert the longer piece into each cupcake about an inch from one edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62034F3BADA3954D80D3CB3ECFC86DB8', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle dehydrated marshmallows over the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71005E541EBECF4E8804A8DEE17A8EB2', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 6, NULL, NULL, 2, NULL, 0, 'Set the trash can lid over the Pocky Stick allowing a small opening near the front of the trash can.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DB02BDE8F9C7C4A979CDE5297DF74B1', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 7, NULL, NULL, 2, NULL, 0, 'Insert one white chocolate Forky into the cupcake at the very front edge of the garbage can.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B859143ABB1EA4E8FBBB68B28602E6A', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', X'1B19C6E7B35E604DAD8E2F4F9E8F7746', 8, NULL, NULL, 2, NULL, 0, 'Wrap a red modeling chocolate arms/hands around the spork and press the hands onto the top edge of the garbage can.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95C63486A979CF458F19EC6D74695288', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 0, NULL, NULL, 2, NULL, 0, 'Melt butter and marshmallows in a large saucepan set over low heat then stir in the cereal until evenly coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBA7153A71604C4FB820420C1EE0C8E3', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 1, NULL, NULL, 2, NULL, 0, 'Gently spread into an even layer in a well buttered 9 x 13 inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26E47CB19BB7D343ABF62843D89ABE98', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 2, NULL, NULL, 2, NULL, 0, 'Allow to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'697FF41A239B2F40A81B734747190F34', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 3, NULL, NULL, 2, NULL, 0, 'Cut 12 circles out of the treats using the 2-inch round cutter from the DIY-Lish Decorating Kit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8718375BBB5B0949B781B580EBE5D9FA', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 4, NULL, NULL, 2, NULL, 0, 'Knead then roll out the fondant to about 1/16 of an inch thickness. Mix together, in a 1 to 6 ratio, a small amount of white and brown fondant to create tan fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'109BC6EEDC796D46A36E4E233A8FFC6D', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 5, NULL, NULL, 2, NULL, 0, 'For each bear cut out 1 black circle, 2 black teardrops, and 3 tan teardrops. Attach one tan teardrop to the black circle using water and attach the other tan teardrops to the black teardrops, cutting off the tips, creating the ears. Press two black candies on either side of the tan teardrop creating the eyes. Roll a tiny piece of black fondant into an oval and attach, using water, along the bottom edge of the tan teardrop creating the nose. Use a toothpick to create two nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE24E57D3F98BA449D405C4D6C2CEE28', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 6, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of melted candy melts on the top of a rice krispie treat and attach a fondant bear face on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D39E8EED33424342853B021114DBD946', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 7, NULL, NULL, 2, NULL, 0, 'Brush a small amount of candy melts onto the edge of one of the ears and press it onto the rice krispie treat along the top edge of the bears head. Repeat with the other ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42D85FA14B8C004F92DA52BE729A71B3', X'18DDEA21C39175469F3274B31E48A364', X'18DDEA21C39175469F3274B31E48A364', 8, NULL, NULL, 2, NULL, 0, 'Create more fondant animals using the instructions found in the DIY-Lish Cute Critters Decorating Kit and attach them to the remaining rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47DA9B8F3681F1409E49D53BBF3925E1', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 0, NULL, NULL, 2, NULL, 0, 'Grease a large mixing bowl, rubber spatula, and 9-inch x 13-inch pan with butter or non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7918EDF3AA87A48BC3F7B9120CD726C', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 1, NULL, NULL, 2, NULL, 0, 'Pour the cereal into the bowl and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FC5A775F265344EB08FDC52EF616E40', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 2, NULL, NULL, 2, NULL, 0, 'Begin melting the butter in a medium-large saucepan set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0148105FF8ED6845BC96F2B1495321A7', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 3, NULL, NULL, 2, NULL, 0, 'Add marshmallows and continue to heat, stirring often, until about 75% melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A159D2B500C8474E87D2867364E0F232', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 4, NULL, NULL, 2, NULL, 0, 'Remove from the heat and continue to stir vigorously until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6911980D9CEA3A45BA176B2018918CE1', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 5, NULL, NULL, 2, NULL, 0, 'Pour the melted marshmallows over the cereal and stir to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8AC92E6E7F0F940867CAE6069A645F7', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 6, NULL, NULL, 2, NULL, 0, 'Spread into an even layer in the pan and set aside to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'199D3CEDE9EC6F4292AD15260AB1EE4C', X'E533DC54BE61D341B1C1FBD8C4DB10BC', X'E533DC54BE61D341B1C1FBD8C4DB10BC', 7, NULL, NULL, 2, NULL, 0, 'Cut into twelve 2-inch by 3-inch rectangles, saving the scraps to fill in any craters on the rectangle treats before enjoying as a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A9FC2140409AD4A8F84F7F2AABECAC1', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 0, NULL, NULL, 2, NULL, 0, 'Insert the tip of a lollipop stick into the melted and tempered white chocolate or melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BAC11D5E7A58544A919EFCCEED8796F', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 1, NULL, NULL, 2, NULL, 0, 'Then insert the stick into a cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'260CDC9B6CE36145A22A6D840DAFA4E2', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32491AA43247E74DA52D7F205D099FF4', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 3, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to harden for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4F8ED25C9C10B468EAE7DF376899154', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 4, NULL, NULL, 2, NULL, 0, 'Fill a tall 3 inch diameter glass with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21DC5BD31DEEDD42A48EF459D26A4DC0', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 5, NULL, NULL, 2, NULL, 0, 'Dip a cereal treat into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E02AF839637404FB7204564A17348E4', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 6, NULL, NULL, 2, NULL, 0, 'Lift it out and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CF7C9AD18DD464EA8912F1B7ACD0AD3', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 7, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'878054033E09F445B6A551557C7EA35C', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 8, NULL, NULL, 2, NULL, 0, 'If using pure white chocolate allow the chocolate to dry at room temperature or pop in the refrigerator for about 5 minutes. If using confectionery coating, pop in the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DC0259D2FC22049963C960173B86EAC', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 9, NULL, NULL, 2, NULL, 0, 'Repeat, dipping all 12 rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86AAE71B0BE3EF478652F2ABB1AFED51', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 10, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of the gold modeling chocolate and roll into pointed logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F00B7FB0D970947BFB770AF4B8B41CA', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 11, NULL, NULL, 2, NULL, 0, 'Twist two logs together to create a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BADE4BF71919BD4FB737447093A2EA94', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 12, NULL, NULL, 2, NULL, 0, 'If desired, brush the horns with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'753107B5F257AC459002A36471D2FA57', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 13, NULL, NULL, 2, NULL, 0, 'Roll out the white modeling chocolate to about 1/4 inch thickness and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3B16311607FAB4FA4C618990027E979', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 14, NULL, NULL, 2, NULL, 0, 'Roll out the pink modeling chocolate really thin and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBB6C28F67D5B443B457206A2AF7E252', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 15, NULL, NULL, 2, NULL, 0, 'Use water to attach the inner pink ears (triangles) to the outer white ears (triangles).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A23351259E9E854EBE31491276E19DFD', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 16, NULL, NULL, 2, NULL, 0, 'Dust a silicone rose mold with corn starch.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1BF28C70F65F64989CF225DD5CAC9E7', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 17, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of your colored modeling chocolate and press them into the mold creating various sized roses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A92EDA0E48655C439039F76DD386AD8E', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 18, NULL, NULL, 2, NULL, 0, 'Attach one horn to the top of each of the rice krispie treats and attach two ears on either side using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47B959D0A27B84448A1FAC599E4755C3', X'244D938ABB4E4E40AEC40F10BB275073', X'244D938ABB4E4E40AEC40F10BB275073', 19, NULL, NULL, 2, NULL, 0, 'Then attach 5 flowers on the front, top edge, of each treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DA89BE91F8EF745930DBF171BAD5A4D', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEC846F4CF37C34AB9E64D44B7B32FBB', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', 1, NULL, NULL, 2, NULL, 0, 'Allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A0D56813896774DBA9B28F83C41FC55', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C7B33B8669C3E49B2CB58645FBF2F23', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', 3, NULL, NULL, 2, NULL, 0, 'If desired, allow the white chocolate to puddle at the bottom of the strawberry for a few seconds, then lift up the berry and place it on a clean spot on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08428033FBB00E42BB5BCA8F19814E98', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', X'BBE34FDEC9CCC24AB84CB11029AFDDE8', 4, NULL, NULL, 2, NULL, 0, 'Chill in the fridge (for 3-5 minutes) if using candy melts or let air dry (for about 10 minutes) if using pure white chocolate just until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF0CD374477D6B439959D21BADE08B89', X'25A718E598C52A4A9F27BD61A82DB6DA', X'25A718E598C52A4A9F27BD61A82DB6DA', 0, NULL, NULL, 2, NULL, 0, 'EYES and NOSE: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two small circles and one 1/2 inch heart for each polar bear using a small plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC588C71488D8B4CBD12AEC731634732', X'25A718E598C52A4A9F27BD61A82DB6DA', X'25A718E598C52A4A9F27BD61A82DB6DA', 1, NULL, NULL, 2, NULL, 0, 'EARS: Cut a curved sliver off of two white chocolate wafers for each polar bear or create ears using 1/4 inch thick white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E95900AA6B504E41ABF4981733D5FF1E', X'25A718E598C52A4A9F27BD61A82DB6DA', X'25A718E598C52A4A9F27BD61A82DB6DA', 2, NULL, NULL, 2, NULL, 0, 'BOW: Cut two small hearts and one small circle out of pink or red modeling chocolate. Set the hearts together with the points touching then attach the circle over top using water or clear piping gel to `glue` the bow together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0406DC9F2298124B8D8B46B4058AA57F', X'25A718E598C52A4A9F27BD61A82DB6DA', X'25A718E598C52A4A9F27BD61A82DB6DA', 3, NULL, NULL, 2, NULL, 0, 'Dip the white chocolate coated strawberries into white chocolate again and press a fork up against the chocolate then pull away quickly. Do this all over the berry, over and over again, until spikes of fur start to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC1E46F382A70F4C877B5C02363E577B', X'25A718E598C52A4A9F27BD61A82DB6DA', X'25A718E598C52A4A9F27BD61A82DB6DA', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'444BA46A5B95F647A8EEEBEDE6B941C7', X'25A718E598C52A4A9F27BD61A82DB6DA', X'25A718E598C52A4A9F27BD61A82DB6DA', 5, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add a nose to the tip of the strawberry and two eyes above it. Add a bow if making female polar bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DB1197AD9F58B48BE36B32BB0537D7E', X'87477B671F30F649AB5A6E6A7D40475C', X'87477B671F30F649AB5A6E6A7D40475C', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'304526E4B937C84D89967E87B54E2512', X'87477B671F30F649AB5A6E6A7D40475C', X'87477B671F30F649AB5A6E6A7D40475C', 1, NULL, NULL, 2, NULL, 0, 'EARS: You can cut a small sliver off two dark chocolate wafers to create the ears or you can cut out black modeling chocolate circles (with a curved notch removed) to create the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBACB68C74D77F49B7F3D2B1B57214B2', X'87477B671F30F649AB5A6E6A7D40475C', X'87477B671F30F649AB5A6E6A7D40475C', 2, NULL, NULL, 2, NULL, 0, 'BOW: Roll out red modeling chocolate to about 1/8th inch thickness and cut out two small hearts and one small circle. Set the points of the hearts together then use either water or clear piping gel to attach the circle over the intersection of the points creating a bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62D94F8B2873B343BF11445BF7679603', X'87477B671F30F649AB5A6E6A7D40475C', X'87477B671F30F649AB5A6E6A7D40475C', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4A01F67CD65FE4BBA2F86253B5897B1', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 0, NULL, NULL, 2, NULL, 0, 'Roll some brightly colored modeling chocolate out on a silicone mat or a cutting board that has been lightly dusted with powdered sugar to about 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B289578B697F4C48A323533AB0F0FD01', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 1, NULL, NULL, 2, NULL, 0, 'Cut out twelve 1 1/2 inch circles using a round cookie cutter for the owl''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A42E9C6CDD0A94BB5F5BFFB2F63A47A', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 2, NULL, NULL, 2, NULL, 0, 'Cut a small notch out of the circle using the same round cookie cutter creating the two ear tufts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08703281FACA784792F20E04D4F6FD15', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 3, NULL, NULL, 2, NULL, 0, 'Use a small leaf plunger cutter to cut out two wings from the same color modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BA1D0E85C65FC42B3BE56620BA982EB', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 4, NULL, NULL, 2, NULL, 0, 'Roll out some white, black, and orange modeling chocolate to about 1/16th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'713CAEEF31882940822ACF9D54DF8A08', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 5, NULL, NULL, 2, NULL, 0, 'Cut out twenty four 5/8 inch white circles and 3/8 inch black circles for the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4216351868C3C41860884B143471669', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 6, NULL, NULL, 2, NULL, 0, 'Lightly brush some water on one side of the black circles and attach them to the white circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3B7635010605F4BA99ADD4C8B454082', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 7, NULL, NULL, 2, NULL, 0, 'Attach the eyes to the owl''s body using water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA690905FF1441428686F682384CC3DE', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 8, NULL, NULL, 2, NULL, 0, 'Cut out 12 little triangle shaped beaks and 24 little flower shaped feet from the orange modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0671408553E05D418B39759CA6CEFE8E', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 9, NULL, NULL, 2, NULL, 0, 'Attach a beak just below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84788260903D834D89B97BF5A92BF787', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 10, NULL, NULL, 2, NULL, 0, 'Press the flower shaped feet onto the backside of the owls'' body so that only 3 of the flower petals show below the owl''s body from the front side.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEC029BE5481F149B17BCBB4F2B53EEF', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 11, NULL, NULL, 2, NULL, 0, 'Use water to attach wings on either side of the body.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'389EBC8932A23142B22D2961A2665AC8', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 12, NULL, NULL, 2, NULL, 0, 'Use a curved fondant sculpting tool to create two rows of feathers on the owl''s belly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2A847ABDAF7814DB552B7738C1F2EAE', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 13, NULL, NULL, 2, NULL, 0, 'Repeat creating at total of 12 owls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23C4EC2DB5B0A042AE1B19A7C9991EAF', X'E5D0E689B83C284893AF0F84A51CAA11', X'E5D0E689B83C284893AF0F84A51CAA11', 14, NULL, NULL, 2, NULL, 0, 'Press the modeling chocolate owls onto a frosted cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADE1CC491D79AE40AF2A6766055BA93A', X'9214A029A8215A45B1B5F410C7F0A1D9', X'9214A029A8215A45B1B5F410C7F0A1D9', 0, NULL, NULL, 2, NULL, 0, 'Cut 4 grahams in half lengthwise to create four (5` x 1 1/16`) tops and four bottoms.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2626D42E602E5D43BB8B7546917EB8C1', X'9214A029A8215A45B1B5F410C7F0A1D9', X'9214A029A8215A45B1B5F410C7F0A1D9', 1, NULL, NULL, 2, NULL, 0, 'Cut 8 long (5` x 9/16`) side panels out of 2 graham crackers and 8 short (3/4`) end panels out of the remaining cracker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B8B4A4F3D175D409559993A566DC585', X'9214A029A8215A45B1B5F410C7F0A1D9', X'9214A029A8215A45B1B5F410C7F0A1D9', 2, NULL, NULL, 2, NULL, 0, 'Use the melted peanut butter chips to attach two long side panels and 2 short end panels to the 4 bottom crackers, creating open boxes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D5FB0CC0F7A7149AE6553EB0337EF2F', X'9214A029A8215A45B1B5F410C7F0A1D9', X'9214A029A8215A45B1B5F410C7F0A1D9', 3, NULL, NULL, 2, NULL, 0, 'Draw ruler lines and numbers on the top pieces using a food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0A24BDF68B82440BCED88DC34603379', X'9214A029A8215A45B1B5F410C7F0A1D9', X'9214A029A8215A45B1B5F410C7F0A1D9', 4, NULL, NULL, 2, NULL, 0, 'Fill boxes with Welch''s Fruit ''n Yogurt Snacks and cover with the graham cracker ruler.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D1F4A828A9579468903931E710227B3', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A47E351BF56664C89A8EC7277C237CA', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 1, NULL, NULL, 2, NULL, 0, 'Mix together brownie mix, water, oil, and eggs just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'605D70F84A44064D8E484290DB69688D', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 2, NULL, NULL, 2, NULL, 0, 'Pour into a non-stick tin foil or parchment paper lined 9x13 inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1906C7B4E7C55C4CBCF1862054D10F1B', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 3, NULL, NULL, 2, NULL, 0, 'Bake for 18-22 minutes until the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49E6880003A7EB4F8EB981D4C415B053', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and cool completely for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0FBE9C4C4FB704FADDC48CC27B19387', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 5, NULL, NULL, 2, NULL, 0, 'Remove brownie from pan and set on a cake board or cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAA59200C59EB3449E3EA14CC0373587', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 6, NULL, NULL, 2, NULL, 0, 'Spread Pillsbury Jif Peanut Butter Frosting over about 1/2 of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A265CDBAB2383F4AB29928CD28A61E6A', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle cookie crumbs over the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C52C0FEBDADEEB428F3F5E1AC00D5DC9', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 8, NULL, NULL, 2, NULL, 0, 'Spread Pillsbury Funfetti Aqua Blue Vanilla Frosting over the remaining cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29CFF2D3EDAD634A9FABF2ACD61F0423', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 9, NULL, NULL, 2, NULL, 0, 'Add small dollops of Pillsbury Creamy Supreme Vanilla Frosting over the blue frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69885A4EB81A304080B04950B52F2E3F', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 10, NULL, NULL, 2, NULL, 0, 'Use an offset spatula or spoon to swirl the blue and white frosting together making the frosting look like ocean waves with some peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C004CCCF7BEF8E42B37657420A2517E2', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 11, NULL, NULL, 2, NULL, 0, 'If desired, crafts some candy crabs (see the video and tutorial above for instructions) and set them on the sandy beach.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0FE89B5AAFABE49A75264ECCB82F604', X'6159AF61DF124C41B3B3FE9D23EC1B47', X'6159AF61DF124C41B3B3FE9D23EC1B47', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E6E64D39ACD2D4894DF51F5BD020CF9', X'25A8AA54CE1DAA45B4A6B29F88092CC3', X'25A8AA54CE1DAA45B4A6B29F88092CC3', 0, NULL, NULL, 2, NULL, 0, 'Grease the bottom and sides of a 9` x 13` with butter or cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'419539A70DADDA4397BC1A2BF1711451', X'25A8AA54CE1DAA45B4A6B29F88092CC3', X'25A8AA54CE1DAA45B4A6B29F88092CC3', 1, NULL, NULL, 2, NULL, 0, 'Combine the powdered sugar and corn starch and dust the inside of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'255B69D6EC479440AAFC4AAB3D5D21D2', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over 1/2 cup of water in the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED721DEED72FAA4AB28F486632213679', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 1, NULL, NULL, 2, NULL, 0, 'Let sit 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'976E2FE69127854EB4CC01E5756C5865', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 2, NULL, NULL, 2, NULL, 0, 'Stir together remaining water, sugar, corn syrup and salt in a small saucepan set over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D8543D695F4D348BCC2296BB3B5BCDA', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 3, NULL, NULL, 2, NULL, 0, 'Cook, covered for 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E2DE410AC9C3E4681940B04EF12215A', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 4, NULL, NULL, 2, NULL, 0, 'Remove lid and cook, without stirring until the mixture reaches 240 degrees Fahrenheit (about 7-8 minutes.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2349DB0A5891C846A5B550C122922071', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 5, NULL, NULL, 2, NULL, 0, 'Slowly pour hot liquid into the gelatin with the mixer turned on low speed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2477F610D4D46E48B01A95D3983B510F', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 6, NULL, NULL, 2, NULL, 0, 'Increase mixer speed to high and whip until the marshmallow becomes very thick and cools to a lukewarm temperature, about 12-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCBB254980EA6F488F7960F8419CF476', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 7, NULL, NULL, 2, NULL, 0, 'Add the vanilla and whip just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FDFFC8F8CA242478577C080DD4B9482', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 8, NULL, NULL, 2, NULL, 0, 'Spread the marshmallow into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71E7FD4AFD5B6941835E26DA7997C694', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 9, NULL, NULL, 2, NULL, 0, 'Dust top with powdered sugar/corn starch.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C2F61A2DCB3A34F96B9DCAB863E3B58', X'96760B7BF6240944A6F0264B05FC31D8', X'96760B7BF6240944A6F0264B05FC31D8', 10, NULL, NULL, 2, NULL, 0, 'Let cool for 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2487F873A0C5294B98F19053907AB6C2', X'AB5598BB7A24D2449057B3884E501460', X'AB5598BB7A24D2449057B3884E501460', 0, NULL, NULL, 2, NULL, 0, 'Turn out onto a cutting board, cut into star shapes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5531A542688576469898DDFBD5F7A91C', X'AB5598BB7A24D2449057B3884E501460', X'AB5598BB7A24D2449057B3884E501460', 1, NULL, NULL, 2, NULL, 0, 'Dip in melted chocolate, attach two candy eyes, and coat in crushed graham crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2428111DDAB40A4586CA0B355990DBDD', X'AB5598BB7A24D2449057B3884E501460', X'AB5598BB7A24D2449057B3884E501460', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet and allow the chocolate to harden before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC17F06BE88D9446B1F237A01F7A670C', X'20AFC89369EEAB49BF068E68CA947343', X'20AFC89369EEAB49BF068E68CA947343', 0, NULL, NULL, 2, NULL, 0, 'Combine cake mix, pudding mix, water, oil,egg whites, and sour cream in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3FDA1F719788F4093D41171E905DF33', X'20AFC89369EEAB49BF068E68CA947343', X'20AFC89369EEAB49BF068E68CA947343', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds then increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FB9B98714CD9E4CBD22009A3C44EE54', X'20AFC89369EEAB49BF068E68CA947343', X'20AFC89369EEAB49BF068E68CA947343', 2, NULL, NULL, 2, NULL, 0, 'Equally divide cake batter among 24 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F676CFCED97E6641A147CC7464573CE3', X'20AFC89369EEAB49BF068E68CA947343', X'20AFC89369EEAB49BF068E68CA947343', 3, NULL, NULL, 2, NULL, 0, 'Bake for 15-18 minutes until the cakes spring back when you press in the center of a cupcake or a toothpick inserted into the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B0603D347CF1348AB3A607DD5FE3BE5', X'20AFC89369EEAB49BF068E68CA947343', X'20AFC89369EEAB49BF068E68CA947343', 4, NULL, NULL, 2, NULL, 0, 'Let cool in pan for 10 minutes then remove and set on a cooling rack and cool for another 50 minutes before frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'247010CCF9F4B449BDEC41FF7370FE09', X'950212A5A9EF744194FA4368AEAAB217', X'950212A5A9EF744194FA4368AEAAB217', 0, NULL, NULL, 2, NULL, 0, 'Pipe vanilla frosting fur all over cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FCDF594DD6889468E7283EA1C3D270A', X'950212A5A9EF744194FA4368AEAAB217', X'950212A5A9EF744194FA4368AEAAB217', 1, NULL, NULL, 2, NULL, 0, 'Pipe two swirls for the snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D6B2562F6FE6E468CC0698B4F289DE0', X'950212A5A9EF744194FA4368AEAAB217', X'950212A5A9EF744194FA4368AEAAB217', 2, NULL, NULL, 2, NULL, 0, 'Attach one pink heart nose, six chocolate jimmies whiskers, and two candy eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C525F7D626DF16468C6FAADA271ACAC9', X'950212A5A9EF744194FA4368AEAAB217', X'950212A5A9EF744194FA4368AEAAB217', 3, NULL, NULL, 2, NULL, 0, 'Break one red heart sprinkle into two pieces, so you have one oblong shaped tongue. Insert it under the cat''s snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FF15F76966BF34DB00B1705780FC3D3', X'950212A5A9EF744194FA4368AEAAB217', X'950212A5A9EF744194FA4368AEAAB217', 4, NULL, NULL, 2, NULL, 0, 'Pipe two ears and attach an upside down pink heart to each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4E2872ED487F5419509C14D1BF624B5', X'DDF6A319ACF7A64DBCD4CB1D661F5329', X'DDF6A319ACF7A64DBCD4CB1D661F5329', 0, NULL, NULL, 2, NULL, 0, 'Pipe spikes of chocolate fudge fur all over top of cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'707C9E8FEF6D18499B177935EC3FF3BF', X'DDF6A319ACF7A64DBCD4CB1D661F5329', X'DDF6A319ACF7A64DBCD4CB1D661F5329', 1, NULL, NULL, 2, NULL, 0, 'Pipe taller spikes of frosting to create a snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F026ABA2225094D957CB5AA83A2BE72', X'DDF6A319ACF7A64DBCD4CB1D661F5329', X'DDF6A319ACF7A64DBCD4CB1D661F5329', 2, NULL, NULL, 2, NULL, 0, 'Attach one black candy coated chocolate ball on the snout for the nose, add two candy eyes, and one red tongue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'021235EC79DDB0439F58739E6CE23422', X'DDF6A319ACF7A64DBCD4CB1D661F5329', X'DDF6A319ACF7A64DBCD4CB1D661F5329', 3, NULL, NULL, 2, NULL, 0, 'Pipe on two big floppy chocolate fudge frosting ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AC334BB229F3E42B6D898524D1393CA', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA0D25AB1090AA4A896F8B70968BA344', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB9466B59900E749BD52F4259701BA3E', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F3B55B8E4A08C499E2F8F0818C7D76B', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 3, NULL, NULL, 2, NULL, 0, 'Stir in eggs, vanilla, butterscotch flavoring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'517D7D934393134F876499C95BB2B416', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until its mixed in.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E78C2BBD22DB8F49B2F08262D4E51947', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 5, NULL, NULL, 2, NULL, 0, 'Roll your cookie dough out to 1/8 inch thickness either on a flour dusted cutting board or on a non-stick silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3165B52D2592FB449899455FC648A1F8', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 6, NULL, NULL, 2, NULL, 0, 'Cut out beer mug shaped cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'047045EAADF2D7488F2924258FA62C3F', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 7, NULL, NULL, 2, NULL, 0, 'Cut a rectangle out from the center of each much (allowing about 3/8 inch border around the bottom and sides of the mug.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF6C080A17886E4A96220F3D14D95806', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 8, NULL, NULL, 2, NULL, 0, 'Use a small half moon shaped cutter to cut out the inside of the handle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BD6851A94D16D4083D3819214B13652', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 9, NULL, NULL, 2, NULL, 0, 'You should get 16-18 cookies out of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D009C7A208ECF46ACA73550C67E1D0C', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 10, NULL, NULL, 2, NULL, 0, 'Remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01D86F392E0BB54ABD1F07338D1D4056', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 11, NULL, NULL, 2, NULL, 0, 'Freeze the cookies for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40165DD3D95C5D48836811F52DE58E7C', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 12, NULL, NULL, 2, NULL, 0, 'Set the cut-out cookies on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5718104C9858464CA71BC14DCA4743D7', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 13, NULL, NULL, 2, NULL, 0, 'Bake for 8-9 minutes until the cookies look set (dull, no longer wet).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26A01ABD046A1F4BBF79DEED13412564', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 14, NULL, NULL, 2, NULL, 0, 'Remove from oven and sprinkle butterscotch candy crumbs in the rectangle hole, filling it 3/4''s full.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD1C40C788D64C4C8E65586074F9D7BE', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 15, NULL, NULL, 2, NULL, 0, 'Return to the oven and bake for approximately 3 minutes until the candy is melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC24FC24536C5C4AA05423573539DD3A', X'56CFB2657AF3834BA6F15BFD68A163DB', X'56CFB2657AF3834BA6F15BFD68A163DB', 16, NULL, NULL, 2, NULL, 0, 'Remove and allow the cookies to cool completely on the cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E41B9A98078EE24D87BEB3083F936823', X'80A596E5A5FE2241A68B41FC2E37CEAD', X'80A596E5A5FE2241A68B41FC2E37CEAD', 0, NULL, NULL, 2, NULL, 0, 'Beat all ingredients together until icing forms peaks (7-10 minutes at low speed with a heavy-duty mixer, 10-12 minutes at high speed with a hand-held mixer).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FA70EB28BA02444944A53B31A70548D', X'80A596E5A5FE2241A68B41FC2E37CEAD', X'80A596E5A5FE2241A68B41FC2E37CEAD', 1, NULL, NULL, 2, NULL, 0, 'If icing is too thick, add more cream soda, a few drops at a time, until you get thick icing that is easy to pipe.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1690993F633336438EF6543CC6C90772', X'80A596E5A5FE2241A68B41FC2E37CEAD', X'80A596E5A5FE2241A68B41FC2E37CEAD', 2, NULL, NULL, 2, NULL, 0, 'Pour icing into a pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EACCD4F41B1DB6438EC2847F3F6543DC', X'80A596E5A5FE2241A68B41FC2E37CEAD', X'80A596E5A5FE2241A68B41FC2E37CEAD', 3, NULL, NULL, 2, NULL, 0, 'Pipe icing on the cookies creating the butterbeer foam.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3A42DDDD80BD443A90AA56789C2D1C0', X'80A596E5A5FE2241A68B41FC2E37CEAD', X'80A596E5A5FE2241A68B41FC2E37CEAD', 4, NULL, NULL, 2, NULL, 0, 'Allow the icing to dry completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7AF66E9B513A445939FC588855D6D60', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'681D381683384B49A69E74DC7D3FD08C', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B035B5CD26B2274CBB2CE4501E427B76', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 2, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE16E2571D4B68429FB2E5CD49F17EF6', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 3, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C4C9EFE90C78F4AB5C71FDF17465F64', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 4, NULL, NULL, 2, NULL, 0, 'Stir in eggs, vanilla, and almond or peppermint extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FB6CB64F70954449076BC89203237B4', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 5, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until it''s mixed in.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9D84585BDC8DE4ABE916720851BE0DC', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 6, NULL, NULL, 2, NULL, 0, 'Roll out dough to 1/4 of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FADFF1E45BFC3E499542A50D320D2A5F', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 7, NULL, NULL, 2, NULL, 0, 'Cut one large 2 1/2 inch circle then place the smaller, 1 3/4 inch round cutter in the center of the cookie and cut the smaller circle out of the larger circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB0C2AAF06FB704F82A5C2DD696C6D37', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 8, NULL, NULL, 2, NULL, 0, 'Remove the center piece.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'079604DCFB22FF4B90E9470F38AAA726', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 9, NULL, NULL, 2, NULL, 0, 'Continue cutting out cookies, removing excess dough and re-rolling as needed to create 36 cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BFA3C383B430B429E925A38AA779F53', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 10, NULL, NULL, 2, NULL, 0, 'Freeze cookies for 30 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63C09AFBAEC3C941AD1A75B0AB1D76B9', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 11, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF62A7B88F98524E879E1D40677F1B88', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 12, NULL, NULL, 2, NULL, 0, 'Bake 8 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A217AEFF5B3C8B45AB0EA6BE14A50351', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 13, NULL, NULL, 2, NULL, 0, 'Remove and spoon in the crushed candy filling the hole about 1/2 full.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6F2EE26D3BEE14F96C839A871DB9317', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 14, NULL, NULL, 2, NULL, 0, 'Return to oven and melt for about 3 minutes until the candy is crystal clear and melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE0F13952C690D4CA73F07EBB0A8A693', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 15, NULL, NULL, 2, NULL, 0, 'Do not overheat or bubbles will form in the candy centers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E84E836AB0EF12468B3438A6047AEB88', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 16, NULL, NULL, 2, NULL, 0, 'Allow all cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D73ABCD73ECD1342BC13126EA7285894', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 17, NULL, NULL, 2, NULL, 0, 'Brush the cookies with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DFB366DDDBD124C986043076D428692', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 18, NULL, NULL, 2, NULL, 0, 'Dip a pretzel dipping stick into chocolate, remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'182D5ED4F2D0F64AAD9C08363ACC5451', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 19, NULL, NULL, 2, NULL, 0, 'Attach one pretzel handle to each cookie using chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A2BCCF39787074992B0C8F5FF4C9DE0', X'D2A89C19AF5A554EB4D33B2908EAC349', X'D2A89C19AF5A554EB4D33B2908EAC349', 20, NULL, NULL, 2, NULL, 0, 'Follow instructions in the tutorial if you want your handles to be more secure.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBBA82B28187F044AB828FD27AD61322', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 0, NULL, NULL, 2, NULL, 0, 'Dip Nutter Butter Cookies in Yellow Candy Melts and set them on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAC4B04BC6AB9F46BD4CD1FD89513329', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 1, NULL, NULL, 2, NULL, 0, 'Freeze for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCFFAE7B21D02A4A8DE7C36FF1A302FD', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 2, NULL, NULL, 2, NULL, 0, 'Fit a clay extruder with a ribbon disc (small rectangle whole).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B5EDFDAB0EF4B458B792001C9443292', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 3, NULL, NULL, 2, NULL, 0, 'Insert black modeling chocolate in the clay extruder and press out ribbons of the candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C507E1C64BC144EA41558B63226D250', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 4, NULL, NULL, 2, NULL, 0, 'Brush clear piping gel in a thin line over the upper middle part of the cookie and lay a strip of black modeling chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'206C8EB49F8C22468DDC64A4B5BF69C2', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 5, NULL, NULL, 2, NULL, 0, 'Cut off the excess modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'778763CFA13E4042BC414BDC20ED0D01', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating four black, curved stripes across each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A20A3BDBFDE9C242BE95E31E88E07B2C', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 7, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of black modeling chocolate and roll into a cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8FDB8FD76F15543B87015A65FDA8167', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 8, NULL, NULL, 2, NULL, 0, 'Attach along the bottom edge of the cookie to create the bee''s stinger.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BF5A8C9E03EE14AA0DA81A25F2379D9', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 9, NULL, NULL, 2, NULL, 0, 'Press more black modeling chocolate through the clay extruder fitted with a round hole disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31ED41080246DF4DA894956AF32477A3', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 10, NULL, NULL, 2, NULL, 0, 'Cut tiny pieces of the modeling chocolate to create a smile then create two antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'912AD93D9D65924AAE84AEF25BA52CF5', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 11, NULL, NULL, 2, NULL, 0, 'Attach the smile using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C536AD340FD7964CB7EFF24E08E530AB', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 12, NULL, NULL, 2, NULL, 0, 'Attach the antennae using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50B7C03FA39E2D488D31326D40D1A1B0', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 13, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C13704CD89000742B63E2C31A058A9B1', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 14, NULL, NULL, 2, NULL, 0, 'Pipe two wings and set a cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1BB3FEF95C3484CAC9239488CB392D1', X'E74ACA84A84BA9448D61593F25F22409', X'E74ACA84A84BA9448D61593F25F22409', 15, NULL, NULL, 2, NULL, 0, 'Allow the wings and antennae to harden before moving the cookies.');
