INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F76F272DE7F56D4E8E23785F3ABED485', X'03653994F0819C4D8831FC70A84D99C2', X'03653994F0819C4D8831FC70A84D99C2', 15, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35500C6895E49C4386271EEF04BB6452', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 0, NULL, NULL, 2, NULL, 0, 'Roll out peanut butter candy clay and cut out a tear drop shape using a 3 inch metal cutter to create Hognob''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24EFE89528DBB442B6089CC194A3013E', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 1, NULL, NULL, 2, NULL, 0, 'Cut two small triangles for Hognob''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4AF91B8EC343641881E00780393303E', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 2, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of the candy clay and shape it into an oval nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A166E721A29FE64E911AE6388183AB7F', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 3, NULL, NULL, 2, NULL, 0, 'Attach it to the tear drop head using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE50DBA8FB8A8143824859A081C54DC5', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 4, NULL, NULL, 2, NULL, 0, 'Press the end of a wooden skewer or lollipop stick into the oval to create two nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C67A8707EAB15643878FB7E9A82DDC10', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 5, NULL, NULL, 2, NULL, 0, 'Roll a small piece of peanut butter clay into a thin log for Hognob''s lower lip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB750CB5327F664493C6A27104C4DC4E', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 6, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of white candy clay to create two tusks and 3 small teeth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'962CFBBFD0F9E5419ACC1FC999EE022B', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 7, NULL, NULL, 2, NULL, 0, 'Use piping gel to attach the tusks and teeth just below the nose then attach the lower lip along the bottom edge of the teeth and wrapping up around the bottom edge of the tusks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9578E43FBE83F94F95A306C0B9F4BF6F', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 8, NULL, NULL, 2, NULL, 0, 'Create three wrinkles above Hognob''s nose using a fondant/gumpaste tool or toothpick.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA0505C7AD2729499555AC4EC537433D', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 9, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7140C30E78C1F241A6D9FC2F4FE968B0', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 10, NULL, NULL, 2, NULL, 0, 'Spoon peanut butter frosting into a pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBDF0C37D884694DA1EF51015A6BE5DF', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 11, NULL, NULL, 2, NULL, 0, 'Pipe peanut butter frosting fur over top of a cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70018F814A06AE49AFA339DCA27BBC80', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 12, NULL, NULL, 2, NULL, 0, 'Press one Hognob head onto the frosting so that the curved edge lines up with one edge of the cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'167AA4F4054F51479FB0CE3B766610F4', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 13, NULL, NULL, 2, NULL, 0, 'Pipe on more fur and a tuft of hair over top of the pointed edge of the tear drop shaped head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FE5B178BF263F48B113D4FD8C901D6A', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 14, NULL, NULL, 2, NULL, 0, 'Bend the triangle pieces of peanut butter candy clay into curved ears and attach one on either side of Hognob''s head near the top of the cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33922BCF2358D441943878E51AC1A20E', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 15, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'269AD59E0BD59248978438AC45FDD0B1', X'CE8DC85881026C47B56511D1852FAC9E', X'CE8DC85881026C47B56511D1852FAC9E', 16, NULL, NULL, 2, NULL, 0, 'ENJOY!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1A8FFBFF820BD4B82EB27C32B99E31B', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 0, NULL, NULL, 2, NULL, 0, 'Pinch off about 1/2 tablespoon piece of skin colored modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAC8F03D3077004D81C96DF021A17A7D', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 1, NULL, NULL, 2, NULL, 0, 'Flatten and shape into Dug''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EAA7E2E4AAF624F93B01486D36C8399', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 2, NULL, NULL, 2, NULL, 0, 'Press some skin colored modeling chocolate through a clay extruder fitted with a single hole disc creating long tubes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14298C07A415DD4A91231AC70AFEDBC5', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 3, NULL, NULL, 2, NULL, 0, 'Brush clear piping gel along the bottom (chin) edge of Dug''s head and press a tube of modeling chocolate along the edge creating the lower lip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65265A95F3C6944C8D9FEEACEE2027B0', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 4, NULL, NULL, 2, NULL, 0, 'Wrap the tube around at one corner and create the upper lip, leaving the mouth open.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DAA10C76DAAC74B859F4953FE7329E1', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 5, NULL, NULL, 2, NULL, 0, 'Cut off any excess and pinch the two edges together to form his open mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEAEB36867C39245B0AFF69A62E483BD', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 6, NULL, NULL, 2, NULL, 0, 'Roll the white modeling chocolate out really thin and cut two small circles and two even smaller circles using round plunger cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDFD9CF7581EAD439A2950D20813AED7', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 7, NULL, NULL, 2, NULL, 0, 'Cut 1/4 off each circle creating teeth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'113CB65AD867C0418A5507A06A27BA2C', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 8, NULL, NULL, 2, NULL, 0, 'Brush some clear piping gel just under the upper lip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB0A5CBCA99EBE41BCD941DE9CCEC076', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 9, NULL, NULL, 2, NULL, 0, 'Insert the four teeth up against the upper lip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'731F09FB34F25843A8CB122E1CDBFCA5', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 10, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of skin colored modeling chocolate and shape into an oval shaped nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D6000799FD471439E98E5C48E2BE860', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 11, NULL, NULL, 2, NULL, 0, 'Use piping gel to attach it just above the mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C94F28B5C9D6245AA558F771F75DA6D', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 12, NULL, NULL, 2, NULL, 0, 'Use a wooden skewer or lollipop stick to make to indentations for nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53E3BE5A9B02014785666D5C5785F470', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 13, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes using piping gel just above the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69595517B5314E4E874F67CD3C24AE75', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 14, NULL, NULL, 2, NULL, 0, 'Frost a cupcake with chocolate frosting smoothing out the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD831B1AE0873B4582D4E57EEB43183D', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 15, NULL, NULL, 2, NULL, 0, 'Set a modeling chocolate Dug face on top so that the chin matches up with one edge of the cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB82C33429284444BED231405A84E0D9', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 16, NULL, NULL, 2, NULL, 0, 'Spoon chocolate frosting into a pastry bag fitted with a grass pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAF718AB4BB8754CBD18DC2858640526', X'D682F0F147C44942A9C99D85D5EA99C3', X'D682F0F147C44942A9C99D85D5EA99C3', 17, NULL, NULL, 2, NULL, 0, 'Pipe frosting hair around the face.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD880D4055B4A543BE1967B15B362308', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 0, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of pink candy melts onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50AF4D18E7E4F54FACA4711D09A17080', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 1, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in pink candy melts then remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CA917CD63B86C4A8B6D14229416D79A', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 2, NULL, NULL, 2, NULL, 0, 'Set over top of the spoonful of pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9502188875B7C14C9989D70A1E308E0D', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 3, NULL, NULL, 2, NULL, 0, 'Immediately attach two candy eyes, two jumbo pink heart sprinkle wings, and two heart sprinkle ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3567199ECD0344D8AA0C9D09B565F37', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 4, NULL, NULL, 2, NULL, 0, 'Chill in the freezer until hardened, about 3-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'521EB6A2DD648045B0C1325ABF69A695', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 24 pretzels owls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6449EC030650164CBAE6ADDEF9C5FA22', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 6, NULL, NULL, 2, NULL, 0, 'Dip a pretzel dipping stick into milk chocolate then set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EBDB09B3FC2E04EBE01ACB540C06B45', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 7, NULL, NULL, 2, NULL, 0, 'Immediately press one pretzel owl along the top edge of the chocolate dipped pretzel making it look like the owl is perched on a branch.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCEC7B66C436DD46B1D05DD4D6DEDA5B', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 8, NULL, NULL, 2, NULL, 0, 'Add two red heart sprinkle feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E2515E39724CA40987D534024B7B7E2', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E1DE66FB1439A40A5B84BF484B56D78', X'92C832DFB168FC4B95C9DD9858E4E1B6', X'92C832DFB168FC4B95C9DD9858E4E1B6', 10, NULL, NULL, 2, NULL, 0, 'Let the pretzels dry completely before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7672236A809644AA06777A4EF442443', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 0, NULL, NULL, 2, NULL, 0, 'Break both curved parts off of 12 of the heart sprinkles creating 24 little red tongues.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC483C412764334E8773D9C629DD73ED', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 1, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of white chocolate onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4790E344AF942747A2F82C3E9542D4C8', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 2, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in white chocolate then remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE7F8DEDE821EE469817248167B2F53D', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 3, NULL, NULL, 2, NULL, 0, 'Set over top of the spoonful of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56039BE72C8B5240BA78E8E07A1F2B59', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 4, NULL, NULL, 2, NULL, 0, 'Immediately press one red tongue and 1 brown candy nose onto the bottom edge of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C062E4DE0986024BA01F6B3CDFF63979', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 5, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure chocolate and in the freezer if using Candy Melts until hardened, about 3-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB23B8F4F7875A4BB3F56169FC245298', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 24 white chocolate dipped pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1BBA9F39992AD49A88C28817781E3FD', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 7, NULL, NULL, 2, NULL, 0, 'Paint on a spot of milk chocolate above and to one side of the candy nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A157092776DF24AB9D0B79056021E8D', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 8, NULL, NULL, 2, NULL, 0, 'Immediately press one candy eye over the chocolate spot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48F9CCA58ED91C4E9DC89D5A1C33EF59', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 9, NULL, NULL, 2, NULL, 0, 'Attach the other candy eye using a small amount of milk chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C2A5792F89A13449E32FFF9DA525F71', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 10, NULL, NULL, 2, NULL, 0, 'Paint on some tiny freckles next to the candy nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51C238A436CF9148BE5FAE63627F3C93', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', X'F0DCC3E3DC788F479BCCEA44B59AA9C4', 11, NULL, NULL, 2, NULL, 0, 'Let the chocolate harden completely before packaging them.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'949AA45B4CC87A429023F782ABA798EC', X'284BE2C85FD04943A1E6576D8B9B7B74', X'284BE2C85FD04943A1E6576D8B9B7B74', 0, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in pink candy melts using a dipping tool or fork.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5EE8C19FC84264C9D7DA8F4538C984F', X'284BE2C85FD04943A1E6576D8B9B7B74', X'284BE2C85FD04943A1E6576D8B9B7B74', 1, NULL, NULL, 2, NULL, 0, 'Remove and allow excess candy to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA7F5FDCA456A04DAFD745FCE6C2CBBC', X'284BE2C85FD04943A1E6576D8B9B7B74', X'284BE2C85FD04943A1E6576D8B9B7B74', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46BF69561AAAAE4681E1E153F07A7877', X'284BE2C85FD04943A1E6576D8B9B7B74', X'284BE2C85FD04943A1E6576D8B9B7B74', 3, NULL, NULL, 2, NULL, 0, 'Attach 4 jumbo pink heart sprinkles to create a tail and 3 fins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21D097DD12D15A40B384B790FC087E1A', X'284BE2C85FD04943A1E6576D8B9B7B74', X'284BE2C85FD04943A1E6576D8B9B7B74', 4, NULL, NULL, 2, NULL, 0, 'Attach 1 candy eye and 1 mini red heart sprinkle creating lips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'943783F5E3301E4E82A53C7DE03010C8', X'284BE2C85FD04943A1E6576D8B9B7B74', X'284BE2C85FD04943A1E6576D8B9B7B74', 5, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B6A1AF115855E43B4BDFF1F05577D27', X'284BE2C85FD04943A1E6576D8B9B7B74', X'284BE2C85FD04943A1E6576D8B9B7B74', 6, NULL, NULL, 2, NULL, 0, 'Repeat dipping and decorating a total of 36 pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A703FCC86233344B19AA6FD2FB00BE9', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 0, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of the white modeling chocolate and roll into a thin rope.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C424567EC9978B4EA7682B46DE876A68', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 1, NULL, NULL, 2, NULL, 0, 'Cut off 1/2 inch pieces and roll into a cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80620E331EAE854A87E405DEAB6A70DE', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 2, NULL, NULL, 2, NULL, 0, 'Twist two of those small pieces together to form a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE5EF79A799A7B4E8AB46876595A7C46', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 3, NULL, NULL, 2, NULL, 0, 'Brush with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9892ECC054E2942AB7B18C79910149B', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 4, NULL, NULL, 2, NULL, 0, 'Set aside to dry for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12C6E8FB3B432648978B3E0C2C19E1A6', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 5, NULL, NULL, 2, NULL, 0, 'Attach one small white heart to one jumbo pink heart using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33D19D5114FC9946B8837FFFC6766197', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 6, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of white chocolate onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00CC635C732F2D4F81D542C9DFE1BFD2', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 7, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in white chocolate then remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1238BCF11158A4EA240DC1757F90EF3', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 8, NULL, NULL, 2, NULL, 0, 'Set over top of the spoonful of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51F1753FB53B0A4C9522AF3002FE1731', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 9, NULL, NULL, 2, NULL, 0, 'Immediately press one unicorn horn in between the two rounded parts of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED488FC7EEFB824685FE6C5D0F726A17', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 10, NULL, NULL, 2, NULL, 0, 'Attach two pink heart ears on either side of the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'857261AE2693AD4F9756BD2C07EDE497', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle 5 rainbow heart sprinkles on the pretzel in between the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7CCC9DC08ECC4E4886CAA01F2ADB8B3E', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 12, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure white chocolate and in the freezer is using Candy Melts for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B39AEA39792FED4195F11A0D3DC287A6', X'0DBD11BBE1C1DF4195690DD57A0BF09F', X'0DBD11BBE1C1DF4195690DD57A0BF09F', 13, NULL, NULL, 2, NULL, 0, 'Repeat dipping and decorating a total of 24 pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD9D07938F0BCB4E84EF1D455FCC8F55', X'C1CAB346C4CAC440A7CADFB5776B7213', X'C1CAB346C4CAC440A7CADFB5776B7213', 0, NULL, NULL, 2, NULL, 0, 'Attach one white peanut butter cup mini to the milk chocolate peanut butter cup using clear piping gel creating Paddington''s snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F65B3A773296254DB795234479A10D0D', X'C1CAB346C4CAC440A7CADFB5776B7213', X'C1CAB346C4CAC440A7CADFB5776B7213', 1, NULL, NULL, 2, NULL, 0, 'Pinch off a tiny piece of black modeling chocolate, roll it into a ball, then use clear piping gel to attach it to the top edge of the white peanut butter cup creating a nose. Insert a skewer to create two nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87C5BD9894B8F94398935E5461CCAF59', X'C1CAB346C4CAC440A7CADFB5776B7213', X'C1CAB346C4CAC440A7CADFB5776B7213', 2, NULL, NULL, 2, NULL, 0, 'Draw on a smile using the food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3954EE62BE67F247AD637F79789F7BEF', X'C1CAB346C4CAC440A7CADFB5776B7213', X'C1CAB346C4CAC440A7CADFB5776B7213', 3, NULL, NULL, 2, NULL, 0, 'Pinch off two more small pieces of black modeling chocolate, roll them into balls, and attach them above the white peanut butter cup for his eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB6B64DF7B2FEC40A179654628625F54', X'C1CAB346C4CAC440A7CADFB5776B7213', X'C1CAB346C4CAC440A7CADFB5776B7213', 4, NULL, NULL, 2, NULL, 0, 'Add highlights to the eyes using white food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59BA8027BA13B848B92CA65B0C104BED', X'C1CAB346C4CAC440A7CADFB5776B7213', X'C1CAB346C4CAC440A7CADFB5776B7213', 5, NULL, NULL, 2, NULL, 0, 'Set aside and allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5016435E5A30EB48B43EA2A1E678F60D', X'49889DF7B194E543879DC8C67D8C3140', X'49889DF7B194E543879DC8C67D8C3140', 0, NULL, NULL, 2, NULL, 0, 'Roll out the red modeling chocolate and cut out one 2 inch circle, then cut a 1 1/8 inch circle from the center of it.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D32CB42D3DFCF4B8A88603FFF59E572', X'49889DF7B194E543879DC8C67D8C3140', X'49889DF7B194E543879DC8C67D8C3140', 1, NULL, NULL, 2, NULL, 0, 'Cut a 1/2 inch wide by 3 1/2 inch long strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'464F5CE84B8EF846AA53F1BF81D6B839', X'49889DF7B194E543879DC8C67D8C3140', X'49889DF7B194E543879DC8C67D8C3140', 2, NULL, NULL, 2, NULL, 0, 'Brush the inside of the cut circle with clear piping gel and insert the strip along the inner edge, creating the tube shaped crown of the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4399EC137CD444B812A98C9293EDE42', X'49889DF7B194E543879DC8C67D8C3140', X'49889DF7B194E543879DC8C67D8C3140', 3, NULL, NULL, 2, NULL, 0, 'Brush clear piping gel all around the top edge of the crown piece.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A7F14DBE0424749A0C8D19E0D789BFE', X'49889DF7B194E543879DC8C67D8C3140', X'49889DF7B194E543879DC8C67D8C3140', 4, NULL, NULL, 2, NULL, 0, 'Flatten out the outer edge of the smaller circle then attach it on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0261972F6B79224AB79DB7BA23360BF3', X'49889DF7B194E543879DC8C67D8C3140', X'49889DF7B194E543879DC8C67D8C3140', 5, NULL, NULL, 2, NULL, 0, 'Let this hat dry for just a few minutes then brush some clear piping gel inside the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B6463BDBE29EE478C49BCCCE594804B', X'49889DF7B194E543879DC8C67D8C3140', X'49889DF7B194E543879DC8C67D8C3140', 6, NULL, NULL, 2, NULL, 0, 'Press the hat over your peanut butter cup bear, shaping it to look like Paddington''s hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09086D85B41E984883EDF244B2EA0EE3', X'2CAE111655652E4087A04CD3768A7413', X'2CAE111655652E4087A04CD3768A7413', 0, NULL, NULL, 2, NULL, 0, 'Cut thin slices from a frozen white cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3A95ABE96500847BEE9F5CF73B96E77', X'2CAE111655652E4087A04CD3768A7413', X'2CAE111655652E4087A04CD3768A7413', 1, NULL, NULL, 2, NULL, 0, 'Cut out twelve 2''s using a #2 fondant cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B39EBFEB7BD7144494D8B7A39FE1716D', X'2CAE111655652E4087A04CD3768A7413', X'2CAE111655652E4087A04CD3768A7413', 2, NULL, NULL, 2, NULL, 0, 'Pipe orange gel on six of them then top each with another.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9276C513D4AE974A8C9A3BD3129A8EDA', X'99F3903051B8684EB1A16B428F32FB90', X'99F3903051B8684EB1A16B428F32FB90', 0, NULL, NULL, 2, NULL, 0, 'Insert one cookie stick into the center of a chocolate cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17CB30D3AEC5B042B7BFF4FDF2B3D263', X'99F3903051B8684EB1A16B428F32FB90', X'99F3903051B8684EB1A16B428F32FB90', 1, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of chocolate frosting around the stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B349717BB47EAC46809F4BBB8B3CA813', X'99F3903051B8684EB1A16B428F32FB90', X'99F3903051B8684EB1A16B428F32FB90', 2, NULL, NULL, 2, NULL, 0, 'Attach two malt ball paws holding one #2 marmalade sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43D32CE810C3C24FAE1B3A0D5719AA7B', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 0, NULL, NULL, 2, NULL, 0, 'Fill the bottom trays of your gummy candy maker with ice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6DE9CE9D4F5B2F46A0A8786EB27ABE8F', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 1, NULL, NULL, 2, NULL, 0, 'Set the silicone bear, worm, and fish molds over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'585A452BEBE4F34FBBEEB68FCD9C934F', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 2, NULL, NULL, 2, NULL, 0, 'Stir together the flavored and unflavored gelatin in the Candy Maker mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EB5E3BBAA289D4D893387C6E90CDE1E', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 3, NULL, NULL, 2, NULL, 0, 'Pour in the water, as you continue to stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7648AF2557911940846825AD48ABBF4F', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 4, NULL, NULL, 2, NULL, 0, 'Allow the mixture to melt and become warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E9DC0ED17726C428543689DAF2BF6BA', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 5, NULL, NULL, 2, NULL, 0, 'Pour into the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFC894FB36E38E4D8BE9F7702A7B9542', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 6, NULL, NULL, 2, NULL, 0, 'Allow the candy to harden, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A802F612AF6EC4EA4E0DC86C8610508', X'4602A9BEBF99CF45B152EEA6DED2FDED', X'4602A9BEBF99CF45B152EEA6DED2FDED', 7, NULL, NULL, 2, NULL, 0, 'Remove the molds from the trays and peel the candies out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EA210D274DD9047BB31D07F1F2A65B6', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F315C2B4C2E5534DBBF2A37A97FDCFC9', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E49583AEE6E2148A23FE6FA27FDA980', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A11B8250ABE434B95AA01AF1AF309B3', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 3, NULL, NULL, 2, NULL, 0, 'Add the egg, vanilla, and almond extract, and yellow food coloring and beat until well combined. You want this to be bright yellow. The color will tone down once the flour is added.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F80FB1373C5F9F44AEC495DB89276F4C', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 4, NULL, NULL, 2, NULL, 0, 'Stir in the flour mixture just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D93FA0AA233C245AA326AC3F6830BB1', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 5, NULL, NULL, 2, NULL, 0, 'Dust a silicone mat and non-stick rolling pin lightly with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F102AB99AF0ACC4F96E3393D917716BD', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 6, NULL, NULL, 2, NULL, 0, 'Roll out some of the dough to between 1/4 6/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74E82AA3A0949744842DC28DBFB5B33F', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 7, NULL, NULL, 2, NULL, 0, 'Create the middle cookies with a hole in the middle using a 2 1/2 inch and a 3 1/2 inch long oblong cutter. Press the 2 1/2 inch cutter into the dough. Remove the cutter and the oblong shaped dough. Center the larger oblong cutter around the hole, press down to cut the dough. Leave that dough in place and continue to cut until you have a total of 9 oblong cookies with holes in them.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'386AC29E6D324B439E91C67303E0C717', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 8, NULL, NULL, 2, NULL, 0, 'Remove the excess dough around each oblong then place the silicone mat in the freezer. Do not move these cookies until they are chilled. Once firm, carefully peel the cookies up from the silicone mat and place the cookies on a parchment lined baking sheet or simply bake them on the silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A576DB28FF70604BAA7D35BD4982C2D5', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 9, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough out to about 1/8th inch thickness and cut out 18 more oblong shaped cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9757B0F83B085A4E93781D7D0E4235CD', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 10, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough out to 1/8th inch thickness and cut out 9 of the Minions legs and feet. I used a bell shaped cutter, but a mini gingerbread or teddy bear cutter will work too.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F7742364CD0104D85196C0A47A9DE89', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 11, NULL, NULL, 2, NULL, 0, 'Use a tiny bit of water to attach the feet/legs to 9 of the oblong shaped cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A26BFCBFF17A945BB8CA976C975CB21', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 12, NULL, NULL, 2, NULL, 0, 'Freeze all the cookies for 15 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00A89E36B6788D4A86601FEBA9EC565D', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 13, NULL, NULL, 2, NULL, 0, 'Bake for 12  14 minutes until the bottom of the cookies just begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F698C02BC2487468BDB9DC050DD32A1', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 14, NULL, NULL, 2, NULL, 0, 'Remove and allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C55A0C6A4C562F40A3C2359C5B47ED7F', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 15, NULL, NULL, 2, NULL, 0, 'Use yellow candy melts to attach the middle cookie to the bottom cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9B8066066B4604488F6CBDF3933C7D8', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 16, NULL, NULL, 2, NULL, 0, 'Fill the middle cookie with candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5924F06A6EA0864D8C3381B2D0DF32AA', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 17, NULL, NULL, 2, NULL, 0, 'Roll out modeling chocolate, cut using a t-shirt or sweater cookie cutter, then decorate using more modeling chocolate and/or sprinkles. Attach the decorations to the sweaters using water or clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B4FE27565BA8A499DF863942310A85F', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 18, NULL, NULL, 2, NULL, 0, 'Let the modeling chocolate sweaters dry until firm (15-30 minutes) then use yellow candy melts to attach them to the top cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'124EA83FC09C604598EBFCBC936C9FBB', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 19, NULL, NULL, 2, NULL, 0, 'Roll out gray modeling chocolate, cut using a large round plunger cutter, then cut out a hole in the center using a smaller round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25D6432A93C0354392CB4DAB2F82C27D', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 20, NULL, NULL, 2, NULL, 0, 'Attach the gray goggles to jumbo candy eyes using clear piping gel or make modeling chocolate eyes and attach them under the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB4AA6E1A1E32548850B26888C0C3780', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 21, NULL, NULL, 2, NULL, 0, 'Glue the eyes and sweater to the top cookie using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'962F3928D8C57F4B8896783C37BDD636', X'BEC872D0C37F94459FD220F286AB24B8', X'BEC872D0C37F94459FD220F286AB24B8', 22, NULL, NULL, 2, NULL, 0, 'Attach the top cookie to the middle cookie using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75D19F037CCED548B8BAC28C4766B2DF', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 0, NULL, NULL, 2, NULL, 0, 'Unwrap the Rice Krispie Treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4040D77FE0B7CB418BA654DE0D970785', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 1, NULL, NULL, 2, NULL, 0, 'Cut a triangle out of one end of the cereal treat creating two ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F68C3F2042C18C4ABCCBF7CB3BD2BD28', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 2, NULL, NULL, 2, NULL, 0, 'Pour the chocolate into a tall drinking glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84400ACEA8E3364EB81E81A555460C21', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 3, NULL, NULL, 2, NULL, 0, 'Dip the tip of a lollipop stick or paper straw into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'782498E280D97642B70D21406DB1969C', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 4, NULL, NULL, 2, NULL, 0, 'Insert the stick into the cereal treat. Repeat with all the treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C835464A93683241BE281580D7BE333C', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 5, NULL, NULL, 2, NULL, 0, 'Let the chocolate dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A828838B53A42F4291A59B197E98299A', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 6, NULL, NULL, 2, NULL, 0, 'Holding the end of the stick, dip the Rice Krispie Treat into the chocolate, then lift it out and shake it to remove any excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13FDF73EE9A7084A86C58C4153BA26A1', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 7, NULL, NULL, 2, NULL, 0, 'Set the chocolate dipped treat on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B451C737D542E489054347C5E9B25E9', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 8, NULL, NULL, 2, NULL, 0, 'Immediately add two candy eyes and 1 pink heart sprinkle nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92936FC1ECAD004FAB83BD4D95799498', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 9, NULL, NULL, 2, NULL, 0, 'Add three white jimmies on either side of the nose to form whiskers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A189B0A4D0E95D47BB0ACE5C60E88077', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 10, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator (for pure chocolate) or the freezer (for candy melts) until hardened, about 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09D88F086B34764B8F7797DCD10A0F18', X'182FA83762ADE842AFDE685BF3E9459E', X'182FA83762ADE842AFDE685BF3E9459E', 11, NULL, NULL, 2, NULL, 0, 'Repeat dipping and decorating all 8 treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'212282BBC015FE45A4711E5E6CB7DDA5', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 0, NULL, NULL, 2, NULL, 0, 'Insert wooden popsicle sticks into 4 silicone swirl popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAEE703BC678794DBBE63E946D0146B9', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 1, NULL, NULL, 2, NULL, 0, 'Combine milk and chocolate spread in a blender or mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E10181FCA1B2A44BBB1777EFA83DB6CC', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 2, NULL, NULL, 2, NULL, 0, 'Blend using an electric blender, a stick blender, or a whisk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F172DC8C40F80E43BF5832E89A7B1C86', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 3, NULL, NULL, 2, NULL, 0, 'Pour into swirl popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F929F88812085498D7F435CD99B3267', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 4, NULL, NULL, 2, NULL, 0, 'Freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24FD09AFF33C724C9D7E49BBF714E49D', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 5, NULL, NULL, 2, NULL, 0, 'Before serving remove the popsicles from the freezer and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'376AE47DD046F244847B2B2183DDEFEE', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 6, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of clear piping gel on the back of a large candy eye then press it onto the popsicle about 1/3rd of the way from the top of the swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74855A9300B7EA4E9102A114ECD1962E', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 7, NULL, NULL, 2, NULL, 0, 'Repeat adding a second eye next to the first.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F39F208031AE3145847A45E6485BAE17', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 8, NULL, NULL, 2, NULL, 0, 'Brush piping gel onto the back of a candy smile and attach it about an inch below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A07E6BF9595AAA4581F4CB34089BA321', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 9, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 10 minutes so that the piping gel can set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3509094E232E464BBF69A7ADA366F859', X'27960BF3F311B342B1FD2F0C194D7216', X'27960BF3F311B342B1FD2F0C194D7216', 10, NULL, NULL, 2, NULL, 0, 'Remove, serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAC30542BA4A0B4FAF92D4AEFF79155E', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 0, NULL, NULL, 2, NULL, 0, 'Heat Donut Holes according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D13F98863CC20B44A568ADEAB311CB62', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile combine the granulated sugar with 1 tablespoon of the cinnamon sugar that comes in the box of Donut Holes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F33D9D42D82D7E4C9A10B490E5AD8585', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 2, NULL, NULL, 2, NULL, 0, 'Pour into a zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BCC71DEC7F5284681F7BFA22E345CEF', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 3, NULL, NULL, 2, NULL, 0, 'Squeeze in about 10 drops of red food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0FA0CE66DF7E24DB69D909F17781213', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 4, NULL, NULL, 2, NULL, 0, 'Shake the bag several times then massage the red coloring into the sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F463D061797444AB1A51FB9D27965E4', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 5, NULL, NULL, 2, NULL, 0, 'Pour the sugar into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9408C3404B43BF4FA511E75FC840E0E6', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 6, NULL, NULL, 2, NULL, 0, 'Roll the hot Donut Holes in the red sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AAAF329D5E8794DB2A4C04F619526C5', X'C64963BF0B08EC4D8EB1F480DF47CD64', X'C64963BF0B08EC4D8EB1F480DF47CD64', 7, NULL, NULL, 2, NULL, 0, 'Insert one half of a pretzel stick into each Donut Hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79A70A0D9438744FBA34823F76F32862', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80A76D8DDB5FF548A5DC0F693066716E', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 1, NULL, NULL, 2, NULL, 0, 'Combine butter and cream cheese in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CE9BC9A84F0494F949EE187D03519C2', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 2, NULL, NULL, 2, NULL, 0, 'Beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C2012ACBD7DB04B9777C12EC95502BB', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 3, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until well mixed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AF0D36C8AD2544AB6136F1AC3580F6E', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 4, NULL, NULL, 2, NULL, 0, 'Pour in egg, vanilla, almond extract, and food coloring and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EBE3C44077001479F12B22F8B0606F6', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 5, NULL, NULL, 2, NULL, 0, 'Whisk together flour, baking powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13DB49DF15AEB040BB2788D16CF05318', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 6, NULL, NULL, 2, NULL, 0, 'Stir the dry ingredients into the wet just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACF0DDF9827CAB4580EB600D7A06BD6F', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 7, NULL, NULL, 2, NULL, 0, 'Scoop out 40 heaping tablespoons of dough and set on parchment paper lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F7BB82678F86142975F8A71FE022F8C', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 8, NULL, NULL, 2, NULL, 0, 'Use a fork to spike the dough to make it look furry, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F69F8868CD0B14408B3DF5FFB2E84891', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 9, NULL, NULL, 2, NULL, 0, 'Bake one tray at a time for 12-16 minutes until the edges of the cookie feel set and the tops no longer look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F222F48D74DE4ABDD87BB876F1171F', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 10, NULL, NULL, 2, NULL, 0, 'Remove cookies and allow to cool completely,');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CED53FEBF7EB3C43B089711D380FCCBD', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 11, NULL, NULL, 2, NULL, 0, 'Peel cookies off parchment paper and pair them up based on size.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5555CB408AAEC842903B5EE69B87BEF9', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 12, NULL, NULL, 2, NULL, 0, 'Use blue cookie icing to attach two candy eyes to half of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB379F9441FDC94C9EC6A106E63EF36F', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 13, NULL, NULL, 2, NULL, 0, 'Place a scoop of ice cream in between two cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7FE101A8ED4E44FAB9EB3FE6E5292B9', X'9A25B11AC54614468671518E65A5EA50', X'9A25B11AC54614468671518E65A5EA50', 14, NULL, NULL, 2, NULL, 0, 'Freeze until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F3FFFD7469B754CB6ECEB9C707E80B0', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 0, NULL, NULL, 2, NULL, 0, 'Carve a Minion shaped oval out of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5873F29EE8E38E4BB6641F6403B0D784', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 1, NULL, NULL, 2, NULL, 0, 'Reserve all the cake scraps for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'095843229492DF459F2644ECA57C72F4', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 2, NULL, NULL, 2, NULL, 0, 'Spoon all the frosting colors into individual pastry bags, each fitted with a star tip, reserving about 1/2 cup of the black.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF599651AF3CAB4D8E09FBEA0089BB00', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 3, NULL, NULL, 2, NULL, 0, 'Place the reserved black frosting into a pastry bag fitted with a #4 round tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84FEE515FE007B48B7DD119601A0A3A6', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 4, NULL, NULL, 2, NULL, 0, 'Decorate the cake by piping stars all over the surface and sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8EC90BA109FB442A957E9C54F1FC3B1', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 5, NULL, NULL, 2, NULL, 0, 'Start by making two silver gray circles for the Minion''s goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03CC6AE0DED2D947870D26997BA32CA2', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 6, NULL, NULL, 2, NULL, 0, 'Pipe a small black circle in the middle of those circles then fill in around the black circle using white frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7903E085975B1549A364032C8AA532C6', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 7, NULL, NULL, 2, NULL, 0, 'Pipe a black band on either side of the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DFF9BCBA56D8145B9945AE5304EB787', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 8, NULL, NULL, 2, NULL, 0, 'Pipe yellow above and just below the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A5E4D0F0E722748AF06EAB036A1B7EE', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 9, NULL, NULL, 2, NULL, 0, 'Pipe on the black and white striped prisoner uniform.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C67867A3D2A91640ADD365DBD4A5F86B', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 10, NULL, NULL, 2, NULL, 0, 'Pipe a line around the straps to the uniform and pipe on the outline of a pocket in the middle of the uniform.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B59DDAC635B21F4DB3917C8C4AAA7FF8', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 11, NULL, NULL, 2, NULL, 0, 'Pipe white stars inside the pocket border then pipe on a prisoner number over the white stars using black frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1029DA0A1F341A428DD6B971022CA718', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 12, NULL, NULL, 2, NULL, 0, 'Pipe on a black smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5966666DA2B5447810C3A942E2C47A7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 13, NULL, NULL, 2, NULL, 0, 'Glue together the cake scraps using frosting and cut a rectangle prisoner hat out of the scraps.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F41EC9E1D3EEF94EB8443E06C9C84232', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 14, NULL, NULL, 2, NULL, 0, 'Attach the hat to the top of the Minion and pipe on black and white stripes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC9AE5C10ABCFE44BFFABBA031F3A61F', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 15, NULL, NULL, 2, NULL, 0, 'Crumble all the remaining cake scraps and blend together with a tablespoon or two of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B668F8B92B5B340A9B6B623BF3C459B', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 16, NULL, NULL, 2, NULL, 0, 'Use this cake ball mixture to shape two arms, two legs, and two shoes for the Minion.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E60910A8719C64E9FFA23B43B445777', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 17, NULL, NULL, 2, NULL, 0, 'Pipe yellow frosting over the arm then pipe on black gloves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDC57916154D8942890714A4D80344A9', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', X'B5766BBE18D5B94D8ABE7CC2B0464BD7', 18, NULL, NULL, 2, NULL, 0, 'Pipe white frosting over the legs then black over the shoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'662A6DDEC9C51B44BA9308BF5D0569AF', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 0, NULL, NULL, 2, NULL, 0, 'Cut 6 of the StackerMallows in half lengthwise.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2A781CBA1358444A8C9917BEF8117BE', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 1, NULL, NULL, 2, NULL, 0, 'Brush a very thin layer of the melted semi-sweet chocolate over the inside of a graham cracker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'756F5B6B043C504D87A851792A490AD4', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 2, NULL, NULL, 2, NULL, 0, 'Arrange one whole StackerMallow and two halves on top of the wet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC0928B95D0F434E98E5814CD16C39FD', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 3, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of chocolate on another graham cracker square and set it on top of the marshmallows creating a sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B78E22CFFCB974B95B79328C1C42B8C', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 4, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 6 s''mores sandwiches.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF08B045C6FA0143853B6194EFF08C36', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 5, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1969E3F00209D64F8BBA154CB597DCB3', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 6, NULL, NULL, 2, NULL, 0, 'Dip one end of a paper straw or lollipop stick into melted yellow Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAB92B5E69E6F644A215C574929A6BEE', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 7, NULL, NULL, 2, NULL, 0, 'Insert that tip into the marshmallow center of a s''mores sandwich so that the straw goes up about 3/4 of the way through the sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72E0313DDAC34D40A3FAFAD3B5C87DAD', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 8, NULL, NULL, 2, NULL, 0, 'Repeat, adding straws to all the s''mores, then allow the candy coating to dry for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9FA8F6384E1A941B3928C293F9781C7', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 9, NULL, NULL, 2, NULL, 0, 'Dip each of the s''mores into yellow candy melts, allowing the excess candy coating to drip off before setting them on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE749FD5A883E24E9B600C738D6B5FE4', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 10, NULL, NULL, 2, NULL, 0, 'Freeze for 5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E713BBF6E9F4534086807CCC2C63D803', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 11, NULL, NULL, 2, NULL, 0, 'Meanwhile, roll out the silver colored candy clay to about 1/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24C711E23D1DC64BBE705110237A417A', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 12, NULL, NULL, 2, NULL, 0, 'Cut out 1 inch circles (1 or 2 per Minion depending on how many eyes you want to add).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D920CFD5AF306542BBD19BAC2C97036F', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 13, NULL, NULL, 2, NULL, 0, 'Cut a 3/4 inch circle out of each of those 1 inch circles to create the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB4A292DA130D148AB7CD8165C707AE3', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 14, NULL, NULL, 2, NULL, 0, 'Insert a jumbo candy eye in the center of each (or make 3/4 inch round candy clay eyes to insert).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82744CA4CF29A243AF963AFA94B2834B', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 15, NULL, NULL, 2, NULL, 0, 'Roll out the black candy clay and cut into 1/4 inch wide by 2 inch long strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0AB5FB2C92AD548B369649EA72F6ABB', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 16, NULL, NULL, 2, NULL, 0, 'Create smiles and hair using black candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93418E6C8334A249908174455A886030', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 17, NULL, NULL, 2, NULL, 0, 'Use yellow candy melts to attach one or two goggles (eyes) to each s''mores pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DC39C542B79224CB6F4C2BE717FCD35', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 18, NULL, NULL, 2, NULL, 0, 'Attach two black strips on either side of the goggles then add smiles and hair to some.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3325CF96D5EA1A4C96BFDCE1C04C531F', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', X'DC116AAECBEE0E4AB9F3678B8DBD9F76', 19, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to 2 weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'378FFA8DFDF7414693F1C5238E3F9DA2', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 0, NULL, NULL, 2, NULL, 0, 'Spread some peanut butter on 12 of the butterfly crackers then set another cracker over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB5712DD47B0654E8A5E29B945B14E05', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 1, NULL, NULL, 2, NULL, 0, 'Dip a peanut butter cracker into the melted candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40B6C3883AC8A04CBF48946B5DA154B9', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 2, NULL, NULL, 2, NULL, 0, 'Use a chocolate dipping fork to remove the butterfly and allow the excess candy melts to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99B52B2574A1894B8F4F72279642F9EA', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 3, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A722E6046F90E4C98F9756B6B77A06F', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 4, NULL, NULL, 2, NULL, 0, 'Immediately press two regular yellow or orange M&M''s on the upper portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DC9F8018B8FEC47ADB70EB66A50ED1A', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 5, NULL, NULL, 2, NULL, 0, 'Press two mini yellow or orange M&M''s on the lower portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B31E9F6BEB36C6429934B0A3F0B90A39', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 6, NULL, NULL, 2, NULL, 0, 'Press one regular green M&M at the top of the mid section creating the butterfly''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4C2020CFD8A4F42B483A67135AA7577', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 7, NULL, NULL, 2, NULL, 0, 'Arrange three mini green M&M''s in a row below the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82E86B1CBBE3C14F9020ACB4438EACC2', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 8, NULL, NULL, 2, NULL, 0, 'Insert two candy licorice pieces into the top of each cracker creating antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54C4A21EE235CB4ABA29B37971A1A619', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 9, NULL, NULL, 2, NULL, 0, 'Pop the cracker into the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'915D88BAF6DB3A45BB7164F5AE32CF33', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 10, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B505A3C2C89514C809947BCAB31420F', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 11, NULL, NULL, 2, NULL, 0, 'Remove the crackers from the freezer and let sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2753E474487FEB4B92F04145221EB16F', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 12, NULL, NULL, 2, NULL, 0, 'Draw two eyes and a smile on the green butterfly heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5977BFF125DD8D4FA9558E505294D5B5', X'331423FAE41A9C4893CB926428DA5DD0', X'331423FAE41A9C4893CB926428DA5DD0', 13, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'958F77DA23CFC5499F633E307D1DBE68', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 0, NULL, NULL, 2, NULL, 0, 'Mix the blue food coloring in the can of white frosting until you get the desired blue color.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDC3EF2D457C71429EB1C9A99E6DB7ED', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 1, NULL, NULL, 2, NULL, 0, 'Frost the top of the Rice Krispie Treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FA45712FD90CA48AAD579FC9D3EE899', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 2, NULL, NULL, 2, NULL, 0, 'Alternate the orange and yellow mini M&Ms around the outside of the Rice Krispie Treat to form the pool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6453A3FEA2A07440988EA2307806B9FC', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 3, NULL, NULL, 2, NULL, 0, 'Put a Gummy Lifesaver candy in the middle of the pool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DAEEDF31C368347A42C18EBF472A5E3', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 4, NULL, NULL, 2, NULL, 0, 'Cut the Teddy Graham in half and attach to the top of the Lifesaver with frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'428A42C50D8CCF44A9098B2B94B10A76', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 5, NULL, NULL, 2, NULL, 0, 'Or put 2 Teddy Grahams that have been cut in half in the pool with a gumball.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'223F0AD595067847B33FC8616EA45086', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 6, NULL, NULL, 2, NULL, 0, 'Or place a bite size Rips candy in the pool and lay a Teddy Graham on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEBA3C62CE85614F95EDCA4EC8DFC5E0', X'9398A31AAB14694AB661B63FD3FA7172', X'9398A31AAB14694AB661B63FD3FA7172', 7, NULL, NULL, 2, NULL, 0, 'Push in paper drink umbrellas in the cereal treat pool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9156155953C1E941BBAFAC8884E74589', X'48F08F854EC4654CB4393A4CF02BC1F1', X'48F08F854EC4654CB4393A4CF02BC1F1', 0, NULL, NULL, 2, NULL, 0, 'Dip an Oreo Cookie into white Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8681D731037C4B4980A003568E7B80F3', X'48F08F854EC4654CB4393A4CF02BC1F1', X'48F08F854EC4654CB4393A4CF02BC1F1', 1, NULL, NULL, 2, NULL, 0, 'Remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4233A24617CED419717011B25EC16D0', X'48F08F854EC4654CB4393A4CF02BC1F1', X'48F08F854EC4654CB4393A4CF02BC1F1', 2, NULL, NULL, 2, NULL, 0, 'Set cookie on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3B8DC05A4DAD5448C89A94890E29BCF', X'48F08F854EC4654CB4393A4CF02BC1F1', X'48F08F854EC4654CB4393A4CF02BC1F1', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on some Pop Rocks and star sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72691ADDC1C25048852A97CB2C376F98', X'48F08F854EC4654CB4393A4CF02BC1F1', X'48F08F854EC4654CB4393A4CF02BC1F1', 4, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A964B0440F89CE47AB80AD086EA5C73B', X'48F08F854EC4654CB4393A4CF02BC1F1', X'48F08F854EC4654CB4393A4CF02BC1F1', 5, NULL, NULL, 2, NULL, 0, 'Let candy coating dry on cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA3F360695DE534BA935E4DCB1B54E3F', X'48F08F854EC4654CB4393A4CF02BC1F1', X'48F08F854EC4654CB4393A4CF02BC1F1', 6, NULL, NULL, 2, NULL, 0, 'Serve');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21B868BA4E3E9B49B5DC6BED4F3050D0', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 0, NULL, NULL, 2, NULL, 0, 'Line a baking pan with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C63D03CAE6DB764CADB3CC4A4B9882F9', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 1, NULL, NULL, 2, NULL, 0, 'Set an 8 x 5.75 X 2 inch oval cake ring on the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70D1F06BD3CE144FA134641C9DCE11E0', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 2, NULL, NULL, 2, NULL, 0, 'Pour 6 ounces of the yellow chocolate into the cake ring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE5088179FF36F4D9BA72AE7D63E48AB', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 3, NULL, NULL, 2, NULL, 0, 'Spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87870F156DF4E64CAC08298AB455126A', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 4, NULL, NULL, 2, NULL, 0, 'Tap the pan gently to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6F370E7D59F5F46AC3CFA0D3865CC5F', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 5, NULL, NULL, 2, NULL, 0, 'Chill in the fridge if using pure white chocolate and in the freezer if using Candy Melts for about 10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16CEAE1B2017CD459E30CF7916815C00', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 6, NULL, NULL, 2, NULL, 0, 'Remove from cake ring and use a knife to trim any excess around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C890999A914884DB5E2D14158AFF922', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 7, NULL, NULL, 2, NULL, 0, 'Repeat creating another yellow chocolate oval using 6 ounces of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B077EE7DE7C3B5499F913C71A2873386', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 8, NULL, NULL, 2, NULL, 0, 'Roll out white candy clay to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C524558060B5E6438184BF18736FA370', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 9, NULL, NULL, 2, NULL, 0, 'Cut two circles using a 2 1/4 inch round cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'634846251AD5F3489C0B4460398C6976', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 10, NULL, NULL, 2, NULL, 0, 'Use the 1 5/8 inch round cutter to cut a hole out of the center of each large circle (for the Minion''s goggles).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA8457542B0E284792F7568B90A344FE', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 11, NULL, NULL, 2, NULL, 0, 'Flatten those smaller circles to about 1/8th of an inch thickness and cut using a 1 1/2 inch cutter (for the whites of the eyes).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'009E98668F7FE845A7E7F09839F95BDC', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 12, NULL, NULL, 2, NULL, 0, 'Roll out some black and white candy clay to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29D137E3B500964E89E4CE54B33858F6', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 13, NULL, NULL, 2, NULL, 0, 'Cut a 7/8 x 1/2 rectangle out of each color.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3E94B9C6CE90647888043E9E629E551', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 14, NULL, NULL, 2, NULL, 0, 'Cut both in half lengthwise.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B972FE234CFCD4C871C153207D9CED1', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 15, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of piping gel along one long edge of a white piece and press it up against a long edge of a black piece.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81028DF9D111BB42BB58B37FDA0053FB', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 16, NULL, NULL, 2, NULL, 0, 'Repeat creating two 7/8 X 1/2 inch black and white rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEB143286C574B4DB8E47BC01B8E2C24', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 17, NULL, NULL, 2, NULL, 0, 'Cut a notch on the edge in the mid section of the black part of each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D268993AC2471E479E25E4830B023E6C', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 18, NULL, NULL, 2, NULL, 0, 'Use piping gel to attach them to the google pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'898E7C71BAC28D478FCD3BA88CED1183', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 19, NULL, NULL, 2, NULL, 0, 'Combine about a teaspoon of lemon extract with about a teaspoon of edible silver luster dust until you get a nice fluid yet thick mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3712A10D80FDA4EBB4010DE6F16E028', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 20, NULL, NULL, 2, NULL, 0, 'Paint the white candy clay goggles silver.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DFACC671C89EB4E9AD1D716C01E42AE', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 21, NULL, NULL, 2, NULL, 0, 'Let dry for at least 30 minutes before touching.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAF62C2A92B43143BA12C61E06C92FDD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 22, NULL, NULL, 2, NULL, 0, 'Roll out some black candy clay to 1/16th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0421EE3DE024174980CBB3FB037F5127', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 23, NULL, NULL, 2, NULL, 0, 'Cut two circles using a 3/4 inch round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D6D1F256142B64486C9FADE4EC893BC', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 24, NULL, NULL, 2, NULL, 0, 'Attach to the center of the white eye pieces (1 1/2 inch circles of candy clay).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7484161436C4B744B85D4FE3BCAAB5D7', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 25, NULL, NULL, 2, NULL, 0, 'Roll out blue candy clay to 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA3B26C1714E124A8365B1554C7AE917', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 26, NULL, NULL, 2, NULL, 0, 'Cut out the Minion Template (resized if needed to fit the size of your chocolate Minion).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'991F0C6778A1A74EB0FD6408FBCDB3C4', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 27, NULL, NULL, 2, NULL, 0, 'Cut out the blue overalls, the straps, and the pocket and set on the blue candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E5939E4386B624EB0DC4B3EE0E02879', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 28, NULL, NULL, 2, NULL, 0, 'Cut the candy clay out around the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CCB400856F5974BB459BA684B15E6AC', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 29, NULL, NULL, 2, NULL, 0, 'Use a fondant sticking wheel to create the stitching around the pockets and crotch area of the overalls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2235EE80CBBC734D94878855980E406F', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 30, NULL, NULL, 2, NULL, 0, 'Draw the `G` logo onto the half moon shaped pocked using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4364A09112B0BD40963E0DCA0D515125', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 31, NULL, NULL, 2, NULL, 0, 'Use piping gel to attach all the pieces to one of the yellow chocolate ovals creating your Minion.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'416DA5598EE57C45AE5CCA03A518599C', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 32, NULL, NULL, 2, NULL, 0, 'Draw on a smile using black food coloring marker or use a thin log of black candy clay to create one, if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F803584D88C9FE4A9382F5FED46A7347', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 33, NULL, NULL, 2, NULL, 0, 'Roll out yellow candy clay into a 24 inch long log.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B569A7611132046AD3E1A82AD618A62', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 34, NULL, NULL, 2, NULL, 0, 'Flatten and roll out to 1/2 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EB6F95484205D4E8D80AD5CBEFC3AE3', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 35, NULL, NULL, 2, NULL, 0, 'Cut into a 1 1/2 inch (or 2 1/2 - 3 inch) wide by 24 inch long strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9028BC3C18C4204D87C62B83E274301E', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 36, NULL, NULL, 2, NULL, 0, 'Set the oval cake ring on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'724805FE0A904C4D8147DE6284AE2270', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 37, NULL, NULL, 2, NULL, 0, 'Wrap the yellow strip of candy clay around the inside of the oval ring so that it creates the walls of your chocolate box.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'253952D15F9178448C2E3BBDBED222BE', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 38, NULL, NULL, 2, NULL, 0, 'Cut the ends as needed so they match up neatly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC948E46DB37684FB27E92648602B7E0', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 39, NULL, NULL, 2, NULL, 0, 'Attach the seam using a small amount of piping gel. Make sure the seam does not stick to the inside of the cake ring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4256A79BBCF84244AD2C519928792E80', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 40, NULL, NULL, 2, NULL, 0, 'Let dry for at least 30 minutes, preferably longer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D20BDA11F0638F4FA170B4A91E43BD7D', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 41, NULL, NULL, 2, NULL, 0, 'Remove the cake ring from the yellow candy clay walls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D7DB8CB729A19459469429B2395D2EF', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 42, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of yellow chocolate on the top edge of the candy clay walls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2877A4E6B46FD24DA60239C3569CCE8D', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 43, NULL, NULL, 2, NULL, 0, 'Set the plain yellow chocolate oval on top creating the base of the box.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10AA2A4458412C449F244828F6F63EAE', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 44, NULL, NULL, 2, NULL, 0, 'Let dry for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF99DC3F84A63F41ACE73BFC52977E34', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 45, NULL, NULL, 2, NULL, 0, 'Carefully turn the box base right side up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99D2BE5A05A66641B6D2CF063A0E1137', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 46, NULL, NULL, 2, NULL, 0, 'If needed, brush the interior with melted yellow chocolate to reinforce the walls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FE045EA446A4D40B3DF987BA91969F6', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 47, NULL, NULL, 2, NULL, 0, 'Let dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C535B1ADE7BCE45B826881B4DDCB6D1', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', X'19D15ACCAEDE2545A2CF4218CFF2D0AD', 48, NULL, NULL, 2, NULL, 0, 'Fill box with chocolate or candy and set the decorated chocolate Minion lid on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61AAAF8147BBB74DB512A273A4B89437', X'8D5775578B423B4DA7C1D5FD20C8DFD0', X'8D5775578B423B4DA7C1D5FD20C8DFD0', 0, NULL, NULL, 2, NULL, 0, 'Stir together the yogurt and Cool Whip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'282A7C58AF5DD24D9224492900889E31', X'8D5775578B423B4DA7C1D5FD20C8DFD0', X'8D5775578B423B4DA7C1D5FD20C8DFD0', 1, NULL, NULL, 2, NULL, 0, 'Pour into the 3 bunny shaped cavities in the silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF34DAD1F2ED1546AC4BF58B06185B35', X'8D5775578B423B4DA7C1D5FD20C8DFD0', X'8D5775578B423B4DA7C1D5FD20C8DFD0', 2, NULL, NULL, 2, NULL, 0, 'Insert popsicle sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'619C786E7D1D91488A6609CB94DB4202', X'8D5775578B423B4DA7C1D5FD20C8DFD0', X'8D5775578B423B4DA7C1D5FD20C8DFD0', 3, NULL, NULL, 2, NULL, 0, 'Freeze for about 4 hours until the pops are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A854502E0B8E2E4CAA49090EB9300DCA', X'8D5775578B423B4DA7C1D5FD20C8DFD0', X'8D5775578B423B4DA7C1D5FD20C8DFD0', 4, NULL, NULL, 2, NULL, 0, 'Remove from mold and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F57E0AAEB713FE45A720DC0AEEEC0CDE', X'8D5775578B423B4DA7C1D5FD20C8DFD0', X'8D5775578B423B4DA7C1D5FD20C8DFD0', 5, NULL, NULL, 2, NULL, 0, 'Store in the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A653C511137DC4D8930AC4A53FC77F1', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 0, NULL, NULL, 2, NULL, 0, 'Unwrap the Yellow Reese''s Eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A8CC13F6CE8AF4C8E62598DF1A9A63F', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 1, NULL, NULL, 2, NULL, 0, 'Pipe little feathers using yellow candy melts onto a parchment paper lined baking sheet and immediately attach a Reese''s egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8521A724904F254AA394E641FFC02951', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 2, NULL, NULL, 2, NULL, 0, 'Add 3 dots of yellow candy melts to the egg and attach two candy eyes and one beak.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD8A90EDBB97B447B09063E4E6019408', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 3, NULL, NULL, 2, NULL, 0, 'Freeze for a minute or so just until the candy feathers harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F78E09364585BD4F8ACD6B7C71134635', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 4, NULL, NULL, 2, NULL, 0, 'Carefully peel the chicks off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5FAA7F79263B746BCE844DC41431C2C', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 5, NULL, NULL, 2, NULL, 0, 'Pour a spoonful of white candy melts into the smaller half of a plastic egg. Brush the candy coating up the sides of the egg painting the top edge so it''s jagged.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD5BFD5861772043AFF1CCFAC913A1F9', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 6, NULL, NULL, 2, NULL, 0, 'Freeze upright for 1 1/2 minutes then remove and let sit at room temperature for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7DBDBFBFC617046B22E2A021E3756A5', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 7, NULL, NULL, 2, NULL, 0, 'Pour another small spoonful of candy melts into the egg and brush it up the sides adding a second coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E60F2A07328224B82AC933FE0C4AA4E', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 8, NULL, NULL, 2, NULL, 0, 'Freeze for another 2 minutes then remove and gently tug on the candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A2B7D8B07021F4895DF32BF95A9C051', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 9, NULL, NULL, 2, NULL, 0, 'If the candy shell releases easily then pull it out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DEC6E1AF4BDEC48A41CDB12E1D64B40', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 10, NULL, NULL, 2, NULL, 0, 'If not, freeze for another minute then try again.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56A0194035B9CD43B784A221D3AE7D20', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 11, NULL, NULL, 2, NULL, 0, 'Repeat making a total of 12 egg shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F4CD517DFF18144943B7721BEB2F311', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 12, NULL, NULL, 2, NULL, 0, 'Set the shells upright and spoon in about a teaspoon of white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C044167E8F4C704CBA68ABA693D198BD', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 13, NULL, NULL, 2, NULL, 0, 'Let that candy thicken slightly then set a chick upright in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D780BC565E77D242AC4BCB699F0A7E44', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 14, NULL, NULL, 2, NULL, 0, 'Hold the chick in place until it stands upright on it''s own.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'540FBAB0A0C1BC4482A775F6754D5B81', X'23AB840A7D2CEF428465DB830396BC89', X'23AB840A7D2CEF428465DB830396BC89', 15, NULL, NULL, 2, NULL, 0, 'Arrange on a serving platter and sprinkle toasted coconut around the chicks, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9047C2A5587B94387026DBEEAC14780', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine butter and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52E941F95E148E4499080E18443B93E9', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 1, NULL, NULL, 2, NULL, 0, 'Cream until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFA16E233DB30D4B96C38452B7A982F4', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 2, NULL, NULL, 2, NULL, 0, 'Mix in egg and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F17724E47C65243AD55B8ABB7976903', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 3, NULL, NULL, 2, NULL, 0, 'Combine flour, cocoa powder, salt, and baking powder in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09806AEE170C484EAD5F8857DAAD502C', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 4, NULL, NULL, 2, NULL, 0, 'Pour into the butter mixture and mix just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F6EDD40E371514794EB961016BA4018', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 5, NULL, NULL, 2, NULL, 0, 'Scoop out the dough and flatten into a disc then wrap in plastic wrap or place in a zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F98DFB007086174393730844AB2A27F2', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'415A6F624982F441BD660978C1DC1229', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 7, NULL, NULL, 2, NULL, 0, 'Remove dough from refrigerator.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74289DFF7F7312458DDEA41B8076EDF3', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 8, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'792451D681DF2D42A21F63DC73DA310D', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 9, NULL, NULL, 2, NULL, 0, 'Roll dough out between 1/8th and 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56D9F9D8A3769D4B82556FD3FB507E5C', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 10, NULL, NULL, 2, NULL, 0, 'Cut out 12 large Batman shaped cookies using a 4 inch cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E283451B68B874DA6729829B36FA4FB', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 11, NULL, NULL, 2, NULL, 0, 'Remove excess dough and place cut-outs in the freezer for about 10 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB5D9DCEA78CA14AB11C1FA021965D69', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 12, NULL, NULL, 2, NULL, 0, 'Remove and peel off cut-outs then set them on parchment paper lined baking sheets and bake for 14-16 minutes until the edges are set and the tops no longer look wet and really shiny.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB677E361BC8F54BAC1EF0CCE94AA18B', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 13, NULL, NULL, 2, NULL, 0, 'Roll out the remaining dough to about 1/2 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF0DE6A7E0953149BCF53CEF6D1B9DF1', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 14, NULL, NULL, 2, NULL, 0, 'Cut out a few Batman shapes using the 4 inch cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3037C3EBB6C5B349BE5C60F372EBC626', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 15, NULL, NULL, 2, NULL, 0, 'Use the smaller 2 1/4 inch Batman cutter to cut a hole in the center of those cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A648B2F088D9645825580B004843632', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 16, NULL, NULL, 2, NULL, 0, 'Remove and re-roll the excess dough and continue until you have 6 cookies with holes in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C575710556356449A13A469FDA58680E', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 17, NULL, NULL, 2, NULL, 0, 'Freeze the cut-outs for 10-15 minutes until firm before transferring them to a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67A10CF9E5F6AC45B93136D409F7E2CA', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 18, NULL, NULL, 2, NULL, 0, 'Bake for 16-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF325E54B416FA4498B396859DF88B82', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 19, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'661B69548858104FB9220256BE4F7A11', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 20, NULL, NULL, 2, NULL, 0, 'Pour the melted chocolate into a disposable pastry bag or zip top bag then cut off one tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2633E957A0077C4D86203E91FCCD42C1', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 21, NULL, NULL, 2, NULL, 0, 'Pipe a small amount of chocolate on the back side of a cookie with a hole in the middle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9CA9293B4F06E4CAC07860E980EB563', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 22, NULL, NULL, 2, NULL, 0, 'Set that cookie on top of a whole cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'960182D5DB1A164597CE19D005D60D4D', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 23, NULL, NULL, 2, NULL, 0, 'Pour candy into the center of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1FB7CA54FAA954390E81B7054FAC218', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 24, NULL, NULL, 2, NULL, 0, 'Pipe chocolate over the top edge of the cookie with the hole in it then immediately set a whole cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5F165DFA881C94194409333B70E3B8D', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 25, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'690A4A2D74F74343BA0D1281C17DD671', X'8B2BFB92D69E014BBE754ECC326127B9', X'8B2BFB92D69E014BBE754ECC326127B9', 26, NULL, NULL, 2, NULL, 0, 'Let the chocolate dry for 30 minutes before picking up the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E44DE499BF702545801B6C745F924A4E', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 0, NULL, NULL, 2, NULL, 0, 'Paint the pad and toes in each paw print mold with the colored candy melts, making 4 paws with each of the six colors.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05DA8E2FBDA6F446981A341340793B88', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 1, NULL, NULL, 2, NULL, 0, 'Pop the molds in the freezer for 2-3 minutes until the colored candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A1DADA84C02B1408159AFD664837CFB', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 2, NULL, NULL, 2, NULL, 0, 'Remove and let the molds sit at room temperature for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68A8BDC9F4957D45963EC886F3C81842', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 3, NULL, NULL, 2, NULL, 0, 'Then fill one paw print about three quarters full of white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F51CD3BE5B240E48BBAD5559A237CCE8', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 4, NULL, NULL, 2, NULL, 0, 'Immediately sprinkle on enough cookie pieces to cover the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C840AEB87C2424F9C4A2659F6B97C90', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 5, NULL, NULL, 2, NULL, 0, 'Repeat filling all the paw prints.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3762FB0DAC87C34FA8C9341A14AB97DB', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 15-20 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'583EB3F10F0D4143AAE0E85901E4037E', X'EB2610D167357B4B86CDBE04402B3137', X'EB2610D167357B4B86CDBE04402B3137', 7, NULL, NULL, 2, NULL, 0, 'Remove from the freezer and push the candy out of the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B8885EA318B224F86E12B517BF1B0A9', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Reese''s Cup and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F38906E52E28674FAA53D91D25D24CF4', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 1, NULL, NULL, 2, NULL, 0, 'Roll out tan candy clay and cut into a 3/4 moon shape beard.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F781012687D054CB34BA83F53DE0EC8', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 2, NULL, NULL, 2, NULL, 0, 'Use a knife to make the beard look furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CEF0E1D6FF735499BBC8BC053E80A75', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 3, NULL, NULL, 2, NULL, 0, 'Cut two small eyebrows and also make look furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2C4203E52DDE34CB272B4C247DD8415', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 4, NULL, NULL, 2, NULL, 0, 'Roll out some brown candy clay and cut two tear drop shaped ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'451737ECB7BDD74782BCAE5FAE3D5EBE', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 5, NULL, NULL, 2, NULL, 0, 'Press a toothpick from the bottom edge upward forming a crease in each ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FD5BCC97222DE4B80B76A81E5D8F19F', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 6, NULL, NULL, 2, NULL, 0, 'Cut the bottom edge of the ear into a curve that will match up with the outer edge of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BEBD2C840D20644B358E9F2BEEF9558', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 7, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of brown candy clay and roll it into a log for Beast''s mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69B806960F79B341A4B4078A482F6B16', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 8, NULL, NULL, 2, NULL, 0, 'Roll out a tiny piece of white candy clay and cut it using a small triangle or teardrop shaped cutter. Cut that piece in half and cut each half so that you end up with two short fangs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC0E9FD0EA07FC44A010B201369FC379', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 9, NULL, NULL, 2, NULL, 0, 'Roll a small piece of the tan candy clay into Beast''s nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F3870C5201E9B45A1291A1F0E61DA41', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 10, NULL, NULL, 2, NULL, 0, 'Roll some black candy candy clay into a thin log and attach it to the base of the nose using a small dab of water then cut off any excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E63A7190239BF14DAD1B92FA01E63189', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 11, NULL, NULL, 2, NULL, 0, 'Roll out black candy clay and cut two black eyes using a small round plunger cutter or pinch off small pieces and roll them into balls then flatten them into small circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EC828F2EE98754C8665B4EF959A1D98', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 12, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of black candy clay and roll into a horn then twist it and curve it.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD4086DDF1223D4382FA42F88C8CF441', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 13, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of brown candy clay and roll and shape into Beast''s paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC8F102BD3BDCC489F40DFF2EFF177A1', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 14, NULL, NULL, 2, NULL, 0, 'Use melted chocolate to attach the pieces to the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB96C67B1F847945935642072AFEC82B', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 15, NULL, NULL, 2, NULL, 0, 'Set aside for a few minutes to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB1BFDF49793924B91998074923BB082', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 16, NULL, NULL, 2, NULL, 0, 'Then use a knife to make the nose look furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E965B23A4CDF6642A3FB2B4A10FFF751', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 17, NULL, NULL, 2, NULL, 0, 'Create white highlights on each eye using small dabs of white food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC1E07B5D0134A4FAB615AFD7B88F8B2', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 18, NULL, NULL, 2, NULL, 0, 'Set aside and allow the food coloring to dry for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FEE4388B4458043AFDAC2CECF6C1392', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 19, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of blue frosting onto a cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'136B0A35D1324642AEE85AB41B728532', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 20, NULL, NULL, 2, NULL, 0, 'Optional: break a Pocky stick so that it is just long enough to be inserted into the cupcake, through the frosting, so that it supports the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'446D94A07E7B514A88F1DFED8DFC5C18', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 21, NULL, NULL, 2, NULL, 0, 'Pipe blue frosting around the top edge of the Pocky stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5ED21929F41F38449B974A1AA35FAE34', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 22, NULL, NULL, 2, NULL, 0, 'Set Beast''s head on the top part of the blue frosting swirl in front of the Pocky Stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC635DAC918907469EE1815CDA7CC727', X'E5C8CA2D815850449A51585FE2393A14', X'E5C8CA2D815850449A51585FE2393A14', 23, NULL, NULL, 2, NULL, 0, 'Insert two paws into the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D50E44D37606F43873D605281702D8A', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 0, NULL, NULL, 2, NULL, 0, 'Roll out dark brown candy clay and cut a circle that is the same size as the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB75302FB4A50D41B0751CED73EC7A39', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 1, NULL, NULL, 2, NULL, 0, 'Use a small heart cutter to cut a heart out of 2/3 of the brown candy clay circle creating Belle''s hairline.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41B8ECEEE0AEB246AF22B82876A96A8E', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 2, NULL, NULL, 2, NULL, 0, 'Cut a small 3/4 moon shape for Belle''s bun.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92758E102DBE97418A5F9BB143E6024F', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 3, NULL, NULL, 2, NULL, 0, 'Attach the bun to the hair using a small dab of water. Let it set aside to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A9A738D0DE24E4A9DE6BFF8858BA8DD', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 4, NULL, NULL, 2, NULL, 0, 'Cut a curved tear drop shape out of dark brown candy clay then pinch it so that you have a long pony tail with a flat piece that can be attached behind the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D11AF5A67582540BA0DE19717C96891', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 5, NULL, NULL, 2, NULL, 0, 'Roll out some peach colored candy clay and cut a circle to match the size of the hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6ACEA6CA962A68459A20909C80DFE570', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 6, NULL, NULL, 2, NULL, 0, 'Brush a tiny amount of water on the top edge of the peach circle then press the hair piece on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7048C2D467B1684AB760F6E8244931ED', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 7, NULL, NULL, 2, NULL, 0, 'Roll out a thin log of yellow candy clay and drape it over the seam between Belle''s hair and bun, cutting off the excess yellow candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4559EE29F402948BDD5A8294E0B01EE', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 8, NULL, NULL, 2, NULL, 0, 'Then wrap a log around the ponytail and cut off excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0108105EC9FCD94082C849BAF41A7B2F', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 9, NULL, NULL, 2, NULL, 0, 'Create two small eyes out of black candy clay and lips using a tiny piece of red candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EFB3FC2372B7141831FA528922F45DB', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 10, NULL, NULL, 2, NULL, 0, 'Attach the eyes and lips to Belle''s face using water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13090D3582DE9D4CAEBA692F3F8D4B1E', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 11, NULL, NULL, 2, NULL, 0, 'Add a highlight to each eye by dabbing on some white food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBDEEDF5BA3AB249A2663530989A3BC8', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 12, NULL, NULL, 2, NULL, 0, 'Set aside to allow the food coloring to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECC1728822AB2D418BA9680F94D38303', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 13, NULL, NULL, 2, NULL, 0, 'Use melted chocolate to attach Belle''s face to a Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0D2B7D9C7DDD44E925206E12564C250', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 14, NULL, NULL, 2, NULL, 0, 'Attach the ponytail to the back of the Reese''s Cup so that it hangs down past the bottom edge of her hairline.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2882D0D67816E34BB678CA6BF5289746', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 15, NULL, NULL, 2, NULL, 0, 'Shape two arms out of peach candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9061DAE98A41140819BA5118FE879B6', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 16, NULL, NULL, 2, NULL, 0, 'Pipe a golden yellow swirl of frosting on a cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06BF7076C88148449D97527F7FC75674', X'520D6D64BC4F7640A0B8C3A8F542A15C', X'520D6D64BC4F7640A0B8C3A8F542A15C', 17, NULL, NULL, 2, NULL, 0, 'Attach the Reese''s cup and Belle''s arms to the frosted cupcake using a chocolate Pocky stick for support if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A00BF7103A74424F9A4F79B3B5F292C5', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 0, NULL, NULL, 2, NULL, 0, 'Pour the chocolate into a 3-4 inch tall glass, mason jar or mug.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'737B5E6252277C4DB7AE0999BC3328C1', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 1, NULL, NULL, 2, NULL, 0, 'Use a thin knife to crack open the Nutter Butter Cookies leaving the filling on one cookie half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22D29DB25D41C84697A3EA0271FA4982', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 2, NULL, NULL, 2, NULL, 0, 'Brush the other cookie half with a thin layer of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F919167C36928B46AAE035C4FD6FC8CE', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 3, NULL, NULL, 2, NULL, 0, 'Lay the top 1/3 rd of a craft stick over the chocolate then flip it over so that both sides are covered in chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22DCF4C4B3247743A45518D02EDB7903', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 4, NULL, NULL, 2, NULL, 0, 'Set the other cookie half on top recreating the sandwich cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FFD66584F943644AAC04ABA4BD47CA6', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 5, NULL, NULL, 2, NULL, 0, 'Repeat with all the cookies then allow the chocolate to harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DB404ECDD8DD94DAB4EFFE7250CE6D5', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 6, NULL, NULL, 2, NULL, 0, 'Roll out the light brown modeling chocolate/candy clay to about 3/16th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3174C7885AEDCB4FB9BD271B4DB8ABBC', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 7, NULL, NULL, 2, NULL, 0, 'Cut out six 1 1/2 inch circles for the snouts and twelve 1 7/8 inch tear drops for the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'074D8A3EAAC47E45BD5F6EAA2B027D39', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 8, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of the black candy clay and shape them into noses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43C2163E46278646BEADE528E9B6C14F', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 9, NULL, NULL, 2, NULL, 0, 'Make tiny tongues using the red candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8702424F442C0441AFE1887AE28E77AC', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 10, NULL, NULL, 2, NULL, 0, 'Reshape the chocolate snout so that it''s more oval.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF0531B495FCCE4E927A808833B41310', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 11, NULL, NULL, 2, NULL, 0, 'Attach the nose to the snout using a dab of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'779D66CAB3769944BFF8289666D172CB', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 12, NULL, NULL, 2, NULL, 0, 'Use the blunt end of a wood skewer to create 3 dimples on either side of the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84556C618D02C34BA4D0790000DAFA08', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 13, NULL, NULL, 2, NULL, 0, 'Attach the tongue to the bottom edge of the snout using water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D776DF980EEABA47A3A8E0668DF81690', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 14, NULL, NULL, 2, NULL, 0, 'Line a baking tray with parchment paper or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A37AB046C8CE344F8F203480DEF3FC38', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 15, NULL, NULL, 2, NULL, 0, 'Arrange the candy clay ears with just enough room in between the two so that the Nutter Butter Pops can be set on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B765BF6513E1049A806CA1C9F642BC7', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 16, NULL, NULL, 2, NULL, 0, 'Dip a Nutter Butter Pop into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2CD441F7C407845AF772D5DDD0BAEA9', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 17, NULL, NULL, 2, NULL, 0, 'Lift it out of the glass and allow the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9F34FF205D6EC4C98EBB60C3F23A55D', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 18, NULL, NULL, 2, NULL, 0, 'Set on the candy clay ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2F9DF629EAA0244A63141643E8C4172', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 19, NULL, NULL, 2, NULL, 0, 'Immediately attach the snout and two candy eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E0E0921935CA147906F00C01EAAFA4F', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 20, NULL, NULL, 2, NULL, 0, 'Repeat creating 5 more puppy pops.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C8E2B2BC264FB41974A2D4F50BA204B', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 21, NULL, NULL, 2, NULL, 0, 'If using pure chocolate place the pops in the refrigerator to chill until the chocolate hardens for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9EE4C37FCF51149A7EFB1CADA30DBD0', X'2F4502AE0CD4E543B78528FA7E8C2D21', X'2F4502AE0CD4E543B78528FA7E8C2D21', 22, NULL, NULL, 2, NULL, 0, 'If using Candy Melts place them in the freezer instead.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F2D7C1364E3964B974DA283A4003AC3', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 0, NULL, NULL, 2, NULL, 0, 'Combine the yellow Candy Melts and the sweetened condensed milk in a microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'448AC65E7E1B244B8BE74A7F5E504EEF', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 1 minute then stir well until the candy begins to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E930E9220A7C7245A787E43C42274F5E', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 2, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds on high power then let the bowl sit in the microwave for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04322E814937554C9CE4012B96A7F844', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir thoroughly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2602CA233409724EAAFC967CCCFC52B6', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 4, NULL, NULL, 2, NULL, 0, 'Let rest for 2 more minutes then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE5903C6838D0743A9F67236FE720C4F', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 5, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A93FEE2D55E76D40A31C3BC924E51393', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 6, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and set the bowl aside for several hours until the fudge firms up or refrigerate for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5F9FD77EEE9624EA0498D2CC5BE523A', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile rub the black Candy Melts wafers in the palm of your hand. This will remove any white marks and soften the wafer slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1F252A6CD4BFE489EE7C390BB20B7E4', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 8, NULL, NULL, 2, NULL, 0, 'Cut out one or two smiles from each wafer using a 1 1/4 inch round cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15B1FA44FDA4D8448CE8D9523BF4F965', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 9, NULL, NULL, 2, NULL, 0, 'Scoop out 1 1/2 tablespoons of fudge using an ice cream scoop or spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA12832192744E4DB0C7F34C74440C96', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 10, NULL, NULL, 2, NULL, 0, 'Roll into a ball then flatten into a 2 1/4 inch round disc onto a non-stick silicone mat or piece of wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C3F330FEB7CF049AF66EDAD34C24033', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 11, NULL, NULL, 2, NULL, 0, 'Immediately press two heart sprinkles or two Sixlets into the fudge creating the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBD6D331B38A0E48B705FDC46F0336B7', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 12, NULL, NULL, 2, NULL, 0, 'Press a black Candy Melts smile below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9A392F8AAA5164084405D090D8AF661', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 13, NULL, NULL, 2, NULL, 0, 'Repeat the process, creating a total of 30 emoji.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1450469B29B8349B8C0A4B783932EAC', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 14, NULL, NULL, 2, NULL, 0, 'Pop the candy into the freezer for 10 minutes until they firm up and are easy to remove from the silicone mat or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'924D41D4C7F46B43A9318BAE64226516', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 15, NULL, NULL, 2, NULL, 0, 'Let warm up for 15 minutes before packaging them in clear 3 inch x 5 inch cellophane bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87B82295FAC2F443A21B588A6061AD8E', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 16, NULL, NULL, 2, NULL, 0, 'Fold over the top of the bag and tape it closed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF5F91267C93A840A4CBD0F004CEF0BF', X'F865EACCF59C8F419671E3E0E683A0E2', X'F865EACCF59C8F419671E3E0E683A0E2', 17, NULL, NULL, 2, NULL, 0, 'Print and cut the Valentine''s Day tags, fold them over, add your child''s name and the recipient''s name, then use double stick tape to attach them to the top of the bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDD3228CD7DD9045AF0E937A7439EA68', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 0, NULL, NULL, 2, NULL, 0, 'Line a pan with parchment paper, wax paper, or a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F15EC80C0E2AD146A86AF30CEC6EEC90', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 1, NULL, NULL, 2, NULL, 0, 'Combine the chocolate chips and Dulce de Leche in a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FC038275FEE504F91D86398498B674F', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 2, NULL, NULL, 2, NULL, 0, 'Heat over low heat, stirring frequently until just small pieces of solid chocolate remain.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2A707FFA8A4A948AB6DB60384360AFC', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and let sit for 3-4 minutes, then stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69059704E2923D4DB50EC3E9B2637F69', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 4, NULL, NULL, 2, NULL, 0, 'Pour into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'557A789F114640419EA121F1CF702FA8', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 5, NULL, NULL, 2, NULL, 0, 'Cut off the tip and pipe out 10 big swirls of the fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC8B9E3D10120D45A456E43057761284', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 6, NULL, NULL, 2, NULL, 0, 'Immediately press two candy eyes onto each swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3109F7C80B668F4ABF2074F91D4B081A', X'6D6B558DA305934D94E195AC25E50212', X'6D6B558DA305934D94E195AC25E50212', 7, NULL, NULL, 2, NULL, 0, 'Then cut the white candy wafers into smiles and press onto the fudge just below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B43A377FB4606D4F96E473E35626AFD4', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 0, NULL, NULL, 2, NULL, 0, 'Unwrap the Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F084C23B5F529458DD79E59EC921B61', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 1, NULL, NULL, 2, NULL, 0, 'Brush a small amount of melted White Candy Melts over the top (the wider end) of a Miniature Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3681E34FC7A458448E9A163643A44BD5', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 2, NULL, NULL, 2, NULL, 0, 'Turn it upside down and press it onto one of the 1.5 ounce White Reese''s Cup near one edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F47268A65469314CB9C63984427D87E7', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 3, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes and one brown Reese''s Pieces nose using more White Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'886BCB4F95CDC84A85F8C6A54640989F', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 4, NULL, NULL, 2, NULL, 0, 'Set aside to allow the Candy Melts to dry for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66A7D589C658E648AF06D443F9E6D238', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 5, NULL, NULL, 2, NULL, 0, 'Then attach two Mini White Reese''s Cup ears to the edge of the 1.5 ounce Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AF2DF769D5EB240AF79CD3E5E145D86', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 6, NULL, NULL, 2, NULL, 0, 'Let dry for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F541C95481DDE4599DD3000934EE146', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 7, NULL, NULL, 2, NULL, 0, 'Draw a pad and 5 toes onto the remaining 12 White Mini Reese''s Cups using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF1BD043D0662249B85873736BA8FCB8', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 8, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of white frosting on each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DF380B8F7721A429FDA747C133AEF01', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 9, NULL, NULL, 2, NULL, 0, 'Snuggle a Reese''s Cup Polar Bear onto the top part of the swirl adding a bit more frosting behind the Reese''s Cup if needed to keep the polar bear in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3141E35B9766C647B8CA4072D78010A6', X'32494FCADB8F604F87C15E55021566A1', X'32494FCADB8F604F87C15E55021566A1', 10, NULL, NULL, 2, NULL, 0, 'Press two paws into the frosting just above the edge of the cupcake wrapper on either side of the Polar Bear''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2CD5B87AC9EEA41962606AF1D58DC19', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD52237B95B97747B8153DC696563095', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 1, NULL, NULL, 2, NULL, 0, 'Roll out white candy clay and cut out 2 tear drop shaped ears per unicorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52B5EA8BA2E94E47AADB7364DB381666', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 2, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of white candy clay and roll into a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7575CFB86367FA4BBCB6E7B5127A0BFB', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 3, NULL, NULL, 2, NULL, 0, 'Lightly score across the horn using the back of a knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54DCA4F1E158D24CAAD679548EBAA7CB', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 4, NULL, NULL, 2, NULL, 0, 'Brush gold luster dust over horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27F0D0A1114EE04BAF4F7FBA82BA1623', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 5, NULL, NULL, 2, NULL, 0, 'Roll out pink candy clay and cut out 1 oval shaped nose for each unicorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7EE3A26DF240848A739BEAF479E0C0C', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 6, NULL, NULL, 2, NULL, 0, 'Brush the ovals with pink luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0610D8D5A54E340BD46BBD48A7B6F0C', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 7, NULL, NULL, 2, NULL, 0, 'Make two indentations in the pink oval to create the unicorn''s nostrils using the flat end of a wood skewer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C5A052AB9784C4AA50E8AEABB22ECC0', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 8, NULL, NULL, 2, NULL, 0, 'Attach one pink oval along one edge of a White Reese''s Cup using Candy Melts as edible glue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5834C7E0B989C2498ADCC6244B9E693F', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 9, NULL, NULL, 2, NULL, 0, 'Let the candy melts dry for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02032085EE32DF49BE00B526BACB7BBB', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 10, NULL, NULL, 2, NULL, 0, 'Use your finger to smooth out the edge of the oval so that the top edge of the oval slopes down to the edge of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'265BFD3F8601A44C97533652225E85BD', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 11, NULL, NULL, 2, NULL, 0, 'Brush more pink luster dust over the oval.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AA0A0BE05F3224A84736219185EF7FF', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 12, NULL, NULL, 2, NULL, 0, 'Attach two candy clay ears and the horn opposite the pink nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'345D1C2E0E54954DBB21626F9FC7B2B1', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 13, NULL, NULL, 2, NULL, 0, 'Roll pink, purple, yellow and blue candy clay into 1/4 thick logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADED6AA46E901B42B86518FA9D4AE777', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 14, NULL, NULL, 2, NULL, 0, 'Arrange the four logs into one thick log and squeeze together into a log that will fit into the clay extruder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09FBF7C855CE4B4BAE2CF49AA12FA328', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 15, NULL, NULL, 2, NULL, 0, 'Fit the clay extruder with a multi hole disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91252283EB0E364580875B01FA8D9CF8', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 16, NULL, NULL, 2, NULL, 0, 'Extrude the colorful candy clay through the extruder creating long strands of rainbow colored hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B76FA6CF1193140AF2FC93FC2B7768A', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 17, NULL, NULL, 2, NULL, 0, 'Cut off a 1 1/2 inch long section of the hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A0B4E6831767349846A569B7538A7CA', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 18, NULL, NULL, 2, NULL, 0, 'Pinch together one end and drape it over the Reese''s Cup in between one ear and the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94B315007313EF4C9953B04C7E2D4BDB', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 19, NULL, NULL, 2, NULL, 0, 'Press the candy clay hair onto the back edge of the Reese''s Cup. It should stick all on it''s own, but if it doesn''t, brush some candy melts over it to hold it in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D537E8B889C104D9C8079576C38F03E', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 20, NULL, NULL, 2, NULL, 0, 'Cut off the hair on the front of the Reese''s Cup if needed so that the unicorn''s mane hangs just below the bottom edge of the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0F17E73987A5C4281AA6BB1AD7D82A4', X'70DF7BE66D83844D9121AAC3E25A577E', X'70DF7BE66D83844D9121AAC3E25A577E', 21, NULL, NULL, 2, NULL, 0, 'Repeat adding hair in between the other ear and horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D38DB9DB6066BB41BC0C66FD60D5E8A9', X'88717EBED6D43A42A5A4A78D0F45BBFF', X'88717EBED6D43A42A5A4A78D0F45BBFF', 0, NULL, NULL, 2, NULL, 0, 'Roll yellow candy clay into a log and insert it into a clay extruder that is fitted with a small single hole plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC0DE0BD02406A4AA656EF5E53A12F52', X'88717EBED6D43A42A5A4A78D0F45BBFF', X'88717EBED6D43A42A5A4A78D0F45BBFF', 1, NULL, NULL, 2, NULL, 0, 'Extrude a long strand of yellow onto your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A37C5995C742544C9CD47B89870F901E', X'88717EBED6D43A42A5A4A78D0F45BBFF', X'88717EBED6D43A42A5A4A78D0F45BBFF', 2, NULL, NULL, 2, NULL, 0, 'Do the same creating a long strand of green, blue, and purple candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45536DD9B679114BAD4733384E7ABB80', X'88717EBED6D43A42A5A4A78D0F45BBFF', X'88717EBED6D43A42A5A4A78D0F45BBFF', 3, NULL, NULL, 2, NULL, 0, 'Press the four strands together then cut off 2 inch long sections and form them into rainbows cutting off the excess as needed to make a nice straight bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CECB16656C28EB4C8B0242A81CD9ED91', X'88717EBED6D43A42A5A4A78D0F45BBFF', X'88717EBED6D43A42A5A4A78D0F45BBFF', 4, NULL, NULL, 2, NULL, 0, 'Brush white candy melts over the rainbow and allow that to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C647756F10432C48AA14F15130804F61', X'88717EBED6D43A42A5A4A78D0F45BBFF', X'88717EBED6D43A42A5A4A78D0F45BBFF', 5, NULL, NULL, 2, NULL, 0, 'Turn the rainbows over so that the colorful side is facing up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7D09E8EF2A5C140B10A2182B0963D30', X'49B67D132942AF438723387399EF70A1', X'49B67D132942AF438723387399EF70A1', 0, NULL, NULL, 2, NULL, 0, 'Pipe a big tall swirl of frosting onto each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F753A58E99B3BA48A2C349ADB8DE91E8', X'49B67D132942AF438723387399EF70A1', X'49B67D132942AF438723387399EF70A1', 1, NULL, NULL, 2, NULL, 0, 'Snuggle a Reese''s Cup Unicorn into the frosting near the top portion of the swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA900EC019D2284CB3A63A582B9391DC', X'49B67D132942AF438723387399EF70A1', X'49B67D132942AF438723387399EF70A1', 2, NULL, NULL, 2, NULL, 0, 'If needed pipe a bit more frosting behind the unicorn in order to get the Reese''s Cup to stay upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09379755B1FEC54DBF10446E1EBA95E3', X'49B67D132942AF438723387399EF70A1', X'49B67D132942AF438723387399EF70A1', 3, NULL, NULL, 2, NULL, 0, 'Press two Vanilla Whoppers below the unicorn creating two hooves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A44190E020891546B428A60FBF1AA2A5', X'49B67D132942AF438723387399EF70A1', X'49B67D132942AF438723387399EF70A1', 4, NULL, NULL, 2, NULL, 0, 'Set a candy clay rainbow in the frosting in between the hooves to make it look like the unicorn is holding the rainbow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA22F27D44DE5940B2E93EE2E1AE7241', X'A97736876B77A54B81951D51901002EC', X'A97736876B77A54B81951D51901002EC', 0, NULL, NULL, 2, NULL, 0, 'Set one Reese''s Cup on your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84980C267FC0CF49AFABD8F6ACEA942F', X'A97736876B77A54B81951D51901002EC', X'A97736876B77A54B81951D51901002EC', 1, NULL, NULL, 2, NULL, 0, 'Brush a dab of melted chocolate in the center on top of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C85A50B6BAD93343B6A6C2A220CFBFD2', X'A97736876B77A54B81951D51901002EC', X'A97736876B77A54B81951D51901002EC', 2, NULL, NULL, 2, NULL, 0, 'Allow that to set up a bit then set another Reese''s Cup on it''s side down onto the melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01D05346E67F60458F3F2CD9D4B5FFE3', X'A97736876B77A54B81951D51901002EC', X'A97736876B77A54B81951D51901002EC', 3, NULL, NULL, 2, NULL, 0, 'Hold the candy in place until the chocolate sets and the Reese''s Cup stays upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'387736B754D42A4580C159F2EA17A707', X'A97736876B77A54B81951D51901002EC', X'A97736876B77A54B81951D51901002EC', 4, NULL, NULL, 2, NULL, 0, 'Attach 2 candy eyes to the top, back edge, of the upright Reese''s Cup using dabs of chocolate as edible `glue.`');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D9CC8CEFDB944468FD9F99D4AE30798', X'91BCDC733140ED44B38808465FCC5D07', X'91BCDC733140ED44B38808465FCC5D07', 0, NULL, NULL, 2, NULL, 0, 'Attach 1 red candy nose and 2 candy antlers to the upright Reese''s Cup using chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'317C1A6B8BFC9246B44F3099760299E0', X'91BCDC733140ED44B38808465FCC5D07', X'91BCDC733140ED44B38808465FCC5D07', 1, NULL, NULL, 2, NULL, 0, 'Cut 3/8 inch wide strips from the Fruit Roll-Ups sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B165FD8883E2824D9D4CF6AFA3C27D7C', X'91BCDC733140ED44B38808465FCC5D07', X'91BCDC733140ED44B38808465FCC5D07', 2, NULL, NULL, 2, NULL, 0, 'Wrap a strip of Fruit Roll-Ups around the base of the upright Reese''s Cup forming a scarf.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71D22A320BEA2442932B7CA98536A9D7', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 0, NULL, NULL, 2, NULL, 0, 'Roll out brown candy clay to about 1/8th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F178C5EDE6B53C46A4BD97DEDE74763A', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 1, NULL, NULL, 2, NULL, 0, 'Cut out 24 small leaves (or antlers) using a 1 - 1/2 inch cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6A80A3F9D27014AB22ED506B7532902', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 2, NULL, NULL, 2, NULL, 0, 'If using a leaf cutter you may need to cut off a bit to get an antler shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D124874BD1C5E469DC08D37360E328D', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 3, NULL, NULL, 2, NULL, 0, 'Attach two antlers to the back side of each upright Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'346529CC01CE544D9FE6E3EA57CE1EA7', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 4, NULL, NULL, 2, NULL, 0, 'Roll out the green candy clay to about 1/16th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A7DECE8304DAC44B57295FEE0AF6F45', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 5, NULL, NULL, 2, NULL, 0, 'Cut twelve 3/8th inch wide by 6 or 7 inch long strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6EC196C4D7ECD40ABC5B248277591F9', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 6, NULL, NULL, 2, NULL, 0, 'Wrap one strip around the base of each upright Reese''s Cup forming a scarf.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'254E793B8FD2EF49A2DFBE8911621562', X'5C0A174EB3A615438EFFDB159672E3CD', X'5C0A174EB3A615438EFFDB159672E3CD', 7, NULL, NULL, 2, NULL, 0, 'Cut off the ends of the strip so you can a nice length of scarf on each Rudolph.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E02563DA4B9C5D4C98973FF8EAAD7DA9', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 0, NULL, NULL, 2, NULL, 0, 'Fill a plastic shot glass with chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B27390C7F0304438A36B605A30671D8', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 1, NULL, NULL, 2, NULL, 0, 'Set aside for 3-5 minutes (if your chocolate is thin it will take 5 minutes to create a thick enough coating on the glass).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E62D36A8C08DD4C9EDCC1FC860CF262', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 2, NULL, NULL, 2, NULL, 0, 'Turn shot glass upside down and allow the wet chocolate to drip out (tapping the cup as needed to help the chocolate fall out more quickly).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37FB392F182A9546BE5A3D0D8504C85A', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 3, NULL, NULL, 2, NULL, 0, 'Scrape the chocolate off the opening of the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D661063E035A6448D11D0DDF37B5EC4', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 4, NULL, NULL, 2, NULL, 0, 'Set the glass open side down on a parchment-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB4C6FD5EECC7940B7D15B819BF4D0CC', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 5, NULL, NULL, 2, NULL, 0, 'If using pure tempered chocolate you can either leave the chocolate cup at room temperature or refrigerate it for 5-10 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B58F998712DAC48B03C42B3CCF79F73', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 6, NULL, NULL, 2, NULL, 0, 'If using candy melts, freeze the chocolate cup for about 5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D5EC3428A2F364BA236A056A9666E79', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 7, NULL, NULL, 2, NULL, 0, 'Once the chocolate hardens, pull the chocolate out of the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E13E7C2C048BAD4B9FB2551CC15F7827', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 8, NULL, NULL, 2, NULL, 0, 'Use melted chocolate as `glue` to attach two candy eyes and one orange sunflower beak and one red sunflower wattle to your cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'974AB6DE9B09DF438EE40DB3F641CF49', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 9, NULL, NULL, 2, NULL, 0, 'Pipe two 1 1/2 inch long chocolate legs and two feet onto a parchment paper lined baking sheet for each turkey, making several extra sets in case of breakage.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F726C9BB590FC145B26C7D566D927A57', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 10, NULL, NULL, 2, NULL, 0, 'Let the chocolate set until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B16B49518BD8994FA20FD9646409B292', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 11, NULL, NULL, 2, NULL, 0, 'Attach two legs to the base of each chocolate cup using melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45EFC51603ED3B489D20DB00851A53DE', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 12, NULL, NULL, 2, NULL, 0, 'Attach two feet to the ends of the legs, holding them in place until the stay upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E0FBA1327E64046893DF2DDB6A6F4F1', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 13, NULL, NULL, 2, NULL, 0, 'Roll out candy clay to 1/16th-inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8D33C2BF5780442B96D40FF48CC9326', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 14, NULL, NULL, 2, NULL, 0, 'Cut out 5 long teardrop-shaped feathers for each turkey.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE3E5752EA5F324DBD218B243BEA42A4', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 15, NULL, NULL, 2, NULL, 0, 'Press the pointed ends of 5 candy clay feathers together forming a fan of feathers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96451D9BFC11D349B9CDE593BCAD15B0', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 16, NULL, NULL, 2, NULL, 0, 'If needed, you can brush a tiny bit of water on the edges of the candy clay feathers to get them to stick to each other.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A30A970B690AA47907C5E34D196E8F0', X'3E787412058A8548A5B4D269D891CAD0', X'3E787412058A8548A5B4D269D891CAD0', 17, NULL, NULL, 2, NULL, 0, 'Let the feathers dry for 30 minutes then use melted chocolate to attach the feathers to the upper edge of the back of each chocolate cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A6FBE60C10D0D48B29A9975FFBC63EA', X'97327147D397A340BF456EEAC4FBA0A8', X'97327147D397A340BF456EEAC4FBA0A8', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese using an electric mixer until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E2FBAD6ADA28C4DB427D8B18B5A73DC', X'97327147D397A340BF456EEAC4FBA0A8', X'97327147D397A340BF456EEAC4FBA0A8', 1, NULL, NULL, 2, NULL, 0, 'Add powdered sugar and beat until combined. Add the pumpkin, vanilla, and pie spice and beat just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D664E3D946DDFB488885F9095591CE51', X'97327147D397A340BF456EEAC4FBA0A8', X'97327147D397A340BF456EEAC4FBA0A8', 2, NULL, NULL, 2, NULL, 0, 'Spoon in the whipped cream and beat until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D1C62B8D0002B49AAA44A2E63658161', X'97327147D397A340BF456EEAC4FBA0A8', X'97327147D397A340BF456EEAC4FBA0A8', 3, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into the chocolate turkey cups just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2E97665C810CD42A7FDEE68B8D7D415', X'4B5D660979C24B488F77DFEE8C926AED', X'4B5D660979C24B488F77DFEE8C926AED', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF8C617FEE6D814FA464B50C1B36BB78', X'4B5D660979C24B488F77DFEE8C926AED', X'4B5D660979C24B488F77DFEE8C926AED', 1, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3276B544028C84385B9108BFAB25B02', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 0, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5045139647D4674B943BA1FE97B0E5A2', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 1, NULL, NULL, 2, NULL, 0, 'Add salt and noodles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7CBE930F3CA4DC4C812427CA64A2702D', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 2, NULL, NULL, 2, NULL, 0, 'Boil for 12 minutes until the noodles are soft. (They will crisp a bit in the oven.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A9385E75BEF7446902F7C3CE8206DC7', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 3, NULL, NULL, 2, NULL, 0, 'Brush two cookie sheets with olive oil or olive oil spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0A77D5C34E43744B8DE8D81C12EBD3A', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 4, NULL, NULL, 2, NULL, 0, 'Set noodles in a single layer on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B47A2AC10AC45498A58BCC202CB6715', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 5, NULL, NULL, 2, NULL, 0, 'Cut one 2 1/2 inch circle out of one noodle then cut that noodle into 2 even strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F4F94D47E91324087E71D54B7F54017', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 6, NULL, NULL, 2, NULL, 0, 'Repeat with 3 more noodles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C17CA86DEE2E7048A6BED0D96A9B905A', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 7, NULL, NULL, 2, NULL, 0, 'Cut five 2 1/2 inch circles out of the remaining four noodles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74A274D4DF5848428B1F9C0401777946', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 8, NULL, NULL, 2, NULL, 0, 'Set the cut pieces back on the oil covered pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0677DD8C6B22C14CA4E58750DC135DD5', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 9, NULL, NULL, 2, NULL, 0, 'Brush olive oil or spray olive oil in the bottom and up the sides of eight 6 ounce ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB661BDFE1F3C9419DDEC0566C73416C', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 10, NULL, NULL, 2, NULL, 0, 'Wrap a noodle strip around the inside of a ramekin allowing the noodle to overlap slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C33F9F760B0684DB655DFBAA42A17A6', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 11, NULL, NULL, 2, NULL, 0, 'Set one round noodle in the bottom of the ramekin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9DD72F4A22F184CBD644B3F0E187A27', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 12, NULL, NULL, 2, NULL, 0, 'Repeat lining all 8 ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFD431F76EF0CA44947DD7CF9F4DBA63', X'33806AE7C2F70143A5B10ED6B73B31C5', X'33806AE7C2F70143A5B10ED6B73B31C5', 13, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5145C79B4C546940A88006ACFE8FEFEA', X'38B04AD779EA174296032364E8114FAB', X'38B04AD779EA174296032364E8114FAB', 0, NULL, NULL, 2, NULL, 0, 'Stir together the ricotta, mozzarella, parmesan, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98C9E3044904B34B9F2C1A1D3654D47D', X'10874AD2159B03489DC3C609DA25936E', X'10874AD2159B03489DC3C609DA25936E', 0, NULL, NULL, 2, NULL, 0, 'Stir together the diced and thawed chicken fingers with the marinara sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D3093C110106049BB0E1E7433A91913', X'10874AD2159B03489DC3C609DA25936E', X'10874AD2159B03489DC3C609DA25936E', 1, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43C54520CBF45F4CBCAA388D17C8E685', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 0, NULL, NULL, 2, NULL, 0, 'Evenly divide the chicken filling among the 8 ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB11AA0220A428409B97412598B5C203', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 1, NULL, NULL, 2, NULL, 0, 'Compact the filling into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'708C24E3ED2A824A86146B155355E49F', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 2, NULL, NULL, 2, NULL, 0, 'Set another round noodle on top of the chicken filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10625F63973826418EF83ED130914598', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 3, NULL, NULL, 2, NULL, 0, 'Evenly divide the cheese filling among the 8 ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4A1BD1EBBBDF944818B0B3504A9BA82', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 4, NULL, NULL, 2, NULL, 0, 'Spread the filling in an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9638CE30183AA14882480EA8900851BA', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 5, NULL, NULL, 2, NULL, 0, 'Set the final round noodle on top of the cheese filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B45686BFA9E28841AE9892B05E7974CC', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 6, NULL, NULL, 2, NULL, 0, 'Spread a teaspoon of Alfredo sauce over top then sprinkle with 1/2 ounce of mozzarella cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D889DB4594B61D4DA65A1BE25DCE3F83', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 7, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6D59F4682322D488779AB741AF3766F', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 8, NULL, NULL, 2, NULL, 0, 'Cut olives in half. Use a bubble tea straw. a small round plunger or fondant cutter, or a large round pastry tip to cut 1/4 inch round circles from the olives.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06FBB53615800D49B92EA10CD7B59955', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 9, NULL, NULL, 2, NULL, 0, 'Cut the mini carrots into 3/4 inch long pieces for the snowmen noses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'067CD20FAE9B8848A535A63FF814BF60', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 10, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEDFAAFD9B60994EA350152D0F2FC6CC', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 11, NULL, NULL, 2, NULL, 0, 'Arrange 2 olive eyes and one carrot nose on each lasagna.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'898B22985E0CD14D8BB2A14F6B506AA3', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 12, NULL, NULL, 2, NULL, 0, 'Then arrange 5 olives to form a smile under the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC5A489B4DCD5943AFE255E987B165A2', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 13, NULL, NULL, 2, NULL, 0, 'Bake for 22-26 minutes until the cheese is bubbly and melted and the lasagnas are heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E893A273454904092B0E41CDBD5EF0F', X'C0731895CB1990418E9BBE7F1C72D3EE', X'C0731895CB1990418E9BBE7F1C72D3EE', 14, NULL, NULL, 2, NULL, 0, 'Allow to cool for a few minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F999AD03F580084F86CEE2E902FD4086', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 0, NULL, NULL, 2, NULL, 0, 'Heat red candy melts in a microwave safe bowl for two 30 second increments, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDA362B8CE19A74FBF3CB6DE9BCBDB0E', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 1, NULL, NULL, 2, NULL, 0, 'Heat for another 30 seconds, then let the bowl rest in the microwave for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30C61C42B325FC468CB5239E9ECD8383', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir vigorously until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5B3A1BCEB27614A89D993E22561C625', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 3, NULL, NULL, 2, NULL, 0, 'Cool, stirring often, to 91 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2705B14CB58DD469C0C55A279D726A0', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 4, NULL, NULL, 2, NULL, 0, 'Stir in the corn syrup and blend just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2ADD63C80916341B976358B443A9244', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 5, NULL, NULL, 2, NULL, 0, 'Pour out onto plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F77F90800FD3254985A1BABF926F4C4E', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 6, NULL, NULL, 2, NULL, 0, 'Wrap and set aside to firm up for several hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49724D6636915841988853CFB830F789', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 7, NULL, NULL, 2, NULL, 0, 'When ready, remove from plastic wrap and knead until the candy clay is pliable.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D68BAA6A6CA21541B41C36FD1E5B709F', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 8, NULL, NULL, 2, NULL, 0, 'Roll out red candy clay really thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BABC4979D802A49836A256AA7577F5C', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 9, NULL, NULL, 2, NULL, 0, 'Cut 3/8 inch by 7-inch strips from the candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'668E092D786D2F4A802DE6AA3AD56EBA', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 10, NULL, NULL, 2, NULL, 0, 'Set one strip on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56384E4FC8729F488D42A35B861315B1', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 11, NULL, NULL, 2, NULL, 0, 'Pour melted white candy melts into a disposable pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE37242D3EF5B246B1FCDAB5415F5921', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 12, NULL, NULL, 2, NULL, 0, 'Snip off the tip of the bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C7527A1EC05DF45A98E80CFE15C2A21', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 13, NULL, NULL, 2, NULL, 0, 'Pipe a small mound of candy in the center of the red candy clay strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54F7389E6EEFCB46BFAA8649BC447D0C', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 14, NULL, NULL, 2, NULL, 0, 'Set two pretzel rings, touching, over the white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C82B3FB3F488694384249C553CB6875E', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 15, NULL, NULL, 2, NULL, 0, 'Pipe a small amount of white candy where the pretzels come together just to help `glue'' them together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12DDB8CD21A23A438CDC3B46EE2FF367', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 16, NULL, NULL, 2, NULL, 0, 'Pipe white candy into the center of both pretzel rings.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFA4C4EE9416354C8020D2B8DDC10FA1', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 17, NULL, NULL, 2, NULL, 0, 'Set two mini chocolate chips and one orange sunflower seed in the top pretzel forming the snowman''s eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'403E8C0219D63D41BFCF32889668C6DD', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 18, NULL, NULL, 2, NULL, 0, 'Set three mini chocolate chips in the bottom pretzel forming 3 buttons.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59A1E007ABF1D1449C202057ED4F514E', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 19, NULL, NULL, 2, NULL, 0, 'Add another small amount of white candy melts in between the two pretzels then overlap the two ends of the red candy clay strip forming the snowman''s scarf.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8ED250F1F95FD3468F6A273916824E54', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 20, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes just until the white candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'076172D3E8C47548889A25AB2DBF8458', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 21, NULL, NULL, 2, NULL, 0, 'Remove and let sit at room temperature for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3830AD07726E8419595E4D7EAE4CE92', X'4C0333372B279F4E9C8566DED59FB0B6', X'4C0333372B279F4E9C8566DED59FB0B6', 22, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to create a smile formed by 5-7 dots.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAC9362F5BCA654BBDDDD5F1563AAB96', X'410E88936B0C6F488CDD66DEF481F9E6', X'410E88936B0C6F488CDD66DEF481F9E6', 0, NULL, NULL, 2, NULL, 0, 'Pull apart one Mini Oreo Cookie and scrape out the filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C33137B5CAE37841867A279B0AFF5DB3', X'410E88936B0C6F488CDD66DEF481F9E6', X'410E88936B0C6F488CDD66DEF481F9E6', 1, NULL, NULL, 2, NULL, 0, 'Insert both cookie halves into one of the Oreo Mega Stuf Cookies creating Mickey Mouse ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFD5C11B0EC692498CCCD13D779E0765', X'410E88936B0C6F488CDD66DEF481F9E6', X'410E88936B0C6F488CDD66DEF481F9E6', 2, NULL, NULL, 2, NULL, 0, 'Repeat creating 12 Mickey Mouse Oreo Cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4BB102915915A4E87AEBE6D2E133C0E', X'410E88936B0C6F488CDD66DEF481F9E6', X'410E88936B0C6F488CDD66DEF481F9E6', 3, NULL, NULL, 2, NULL, 0, 'Insert two candy antlers in between Mickey''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21417BA291A53648ADC00834303BA194', X'410E88936B0C6F488CDD66DEF481F9E6', X'410E88936B0C6F488CDD66DEF481F9E6', 4, NULL, NULL, 2, NULL, 0, 'Dab a bit of melted chocolate on the back of a red nose and press it onto the Mega Stuf cookie opposite the antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3C35CA50A9BAA4CA05ED43F3A9BB5E0', X'410E88936B0C6F488CDD66DEF481F9E6', X'410E88936B0C6F488CDD66DEF481F9E6', 5, NULL, NULL, 2, NULL, 0, 'Dip the tip of a wooden craft stick in chocolate and insert it into the cookie opposite the antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB1923B7855E8C4582375D78502D4CDE', X'410E88936B0C6F488CDD66DEF481F9E6', X'410E88936B0C6F488CDD66DEF481F9E6', 6, NULL, NULL, 2, NULL, 0, 'Let chocolate dry before picking up your cookie pops.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A0ABBB61DCF1A4AAB56FC2279333995', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 0, NULL, NULL, 2, NULL, 0, 'Split apart an Oreo Mega Stuf Cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33FFBD0EC5424E4C9731B7A16744CF55', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 1, NULL, NULL, 2, NULL, 0, 'Brush chocolate on the inside of the cookie and press the two halves back together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'100BF8B925EB8A41A20CBED57EDAD3AA', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 2, NULL, NULL, 2, NULL, 0, 'Split apart a Mini Oreo Cookie and scrape out the filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C20E6C0674A7BA4AB6C9D96AC2DC5F39', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 3, NULL, NULL, 2, NULL, 0, 'Dip one edge of each mini cookie in chocolate and insert them into the Mega Stuf cookie creating Mickey''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D278A8C82E6CB4297B347D48A4164BD', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 4, NULL, NULL, 2, NULL, 0, 'Flatten one tip of a paper straw and dip the tip in chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A949749E015BE42A71E4B2C46BEE6E7', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 5, NULL, NULL, 2, NULL, 0, 'Insert into the Mega Stuf cookie opposite the cookie ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4DBF10C59C582419862882B8901B409', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating 12 Mickey cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE2D523C1B26CB46B37D7220ED48CD57', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 7, NULL, NULL, 2, NULL, 0, 'Freeze cookie for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D0F2CDF4443404292D105CF8A4302FA', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 8, NULL, NULL, 2, NULL, 0, 'Dip the cookies in chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D19670C176D4F498BA82BD51B96D045', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 9, NULL, NULL, 2, NULL, 0, 'Set cookie on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'401FA53C164BF142801A33E608EDA404', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 10, NULL, NULL, 2, NULL, 0, 'Press one red candy nose onto the cookie opposite the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB0158088215954A808DE6668242FE0D', X'2D4CFD361B6408469AB55AC0019D8E2E', X'2D4CFD361B6408469AB55AC0019D8E2E', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D3E992E970D9741951B90B6FEBAA02B', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 0, NULL, NULL, 2, NULL, 0, 'Unwrap peppermint patties.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2D18E8EA8FD9B42B870614F1BEC54EE', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 1, NULL, NULL, 2, NULL, 0, 'Brush a small amount of melted black candy melts on the back of each mini peppermint patty and attach one to each larger peppermint patty.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB4D04C9B4648F4CB8698A11A091BAD1', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 2, NULL, NULL, 2, NULL, 0, 'Dab some black candy coating in the center of the mini peppermint patty and arrange 3 white jimmies on either size to create the cat''s whiskers and add a jumbo pink heart in the center on top for the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CEFB329FA5A804DBFA5134A33789C99', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 3, NULL, NULL, 2, NULL, 0, 'Use black candy coating to attach two candy eyes to each peppermint patty.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5549236F1FE46A46BC6382F304C69F33', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 4, NULL, NULL, 2, NULL, 0, 'Brush two triangle shaped ears onto a parchment lined baking sheet and set the peppermint patty cat over the bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D6E16937600BC4D8CC50BF233FB19DA', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 5, NULL, NULL, 2, NULL, 0, 'Immediately set 1 small pink heart sprinkle up against the peppermint patty on top of each candy ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E02B1B48C0316418719BC9CE9E122C8', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 6, NULL, NULL, 2, NULL, 0, 'Let the candy ears harden before moving your peppermint patty cats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC1A6FF4A5EFFC43AFCC0EBFE96129A2', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 7, NULL, NULL, 2, NULL, 0, 'Pour white candy melts into a zip top bag, snip off one tip, and pipe 12 fish carcasses onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62C3A645EC10B5428426FFA1FE5A396B', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 8, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAF2D2E257DDB849B572958A6C34E714', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 9, NULL, NULL, 2, NULL, 0, 'Remove and peel the fish carcasses off the paper and flip over so the smooth side faces out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7A56D4FC1BC914EADC905B43E0845B2', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 10, NULL, NULL, 2, NULL, 0, 'Assemble the cupcakes by wrapping each cupcake in a picket fence cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40AF319C45ABC94F99653FCCDC9BFCE4', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 11, NULL, NULL, 2, NULL, 0, 'Then press one peppermint patty cat on the front near the top of the frosting swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D9DEFCF21B3B548847D129F58D5D4E2', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 12, NULL, NULL, 2, NULL, 0, 'Press one black M&M on either side of the frosting swirl below the cat head to create the paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D34FFE3846DE441AD0064B4E3AE650A', X'FB86C4BA33F8464984061021EF2621FA', X'FB86C4BA33F8464984061021EF2621FA', 13, NULL, NULL, 2, NULL, 0, 'Attach one fish carcass to the frosting swirl in between the two candy paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A74BCE5B68EFC42B80BD4FE4826E4F9', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 0, NULL, NULL, 2, NULL, 0, 'Spread Light Cocoa Candy Melts over the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'468DE87913D3BF4595CCE5BE19E6DA87', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 1, NULL, NULL, 2, NULL, 0, 'Using a food use only paint brush press the brush into the wet candy melts and pull up quickly creating furry spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1754AE977D834C46934485BDBCB915B7', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 2, NULL, NULL, 2, NULL, 0, 'Attach a mini Reese''s Cup snout and brush candy melts all around it making it furry too.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'845CD5C6E16B344F8C4F14A60699EAD5', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 3, NULL, NULL, 2, NULL, 0, 'Attach a black candy nose and yellow candy eyes using more candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FC72D92017AE744B52D75CEBC56FB1A', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 4, NULL, NULL, 2, NULL, 0, 'Pop the Reese''s Cup into the freezer for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17909B502E3C124881AB62A689968BAA', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 5, NULL, NULL, 2, NULL, 0, 'Cut ears from two candy melt wafers and attach them to the Reese''s Cup and make them furry using more candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A6E2E597A48704F92B5A6C0FEDEDB29', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 6, NULL, NULL, 2, NULL, 0, 'Either pipe on white candy melt fangs or attach 4 small white heart sprinkles for fangs using more light cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10C6FA2A23ADCD43A23A4D371EE47405', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 7, NULL, NULL, 2, NULL, 0, 'Pipe a candy moon with a pick attached onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB3E64384898264AB3C8DD280B3B3FD1', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 8, NULL, NULL, 2, NULL, 0, 'Freeze candy moon for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80CACBC20A379B459BA6E160A3998B64', X'93C4B66615CB0B42887066795DB651F6', X'93C4B66615CB0B42887066795DB651F6', 9, NULL, NULL, 2, NULL, 0, 'Insert the pick into the frosting so the moon sits behind the werewolf.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F66AC38B48EB124EB8AD59C98AA968EB', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 0, NULL, NULL, 2, NULL, 0, 'Cut one slice of orange cheese into a book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'015CDDABE9A7304BB1E571F892A71BD3', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 1, NULL, NULL, 2, NULL, 0, 'Use the back of a knife to score the book to create the illusion of a spine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE75650FC2D5B94E99A1808C1580BBEC', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 2, NULL, NULL, 2, NULL, 0, 'Cut two long thin strips from the white cheese slice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE1E717728FA67478475B0442AAC3E88', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 3, NULL, NULL, 2, NULL, 0, 'Set one strip of white cheese across the cheese book about 1/4 inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21B399FC658E73498DB529A3EF07A64C', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 4, NULL, NULL, 2, NULL, 0, 'Set the other strip near the bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D511F190B2524A4B93480DAA6D866533', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 5, NULL, NULL, 2, NULL, 0, 'Cut 2 small circles out of the white cheese, for the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE99E84083BC144F8B8CF6266AF1799C', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 6, NULL, NULL, 2, NULL, 0, 'Cut 1 slice off the edge of an olive and cut that into two very small circles for the pupils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE60145442EA334DAB9F4C349921F60B', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 7, NULL, NULL, 2, NULL, 0, 'Cut two slices from the middle of the olive.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14505025BA2E3841914D44C442CB196C', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 8, NULL, NULL, 2, NULL, 0, 'Use a round cutter to cut the centers of the slices out creating the rims of the bookworm''s glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36FBD5166129AD4FA0F439DFA8A5674C', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 9, NULL, NULL, 2, NULL, 0, 'Cut another slice from the olive and cut it in half. Half will become the smile. Cut the other piece in half to form the nose piece of the glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5E55808FA325646968A330FF4BA1975', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 10, NULL, NULL, 2, NULL, 0, 'Heat the Farm Rich Mozzarella Bites according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60B34F6302A0AA4087C8710DEAE911B1', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 11, NULL, NULL, 2, NULL, 0, 'Arrange 6 of the Mozzarella Bites on a plate forming a worm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88D7610B467ADD4DA15B74D003246C45', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 12, NULL, NULL, 2, NULL, 0, 'Cut one thin slice off the remaining Mozzarella Bite, then cut two small half moon shapes out for the worm''s hands.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A571D9D250C79D4AB56FAE4187958901', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 13, NULL, NULL, 2, NULL, 0, 'Arrange the olive glasses, cheese eyes, and olive smile on the top Mozzarella Bite (head.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33BF8F13DFC7D24A8F552BE5642855E3', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 14, NULL, NULL, 2, NULL, 0, 'Set the book between the two Mozzarella Bites that are just below the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F20AD9652AD394D8906A31A9A64B9C0', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 15, NULL, NULL, 2, NULL, 0, 'Set one hand on either side of the cheese book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1085B836F4C51B40AB1E3F16BF6E4602', X'AD479EEC7EBFE049ABC53E8874EC0ED9', X'AD479EEC7EBFE049ABC53E8874EC0ED9', 16, NULL, NULL, 2, NULL, 0, 'Heat the marinara sauce and serve on the side.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'344EEA14C79C9449857DCDB2110C8F69', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B35F6C6B93C1554D9747BC5B1D00869A', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25410C868EEEAD48A0223714E9FCACF9', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F83839CF3D2D84AA498EBAB0C14E390', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 3, NULL, NULL, 2, NULL, 0, 'Stir in vanilla, and almond extract then stir in the egg until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A5087DEA06A6D4783E09CB50C453F92', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FE14782C7D2E247B221673143568C4E', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 5, NULL, NULL, 2, NULL, 0, 'If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14259E839FED79419121359D2261EE8E', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 6, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface or non-stick mat with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6363E394336FB846BF859E72C26E9937', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 7, NULL, NULL, 2, NULL, 0, 'Roll the cookie dough out to between a 1/16 inch and 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49CCCCBB864FE641AA4089C136947BFF', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 8, NULL, NULL, 2, NULL, 0, 'Cut dough using cookie cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'730114281A7CB44CB7B4E74941DB1554', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 9, NULL, NULL, 2, NULL, 0, 'Remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF2F655C0A0D07418DA48FCD566F69AD', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 10, NULL, NULL, 2, NULL, 0, 'Freeze cut cookies for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12D3B854C3CCDE49819572E6985888A6', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 11, NULL, NULL, 2, NULL, 0, 'Peel off cookies and set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F894EFBF8E5AF4AA27399D963F3AFA4', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 12, NULL, NULL, 2, NULL, 0, 'Bake cookies for 6-12 minutes, depending on the size, until the edges are set and the tops look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'341CB318DDD9534286CD0B7C1A03818C', X'F67F9023152E824E81643F3828A2606D', X'F67F9023152E824E81643F3828A2606D', 13, NULL, NULL, 2, NULL, 0, 'Cool cookies completely then decorate using one of the methods in the tutorial.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE8508F1EBF6014FA88C006B1268F706', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 0, NULL, NULL, 2, NULL, 0, 'Pour crisp rice cereal in very large heatproof bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3529BF1B3DBA6438766AE0468D571C9', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 1, NULL, NULL, 2, NULL, 0, 'Cut butter into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CD0877E5AD5E04EB0E08B4B725FDFD9', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 2, NULL, NULL, 2, NULL, 0, 'Place butter, brown sugar, and corn syrup in a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C60816ACEE75043AEEF866B6B86A884', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 3, NULL, NULL, 2, NULL, 0, 'Heat on medium, stirring as needed, until butter melts and mixture begins to bubble around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14DACFA64A6AAB4DB0E4F99D1C8993D9', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 4, NULL, NULL, 2, NULL, 0, 'Place lid on pan for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF6C4659DA0B17419961AD0B7933340E', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 5, NULL, NULL, 2, NULL, 0, 'Remove lid and allow to boil for 2 more minutes, without stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9BCD707E6927B4F840EBCA32A041B44', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and add sweetened condensed milk, stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD458B2078E38C43A599C08A53948F70', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 7, NULL, NULL, 2, NULL, 0, 'Return to medium heat and allow to boil for three minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA1FEA660AB42B4EBD66AB2DE4624E98', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 8, NULL, NULL, 2, NULL, 0, 'Pour over cereal and stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'914EA12FD329F94BBE3C7000CF48F097', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 9, NULL, NULL, 2, NULL, 0, 'Let cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'923BB74EA286BD4CA733C50FF849D487', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 10, NULL, NULL, 2, NULL, 0, 'Line plastic molds with plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'214697CA1724D046AF054E5DF447A742', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 11, NULL, NULL, 2, NULL, 0, 'Fill molds with cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35078008F3584441A22A166F2BCF3C66', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 12, NULL, NULL, 2, NULL, 0, 'Press to compress cereal so that it holds it''s shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'745AAFFDC9F8FD419C5A2508C6414E14', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 13, NULL, NULL, 2, NULL, 0, 'Un-mold treats and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86D7B35297BD5748BE5278F8E9BF0E1E', X'F50397792FDBD14C941464E3FD25B6F7', X'F50397792FDBD14C941464E3FD25B6F7', 14, NULL, NULL, 2, NULL, 0, 'Decorate, if desired by attaching candies using edible `glue.`');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84FA3B4506BF7744A03FAB7026A4911B', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', 0, NULL, NULL, 2, NULL, 0, 'Heat marshmallows, butter, and candy melts in a large microwave safe bowl on high power for 30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DD5BB9535C16F4BBD66F1DD0A943D38', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', 1, NULL, NULL, 2, NULL, 0, 'Stir in 1 drop of black food coloring, if using, to make the marshmallow mixture light grey.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8DFEC55BFD6604E8225122E88769498', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', 2, NULL, NULL, 2, NULL, 0, 'Stir in Rice Krispies Cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16AA45C13C7B024096B5059E54FEAB81', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', 3, NULL, NULL, 2, NULL, 0, 'Press cereal mixture into your 3-D dolphin mold filling it to the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D24E757E5EFA1F43920D1FBD8D3123DC', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', 4, NULL, NULL, 2, NULL, 0, 'Turn mold upside down and allow treat to pop out. (Use a thin knife to help it out, if it wont pop out on it''s own.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E1294A1582C944D990CB25E9235C598', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', 5, NULL, NULL, 2, NULL, 0, 'Repeat, creating 8 dolphins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F320683B6CD2254B9BF38E22BD509586', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', X'A1E08AF5304D8A4D9A0BAC9FCA9003F9', 6, NULL, NULL, 2, NULL, 0, 'Insert one mini chocolate chip eye on either side of each dolphin''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B8B509748EF9B429FDA2E451DF53EAF', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 0, NULL, NULL, 2, NULL, 0, 'Line a 9x13 baking pan with a well-buttered piece of parchment or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1497B41B1B5FD54AA81A2FD1C76FC8A0', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 1, NULL, NULL, 2, NULL, 0, 'Pour marshmallows, butter and red candy melts into a large microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1ADAB59D0997AE429C5145299F4BA7B2', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E370939C71E06D478489EDC237BE46F0', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 3, NULL, NULL, 2, NULL, 0, 'Stir in red food coloring until the marshmallows are vibrant red.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'325B0AABA4F9D3429D3A7C36B5F38C1C', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 4, NULL, NULL, 2, NULL, 0, 'Pour in cereal and mix until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A285E47CCAF924E86781452AD9E2DFC', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 5, NULL, NULL, 2, NULL, 0, 'Spread into an even layer in pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F24ED7E1845B2141A11A319497B850DE', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 6, NULL, NULL, 2, NULL, 0, 'Repeat the process above using white candy melts and white food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C1F4C574B527947832842D8CEDFD69E', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 7, NULL, NULL, 2, NULL, 0, 'Spread the white cereal layer over the red layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A11742C9088D5446BFC8ED2D12CBBCD5', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 8, NULL, NULL, 2, NULL, 0, 'Repeat using blue candy melts and blue coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E27AEB86D9136439776DBAE6EF0B899', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 9, NULL, NULL, 2, NULL, 0, 'Spread the blue layer over the white layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0DF4ABC2F92F345BEEFD2F341F9064B', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 10, NULL, NULL, 2, NULL, 0, 'Press down on the blue layer to compact all three layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE12D20AC5C8C548B4396018ABCFD8B0', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 11, NULL, NULL, 2, NULL, 0, 'Remove from pan and set on a silicone mat or buttered parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C711A69B8601844384222026FFF4B27A', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 12, NULL, NULL, 2, NULL, 0, 'Use a rolling pin to compress the cereal, spreading it out about an inch all around the rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93161550D844464EB8E2F5357D812129', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 13, NULL, NULL, 2, NULL, 0, 'Roll up into a pinwheel from the short side.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E7638BD7D53E44488D688AA6359AA47', X'A9F4E48B8D55A94AA259EAD0286AE6A4', X'A9F4E48B8D55A94AA259EAD0286AE6A4', 14, NULL, NULL, 2, NULL, 0, 'Cut into 12 slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E33BEE396796494AAC0F620395B3B5F5', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 0, NULL, NULL, 2, NULL, 0, 'Dip one pretzel in red candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B3F4992EF226D4DA001F0FAD10D026D', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 1, NULL, NULL, 2, NULL, 0, 'Set on a parchment lined baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D318CE63C44174387B03ECC23247D32', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 2, NULL, NULL, 2, NULL, 0, 'Dip one pretzel in white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEBAB2DC3B41754FA7059B17319FF94E', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 3, NULL, NULL, 2, NULL, 0, 'Set it next to the red pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'165C73340FC76C44B847C8690C45A2C5', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 4, NULL, NULL, 2, NULL, 0, 'Dip another red pretzel, set it next to the white one.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'395AD6677E0CFF4F9930DA10645BB839', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 5, NULL, NULL, 2, NULL, 0, 'Then add another red dipped pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5224FC09DBE8694BB807DC14ECD2BBE0', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 6, NULL, NULL, 2, NULL, 0, 'Place the candy coated pretzel in the freezer for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C790D3649B4D1479F8A41F1147DB9FA', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 7, NULL, NULL, 2, NULL, 0, 'Pipe a rectangle of blue candy melts in the upper left corner of the red and white striped pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CB8A45053A1544B88846E7776AE5CC6', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 8, NULL, NULL, 2, NULL, 0, 'Freeze it for about 2 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A596F7353D643498C310595996F16D6', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 9, NULL, NULL, 2, NULL, 0, 'Attach 11 white star sprinkles to the blue rectangle using more blue candy melts as `glue.`');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48E3BACAEF608A44BFE37AA074E8D9B7', X'890F0A3818ED87439C30058E04250097', X'890F0A3818ED87439C30058E04250097', 10, NULL, NULL, 2, NULL, 0, 'Repeat, creating 12 American Flag Pretzels');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18B4424DF03E064E958333869F5C94F9', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05BC43C1E8232C4FA455F52A642DC611', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 1, NULL, NULL, 2, NULL, 0, 'Line 60 cavities in mini cupcake pans with paper cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D687A87FE5352B4EB0D5F4D00657FA18', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 2, NULL, NULL, 2, NULL, 0, 'Make cake mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4117FACEAC06CB468213B2D29CB835A7', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 3, NULL, NULL, 2, NULL, 0, 'Equally divide batter into 6 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0F35CBBC1EBF142BF02577F530E0898', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 4, NULL, NULL, 2, NULL, 0, 'Color batter using food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D766EBCD075A5F4D91F5DE093B866102', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 5, NULL, NULL, 2, NULL, 0, 'Pipe or spoon batter into mini cupcake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05FD9575D32537448354D1F01CB74EE6', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 6, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the cakes spring back when touched.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BD1571468A5354289E18E974AACDD4F', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 7, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F82B43E75D38A4F9F24FBE83D6204F7', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 8, NULL, NULL, 2, NULL, 0, 'Pipe or spread white frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCC63E854EE2714E8710ED6EC6A06187', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle on a bit of rainbow nonpareils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'571C399E037192429A49A80C7B8196AE', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 10, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoon of candy into a plastic 1 ounce shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1DBC9F1FFE461498C8A04A1E0E75DB2', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 11, NULL, NULL, 2, NULL, 0, 'Set a mini cupcake on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'624CDF67F8151F47B0615CD7908A85D0', X'B92B604A2A439242B19E8E4FD340DFCA', X'B92B604A2A439242B19E8E4FD340DFCA', 12, NULL, NULL, 2, NULL, 0, 'Repeat, creating a total of 60 mini cupcakes in candy filled shot glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'654F4F2D00A6FD478BB28C75D0B695D1', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0C09E997B715B4AA010397EFE7D1908', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C98A5D046606814D8EB856F6F79EB122', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 2, NULL, NULL, 2, NULL, 0, 'Pour blue candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'917D35F1EDFE93448EC7FABEADB31D50', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 3, NULL, NULL, 2, NULL, 0, 'Heat for 30 second burst of high power, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41EE6ADE934DCA499C6743C848811D42', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 4, NULL, NULL, 2, NULL, 0, 'Pour candy melts over the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56DE90F7C6CA584D957E0F745C581C6A', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 5, NULL, NULL, 2, NULL, 0, 'Toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81E633797D089D4B939C5D86B33FBD92', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 6, NULL, NULL, 2, NULL, 0, 'Pour out onto a parchment paper lined baking sheets and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48652E466A999D458E242CEAA3A339DD', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 7, NULL, NULL, 2, NULL, 0, 'Immediately press Finding Dory and Nemo marshmallows all over the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14B3127A38D4DB429039FEFFAFC9623D', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle on white sugar pearls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DB1B4CEFA3FFF46BC8EC0485434F8D2', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 9, NULL, NULL, 2, NULL, 0, 'If desired, press some of the cookies onto the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75FC884248AAC2488241CD960986F1C4', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate for 8-10 minutes until the candy coating n the popcorn hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FF04F3F3B96F9479C7060DDBB147408', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 11, NULL, NULL, 2, NULL, 0, 'Break popcorn into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75E923E1BA05C94E9BE5FE919B7F1F6A', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 12, NULL, NULL, 2, NULL, 0, 'Package in clear cellophane bags with some of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A572908147AAE3409E956A32CA77924C', X'EBAE24A8C221034EBAAECE4F33BEB193', X'EBAE24A8C221034EBAAECE4F33BEB193', 13, NULL, NULL, 2, NULL, 0, 'Top bag with printable label.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF8C23AAB7F76547B6F5F4EB220B174E', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 0, NULL, NULL, 2, NULL, 0, 'Unwrap one of the PB&J bars and put it into a mini food processor or blender along with the chopped dates, dried cherries, and 4 teaspoons of the peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B16983B6DFDC664BA2D963C10484551D', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 1, NULL, NULL, 2, NULL, 0, 'Pulse until really well chopped.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9209105D4EEFA44B949EA9BBB706DC1E', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 2, NULL, NULL, 2, NULL, 0, 'To test if it''s chopped enough, squeeze about a tablespoon of the mixture in your hand and it should stick together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A63A425B1170B48A8D2E2BA9C96EED2', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 3, NULL, NULL, 2, NULL, 0, 'Dump all the crumbs out onto your work surface and squeeze together into a flat disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D8152BB1E26D845B1CB37019CAA2AF3', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 4, NULL, NULL, 2, NULL, 0, 'Flatten the disc of dough out to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'708C67B07C17F64386471CBC6F12B40E', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 5, NULL, NULL, 2, NULL, 0, 'Cut out 9 small stars using a cookie cutter, reworking the dough as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55B75AC66B8FCF4997782E5078B413BB', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 6, NULL, NULL, 2, NULL, 0, 'Pinch the points of the stars to look more like a starfish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F04CDBD83B622B4FB462E8B1430F9949', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 7, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes to each starfish using peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8A5A7B273C4C34AA426D26CF57F6FD5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 8, NULL, NULL, 2, NULL, 0, 'Brush a teaspoon of peanut butter over each of the remaining PB&J Bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'110D3ED7961C084FB5D3C69269F18A97', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 9, NULL, NULL, 2, NULL, 0, 'Crush the Honey Crunch Cereal into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3D6F3022078CB4AB19133A87CC07012', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle crumbs over top of the peanut butter coated PB&J bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7E01D3FF15D574688138C6E36F09C93', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 11, NULL, NULL, 2, NULL, 0, 'Spread a small amount of peanut butter onto the backside of a starfish and press it into the sandy cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DD88BA2BD888548A0CB0C42E7860924', X'63A002D2123A4D4E8112BBC6EE4FDFA5', X'63A002D2123A4D4E8112BBC6EE4FDFA5', 12, NULL, NULL, 2, NULL, 0, 'Repeat, creating a total of 9 sandy beaches topped with a starfish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77E1B43B2214C14BAC095F3B0CE1570D', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 0, NULL, NULL, 2, NULL, 0, 'Cut 2 graham crackers for the front and rear peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02F9C08A6BEE0A4383C3BE4740A5CF3D', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 1, NULL, NULL, 2, NULL, 0, 'Cut 2 graham crackers to 3 3/4 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C1DA3BB25CA4447872ECC6C61820450', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 2, NULL, NULL, 2, NULL, 0, 'Cut 1 graham cracker to 3 1/4 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'192D05CED5F16E4583289EAA8C801431', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 3, NULL, NULL, 2, NULL, 0, 'Stir together red and chocolate brown cookie icing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC9CF6252EABF948BD863F11E02E5C26', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 4, NULL, NULL, 2, NULL, 0, 'Add enough water to thin it out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F88A948A06F6644E88A45B997E7ACEAB', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 5, NULL, NULL, 2, NULL, 0, 'Brush red icing over 2 whole graham crackers, the two peaks, and the 3 short graham crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F026D1DD27635C41A162D8F24A380FDF', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 6, NULL, NULL, 2, NULL, 0, 'Thin out white icing with water and brush over the 4 remaining graham crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'663FF0FF69C3EF41AA5B435C0E6405F1', X'F58DD0E9F33AFA48ADE06063FF88DB4B', X'F58DD0E9F33AFA48ADE06063FF88DB4B', 7, NULL, NULL, 2, NULL, 0, 'Assemble the graham cracker barn using thick red and white cookie icing, allowing each section to dry before adding the next.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12030026C1E7514CAB787C3DF80FEE2A', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Peppermint Patties and set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDB09CC1E1988549AAFA656EF04FC8B7', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 1, NULL, NULL, 2, NULL, 0, 'Using a Peppermint Patty as a char(36)e, pipe two tear drop shaped ears out of the black candy coating then set the Patty on top, over the tips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98A4D71176F3994893F2CB2F9D17EA1B', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 2, NULL, NULL, 2, NULL, 0, 'Pipe 4, 1 inch long, tear drop shaped legs for each lamb');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'715610C43FB6D243862C462138063688', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 3, NULL, NULL, 2, NULL, 0, 'Place pan in freezer for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0B39C0C6ED3E3449A0ABCED3DBCCA72', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 4, NULL, NULL, 2, NULL, 0, 'Remove and peel candies off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38FE0B60455A5345B2EBD9FF9D777763', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 5, NULL, NULL, 2, NULL, 0, 'Use black candy coating to attach two candy eyes and one pink sugar pearl nose to each Peppermint Patty.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'024EA748ED4DA74A93BA43FB23B604DD', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 6, NULL, NULL, 2, NULL, 0, 'Cut the domed tops off each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'304098557DDE024680AE734CD1E152C0', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 7, NULL, NULL, 2, NULL, 0, 'Pipe frosting on the cut top of half of the cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3BC324330DE7A40B9DA3A286D97B9E6', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 8, NULL, NULL, 2, NULL, 0, 'Turn another cupcake upside down on top of the frosted cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35A365FF59ABC74096B49141D9CD566A', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 9, NULL, NULL, 2, NULL, 0, 'Carve each stack of cupcakes into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E04B06D0092CFA46BBB49E57601DB481', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 10, NULL, NULL, 2, NULL, 0, 'Pipe or spread frosting all over the cupcake, making it look woolly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29CB5F8D6BB9F5488D3CDBD56C71DA79', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 11, NULL, NULL, 2, NULL, 0, 'Pipe an inch tall mound of frosting on top of the frosted cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24CFF26E7903C3418D909218C0E9E6D3', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 12, NULL, NULL, 2, NULL, 0, 'Set a Peppermint Patty sheep in front of the mound of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'989BFEA148448D42BEA20B5BB16FC867', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 13, NULL, NULL, 2, NULL, 0, 'Pipe some dots of frosting on top of the Peppermint Patty sheep heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EEC8C62CB84F8498D4C26F5F7202102', X'4C1D187507042843A138B985ADDCA345', X'4C1D187507042843A138B985ADDCA345', 14, NULL, NULL, 2, NULL, 0, 'Insert 4 tear drop shaped legs into each lamb cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCBE1AC9EAD9064491BE08C7C151A97A', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'717EE3818752324CBB908101E16CB52D', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 1, NULL, NULL, 2, NULL, 0, 'Pour melted candy melts onto the lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEE83D01308926498779E3E5D4C236A0', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 2, NULL, NULL, 2, NULL, 0, 'Spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3002351667DF284E89D2CC2F82617FA8', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 3, NULL, NULL, 2, NULL, 0, 'Arrange Minion icing decorations over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDBCB2694FEFD045905079975016D148', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 4, NULL, NULL, 2, NULL, 0, 'Toss on blue sprinkles and add a few candy bananas.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'890CCA07490CF547B1360E49C26DB7CB', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate until the chocolate sets but is still soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FC74FFB08E6114C8476E7E9B85EF0A8', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 6, NULL, NULL, 2, NULL, 0, 'Remove and cut into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20021F88C0047B4C832CFF840FFECFB6', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 8-10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0F2200174C3F844A7009F0C49CC6851', X'0F22E7459660724DBFA777DECAA124ED', X'0F22E7459660724DBFA777DECAA124ED', 8, NULL, NULL, 2, NULL, 0, 'Remove and break into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9433CB2E95D60B4692A4B71F2DA3D0F3', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 0, NULL, NULL, 2, NULL, 0, 'Melt and temper pure white chocolate or melt white confectionery coating. See the Chocolate Making Tips page at HungryHappenings.com for detailed instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'353E4059EE4BA24B98512AF4A54F21CD', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 1, NULL, NULL, 2, NULL, 0, 'Spread the white chocolate out onto a parchment paper lined baking tray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BE9876FAEB54E46BED156E2E0DA50CD', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 2, NULL, NULL, 2, NULL, 0, 'Immediately set the dark chocolate wafers all around the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6AA06C31CC321428B3AEA7006AB55D5', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 3, NULL, NULL, 2, NULL, 0, 'Add two chocolate chip ears next to each chocolate wafer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE5F6C2CC505AC4F86E56AE499F27DBF', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 4, NULL, NULL, 2, NULL, 0, 'Toss on sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E584E15EC4DB0469A897DC4AD737841', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate for 2-5 minutes until the white chocolate begins to set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B86B37DA0EA93409E4FEAAD915D74DF', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 6, NULL, NULL, 2, NULL, 0, 'Remove and cut into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC0870CF59E11B42B2083925168FE966', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5 more minutes until the white chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39BB4BB4E122574DB7A76A70F00A4707', X'BFEBD60288755446A5EB9F90F0265799', X'BFEBD60288755446A5EB9F90F0265799', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to 2 months.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57FE609828C0BC4FA5EF71D342A42FEA', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 0, NULL, NULL, 2, NULL, 0, 'Unwrap candy eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'329B3B8F1B7F5943A1D8FB2405A90285', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 1, NULL, NULL, 2, NULL, 0, 'Draw pupils onto each white confetti sprinkle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'583803A13DBF8446B5732840E1D0337F', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 2, NULL, NULL, 2, NULL, 0, 'Use white confectionery coating to attach two eyes to each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'481157BB42EC6B4986B3C3AAAE8C0DEA', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 3, NULL, NULL, 2, NULL, 0, 'Draw eyebrows over each eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69AADDC9718764409F1BB5BD11273ACC', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 4, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of white modeling chocolate and roll into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4D06A1A497DF34285865F36A6238F20', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 5, NULL, NULL, 2, NULL, 0, 'Roll balls into logs, indent the upper portion of the log, shaping into squarish ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6F68629EDD08B4AB84CC48D2DD064A0', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 6, NULL, NULL, 2, NULL, 0, 'Add a black dot inside each ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'563820D6FE42F24B9C865F09FB4C6B03', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 7, NULL, NULL, 2, NULL, 0, 'Flatten out opposite end of log.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE9FFD704413D74A82E8FDC7E17D3C1E', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 8, NULL, NULL, 2, NULL, 0, 'Use white confectionery coating to attach two ears to the top of each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDA3C9596B6A184C834E34072E7A78A1', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 9, NULL, NULL, 2, NULL, 0, 'Draw a smile onto each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AFF4F86E92B164CB5D1A69DDBA045AC', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 10, NULL, NULL, 2, NULL, 0, 'Pinch off a teaspoon size piece of black modeling chocolate and shape into Norm''s nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FFE0EC17C67224AB7E2273B2C49C573', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 11, NULL, NULL, 2, NULL, 0, 'Attach to egg using melted white coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BEDD82432D096469AD5FE2CFA2F24D2', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 12, NULL, NULL, 2, NULL, 0, 'Repeat, creating a total of 6 Norm of the North eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B01A993AE8E1734BAC571F39AD032587', X'FD4419B1504C7B4EA3DDD00B97BE4358', X'FD4419B1504C7B4EA3DDD00B97BE4358', 13, NULL, NULL, 2, NULL, 0, 'Dip lollipop stick or cake pops straw into white confectionery coating and attach to back side of each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AC492679A88C04AA66011FFE556B214', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 0, NULL, NULL, 2, NULL, 0, 'Shred white cheddar cheese using the small holes on a box grater.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1B66AA1713E814B8B92032002EE7C81', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 1, NULL, NULL, 2, NULL, 0, 'Reserve 1 1/2 tablespoons of the flavored cream cheese to assemble your cheese ball Snowgies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA05351FA1124C4CB75711F113284F8A', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 2, NULL, NULL, 2, NULL, 0, 'Combine 3 ounces (about 1/2 cup) of the shredded white cheese with the remaining cream cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D6A71D7043DF842A8D1CE690C99CC9E', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 3, NULL, NULL, 2, NULL, 0, 'Scoop 20 balls of cream cheese mixture, about 2 teaspoons in each scoop, and roll in cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64E3E3D8CDB26E45A8D26E602777A8CE', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 20 tiny balls of cream cheese and roll in shredded cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70972A130E957B4EB82F160B9A6FE743', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 5, NULL, NULL, 2, NULL, 0, 'Set one of the larger balls on your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31F0885C98BD9344B22123CE7A0AF923', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 6, NULL, NULL, 2, NULL, 0, 'Use some of the reserved cream cheese to attach another larger ball on top of that ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'096847F82FE9DF439F21F06F204C34C8', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 7, NULL, NULL, 2, NULL, 0, 'Then attach two of the tiny cheese balls creating the Snowgie''s feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E56B6ED2CA94F34FBC7DFFD33C44D0FD', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 8, NULL, NULL, 2, NULL, 0, 'Use a Nori smiley face punch to punch out smiley faces out of Nori sheets or black sugar sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DE9248BE51CD84599451E2CBEB2BAB4', X'53D1F8C4FEF7C54686DE4BF23F8438EC', X'53D1F8C4FEF7C54686DE4BF23F8438EC', 9, NULL, NULL, 2, NULL, 0, 'Brush the smiley face pieces lightly with water and press onto the Snowgie cheese balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F9C3FFEA42587448C19403718B85B32', X'6D7AC04420F33747A7F57BA43E39A041', X'6D7AC04420F33747A7F57BA43E39A041', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'231E9DD977FD3C4B9F16F9B98F1994B7', X'6D7AC04420F33747A7F57BA43E39A041', X'6D7AC04420F33747A7F57BA43E39A041', 1, NULL, NULL, 2, NULL, 0, 'Pour white chocolate into a tall glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8447C6856E574F44A9A5DB9DDC811B82', X'6D7AC04420F33747A7F57BA43E39A041', X'6D7AC04420F33747A7F57BA43E39A041', 2, NULL, NULL, 2, NULL, 0, 'Dip one pretzel rod in the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B11CCFDC27BCBD4EAF8722A245CF074C', X'6D7AC04420F33747A7F57BA43E39A041', X'6D7AC04420F33747A7F57BA43E39A041', 3, NULL, NULL, 2, NULL, 0, 'Remove and allow the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EE7C37656761D4CBF68726065A052D6', X'6D7AC04420F33747A7F57BA43E39A041', X'6D7AC04420F33747A7F57BA43E39A041', 4, NULL, NULL, 2, NULL, 0, 'Roll in the blue/white/snowflake sprinkles and the blue and white snowflake candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5DBFA16B7523E42A349650DFD65B310', X'6D7AC04420F33747A7F57BA43E39A041', X'6D7AC04420F33747A7F57BA43E39A041', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper or wax paper lined baking sheet and allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77EA4CBDC9F6C24F9295419040082AD8', X'7089804A3DC1C54AA374E0F8FF66F6A2', X'7089804A3DC1C54AA374E0F8FF66F6A2', 0, NULL, NULL, 2, NULL, 0, 'Pour cherry gelatin and unflavored gelatin into a saucepan and stir together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08B4DD2C2DF5324E831C4C3B275A39AE', X'7089804A3DC1C54AA374E0F8FF66F6A2', X'7089804A3DC1C54AA374E0F8FF66F6A2', 1, NULL, NULL, 2, NULL, 0, 'Pour in the cola and stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69D04C2007028C45A871CC176D6A80AA', X'7089804A3DC1C54AA374E0F8FF66F6A2', X'7089804A3DC1C54AA374E0F8FF66F6A2', 2, NULL, NULL, 2, NULL, 0, 'Turn on heat to medium low and heat until the mixture just starts to bubble around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7345A258AB89F4C80ADEB8E130752F7', X'7089804A3DC1C54AA374E0F8FF66F6A2', X'7089804A3DC1C54AA374E0F8FF66F6A2', 3, NULL, NULL, 2, NULL, 0, 'Add a few drops of black food coloring and stir until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C2EFA2BC360C34C88D55BC80AB74C64', X'7089804A3DC1C54AA374E0F8FF66F6A2', X'7089804A3DC1C54AA374E0F8FF66F6A2', 4, NULL, NULL, 2, NULL, 0, 'Pour into Darth Vader molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8B631BDB93BDD40ABF4009CCCCC2C25', X'7089804A3DC1C54AA374E0F8FF66F6A2', X'7089804A3DC1C54AA374E0F8FF66F6A2', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83CE7CCA0713CD4B88A874A2A9611567', X'7089804A3DC1C54AA374E0F8FF66F6A2', X'7089804A3DC1C54AA374E0F8FF66F6A2', 6, NULL, NULL, 2, NULL, 0, 'Remove and un-mold the candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45F0F397B912D94DA67100BBB7D957D0', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5151CB292ABB76468F1CD1028755025B', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 1, NULL, NULL, 2, NULL, 0, 'Line two 9x13 baking pans with non-stick foil and spray with baking spray, or spray the pans, line with parchment paper, spray again, and dust with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41DE831DBFCA794CA1E235405F61113F', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 2, NULL, NULL, 2, NULL, 0, 'Pour Coca-Cola into a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40D518406BC242419AB018C3E36AA768', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 3, NULL, NULL, 2, NULL, 0, 'Heat over medium heat, stirring often, until thickened and reduced to about 2 1/2 tablespoons, about 30 minutes. Watch it near the end so it doesn''t burn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B0BA83EE39EFC43BDCB98678AF83CF4', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 4, NULL, NULL, 2, NULL, 0, 'Reserve 1 teaspoon of the Coke syrup for the filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91D619C086D32B408EC5158D7448A035', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 5, NULL, NULL, 2, NULL, 0, 'Pour the remaining syrup in a microwave safe bowl along with the chocolate and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18A4E90E5EEA814C8E85E8F198D2A2E5', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 6, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C20255C67E6634458A2A9E685D1BD7BF', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 7, NULL, NULL, 2, NULL, 0, 'Stir then let rest for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88030DF45CD894419D4D874F965AFB5E', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 8, NULL, NULL, 2, NULL, 0, 'Stir again until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C4B767784E61C4B8AE08C6B68092832', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 9, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27394AFC0FAF0E49B81DAE4E2AF38C27', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 10, NULL, NULL, 2, NULL, 0, 'Beat egg yolks using an electric mixer set on medium high speed until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'917D0F61D594CD4C808E65072B5B22F8', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 11, NULL, NULL, 2, NULL, 0, 'With the beaters running, sprinkle in about 3 tablespoons of the sugar and continue to beat until the eggs become pale yellow and thick enough that it falls in a thick ribbon when the beaters are lifted out of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFC0927CFD012F46842D6F41692E8F1D', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 12, NULL, NULL, 2, NULL, 0, 'Fold the egg yolks into the melted chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F06C935AA696494BBBEE1F5407300623', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 13, NULL, NULL, 2, NULL, 0, 'Sift together the flour, cocoa powder, and salt and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28B5CF706DB2434B9FA65D01F435EAE2', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 14, NULL, NULL, 2, NULL, 0, 'In a clean bowl with clean beaters whip the egg whites and cream of tarter until foamy. Add 1 teaspoon of the sugar and continue to beat to soft peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B1EAEF7844565478F26F09E2BE37E57', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 15, NULL, NULL, 2, NULL, 0, 'Continue beating and sprinkle in the remaining sugar until the whites become glossy and hold stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0621D0F67830B44F8E5E9AE7D195467D', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 16, NULL, NULL, 2, NULL, 0, 'Don''t over beat until dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DF8CFEDC4E25048843EA70FD910FC28', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 17, NULL, NULL, 2, NULL, 0, 'Fold 1/3rd of the egg whites into the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F245B7F466AF2941AE5033C1BE343AF8', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 18, NULL, NULL, 2, NULL, 0, 'Then gently fold in the remaining egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E32EA239D97E64DAA5DA3C332393B90', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 19, NULL, NULL, 2, NULL, 0, 'Fold in the dry ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6BF664F81FB8347AB7D40FAD15F65ED', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 20, NULL, NULL, 2, NULL, 0, 'Equally divide the batter among the two cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF8FE96323A9DC4EA14CF9C2CCB22471', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 21, NULL, NULL, 2, NULL, 0, 'Bake for about 8 minutes, until the center of the cake springs back when touched.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56ECBFD5CC1C034694D157179CAE7A59', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 22, NULL, NULL, 2, NULL, 0, 'While the cakes are baking cut 8 strips of parchment paper that are at least 4 1/2 inches wide by 8 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF457A236E0DA94692A4379E8EDE68FE', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 23, NULL, NULL, 2, NULL, 0, 'Set them out on your counter and dust them with cocoa powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0E38C441623964AA547E7B5E054961D', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 24, NULL, NULL, 2, NULL, 0, 'Remove the cakes from the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94EDE37CBAF0044D9F852E0801C2F1CE', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 25, NULL, NULL, 2, NULL, 0, 'Let them sit for 3 minutes, then remove one from the pan. Cut into 4 equal rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1CA12ACBF4664479B17FE42CBCC9C5A', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 26, NULL, NULL, 2, NULL, 0, 'Immediately, set one cake rectangle on each parchment paper strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B4A25F6B7A92A4F8038E762E11D1804', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 27, NULL, NULL, 2, NULL, 0, 'Roll up the cakes and parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CE241888D6161489977E8658F262CDE', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 28, NULL, NULL, 2, NULL, 0, 'Repeat with second cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A75D2C12B3631341B19228F518E4AC0D', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 29, NULL, NULL, 2, NULL, 0, 'Allow cakes to cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB3992BF5156504EB8F8493A0BBDB595', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 30, NULL, NULL, 2, NULL, 0, 'Meanwhile make your filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91EE55C7317FC44CA75C78889D1714CB', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 31, NULL, NULL, 2, NULL, 0, 'Combine the shortening, powdered sugar, marshmallow fluff, and whipping cream in a large mixing bowl and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B65C0DB8C0C45E4D81C8985FB4EA26C4', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 32, NULL, NULL, 2, NULL, 0, 'Stir in the reserved Coca-Cola syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'393662536BA4C043A214EECCBEFEA3E0', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 33, NULL, NULL, 2, NULL, 0, 'When cakes are ready, carefully unroll them and remove the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A5A29412A0FA74A9000B9959796D4EF', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 34, NULL, NULL, 2, NULL, 0, 'Pipe or spread filling on each cake leaving a border around the edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'847ED3B7703B3A42ADFD682D2EDE3449', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 35, NULL, NULL, 2, NULL, 0, 'Roll up cake and repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F441F953FCE3F4DA77996146E884423', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 36, NULL, NULL, 2, NULL, 0, 'Now they are ready to become penguins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8A835FCCFE0C444A2C3A90A19F5DDC7', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 37, NULL, NULL, 2, NULL, 0, 'Color a small amount of the white modeling chocolate orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4828E75A98351B46A69CEDBCB9812F4C', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 38, NULL, NULL, 2, NULL, 0, 'Roll out the white, orange and dark modeling chocolate to about 1/16-1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'726BAAC3A71C7A44A7A3E1DA634B4344', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 39, NULL, NULL, 2, NULL, 0, 'Cut oval bellies, two white eyes, two small dark pupils, two tear drop shaped wings, and 3 triangles for the beak and feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE9B4F9AB6BE474B8B670D5E56461044', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 40, NULL, NULL, 2, NULL, 0, 'Lightly brush water on the back side of the pupils and attach to the whites of the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13DA81D47A9C2C47B01AA219EB760315', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 41, NULL, NULL, 2, NULL, 0, 'Attach the eyes, along with a beak, two wings and a white belly using the candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BDDBBC3EA5A9F42A1B77ED75C7F53DA', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 42, NULL, NULL, 2, NULL, 0, 'Cut two small slits along one edge of the remaining triangles, creating feet and attach them to the cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0ADD083861E55418A06ECE5C725035A', X'76C0218E1C3B2C4286A04718BB1E9A2A', X'76C0218E1C3B2C4286A04718BB1E9A2A', 43, NULL, NULL, 2, NULL, 0, 'You can serve these standing up if you trim the bottoms as needed so they stand upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'406FF44E0ABC5E41A8F3D52F3C79AABB', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 0, NULL, NULL, 2, NULL, 0, 'VANILLA CUSTARD NO CHURN ICE CREAM-');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15221E62116BAB43AF7010F72AA59A11', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 1, NULL, NULL, 2, NULL, 0, 'Whisk heavy whipping cream and Vanilla Custard flavoring to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F999461FA944D04D962F491EB1C59060', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 2, NULL, NULL, 2, NULL, 0, 'Stir in sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'884D1828D0544C4490C8F6F2EEA9973E', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 3, NULL, NULL, 2, NULL, 0, 'PANDA BEAR DECORATIONS-');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C330FB8AEA89CE4AB11672FB032668A9', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 4, NULL, NULL, 2, NULL, 0, 'Heat candy melts on high in the microwave for 10-20 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90393EABC8DCE54A8687DE8C92F0326B', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 5, NULL, NULL, 2, NULL, 0, 'Pour candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41629543728B174DB188A90F289E08A6', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 6, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment paper on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'509F180F1C86744DB14103664599DA05', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 7, NULL, NULL, 2, NULL, 0, 'Pipe 10 ears, 10 eye patches, 5 noses, and 5 smiles for each panda bear cone onto the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C35E9DA17EDFC04F920E93510B884B29', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 8, NULL, NULL, 2, NULL, 0, 'Freeze for about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1DC32CAA712A645A7F4F993529040FA', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 9, NULL, NULL, 2, NULL, 0, 'Let sit for 10 minutes, then peel off paper and turn over.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3A2198D53322144BD768756A9B06BBD', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 10, NULL, NULL, 2, NULL, 0, 'Use melted candy coating to attach a candy eye to each eye patch.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D9C572CDEF2EC4CBF9A9A572B7CACFC', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 11, NULL, NULL, 2, NULL, 0, 'ICE CREAM CONE ASSEMBLY-');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F013E511D4108448B6431E918F39E9AA', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 12, NULL, NULL, 2, NULL, 0, 'Fill 5 ice cream cones with ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50FB55F818A9E2448165CDBFFAACFA42', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 13, NULL, NULL, 2, NULL, 0, 'Top each with a rounded scoop of ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F2C327D4549B24EA46C7DD4058CE175', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 14, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD2532632808EB47830837B95F6AB4E3', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 15, NULL, NULL, 2, NULL, 0, 'Remove one cone at a time and insert 2 ears, 2 eyes, 1 nose and 1 smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD29F3C97D9A86489646FE7E07261789', X'F9C2CB607E9A5C40B942BBDD56ECED52', X'F9C2CB607E9A5C40B942BBDD56ECED52', 16, NULL, NULL, 2, NULL, 0, 'Freeze until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAF63C9CD1240741A577848944CBE819', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 0, NULL, NULL, 2, NULL, 0, 'Grease small 1 1/2 to 2 1/2 inch star shaped cookie cutters with butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD51BB05AF332E45818DEC4A5921F87A', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 1, NULL, NULL, 2, NULL, 0, 'Cut marshmallows with star shaped cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60D2FC3AD13AB94D8F9BD881A7CD6ADE', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 2, NULL, NULL, 2, NULL, 0, 'Cut stars in half to make 2 thin stars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6F9DD717BFA614A83C80752B40E8245', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 3, NULL, NULL, 2, NULL, 0, 'Melt and temper dark chocolate, if using pure chocolate, or melt candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF9419331610844DA7E48FCFB2D3FC41', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 4, NULL, NULL, 2, NULL, 0, 'Dip a marshmallow star in chocolate then coat with graham cracker crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93570572BE4BEA4E9927EBD30795ABF2', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 5, NULL, NULL, 2, NULL, 0, 'Set dipped marshmallow on a parchment paper lined baking sheet and allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FA1BE75366682418A513ABBC361AB68', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 6, NULL, NULL, 2, NULL, 0, 'Repeat, creating 60 starfish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AD145D4C7001A4DAA2F718827AE3B63', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 7, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes to each using the remaining chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A436AB5A449D240B66FACE0D878FB6A', X'F7C5CB411AA11248BE8B15964F3BD645', X'F7C5CB411AA11248BE8B15964F3BD645', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to a month.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'549E30B96F6A6440B2AB69F8C9544690', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside. Cream butter and sugar until light and fluffy. Stir in egg, vanilla, almond extract, and yellow food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0D7DE639B6A174AA984368E9D605DC4', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 1, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined. If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71FAAAFDB0A00A4B882176BF0B6626CD', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 2, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface or non-stick mat with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B6D54F68A817742B8B3E6626756F4A5', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 3, NULL, NULL, 2, NULL, 0, 'Roll the cookie dough out to between a 1/16 inch and 1/8th inch thickness, cut 17 or 18 circles using a round cutter, and drape circles of dough over each mound on your pan and smooth with your fingers. Freeze pan for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A19B008E9BAAFA46A8CB4CD6C6038F7A', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 4, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degree oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE455593497CCD4DB5781FC9E83A3964', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5881D16354ACA744AD4546CAD810EF76', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 6, NULL, NULL, 2, NULL, 0, 'Roll out the remaining dough and cut out 17-18 sunshine shapes. Set them on parchment lined baking sheets, refrigerate for 15 minutes, then bake for 10-14 minutes until the tops look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8BDCFCD1F4F7D418C6D9FEE8BB3C8AB', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 7, NULL, NULL, 2, NULL, 0, 'Cool cookies completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B003206A0CDF1243BA1CFDC55217EF62', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 8, NULL, NULL, 2, NULL, 0, 'Fill each half sphere cookie with candy, pipe on some yellow royal icing or melted candy coating around the top edge of the cookie, set a sun shaped cookie over top, turn the cookies right side up and allow to dry completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E40D861A7211C41A272AE94AE9FE270', X'9C327F6DE6DD9A43825CDD5F8115643F', X'9C327F6DE6DD9A43825CDD5F8115643F', 9, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes using icing or candy melts, then draw on smiles using black food coloring markers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D700374397A4146871CA0743CCE1DB3', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFC467BB73BECF4C9A276669B0284D06', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 1, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AF5D682F2111C4F905DBC5EEBFD5C2C', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 2, NULL, NULL, 2, NULL, 0, 'Stir in eggs, vanilla, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25BCC63F8F512B4F84853F8B6ABEE07F', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 3, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until it''s mixed in.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'660F902E5E5AB3458060301740E87FED', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 4, NULL, NULL, 2, NULL, 0, 'Roll cookie dough out to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2760ED1E878FDB45B063ED914315EEA1', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 5, NULL, NULL, 2, NULL, 0, 'Cut out 24 circles using a 3 inch cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B2C431B52A4054C99B29125DB616584', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 6, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet and refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'612D010D69C0054596161938BDDDE87F', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 7, NULL, NULL, 2, NULL, 0, 'Cut out 24 more round cookies, then using a 2 1/4 inch cutter, cut a circle from the center of each of those cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7A8869AC4274F42957E273889415033', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 8, NULL, NULL, 2, NULL, 0, 'Re-rolling dough as needed, do this again creating 24 more cookies with holes in them, making a total of 48 holey cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59F74388A630E54C8D6110AFBC3BF2F9', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFAB3AF30F435C4F81A106AF07AF994A', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 10, NULL, NULL, 2, NULL, 0, 'Bake cookies in a 375 degree oven for 7-11 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBAEE8C3690E8442AA513853158BB9BE', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 11, NULL, NULL, 2, NULL, 0, 'Cut open one tube of the Betty Crocker Cookie Icing and pour it into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82450FD2088F634CB43C42F8EABE17C6', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 12, NULL, NULL, 2, NULL, 0, 'Heat it for about 20 seconds until it is thin and pourable.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1471478B43F7642886F6F64B6BA8443', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 13, NULL, NULL, 2, NULL, 0, 'Dip the tops of the whole round cookies in the icing so you get a nice smooth surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8063E2A042C9844AB723157CB610B3E', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 14, NULL, NULL, 2, NULL, 0, 'Allow the cookies to dry for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'460F71DEE2E9BB48A3230C4E58CAA21B', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 15, NULL, NULL, 2, NULL, 0, 'Attach one holey cookie on top of each of the frosted cookies using a thin band of white icing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA45D66A8022AD4C990FF95BB125B0FF', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 16, NULL, NULL, 2, NULL, 0, 'Allow these cookies to dry overnight! The icing needs to be very hard so the googly eyes can slide around on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECB15CC3B3705F4E9452775FFF2F176C', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 17, NULL, NULL, 2, NULL, 0, 'Place three baking pans in a 350 degree oven and allow them to get good and hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CB454F265529742942F25D752AADDDA', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 18, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the clear tops of the googly eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D34C044B3BD434E9FB5C31CCD3196CE', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 19, NULL, NULL, 2, NULL, 0, 'Start by placing the Isomalt nibs or broken sticks into a glass microwave safe bowl with a pour spout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B273256A66DF64AAC97267DB08CE23B', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 20, NULL, NULL, 2, NULL, 0, 'Heat in the microwave until melted and thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C3E91A500AF6045B7D85B7AF950C371', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 21, NULL, NULL, 2, NULL, 0, 'Remove one hot pan from the oven. Set it on a towel or trivets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCFE6BB5B5527F44926B6DD835D271F2', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 22, NULL, NULL, 2, NULL, 0, 'Place a piece of parchment paper in the pan. Set 8 cookies on the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D74180B6FDB8A345B0D22D4C9BD12090', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 23, NULL, NULL, 2, NULL, 0, 'Pour some isomalt in the center of each cookie, allowing it to completely cover the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8418266D7529004D863097E9D4F6CA04', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 24, NULL, NULL, 2, NULL, 0, 'Allow the isomalt to harden overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'011067B79AC97A4E8491EC5BA0A23922', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 25, NULL, NULL, 2, NULL, 0, 'Good morning. Now you are ready to assemble your Cookie Googly Eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CF30C8F471F464DB584FFC264639BA7', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 26, NULL, NULL, 2, NULL, 0, 'The Ghirardelli Candy Making Wafers and most Candy Melt type wafers have a tip on one side.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36ADD4C3AF644542B9C875BE84223E3C', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 27, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to cut wafers into thin flat wafers, cutting off the tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'399E12DA979AE345B9EDD961DDD24601', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 28, NULL, NULL, 2, NULL, 0, 'Set one candy wafer inside the stacked cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5770FCC63878174B83BC589531A8622B', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 29, NULL, NULL, 2, NULL, 0, 'Pipe some icing on the back side of the candy filled cookies and set one on each cookie stack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F715653AB9D51E4A9C8CD9CDCC355931', X'4A8F25933C661640960593E131B2A794', X'4A8F25933C661640960593E131B2A794', 30, NULL, NULL, 2, NULL, 0, 'Now go have fun wiggling your cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B1EE3DA6184E84084231F9FB159D785', X'67162A7C2284EA4BB28BD33DA67620A6', X'67162A7C2284EA4BB28BD33DA67620A6', 0, NULL, NULL, 2, NULL, 0, 'Cream butter, brown sugar, and granulated sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C6ABA144E52A94B8E90C107A585710A', X'67162A7C2284EA4BB28BD33DA67620A6', X'67162A7C2284EA4BB28BD33DA67620A6', 1, NULL, NULL, 2, NULL, 0, 'Add egg, egg yolk, and vanilla and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D4E4434B9A5084F9D43AA199F9B71F4', X'67162A7C2284EA4BB28BD33DA67620A6', X'67162A7C2284EA4BB28BD33DA67620A6', 2, NULL, NULL, 2, NULL, 0, 'Combine salt and baking soda with 1 1/2 cups of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87764A11BB489547A3EF077B061F8940', X'67162A7C2284EA4BB28BD33DA67620A6', X'67162A7C2284EA4BB28BD33DA67620A6', 3, NULL, NULL, 2, NULL, 0, 'Add to wet ingredients and stir just to combine. If the dough is sticky add the remaining 1/4 cup flour, if not, then just leave it out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9CC2013BFD37046B224C8E64F1D719E', X'67162A7C2284EA4BB28BD33DA67620A6', X'67162A7C2284EA4BB28BD33DA67620A6', 4, NULL, NULL, 2, NULL, 0, 'Stir in mini chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6A22EC9D6A46A4A9ABB556BE730B11D', X'67162A7C2284EA4BB28BD33DA67620A6', X'67162A7C2284EA4BB28BD33DA67620A6', 5, NULL, NULL, 2, NULL, 0, 'Line glass bowl with non-stick tin foil. Roll out dough and drape over bowl. Freeze for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E56F70B44D92AF4FB3E54389F08AF6C7', X'67162A7C2284EA4BB28BD33DA67620A6', X'67162A7C2284EA4BB28BD33DA67620A6', 6, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees. Bake for 30-36 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AAD2C3453BD6E46AF0C92632E6FB671', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 0, NULL, NULL, 2, NULL, 0, 'Cream butter, brown sugar and granulated sugar and beat on medium speed for about a minute until well blended. Add egg, egg yolk, and vanilla and beat until fully incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF3A419B67A8F349982C048EE7FD7AD5', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 1, NULL, NULL, 2, NULL, 0, 'Mix flour, salt, and baking soda together then incorporate it into the wet ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'418C2D4F08527B49B1166CFAF9CD3DB4', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 2, NULL, NULL, 2, NULL, 0, 'Fold chocolate chips into the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23FE930E8B11E845ADCB6E6B5C760203', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 3, NULL, NULL, 2, NULL, 0, 'Scoop out 16-18 cookies, about 1/4 cup of dough for each. Place cookies on parchment paper lined baking sheets. Refrigerate for 30-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB6F2FD14884C04383A17B938CB6E9AD', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 4, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'801982E59FE6A34F81C836E23CCD7BA6', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies one tray at a time, for 12-16 minutes. The cookies should begin to set around the edges, be light golden brown, yet still be soft in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95966B3B1F24484EB5E2F6E3C27CF017', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 6, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95F83C22D66421498731B64DF29C9D65', X'2E9D1F08340F194C90F59801178540AC', X'2E9D1F08340F194C90F59801178540AC', 7, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes to each cookie using melted confectionery coating. Pour remaining coating into a squeeze bottle or small zip top bag. Pipe smiles onto cookies. Let candy coating harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6F5FD95875D8440B62F713FF8CCD066', X'B3DDB3D00BCDB044B82D8104DF217403', X'B3DDB3D00BCDB044B82D8104DF217403', 0, NULL, NULL, 2, NULL, 0, 'Cut soft flour tortillas into 4 1/4 inch by 2 3/4 inch rectangles. (You can change these dimensions if youd like larger sandwiches.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C85466F3004E7C4AB8C85F2A96AED184', X'B3DDB3D00BCDB044B82D8104DF217403', X'B3DDB3D00BCDB044B82D8104DF217403', 1, NULL, NULL, 2, NULL, 0, 'Then cut slices of white cheese and deli meat into 1 3/4 inch by 2 1/2 inch rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06E0BCFA4B8BC1429825B25EAE679CBE', X'B3DDB3D00BCDB044B82D8104DF217403', X'B3DDB3D00BCDB044B82D8104DF217403', 2, NULL, NULL, 2, NULL, 0, 'Stack slices of cheese and deli meat, alternating the two until you have 5 slices or pages for your book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BB92FB76BDE3C4C9E19F9EF22510504', X'B3DDB3D00BCDB044B82D8104DF217403', X'B3DDB3D00BCDB044B82D8104DF217403', 3, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of mayonnaise on one side of the tortilla rectangles, keeping it 1/4 inch from the edge then set the cheese and meat pages on one edge of the tortilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71F14F322899D3488548873707617220', X'B3DDB3D00BCDB044B82D8104DF217403', X'B3DDB3D00BCDB044B82D8104DF217403', 4, NULL, NULL, 2, NULL, 0, 'Fold the tortilla over the `pages` forming a book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D73E1FC8044E964FB5D2BC20629C77EA', X'B3DDB3D00BCDB044B82D8104DF217403', X'B3DDB3D00BCDB044B82D8104DF217403', 5, NULL, NULL, 2, NULL, 0, 'Use a food coloring marker to write the title of the book on the front of each sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38639B537F876249BE9D68478EF9A9F7', X'B3DDB3D00BCDB044B82D8104DF217403', X'B3DDB3D00BCDB044B82D8104DF217403', 6, NULL, NULL, 2, NULL, 0, 'Add some lines and repeat the name on the binding (edge) of the book.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA527D4C03A2DE45941679F1C63F0522', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 0, NULL, NULL, 2, NULL, 0, 'Cut Twix Bites in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3511DD72529AB7459D11A4B5F2335D06', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 1, NULL, NULL, 2, NULL, 0, 'Pop the Orville Redenbacher''s Naturals Simply Salt Popcorn according to package instructions. Remove un-popped kernals and pour popcorn into a very large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B2B2F1C2A2DB14982B0A8E560AADEBA', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 2, NULL, NULL, 2, NULL, 0, 'Melt white confectionery coating. Heat it in the microwave on high power for 1 minute, then stir, and heat on high for 30 seconds. Then allow the candy coating to sit in the microwave for 1-2 minutes. Remove bowl and stir. If wafers are not completely melted, heat on high for 10 second increments, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8306E702088C6249BC1DC4CA931D039C', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 3, NULL, NULL, 2, NULL, 0, 'Heat dark cocoa confectionery coating in microwave on high for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D295A293F620649809A98E4C5AAB629', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 4, NULL, NULL, 2, NULL, 0, 'Pour peanut butter chips into bowl of melted white candy coating. Stir, and wait 1 minute, then gently stir again. The chips will melt slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32582905F1DFCF45A7306841BDA68CDB', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 5, NULL, NULL, 2, NULL, 0, 'Pour over the popcorn. Toss to stir until all the popcorn is coated. Toss in the Twix Bites.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1C78B11DEFA354488ABF392974B1C90', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 6, NULL, NULL, 2, NULL, 0, 'Pour popcorn onto a parchment paper lined baking sheet. Spread into a thin layer. Drizzle with the melted dark confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53DE11ED76EBDE488AD666B5789DE26E', X'10F9114A5D38C84B93812FC137C94F19', X'10F9114A5D38C84B93812FC137C94F19', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes or refrigerate for 15 minutes. Break into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6A427F434FB1F43AC9489AFE57C8669', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF506B580C41B24F8C5D4947EC6EAC01', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 1, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB5BE922D488E34F83F0727FCD778979', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62682C3919669C42B32ED5B4BF318B38', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 3, NULL, NULL, 2, NULL, 0, 'Stir in vanilla bean paste, and almond extract then mix in the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19C8BC795D9EAB4A9558648BFE48ABFF', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 4, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25A966B7CAF9AD448E94F4A292602342', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 5, NULL, NULL, 2, NULL, 0, 'Divide the dough into 4 equal parts (about 1 cup of dough per color).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFE742B5D7D2054AA29A4BB64DB28110', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 6, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoon size spoonfuls of dough and press into a whoopie pie pan creating a total of 8 of each color.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7B7A9BCF6C9DD44A9A7C705C3FFFA44', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAC721BA532FC347A2AEFC3A6344120A', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 8, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges of the cookies turn light brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'433EBB7D5901414FB2D8A4777DE73906', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 9, NULL, NULL, 2, NULL, 0, 'Allow to cool then remove from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B66C83E30692B6448D1DA7504002F069', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 10, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoons size spoonfuls of dough and press into individual cavities in brownie pop silicone molds, making 8 hat crowns of each color.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A9C37885C1A8B44A3DC50E63969C023', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 11, NULL, NULL, 2, NULL, 0, 'Use your finger to create a well in the center of each dough ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'546752E5AA78CE42BB3DAF6F87512357', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 12, NULL, NULL, 2, NULL, 0, 'Press the dough up the sides of the mold, up to the fill line, about 1/4 inch from the top of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A746A7BCC1ECFA42BF89462007AAEF7A', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 13, NULL, NULL, 2, NULL, 0, 'Freeze molds for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86ECC38F72696F4EAD79D2C925B380E8', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 14, NULL, NULL, 2, NULL, 0, 'Bake for 14 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9CBF8120F9CF54883C5EDE3A5D8F8E0', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 15, NULL, NULL, 2, NULL, 0, 'Remove from oven and use the handle of a wooden spoon to reshape the well in the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17481F0884DA124FA1EA1432B74D87A8', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 16, NULL, NULL, 2, NULL, 0, 'Return to the oven for 6-8 minutes until cookies are baked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDE5E025C6CE0B45ABE5BCCE276F9BDD', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 17, NULL, NULL, 2, NULL, 0, 'Reshape the well in each cookie if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53CAF0E46E91FB429748E9D4E4306340', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 18, NULL, NULL, 2, NULL, 0, 'Allow to cool completely, then remove from mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBFBEBB3E2B52A49BB9A23FFA5E249ED', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 19, NULL, NULL, 2, NULL, 0, 'Pour melted white Candy Melts into a squeeze bottle or disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'615F456B9CB3904FA5217B2CC17C2D63', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 20, NULL, NULL, 2, NULL, 0, 'Turn a hat crown upside down and fill it with M&M Minis.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2583ADA529BC54AB6463B7231E773B7', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 21, NULL, NULL, 2, NULL, 0, 'Pipe a line of white Candy Melts around the rim of the hat crown cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B074D7BF585B943937F4B3FE0C186F7', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 22, NULL, NULL, 2, NULL, 0, 'Immediately turn one of the round cookies upside down and center it over the hat crown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E874905DCF6CC4AA877F8A9372449EF', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 23, NULL, NULL, 2, NULL, 0, 'Press the two cookies together then carefully, holding the top of the hat crown and the bottom of the round cookies, flip the whole thing over.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60BC9B6ED6C79347B929CEFEA0567F8D', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 24, NULL, NULL, 2, NULL, 0, 'Set it on your work surface and pipe a line of white Candy Melts around the base of the hat crown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84BB8C9D474F7E4399A240A94171A020', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 25, NULL, NULL, 2, NULL, 0, 'Pipe decorations on the hat brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81E4AEEDBE77A144A254F5CC207FE704', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 26, NULL, NULL, 2, NULL, 0, 'Set aside to allow the candy to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EEBEBF61B5F3B48BE50EF41D38A2731', X'700F174BE0D22140999F69255E2BF36B', X'700F174BE0D22140999F69255E2BF36B', 27, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75BABE6CB0ADB14796E118176E6F71BD', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 0, NULL, NULL, 2, NULL, 0, 'Roll some white and some black (or dark) modeling chocolate out on a non-stick mat using a fondant roller.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47863A99422CC846BDFA981A84CB3188', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 1, NULL, NULL, 2, NULL, 0, 'For each bear:');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A07B155E8815FE44932BDF526E3BC548', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 2, NULL, NULL, 2, NULL, 0, 'ears - cut out 2 white circles using the medium round plunger cutter');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC1865A0BEC229429E36EC6B5C666AE2', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 3, NULL, NULL, 2, NULL, 0, 'inner Ears - cut 2 black circles using the small round plunger cutter');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10DD984793B649479263D35F9A185728', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 4, NULL, NULL, 2, NULL, 0, 'nose - cut 2 black circles using the small round cutter and pinch it between your thumb and finger shaping it into a triangle with rounded edges');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'846DC4E85E7295419FA0CA031DA09BA9', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 5, NULL, NULL, 2, NULL, 0, 'eyes - pinch off 2 tiny pieces of black and roll into balls');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C686A429B08DB340AF52154F416CD941', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 6, NULL, NULL, 2, NULL, 0, 'snout - pinch off 1 pea size piece of white and roll into a ball');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90AEA7745EC400409D08BD7FB9FA26EC', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 7, NULL, NULL, 2, NULL, 0, 'Pour melted white chocolate into a tall glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7717FD0F52AFF4AA1DE70AD60EEBA38', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 8, NULL, NULL, 2, NULL, 0, 'Dip pretzel into the chocolate, lift it out, shake it, and allow excess chocolate to drip back into the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD8AEB7D68C06D4D917D9DC2ECE3D788', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 9, NULL, NULL, 2, NULL, 0, 'If using pure tempered white chocolate, refrigerate the pretzels for about 4-5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42EDFF98BAA1EC43994CB7A439D1157A', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 10, NULL, NULL, 2, NULL, 0, 'If using confectionery coating (Candy Melts) place in the freezer for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'573468B79808374C9C1CA830029A2794', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 11, NULL, NULL, 2, NULL, 0, 'Use a bit of melted white chocolate to attach the round ball onto the pretzel, about an inch from the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3AFF4887B780B49B2069EEB2D6FA896', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 12, NULL, NULL, 2, NULL, 0, 'Dip a pretzel back into the white chocolate, giving it a second coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E038B24F60BBC41B743423BB2EFC2C7', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 13, NULL, NULL, 2, NULL, 0, 'Allow the white chocolate to set until it thickens, holding the pretzel for about 10 seconds or so, then press a fork onto the pretzel, lift up, pulling away the chocolate, creating spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF2592BCDC1E424FADC5BAFF43C7E26F', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 14, NULL, NULL, 2, NULL, 0, 'Continue doing this, adding white chocolate if needed, until the entire pretzel looks furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3936D62E5CB7D46BB4BF2F12A7C4AEA', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 15, NULL, NULL, 2, NULL, 0, 'Attach the ears, eyes, and nose using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B351725F8F0074892B09778ABCE14A0', X'52B875BD43607A4A9AABC4C8945A082E', X'52B875BD43607A4A9AABC4C8945A082E', 16, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for several weeks. Do not refrigerate or freeze.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'070D530D6B88354390833D127AEE281A', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 0, NULL, NULL, 2, NULL, 0, 'Crumble cake and mix in the vanilla and chocolate frosting until mixture holds together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2C22146CEA4C24DB7C0D9DD54C7CB02', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 1, NULL, NULL, 2, NULL, 0, 'Scoop heaping tablespoonfuls of the dough and roll into hot dog shaped logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE44C111FED0434A8FD3ABE5B8A7D1C8', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 2, NULL, NULL, 2, NULL, 0, 'Combine the peanut butter chips and 6 ounces of the white confectionery coating in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD2D31B0E41461468E0C7B2B26A91713', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 3, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24939511C5E7EF478A07ED51B84F5879', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 4, NULL, NULL, 2, NULL, 0, 'Thin with paramount crystal if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89773D3449C48B4FBBCB20C5FDDAEF91', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 5, NULL, NULL, 2, NULL, 0, 'Stir in graham cracker crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DAD9238DFE9454F9A614A4A543EE4EB', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 6, NULL, NULL, 2, NULL, 0, 'Dip the end of a lollipop stick into the melted candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F38BAD3E97A18043946D0D01A4AECAA9', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 7, NULL, NULL, 2, NULL, 0, 'Insert the stick into one end of a cake pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D73E64090A3C0469F6359BB76D25869', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 8, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C22C6B92900B0848A4DC579AF965B036', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 9, NULL, NULL, 2, NULL, 0, 'Freeze the pops for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B22E567B3A9C640B4EDECD47327B71C', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 10, NULL, NULL, 2, NULL, 0, 'Pour the peanut butter candy coating over each `hot dog.` then twirl it around a bit over the bowl, allowing the excess candy coating to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA312D58C15A3847AA17092B09556894', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 11, NULL, NULL, 2, NULL, 0, 'Melt your remaining 2 ounces of white confectionery coating then color using yellow candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71BEC76575E48E4F95D3B8A9C93AC927', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 12, NULL, NULL, 2, NULL, 0, 'Pipe it over the cake pops to resemble mustard.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FE7A6B606841D468C9C470865FA4A88', X'8C77FD2C63471249AD9DB2228DEFF474', X'8C77FD2C63471249AD9DB2228DEFF474', 13, NULL, NULL, 2, NULL, 0, 'Freeze for 2-3 minutes until the `mustard` is set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E471A76E36834D4CAD94A0E54053FD26', X'81672B7ECCFBEA4A8EF86B46CA7390DE', X'81672B7ECCFBEA4A8EF86B46CA7390DE', 0, NULL, NULL, 2, NULL, 0, 'Stir together the cream cheese and cheddar cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CED93EA57EBFD945ADAB3AC0660D57DB', X'81672B7ECCFBEA4A8EF86B46CA7390DE', X'81672B7ECCFBEA4A8EF86B46CA7390DE', 1, NULL, NULL, 2, NULL, 0, 'Reserve 2 tablespoons of this mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD6E10E37074894DA0CCDB1B13C1FFFD', X'81672B7ECCFBEA4A8EF86B46CA7390DE', X'81672B7ECCFBEA4A8EF86B46CA7390DE', 2, NULL, NULL, 2, NULL, 0, 'Stir the bacon into the remaining cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7B098867792294CB2413375F62CB058', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 0, NULL, NULL, 2, NULL, 0, 'Scoop out 1/2 tablespoon size scoops of the cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8608E1DF1EE38F4DB14AEFC7A1BE6280', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 1, NULL, NULL, 2, NULL, 0, 'Roll into the small pieces of finely shredded cheddar cheese shaping into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EB844E9B6412C4690BBE5F9F6D1A0AA', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 2, NULL, NULL, 2, NULL, 0, 'Cut 2 tear drop shapes, 2 flower shapes, and one tiny triangle from the cheddar cheese for each cheese ball chick you have made.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CD539876F9D174A9D64CB950EB68CF7', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 3, NULL, NULL, 2, NULL, 0, 'Brush orange food coloring over the triangles and flowers (or brush the coloring over the slice of cheese, allow to dry, then cut out the shapes).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECE44E8E6D5F5B4282A831057EDABA9D', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 4, NULL, NULL, 2, NULL, 0, 'Cut thin slivers off the edges of the black olives and cut into tiny circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B02FAF4E4E7D94492B0EFF5032ADFF9', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 5, NULL, NULL, 2, NULL, 0, 'Attach two tear drop shaped wings to the sides of each cheese ball using the reserved cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20C9811274DCE2429FBC2E479C3621FA', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 6, NULL, NULL, 2, NULL, 0, 'If needed, warm the reserved cheese ball mixture in the microwave on the defrost setting for about 10 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01982B2937F67045811B4DBE7E05D83C', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 7, NULL, NULL, 2, NULL, 0, 'Attach a triangle beak and two flower shaped feet to each cheese ball then attach two black olive eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'888BFF20503EDF4C9AD56A12ADC812D0', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 8, NULL, NULL, 2, NULL, 0, 'Store in the refrigerator in an airtight container until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20C53CC113C28E40BD82E806F8E847FC', X'3067267C17A4B94CB77AC4BC18EAEDE3', X'3067267C17A4B94CB77AC4BC18EAEDE3', 9, NULL, NULL, 2, NULL, 0, 'Set cheese balls on crackers just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E60D486F700E5041B1928DB9A4772DCE', X'0283A6BCE1426643AAF8A5FED573B6F7', X'0283A6BCE1426643AAF8A5FED573B6F7', 0, NULL, NULL, 2, NULL, 0, 'Flatten an orange slice into an even 1/4` thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'111C203AAE70D149A905155A64EF832D', X'0283A6BCE1426643AAF8A5FED573B6F7', X'0283A6BCE1426643AAF8A5FED573B6F7', 1, NULL, NULL, 2, NULL, 0, 'Cut two feet and a beak.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89D18A7381FE1A4088E9B271701EE4FE', X'0283A6BCE1426643AAF8A5FED573B6F7', X'0283A6BCE1426643AAF8A5FED573B6F7', 2, NULL, NULL, 2, NULL, 0, 'Cut a thin slice from each end of a marshmallow.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAED609DE651E7419D303B1F098E3EC0', X'0283A6BCE1426643AAF8A5FED573B6F7', X'0283A6BCE1426643AAF8A5FED573B6F7', 3, NULL, NULL, 2, NULL, 0, 'Flatten a soft Tootsie Roll then roll it out thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76E98EAD2350E14F8B8A7B5383F26005', X'0283A6BCE1426643AAF8A5FED573B6F7', X'0283A6BCE1426643AAF8A5FED573B6F7', 4, NULL, NULL, 2, NULL, 0, 'Cut two wings from the Tootsie Roll.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23EA494E7BCD5A438EC845802794191F', X'0283A6BCE1426643AAF8A5FED573B6F7', X'0283A6BCE1426643AAF8A5FED573B6F7', 5, NULL, NULL, 2, NULL, 0, 'Attach the candy decorations to the snack cake with frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AF9C0BD2C19CD4B91B37A2132B85BA1', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 0, NULL, NULL, 2, NULL, 0, 'Pour melted and tempered white chocolate or melted confectionery coating/candy melts into a tall glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D20EB54E5B41D469FADECBC9FEE6549', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 1, NULL, NULL, 2, NULL, 0, 'Hold onto the tip of a sugar cone and dip it into the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E008038E4A0784DB4EC4A9D73FEDD0B', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 2, NULL, NULL, 2, NULL, 0, 'Coat all but the very tip of the sugar cone with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76C0CA028F798A47B2E692A4468D6273', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 3, NULL, NULL, 2, NULL, 0, 'Remove the cone from the glass and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'749E48D9DE64874AB757221DF3B5FAE3', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 4, NULL, NULL, 2, NULL, 0, 'While wet, sprinkle colorful sanding sugar all over the cone (inside too, if you''d like).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B1D93B0A6500C4AB592BA626487A8F6', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98B30D92B794254B9DFD52612477E617', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 6, NULL, NULL, 2, NULL, 0, 'Place the dipped cones in the refrigerator for about 10 minutes until the white chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65E23244E3B8FC4CB575732602EE4DB1', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 7, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of white modeling chocolate and roll them into 1 1/4`-1 1/2` long logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38C80BB4F14AAD499C407AB74BD30C54', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 8, NULL, NULL, 2, NULL, 0, 'Insert a skewer into the log, creating a hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B9D8742E980FF4281CDEE0B5C1AF542', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 9, NULL, NULL, 2, NULL, 0, 'Move the skewer around making the hole a bit bigger.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99313CB7E549A4419EE484A0B4922BBA', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 10, NULL, NULL, 2, NULL, 0, 'Remove the skewer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6471DFC899E8584EA00CE3C51D7449AB', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 11, NULL, NULL, 2, NULL, 0, 'Press one log onto each dipped cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3A270A86E012A42A11D572947277170', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', X'FF9E25AA8B613B4ABB7BC4CCBC0FE2C4', 12, NULL, NULL, 2, NULL, 0, 'Reshape the log, if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81E9EEA4A425B14BB65FD3B988EFB8FB', X'CCA590FE2B552F4C975718AA7E450B16', X'CCA590FE2B552F4C975718AA7E450B16', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C732A49BFFFA247B5F552A04BD1A33C', X'CCA590FE2B552F4C975718AA7E450B16', X'CCA590FE2B552F4C975718AA7E450B16', 1, NULL, NULL, 2, NULL, 0, 'Spread 1/3 cup pizza sauce in the middle of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B243B6566A2A743BF5440D560CA7C70', X'CCA590FE2B552F4C975718AA7E450B16', X'CCA590FE2B552F4C975718AA7E450B16', 2, NULL, NULL, 2, NULL, 0, 'Blend together 8 ounces cream cheese, 1/2 cup shredded Mozzarella Cheese, 1/2 cup shredded Parmesan cheese, 1/2 cup sour cream, and a pinch of salt, then spread half of it over the pizza sauce in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16B38258CA69D94EB1FE2A8D7B54C0B9', X'CCA590FE2B552F4C975718AA7E450B16', X'CCA590FE2B552F4C975718AA7E450B16', 3, NULL, NULL, 2, NULL, 0, 'Top with 3/4 cup shredded Mozzarella cheese, then sprinkle on half of the pepperoni or your favorite pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAC01DC45B9C7147B8D347C088A620E8', X'CCA590FE2B552F4C975718AA7E450B16', X'CCA590FE2B552F4C975718AA7E450B16', 4, NULL, NULL, 2, NULL, 0, 'Layer on another 1/3 cup pizza sauce, the remaining cheese dip, 1/3 cup pizza sauce, another 3/4 cup Mozzarella cheese, and more pepperoni or pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8B38E6D44EFCA4488C60F6B8CF845DF', X'CCA590FE2B552F4C975718AA7E450B16', X'CCA590FE2B552F4C975718AA7E450B16', 5, NULL, NULL, 2, NULL, 0, 'Bake the dip for 28-30 minutes until the cheese is melted and the pizza crust is golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13DB8930A080AC45B0436F42012CDA1C', X'CCA590FE2B552F4C975718AA7E450B16', X'CCA590FE2B552F4C975718AA7E450B16', 6, NULL, NULL, 2, NULL, 0, 'Serve while hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBA1CDB3F3D93B49A5AAD1F24341B494', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 0, NULL, NULL, 2, NULL, 0, 'Pour red candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4634B01841C1B940B6D2E787A1157A0D', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4ABBDAEDFD428E419151F5F4E7E825AF', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C066766C2DD44448A52757BA37CDA14C', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 3, NULL, NULL, 2, NULL, 0, 'Heat for 15-20 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'680B8D53BA8091449E89C3EB1BFFE38F', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 4, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of red candy melts on the bottom and up the sides of a 2 ounce plastic shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFD478DB9D92F440835BEDE3251199FE', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 5, NULL, NULL, 2, NULL, 0, 'Wipe the top edge of the glass of any candy that has dripped over the edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CEAFAC7A3A94D4AA11C1D21C263A477', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 90 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8A56F450896C54ABD61E684D830D8BA', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 7, NULL, NULL, 2, NULL, 0, 'Remove from freezer and brush a second layer of red candy melts over the first layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF05ACED760B1841857287008CB68B46', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 8, NULL, NULL, 2, NULL, 0, 'Freeze for 2-3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44111B02C5374647A0C2B20FA850094B', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 9, NULL, NULL, 2, NULL, 0, 'Remove and slide the candy cup out of the plastic shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C1005F944CD1740B4C690C19FA9ABD7', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 10, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 6 candy cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B306DA28865688488E8C3DA48DCD3807', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 11, NULL, NULL, 2, NULL, 0, 'Roll white modeling chocolate into long thin logs, or use a clay extruder to create the logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB4AE3336935BD49B47FFB8152357524', X'B20EF368438D4049900DECDB74FFBCDC', X'B20EF368438D4049900DECDB74FFBCDC', 12, NULL, NULL, 2, NULL, 0, 'Use clear piping gel (or remaining red candy melts) to attach a log of white modeling chocolate around the top edge of a candy cup. Cut off any excess and pinch the two seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F52F1450231D34B873F847A0E439ADA', X'CD2CC73888E1AF47A396D9AFC7629D78', X'CD2CC73888E1AF47A396D9AFC7629D78', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped chocolate into a shallow bowl');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D65332DB1436D498B67CC33E6973AD2', X'CD2CC73888E1AF47A396D9AFC7629D78', X'CD2CC73888E1AF47A396D9AFC7629D78', 1, NULL, NULL, 2, NULL, 0, 'Heat your cream over medium heat to a simmer on the stove, just until bubbles begin to form then immediately pour the hot cream over the chocolate, making sure it covers all of the chocolate. Let the chocolate and cream sit, undisturbed for 3 minutes to allow the chocolate to begin to melt. Then begin stirring the mixture in small circles in the center of the bowl. As the chocolate and cream start to darken and come together you can begin to make your circle bigger. Continue stirring until the cream and chocolate come together then widen the circle again and continue until the entire bowl of cream comes together. Be sure to scrape the bottom and sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C437E880DEFF6438791176941BF8472', X'CD2CC73888E1AF47A396D9AFC7629D78', X'CD2CC73888E1AF47A396D9AFC7629D78', 2, NULL, NULL, 2, NULL, 0, 'Add 16 drops of cola flavoring. Taste. Add more if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A4566DB1FC12E4BB9AA96B2F201B74C', X'CD2CC73888E1AF47A396D9AFC7629D78', X'CD2CC73888E1AF47A396D9AFC7629D78', 3, NULL, NULL, 2, NULL, 0, 'Pour the chocolate cola ganache into the red candy cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8DD4AD0FE86AD49B28A1C4420A79366', X'CD2CC73888E1AF47A396D9AFC7629D78', X'CD2CC73888E1AF47A396D9AFC7629D78', 4, NULL, NULL, 2, NULL, 0, 'Optional: Cut straws into shorter pieces and insert one into each cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB0DE9C151F9604FBDBF42786151D107', X'CD2CC73888E1AF47A396D9AFC7629D78', X'CD2CC73888E1AF47A396D9AFC7629D78', 5, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for at least an hour so the ganache can set up before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8F956C05076C1488354947834DB98A0', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B08B7D54CEC20B428C04660D5EC2F5BA', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 1, NULL, NULL, 2, NULL, 0, 'Heat a large skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB51DAEBFDA7134EB79A3E7F57A3A67E', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 2, NULL, NULL, 2, NULL, 0, 'Add ground beef and cook until browned; drain.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'816FB8379EACF846A3EFB3FB249198C2', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 3, NULL, NULL, 2, NULL, 0, 'Add taco seasoning and water and bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79126F2C41BB02489DB3007CB4F86E77', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 4, NULL, NULL, 2, NULL, 0, 'Lower heat and simmer until most of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F5B170E0E3E6B4CAD0B537EFF12AAE5', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 5, NULL, NULL, 2, NULL, 0, 'Spread refried beans into bottom of a round or rectangle baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E15186589F8C4F4C93AD89FB6027847A', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 6, NULL, NULL, 2, NULL, 0, 'Spread the taco meat over the layer of refried beans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'965B22776710AC469191564519956FDB', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle on diced tomatoes, diced jalapenos, and chopped olives.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CED371A66AE76D4FB9B0B1698D74307A', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 8, NULL, NULL, 2, NULL, 0, 'Top with shredded cheeses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAE57D699A731F449E69B9FB93BF635D', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 9, NULL, NULL, 2, NULL, 0, 'Arrange black olive slices in the appropriate date (2015) over top of the cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBF42C6FF30CB344AFC81B0E98259FAA', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 10, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes or until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC847C77ACF12B4689A414BF697028EA', X'8EF16F3D6CD7D94C93479B1E8110571D', X'8EF16F3D6CD7D94C93479B1E8110571D', 11, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B491B822BB87224DAD9A63FB6977282C', X'20F7362BECB95844B1532DA683A5B0B9', X'20F7362BECB95844B1532DA683A5B0B9', 0, NULL, NULL, 2, NULL, 0, 'Crumble the cake and blend it together with the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55009163D904C041BCC1553E7F7EBBF2', X'20F7362BECB95844B1532DA683A5B0B9', X'20F7362BECB95844B1532DA683A5B0B9', 1, NULL, NULL, 2, NULL, 0, 'Add more frosting if desired until the cake ball filling has a nice chocolate truffle style texture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4A32BFA80752A45B304CA6780F51292', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 0, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoonful of white chocolate into one popsicle cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7E507B93917D74E90A192393A554B3A', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 1, NULL, NULL, 2, NULL, 0, 'Paint it into a thin layer on the bottom and up the sides of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D48A4ECAB9AC0142B4908671281FAB61', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 2, NULL, NULL, 2, NULL, 0, 'Immediately insert then remove a popsicle stick into the mold, to remove any white chocolate from that area.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FB7546570E32D46A5D3D1E45BFB0338', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 3, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 white chocolate shells in your popsicle mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AC3D741FFCD4B4FAC7CBE1C822E2F1A', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 4, NULL, NULL, 2, NULL, 0, 'Pop the mold into the refrigerator, if using pure white chocolate, and into the freezer if using confectionery coating (Candy Melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDA4BB85DE152A47A7DF10A8EF1334E9', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 5, NULL, NULL, 2, NULL, 0, 'Chill for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43D4B156914C9F4B94DF3653A8E83F44', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 6, NULL, NULL, 2, NULL, 0, 'Scoop out 1 tablespoonful of the cake ball filling and press it into the candy shell, leaving at least 1/16 of an inch of space at the top for the additional layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01613BDD3FC2B3459050256E84BAC351', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 7, NULL, NULL, 2, NULL, 0, 'Repeat filling all the popsicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D911E42468B0F42904502AD1B214C7E', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 8, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon of white chocolate over top and spread it out thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A69FE3971208754590FC279E2A0D4044', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 9, NULL, NULL, 2, NULL, 0, 'Use an offset spatula to scrape off any excess white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6783572F0E893D4396EE9D1519CEE5E8', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 10, NULL, NULL, 2, NULL, 0, 'Repeat covering all the cakesicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A212DAAF9C5D6F4EA0A3246251AAD112', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 11, NULL, NULL, 2, NULL, 0, 'Chill for about 2 minutes until the candy layer hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5371CFF355A2A845B3C946C4CBDE9676', X'455F829C322C3943AE4D0B9266DE80B7', X'455F829C322C3943AE4D0B9266DE80B7', 12, NULL, NULL, 2, NULL, 0, 'Unmold the cakesicles and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8401505DD52AF4DBF6E3E4EB319EF45', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 0, NULL, NULL, 2, NULL, 0, 'Grease a large mixing bowl, rubber spatula, and 9-inch x 13-inch pan with butter or non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4441C5DE9A9F6C4FB9E61FE4D3554AA9', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 1, NULL, NULL, 2, NULL, 0, 'Pour the cereal into the bowl and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C17BFA053E1484E8BA132C3068EA4F5', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 2, NULL, NULL, 2, NULL, 0, 'Begin melting the butter in a medium-large saucepan set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BBDCAB55A5AE140B486273245342752', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 3, NULL, NULL, 2, NULL, 0, 'Add marshmallows and continue to heat, stirring often, until about 75% melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3047E1BECE1AE84CB18CA28B7DCFAAD1', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 4, NULL, NULL, 2, NULL, 0, 'Remove from the heat and continue to stir vigorously until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'298702BB3CD1E242BBA1CF0AB2BD1AB4', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 5, NULL, NULL, 2, NULL, 0, 'Pour the melted marshmallows over the cereal and stir to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F47458460449984880255F04B6480DAE', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 6, NULL, NULL, 2, NULL, 0, 'Spread into an even layer in the pan and set aside to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA8ADA5A5E48774B9938E088C37F6CD7', X'E6CBFABA5D3B7C449676CE8EFD7071A6', X'E6CBFABA5D3B7C449676CE8EFD7071A6', 7, NULL, NULL, 2, NULL, 0, 'Cut into twelve 2-inch by 3-inch rectangles, saving the scraps to fill in any craters on the rectangle treats before enjoying as a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8661F0F755D0894880807D6BBB67BAC5', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 0, NULL, NULL, 2, NULL, 0, 'Insert the tip of a lollipop stick into the melted and tempered white chocolate or melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D74432714FA8244B13A90CB44D7135E', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 1, NULL, NULL, 2, NULL, 0, 'Then insert the stick into a cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78007CB5EC12C9439F6771511B91EF31', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCBC5A05B3C4024B9205D37C8B66F67D', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 3, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to harden for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4E4E380D6A9604B85AAA3388B185217', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 4, NULL, NULL, 2, NULL, 0, 'Fill a tall 3 inch diameter glass with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7029DB3D82F7ED40BCDAF26A856A4926', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 5, NULL, NULL, 2, NULL, 0, 'Dip a cereal treat into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1A4BCE212237A4BB1573CACE622D3E8', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 6, NULL, NULL, 2, NULL, 0, 'Lift it out and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15808E6197A17E4FAC7B33DE021CF356', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 7, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B2745FEE622454C826696E864BA6E71', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 8, NULL, NULL, 2, NULL, 0, 'If using pure white chocolate allow the chocolate to dry at room temperature or pop in the refrigerator for about 5 minutes. If using confectionery coating, pop in the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F66B125E980D494882CC2C688BCE7949', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 9, NULL, NULL, 2, NULL, 0, 'Repeat, dipping all 12 rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CCCFEF06C53DB4E9BCEF221BDA13519', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 10, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of the gold modeling chocolate and roll into pointed logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EF21552DABB9B48863DC439BD6DAE5C', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 11, NULL, NULL, 2, NULL, 0, 'Twist two logs together to create a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF79BFF9490F6543967680253417210E', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 12, NULL, NULL, 2, NULL, 0, 'If desired, brush the horns with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8763D5DD90E924BBB47C60B077EA878', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 13, NULL, NULL, 2, NULL, 0, 'Roll out the white modeling chocolate to about 1/4 inch thickness and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55512987039B0A44A4ED20EB53E5F17B', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 14, NULL, NULL, 2, NULL, 0, 'Roll out the pink modeling chocolate really thin and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'551E03F2B0A6C14EBF046869AEFDFC7F', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 15, NULL, NULL, 2, NULL, 0, 'Use water to attach the inner pink ears (triangles) to the outer white ears (triangles).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFA40D1A88EDE745BC5CB903FB46427B', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 16, NULL, NULL, 2, NULL, 0, 'Dust a silicone rose mold with corn starch.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03D4D60B7E85E2429F1FF247176BF13C', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 17, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of your colored modeling chocolate and press them into the mold creating various sized roses.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E11B916F810BA48AD2B04A37F23BA7C', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 18, NULL, NULL, 2, NULL, 0, 'Attach one horn to the top of each of the rice krispie treats and attach two ears on either side using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FE1060CEA72314A8EC7B537A766BF72', X'AC559CAFE03E9C408D6CC63D05BFF27C', X'AC559CAFE03E9C408D6CC63D05BFF27C', 19, NULL, NULL, 2, NULL, 0, 'Then attach 5 flowers on the front, top edge, of each treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5A3F77EADF0514F83D1C564B1A80BBA', X'E6CF67AA2CE35F428BCA9ED783AD3F68', X'E6CF67AA2CE35F428BCA9ED783AD3F68', 0, NULL, NULL, 2, NULL, 0, 'Combine cake mix, pudding mix, water, oil,egg whites, and sour cream in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40C550C04E91C349930A1F0F91D5D350', X'E6CF67AA2CE35F428BCA9ED783AD3F68', X'E6CF67AA2CE35F428BCA9ED783AD3F68', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds then increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41A75F6DA8E4674C99A4D7D626CA7ED3', X'E6CF67AA2CE35F428BCA9ED783AD3F68', X'E6CF67AA2CE35F428BCA9ED783AD3F68', 2, NULL, NULL, 2, NULL, 0, 'Equally divide cake batter among 24 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'522D24B1F2E8D64BAA3597AB75FC9EAB', X'E6CF67AA2CE35F428BCA9ED783AD3F68', X'E6CF67AA2CE35F428BCA9ED783AD3F68', 3, NULL, NULL, 2, NULL, 0, 'Bake for 15-18 minutes until the cakes spring back when you press in the center of a cupcake or a toothpick inserted into the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDB4F424DBA7FD41979CE3903E3562F0', X'E6CF67AA2CE35F428BCA9ED783AD3F68', X'E6CF67AA2CE35F428BCA9ED783AD3F68', 4, NULL, NULL, 2, NULL, 0, 'Let cool in pan for 10 minutes then remove and set on a cooling rack and cool for another 50 minutes before frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80B0826FFBD9164EA88630DB0E393CC3', X'05CE0275AD1ACD4BAB14AC6F50401F34', X'05CE0275AD1ACD4BAB14AC6F50401F34', 0, NULL, NULL, 2, NULL, 0, 'Pipe vanilla frosting fur all over cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'486DECE01BEDD348B1C9529D8184FCAE', X'05CE0275AD1ACD4BAB14AC6F50401F34', X'05CE0275AD1ACD4BAB14AC6F50401F34', 1, NULL, NULL, 2, NULL, 0, 'Pipe two swirls for the snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'198F5B03EEB8A24DB66290EC534440E8', X'05CE0275AD1ACD4BAB14AC6F50401F34', X'05CE0275AD1ACD4BAB14AC6F50401F34', 2, NULL, NULL, 2, NULL, 0, 'Attach one pink heart nose, six chocolate jimmies whiskers, and two candy eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED2F04BFB035694E871DC9AE6FAC6C84', X'05CE0275AD1ACD4BAB14AC6F50401F34', X'05CE0275AD1ACD4BAB14AC6F50401F34', 3, NULL, NULL, 2, NULL, 0, 'Break one red heart sprinkle into two pieces, so you have one oblong shaped tongue. Insert it under the cat''s snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A13534A6237C074B8A1966B4C49FFB8A', X'05CE0275AD1ACD4BAB14AC6F50401F34', X'05CE0275AD1ACD4BAB14AC6F50401F34', 4, NULL, NULL, 2, NULL, 0, 'Pipe two ears and attach an upside down pink heart to each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A060C6F6447854FA885C454DFD9CDB1', X'810C68FBB0355448ABB8F4254516B4F1', X'810C68FBB0355448ABB8F4254516B4F1', 0, NULL, NULL, 2, NULL, 0, 'Pipe spikes of chocolate fudge fur all over top of cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF2AEEB786DD3C42ADFC474165EADB89', X'810C68FBB0355448ABB8F4254516B4F1', X'810C68FBB0355448ABB8F4254516B4F1', 1, NULL, NULL, 2, NULL, 0, 'Pipe taller spikes of frosting to create a snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D724039C47F71C418E1998CC81FE9166', X'810C68FBB0355448ABB8F4254516B4F1', X'810C68FBB0355448ABB8F4254516B4F1', 2, NULL, NULL, 2, NULL, 0, 'Attach one black candy coated chocolate ball on the snout for the nose, add two candy eyes, and one red tongue.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D03C11FBE5160443A78D79C7D93F8F56', X'810C68FBB0355448ABB8F4254516B4F1', X'810C68FBB0355448ABB8F4254516B4F1', 3, NULL, NULL, 2, NULL, 0, 'Pipe on two big floppy chocolate fudge frosting ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63EE1573E2E3B2418ECE76F000DC5F1C', X'36FF93CF4E106F419C7859F27FF01714', X'36FF93CF4E106F419C7859F27FF01714', 0, NULL, NULL, 2, NULL, 0, 'Pour white Cannellini Beans, roasted red peppers, basil pesto, and goat cheese in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74E42FD5000A1C46AECBEBBA5C8D4F08', X'36FF93CF4E106F419C7859F27FF01714', X'36FF93CF4E106F419C7859F27FF01714', 1, NULL, NULL, 2, NULL, 0, 'Pulse until fairly smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CE7C28D02C1EC45856F0600E0673208', X'36FF93CF4E106F419C7859F27FF01714', X'36FF93CF4E106F419C7859F27FF01714', 2, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0F9BD884634494F83E8E6EC21CDC68B', X'36FF93CF4E106F419C7859F27FF01714', X'36FF93CF4E106F419C7859F27FF01714', 3, NULL, NULL, 2, NULL, 0, 'Pour into a bowl and serve with chips, crackers, or vegetables.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26E9A0CE40B4CB45AE7F8B87B092B04A', X'7DE86FB0285B784E97A3547F427A4C74', X'7DE86FB0285B784E97A3547F427A4C74', 0, NULL, NULL, 2, NULL, 0, 'Heat 2 cups vegetable oil in a 12 inch skillet set over medium high heat until it reaches 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'986C9F6FBD817742AFA8304173C94080', X'7DE86FB0285B784E97A3547F427A4C74', X'7DE86FB0285B784E97A3547F427A4C74', 1, NULL, NULL, 2, NULL, 0, 'Add 6-8 won ton triangles to the hot oil, at a time, frying them, flipping them over as needed, until they lightly brown on both sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67EA28D5A05A3B429B9BE017571E386C', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a 12 inch skillet over high heat until it just begins to smoke.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D0B38B5D5AE6C4DA155FEC2313B1C28', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 1, NULL, NULL, 2, NULL, 0, 'Toss the shrimp with salt and sugar and pour into the hot oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75B2A245DCAE07419C2DAAFEFF6335C1', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 2, NULL, NULL, 2, NULL, 0, 'Spread shrimp out into a single layer and cook until browned on the bottom, about 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5171208A3284B4FADD7E9ADCA6F6CA6', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 3, NULL, NULL, 2, NULL, 0, 'Remove the skillet from the heat and flip over each of the shrimp.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DDBEDF734A4BB43B5FC9865856CA99E', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 4, NULL, NULL, 2, NULL, 0, 'Return the skillet to the heat and allow shrimp to cook for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3BF77CEC713FA4199DC21A4CF723AA3', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 5, NULL, NULL, 2, NULL, 0, 'Pour the teriyaki sauce into the pan and stir to evenly coat shrimp.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A20E716D50DBC45A35D88E2BE13173D', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 6, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and cook, stirring as needed, for about one more minute until the shrimp is cooked through and is well coated with the sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FDCE79554B96E4D8EC2ECF5FD7C5649', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle on sesame seeds, stir, then remove from heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65955F66D50FD94C94F45D9FAC231066', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 8, NULL, NULL, 2, NULL, 0, 'Combine softened cream cheese, Worcestershire sauce, lemon juice, and garlic in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11479CF846122A4B85DF5B72C2F0A3E0', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 9, NULL, NULL, 2, NULL, 0, 'Reserve a tablespoon or so of the green onions for garnish, then add the rest to the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'331AF7337F6AEC44905DB084B0E43D63', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 10, NULL, NULL, 2, NULL, 0, 'Stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EDD541C4CF82649BB66519A005AFE05', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 11, NULL, NULL, 2, NULL, 0, 'Spread into a 9 inch round pie plate or 1 inch tall dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59AEF80D757D914CACD0389EB0666B9A', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 12, NULL, NULL, 2, NULL, 0, 'Spoon the shrimp over the dip, garnish with the reserved green onions, and serve with crispy won ton chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E29EFE1D3B02C648B9A11924D9EE81C1', X'FF194A0C6A6554419084CFB54C6CD1DB', X'FF194A0C6A6554419084CFB54C6CD1DB', 13, NULL, NULL, 2, NULL, 0, 'If you prefer this dip may be served hot. Heat the cream cheese mixture in a 350 degree F oven for about 20 minutes then cook the shrimp and spread over top of the baked dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51B15D62748D3D44B9F1D99D8A8CC635', X'47D05ECC01238A48BEECA03FCA105C2B', X'47D05ECC01238A48BEECA03FCA105C2B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13B9D7E7FA80164D9FC499CC38C972DB', X'47D05ECC01238A48BEECA03FCA105C2B', X'47D05ECC01238A48BEECA03FCA105C2B', 1, NULL, NULL, 2, NULL, 0, 'Spray 8 glass custard cups with non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D0F5A195A29CF46929A746C5320EA02', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB3967DA97A13F4BAD7B3E3261EC3622', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 1, NULL, NULL, 2, NULL, 0, 'Wet 2 paper towels with water then ring out excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADAA1847AECFB946838A0ADE335712A5', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 2, NULL, NULL, 2, NULL, 0, 'Set 8 corn tortillas in between the two paper towels.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DCD454D20D2C64DABDECB97ABBC61FC', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 3, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high for 30-45 seconds, just to soften the tortillas.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCB4D60C2F65924AA6164E4F39992741', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 4, NULL, NULL, 2, NULL, 0, 'Brush both sides of the corn tortillas with oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53BA85DF4674BE4F81655ADC2795A36D', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 5, NULL, NULL, 2, NULL, 0, 'Press into a bowl shape in the custard cups, crimping as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AE1DE1C27B6C544ACA0B157789976C0', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 6, NULL, NULL, 2, NULL, 0, 'Bake for 6 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'162FF91BFC47704D94C349DED333455B', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 7, NULL, NULL, 2, NULL, 0, 'Remove tortillas from custard cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'946FDF72170BC643BA03892A6BEE0F8C', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 8, NULL, NULL, 2, NULL, 0, 'Turn them upside down on a baking sheet and bake for 2-4 more minutes until the bottoms lightly brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E7574FCA1912D448F3F19E07B57F386', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 9, NULL, NULL, 2, NULL, 0, 'Remove and set right side up on baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29FD219BDC472A4FBEA90A3FDC0C2303', X'1981DC765A18EE40AEFA3760E8B7B27A', X'1981DC765A18EE40AEFA3760E8B7B27A', 10, NULL, NULL, 2, NULL, 0, 'Reduce oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35BD682FA91AD54A87815CB43F8F36B8', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a skillet set over medium high heat just until shimmering.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69E65A7043F8D1438658825B3AA89D48', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 1, NULL, NULL, 2, NULL, 0, 'Add shallot and stir occasionally until light golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC825566CFB54A4F84CAF110C0548F52', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 2, NULL, NULL, 2, NULL, 0, 'Stir together garlic, chili powder, coriander, cumin, salt, and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C326C7B126E464F9F8D079B06C7B858', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 3, NULL, NULL, 2, NULL, 0, 'Add to cooked shallots and stir constantly for about 30 seconds, until fragrant.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6141F2800147A0429F4BB770865C4705', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 4, NULL, NULL, 2, NULL, 0, 'Add chicken pieces and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EEBAC933F83AB4CB5786CC03AFFEBE0', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 5, NULL, NULL, 2, NULL, 0, 'Let cook, stirring occasionally, for 1 minute, then pour in the tomato sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FCCB6369F3A7245891B09B642E729AA', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 6, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then reduce heat to low and cook for 14 minutes, stirring as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D8310C18075284BBFB20899B3EA4BB9', X'3EAE1AD70ADF974DA930428B2D2B29D9', X'3EAE1AD70ADF974DA930428B2D2B29D9', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat and set aside until ready to assemble enchilada bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'407EB3F317B3F844B9B2242F9ECD1B8E', X'E7662C60A56DAC4492C8E280D5FFA814', X'E7662C60A56DAC4492C8E280D5FFA814', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in skillet set over medium high heat until shimmering.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4833B148141E084F8DB7BCF01620B716', X'E7662C60A56DAC4492C8E280D5FFA814', X'E7662C60A56DAC4492C8E280D5FFA814', 1, NULL, NULL, 2, NULL, 0, 'Add chopped poblano pepper and shallot and cook until peppers begin to blister and soften.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63D2F94D66996140B4D256F174F77DF6', X'E7662C60A56DAC4492C8E280D5FFA814', X'E7662C60A56DAC4492C8E280D5FFA814', 2, NULL, NULL, 2, NULL, 0, 'Toss in garlic and corn and cook for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44358CF106CF0144AC21F2162169406F', X'E7662C60A56DAC4492C8E280D5FFA814', X'E7662C60A56DAC4492C8E280D5FFA814', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4C156893695984CA53300BBEC4A39C1', X'F50D17A4E8BBDF46BF614A4FB88C44D8', X'F50D17A4E8BBDF46BF614A4FB88C44D8', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EE29CCCDF74D24FAB99624D37C35AD0', X'F50D17A4E8BBDF46BF614A4FB88C44D8', X'F50D17A4E8BBDF46BF614A4FB88C44D8', 1, NULL, NULL, 2, NULL, 0, 'Fill each corn tortilla bowl with about a tablespoon of the cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF12D766ADCE06498E3F9A1B1068002E', X'F50D17A4E8BBDF46BF614A4FB88C44D8', X'F50D17A4E8BBDF46BF614A4FB88C44D8', 2, NULL, NULL, 2, NULL, 0, 'Stir about 2/3 cup of the cheese into the chicken then equally divide it among the corn tortilla bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'035FA20BB15783428A6A1ADB924481AF', X'F50D17A4E8BBDF46BF614A4FB88C44D8', X'F50D17A4E8BBDF46BF614A4FB88C44D8', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on Poblano pepper and corn mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65C4139C9DB41842B4A3D01110A3BEFE', X'F50D17A4E8BBDF46BF614A4FB88C44D8', X'F50D17A4E8BBDF46BF614A4FB88C44D8', 4, NULL, NULL, 2, NULL, 0, 'Top with the remaining cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'600BD718208BB948803F253D252BCEB6', X'F50D17A4E8BBDF46BF614A4FB88C44D8', X'F50D17A4E8BBDF46BF614A4FB88C44D8', 5, NULL, NULL, 2, NULL, 0, 'Bake for 8 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87B422819AB0F2499CA761079A615EC2', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5907813D6FC8184FA02754BB8BD19D42', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 1, NULL, NULL, 2, NULL, 0, 'Unroll pizza dough onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C52A3C825DBA844AE1FCA47CB5263C4', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 2, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4E87141CD29184A8E417F89F309551C', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 3, NULL, NULL, 2, NULL, 0, 'Allow crust to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD6F8684C92CE24C96662126BD351C7D', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 4, NULL, NULL, 2, NULL, 0, 'Cut out using the Fish Template.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CE1421F4CA10340984BE511BBCEE8CB', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 5, NULL, NULL, 2, NULL, 0, 'Spread veggie cream cheese over fish shaped crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A744CCB12EB494DBDAD8CE81AEFD5FC', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 6, NULL, NULL, 2, NULL, 0, 'Finely slice carrots.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEB3E45C8F97E34AAC145407F4E233DD', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 7, NULL, NULL, 2, NULL, 0, 'Arrange carrots to form scales on the body of the fish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2C9464E2830A64F9E269D740740E275', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 8, NULL, NULL, 2, NULL, 0, 'Cut red peppers into thin strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B699D55F64709A4FA4ABC0B80BD1F279', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 9, NULL, NULL, 2, NULL, 0, 'Arrange peppers over fins and cut off excess using kitchen shears.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF87E43C4FFE2941A7B8D42433096B1F', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 10, NULL, NULL, 2, NULL, 0, 'Finely chop yellow pepper and sprinkle over face of fish.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9672EE95C10EA84981DE7B4819B51993', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 11, NULL, NULL, 2, NULL, 0, 'Cut 2 large ovals from the white cheese or cream cheese. You can also pipe the cream cheese on, if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96990A509030FF48901957CDADEBD1A4', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 12, NULL, NULL, 2, NULL, 0, 'Cut one jumbo olive in half and set one piece on each white oval creating eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F16CCBD641865C4F881A3292FC76F070', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 13, NULL, NULL, 2, NULL, 0, 'Set eyes on fish''s face.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'525491E814084C4BB63D358C353B990A', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 14, NULL, NULL, 2, NULL, 0, 'Cut two thin slivers from the other black olive to create eyebrows over the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B89FF5A768B0B4393ACA76123C6F3DA', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 15, NULL, NULL, 2, NULL, 0, 'Cut the stem from the spinach leaf or cut a green onion into a thin strip and create a mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA27ED8E229E2A41B4A9F8A9409B0DE4', X'3ABBA6D768414247A8E32432D96A3B2A', X'3ABBA6D768414247A8E32432D96A3B2A', 16, NULL, NULL, 2, NULL, 0, 'Refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0415D27F749EEE4BB0CF048272A3D519', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'936960568A05E04CBF48F0DB7D2F000D', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 1, NULL, NULL, 2, NULL, 0, 'Stir together grated Parmesan Cheese, flour, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'858A5C535241EF478FF8575A3EF6B3A3', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 2, NULL, NULL, 2, NULL, 0, 'Whisk together milk, egg, and sour cream');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C7FF00023350F4BB6B121553F9AF6E7', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 3, NULL, NULL, 2, NULL, 0, 'Combine wet and dry ingredients, add melted butter, and stir until a ball forms.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4181FA5EF9C02C498FB76262660EB943', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 4, NULL, NULL, 2, NULL, 0, 'Grease a 9x5 loaf pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5079FB616D121840A9B4968D6E5174BC', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle 1/2 of the shredded Parmesan cheese over the bottom of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A797FF961F5A4245AD347868FA395939', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 6, NULL, NULL, 2, NULL, 0, 'Layer bread dough with rows of pepperoni.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98C316DB7CF14148ADE6479471C7E3FA', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle on remaining cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'613900976BC4C84FB897945F49552C45', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 8, NULL, NULL, 2, NULL, 0, 'Bake for 55-65 minutes until golden brown on top and toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AB166BB822A31439239AC879279EDBA', X'7A17CC066D63054DBDA0E834819CE848', X'7A17CC066D63054DBDA0E834819CE848', 9, NULL, NULL, 2, NULL, 0, 'Cool for 10 minutes in pan, remove, then cool completely before slicing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B72853FDCFBE9340864DAECDEA7B61A9', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 0, NULL, NULL, 2, NULL, 0, 'Cut Twix Bites in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B47805418987AB46BF7E2C282ADB6941', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 1, NULL, NULL, 2, NULL, 0, 'Pop the Orville Redenbacher''s Naturals Simply Salt Popcorn according to package instructions. Remove un-popped kernals and pour popcorn into a very large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E11034C1CBCD664BB7AF485F6537D08B', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 2, NULL, NULL, 2, NULL, 0, 'Melt white confectionery coating. Heat it in the microwave on high power for 1 minute, then stir, and heat on high for 30 seconds. Then allow the candy coating to sit in the microwave for 1-2 minutes. Remove bowl and stir. If wafers are not completely melted, heat on high for 10 second increments, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E863A0C86A33104F9F1165A13CACC01E', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 3, NULL, NULL, 2, NULL, 0, 'Heat dark cocoa confectionery coating in microwave on high for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C286C88F5E895E4BA202FEB7B1ECA6AF', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 4, NULL, NULL, 2, NULL, 0, 'Pour peanut butter chips into bowl of melted white candy coating. Stir, and wait 1 minute, then gently stir again. The chips will melt slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1726ECCC97B34048A55EAF6009F8775E', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 5, NULL, NULL, 2, NULL, 0, 'Pour over the popcorn. Toss to stir until all the popcorn is coated. Toss in the Twix Bites.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50201E9337543F4EB307A5A301547019', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 6, NULL, NULL, 2, NULL, 0, 'Pour popcorn onto a parchment paper lined baking sheet. Spread into a thin layer. Drizzle with the melted dark confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D8E0DCDC1A5B44F9645BCA097475E0E', X'73603D2ED79E13419E9ECCA1DC4F9F86', X'73603D2ED79E13419E9ECCA1DC4F9F86', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes or refrigerate for 15 minutes. Break into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DC1EADD93CABC4E97CB1B4D9D2F91B2', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 0, NULL, NULL, 2, NULL, 0, 'Crumble cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB44FBA3B44223499BE65BFBFAA77025', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 1, NULL, NULL, 2, NULL, 0, 'Mix in frosting, adding just enough so that the cake balls hold together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7CEF5FCAF467347A191EC0DA4DE94D2', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 2, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoonfuls of cake ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F74A55BA6D94941B5D7DD044F914459', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 3, NULL, NULL, 2, NULL, 0, 'Wrap each spoonful around maraschino cherries.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EFD0A72CA3C724A98AEDF2434C957DB', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 4, NULL, NULL, 2, NULL, 0, 'Roll into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F16A7206F322484CA387A5DF7147A387', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 5, NULL, NULL, 2, NULL, 0, 'Melt Cordial Cherry Candy Melts');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B7091D6D24D8741A938F73DC0BFC784', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 6, NULL, NULL, 2, NULL, 0, 'Dip cake balls into candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0570411FF7B0C8418A151E32389E57D3', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 7, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes, until the candy coating is set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAAE05C61C8FE04DBB2C825D57649914', X'FDC3E528726EE8448A6A33CD683D4453', X'FDC3E528726EE8448A6A33CD683D4453', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to 5 days.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38DBC8638041A34D845143AADA22A2C7', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 0, NULL, NULL, 2, NULL, 0, 'Cut fruit flavored gumdrops into small 1/4 inch pieces, toss in sugar, then pour into a colander to remove excess sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B715E96181AA34AA2106AA0214195EC', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 1, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90CA0762B9C3BB4BA1C3C8C85D09596B', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 2, NULL, NULL, 2, NULL, 0, 'Cream the Country Crock Unsalted Baking Sticks and sugar together until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6232F01D42CFA4788F0BCB8F18FBD7F', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE0B2EF75A35BC4C8A496F61260D4802', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together flour, salt, baking powder and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'399860302C246949963B5662765360F1', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 5, NULL, NULL, 2, NULL, 0, 'Stir the dry ingredients into the wet mixture just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD6C9BFA547DD240859CA363CC79CA72', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 6, NULL, NULL, 2, NULL, 0, 'Mix in about half of the gumdrop pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5217C2305EE2B949819862CE2C98FA8F', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 7, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 tablespoons of dough onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A11D38E7E772AB4ABA43BEB863CB3AEE', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 8, NULL, NULL, 2, NULL, 0, 'Flatten cookies and top them with more gumdrops.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C90EC49761A9D48BB6117FFDBD555D9', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 9, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes, just until the tops of the cookies start to look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E70F134A5AB6684281214A462A81BA60', X'590D5F4B20186640AEAAB3076660C0A4', X'590D5F4B20186640AEAAB3076660C0A4', 10, NULL, NULL, 2, NULL, 0, 'Cool your cookies on baking sheet for 2-3 minutes, then remove and place on a wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7418171CB8B6047A4C06C0B493EFADD', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 0, NULL, NULL, 2, NULL, 0, 'Peel and dice potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B56F2A2AC7DDB4408558E1E4C6BEF5E0', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 1, NULL, NULL, 2, NULL, 0, 'Rinse with cold water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA2EB41530711049AA444E5ADBCB1C82', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 2, NULL, NULL, 2, NULL, 0, 'Place in a small saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A61155C421CDC446B455418E8FDD2EBB', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 3, NULL, NULL, 2, NULL, 0, 'Pour water over potatoes so that the water goes 1 inch above the potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'971A75E4EC6FDA468E9E8ED55598A51F', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 4, NULL, NULL, 2, NULL, 0, 'Add the white vinegar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D524E2FD4336AD43A7702BFA7F843111', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 5, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat then lower heat to medium and continue to cook for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'003869AA41CCA343A0B9D4E8B26FEB84', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and drain off water.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29F0B72E215E2B4BB5386207BBD17B07', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 7, NULL, NULL, 2, NULL, 0, 'Pour out onto paper towels and pat dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5600A7DDB8191B49B5698A0B66150A18', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 8, NULL, NULL, 2, NULL, 0, 'Heat oil in a 12 inch skillet over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35B8682C4D2AF542B58052C0B3F064D6', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 9, NULL, NULL, 2, NULL, 0, 'Once the oil begins to shimmer, toss in the potatoes and onions, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBA0E82F13F28948A9E15F79DDB7DA18', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 10, NULL, NULL, 2, NULL, 0, 'Cook until they begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94A44F019283E04A8ECDBF11F2E1A3B2', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 11, NULL, NULL, 2, NULL, 0, 'Add the peppers, sausage, and a pinch of season salt and cook until heated through and lightly browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5FCEEC92F3E75469A984C4FCCD249F1', X'D8D5B962AF4E144C86A90AD2712FA6DF', X'D8D5B962AF4E144C86A90AD2712FA6DF', 12, NULL, NULL, 2, NULL, 0, 'Spread onto flour tortillas, top with sour cream if using, roll up and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A31A1FA0060ABA4488BC01035BBC9DC5', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29D0A29E5BFA2F44B77FB8AD21180719', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 1, NULL, NULL, 2, NULL, 0, 'Cream butter, brown sugar, granulated sugar and peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10487CF278380B43A254751035550AFE', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 2, NULL, NULL, 2, NULL, 0, 'Add egg and stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33DBEB77D4DA4F45AD1E2B2B6CC40408', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 3, NULL, NULL, 2, NULL, 0, 'Stir in the flour, baking soda, baking powder, and salt just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB6634BADA90544AA539712855C9F6FB', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 1/4 cup portions of dough and set on parchment paper lined cookie sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8C2A73B4F56164F96D84779116F4DB3', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 5, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until the edges begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'438BE992D85B9B43B80B804CDADC4CD1', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 6, NULL, NULL, 2, NULL, 0, 'The cookies will flatten a bit more as they cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C718D8B8C2C784795F9FBFF32657C07', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 7, NULL, NULL, 2, NULL, 0, 'Let cool completely on the cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F3366F7AEF034DAE761BB367A94379', X'1498D501CE3A22489D36352E8EFDCF13', X'1498D501CE3A22489D36352E8EFDCF13', 8, NULL, NULL, 2, NULL, 0, 'Remove and create an ice cream sundae on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4218ACEE208FBF4FADF693A7E1FE4480', X'7E326F8596B2B1448449D99124518702', X'7E326F8596B2B1448449D99124518702', 0, NULL, NULL, 2, NULL, 0, 'Heat hot fudge according to instructions on jar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F3F2D067CA8C84082E47C5C192D118B', X'7E326F8596B2B1448449D99124518702', X'7E326F8596B2B1448449D99124518702', 1, NULL, NULL, 2, NULL, 0, 'Scoop Blue Bunny® PB ''N Cones Ice Cream onto each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDF437C3B4735045AB7CDECE45FA5771', X'7E326F8596B2B1448449D99124518702', X'7E326F8596B2B1448449D99124518702', 2, NULL, NULL, 2, NULL, 0, 'Drizzle hot fudge and melted peanut butter over each scoop of ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAFF127816B04E41BB6A1F511027E6D7', X'7E326F8596B2B1448449D99124518702', X'7E326F8596B2B1448449D99124518702', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on some chopped peanuts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'871D30CF3DFBB749BEE525F9276E3669', X'7E326F8596B2B1448449D99124518702', X'7E326F8596B2B1448449D99124518702', 4, NULL, NULL, 2, NULL, 0, 'Add a swirl of whipped topping.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA57FC678506F6408F61F53C86DC5BA5', X'7E326F8596B2B1448449D99124518702', X'7E326F8596B2B1448449D99124518702', 5, NULL, NULL, 2, NULL, 0, 'Top with a maraschino cherry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04E6DD348FF03141BEBC8905591D26C1', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 0, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of creamy peanut butter on half of the RITZ Crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3CE25C023CD0E4EB72955FE1C981BB5', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 1, NULL, NULL, 2, NULL, 0, 'Sandwich two crackers together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63130E03F3EE4449B71DEC7716A4E876', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 2, NULL, NULL, 2, NULL, 0, 'Set aside 10 of the cracker sandwiches to use for Rudolph''s antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF4354F9BE4B584EAFED89FD4DFCFB0E', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 3, NULL, NULL, 2, NULL, 0, 'Dip the remaining cracker sandwiches in chocolate and chill until set. Use the refrigerator if using pure chocolate and the freezer if using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03FEF5B0B639CD41B38B76BB914CE158', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 4, NULL, NULL, 2, NULL, 0, 'Create a column of 6 chocolate dipped RITZ Crackers down the center of your serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B62D144F1F7704F9E5C39F0F3ACB7C9', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 5, NULL, NULL, 2, NULL, 0, 'Arrange 1 column of 6 chocolate dipped crackers on either side of the middle column.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FD5A9A4E34FCB43B9D8073681D4CBBA', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 6, NULL, NULL, 2, NULL, 0, 'Then arrange one column of 4 chocolate dipped crackers on either side of those so that the top and bottom rows have three chocolate dipped crackers in them.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13DF344836EEB04CBB5A1952FC8EE6CA', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 7, NULL, NULL, 2, NULL, 0, 'Arrange three of the plain peanut butter crackers above column number 2 and again above column number 4 then arrange two crackers on either side to finish the antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8617808A04E2A459FE5174AD3E467FE', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 8, NULL, NULL, 2, NULL, 0, 'Roll out the three colors of candy clay to about 1/16th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4C06240E2A66B448C62E8F30213D496', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 9, NULL, NULL, 2, NULL, 0, 'Cut two white circles using a 2 1/4 inch round cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7F1198E8C228543884ED6DC5099A3AF', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 10, NULL, NULL, 2, NULL, 0, 'Cut two black circles using a 1 1/4 inch round cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8FD12FC84B2A84D87F1FA8AF1557600', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 11, NULL, NULL, 2, NULL, 0, 'Cut one red circle using a 3 inch round cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EA2A48F45336E42B1185EA16C61C2B8', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 12, NULL, NULL, 2, NULL, 0, 'Brush a bit of water on the back side of a black circle and press it onto a white circle creating one eye. Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28A37E26D5BF174795B6BB9D84DA5D4D', X'9D726FE41F6F1A43BC68E594AF0E1B40', X'9D726FE41F6F1A43BC68E594AF0E1B40', 13, NULL, NULL, 2, NULL, 0, 'Set the red nose and two eyes on the chocolate dipped crackers creating Rudolph''s eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'886D234CBCF25C4EA98DB221F96A9530', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D1E17AB325A8B4F9CDF3D55B24DE9BE', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91A28CEBAB3361478CAB35DC0C8C2FAF', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 2, NULL, NULL, 2, NULL, 0, 'Spray a 9 x 5 inch loaf pan with baking spray then wipe out excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14714511DB52E544AAD27FE8E5C11001', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 3, NULL, NULL, 2, NULL, 0, 'Whisk the flour and salt together and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D186C47EC65ED246A418215618213A88', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 4, NULL, NULL, 2, NULL, 0, 'Stir together the cocoa powder and chocolate in a medium microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2294AFEA4858747881670C61C35DA30', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 5, NULL, NULL, 2, NULL, 0, 'Pour Hazelnut and Vanilla flavored creamers into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22E6D2902717814EB8BBB769D519D0E5', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 6, NULL, NULL, 2, NULL, 0, 'Heat on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2ADB1AB4F3052449948E5EFCF86A06EA', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 7, NULL, NULL, 2, NULL, 0, 'Pour over top of the chocolate and stir until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'395D4A6E596CC9459094B9EFA7DE994D', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 8, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds on high in the microwave then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DDEB870EF308B4587DB8B303F629CDA', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 9, NULL, NULL, 2, NULL, 0, 'Let cool for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C37A9E4B95C03C43AE908F31DAAF6116', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 10, NULL, NULL, 2, NULL, 0, 'Pour cooled chocolate, softened butter, granulated sugar, and brown sugar into the bowl of a stand mixer or large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B0065D7E5C64F4191814A3AFC87E3E5', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 11, NULL, NULL, 2, NULL, 0, 'Beat using a paddle attachment for 2-3 minutes until it becomes lighter in color and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1766476723E366479C7D2335A7F716C8', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 12, NULL, NULL, 2, NULL, 0, 'Continue mixing, adding one egg at a time, until each incorporates into the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'939C8180608A2E448FB7D1090F2F39D9', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 13, NULL, NULL, 2, NULL, 0, 'Turn the mixer to low and add the flour and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B651881D0DA5DA419DACAD822C9B9414', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 14, NULL, NULL, 2, NULL, 0, 'Mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F094FFBA779FA54CB6990ED6C993885B', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 15, NULL, NULL, 2, NULL, 0, 'Pour into loaf pan and smooth out the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31ECF947D588114192AA068B3B50C1C2', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 16, NULL, NULL, 2, NULL, 0, 'Bake for 55-65 minutes until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4289C868F650C540BE0B7B019A060D16', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 17, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool in pan for 10 minutes before inverting onto a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84D99E6911B9464A8FB9C888E0EB8FC9', X'5F40C6C017F9D145BE4DF84AF86A2986', X'5F40C6C017F9D145BE4DF84AF86A2986', 18, NULL, NULL, 2, NULL, 0, 'Cool completely before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20541AB612885B4E84017724D265272E', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 0, NULL, NULL, 2, NULL, 0, 'Stir together the brown sugar and cornstarch in a small saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB416465E3294A49AB127C0E4BB8AC21', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 1, NULL, NULL, 2, NULL, 0, 'Pour in the pineapple juice, water, vinegar, and soy sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F1088752086774AA1C315586721BFBE', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 2, NULL, NULL, 2, NULL, 0, 'Stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86FC7C1627C86B4C9DE84EE9B6CA0F44', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 3, NULL, NULL, 2, NULL, 0, 'Heat over medium heat, stirring constantly until thickened, 1-2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A17298BE1206FB46ABCC43544EB8DC6F', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 4, NULL, NULL, 2, NULL, 0, 'Remove from heat and let cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAA940D9E3204D47BCA3F867EEA93ED6', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 5, NULL, NULL, 2, NULL, 0, 'Stir in lemon juice to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3770D9D04E004C4188ABD3D0FD96BC70', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 6, NULL, NULL, 2, NULL, 0, 'Add cream cheese and puree in a blender or food processor until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DB5B64E7324F24DB6474795CC997505', X'4B5562AF439F524A85198DA7AAA504D9', X'4B5562AF439F524A85198DA7AAA504D9', 7, NULL, NULL, 2, NULL, 0, 'Serve with Egg Roll Monkeys');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CB5C1F1732AD647903728E3D1730547', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'152FE2B813A3C843952CF1FA2FE169D2', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 1, NULL, NULL, 2, NULL, 0, 'Line two 9x13 baking pans with non-stick foil and spray with baking spray, or spray the pans, line with parchment paper, spray again, and dust with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'430E54C0B6823A47B016BEECE84E0B9F', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 2, NULL, NULL, 2, NULL, 0, 'Pour Coca-Cola into a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6DCB32EDCA9B9F4FBE52864947313847', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 3, NULL, NULL, 2, NULL, 0, 'Heat over medium heat, stirring often, until thickened and reduced to about 2 1/2 tablespoons, about 30 minutes. Watch it near the end so it doesn''t burn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3F6EB8417E2954EA9BBD6AF69F67003', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 4, NULL, NULL, 2, NULL, 0, 'Reserve 1 teaspoon of the Coke syrup for the filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEF297B03F81634D87897C196C9055E1', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 5, NULL, NULL, 2, NULL, 0, 'Pour the remaining syrup in a microwave safe bowl along with the chocolate and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC908A8F9D3E664DBA81B85FDC66E883', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 6, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9692F6B86BD11E48822D65D1B41EA1EE', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 7, NULL, NULL, 2, NULL, 0, 'Stir then let rest for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C345BF54BB81B6468D2E58A65D6AC463', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 8, NULL, NULL, 2, NULL, 0, 'Stir again until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'144146B75CF26245830D0AB544A61267', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 9, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E54E316DF721C6468C8C0E811BB41BA3', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 10, NULL, NULL, 2, NULL, 0, 'Beat egg yolks using an electric mixer set on medium high speed until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D859E0ED055B0459DA72265746D5226', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 11, NULL, NULL, 2, NULL, 0, 'With the beaters running, sprinkle in about 3 tablespoons of the sugar and continue to beat until the eggs become pale yellow and thick enough that it falls in a thick ribbon when the beaters are lifted out of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'308243C1B27DB74A85F4CEE31E44FA26', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 12, NULL, NULL, 2, NULL, 0, 'Fold the egg yolks into the melted chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'124401E99E17414EB7173B408335E185', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 13, NULL, NULL, 2, NULL, 0, 'Sift together the flour, cocoa powder, and salt and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A82D6525F59C944EA3727D76982D71AD', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 14, NULL, NULL, 2, NULL, 0, 'In a clean bowl with clean beaters whip the egg whites and cream of tarter until foamy. Add 1 teaspoon of the sugar and continue to beat to soft peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB8829FD795D114998F8DE410CA2FB5C', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 15, NULL, NULL, 2, NULL, 0, 'Continue beating and sprinkle in the remaining sugar until the whites become glossy and hold stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3E75C7836FE224CB0524FC2ACA19849', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 16, NULL, NULL, 2, NULL, 0, 'Don''t over beat until dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BC776C3CBF3974283CF40CBF1C98FDA', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 17, NULL, NULL, 2, NULL, 0, 'Fold 1/3rd of the egg whites into the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CC5C2530D93774D8D5D1B6A543F4814', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 18, NULL, NULL, 2, NULL, 0, 'Then gently fold in the remaining egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'972A35A0A6744C4A98E43E48D944D756', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 19, NULL, NULL, 2, NULL, 0, 'Fold in the dry ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6965F7D57345F54383DB2F429B29585C', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 20, NULL, NULL, 2, NULL, 0, 'Equally divide the batter among the two cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1C389AB3F6A5B41AF40FF17572261F3', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 21, NULL, NULL, 2, NULL, 0, 'Bake for about 8 minutes, until the center of the cake springs back when touched.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2567B062836AC4182D4AFC68DF786E5', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 22, NULL, NULL, 2, NULL, 0, 'While the cakes are baking cut 8 strips of parchment paper that are at least 4 1/2 inches wide by 8 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C0E93D83C8BA24C8A5296F9AEE7E676', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 23, NULL, NULL, 2, NULL, 0, 'Set them out on your counter and dust them with cocoa powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBA4D15139DBB04B80B8EF09BE41B9FA', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 24, NULL, NULL, 2, NULL, 0, 'Remove the cakes from the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47AFF121069E5846AAED8AFD6E91082D', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 25, NULL, NULL, 2, NULL, 0, 'Let them sit for 3 minutes, then remove one from the pan. Cut into 4 equal rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A13AE14856FEEE409D006F02ECC6C6DF', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 26, NULL, NULL, 2, NULL, 0, 'Immediately, set one cake rectangle on each parchment paper strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B77D54219288247B90B3B9524A19E0A', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 27, NULL, NULL, 2, NULL, 0, 'Roll up the cakes and parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14C5DA3F507741489B72489C67CC12AC', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 28, NULL, NULL, 2, NULL, 0, 'Repeat with second cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8AC5B9BF4A53948AFC10231FC53419B', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 29, NULL, NULL, 2, NULL, 0, 'Allow cakes to cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C80F5824D2BB984CB1EADEBE549A61B5', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 30, NULL, NULL, 2, NULL, 0, 'Meanwhile make your filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0708A04F538BE94B93462D855C063015', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 31, NULL, NULL, 2, NULL, 0, 'Combine the shortening, powdered sugar, marshmallow fluff, and whipping cream in a large mixing bowl and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53F34BFB73B7B3498A2B830874B55EBF', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 32, NULL, NULL, 2, NULL, 0, 'Stir in the reserved Coca-Cola syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5990FBE8ECB58243A5BC83E91B39B645', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 33, NULL, NULL, 2, NULL, 0, 'When cakes are ready, carefully unroll them and remove the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'470BA53AF63BC34D9B650B5A5331AA11', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 34, NULL, NULL, 2, NULL, 0, 'Pipe or spread filling on each cake leaving a border around the edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CAEC399FE364045BD2DF62DBE8F44C8', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 35, NULL, NULL, 2, NULL, 0, 'Roll up cake and repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C772650C80CA44F85928E0E84EC5470', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 36, NULL, NULL, 2, NULL, 0, 'Now they are ready to become penguins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'041BABC5A8ACE1469FA36BA75832DFC7', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 37, NULL, NULL, 2, NULL, 0, 'Color a small amount of the white modeling chocolate orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33DEF8941F143A418282DF104FB67B57', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 38, NULL, NULL, 2, NULL, 0, 'Roll out the white, orange and dark modeling chocolate to about 1/16-1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3DF5203C2696E428FA646B054CB3746', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 39, NULL, NULL, 2, NULL, 0, 'Cut oval bellies, two white eyes, two small dark pupils, two tear drop shaped wings, and 3 triangles for the beak and feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F911AF5D0017CF4A9FC7EC57015D9549', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 40, NULL, NULL, 2, NULL, 0, 'Lightly brush water on the back side of the pupils and attach to the whites of the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29347527245B3C4E9D8715DA6065AF65', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 41, NULL, NULL, 2, NULL, 0, 'Attach the eyes, along with a beak, two wings and a white belly using the candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7155660A821F7F45BA9B61B9E687CAC4', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 42, NULL, NULL, 2, NULL, 0, 'Cut two small slits along one edge of the remaining triangles, creating feet and attach them to the cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'702D90A414853245AD5C06A9B47BC1A9', X'6C46DB64A9D17C43AA5E37606D0D537F', X'6C46DB64A9D17C43AA5E37606D0D537F', 43, NULL, NULL, 2, NULL, 0, 'You can serve these standing up if you trim the bottoms as needed so they stand upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CB60CF039588241AC6484BBCEE50A18', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 0, NULL, NULL, 2, NULL, 0, 'Line baking sheets with non-stick tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5BD65DF914B794CA0FAB343A84974E7', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 1, NULL, NULL, 2, NULL, 0, 'Spray a star shaped cookie cutter with cooking spray. Set cookie cutter on baking sheet. Spoon chicken into cutter, press down into a 1/2 inch thick layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49EDDFC18C8DB9419643F64C43E6EDFA', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 2, NULL, NULL, 2, NULL, 0, 'Remove cutter and repeat making about 22 stars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE8091B324AA404CAD6669007B80E36E', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 3, NULL, NULL, 2, NULL, 0, 'Pinch the pointed ends of each star, reshaping them to look like starfish legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05BF90A82B836A42A3FC4A9F9B03D96F', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 4, NULL, NULL, 2, NULL, 0, 'Freeze for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'845F7D2F6A49C54F971D67558CAD11F8', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 5, NULL, NULL, 2, NULL, 0, 'Crush potato chips into fine crumbs, either using a food processor or a rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0379D1FF6493E64697718EB9B54FB9B7', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 6, NULL, NULL, 2, NULL, 0, 'Pour crumbs into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5AA78EF93CEE448B180B709F849EF7C', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 7, NULL, NULL, 2, NULL, 0, 'Blend together eggs and French onion dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DC028B1B31464418D186C5C0B56DD79', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 8, NULL, NULL, 2, NULL, 0, 'Pour into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D48C3883C2314B4EB96E8763EC284468', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 9, NULL, NULL, 2, NULL, 0, 'Combine flour and salt and pour into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'096F9F1C963A1F4FB6FC76DEBF5EC89A', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 10, NULL, NULL, 2, NULL, 0, 'Dip one frozen starfish chicken patty into the flour then pat off excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB7FED647D55E94FB9F00A0BE863618C', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 11, NULL, NULL, 2, NULL, 0, 'Dip into egg mixture allowing excess liquid to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09B2B5F9B831BF4182EF9760E4D67180', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 12, NULL, NULL, 2, NULL, 0, 'Coat in potato chip crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CC4B62B3C14B54398FA38BC0D5F059A', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 13, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A175933BEED2CF408783AC4F1189C57E', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 14, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C840244DD6CA2B4292E91DE51B737C40', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 15, NULL, NULL, 2, NULL, 0, 'Heat oil to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA88170982DD9A4080C8E001C7CB6A8C', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 16, NULL, NULL, 2, NULL, 0, 'Fry starfish until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'478268D5BAB6E1439EE3A7C4A043CA9E', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 17, NULL, NULL, 2, NULL, 0, 'Set starfish on a cooling rack set over a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A87828C89177C04E86D722D0D114F2D9', X'DEE231E2A80EE64CB13872CE3F43986F', X'DEE231E2A80EE64CB13872CE3F43986F', 18, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until cooked through and a thermometer inserted into the chicken registers 165 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F437E628F5BF0E409AAD0E4B5D0563C2', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside. Cream butter and sugar until light and fluffy. Stir in egg, vanilla, almond extract, and yellow food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B82B4BD4743D574ABAEE222FDB44565A', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 1, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined. If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03F25DE8375F82479EDC511859C013A7', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 2, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface or non-stick mat with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5768143B4E98E141908DDEC097576A9C', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 3, NULL, NULL, 2, NULL, 0, 'Roll the cookie dough out to between a 1/16 inch and 1/8th inch thickness, cut 17 or 18 circles using a round cutter, and drape circles of dough over each mound on your pan and smooth with your fingers. Freeze pan for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDFC05A16E0BFF479BC94BC37E6E33C1', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 4, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degree oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2205901BE1F91E4A8723C7243CA5A45C', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies for 8-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90F1091CA21AA5419DCBEEA16C10DBAF', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 6, NULL, NULL, 2, NULL, 0, 'Roll out the remaining dough and cut out 17-18 sunshine shapes. Set them on parchment lined baking sheets, refrigerate for 15 minutes, then bake for 10-14 minutes until the tops look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B83EEB651056344C937E1E550755C9DF', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 7, NULL, NULL, 2, NULL, 0, 'Cool cookies completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'887C50E0C74398418C6066A107E6B7E4', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 8, NULL, NULL, 2, NULL, 0, 'Fill each half sphere cookie with candy, pipe on some yellow royal icing or melted candy coating around the top edge of the cookie, set a sun shaped cookie over top, turn the cookies right side up and allow to dry completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45127C6C210097459F2361349086B820', X'B54F125B6CF72D469A66BE4B2E8D4492', X'B54F125B6CF72D469A66BE4B2E8D4492', 9, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes using icing or candy melts, then draw on smiles using black food coloring markers.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'759DE3ECE9CFA544929976A1C0ADEAB8', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat grill to medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'398B593F15E8D7408CB76B4D83C244BB', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 1, NULL, NULL, 2, NULL, 0, 'Grill MorningStar Farms Garden Veggie Patties until they reach an internal temperature of 165 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38F6B01DBD6E0D4CBA5FBAC551564935', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 2, NULL, NULL, 2, NULL, 0, 'Chop into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F20707FA7FEDA43AA5914E52EC930FE', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 3, NULL, NULL, 2, NULL, 0, 'Reduce grill temperature to medium low.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EF7AC29F3C61D4882B86C9FEC7628BB', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 4, NULL, NULL, 2, NULL, 0, 'Top pizza crusts each with 1/3 cup of pizza sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D3489B4C8C58C4BAEE27FF6CFE7FBD0', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle 3 ounces of mozzarella cheese on each pizza.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DB0A26E807ECF47A8C3508DF8F4B0AD', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 6, NULL, NULL, 2, NULL, 0, 'Top with vegetables, chopped Veggie Patties, and feta cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2968BA426FEDC84EBBF8A6FAEDB092F1', X'A452678DDC56F040A919D805A2D7547C', X'A452678DDC56F040A919D805A2D7547C', 7, NULL, NULL, 2, NULL, 0, 'Grill for 8-10 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'538A6917537A5545995FD5ADC98EBCFA', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90A028B64BB2474EB9664BBD2916647A', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 1, NULL, NULL, 2, NULL, 0, 'Spray the springform pan with non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'917FB8E0B694094BB53D2F4AEE27E1DB', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 2, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'955A8178C9D5294DBDA3B0D9FF9C74E7', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 3, NULL, NULL, 2, NULL, 0, 'Pour the Town House Pretzel Thins in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DBA45BA48991D4281E99F4CD537C286', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 4, NULL, NULL, 2, NULL, 0, 'Pulse to fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECAA77B997CFA240B0C9509BEC46FDB0', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 5, NULL, NULL, 2, NULL, 0, 'Add Parmesan Cheese and butter and pulse until it resembles wet sand.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B01C7BE4D7A8F9488B53AF1FCA98118A', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 6, NULL, NULL, 2, NULL, 0, 'Press crumb mixture into the bottom of the springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A086CC59153BA24586A0F69C723672BC', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 7, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until the crust starts to brown around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B84AE82FAB6D244393C675957A332EB9', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 8, NULL, NULL, 2, NULL, 0, 'Cut two ounces of cream cheese off one block and reserve for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA07F0FF56FEB14D890D761C427E760D', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 9, NULL, NULL, 2, NULL, 0, 'Clean food processor bowl, and place remaining 14 ounces of cream cheese inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'320865176007554D80221A3094F0304F', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 10, NULL, NULL, 2, NULL, 0, 'Pulse until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCD738E5C0D8194CB27C7813C1FD166B', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 11, NULL, NULL, 2, NULL, 0, 'Add the sour cream, Parmesan cheese, roasted garlic, egg, egg yolk, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F6ED697E3A9984F81E8533B1CAAA862', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 12, NULL, NULL, 2, NULL, 0, 'Pulse until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2B0C6F552737748808677AA42E4C3B4', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 13, NULL, NULL, 2, NULL, 0, 'Add artichokes, and pulse until chunky.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD1527342EC1E44CB3C64C9F0CB72576', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 14, NULL, NULL, 2, NULL, 0, 'Add more salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF615EC60A666C4B8655BED7C2DFD20E', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 15, NULL, NULL, 2, NULL, 0, 'Pour cheesecake filling over crust and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE201F5FDC4D7447BA021906216EA582', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 16, NULL, NULL, 2, NULL, 0, 'Wrap bottom of springform pan in tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'892DDD0451F76D48B10C0862DA241E49', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 17, NULL, NULL, 2, NULL, 0, 'Set in the 9x13 baking pan, set it in the hot oven, pour hot water into the baking pan, being careful to not spill any into the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'624F3C09B46E3D48A216A762CB2B9211', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 18, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B793B47E32C754CA097DF3193750647', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 19, NULL, NULL, 2, NULL, 0, 'Turn off oven, open oven door, and allow cheesecake to remain in oven for 15 more minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F6069F1BA98AD48AADCA364D588AEB8', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 20, NULL, NULL, 2, NULL, 0, 'Remove cheesecake from water bath and allow it to cool at room temperature for an hour. Refrigerate for at least 4 hours or up to several days.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70B69778294E8147B67C2184D19EE197', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 21, NULL, NULL, 2, NULL, 0, 'Remove from springform pan and set on a serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78840019A67BB248938F8DD38DBA6CC6', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 22, NULL, NULL, 2, NULL, 0, 'Stir black food coloring into the reserved 2 ounces of cream cheese and put it in a pastry bag fitted with a #3 pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E97230995E68446984C35D61B896F84', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 23, NULL, NULL, 2, NULL, 0, 'Pipe on 1 through 12 onto the round cheesecake in a clock sequence.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF61B6B7C14D934DAC8E0B6A209A467F', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 24, NULL, NULL, 2, NULL, 0, 'Change the tips and put the #6 tip on the pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3CA21AD1F409B4CAC843807E1808DB1', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 25, NULL, NULL, 2, NULL, 0, 'Pipe on the hands of the clock.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'217F1269F7D6A84B9BC3DA58E19981B7', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 26, NULL, NULL, 2, NULL, 0, 'Refrigerate until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38856578E7BF2748BB546A0BADA342A3', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 27, NULL, NULL, 2, NULL, 0, 'I popped my cheesecake in the freezer and will thaw it on New Year''s Eve before the party.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'288B8661FA055C4F829D8E44B3C5ECC1', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 28, NULL, NULL, 2, NULL, 0, 'You can serve the cheesecake all on it''s own, or with more Town House Sea Salt Pretzel Thins or some Town House Sea Salt and Olive Oil Flatbread Crisps.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E5DF1C92D338C49AE365FA7E2B96A29', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 29, NULL, NULL, 2, NULL, 0, 'To make the chocolate ganache, heat 6 ounces of semi-sweet chocolate with 1/2 cup heavy whipping cream on high in the microwave for 30 seconds, then stir, and heat for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A582D9EFC7F4246947295613790C2D7', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 30, NULL, NULL, 2, NULL, 0, 'Serve with Town House Sea Salt Pretzel Thins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'600F50F600CA014282334FA799C6B1E4', X'E4637DE6C16DBD4898117EF8397DF460', X'E4637DE6C16DBD4898117EF8397DF460', 31, NULL, NULL, 2, NULL, 0, 'To make my cheese balls, I crushed 10 Town House Sea Salt and Olive Oil Flatbread Crisps to fine crumbs then blended 4 ounces cream cheese with 4 ounces garlic and herb Boursin cheese, 1/4 cup Parmesan cheese, and 4 ounces Italian blend shredded cheese. I scooped out a tablespoonful and wrapped it around a black stir stick and sprinkled on the crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'028BD0D90355254EA2D491DCF0FA4DCC', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5B8E6A9D8D348488EEC03C1A7CAF6A4', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 1, NULL, NULL, 2, NULL, 0, 'Stir together grated Parmesan Cheese, flour, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FC78A59AC0BDA4BA1281A9AAB1E39B0', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 2, NULL, NULL, 2, NULL, 0, 'Whisk together milk, egg, and sour cream');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'585DAD60E0B67247B93921E942C41B57', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 3, NULL, NULL, 2, NULL, 0, 'Combine wet and dry ingredients, add melted butter, and stir until a ball forms.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C5660182FAF8D4CAD1497524C38D3F2', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 4, NULL, NULL, 2, NULL, 0, 'Grease a 9x5 loaf pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'023F3A8BCF636D4BAC0861C1F8B311BD', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle 1/2 of the shredded Parmesan cheese over the bottom of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFEDA7B6A764664B8C316DE1307B187D', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 6, NULL, NULL, 2, NULL, 0, 'Layer bread dough with rows of pepperoni.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'491AB8FBCBBD3B46A301BDDBB0D3AC2A', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle on remaining cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AC18EE3F98EA848A55D4F461DA6E6CB', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 8, NULL, NULL, 2, NULL, 0, 'Bake for 55-65 minutes until golden brown on top and toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'346A6FFF27FC654F86D1901E3635932A', X'E052DFCBD18D1A48966563A9A80C67D3', X'E052DFCBD18D1A48966563A9A80C67D3', 9, NULL, NULL, 2, NULL, 0, 'Cool for 10 minutes in pan, remove, then cool completely before slicing.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47FD114699CB6D41858F852C234BDD53', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 0, NULL, NULL, 2, NULL, 0, 'Sift together flour, salt, and baking powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65F70B1A30AE5B4780172630B2988AFD', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 1, NULL, NULL, 2, NULL, 0, 'cream butter and sugar');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DF68654ECF75542BAF601DBB97DF6D4', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 2, NULL, NULL, 2, NULL, 0, 'add eggs, vanilla, and almond extract and beat until combined');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BADC61612BD9324C8F8C245D4EF2E240', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 3, NULL, NULL, 2, NULL, 0, 'add dry ingredients and stir to combine');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7395ABA8B40DF74CA2F81A502E866E5E', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 4, NULL, NULL, 2, NULL, 0, 'roll dough out to 1/4 inch thickness');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B27CF930B0D36E44BA5A53BE1F5057AC', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 5, NULL, NULL, 2, NULL, 0, 'cut 16 apple shaped cookies');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B27A5538B75B6B4FAF930D68F45BCF8B', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 6, NULL, NULL, 2, NULL, 0, 'roll dough out to 3/8 inch thickness');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA670A637E45874C8C4EB08AD0EAAA61', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 7, NULL, NULL, 2, NULL, 0, 'cut eight apple shaped cookies');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54618BF85ED8A54489A1566DEDAAD676', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 8, NULL, NULL, 2, NULL, 0, 'cut circle out of center of these 8 cookies');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BA42D05C197BB47A8CC96959B3BA43B', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 9, NULL, NULL, 2, NULL, 0, 'refrigerate cookies for 30 min.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12BB6652B3EB814CBF60B6ECB9AE839D', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 10, NULL, NULL, 2, NULL, 0, 'bake at 375 for 12 to 14 min.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B13DB4A087F7364589054E4E50A5EF78', X'4A673A8A4672014CBAC9D2CC7ED53508', X'4A673A8A4672014CBAC9D2CC7ED53508', 11, NULL, NULL, 2, NULL, 0, 'cool completely');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B05CFBF588C73948BF60DD05122028D1', X'202F92F6696D054E87EF89D48D3105A5', X'202F92F6696D054E87EF89D48D3105A5', 0, NULL, NULL, 2, NULL, 0, 'heat apple juice in microwave until warm');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88256D108C52E54AB9DE64576F90D88C', X'202F92F6696D054E87EF89D48D3105A5', X'202F92F6696D054E87EF89D48D3105A5', 1, NULL, NULL, 2, NULL, 0, 'beat confectioners'' sugar, meringue powder, apple juice and red coloring until light and fluffy, 7 to 10 min.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AE64BF0E1E8BE43B31245E1436C59E9', X'202F92F6696D054E87EF89D48D3105A5', X'202F92F6696D054E87EF89D48D3105A5', 2, NULL, NULL, 2, NULL, 0, 'Use icing to glue together one Apple cookie and one cookie with a hole in it. Fill hole with candies. Glue a solid Apple cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15B0A15595C1FE44AA819A9FCDEB807B', X'202F92F6696D054E87EF89D48D3105A5', X'202F92F6696D054E87EF89D48D3105A5', 3, NULL, NULL, 2, NULL, 0, 'Ice the top of the cookies. While the icing is wet attach a stem and leaf.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CC2B078AF2259469FB8F2508F429E2F', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49CF0747E677F74EAC22BA56B272480F', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 1, NULL, NULL, 2, NULL, 0, 'Either line a 9 inch by 13 inch baking pan with non-stick tin foil or spray it with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F0A4E5C3290B145A01B0FA213C8878B', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 2, NULL, NULL, 2, NULL, 0, 'Combine cake mix, pudding mix, sour cream, milk, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFC40EF2CBD94447B06FDDE611FAE488', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88A5B28F23C06647B43886C0C4AC3D31', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 4, NULL, NULL, 2, NULL, 0, 'Increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1BE9CC8E6170143ADC435BE974D23F0', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 5, NULL, NULL, 2, NULL, 0, 'Pour into pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7907F6806AE584CBFBD5F087821E80F', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 6, NULL, NULL, 2, NULL, 0, 'Bake for 28-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDD6A482BEFBA84CA8DFDDF95EBA76E4', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 7, NULL, NULL, 2, NULL, 0, 'Allow cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36A4ADA855E04B43BFAD3493429C7483', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 8, NULL, NULL, 2, NULL, 0, 'Crumble cake into a large bowl, preferable the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05CB3F6B9C6A3C458218F2CB5B281E79', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 9, NULL, NULL, 2, NULL, 0, 'Add frosting and stir or beat until well blended and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83B2A439AA6FDD45A982D4024670FC0B', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 10, NULL, NULL, 2, NULL, 0, 'Scoop out 48 tablespoon size portions of the cake ball dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AE881596AF61549AB873281050765E2', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 11, NULL, NULL, 2, NULL, 0, 'Create an indentation in one cake ball and press a maraschino cherry into it.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCD09E40C4DAF84AA67D4C8833542F82', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 12, NULL, NULL, 2, NULL, 0, 'Press the cake ball dough around the cherry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DB4E091227A3748B8AD2EFA40D58B47', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 13, NULL, NULL, 2, NULL, 0, 'Roll into a ball with the stem sticking out.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6C10A4050E9E148AEBBD0DFADED0D17', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 14, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7483E8F6EDEE1C458C767F6CD836B613', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 15, NULL, NULL, 2, NULL, 0, 'Let cake balls sit at room temperature for 1 hour to firm up a bit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A2B892E95863A41B6B48F6B760A85F2', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 16, NULL, NULL, 2, NULL, 0, 'Dip each cake ball into dark chocolate, allowing the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6BA24FD4B573D4FA9CC523DB83A3769', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 17, NULL, NULL, 2, NULL, 0, 'Set on a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD4FC9520AB98941AD62BFC17F676F15', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 18, NULL, NULL, 2, NULL, 0, 'If using pure chocolate, chill the Chocolate Cherry Bombs in the refrigerator until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'717270E154C1F549BD355A8D124A5E07', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 19, NULL, NULL, 2, NULL, 0, 'If using candy melts pop them in the freezer, instead.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51F21AFF66F1944095A18258937825AF', X'1819AE000787884AA07706729FEC2CC0', X'1819AE000787884AA07706729FEC2CC0', 20, NULL, NULL, 2, NULL, 0, 'Let the cherry bombs sit at room temperature for at least 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E74DE1831157F469A4F374166EC3BCB', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 0, NULL, NULL, 2, NULL, 0, 'Set your cream cheese, sour cream, and egg out on the counter to come to room temperature at least one hour, but preferably 2 hours, before you plan to bake your cheesecakes. You need to use room temperature ingredients!');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B88A135836FA04F97EB621D063769BE', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 1, NULL, NULL, 2, NULL, 0, 'Place a rimmed cookie sheet or baking pan filled with 1/2-inch to 1-inch of water on the lowest rack in your oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC54F5A76C95374CB5EA66771C2B8CCF', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 2, NULL, NULL, 2, NULL, 0, 'Arrange another rack in the middle of the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C950094DC77E0418ED9CC2E44A98164', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 3, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 300 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CACBE66CB51BBE4B88A987481F4679CD', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 4, NULL, NULL, 2, NULL, 0, 'Crush 12 OREO Cookies into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4733D61B5DEADE4A877DBDF812279B20', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 5, NULL, NULL, 2, NULL, 0, 'Stir 2 tablespoons of melted butter into the cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8705096EB3943647B176E95AD297DD85', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 6, NULL, NULL, 2, NULL, 0, 'Scoop out about one tablespoon of the cookie crumbs and drop into a paper-lined cupcake pan. Repeat, filling 12 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'483AA0BD8106AC46B1C35BBB688305E7', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 7, NULL, NULL, 2, NULL, 0, 'Press the crumbs down into an even layer in the bottom of the cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E4C968ACBE108499CAFFF1F73CC6D23', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 8, NULL, NULL, 2, NULL, 0, 'Place 8 ounces of softened cream cheese and 1/3 cup granulated sugar in a large mixing bowl and beat using an electric mixer on low speed just until it is creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42C82AD1AEDCB64BBBFB7AF5326C631F', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 9, NULL, NULL, 2, NULL, 0, 'Add the room temperature sour cream and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC489492A34FE243B6E17F867BF3F495', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 10, NULL, NULL, 2, NULL, 0, 'Stir in 4 ounces of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'924F6771C26AAC44B108A80136977D54', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 11, NULL, NULL, 2, NULL, 0, 'Then pour a lightly beaten, room temperature, egg over the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9B248A681E06C48886AA39EAE7A76A9', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 12, NULL, NULL, 2, NULL, 0, 'Stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA9D1D606E61274A97431BAD65602D14', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 13, NULL, NULL, 2, NULL, 0, 'Scoop a little more than 2 tablespoons of the chocolate cheesecake filling over each cookie crust and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F16AE4ADACF40A40A77126CF04AE1C69', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 14, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter about 10 times to allow any air bubbles to rise to the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'413B969B0374D44B9316903C03779E3B', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 15, NULL, NULL, 2, NULL, 0, 'Place on the middle rack in the oven and bake for 24-28 minutes until the tops look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70933263DE96114D8EBDD37D3C48C6DB', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 16, NULL, NULL, 2, NULL, 0, 'The cheesecakes should still jiggle when the pan is jostled and will be glossy but should not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2F99E1C6EA36B47ADFD12CCF89C4505', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 17, NULL, NULL, 2, NULL, 0, 'Turn the oven off, leave the cheesecakes inside, but crack the door open.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9964FB1A19DCB34C835B1327293D232C', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 18, NULL, NULL, 2, NULL, 0, 'Allow the cheesecakes to rest in the warm oven for 45 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFBBC0750793E7409E54240CE6713EEB', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 19, NULL, NULL, 2, NULL, 0, 'Remove and allow the cheesecakes to cool on the counter for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5F7D1172C6CF94A8A69C4C2F8099F1B', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 20, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3B4DC844918EC459699600EBD0D3773', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 21, NULL, NULL, 2, NULL, 0, 'Remove from the pan and peel off the paper before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D745C5EAEBAA048B8E87FDD82DF3DDE', X'DB7E3B8B72FB69469D36ECA00A992BBD', X'DB7E3B8B72FB69469D36ECA00A992BBD', 22, NULL, NULL, 2, NULL, 0, 'Add toppings, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8F453F140C8BF45A6B98CC729996F7A', X'24D261D9FFCB4548A3EE77CBF5A96001', X'24D261D9FFCB4548A3EE77CBF5A96001', 0, NULL, NULL, 2, NULL, 0, 'Finely chop or shave your chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25C865EBC8218742AE15DB266E76796B', X'24D261D9FFCB4548A3EE77CBF5A96001', X'24D261D9FFCB4548A3EE77CBF5A96001', 1, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream in a saucepan over medium heat just until bubbles begin to form around the edges of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DC85DB6E8D3EA4BBD727CC12D369C22', X'45C0DA6D324E0D488E81BA3E1628E2D5', X'45C0DA6D324E0D488E81BA3E1628E2D5', 0, NULL, NULL, 2, NULL, 0, 'Pour chopped chocolate into a shallow bowl, creating a small well in the center of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A1D65F069024740B76A350B6A4D2855', X'45C0DA6D324E0D488E81BA3E1628E2D5', X'45C0DA6D324E0D488E81BA3E1628E2D5', 1, NULL, NULL, 2, NULL, 0, 'Pour the hot liquid over the chocolate allowing the cream to flow into the center of the bowl and underneath the chocolate. Make sure all the chocolate is submerged under the cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF8C86C36A57294AA083341ECCA998D2', X'45C0DA6D324E0D488E81BA3E1628E2D5', X'45C0DA6D324E0D488E81BA3E1628E2D5', 2, NULL, NULL, 2, NULL, 0, 'Let sit for 3 minutes allowing the chocolate to begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C59AC32B6368E54E838A3D05866103FB', X'45C0DA6D324E0D488E81BA3E1628E2D5', X'45C0DA6D324E0D488E81BA3E1628E2D5', 3, NULL, NULL, 2, NULL, 0, 'Begin stirring in a small circle in the center of the bowl to create an emulsion of the chocolate and cream then widen the circle slowly until all of the chocolate and cream blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D54AD4C58BA2CE4EAEF1531F890D2A7B', X'45C0DA6D324E0D488E81BA3E1628E2D5', X'45C0DA6D324E0D488E81BA3E1628E2D5', 4, NULL, NULL, 2, NULL, 0, 'As soon as the chocolate and cream come together, and your chocolate ganache looks shiny and smooth, stop stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDE68A1D731C4D4890745256441FD239', X'45C0DA6D324E0D488E81BA3E1628E2D5', X'45C0DA6D324E0D488E81BA3E1628E2D5', 5, NULL, NULL, 2, NULL, 0, 'Cover the ganache directly with plastic wrap or with a tight-fitting lid and set aside until it firms up, at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'220D26646478A942B5549E6542544630', X'D6D45C01505F4B4CB443694160A6083C', X'D6D45C01505F4B4CB443694160A6083C', 0, NULL, NULL, 2, NULL, 0, 'Pour the hot cream over the chopped chocolate and close the lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5BD8FA8FD1C0542BF8343C4BC8F4D25', X'D6D45C01505F4B4CB443694160A6083C', X'D6D45C01505F4B4CB443694160A6083C', 1, NULL, NULL, 2, NULL, 0, 'Pulse twice then remove the lid and scrape the sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25929D0A63393F469DDD31BCF8B295DA', X'D6D45C01505F4B4CB443694160A6083C', X'D6D45C01505F4B4CB443694160A6083C', 2, NULL, NULL, 2, NULL, 0, 'Replace the lid and let the chocolate melt for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22EE7B3607AC4B44AD7CA036DA9DB815', X'D6D45C01505F4B4CB443694160A6083C', X'D6D45C01505F4B4CB443694160A6083C', 3, NULL, NULL, 2, NULL, 0, 'Pulse 3-6 times just until the chocolate and cream come together and looks glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75FF1B3D6A40F5448F417AA5F34F11F0', X'D6D45C01505F4B4CB443694160A6083C', X'D6D45C01505F4B4CB443694160A6083C', 4, NULL, NULL, 2, NULL, 0, 'Pour into a bowl, cover, and let rest at room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43BFB3F5B1E7884796CF5C7EA9D06D70', X'CCE0B157B85B2A4BBB5B867A295B42C1', X'CCE0B157B85B2A4BBB5B867A295B42C1', 0, NULL, NULL, 2, NULL, 0, 'Pour 1/4 of the hot cream into a tall immersion blender cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'435BCCBD0ABAB04CB365C77E63D5CD22', X'CCE0B157B85B2A4BBB5B867A295B42C1', X'CCE0B157B85B2A4BBB5B867A295B42C1', 1, NULL, NULL, 2, NULL, 0, 'Add the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D924BED3F3F004D9B9C3785F1E039CE', X'CCE0B157B85B2A4BBB5B867A295B42C1', X'CCE0B157B85B2A4BBB5B867A295B42C1', 2, NULL, NULL, 2, NULL, 0, 'Pour the remaining hot cream over top of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3E630A7F144B84F92A168B668C23A94', X'CCE0B157B85B2A4BBB5B867A295B42C1', X'CCE0B157B85B2A4BBB5B867A295B42C1', 3, NULL, NULL, 2, NULL, 0, 'Set it aside for about 2 minutes to allow the chocolate to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'575503F500A0CB4C944FB2EE490F692E', X'CCE0B157B85B2A4BBB5B867A295B42C1', X'CCE0B157B85B2A4BBB5B867A295B42C1', 4, NULL, NULL, 2, NULL, 0, 'Use the immersion blender to combine the chocolate and cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69FE29DE263ABC4FA86321FFFD2F123B', X'CCE0B157B85B2A4BBB5B867A295B42C1', X'CCE0B157B85B2A4BBB5B867A295B42C1', 5, NULL, NULL, 2, NULL, 0, 'Scrape the sides of the cup and pulse again just until the ganache is smooth, glossy, and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B27BD2226D08248B248A988594B11DB', X'CCE0B157B85B2A4BBB5B867A295B42C1', X'CCE0B157B85B2A4BBB5B867A295B42C1', 6, NULL, NULL, 2, NULL, 0, 'Pour into a shallow bowl, cover, and set aside for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBD1ACCB9EF5074BB51B187E07FC9539', X'1A23BD50FFF98343916C5764A86786CC', X'1A23BD50FFF98343916C5764A86786CC', 0, NULL, NULL, 2, NULL, 0, 'Using a small ice cream scoop or spoon, scoop the ganache onto a parchment paper-lined baking sheet and let rest for about 30 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F660A970B9AED54B8E65545ED0D9F8B1', X'1A23BD50FFF98343916C5764A86786CC', X'1A23BD50FFF98343916C5764A86786CC', 1, NULL, NULL, 2, NULL, 0, 'Roll a scoop of ganache into a ball then immediately roll into the toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F69CA43B533E2429085055F1F5B5B26', X'1A23BD50FFF98343916C5764A86786CC', X'1A23BD50FFF98343916C5764A86786CC', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95E370A6A3643043902A54CB3203D9C5', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 0, NULL, NULL, 2, NULL, 0, 'Start by peeling and slicing the bananas into thin slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89572A2FE31114419AD7E8B5A4CEA4AA', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 1, NULL, NULL, 2, NULL, 0, 'Line a baking sheet or cutting board with wax paper, parchment paper, or a silicone mat and arrange the bananas in a single layer then cover them and place them in the freezer for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6599795F0229074CBE93E3C5E355382D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 2, NULL, NULL, 2, NULL, 0, 'Cut the fresh raspberries into quarters. Arrange in a single layer and freeze.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F32D43429F7B044B955C86776319600', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 3, NULL, NULL, 2, NULL, 0, 'Remove the frozen bananas from the freezer and place them in a food processor or blender.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB1792DAF1419B48B8B4938D40B0469D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 4, NULL, NULL, 2, NULL, 0, 'Add 1/3 cup of peanut butter, 3 tablespoons cocoa powder, and 1 tablespoon of almond milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56523CF2E1FEAB4EBA173799E72DBCED', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 5, NULL, NULL, 2, NULL, 0, 'Puree, until the bananas are broken up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E059E7B5354A64DB2CABB403CE00CDB', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 6, NULL, NULL, 2, NULL, 0, 'Scrape down the banana mixture and add one more tablespoon of almond milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C28A153494B33149A4D3CBBB11366063', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 7, NULL, NULL, 2, NULL, 0, 'Continue to blend until smooth, scraping down the bowl as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6FF04BD03529D4D9C4EC494EA613F68', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 8, NULL, NULL, 2, NULL, 0, 'If needed, you can add up to 1 tablespoon of almond milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B85AA89D11F39428BD7F3CA0FB0C022', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 9, NULL, NULL, 2, NULL, 0, 'Mix in the frozen raspberry pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF226B5B49A09641897F77800D996C50', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 10, NULL, NULL, 2, NULL, 0, 'Spread the ice cream into the graham cracker pie crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EB3218A2CAAE447BB7E2652B8072867', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 11, NULL, NULL, 2, NULL, 0, 'Cover and freeze for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F66A0650B550F34AA6441A84B76027F5', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 12, NULL, NULL, 2, NULL, 0, 'Melt 1/3 cup of chocolate chips in the microwave, using 15-30 second bursts of high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'118865DC6B723D44AC5F09A7CDCC29D4', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 13, NULL, NULL, 2, NULL, 0, 'Then measure out 2 tablespoons of very warm tap water (or heat cold water in the microwave for 10 seconds).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32180F7C0CC3F344BCE1987F5385C3B5', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 14, NULL, NULL, 2, NULL, 0, 'Pour the warm water over the melted chocolate and stir until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79D8B0B51310F94C9AD015295B4ED531', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 15, NULL, NULL, 2, NULL, 0, 'Allow the ganache to cool for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A1751974B7C5E4398366AFA5AE7B425', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 16, NULL, NULL, 2, NULL, 0, 'Remove the pie from the freezer and drizzle the chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E8A4E97673A9D40B0A1C80BF02E8D81', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 17, NULL, NULL, 2, NULL, 0, 'Freeze the pie for at least 1 hour or up to 2 weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEA589B4BD52CE4F82C878E2553ECAD0', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 18, NULL, NULL, 2, NULL, 0, 'Remove the pie from the freezer and let rest at room temperature for 20 minutes if the pie has been in the freezer for more than 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'166D670759617A469A54019691E7B8C3', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 19, NULL, NULL, 2, NULL, 0, 'If desired, pipe on rosettes of whipped cream (or dairy-free whipped topping).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8F64BDFD06A0747A6B13E045BA0DA44', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', X'6326CEDBCF6A394682F1BEB5FCE2FA9D', 20, NULL, NULL, 2, NULL, 0, 'Just before serving garnish the pie with slices of fresh bananas and fresh raspberries.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45322795BA74A843B6B8E38268717610', X'6E0705975172224AA12136770F254C25', X'6E0705975172224AA12136770F254C25', 0, NULL, NULL, 2, NULL, 0, 'Stir together the water and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E873B5E8050E2A42AD5649E6F3035B24', X'6E0705975172224AA12136770F254C25', X'6E0705975172224AA12136770F254C25', 1, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09EAFF0FF5CE3A4A81BD9AEBB4330849', X'6E0705975172224AA12136770F254C25', X'6E0705975172224AA12136770F254C25', 2, NULL, NULL, 2, NULL, 0, 'Stir until the sugar dissolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F5F15DBE22625428A36874CB0696D71', X'6E0705975172224AA12136770F254C25', X'6E0705975172224AA12136770F254C25', 3, NULL, NULL, 2, NULL, 0, 'Set aside to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EBC823CA9DD7B4BB361E780CE9057A1', X'5E864C568D88434AA518986F46C71FEF', X'5E864C568D88434AA518986F46C71FEF', 0, NULL, NULL, 2, NULL, 0, 'Place cream cheese in a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F29BD081D8F29D4A8869CA01403DD869', X'5E864C568D88434AA518986F46C71FEF', X'5E864C568D88434AA518986F46C71FEF', 1, NULL, NULL, 2, NULL, 0, 'Blend until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82285AF9E371154188A9F5EE7F5B2762', X'5E864C568D88434AA518986F46C71FEF', X'5E864C568D88434AA518986F46C71FEF', 2, NULL, NULL, 2, NULL, 0, 'Add powdered sugar and pulse until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21041E76DFA23B45934A8FF8A810845C', X'5E864C568D88434AA518986F46C71FEF', X'5E864C568D88434AA518986F46C71FEF', 3, NULL, NULL, 2, NULL, 0, 'Add heavy whipping cream and pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9E97ACAF566344C92D5D6C0A3D34797', X'5E864C568D88434AA518986F46C71FEF', X'5E864C568D88434AA518986F46C71FEF', 4, NULL, NULL, 2, NULL, 0, 'Pour in 2 tablespoons of the simple syrup and blend until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03AA7697034F8043A913F1948B073012', X'290A2CB8CC64434E8C87EA077F753DBB', X'290A2CB8CC64434E8C87EA077F753DBB', 0, NULL, NULL, 2, NULL, 0, 'Combine the blueberries with 2 tablespoons of the simple syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE53956B7A1E224CB5FBEE0CB6E04AF1', X'290A2CB8CC64434E8C87EA077F753DBB', X'290A2CB8CC64434E8C87EA077F753DBB', 1, NULL, NULL, 2, NULL, 0, 'Pour into the bowl of a food processor along with 1/2 cup of the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B329050B1182CB46801854392029FF59', X'290A2CB8CC64434E8C87EA077F753DBB', X'290A2CB8CC64434E8C87EA077F753DBB', 2, NULL, NULL, 2, NULL, 0, 'Pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'096300F1237E2243AC6F40E755D37069', X'290A2CB8CC64434E8C87EA077F753DBB', X'290A2CB8CC64434E8C87EA077F753DBB', 3, NULL, NULL, 2, NULL, 0, 'Remove about 1/3 of the blueberries for the purple layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB9CD9730A26814599C717A60EE623A7', X'290A2CB8CC64434E8C87EA077F753DBB', X'290A2CB8CC64434E8C87EA077F753DBB', 4, NULL, NULL, 2, NULL, 0, 'Color the remaining 2/3 blueberries using a few drops of blue food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E052264B5349E49BEE0A7AF850D7EAD', X'C7BF1C737CB51143A6D11F98F841033E', X'C7BF1C737CB51143A6D11F98F841033E', 0, NULL, NULL, 2, NULL, 0, 'Puree the raspberries with 2 tablespoons of the simple syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'373F91D963C5F74AA1DFD68EE4AE18F1', X'C7BF1C737CB51143A6D11F98F841033E', X'C7BF1C737CB51143A6D11F98F841033E', 1, NULL, NULL, 2, NULL, 0, 'If desired, push the raspberries through a fine mesh sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36749F954FA22547AB2A62C234F360D8', X'C7BF1C737CB51143A6D11F98F841033E', X'C7BF1C737CB51143A6D11F98F841033E', 2, NULL, NULL, 2, NULL, 0, 'Pour back into the food processor and add 1/2 cup of the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69F269A3FC62C04CBD52E6A8920E644B', X'C7BF1C737CB51143A6D11F98F841033E', X'C7BF1C737CB51143A6D11F98F841033E', 3, NULL, NULL, 2, NULL, 0, 'Blend until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F51532C24F7E924B9650DEB9050585A2', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 0, NULL, NULL, 2, NULL, 0, 'Drizzle some of the three colors of fruit puree into a popsicle mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E440B7366A80C3499ACFEDF85C5F7A59', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 1, NULL, NULL, 2, NULL, 0, 'Add some of the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EE9F78C6AE5ED4896B8D2F4F45D58C5', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 2, NULL, NULL, 2, NULL, 0, 'Swirl the mold and use a skewer to swirl the colors a bit more if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'779EE735BABAB145BB7FF90301FFF0C6', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 3, NULL, NULL, 2, NULL, 0, 'Repeat until you''ve filled the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F469694B016FF841B1C9DF9B850E9416', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 4, NULL, NULL, 2, NULL, 0, 'Add the stick and lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DF15229DDFBAC40AFC81CD85DCE63DF', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 5, NULL, NULL, 2, NULL, 0, 'Repeat filling all 9 popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68FDAFAB8B4A874C8AE1B24EE24A22F7', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 6, NULL, NULL, 2, NULL, 0, 'Freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06458C671CE825438CD4EDF2AD5EFAC6', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 7, NULL, NULL, 2, NULL, 0, 'To remove the pops from the molds, either dip in hot water or run under hot water in your sink for about 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'721479A75CB9394A883EE5927F274A4D', X'A351D232FC584F4BB93453D724799B16', X'A351D232FC584F4BB93453D724799B16', 8, NULL, NULL, 2, NULL, 0, 'Them slide the pops out of the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7796215899B674EA832EFFD9C2D4698', X'4867B40A704E3F4C9AB8D9FE9658E460', X'4867B40A704E3F4C9AB8D9FE9658E460', 0, NULL, NULL, 2, NULL, 0, 'Combine about a 1/2 teaspoon of water with about a teaspoon of silver luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20FE19EE6579254EBADCBDB67E537777', X'4867B40A704E3F4C9AB8D9FE9658E460', X'4867B40A704E3F4C9AB8D9FE9658E460', 1, NULL, NULL, 2, NULL, 0, 'You want this to be thin enough to splatter and thick enough to be metallic in color.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8CD67F5F109C548B773080A6F7F0D57', X'4867B40A704E3F4C9AB8D9FE9658E460', X'4867B40A704E3F4C9AB8D9FE9658E460', 2, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush or pastry brush to flick tiny dots of the luster dust onto a popsicle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B87203660680DB4F92B5A2800CC6A28A', X'4867B40A704E3F4C9AB8D9FE9658E460', X'4867B40A704E3F4C9AB8D9FE9658E460', 3, NULL, NULL, 2, NULL, 0, 'Brush tiny dots of the wet luster dust onto a popsicle and immediately attach a silver star sprinkle. They will stick as the water freezes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'142993183B396D4B8677782AC70605B5', X'4867B40A704E3F4C9AB8D9FE9658E460', X'4867B40A704E3F4C9AB8D9FE9658E460', 4, NULL, NULL, 2, NULL, 0, 'Add some dry luster dust too, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66CBE12AFB91C6469668C0E246B27D19', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees. Line baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FC2AFF9B8E7FD42A8DEDFE4A4496B8D', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 1, NULL, NULL, 2, NULL, 0, 'Sift the cookie mix into a large mixing bowl then add the flour and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09008C21CB7E8F49814FAE3916ECE2AA', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl gently whisk together egg, A & W Root Beer Float Dessert Topper, and red and green food coloring, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'799870390CADB64DAF38ADDF22A2662F', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 3, NULL, NULL, 2, NULL, 0, 'Add to cookie mix and stir until a soft dough forms.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39A424F1AC81FB48A5DE494B53AF21C3', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 4, NULL, NULL, 2, NULL, 0, 'Dust work surface liberally with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F03F86F63FF0FE459897A4C18C860BF5', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 5, NULL, NULL, 2, NULL, 0, 'Roll dough to 1/8-1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9936C61DC584AD4482F9378719133299', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 6, NULL, NULL, 2, NULL, 0, 'Cut dough using a 3` round cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4DB86803A377A45A383629586D1AB94', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 7, NULL, NULL, 2, NULL, 0, 'Re-roll dough as necessary to create 24 cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B996596A245664C8BC59272F5FC8B22', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 8, NULL, NULL, 2, NULL, 0, 'Place cookies, about an inch apart on the prepared cookie sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96F77BDC84F5654FA1B792A126133832', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 9, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until the edges are set (slightly firm to the touch.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00A5C5CC907EA849983BB020E8ADA9FC', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 10, NULL, NULL, 2, NULL, 0, 'Cool completely before proceeding.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6D6CD583018EF499F77043F84CC8AE9', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 11, NULL, NULL, 2, NULL, 0, 'Scoop a heaping 1/4 cup ice cream onto a cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'262475A218CD3241B8075F52FCD52DF9', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 12, NULL, NULL, 2, NULL, 0, 'Top with another cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'843754C48B263D42908991D2E59BA2CC', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 13, NULL, NULL, 2, NULL, 0, 'Repeat to make 12 Root Beer Float Ice Cream Sandwiches.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11636C757F71E74C9BAC3E7E0647F75A', X'1CBA4628B2FF0541A680C013D1465287', X'1CBA4628B2FF0541A680C013D1465287', 14, NULL, NULL, 2, NULL, 0, 'Eat immediately, or wrap each sandwich in plastic wrap and freeze until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'391E47CAD702B3478B9B8EC77BC98F3F', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'539936F772B52D488F564B65446AD5A7', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 1, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71B844B1BAD2E847B02DA421A8AA56FC', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F87A098DA7EE4093AD1A7F1AC84012', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 3, NULL, NULL, 2, NULL, 0, 'Stir in egg, vanilla, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67FDDB3BDAB9584EA61A84AB839FAB6A', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89B801AE48BD334EB224B86B35A2D5EE', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 5, NULL, NULL, 2, NULL, 0, 'Flatten dough into a disc and place in a large zip-top bag then chill in the refrigerator for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA248D57AA6A5048A2E4725D6FC7685E', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 6, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3C446FE64E63E4F866EEC8454499C92', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 7, NULL, NULL, 2, NULL, 0, 'Roll out cookie dough, on a floured surface, to about 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB04FD68B2403C41A1F6A0587D90E994', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 8, NULL, NULL, 2, NULL, 0, 'Cut out the letters to spell out `sprinkle kindness everywhere you go.` You will have enough dough to make 2 phrases plus you''ll have some left over dough you can use to make shapes like hearts or smiley faces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88F270C4863D0D45819956855E726AC7', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 9, NULL, NULL, 2, NULL, 0, 'Chill the alphabet cutouts for 5 minutes in the freezer or 15 minutes in the refrigerator before placing them on a silicone mat lined baking sheet or parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73910504785E864ABAC9D0DCAB786312', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 10, NULL, NULL, 2, NULL, 0, 'Bake the cookies for 10-12 minutes until the edges look set and the tops no longer look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA95F37AF421C04E83A73C0AEDD2CC95', X'FC500C0183889B4097D773B48A6BA39E', X'FC500C0183889B4097D773B48A6BA39E', 11, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely before decorating.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0CE37B18215704395189BB81388D095', X'6A613DEFBB4FD54FB59739D27D261BE9', X'6A613DEFBB4FD54FB59739D27D261BE9', 0, NULL, NULL, 2, NULL, 0, 'Frost the top of each cookie using chocolate ganache, royal icing, candy melts, or buttercream frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'679E499F3BCA01459303E22DA53FA16C', X'6A613DEFBB4FD54FB59739D27D261BE9', X'6A613DEFBB4FD54FB59739D27D261BE9', 1, NULL, NULL, 2, NULL, 0, 'Immediately top with sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FED901F3FADFA649963D9F4E3F5E5978', X'6A613DEFBB4FD54FB59739D27D261BE9', X'6A613DEFBB4FD54FB59739D27D261BE9', 2, NULL, NULL, 2, NULL, 0, 'Arrange the cookies to spell out `sprinkle kindness wherever you go` on a long platter or cutting board and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'397C4B7BBE7CB149B475B5EB535316AE', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 0, NULL, NULL, 2, NULL, 0, 'Pour white chocolate chips into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E364DFB86AB0BA40BDCEB77C98D1E007', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power, for 30 seconds, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A13F7F7D4CF2B84C99C22BDE7DBE31B3', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 2, NULL, NULL, 2, NULL, 0, 'Heat for two more 30 second bursts on high power, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88D643014EC56348AD41C8982ADEE75F', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 3, NULL, NULL, 2, NULL, 0, 'Let the white chocolate sit for 2 minutes then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'683E7D623B5457458C8707DABC00FCDD', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 4, NULL, NULL, 2, NULL, 0, 'If not completely melted, heat for 10-20 more seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75E3694A7CA89341A7AD585413EDF6E7', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 5, NULL, NULL, 2, NULL, 0, 'Stir in the Biscoff Cookie Spread.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF66EF557A4FED45B69EB334A122992F', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 6, NULL, NULL, 2, NULL, 0, 'Place a piece of plastic wrap directly on top of the mixture and set aside for several hours until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BDEB2EEA97BA34D9C73297E4AF24B92', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 7, NULL, NULL, 2, NULL, 0, 'Optionally, you can place the mixture in the refrigerator for about an hour then let sit on your counter until soft enough to scoop.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DB2DDA8FB952943B12D0BCD29DDA08F', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 8, NULL, NULL, 2, NULL, 0, 'Meanwhile, crush 6 Biscoff Cookies into fine crumbs using a rolling pin or food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'363E58D426731E41BB9980AC32683103', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 9, NULL, NULL, 2, NULL, 0, 'Scoop out twenty-six 2-teaspoon size scoops and set on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B943040C81D2240960BF62F6F763F0D', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 10, NULL, NULL, 2, NULL, 0, 'Roll one scoop into a ball then immediately roll it into the Cookie Crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'017008440E0C814F9268B31362056935', X'2126839C32748447A8B3BF7ABC3D870A', X'2126839C32748447A8B3BF7ABC3D870A', 11, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'341ED2C4DBBA4145B9EBA362B7CDF990', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 0, NULL, NULL, 2, NULL, 0, 'Set your ingredients (cream cheese, eggs, sour cream) out on the counter to come to room temperature at least 1 hour, preferably 2 hours before you plan to bake your mini cheeseccakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA086A33EDB4B84EA2731CB3D6CC6B25', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 1, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7E2E4CAF56A8B4E9737F195D64B499C', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 2, NULL, NULL, 2, NULL, 0, 'Stir together the graham cracker crumbs, brown sugar, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31B9722D9881FD4CBE9F2FAB3041F474', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 3, NULL, NULL, 2, NULL, 0, 'Press about 2 tablespoons of the crumbs into 18 paper-lined muffin cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E65CE3B6EF57648BEFB525C88C15384', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 4, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on low speed, scraping down the bowl, as needed, until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB29549942D2664D99C37BBC9FA4D22B', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 5, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0B4D0288783E84E8D9091F5F13978BF', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 6, NULL, NULL, 2, NULL, 0, 'Add egg and vanilla just until combined. Do not overmix at this point.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B1D4CE19C094E4E88F61944CBA4F2F3', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 7, NULL, NULL, 2, NULL, 0, 'Stir in the sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEA83788B7ADA144A7C8BFA3665AD5DD', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 8, NULL, NULL, 2, NULL, 0, 'Equally divide cheesecake filling among the 18 muffin cups, spooning about 2 tablespoons of filling into each.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB8377638E8B934981E5E574AE5D7985', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 9, NULL, NULL, 2, NULL, 0, 'Bake on the middle rack in the oven for 15-18 minutes until the cheesecakes no longer look wet, but the center still jiggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDD19F7A0EB29C4D894D976ABF550B72', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 10, NULL, NULL, 2, NULL, 0, 'Remove and set on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9A4B35E31F88340A7A1293FA4CE8ABD', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 11, NULL, NULL, 2, NULL, 0, 'Allow to cool for about 45 minutes then refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'316C0229D0D59A4DBAB515E47D330D0F', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 12, NULL, NULL, 2, NULL, 0, 'Before serving, pop the cheesecakes into the freezer for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16A50B8003701D4EA88C48C83F36B730', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 13, NULL, NULL, 2, NULL, 0, 'Remove and immediately peel away the paper liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83EC990F90378B498FE03F0FB27D5C54', X'074E0A70AC7C3B45868341B86071D82E', X'074E0A70AC7C3B45868341B86071D82E', 14, NULL, NULL, 2, NULL, 0, 'Set on a serving dish or individual dessert plates and serve plain or add your favorite topping.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6773490B66D8B944A3F4A628631A1B69', X'74F4A57345F4C9408525D4E8230E7F3C', X'74F4A57345F4C9408525D4E8230E7F3C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B574DD6E29BDD945AB71038B74E66328', X'74F4A57345F4C9408525D4E8230E7F3C', X'74F4A57345F4C9408525D4E8230E7F3C', 1, NULL, NULL, 2, NULL, 0, 'Butter and lightly flour the inside of a 9X13-inch cake pan or two 8-inch round pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0ADAFDE7DB24B4C915E13A894CF0F26', X'74F4A57345F4C9408525D4E8230E7F3C', X'74F4A57345F4C9408525D4E8230E7F3C', 2, NULL, NULL, 2, NULL, 0, 'Pour the can of peaches into the bowl of a food processor or a blender and puree until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4030DB79E9ECBC40A00E2CFAD75D140D', X'74F4A57345F4C9408525D4E8230E7F3C', X'74F4A57345F4C9408525D4E8230E7F3C', 3, NULL, NULL, 2, NULL, 0, 'Combine the peach puree with the melted butter and cake mix.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D1B8F77D500AA4C8AA7A11278F97E45', X'74F4A57345F4C9408525D4E8230E7F3C', X'74F4A57345F4C9408525D4E8230E7F3C', 4, NULL, NULL, 2, NULL, 0, 'Mix according to the cake mix instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7554FE47F547E46B4BE6E61642938BE', X'74F4A57345F4C9408525D4E8230E7F3C', X'74F4A57345F4C9408525D4E8230E7F3C', 5, NULL, NULL, 2, NULL, 0, 'Pour into the prepared pan and bake for 34-38 minutes for round cakes and 38-44 minutes for a sheet cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48119A56112EF44189CDDE9DB6991361', X'74F4A57345F4C9408525D4E8230E7F3C', X'74F4A57345F4C9408525D4E8230E7F3C', 6, NULL, NULL, 2, NULL, 0, 'Cool completely before frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93162287708A6B4595E4306642B3757C', X'CB8A3449B168E3418915F3F6F24A3D13', X'CB8A3449B168E3418915F3F6F24A3D13', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89BED4482A75074F8D62AAD3C362F1D8', X'CB8A3449B168E3418915F3F6F24A3D13', X'CB8A3449B168E3418915F3F6F24A3D13', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EDE02B57FDFED45854430FDBBFAEEDB', X'CB8A3449B168E3418915F3F6F24A3D13', X'CB8A3449B168E3418915F3F6F24A3D13', 2, NULL, NULL, 2, NULL, 0, 'Then add the whipped topping and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAF50CD9CAD56947ADB81BE836B51420', X'CB8A3449B168E3418915F3F6F24A3D13', X'CB8A3449B168E3418915F3F6F24A3D13', 3, NULL, NULL, 2, NULL, 0, 'Spread it over the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'331AB183DC0B6A48A792F4DDFB2DE74A', X'DB24E85A222BDA428C2EAA49ACFBF77C', X'DB24E85A222BDA428C2EAA49ACFBF77C', 0, NULL, NULL, 2, NULL, 0, 'Garnish the top of the cake with 12 sliced peaches or see the instructions in the blog post for topping your cake with caramelized peaches.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41926A07F9284C49A51C902922C496F7', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C23CC60AA24DA44AB912D23950847A53', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 1, NULL, NULL, 2, NULL, 0, 'Beat the butter until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAC6B2EAC967CF43A52CCA420F3068F1', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 2, NULL, NULL, 2, NULL, 0, 'Then add the peanut butter and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E669190EEAE9F44A307F58DB2DDBBE4', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 3, NULL, NULL, 2, NULL, 0, 'Add the brown sugar and granulated sugar and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B23FC3FB3A220B4C84A0400FCEBC6987', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 4, NULL, NULL, 2, NULL, 0, 'Then stir in the eggs until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6E1A7724A407544826CC41FB0EDF892', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 5, NULL, NULL, 2, NULL, 0, 'In a separate bowl stir together the flour, baking soda, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60B50C6157FD4F438821E3421E3B93D6', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 6, NULL, NULL, 2, NULL, 0, 'Combine the dry and wet ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B57087F505C1A24BA5CCDB6F8A3B49D2', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 7, NULL, NULL, 2, NULL, 0, 'Scoop out 1-ounce portions of the cookie dough and arrange on a baking sheet with a few inches in between.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56F0682DBF06274B9CC646ED870D55B0', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 8, NULL, NULL, 2, NULL, 0, 'Use a fork to flatten the cookies using a criss-cross pattern.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D19DDA70D56E8447A879957223C5FA40', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 9, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges are set and the tops of the cookies no longer look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A40EA67866C3DB47AA4030FBBAE77FBF', X'FEECBCA8F1E38E48A283509F039121EB', X'FEECBCA8F1E38E48A283509F039121EB', 10, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool for 10 minutes on the baking sheet then place them on a cooling rack until cooled completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49DBE890458D8347AF75FDC66E553B18', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped chocolate into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB53514848799E49A85967EE65A4812B', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 1, NULL, NULL, 2, NULL, 0, 'Heat your cream over medium heat to a simmer on the stove, just until bubbles begin to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'642C3DE688E913469DB92BBC6FE1215A', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 2, NULL, NULL, 2, NULL, 0, 'Immediately pour the hot cream over the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A91D9C0644070740903264ECD0EF2222', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 3, NULL, NULL, 2, NULL, 0, 'Set aside undisturbed for 3 minutes to allow the chocolate to begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CE174F876781E4D9CBF6A568DD8BA8E', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 4, NULL, NULL, 2, NULL, 0, 'Then begin stirring the mixture in small circles in the center of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF707A79CC15284DB977B6DCF01295D6', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 5, NULL, NULL, 2, NULL, 0, 'As the chocolate and cream start to come together you can begin to make your circle bigger, stirring until all the chocolate and cream emulsify.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9B0842042FD4D439F2B9A3AF0387F60', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 6, NULL, NULL, 2, NULL, 0, 'Cover the bowl of ganache with a tight-fitting lid or press a piece of plastic wrap onto the top surface of the ganache and set it aside at room temperature until it thickens to a frosting-like texture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A981FE1AE6F22A45B12908B178D25BC2', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 7, NULL, NULL, 2, NULL, 0, 'Either fill a pastry bag with the chocolate ganache and pipe it onto half of the peanut butter cookies or simply spread it on using a knife or spatula.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82F90125FBD2F2488FDCD6EBB38DDAAF', X'79A5F4545330884F90FB5831AE608F4A', X'79A5F4545330884F90FB5831AE608F4A', 8, NULL, NULL, 2, NULL, 0, 'Gently press another peanut butter cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B82F23BFCA531448E8DD4153F99463A', X'2B9BDA094853804C92AB47A3EAC1A8DF', X'2B9BDA094853804C92AB47A3EAC1A8DF', 0, NULL, NULL, 2, NULL, 0, 'Either allow the frozen raspberries to thaw then press them through a fine-mesh sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'684753D549B7E24CA71BD9C2C7BB7F09', X'2B9BDA094853804C92AB47A3EAC1A8DF', X'2B9BDA094853804C92AB47A3EAC1A8DF', 1, NULL, NULL, 2, NULL, 0, 'Or heat the raspberries in a medium saucepan set over medium heat until the berries break down then press them through the sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DB16A981A3BAE46A17559309CFB0DDC', X'2B9BDA094853804C92AB47A3EAC1A8DF', X'2B9BDA094853804C92AB47A3EAC1A8DF', 2, NULL, NULL, 2, NULL, 0, 'You should have about 3/4 cup of puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1276BC618E07444AA62A6FBB52CF534', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 0, NULL, NULL, 2, NULL, 0, 'Heat the raspberry puree in the saucepan set over medium heat just until bubbles begin to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'629D28A6FC772548B1E508C5B478EDE5', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 1, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and let it rest for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E85AE96CBF2F1141A36FEEC98BBFF587', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 2, NULL, NULL, 2, NULL, 0, 'Then pour the chocolate chips into the pan and stir just to coat the chocolate in the puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76F9E7F2AC92AD4A959C7F98F2CB4F68', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 3, NULL, NULL, 2, NULL, 0, 'Set the pan aside for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10EC3D8512E69E41A203E400ABDB7FC9', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, whisk or beat the cream until it comes to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45DA7A7FD79EF94AB09FB7B2A85A9789', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 5, NULL, NULL, 2, NULL, 0, 'Stir the chocolate and raspberry puree until smooth then pour it into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E109A8B3B8B5E42B0949862A40DA2B8', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 6, NULL, NULL, 2, NULL, 0, 'Stir 1/3 of the whipped cream into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'304B4E65E284FD49AD6E6B344BA67F1C', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 7, NULL, NULL, 2, NULL, 0, 'Then fold in the remaining cream until all the white streaks are gone.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34690D91F3990442B58908D884B111E2', X'B62B00CABE59564D9C5C779531B1DFFC', X'B62B00CABE59564D9C5C779531B1DFFC', 8, NULL, NULL, 2, NULL, 0, 'Chill until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE5D7ED283E8704BA18963E88B36B1E1', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 0, NULL, NULL, 2, NULL, 0, 'Wash and dry your oranges.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F3D29B541D064991A9F7D43CBCD7AF', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 1, NULL, NULL, 2, NULL, 0, 'Use a Microplane grater to zest the oranges.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB2651B6CBAECC4DAA5D0FDE4404AC94', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 2, NULL, NULL, 2, NULL, 0, 'Reserve the zest for the cookie dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CB9D673229D2E4BA9C9F2CC518AC08C', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 3, NULL, NULL, 2, NULL, 0, 'Cut the oranges in half and juice them into a small microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA13309A93EBE34D8E711B464BC215FE', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 4, NULL, NULL, 2, NULL, 0, 'Heat the orange juice in the microwave for 40 seconds (or on the stove just until it begins to boil.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B16E12282237847B99633BF9B37331C', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 5, NULL, NULL, 2, NULL, 0, 'Pour the hot juice over the dried cranberries and let them soak for 20-30 minutes, stirring every five minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B98D73306808074B87B077A2F980C4EE', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 6, NULL, NULL, 2, NULL, 0, 'Pour the soaked cranberries into a mesh sieve and press down on them to remove all the excess juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F03C36A3858D94B9BF6B88CB657ADBE', X'95493766DD97044C8BA1D131A9B7CF1E', X'95493766DD97044C8BA1D131A9B7CF1E', 7, NULL, NULL, 2, NULL, 0, 'Set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14CF3166575E0F48B58E468F45F326E4', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFFEA2CC991AC24E98BDCFD921801A1B', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and brown sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31D7A52E55DCDD43AFEC448B1A28B837', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 2, NULL, NULL, 2, NULL, 0, 'Stir in the Karo® Corn Syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACF61D10DEDAE640970539EE9F5866B5', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 3, NULL, NULL, 2, NULL, 0, 'Beat in the egg and egg yolk.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFC6511DF9409D4EAFDAFF66AF07D00F', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 4, NULL, NULL, 2, NULL, 0, 'Add the orange zest.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27B26000F5C68F45821E2D2AF704A02F', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 5, NULL, NULL, 2, NULL, 0, 'Stir together the flour, baking soda, and salt and stir into the wet ingredients just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C71843119224DC4180592D0919813112', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 6, NULL, NULL, 2, NULL, 0, 'Toss in the drained cranberries and chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3241843D883FE4E93F76F8F167A18AB', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 7, NULL, NULL, 2, NULL, 0, 'Scoop out 24 portions of the cookie dough, about 3 tablespoons each, onto four parchment paper-lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3581CE3A906F3040862F51CD123F7824', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 8, NULL, NULL, 2, NULL, 0, 'Bake for 16-18 minutes, until the edges just begin to brown and tops of the cookies begin to look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE0954FE69EFA84BB150255FB44E8C0B', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', X'9D66D0A7F3C0FE44BBE778CC25E4C89D', 9, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7695C0BDFF9794C9AD25C409B053F77', X'235DC88975AD13459EC7B7A347306282', X'235DC88975AD13459EC7B7A347306282', 0, NULL, NULL, 2, NULL, 0, 'Crumble the cake and blend it together with the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54079CD8FA1B1C44BD61CF727354FCD4', X'235DC88975AD13459EC7B7A347306282', X'235DC88975AD13459EC7B7A347306282', 1, NULL, NULL, 2, NULL, 0, 'Add more frosting if desired until the cake ball filling has a nice chocolate truffle style texture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B32BD0A6FDE38D40B772160FF5C83412', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 0, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoonful of white chocolate into one popsicle cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A04285DA2EFB24FB2E56A845FFA2EC4', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 1, NULL, NULL, 2, NULL, 0, 'Paint it into a thin layer on the bottom and up the sides of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'532AF7978E93354E9E3055119026B9F1', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 2, NULL, NULL, 2, NULL, 0, 'Immediately insert then remove a popsicle stick into the mold, to remove any white chocolate from that area.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D01D787A311014D94FB89AC15A4B003', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 3, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 white chocolate shells in your popsicle mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B30D0D38444A534ABCC5CFDB5BCEEDF0', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 4, NULL, NULL, 2, NULL, 0, 'Pop the mold into the refrigerator, if using pure white chocolate, and into the freezer if using confectionery coating (Candy Melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47E1C129408F60429912243A0BDEED75', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 5, NULL, NULL, 2, NULL, 0, 'Chill for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C077A2F0D81634D83A0FE2E6D8036AD', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 6, NULL, NULL, 2, NULL, 0, 'Scoop out 1 tablespoonful of the cake ball filling and press it into the candy shell, leaving at least 1/16 of an inch of space at the top for the additional layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E68E942B197D646B799D88B08A08073', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 7, NULL, NULL, 2, NULL, 0, 'Repeat filling all the popsicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FE0AC90558C8543BA11B3C8B55ED88D', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 8, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon of white chocolate over top and spread it out thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E999C448FBEF3F45AD855DAC56196BE3', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 9, NULL, NULL, 2, NULL, 0, 'Use an offset spatula to scrape off any excess white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4AAA24D47CD854D8E37454B7A8F798B', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 10, NULL, NULL, 2, NULL, 0, 'Repeat covering all the cakesicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2328575C82B17F41B6E8182582D3490E', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 11, NULL, NULL, 2, NULL, 0, 'Chill for about 2 minutes until the candy layer hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1836DB910AF6BA478B1D3CB9D2535217', X'3961E9EF3C71AD4C918270FD8A6B7785', X'3961E9EF3C71AD4C918270FD8A6B7785', 12, NULL, NULL, 2, NULL, 0, 'Unmold the cakesicles and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C04AA5A04D7DE4F94753E57CD7972E2', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 0, NULL, NULL, 2, NULL, 0, 'Melt butter and marshmallows in a large saucepan set over low heat then stir in the cereal until evenly coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18C86D8EBF6A374487B37C25C62FDA1D', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 1, NULL, NULL, 2, NULL, 0, 'Gently spread into an even layer in a well buttered 9 x 13 inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1E699539554AD4F8897C7EC514F5EF2', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 2, NULL, NULL, 2, NULL, 0, 'Allow to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'392EA5C00706AD4BBBF3CDB049481E81', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 3, NULL, NULL, 2, NULL, 0, 'Cut 12 circles out of the treats using the 2-inch round cutter from the DIY-Lish Decorating Kit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEA60B8E4E73334FB1A4F97331156E1F', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 4, NULL, NULL, 2, NULL, 0, 'Knead then roll out the fondant to about 1/16 of an inch thickness. Mix together, in a 1 to 6 ratio, a small amount of white and brown fondant to create tan fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9B8EBF2AE2659469A80D24DCE3A898D', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 5, NULL, NULL, 2, NULL, 0, 'For each bear cut out 1 black circle, 2 black teardrops, and 3 tan teardrops. Attach one tan teardrop to the black circle using water and attach the other tan teardrops to the black teardrops, cutting off the tips, creating the ears. Press two black candies on either side of the tan teardrop creating the eyes. Roll a tiny piece of black fondant into an oval and attach, using water, along the bottom edge of the tan teardrop creating the nose. Use a toothpick to create two nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29A739B040A24449B1B132778017AC1C', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 6, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of melted candy melts on the top of a rice krispie treat and attach a fondant bear face on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'038276FBD796534EBD21AEEA4105F0CD', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 7, NULL, NULL, 2, NULL, 0, 'Brush a small amount of candy melts onto the edge of one of the ears and press it onto the rice krispie treat along the top edge of the bears head. Repeat with the other ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28769F7FC07B21409E83763125392606', X'45A572B8AD5E194A8942900D4C8F87D0', X'45A572B8AD5E194A8942900D4C8F87D0', 8, NULL, NULL, 2, NULL, 0, 'Create more fondant animals using the instructions found in the DIY-Lish Cute Critters Decorating Kit and attach them to the remaining rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD4240FBA441914CAA3F134A07D455CC', X'DE75ABBC3081BF4185513327528119F2', X'DE75ABBC3081BF4185513327528119F2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA749CEF7F24844C9FD43347DED51A16', X'DE75ABBC3081BF4185513327528119F2', X'DE75ABBC3081BF4185513327528119F2', 1, NULL, NULL, 2, NULL, 0, 'Line two cupcake pans with 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'913CC77E919C4C4497BA8BBD23F322C8', X'DE75ABBC3081BF4185513327528119F2', X'DE75ABBC3081BF4185513327528119F2', 2, NULL, NULL, 2, NULL, 0, 'Combine the white cake mix, instant pudding mix, sour cream, egg whites, vegetable oil, and water in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'290FFBA871F8A24C94176B733411AF02', X'DE75ABBC3081BF4185513327528119F2', X'DE75ABBC3081BF4185513327528119F2', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds to incorporate the ingredients then increase the speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A58853E2A229774D8D026E7E588F1108', X'DE75ABBC3081BF4185513327528119F2', X'DE75ABBC3081BF4185513327528119F2', 4, NULL, NULL, 2, NULL, 0, 'Equally divide the cake batter among the 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'320ECFF3B60A014FB57AD0AA077CFF82', X'DE75ABBC3081BF4185513327528119F2', X'DE75ABBC3081BF4185513327528119F2', 5, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until the cupcake springs back when your press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B020C81E252B241A25792A0797F5E05', X'DE75ABBC3081BF4185513327528119F2', X'DE75ABBC3081BF4185513327528119F2', 6, NULL, NULL, 2, NULL, 0, 'Remove the cupcakes from the oven and allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DAF224A2058BC42A651BE3FDB19225F', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 0, NULL, NULL, 2, NULL, 0, 'Pour the strawberry pie filing into a strainer set over a large bowl and press down on the strawberries to allow the red gel to fall into the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26868E1424E3744CB05263A8B7122E0D', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 1, NULL, NULL, 2, NULL, 0, 'You will use the gel to fill the cupcakes and the strawberries to make the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'177563955159374BA64DD26C008DF8E8', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 2, NULL, NULL, 2, NULL, 0, 'Spoon the gel into a pastry bag to pipe into the cupcake or just use a small spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'621901E60BB56D4EAE67E98282162441', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 3, NULL, NULL, 2, NULL, 0, 'Use a cupcake corer or knife to cut a well out of the center of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B95DFAE062C3C94B881D668EE8EA2E02', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 4, NULL, NULL, 2, NULL, 0, 'Fill each well in the cupcake with the strawberry pie filling. Reserve any remaining pie filling for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'526A395743A8BC49BA72781DF5640ACF', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 5, NULL, NULL, 2, NULL, 0, 'Place the softened cream cheese in a large mixing bowl and beat, using an electric mixer. until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BAA1C8FB13ADB49A6940701A890774E', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 6, NULL, NULL, 2, NULL, 0, 'Sift the powdered sugar into the bowl and add the reserved strawberries Beat well until the ingredients are fully combined and the strawberries are broken into small bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84E7554FC9BC5F44B74EF74965A028CD', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 7, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E99FD079FB1AB4193AB2512C8B50F10', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 8, NULL, NULL, 2, NULL, 0, 'Either add the whipped topping and beat until combined or fold in the freshly whipped whip cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC867CFD103D8A4BA64E98FD8AA727D5', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 9, NULL, NULL, 2, NULL, 0, 'Spoon the frosting into a large pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A572AE2835107545AB13A357C6E1C437', X'1526FC7FE490574BB564C3B29E5E9A76', X'1526FC7FE490574BB564C3B29E5E9A76', 10, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A833E1EF8FECD44864CC2C2355AE371', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE0D33A680F40543BC9596B15EDDFA6D', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E46FB872C88C1548A69376DDCACACDB4', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8505A6021B46B9498EF712D3F9F97CEA', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3775D0DE45E394BB4B03286B540C327', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F644F6BD42B4E541ABD29D8486985B9E', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F3F3EC14B03F44B1429D26F373EE44', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through (and registers at least 140 degrees Fahrenheit).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7220BE723E69C942B8DD7F82872635D1', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 7, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A68498B517BB240B72EA509E5C74340', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 8, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F3A407C86B409468B7BFECA03785A65', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 9, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CA240897BF4C24D99A11999CF1C6CB6', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 10, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the chocolate in two stages.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'709F8E129008464290C641F3E5F82FA5', X'2A5641C133471C4B992BB5AFF7D9072B', X'2A5641C133471C4B992BB5AFF7D9072B', 11, NULL, NULL, 2, NULL, 0, 'Cover and chill until the mousse thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F1B1EC22E4651459D3B74936CCBBF9A', X'E8378003CA7FF246B1DC65422586C8E0', X'E8378003CA7FF246B1DC65422586C8E0', 0, NULL, NULL, 2, NULL, 0, 'Combine cream cheese and sugar in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2BEE854F6F39341BD9A877397925458', X'E8378003CA7FF246B1DC65422586C8E0', X'E8378003CA7FF246B1DC65422586C8E0', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on medium-high speed until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7E795D8E88D47419C74739A9BDCB12D', X'E8378003CA7FF246B1DC65422586C8E0', X'E8378003CA7FF246B1DC65422586C8E0', 2, NULL, NULL, 2, NULL, 0, 'Add marshmallow fluff and vanilla bean paste or extract and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'342D8018B025874FBEDBCB152A379AE9', X'E8378003CA7FF246B1DC65422586C8E0', X'E8378003CA7FF246B1DC65422586C8E0', 3, NULL, NULL, 2, NULL, 0, 'Pour in whipped topping and beat until creamy and smooth. If using real whipped cream, gently fold it into the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBCD2466BC765349A8608FF65088F44A', X'0582743F54F14343B0F1848BAFEBACE9', X'0582743F54F14343B0F1848BAFEBACE9', 0, NULL, NULL, 2, NULL, 0, 'Fill one disposable pastry bag with about 1 1/2 cups of dark chocolate mousse and fill another back with about the same amount of the cheesecake mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64C18D5D078D28488ED80035468740A0', X'0582743F54F14343B0F1848BAFEBACE9', X'0582743F54F14343B0F1848BAFEBACE9', 1, NULL, NULL, 2, NULL, 0, 'Fit a large pastry bag with a large star pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54F68B6924F7DD489C19E4F99E5510DA', X'0582743F54F14343B0F1848BAFEBACE9', X'0582743F54F14343B0F1848BAFEBACE9', 2, NULL, NULL, 2, NULL, 0, 'Cut off the tips of the filled bags then snuggle them down into the third bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'955E9CECEA21D24F973EFE246405076C', X'0582743F54F14343B0F1848BAFEBACE9', X'0582743F54F14343B0F1848BAFEBACE9', 3, NULL, NULL, 2, NULL, 0, 'Squeeze until the mousse comes of of the bag and you can see both colors of mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAE4E1133989F941B42CB27529729F86', X'0582743F54F14343B0F1848BAFEBACE9', X'0582743F54F14343B0F1848BAFEBACE9', 4, NULL, NULL, 2, NULL, 0, 'Pipe between 2/3 and 3/4 cup of the swirled chocolate cheesecake mousse into small dessert cups or bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FDA462FE7CE6D4F878A9C7851522A5E', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 0, NULL, NULL, 2, NULL, 0, 'Cut gumdrops into small pieces, toss in sugar, then pour into a colander to remove excess sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'510E4ED859EC6C43BD5C7A6C34F4AFA4', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABAB7545A240E645B75EE575E306047B', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 2, NULL, NULL, 2, NULL, 0, 'Cream Country Crock Baking Sticks and sugar together until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C8B89195B6A01408EA80B11D0DE94D0', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F804983AF416D74A82A0944CB7DB353C', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together the flour, salt, baking powder and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2630F41C46DB3146B3BA6F4691F04D78', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 5, NULL, NULL, 2, NULL, 0, 'Stir dry ingredients into the wet mixture just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C83654171B72AD4C850B6A9A4F5AB05E', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 6, NULL, NULL, 2, NULL, 0, 'Mix in about half of the gumdrop pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4ABCB4F6D6C6DD4D91C25C5B7DBBD084', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 7, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 tablespoons of dough onto a parchment paper lined baking sheet. Repeat making 36 scoops.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BA5574D5B2C0C4580817F62FDEA1CE5', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 8, NULL, NULL, 2, NULL, 0, 'Flatten cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'347BFC4C88B6D44FAF0797ED511B9BC8', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 9, NULL, NULL, 2, NULL, 0, 'Top cookies with more gumdrops.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'383ECFC2851EB94F8A8152D36F50F328', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 10, NULL, NULL, 2, NULL, 0, 'Bake for 8-12 minutes, just until the tops of the cookies start to look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7B30A48DF46574996D7BD680EEC42DD', X'342D30E7A57EA6408F68470FE8717E9F', X'342D30E7A57EA6408F68470FE8717E9F', 11, NULL, NULL, 2, NULL, 0, 'Cool cookies on baking sheet for 2-3 minutes, then remove and place on a wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16AB32A4AD30B940B26C6E4E3808F9A5', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 0, NULL, NULL, 2, NULL, 0, 'Grease a 8 small 6 - 8 ounce oven safe bowls or ramekins with butter. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72AF02EFAB705D46B285AFFCEBB0AAA7', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 1, NULL, NULL, 2, NULL, 0, 'Heat your oven to 325F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'481DE30EE3BBC748A0D835555B1951D6', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 2, NULL, NULL, 2, NULL, 0, 'Cut bread into 1 inch cubes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CD654578809AA4689679AE0F9FB0DC6', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 3, NULL, NULL, 2, NULL, 0, 'Arrange the bread in a single layer on 2 wire cooling racks set over rimmed baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B362B81AE42C4D459D827CE7F0BDF325', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 4, NULL, NULL, 2, NULL, 0, 'Bake until dry and lightly golden for about 25 minutes, rotating pans and bread halfway through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A273F75D0DF0564788F939B56AA74B14', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 5, NULL, NULL, 2, NULL, 0, 'Allow the bread cubes to cool completely for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C2B00D5F47ECA44A3D421AB50BC4BC4', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 6, NULL, NULL, 2, NULL, 0, 'Whisk together the eggs, milk, heavy whipping cream, Oregon Chai Tea Latte Concentrate, brown sugar, vanilla bean paste, and salt in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2990F96CAA5CF64CAE0F4E84C825BFB6', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 7, NULL, NULL, 2, NULL, 0, 'Toss in the bread cubes and gently fold until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24883AEFAF71384FA7A1BBD7AB4092D0', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 8, NULL, NULL, 2, NULL, 0, 'Equally divide the bread among the 8 ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B51DA40B5825A848BF28A6FDAFC8EA72', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 9, NULL, NULL, 2, NULL, 0, 'Spoon any custard remaining in the bowl over each of the ramekins (approximately 1 tablespoon per).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C63629C7C0F3F4484CFFA29DBD076B3', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 10, NULL, NULL, 2, NULL, 0, 'Let sit for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3FA473DAE253F48B4B586C6B7ED7DB2', X'DDED9467252E984BB32C69937335CABF', X'DDED9467252E984BB32C69937335CABF', 11, NULL, NULL, 2, NULL, 0, 'Place ramekins on a baking sheet and bake in the oven for about 35 minutes until the tops are deep golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDE0D463342F5E438C806ED5F79C70A4', X'3F8F148AE9A44C4686421855F1B1F28F', X'3F8F148AE9A44C4686421855F1B1F28F', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the 1/4 cup Oregon Chai Tea Latte Concentrate, heavy whipping cream, brown sugar, and butter in a small saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C170A733D6E5724EB029F3FC3E7B1407', X'3F8F148AE9A44C4686421855F1B1F28F', X'3F8F148AE9A44C4686421855F1B1F28F', 1, NULL, NULL, 2, NULL, 0, 'Heat over medium heat, stirring occasionally, until it comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C09A90323BE6A48ACA2DE8443378C66', X'3F8F148AE9A44C4686421855F1B1F28F', X'3F8F148AE9A44C4686421855F1B1F28F', 2, NULL, NULL, 2, NULL, 0, 'Boil for 5-6 minutes until the caramel thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA4A3426C16C7945A30BE7D063D13307', X'3F8F148AE9A44C4686421855F1B1F28F', X'3F8F148AE9A44C4686421855F1B1F28F', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EFEDCF70CD7164A996B7252F2A59637', X'3F8F148AE9A44C4686421855F1B1F28F', X'3F8F148AE9A44C4686421855F1B1F28F', 4, NULL, NULL, 2, NULL, 0, 'Remove bread pudding from oven, drizzle caramel sauce over top, and serve warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D37BB85292030148951AD3B21AFD2DCA', X'962EB2CDB34B744EBDD244DA9F043193', X'962EB2CDB34B744EBDD244DA9F043193', 0, NULL, NULL, 2, NULL, 0, 'Combine the white and peanut butter chips in a microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F28E808A8CBE643A4F5D4C76219EE3F', X'962EB2CDB34B744EBDD244DA9F043193', X'962EB2CDB34B744EBDD244DA9F043193', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power, for 30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0601DAF3C438D543B11BB3B6BED5C8AB', X'962EB2CDB34B744EBDD244DA9F043193', X'962EB2CDB34B744EBDD244DA9F043193', 2, NULL, NULL, 2, NULL, 0, 'Stir in the peanut butter and salt until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABABF6625C98164684EC7E5DF9CA6B61', X'962EB2CDB34B744EBDD244DA9F043193', X'962EB2CDB34B744EBDD244DA9F043193', 3, NULL, NULL, 2, NULL, 0, 'Let cool at room temperature, stirring often, until the consistency of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFE9F27AF021F245AD33D492291E7F36', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E400536EBC0E8143A03E30BE358A69EB', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 1, NULL, NULL, 2, NULL, 0, 'Beat butter and sugar until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D92625A18ACB204F9151E573BFD5A22D', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 2, NULL, NULL, 2, NULL, 0, 'Add vanilla and egg and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3971B2B5F33CD94EB77AE5A99305C321', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, cocoa powder, baking powder, and salt then stir it into the wet ingredients just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F187DB95F42144C83186E4157F2D3AF', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 4, NULL, NULL, 2, NULL, 0, 'Dust a silicone mat or cutting board with cocoa powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1298783A3AE8014EB43AE34610E1F19B', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 5, NULL, NULL, 2, NULL, 0, 'Roll dough out to 1/8th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCA174656A2E1D48A0AC3E7AB9B83769', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 6, NULL, NULL, 2, NULL, 0, 'Cut 2 inch circles using a round cookie cutter. You should get about 60 circles out of one batch of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B06D06F70E5FE64381A3B3BC0228C380', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 7, NULL, NULL, 2, NULL, 0, 'Set dough circles on a silicone mat or parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53291ED0B86043499D005E58D04F55CF', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5 minutes then bake for 10-12 minutes until the edges look set and the top no longer looks wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6D73DC3270A6046A70A47E2959DA4E5', X'E87127934171DD4DB3517F519216E8F2', X'E87127934171DD4DB3517F519216E8F2', 9, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF22D9FA31128442BB01FC630D678897', X'5334F330D84478439E04865608E4AB94', X'5334F330D84478439E04865608E4AB94', 0, NULL, NULL, 2, NULL, 0, 'Once the fudge filling is frosting consistency, you can pipe it or spread it on half of the cookies then top those with another cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'620E63487DD44045AE6AF306688F82D4', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7280C61632FC94597EF687801BC7AC6', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine butter and sugar. Cream until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C9CC048D420CD4381AAED855EB83A57', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 2, NULL, NULL, 2, NULL, 0, 'Mix in vanilla and eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'079E3D86D1045D4295EEA0EF538B4A15', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 3, NULL, NULL, 2, NULL, 0, 'Combine cocoa powder, baking powder, salt, and flour. Stir in to wet ingredients and mix just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE16BBF10499C94A8BDF2EEFDF50D09B', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 4, NULL, NULL, 2, NULL, 0, 'Dust silicone mat or parchment paper lightly with cocoa power.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'179E48FD688CF240A562FCF4AE03521B', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 5, NULL, NULL, 2, NULL, 0, 'Roll out dough to about 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D4FC4A8D0D4A14693DED06D2F251265', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 6, NULL, NULL, 2, NULL, 0, 'Set the paper strawberry template over the tough and use a sharp knife to cut out around the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1F986D69567924A913F7748E7D942E1', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 7, NULL, NULL, 2, NULL, 0, 'Remove the template and remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3842435166C8FA4682F7A422EC349974', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 8, NULL, NULL, 2, NULL, 0, 'Re-roll remaining dough and repeat cutting out a second strawberry shaped crust on another silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03A0BEEF6ED7474AB34C79F55CFFFC0B', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 9, NULL, NULL, 2, NULL, 0, 'Freeze strawberry shaped cookie dough for 10 minutes .');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98BF249CDAE2CA439AEF76E8200F54C1', X'76B1599D5B8410409B1380107E783B6C', X'76B1599D5B8410409B1380107E783B6C', 10, NULL, NULL, 2, NULL, 0, 'Bake for 18-22 minutes until the top of the cookie no longer looks wet and the edges feel set.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1F064CCCE257748987977A25E4D89CF', X'A04E3E7957405C48B641D21D1E0F68E7', X'A04E3E7957405C48B641D21D1E0F68E7', 0, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAD3E904A5E73741953AB201EA823A47', X'A04E3E7957405C48B641D21D1E0F68E7', X'A04E3E7957405C48B641D21D1E0F68E7', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C833F16A98D32C4FBE8687B395E3CE92', X'A04E3E7957405C48B641D21D1E0F68E7', X'A04E3E7957405C48B641D21D1E0F68E7', 2, NULL, NULL, 2, NULL, 0, 'Add whipped topping and beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DEA86D7434EC74BA3CF0052709ABECE', X'A04E3E7957405C48B641D21D1E0F68E7', X'A04E3E7957405C48B641D21D1E0F68E7', 3, NULL, NULL, 2, NULL, 0, 'Spread half of the filling over top of each of the two strawberry shaped cookie crusts .');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60031770725CBD46A8522AE5322C7AAC', X'BBBF71C6E80C424BB94A1D296EC02B30', X'BBBF71C6E80C424BB94A1D296EC02B30', 0, NULL, NULL, 2, NULL, 0, 'Arrange kiwi slices to form the leaves at the top of each strawberry shaped cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FBD0596B06A58419A1142C32C41286C', X'BBBF71C6E80C424BB94A1D296EC02B30', X'BBBF71C6E80C424BB94A1D296EC02B30', 1, NULL, NULL, 2, NULL, 0, 'Arrange the strawberry pieces over the remainder of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBCFCF2D6AFD1F4E881C7DAA1C1B5A90', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 0, NULL, NULL, 2, NULL, 0, 'Pour chocolate and heavy whipping cream into a glass measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6CD7566E2E95148BB66266DB30D97FD', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 1, NULL, NULL, 2, NULL, 0, 'Pop the fruit pizza in the refrigerator for 30 minutes before topping with ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'702AF67E769C6443A00A25388BA68F39', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 2, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high power for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23477049AB2CA245AE37A5FE38BF0666', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 3, NULL, NULL, 2, NULL, 0, 'Let sit in microwave for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2D2ADD2398B434E8641B632EDCDAA5C', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 4, NULL, NULL, 2, NULL, 0, 'Remove and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A741C7EAF2CB5948B4AD77322D261BA4', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 5, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39810B3D3430DD41A6F8BBC47C76D021', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 6, NULL, NULL, 2, NULL, 0, 'Allow the ganache to cool slightly. You still need it to be pourable but you do not want it hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49951903A8981A4996452315AE481E96', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 7, NULL, NULL, 2, NULL, 0, 'Pour the ganache over the bottom 3/4''s of the strawberry fruit pizza.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'071AFA745E832644BCBB0BE126F18C55', X'C2FE17D10FCEB24A9824EB51B839F5D5', X'C2FE17D10FCEB24A9824EB51B839F5D5', 8, NULL, NULL, 2, NULL, 0, 'Drizzle melted white chocolate over the chocolate covered strawberry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BCCB3FCD946A142A7D13AB0CD2AB0C8', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 0, NULL, NULL, 2, NULL, 0, 'Combine the marshmallows and butter in an extra large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'649470961C83B642B8870E0DC1EFFE9A', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 1, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high heat for 1 1/2 - 2 minutes, stirring after one minute, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC06AA16B9D3684390348BFFBF444D0F', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 2, NULL, NULL, 2, NULL, 0, 'Pour in the Nutter Butter Cereal and stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C09703CFCB417438AB58AE037AE862C', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 3, NULL, NULL, 2, NULL, 0, 'Spread in a non-stick tin foil lined or well buttered 9X13 inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6A6335519B63042BC244F2084C6F3BA', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 4, NULL, NULL, 2, NULL, 0, 'Combine the chocolate and heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E130D2419845ED40913E92D1E91F4E14', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 5, NULL, NULL, 2, NULL, 0, 'Heat in the microwave for 30 seconds then let sit for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D06EED92DEAB3478AAB3FCC97A3F607', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 6, NULL, NULL, 2, NULL, 0, 'Remove from microwave and stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92A28EB434134C4A82453136412B7025', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 7, NULL, NULL, 2, NULL, 0, 'Drizzle over Nutter Butter Cereal bars and sprinkle on the Nutter Butter Cookie pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5413193E62DF594C8AB8E097C25E39F3', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 8, NULL, NULL, 2, NULL, 0, 'Set aside for at least an hour until the bars firm up and the chocolate sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1619C8E76B8ED4D9407DA16F00B54C2', X'76E666BC136EB94BB728EC9D8C7284B9', X'76E666BC136EB94BB728EC9D8C7284B9', 9, NULL, NULL, 2, NULL, 0, 'Cut into 24 bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'042DAD9E10B4A84FB8CF42F6B895CB6D', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4CC14D2AC3728408DC20E6398518F4A', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D7751F1C056F94B932928269F908646', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0A73B3C0074934699947E770CC7D69F', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBCA77C9CB6CF74BA00D7EB5C1619F46', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46C636DFBF8C5342AE2FF12CC69F592A', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D521C4BD29D9164795C58953B3EB105C', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99225700B7B1F643920741AC1171A0D4', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 7, NULL, NULL, 2, NULL, 0, 'You may heat until thickened, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'759564E0A5929C48B38135CC770C0C2A', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 8, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'570E2162F1925F42B93DA90F4506831A', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 9, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36935A4F974E7A40A2869497D504F456', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 10, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3343743316B64D4095FFB8B3C68A2DC1', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 11, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the chocolate in two stages.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B02FB945CB59DC41BF0DDE31FF946559', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 12, NULL, NULL, 2, NULL, 0, 'Cover and chill until the mousse thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84978B21A9840E4F9E6DCF5D1F5D558B', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 13, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into bowls or chocolate cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92B28749BD929644855A9810DFFF482D', X'5834F298D0C0484789B7BB3BEAD680E5', X'5834F298D0C0484789B7BB3BEAD680E5', 14, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for at least 15 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C6705F9917A024A96B8C32CAEE9976B', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 0, NULL, NULL, 2, NULL, 0, 'Toast the EGGO® CHOCO-TOAST CHOCOLATEY CHIP according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BECF9101634FAB4BBBD97C791590AB41', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 1, NULL, NULL, 2, NULL, 0, 'Divide into mini toasts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DB2D9985891994DAC289C8DF22B5B74', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 2, NULL, NULL, 2, NULL, 0, 'Arrange toasts around the edge of a springform or pie pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1233F4F262A1EA4E9B1AB5513ECA9D98', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 3, NULL, NULL, 2, NULL, 0, 'Arrange toasts in the bottom of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DB5813A83A78A4D8AF448F9CF148711', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 4, NULL, NULL, 2, NULL, 0, 'Cut remaining toasts into small pieces to fill in the bottom crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'289B1D11495CE143B3C959F37155373D', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 5, NULL, NULL, 2, NULL, 0, 'Make chocolate sauce by combining heavy whipping cream and chocolate in bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'371499D8376DCE45B89CAECD94373098', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 6, NULL, NULL, 2, NULL, 0, 'Heat in microwave on high power for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BE940FCC63D1A46A85B159BF28A0514', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 7, NULL, NULL, 2, NULL, 0, 'Let bowl sit in microwave for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E2E964C9A7D5C44BE3F9E41461A2EEE', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 8, NULL, NULL, 2, NULL, 0, 'Remove and stir slowly until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C12F241AA3263040A8AEBCA08E6884E4', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 9, NULL, NULL, 2, NULL, 0, 'Pour half of the chocolate sauce over the crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB77B00F9A82BF4C9C9F0A5230FCB55D', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 10, NULL, NULL, 2, NULL, 0, 'Reserve the rest to garnish the pie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'134E439572C34948BF306D08F4B35DE9', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 11, NULL, NULL, 2, NULL, 0, 'Pop the crust in the freeze for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26022FA03917134AB03952ADF77A2408', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 12, NULL, NULL, 2, NULL, 0, 'Scoop the softened ice cream over top of the crust and compact it to fill in big gaps between the scoops.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32CC00ABAF0A2E41BFB224A386BBEFBD', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 13, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70A141BB08DC4A4FBF0E18EB770FF66B', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 14, NULL, NULL, 2, NULL, 0, 'Remove and cut into slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'538E20D66ECD484D8063E31E5330A2F0', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 15, NULL, NULL, 2, NULL, 0, 'Rewarm the chocolate sauce in the microwave for 10 second bursts of high power, stirring after each until melted, then drizzle over top of slices of pie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B774B00C1A97AE43B4D9B6D4861BD02E', X'58F559BCF46BBB49A437D7195BEF67AA', X'58F559BCF46BBB49A437D7195BEF67AA', 16, NULL, NULL, 2, NULL, 0, 'Serve with fresh raspberries.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72458FE9AF91284BACFCE2B7843C2D0A', X'73B2F1AEDB58A74584F48D62EB5E277F', X'73B2F1AEDB58A74584F48D62EB5E277F', 0, NULL, NULL, 2, NULL, 0, 'Line a 9 x 13 inch baking pan with non-stick tin foil or spread butter inside the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'316D80122A3539498971FAC67D3C1BEB', X'73B2F1AEDB58A74584F48D62EB5E277F', X'73B2F1AEDB58A74584F48D62EB5E277F', 1, NULL, NULL, 2, NULL, 0, 'Melt butter and marshmallows in a saucepan over medium-low heat (or in the microwave).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92A1BF7A28EE7C4A86760978B8FAB767', X'73B2F1AEDB58A74584F48D62EB5E277F', X'73B2F1AEDB58A74584F48D62EB5E277F', 2, NULL, NULL, 2, NULL, 0, 'Stir in the freeze-dried apples, chopped dried apple rings, and Cinnamon Pebbles Cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADBFBB1CA728E7468A78F3AA09F62A03', X'73B2F1AEDB58A74584F48D62EB5E277F', X'73B2F1AEDB58A74584F48D62EB5E277F', 3, NULL, NULL, 2, NULL, 0, 'Pour into the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AF30A8B217F3B458FF458CB39491DDB', X'73B2F1AEDB58A74584F48D62EB5E277F', X'73B2F1AEDB58A74584F48D62EB5E277F', 4, NULL, NULL, 2, NULL, 0, 'Flatten into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70F704964EBDF14E834556D850531D7E', X'73B2F1AEDB58A74584F48D62EB5E277F', X'73B2F1AEDB58A74584F48D62EB5E277F', 5, NULL, NULL, 2, NULL, 0, 'Set aside for about an hour until the cereal treats firm up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3842DFA28D320A4D9BD46E3FF3E381C9', X'73B2F1AEDB58A74584F48D62EB5E277F', X'73B2F1AEDB58A74584F48D62EB5E277F', 6, NULL, NULL, 2, NULL, 0, 'Remove from pan and cut into 24 squares.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21EC4CD8BD3A144DAF0625BFE402870E', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 0, NULL, NULL, 2, NULL, 0, 'Stir 2/3 cup caramel bits and cashews into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'890A9126D3DF6A439CEB1546046BF77D', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 1, NULL, NULL, 2, NULL, 0, 'Quickly scoop out 24 spoonfuls onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20D0F263270A964AA3AA84D5C6C23F4D', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 2, NULL, NULL, 2, NULL, 0, 'Immediately sprinkle on chopped cashews.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29E0137A1612964E85F0558F1D53C419', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A512A620E06044F945A4F69A33C103B', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 4, NULL, NULL, 2, NULL, 0, 'Remove and let sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7995BBF402638C418ECF69EA1700C529', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 5, NULL, NULL, 2, NULL, 0, 'Combine the remaining 3 tablespoons of caramel bits with the heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA5C976E63C145428B82118407A798A7', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 6, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AED9221E8427D48834BBD3F23C05B34', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 7, NULL, NULL, 2, NULL, 0, 'Stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE1B95EA99057849BF946CDEFFE71B8E', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 8, NULL, NULL, 2, NULL, 0, 'Drizzle over the chocolate clusters.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDE79125C2635943803C5F6A6E9A4950', X'8BB2E7B57E175542A2B585169AAFC07D', X'8BB2E7B57E175542A2B585169AAFC07D', 9, NULL, NULL, 2, NULL, 0, 'Allow the caramel to dry before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33AC8A693C19BE419BF6F6EFB028F339', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAA78BDF57FF444D92D3B0DE993AAC58', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 1, NULL, NULL, 2, NULL, 0, 'Cream butter, brown sugar, granulated sugar and peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36E84073859B5C48BB6A5FF6B709936E', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 2, NULL, NULL, 2, NULL, 0, 'Add egg and stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBED1C15B451034A83B5F49AB2F740A4', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 3, NULL, NULL, 2, NULL, 0, 'Stir in the flour, baking soda, baking powder, and salt just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA2CF618F57C384EBBA5D332508CD19B', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 1/4 cup portions of dough and set on parchment paper lined cookie sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AD76ADA6814514A85F585D5F01B7276', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 5, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until the edges begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83447D241216274788FFF90297D24618', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 6, NULL, NULL, 2, NULL, 0, 'The cookies will flatten a bit more as they cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B1847708B725C4385A80E731C4090C1', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 7, NULL, NULL, 2, NULL, 0, 'Let cool completely on the cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4283D70D2DA5D4D8594145E3E2FC449', X'936BDD6074887C4D841DF60F8CE9BA5C', X'936BDD6074887C4D841DF60F8CE9BA5C', 8, NULL, NULL, 2, NULL, 0, 'Remove and create an ice cream sundae on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'300E3FA914890B4E9EA5C538E15C896D', X'CAE683CD45BF234D85108E13BE075133', X'CAE683CD45BF234D85108E13BE075133', 0, NULL, NULL, 2, NULL, 0, 'Heat hot fudge according to instructions on jar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADFF014BAEDA6349AEF5AAE047FC0403', X'CAE683CD45BF234D85108E13BE075133', X'CAE683CD45BF234D85108E13BE075133', 1, NULL, NULL, 2, NULL, 0, 'Scoop Blue Bunny® PB ''N Cones Ice Cream onto each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A5E13A43A09B346815996C3EF2ED338', X'CAE683CD45BF234D85108E13BE075133', X'CAE683CD45BF234D85108E13BE075133', 2, NULL, NULL, 2, NULL, 0, 'Drizzle hot fudge and melted peanut butter over each scoop of ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79AC55BD03E5B045807A36414C7F6A8B', X'CAE683CD45BF234D85108E13BE075133', X'CAE683CD45BF234D85108E13BE075133', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on some chopped peanuts.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1FDAE454C65714881B7EC9CA514EBCF', X'CAE683CD45BF234D85108E13BE075133', X'CAE683CD45BF234D85108E13BE075133', 4, NULL, NULL, 2, NULL, 0, 'Add a swirl of whipped topping.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2EC8A0D4EE98C40B8B214A880498B88', X'CAE683CD45BF234D85108E13BE075133', X'CAE683CD45BF234D85108E13BE075133', 5, NULL, NULL, 2, NULL, 0, 'Top with a maraschino cherry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B997DCBBAD5E2140A5391DA843A8AD5B', X'0554BDF71E61184F97FD3A529BC286B9', X'0554BDF71E61184F97FD3A529BC286B9', 0, NULL, NULL, 2, NULL, 0, 'Toast the Eggo Minis Waffles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7A0513AAD131D48B14A569966B07ED6', X'0554BDF71E61184F97FD3A529BC286B9', X'0554BDF71E61184F97FD3A529BC286B9', 1, NULL, NULL, 2, NULL, 0, 'Set aside to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'075A891B3C6D204E885B5729DB98B13D', X'0554BDF71E61184F97FD3A529BC286B9', X'0554BDF71E61184F97FD3A529BC286B9', 2, NULL, NULL, 2, NULL, 0, 'Dip about 3/4 of each waffle in melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A51DFF5721BF54C937C52BB3FF951DE', X'0554BDF71E61184F97FD3A529BC286B9', X'0554BDF71E61184F97FD3A529BC286B9', 3, NULL, NULL, 2, NULL, 0, 'Set on a parchment or wax paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECDA44B938996145928A32B3D8AC8651', X'0554BDF71E61184F97FD3A529BC286B9', X'0554BDF71E61184F97FD3A529BC286B9', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until the chocolate hardens, about 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95CD4EF9E2B2AD469A03B40BD0CEB574', X'AF6E2F44B98A964EB5A5C51C3D742372', X'AF6E2F44B98A964EB5A5C51C3D742372', 0, NULL, NULL, 2, NULL, 0, 'Beat together the softened cream cheese, Dulce de Leche, and whipped topping until creamy and smooth. Refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C975A7F9DA45F4DA83A5998BCC704EB', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 0, NULL, NULL, 2, NULL, 0, 'Heat butter in a 12 inch skillet over medium heat until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BD4BB7ADA14B94D80AB13D5554AE364', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 1, NULL, NULL, 2, NULL, 0, 'Add brown sugar and stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55B48A56E17E6F4BB41F2C7480E4F3CB', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 2, NULL, NULL, 2, NULL, 0, 'Continue to cook without stirring until bubbling and caramelized, 3-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4BA96EC64D92D4D83F19BDC87600DE8', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 3, NULL, NULL, 2, NULL, 0, 'Add banana slices and orange juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C41BF27DCDE8384781D7BF2CB4C25A14', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 4, NULL, NULL, 2, NULL, 0, 'Stir until the orange juice incorporates into the caramel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECFE86CE0F79724EA761A8108EBC588A', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 5, NULL, NULL, 2, NULL, 0, 'Continue to cook for 2-4 minutes, tossing the bananas to coat, until the bananas are softened and browned slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37B6AD4D6D14864D830B56B5F9F428FC', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 6, NULL, NULL, 2, NULL, 0, 'Toast all 6 of the Eggo Thick & Fluffy Original Waffles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D1B067E398F8D499D6080C781864761', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 7, NULL, NULL, 2, NULL, 0, 'Set one waffle on each of six dessert plates then top each with 1/6th of the banana slices (1/2 of a banana).');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B90622B19A12A340A2F8689BEE3B763F', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 8, NULL, NULL, 2, NULL, 0, 'Drizzle on some of the caramel sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A9C01FBC743BE4EA81E11234ABF066C', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 9, NULL, NULL, 2, NULL, 0, 'Add 1/6th of the caramel cheesecake mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D3DDA2EF9DCD446986003DE888113FB', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 10, NULL, NULL, 2, NULL, 0, 'Top with a dollop or pipe on a swirl of whipped topping.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'874394D9AD5A304D879C634E6B1FC3C5', X'E5932D1FADCA3040AFA798885D3FA444', X'E5932D1FADCA3040AFA798885D3FA444', 11, NULL, NULL, 2, NULL, 0, 'Set a chocolate dipped mini waffle on top and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E4D35542E4FFB41B4EF19DEC094BDB8', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 0, NULL, NULL, 2, NULL, 0, 'Thaw pie dough according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4554EDCDD2C4944A9496E93B23A6EC9', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F. Spray 8 cannoli forms with non-stick baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6759E85AB5CEC499A7B0FF646A8ADED', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 2, NULL, NULL, 2, NULL, 0, 'Unroll pie dough and cut four 4 1/2 inch circles from each crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08730AB097EFAE4D98CCA51F09D26BC9', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 3, NULL, NULL, 2, NULL, 0, 'Make an egg wash by whisking the egg and water together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71D6D13D6F1C4C42BC1C404D9278CAC3', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 4, NULL, NULL, 2, NULL, 0, 'Wrap one pie dough circle around each cannoli form sealing the edges of the dough with egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54142C276A4C2046941FB1FC46841BD4', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet and freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5094E55373DC2469FD516C74AEE25BC', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 6, NULL, NULL, 2, NULL, 0, 'Remove and bake for 10-12 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'164376DD6A2EEF4A8D529FE18B4EE691', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 7, NULL, NULL, 2, NULL, 0, 'Allow to cool for 10 minutes then carefully remove them from the cannoli forms. Let them cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E31E38FBDA2EB9498F5C0F39D6769B19', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 8, NULL, NULL, 2, NULL, 0, 'Stir together the key lime pie filling or curd with the whipped topping.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3D98A676727E0408C51D15F5780DA72', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 9, NULL, NULL, 2, NULL, 0, 'Spoon into a disposable pastry or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A763AD6FCA2C240BAAC74ADAD16ED6C', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 10, NULL, NULL, 2, NULL, 0, 'Just before serving, snip the tip off the pastry bag and pipe the pie filling into the pie crust cannoli shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97ACF0A47C981548AD614CE19B37BA64', X'37CF0B896520A748A98FD0FF48A303F8', X'37CF0B896520A748A98FD0FF48A303F8', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle powdered sugar over the cannoli and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'232132859A480A449F3A08DFE2FAA3AE', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 0, NULL, NULL, 2, NULL, 0, 'Remove eyes from the Peeps.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1827A4AD77345D4FA65F3F50029990F0', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 1, NULL, NULL, 2, NULL, 0, 'Combine 3 ounces of one color Peeps in a large mixing bowl with 1 tablespoon butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BB4B20C4B07214F9867398F9F935432', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 2, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B22BCC718F706745888CE961956D1465', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'108FA0D72021DF4BA4F9CFEF1A005749', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 4, NULL, NULL, 2, NULL, 0, 'Heat for another 30-60 seconds, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD3A56800BB85A4B892BCF0A109EC738', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 5, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9678E66633B6E74B8B9A1A32DE68877B', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 6, NULL, NULL, 2, NULL, 0, 'Pour in 3 cups of popcorn and stir until evenly coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6242A59F8E3EC4A9F34B1D0F26B1643', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 7, NULL, NULL, 2, NULL, 0, 'Repeat, creating several bowls with different colored popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78635DFBBE245F4B8F2B1859A2256552', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 8, NULL, NULL, 2, NULL, 0, 'Allow the popcorn to cool for a minute or two then pinch off pieces of each color and press together.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96CD0AEC0E68CB4491E0FD3C20BDA41A', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 9, NULL, NULL, 2, NULL, 0, 'Roll into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D425C977284F3488603D5022D386B3A', X'6EEAD66C9D03184586B31C48EA03B755', X'6EEAD66C9D03184586B31C48EA03B755', 10, NULL, NULL, 2, NULL, 0, 'Repeat, creating a total of 12 baseball size popcorn balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE543B99C54BC5478C82722AF1A979C9', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine butter and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'475687569C52904999C25911487CEEEE', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 1, NULL, NULL, 2, NULL, 0, 'Cream until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E86E2C3956C8449B4EB469DC08022D2', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 2, NULL, NULL, 2, NULL, 0, 'Mix in egg and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3268FF80C3F3F247AA837A603D8E7FDB', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 3, NULL, NULL, 2, NULL, 0, 'Combine flour, cocoa powder, salt, and baking powder in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E592963D1A2534F887933118EF1405E', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 4, NULL, NULL, 2, NULL, 0, 'Pour into the butter mixture and mix just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDB38D166E0EA74B8DAF11DA6AE1AD65', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 5, NULL, NULL, 2, NULL, 0, 'Scoop out the dough and flatten into a disc then wrap in plastic wrap or place in a zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'040BF8FFDB71624795E92DEB0AAA2B17', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8122C8B862C3C1439D56AC775F58503F', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 7, NULL, NULL, 2, NULL, 0, 'Remove dough from refrigerator.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A631EE27F4C024FB5BF6D7962B830C1', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 8, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F17E3135B644494E92C04934584AC97F', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 9, NULL, NULL, 2, NULL, 0, 'Roll dough out between 1/8th and 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9C2B6CB617D7F4CBB1E7E2FAAB9C8A3', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 10, NULL, NULL, 2, NULL, 0, 'Cut out 12 large Batman shaped cookies using a 4 inch cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF368ED3CB898140A29DE1650DB7C21A', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 11, NULL, NULL, 2, NULL, 0, 'Remove excess dough and place cut-outs in the freezer for about 10 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3D604F042B2FE45B8B131B378F6BE9C', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 12, NULL, NULL, 2, NULL, 0, 'Remove and peel off cut-outs then set them on parchment paper lined baking sheets and bake for 14-16 minutes until the edges are set and the tops no longer look wet and really shiny.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'615245C05196F74988682ACF4955E205', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 13, NULL, NULL, 2, NULL, 0, 'Roll out the remaining dough to about 1/2 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CEA0F3D4FD55F4B8024476DAC7E3EBE', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 14, NULL, NULL, 2, NULL, 0, 'Cut out a few Batman shapes using the 4 inch cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98D793D1D323C7458527E37837A54F60', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 15, NULL, NULL, 2, NULL, 0, 'Use the smaller 2 1/4 inch Batman cutter to cut a hole in the center of those cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B19B2D408FE7545A636F1DE876E5EA2', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 16, NULL, NULL, 2, NULL, 0, 'Remove and re-roll the excess dough and continue until you have 6 cookies with holes in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9139F3E8A872E449B2DC89D583429341', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 17, NULL, NULL, 2, NULL, 0, 'Freeze the cut-outs for 10-15 minutes until firm before transferring them to a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAE73E28B79112499FD0D1BDE9BA4510', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 18, NULL, NULL, 2, NULL, 0, 'Bake for 16-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFF878439CD7A4489020E4E218AA791A', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 19, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B848848C91D8764F956EF02AA5462AEE', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 20, NULL, NULL, 2, NULL, 0, 'Pour the melted chocolate into a disposable pastry bag or zip top bag then cut off one tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'313079DA0E90E54C988CCE70A8F88B7F', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 21, NULL, NULL, 2, NULL, 0, 'Pipe a small amount of chocolate on the back side of a cookie with a hole in the middle.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B2157D49CF5C6429EDE84E29A8FE4EC', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 22, NULL, NULL, 2, NULL, 0, 'Set that cookie on top of a whole cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14C3B1C5FE4E59418207EDDBC70302DD', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 23, NULL, NULL, 2, NULL, 0, 'Pour candy into the center of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E2FB4E767766049B887A5990B991DE5', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 24, NULL, NULL, 2, NULL, 0, 'Pipe chocolate over the top edge of the cookie with the hole in it then immediately set a whole cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12F3D3BECB7F5A4B8AD8BBF42128A8C4', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 25, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A3A4A2BF9020A43978AFF2F1CE92D90', X'1FC60A89424A4947B25AF6027A18E495', X'1FC60A89424A4947B25AF6027A18E495', 26, NULL, NULL, 2, NULL, 0, 'Let the chocolate dry for 30 minutes before picking up the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44751EB25227F842887125AF9C774B71', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 0, NULL, NULL, 2, NULL, 0, 'Pour oil into a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B76800CF319C9438EE56022B00C5544', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 1, NULL, NULL, 2, NULL, 0, 'Heat over medium high heat to 350 degrees, adjusting the heat as needed to maintain the temperature of the oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCD4CF1725AFD04D9E8C5B91D3B7E949', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 2, NULL, NULL, 2, NULL, 0, 'Prick the bottoms of the mini tortilla boats using a fork.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A068B1647603F947A52808C5FD583055', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 3, NULL, NULL, 2, NULL, 0, 'Fry a few at a time, flipping over often, until crispy and golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30C7E312AE2DF54785B68EA18E55494E', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 4, NULL, NULL, 2, NULL, 0, 'Remove from oil and set on a few layers of paper towels to allow the excess oil to drain off.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B81F2F52CBA5CB4BB280B3F31048DBD0', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 5, NULL, NULL, 2, NULL, 0, 'Combine the cinnamon and sugar in a bowl that is large enough for one tortilla boat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D43C4B10E1771446BE6E00B443C3C343', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 6, NULL, NULL, 2, NULL, 0, 'Toss the tortilla boats in cinnamon sugar then set them aside to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30197C87660E6A41A79F1F56E5E03DFA', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 7, NULL, NULL, 2, NULL, 0, 'Break up one of the tortilla boats into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2B33E1095C4954C83587B70491C1EB3', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 8, NULL, NULL, 2, NULL, 0, 'Scoop ice cream into the fried tortilla boats.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3146851C86B2424F9B36120C427EDA99', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 9, NULL, NULL, 2, NULL, 0, 'Top with some dulce de leche sauce or caramel sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69418BD30866E144B7EF318F17B2BF9F', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 10, NULL, NULL, 2, NULL, 0, 'Pipe or dollop on some whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F427F907CF5EFF408287DF11C3C2C4D4', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle bits of cinnamon sugar tortilla onto each sundae.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'458CF86716BBCF418E7490A3EB9981A0', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', X'0DBF3B84F47AD04CA7EFEFF52E6C1EAD', 12, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA789F03F3ABDA46A492C2281AEA1469', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 0, NULL, NULL, 2, NULL, 0, 'Heat red candy melts in microwave on high power for 30 seconds then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EE3BA38C4F3EE478A8C657DB7D86B19', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 1, NULL, NULL, 2, NULL, 0, 'Heat another 30 seconds then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4DE77A37FF4954194BA9E3A3CFF7931', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 2, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds then let sit in microwave for 2 minutes before stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC0852231D7F0A43A1CE305138D6DE6A', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 3, NULL, NULL, 2, NULL, 0, 'If not all melted, let sit for another 2 minutes then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BC6F885FD9AB445B6A0BB124742C758', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49DED40AE0B2C441B3C04565A0B38893', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 5, NULL, NULL, 2, NULL, 0, 'It''s best to keep the candy from getting over 95 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9634A3F71CB0A24D860C686D040C3E93', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 6, NULL, NULL, 2, NULL, 0, 'If the candy got really hot, let it cool, stirring often, until just warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'896C31930665E545B2D8CABD639160AA', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 7, NULL, NULL, 2, NULL, 0, 'Add the marshmallow creme and stir just until half of it is mixed in then add the food coloring, if using, and continue to stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97E3B8DCE9A29542B3F09B7049364892', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 8, NULL, NULL, 2, NULL, 0, 'The mixture will become thick quickly.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA1C8E4F09F4E644A48E35787D02DD58', X'5E01CFF398ECE840B972A835EE5BF42D', X'5E01CFF398ECE840B972A835EE5BF42D', 9, NULL, NULL, 2, NULL, 0, 'Spread the red nougat into the bottom of a silicone loaf pan or into a metal loaf pan lined with strips of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C385B8FC19EAD4D8010FBB2A2FB17B5', X'18C9C3F63353394A81510119E2364EDC', X'18C9C3F63353394A81510119E2364EDC', 0, NULL, NULL, 2, NULL, 0, 'Repeat the process above layering in the orange, yellow, green, blue, indigo, and purple nougat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0A5CBC80FA2C140B596770478C0A80F', X'18C9C3F63353394A81510119E2364EDC', X'18C9C3F63353394A81510119E2364EDC', 1, NULL, NULL, 2, NULL, 0, 'Set aside for 30 minutes so that the candy firms up.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F68C11105C9DB44D89AC02689B9069CD', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 0, NULL, NULL, 2, NULL, 0, 'Pour just enough chocolate to cover the top of the purple layer and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49F585DF9F34504C9BFA4D943420494D', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 1, NULL, NULL, 2, NULL, 0, 'Tap the mold on the counter a few times to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B136005EE435A45A4542E1930E6F4C4', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 2, NULL, NULL, 2, NULL, 0, 'If using pure chocolate refrigerate for 10-15 minutes until the chocolate hardens.If using Candy Melts freeze for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79B236F4A8D12F4CBA8BE6922C239AA7', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 3, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFE4915DDD01744DBF9BDD65A471BAE3', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 4, NULL, NULL, 2, NULL, 0, 'Let the candy bar sit at room temperature for 15 minutes');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9B7F7953495A14FAE3B1A478AAF8ACF', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 5, NULL, NULL, 2, NULL, 0, 'Set the candy bar up on two glasses that fit underneath.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3AD795460E6EB4098825480CF8FB119', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 6, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate over top of the bar allowing it to run down over the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93DA9528B313BA4E9285DDB0B2E02186', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 7, NULL, NULL, 2, NULL, 0, 'Clean up the drips of chocolate from the bottom edge of the bar using a spatula or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FD0FB7348CA7748859E8534AEDFF80E', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle on rainbow chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85CBCF016B1A944C92EA17EEB6259C30', X'D844B17AF2F68F41B9079655DC2EB67D', X'D844B17AF2F68F41B9079655DC2EB67D', 9, NULL, NULL, 2, NULL, 0, 'Chill for 10-15 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8A331C3E1E9E74799D09B3278E9B95B', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 0, NULL, NULL, 2, NULL, 0, 'Line a pan with parchment paper, wax paper, or a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1DA3BBAA96E6D41A41388522CB935D8', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 1, NULL, NULL, 2, NULL, 0, 'Combine the chocolate chips and Dulce de Leche in a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E725CB45E09454F86209510C1CD5C67', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 2, NULL, NULL, 2, NULL, 0, 'Heat over low heat, stirring frequently until just small pieces of solid chocolate remain.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'593401F074DD564294D36513A0412DD6', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and let sit for 3-4 minutes, then stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F07B607CFB67B64BB84D3BD82F33AD9A', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 4, NULL, NULL, 2, NULL, 0, 'Pour into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19576CB306A1AB4AB63AA02F86C329D0', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 5, NULL, NULL, 2, NULL, 0, 'Cut off the tip and pipe out 10 big swirls of the fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5D8EB0E12F7DA4C836463453B8478F8', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 6, NULL, NULL, 2, NULL, 0, 'Immediately press two candy eyes onto each swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4672130714BD5744B935EDD1316166C6', X'081F875B1B8CE14DBF2C3626ED95C3BB', X'081F875B1B8CE14DBF2C3626ED95C3BB', 7, NULL, NULL, 2, NULL, 0, 'Then cut the white candy wafers into smiles and press onto the fudge just below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B88F6AF2E6A99D468A83B6E17DE886DA', X'9D92E40EF99CDD429F694D5E6089692F', X'9D92E40EF99CDD429F694D5E6089692F', 0, NULL, NULL, 2, NULL, 0, 'Combine whipping cream, powdered sugar, and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CA6985CC147454E820377D4BD74E169', X'9D92E40EF99CDD429F694D5E6089692F', X'9D92E40EF99CDD429F694D5E6089692F', 1, NULL, NULL, 2, NULL, 0, 'Either whisk it until it holds stiff peaks or pour half of it into the Pampered Chef Whip Cream Maker and pump it until you make whipped cream. You''ll need to do this twice, again using the remaining whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDCAE70F81796049A668A7F999BEB1CD', X'9D92E40EF99CDD429F694D5E6089692F', X'9D92E40EF99CDD429F694D5E6089692F', 2, NULL, NULL, 2, NULL, 0, 'Pour the Dulce de Leche into a Classic Batter Bowl or mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8C59EAE2B6D71488AF8BCEB0B1B1C88', X'9D92E40EF99CDD429F694D5E6089692F', X'9D92E40EF99CDD429F694D5E6089692F', 3, NULL, NULL, 2, NULL, 0, 'Stir in half of the whipped cream until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D56336C0781AC24DB6287D65857321B9', X'9D92E40EF99CDD429F694D5E6089692F', X'9D92E40EF99CDD429F694D5E6089692F', 4, NULL, NULL, 2, NULL, 0, 'Fold in the remaining whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'044A3DACE073724EAC2EF8CA13B92DEA', X'9D92E40EF99CDD429F694D5E6089692F', X'9D92E40EF99CDD429F694D5E6089692F', 5, NULL, NULL, 2, NULL, 0, 'Freeze for at least 6 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'624FF1D98B816E4F822EF1AAE4898DBA', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2205B546574D8D4E999F68D91B6E40CA', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 1, NULL, NULL, 2, NULL, 0, 'Spray all 12 cavities in a muffin tin with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A3659D8D23FEF4B84366B02B137C03B', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 2, NULL, NULL, 2, NULL, 0, 'Cream butter, brown sugar and granulated sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3297A0F44313964EBB08750F03AF14D4', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 3, NULL, NULL, 2, NULL, 0, 'Stir in the vanilla then the egg until fully incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6FAF3EBB27B08419240DECE6AFC25A1', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 4, NULL, NULL, 2, NULL, 0, 'Mix flour, salt, and baking soda together then incorporate it into the wet ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A81E6A19731F5F40AD79FD965E6A8DC4', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 5, NULL, NULL, 2, NULL, 0, 'Stir in toffee bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB0AC199067CE247B1F7C3407D660010', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 6, NULL, NULL, 2, NULL, 0, 'Scoop heaping tablespoons of dough into each of 12 cavities in the muffin tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8703E8930CB6DF4F8EA3980133BEB191', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 7, NULL, NULL, 2, NULL, 0, 'Press dough into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'390E2E825E377A43A673086084FF2BDB', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 8, NULL, NULL, 2, NULL, 0, 'Bake cookies for 10-14 minutes until deep golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CF6F25B75E77C4B8E42ACD8DB980A4C', X'EA601B112BC64144A4B27D21B531F75F', X'EA601B112BC64144A4B27D21B531F75F', 9, NULL, NULL, 2, NULL, 0, 'Remove and let cookies cool in pan for 10 minutes then remove and set on a cooling rack until cooled completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C4B597CEA2D3A48AFFA8F4BBBF8B49A', X'1A9D39BBFCB6CD42A8A68E1AD620227D', X'1A9D39BBFCB6CD42A8A68E1AD620227D', 0, NULL, NULL, 2, NULL, 0, 'Pour some of the melted chocolate into Pampered Chef Chocolate Drizzler.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7599748AFEDABF409FD694CC689AF1CB', X'1A9D39BBFCB6CD42A8A68E1AD620227D', X'1A9D39BBFCB6CD42A8A68E1AD620227D', 1, NULL, NULL, 2, NULL, 0, 'Drizzle chocolate over the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA9801B248C53F4D9E29014EE7CF4209', X'1A9D39BBFCB6CD42A8A68E1AD620227D', X'1A9D39BBFCB6CD42A8A68E1AD620227D', 2, NULL, NULL, 2, NULL, 0, 'Scoop caramel ice cream onto cookie cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'148D2D6FE445744BB9BDE6A3E476E9ED', X'1A9D39BBFCB6CD42A8A68E1AD620227D', X'1A9D39BBFCB6CD42A8A68E1AD620227D', 3, NULL, NULL, 2, NULL, 0, 'Drizzle more chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5140C764152CD4AB261FC56C53B3FE6', X'1A9D39BBFCB6CD42A8A68E1AD620227D', X'1A9D39BBFCB6CD42A8A68E1AD620227D', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle toffee bits over ice cream and serve immediately or pop in the freezer until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0C32B1B5C55C54EA03A38C46F3874FB', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73B18700A3381E428F65E854545B3F65', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3EC68D080FA8642A4B54746CC4123F2', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94AED18242B7B0469E75A2C5E47DB4BD', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 3, NULL, NULL, 2, NULL, 0, 'Add the vanilla, and almond extract and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95512EE477141241A0A0E3A93F83D32C', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 4, NULL, NULL, 2, NULL, 0, 'Mix in the eggs until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF471A6FFAB01C429482E06E6867AB35', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 5, NULL, NULL, 2, NULL, 0, 'Stir in the flour mixture just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16783A9C51714B45ABAE3AE85828154C', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 6, NULL, NULL, 2, NULL, 0, 'Roll out the dough to about 3/16th inch thickness on a silicone mat or a flour dusted cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'759D2D81FC33674C8F77FB00D30713C8', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 7, NULL, NULL, 2, NULL, 0, 'Cut out dough using a 3 1/2 inch mason jar cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAFCB8B3738199459FA896F00649144A', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 8, NULL, NULL, 2, NULL, 0, 'Remove excess dough leaving mason jars in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CA216425515C147B222611D9AC82472', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 9, NULL, NULL, 2, NULL, 0, 'Cut the centers out using an oblong cookie cutter or knife leaving about 1/2 - 3/4 inch of cookie dough around the outside.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93970E55811ACD469A2952780ACCF9AC', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 10, NULL, NULL, 2, NULL, 0, 'Remove the center pieces of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'439E4F3E15596F4FA9F24F1352D2B6A5', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 11, NULL, NULL, 2, NULL, 0, 'Place the cookies in the freezer for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC9572EA4C5062428FE6789D6EEAD571', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 12, NULL, NULL, 2, NULL, 0, 'Re-roll dough and repeat creating about 60 cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'897ADBF6E0334F41BCCE5C36B61C1F76', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 13, NULL, NULL, 2, NULL, 0, 'Remove and peel the frozen cookies off the silicone mat or cutting board and place them on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D15F9D832DF2FC49BB854EB947FF303C', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 14, NULL, NULL, 2, NULL, 0, 'Bake for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCD519EDAE1CD2489E916063F626F04D', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 15, NULL, NULL, 2, NULL, 0, 'Meanwhile, crush the clear mint candies into very fine crumbs using a food processor or rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B9F7AAC2352B24FB2E4975B2F9C50B2', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 16, NULL, NULL, 2, NULL, 0, 'Remove hot cookies from oven and spoon candy crumbs into the center of each cookie, filling them about 1/2 full.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'391633F97DFCAB449CED305DFB949A7A', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 17, NULL, NULL, 2, NULL, 0, 'Return cookies to oven and allow the candy to melt for 3-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F26F2757D4287E4DA89339491047DC0C', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 18, NULL, NULL, 2, NULL, 0, 'Remove and allow cookies to cool completely before removing them from the cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B89A660C04B794680B5091F3B527204', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 19, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of piping gel over about 3/4 of the candy glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'509E60B30687C342957A508BD6FB0CCB', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 20, NULL, NULL, 2, NULL, 0, 'Sprinkle candies or sprinkles over the piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4A62E51F4F59A44A4019831D8DFC757', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 21, NULL, NULL, 2, NULL, 0, 'Let the gel dry for about 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0662411B4E484F4DA33ED0C38E1EB511', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 22, NULL, NULL, 2, NULL, 0, 'Paint black candy melts over the lid portion of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3107631743B23A4F87F6FF006A61595C', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 23, NULL, NULL, 2, NULL, 0, 'Let the candy melts dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25C25E9B4A63A541A62A19B4A2A26E41', X'87F80480B7BDCB4FA69B9A77A168882B', X'87F80480B7BDCB4FA69B9A77A168882B', 24, NULL, NULL, 2, NULL, 0, 'Brush edible silver luster dust over the black candy lid to create a shine.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A7D700E6ED7994EB3B8F6FA3745BD60', X'A0A5D8EBCB74074EA636F11CF1B22408', X'A0A5D8EBCB74074EA636F11CF1B22408', 0, NULL, NULL, 2, NULL, 0, 'Line an 8 inch square pan with non-stick tin foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39E5CD5C5A5B984A826418B45E2FA9C2', X'A0A5D8EBCB74074EA636F11CF1B22408', X'A0A5D8EBCB74074EA636F11CF1B22408', 1, NULL, NULL, 2, NULL, 0, 'Combine the white confectionery coating wafers with the peanut butter chips in a large microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C36B8D6630F7DB4E8DF760D6200EA505', X'A0A5D8EBCB74074EA636F11CF1B22408', X'A0A5D8EBCB74074EA636F11CF1B22408', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97BF62A0B00FC24A80390213669A9FE1', X'A0A5D8EBCB74074EA636F11CF1B22408', X'A0A5D8EBCB74074EA636F11CF1B22408', 3, NULL, NULL, 2, NULL, 0, 'Add the peanut butter and salt and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C71673DFEBCDD346A72A3C99B9D3ECEC', X'A0A5D8EBCB74074EA636F11CF1B22408', X'A0A5D8EBCB74074EA636F11CF1B22408', 4, NULL, NULL, 2, NULL, 0, 'Pour into the lined pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A742809EE6F2384DBE593F0E69A334FF', X'A0A5D8EBCB74074EA636F11CF1B22408', X'A0A5D8EBCB74074EA636F11CF1B22408', 5, NULL, NULL, 2, NULL, 0, 'Let firm up at room temperature for at least 6 hours before cutting or freeze for 30-45 minutes, then remove and let thaw for one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82BA7222945AEE4A925FDBD345350A66', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 0, NULL, NULL, 2, NULL, 0, 'Roll out your cookie dough (I chill the dough first) and cut out cookies with the cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A80D95455EFCF84A8C74F9A9024D99CA', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 1, NULL, NULL, 2, NULL, 0, 'With a spatula, place dough pieces on the parchment lined cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70D326778D75384C99D20CFAC025A9B1', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 2, NULL, NULL, 2, NULL, 0, 'I usually place the cookie trays in the freezer to help the cookies hold their shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDFC32354217B94D847DAF4A237292C2', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 3, NULL, NULL, 2, NULL, 0, 'Bake according to recipe.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F1C6FCC4B22A1409D01B4F9EA4D3105', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 4, NULL, NULL, 2, NULL, 0, 'Let cookies cool for about 10 minutes and then place on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'818A8A87637A6047B2690D4AD530AB46', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 5, NULL, NULL, 2, NULL, 0, 'Fill a decorating bag with the ivory tinted royal icing, thin enough to fill the `stick` section of the cookie, and fitted with a #2 tip. Let it dry for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3475B6D9DEF00045870512CC7DB8FDBE', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 6, NULL, NULL, 2, NULL, 0, 'Fill a decorating bag with the green (leaf green with a few drops of gold) royal icing and a #2 tip, and apply it the the top section of the apple. Let it dry for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCB3BD49A311DA4E983575EBDD49F14A', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 7, NULL, NULL, 2, NULL, 0, 'Fill a decorating bag with the caramel colored (warm brown with a bit of gold) royal icing and a #3 tip, filling in the remainder of the apple. Allow to dry overnight, about 8 hours. (If you''re using the candy melts, decorate the cookie with it at this point, instead of the royal icing.)');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1592855CB0756A4DA2B2162F02BDEED6', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 8, NULL, NULL, 2, NULL, 0, 'With the green icing and a #1 tip, outline the apple details.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B0330BC1A08C84D90C87BB365649DD6', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 9, NULL, NULL, 2, NULL, 0, 'With the caramel icing and a #1 tip, add the caramel details.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3132E977F160AE4CAB94A9F0D3961B6B', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 10, NULL, NULL, 2, NULL, 0, 'Use the food marker and add facial details.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F815E787B54104CA21CD93143A08739', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 11, NULL, NULL, 2, NULL, 0, 'Finally, add the cheeks using the red royal icing and a #2 tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings3 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA7DF3EA39F970489C8031376643A4D3', X'C287600CF141A841A752301C6E3F81E6', X'C287600CF141A841A752301C6E3F81E6', 12, NULL, NULL, 2, NULL, 0, 'Have fun and enjoy!');
