INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E27D381664EF94A93E1DCFDFA0685D6', X'B09F1D8400DAA74C850387EF312416D7', X'B09F1D8400DAA74C850387EF312416D7', 0, NULL, NULL, 2, NULL, 0, 'Line a 15 x 18 inch sheet pan with parchment or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60E02ABF5A3E5A428BE8AA1D7F0CB0E1', X'B09F1D8400DAA74C850387EF312416D7', X'B09F1D8400DAA74C850387EF312416D7', 1, NULL, NULL, 2, NULL, 0, 'Brush tablespoon of butter over the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9D3CB8CB7295446B2BCB2693543D505', X'B09F1D8400DAA74C850387EF312416D7', X'B09F1D8400DAA74C850387EF312416D7', 2, NULL, NULL, 2, NULL, 0, 'Brush any remaining butter over a rubber spatula.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B4B34B26E85764AB4C7EB8F3972DA12', X'324B866F2C6A1D4C8150D92BB4802B52', X'324B866F2C6A1D4C8150D92BB4802B52', 0, NULL, NULL, 2, NULL, 0, 'Combine white chocolate chips and peanut butter chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B4A9F5ACF1A01419F78E297137BFA04', X'324B866F2C6A1D4C8150D92BB4802B52', X'324B866F2C6A1D4C8150D92BB4802B52', 1, NULL, NULL, 2, NULL, 0, 'Heat for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCF8F11C19C989479609D8CF85B98C33', X'324B866F2C6A1D4C8150D92BB4802B52', X'324B866F2C6A1D4C8150D92BB4802B52', 2, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and pinch a salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'355E6C8FAADDAC4CBBED649206F34B81', X'324B866F2C6A1D4C8150D92BB4802B52', X'324B866F2C6A1D4C8150D92BB4802B52', 3, NULL, NULL, 2, NULL, 0, 'Set aside to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B795AFE71006EB448FB06D12ADC2A6B9', X'324B866F2C6A1D4C8150D92BB4802B52', X'324B866F2C6A1D4C8150D92BB4802B52', 4, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D9056CFF3CEA549909B1971DE1A4BD5', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 0, NULL, NULL, 2, NULL, 0, 'Combine butter, marshmallows, and semi-sweet chocolate chips in a large microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1A2DB6A89854C4B8FF8C42F74D26E4A', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0316CA2B0FE32E44AD3395BACD40A5BF', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6794FF3B586CB747B9594E96A7925229', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 3, NULL, NULL, 2, NULL, 0, 'Heat for 30 seconds, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A2B20070FE3EF49B9EA023AD1E962CD', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 15 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF71A0AEF2A0FE46A79389E924BDC21A', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 5, NULL, NULL, 2, NULL, 0, 'Stir in cocoa crispy cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9524C4C404D4514E87001D50A91EC378', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 6, NULL, NULL, 2, NULL, 0, 'Pour out onto the pan and flatten into a thin layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C749F056E6BDBF4FBC280B49686CC374', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 7, NULL, NULL, 2, NULL, 0, 'Spread peanut butter fudge filling over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBAB4338C9357149BFE6C57A84461626', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5 minutes, just until the fudge firms up a bit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D61A93FAD1A25144B9CACC4010177B87', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 9, NULL, NULL, 2, NULL, 0, 'Roll the crispy treat cereal up into a log.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C971C94F5B7BB4FA028ED077AC19690', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 10, NULL, NULL, 2, NULL, 0, 'If you want big swirls, roll along the short side of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BDF4E1978F40840AB094C248DF3FCC0', X'8D9A4A33524D75498F31B348F25CC639', X'8D9A4A33524D75498F31B348F25CC639', 11, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes, then cut into slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91C3DE430404DE478AD218BEB3D07F72', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 0, NULL, NULL, 2, NULL, 0, 'Cut 2 graham crackers for the front and rear peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E9248B85F9EA542966DD7DBF76B321F', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 1, NULL, NULL, 2, NULL, 0, 'Cut 2 graham crackers to 3 3/4 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57C9EBE96CED63469F26DD5FE772ABFD', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 2, NULL, NULL, 2, NULL, 0, 'Cut 1 graham cracker to 3 1/4 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9422CF49A4B84249B585D58EEB3C4B67', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 3, NULL, NULL, 2, NULL, 0, 'Stir together red and chocolate brown cookie icing.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E5AC76151816D449A360E6AB12C31BD', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 4, NULL, NULL, 2, NULL, 0, 'Add enough water to thin it out.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B33A3C8C049F7439ED8811B235CFAA1', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 5, NULL, NULL, 2, NULL, 0, 'Brush red icing over 2 whole graham crackers, the two peaks, and the 3 short graham crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E1977471689FD4699E9D242E1474A74', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 6, NULL, NULL, 2, NULL, 0, 'Thin out white icing with water and brush over the 4 remaining graham crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B916E328DFFFE45A99EA928B163E861', X'15ADAC789F8C9844811C39DBACD9598C', X'15ADAC789F8C9844811C39DBACD9598C', 7, NULL, NULL, 2, NULL, 0, 'Assemble the graham cracker barn using thick red and white cookie icing, allowing each section to dry before adding the next.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EE4400C3FADE145B6BF4BAF719746A5', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1B6D27E8A6CC345B5A3E013E840C2EB', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39091700FC523C459149494962B49D8E', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 2, NULL, NULL, 2, NULL, 0, 'Spray a 9 x 5 inch loaf pan with baking spray then wipe out excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEDFE9DAED7724439B9ED223CC4D72E3', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 3, NULL, NULL, 2, NULL, 0, 'Whisk the flour and salt together and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18F03D63C5BFD6449989EAE31673F690', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 4, NULL, NULL, 2, NULL, 0, 'Stir together the cocoa powder and chocolate in a medium microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'359B323B151C5D4B8C047278023A5695', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 5, NULL, NULL, 2, NULL, 0, 'Pour Hazelnut and Vanilla flavored creamers into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06617FC894B3274CB2EF0E258723461B', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 6, NULL, NULL, 2, NULL, 0, 'Heat on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94FFDF0189211F4AAEEFBAD5A2470B40', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 7, NULL, NULL, 2, NULL, 0, 'Pour over top of the chocolate and stir until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8235A4F61BD1214DB4BFCA111EF20093', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 8, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds on high in the microwave then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADB2FCCECCD371428063DBC3215FA133', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 9, NULL, NULL, 2, NULL, 0, 'Let cool for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B149C36C53FC504A817F57CE0FA0E8A2', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 10, NULL, NULL, 2, NULL, 0, 'Pour cooled chocolate, softened butter, granulated sugar, and brown sugar into the bowl of a stand mixer or large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F88C37B0DB830F4EAA8F55EE81CE0E48', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 11, NULL, NULL, 2, NULL, 0, 'Beat using a paddle attachment for 2-3 minutes until it becomes lighter in color and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'882636BF98E9BE408AEECF1164F888CA', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 12, NULL, NULL, 2, NULL, 0, 'Continue mixing, adding one egg at a time, until each incorporates into the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B16774B9C452A641AB17A990FB02AC4F', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 13, NULL, NULL, 2, NULL, 0, 'Turn the mixer to low and add the flour and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25EC87A0BB79CA4191CF9368005934F5', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 14, NULL, NULL, 2, NULL, 0, 'Mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20F1275070DD58468A0385C7E7EE802D', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 15, NULL, NULL, 2, NULL, 0, 'Pour into loaf pan and smooth out the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97C88CD74001A94BB9B0CB97EB562A2B', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 16, NULL, NULL, 2, NULL, 0, 'Bake for 55-65 minutes until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3E01AC87346E64F97511CF6E5039FD9', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 17, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool in pan for 10 minutes before inverting onto a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6025E90D648E254CBC4611B908A4C01B', X'0C8A4DF55832B84DA8F841D2483E3ABA', X'0C8A4DF55832B84DA8F841D2483E3ABA', 18, NULL, NULL, 2, NULL, 0, 'Cool completely before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55BA09B84324DE41BA4B62AD73C54CDC', X'4AC9A904F03BE742BB4465C5AAAF70A5', X'4AC9A904F03BE742BB4465C5AAAF70A5', 0, NULL, NULL, 2, NULL, 0, 'Place a roasting pan filled with enough water to reach half way up the side of a silicone Mickey Mouse mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F3F8D903891F748B2AF5832F6D483D5', X'4AC9A904F03BE742BB4465C5AAAF70A5', X'4AC9A904F03BE742BB4465C5AAAF70A5', 1, NULL, NULL, 2, NULL, 0, 'Place roasting pan in oven and heat to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55C451322CCDA447BCAC42700AB0D5E3', X'A9D58F8800FD1947B0F0AF9D29C713FC', X'A9D58F8800FD1947B0F0AF9D29C713FC', 0, NULL, NULL, 2, NULL, 0, 'Combine heavy whipping cream and finely chopped semi-sweet chocolate in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'716614D80D595D4C84D262DB45EBAF14', X'A9D58F8800FD1947B0F0AF9D29C713FC', X'A9D58F8800FD1947B0F0AF9D29C713FC', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6A1ECF3759C2B42B4D7FF666D4C1739', X'A9D58F8800FD1947B0F0AF9D29C713FC', X'A9D58F8800FD1947B0F0AF9D29C713FC', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'182C3A1411FBDB4091FA8C3E47D4779C', X'A9D58F8800FD1947B0F0AF9D29C713FC', X'A9D58F8800FD1947B0F0AF9D29C713FC', 3, NULL, NULL, 2, NULL, 0, 'Let sit for 3 minutes then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BD3826B7AC50F4EBEBC40ED23C09EF0', X'A9D58F8800FD1947B0F0AF9D29C713FC', X'A9D58F8800FD1947B0F0AF9D29C713FC', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D35CA2FDF906FF4F981C457C290D3A2A', X'A9D58F8800FD1947B0F0AF9D29C713FC', X'A9D58F8800FD1947B0F0AF9D29C713FC', 5, NULL, NULL, 2, NULL, 0, 'Set aside to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD05030757E063458FC7057F692BD2E3', X'A9D58F8800FD1947B0F0AF9D29C713FC', X'A9D58F8800FD1947B0F0AF9D29C713FC', 6, NULL, NULL, 2, NULL, 0, 'Pour 2/3 cup ganache into a bowl and reserve for the crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51B9A689D1E52747B39DBF41FE098E10', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 0, NULL, NULL, 2, NULL, 0, 'Either use a food processor or an electric mixer and beat cream cheese until really light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6777859BAE9734A9FEC02534911583C', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 1, NULL, NULL, 2, NULL, 0, 'Be sure all the lumps of cream cheese are gone.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCA85FF4C547AE48878904AAFC52851C', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 2, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C721DEB45F23A4B9C758D5E75A73CD2', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 3, NULL, NULL, 2, NULL, 0, 'Add eggs, heavy whipping cream, and vanilla and beat just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'497AFE23D5DF324E8C6760CCA95CE27E', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 4, NULL, NULL, 2, NULL, 0, 'Pour 1/4 cup of filling into one small bowl and color peach.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1C28769CF2C2841874F9BA57B8DF9E2', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 5, NULL, NULL, 2, NULL, 0, 'Pour 3/4 teaspoon into another bowl and color bright red.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56244971FA63224C96D72DDDD315665F', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 6, NULL, NULL, 2, NULL, 0, 'Pour 2 teaspoons of filling into a final bowl and leave white.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'965F61D308FC5949916D73A16438A9D9', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 7, NULL, NULL, 2, NULL, 0, 'Pour the cooled chocolate ganache into the remaining cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBA0C13F2255364DB7BC49FA01E82C74', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 8, NULL, NULL, 2, NULL, 0, 'Use some food use only paint brushes or squeeze-it mold painter (squeeze bottles) to paint or pipe chocolate cheesecake filling into a Mickey Mouse silicone mold. Paint the nose, pupils, and outlines around the eyes and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00C0643A1591804C9FF493E4F37A102C', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 9, NULL, NULL, 2, NULL, 0, 'Pop the mold in the freezer for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8443E37D4C0FA746BA3081D95CEA1C48', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 10, NULL, NULL, 2, NULL, 0, 'Remove and paint the red tongue and white eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73F35090C0EF9A4DB36D418844F50D2E', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD072AF1A8E0E942974F971D47F32205', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 12, NULL, NULL, 2, NULL, 0, 'Remove and paint the peach face being sure to make it thick.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92A65BBCA1D4604B969CA254C11F03E7', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 13, NULL, NULL, 2, NULL, 0, 'Freeze for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44FD8D230635534A950FC4FDA57BE381', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 14, NULL, NULL, 2, NULL, 0, 'Remove and pipe or paint chocolate filling around the face then pour the remaining chocolate filling over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25513E32E3BC8349A7336B36C21381E2', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 15, NULL, NULL, 2, NULL, 0, 'Place the mold in a the hot water in the roasting pan in the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B332E851161C9346B428DDF14288D44A', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 16, NULL, NULL, 2, NULL, 0, 'Bake for 38-42 minutes until the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C7A4339FDDA124D848D4C598AD83BEA', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 17, NULL, NULL, 2, NULL, 0, 'The center will still be jiggly, but should not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A5B678E26EDE445941F0CCEEA98EBF1', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 18, NULL, NULL, 2, NULL, 0, 'Remove from oven and water bath and set on a cooling rack to cool for one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44FC3D1A7F1803408888FC15009D67A8', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 19, NULL, NULL, 2, NULL, 0, 'Reheat the reserved chocolate ganache in the microwave for 10-15 seconds until it''s fluid.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04D67D6FB9C908408A25AD4F363DDFCA', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 20, NULL, NULL, 2, NULL, 0, 'Crush Oreo Cookies in food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AE8778FA2BF044CAA2083D7FDE3EBD4', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 21, NULL, NULL, 2, NULL, 0, 'Add 1 1/2 tablespoons of the chocolate ganache and pulse to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77DD2B895EFBD4478D966DF551D9BAC2', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 22, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate ganache over the cooled cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21AADC7892B1F245A2A3AE0232C5417C', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 23, NULL, NULL, 2, NULL, 0, 'Sprinkle the cookie crumbs over the ganache and pat into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A0FD272DE847B4D93DC3C987530CC0D', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 24, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC0C3F170BA12741A4A800B523EBD7AF', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 25, NULL, NULL, 2, NULL, 0, 'Remove and un-mold cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FAB7E3C80795646870F0EED7B23A988', X'C0F4069A54D51346A317656DEA56EB9C', X'C0F4069A54D51346A317656DEA56EB9C', 26, NULL, NULL, 2, NULL, 0, 'Allow to thaw before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CCC20AF1BD36F4EB43F021BE2E4CBAE', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 0, NULL, NULL, 2, NULL, 0, 'CAKE CHIPS:');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93958FBB31808642AC0D13DF0E06559E', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D24AE02D673AAD4FA0A65BFDAE27A69F', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 2, NULL, NULL, 2, NULL, 0, 'Spray two 9 inch round baking pans with cooking spray and line the bottoms with a round of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C1E596FE4C5184884ADA6D0326E6928', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 3, NULL, NULL, 2, NULL, 0, 'Combine cake mix, egg, water and oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6692528713087C48B0AC63D8D3EC5AB1', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 4, NULL, NULL, 2, NULL, 0, 'Beat it on low speed using an electric mixer for 30 seconds, then beat on medium speed for 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97A9F9295CFAA541B43F30E0DE7A481E', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 5, NULL, NULL, 2, NULL, 0, 'Evenly divide the batter and spread it into the bottom of the cake pans and bake for about 10 minutes until the tops just begin to brown. When you press a finger in the center of the cake, the indentation should spring back.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3A36E9F995B2244910B88B077250C12', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 6, NULL, NULL, 2, NULL, 0, 'Allow the cakes to cool for 5 minutes, then remove them from the pans and peel off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8184CDF37C115E4FBC2507B5B854CA95', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 7, NULL, NULL, 2, NULL, 0, 'Cut each cake into 8 wedges.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA06531F714A6341AA172955158A127B', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 8, NULL, NULL, 2, NULL, 0, 'Spray a baking sheet with coconut oil spray (or vegetable oil spray) and set the wedges on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDDF35FBD4FA62419419CC96DBAFF0B8', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 9, NULL, NULL, 2, NULL, 0, 'Spray the coconut oil over top of the wedges and return them to the oven for 10 minutes. Remove and flip the wedges over and bake for another 10-12 minutes until golden brown on both sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'073F167E8800B64A8CAB44DE669600BE', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 10, NULL, NULL, 2, NULL, 0, 'Remove and set on a cooling rack. Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91872463C7F4CB4C9C19E92881F5FD20', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 11, NULL, NULL, 2, NULL, 0, 'CARAMEL GLAZED APPLES & CARAMEL SAUCE:');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2522BB6DBAF8B41AD904C5949F2C002', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 12, NULL, NULL, 2, NULL, 0, 'Peel apples and cut into 1/4 - 1/2 inch cubes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1C6D07269611748A4413D9C9EB3BF69', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 13, NULL, NULL, 2, NULL, 0, 'Place in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'566DDDFB648FB34BB513CE4E993DD82B', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 14, NULL, NULL, 2, NULL, 0, 'Toss them with cinnamon apple juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD9949B3D0495E44AB1B71E16772CAB5', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 15, NULL, NULL, 2, NULL, 0, 'In a skillet, melt butter over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEE576D90CBE7C4292E4C0803E877455', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 16, NULL, NULL, 2, NULL, 0, 'Add brown sugar and stir constantly until the sugar begins to dissolve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'555ACF50BECB6B4186E43F91DB67E531', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 17, NULL, NULL, 2, NULL, 0, 'Add the apples and toss to coat. Continue to stir until the sugar completely dissolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9562D2DBA9A174ABF7027B752713F63', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 18, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium low and let apples simmer for 4-6 minutes, stirring occasionally, until the apples are tender.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A592F4E62D116428CFCE9E4061186BD', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 19, NULL, NULL, 2, NULL, 0, 'Pour the apples into a strainer set over a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A188289AB3D9B641BD81EE1899AEA93B', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 20, NULL, NULL, 2, NULL, 0, 'Let the syrup drain then pour it back into the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'576DA2E7EBA3194492EC2B6EFE99DF47', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 21, NULL, NULL, 2, NULL, 0, 'Increase heat to medium, and stir constantly until thickened into a nice caramel sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DA3E394A74CDA4E8BCAD631C79C0DBB', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 22, NULL, NULL, 2, NULL, 0, 'CHEESECAKE FLUFF:');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08C98F6113BD2743A003EA9B677A5994', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 23, NULL, NULL, 2, NULL, 0, 'Beat cream cheese with sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C7B49D4453BA44A8CBDAF91EC50E82B', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 24, NULL, NULL, 2, NULL, 0, 'Add whipped topping and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7614CD57A48DE64CBE7A06DAE87456E9', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 25, NULL, NULL, 2, NULL, 0, 'ASSEMBLE NACHOS:');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22ACA28D2881F14594E5BFE5FFB9CA30', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 26, NULL, NULL, 2, NULL, 0, 'Spread or pipe the cheesecake fluff over top of the cake chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F351EF7755177438372C9B0CDC526DE', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 27, NULL, NULL, 2, NULL, 0, 'Sprinkle on the caramel glazed apples.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'666D89174A957645BE0905056763F5BA', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 28, NULL, NULL, 2, NULL, 0, 'Drizzle with some chocolate sauce and the cooled caramel sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'842EC46585E1A240A69E0F7D28CA6AE4', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 29, NULL, NULL, 2, NULL, 0, 'Sprinkle on toasted pecan pieces and M&M''s® Pecan Pie.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2706C63B93034C4793CE279330F88B2A', X'0F7E3F152798CE45B5180AAB51DB940A', X'0F7E3F152798CE45B5180AAB51DB940A', 30, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81B9626A0ECC264EB38EB1C91B2891EE', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 0, NULL, NULL, 2, NULL, 0, 'Unwrap 3 Rice Krispies Treat Bars. Use a rolling pin or your fingers to flatten each bar out to a 3 1/4 inch by 2 inch rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51BBF917D60CB944A967FEF6B0D06A69', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 1, NULL, NULL, 2, NULL, 0, 'Press and pinch the short ends of two of the treats together creating a 6 1/2 inch by 2 inch rectangle, then curve the treats into a 2 inch tall cylinder and press the other ends together.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98277C648E841B4FA4BC9E5A736ECBBC', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 2, NULL, NULL, 2, NULL, 0, 'Set the Rice Krispies Treat cylinder on top of a third flattened treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76F5B5872111B247A9B0CC266357B676', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 3, NULL, NULL, 2, NULL, 0, 'Use a knife to cut the third treat out in a circle around the base of the cylinder.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'044EB84DB1D7414EB04BE2FB33F26FB7', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 4, NULL, NULL, 2, NULL, 0, 'Reserve the scraps to create your cup handles. Press the scraps together, roll into a log, then shape into a curved handle.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9718A0004AEC564CB62B265D2E9F05D3', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 8 cups and 8 handles.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C91A0BC2633094BB69BF3E0652C20EC', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 6, NULL, NULL, 2, NULL, 0, 'Place the white confectionery (candy) coating in a microwave safe bowl. Heat on high power for 20-30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F98557625A72FE4E8BA7DFA0B19A06A6', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 7, NULL, NULL, 2, NULL, 0, 'Use a pastry brush or food using only paint brush to brush a layer of confectionery coating all around the inside of each Rice Krispies Treat cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C373C0380A6E6F46BB106139E989C218', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 8, NULL, NULL, 2, NULL, 0, 'Allow the candy coating to dry for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73EA32AC6D782A45AE8ABE515648528A', X'79B8F655360A8547B6A3368F21C20A90', X'79B8F655360A8547B6A3368F21C20A90', 9, NULL, NULL, 2, NULL, 0, 'Use a small amount of the candy coating to attach a handle to each cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CD36DBC74D7704CB987C45F54BA0F26', X'9FA5963F0B475144961C4E3257E30689', X'9FA5963F0B475144961C4E3257E30689', 0, NULL, NULL, 2, NULL, 0, 'Spoon a heaping tablespoonful of white candy coating onto a parchment paper lined baking sheet. Spread it into a 3 1/4 inch circle (for a nice uniform looking circle, spoon it into a round metal cookie cutter.)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39319AD6DFE65148944258DFE01580EA', X'9FA5963F0B475144961C4E3257E30689', X'9FA5963F0B475144961C4E3257E30689', 1, NULL, NULL, 2, NULL, 0, 'Immediately while the candy coating is wet, arrange two colors of M&M''s in a single row around the edge of the circle. Create four circles using Plain M&M''s and four using Peanut Butter M&M''s, or combine them together.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9614078305E72D4D8476F1CD0A0B1ECC', X'9FA5963F0B475144961C4E3257E30689', X'9FA5963F0B475144961C4E3257E30689', 2, NULL, NULL, 2, NULL, 0, 'Freeze the candy 8 minutes until candy coating hardens. If using the metal cutters, simply pop the candy out once it''s hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA528F7AD0331848B650E05E2B0DA9A6', X'9FA5963F0B475144961C4E3257E30689', X'9FA5963F0B475144961C4E3257E30689', 3, NULL, NULL, 2, NULL, 0, 'Set one cup on a candy saucer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E27E1EB6D2D2CC47AC521FA9ADF68C3E', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 0, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream in a small saucepan over medium heat just until bubbles begin to form around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1C3A92B104C5E499E09B4EB47C936F2', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 1, NULL, NULL, 2, NULL, 0, 'Stir in the hot brewed coffee.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70F07C734674E749AC77B00EE2C23CFD', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 2, NULL, NULL, 2, NULL, 0, 'Remove from the heat and pour over the finely chopped semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4586A60275A429498230B0A8F09AFAAE', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 3, NULL, NULL, 2, NULL, 0, 'Let sit for 2 minutes, then stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B826EBECCD4B284DAF923074E82F3C66', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 4, NULL, NULL, 2, NULL, 0, 'Allow ganache to cool for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'500CB8107ECA9B479F2EDBFC256F1ABA', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 5, NULL, NULL, 2, NULL, 0, 'Assemble Cafe Mocha Rice Krispies Treat Cups and add Topping-');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E1D840929729042B725916321EDE997', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 6, NULL, NULL, 2, NULL, 0, 'Fill each Rice Krispies Treat Cup with Cafe Mocha Ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB01E44515BF074E817102A3584BE223', X'5BD8D086AA307046B41D53027ABE6FBE', X'5BD8D086AA307046B41D53027ABE6FBE', 7, NULL, NULL, 2, NULL, 0, 'Add three miniature marshmallows and serve with a spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5D8C9648BE7454F8896F2510E188712', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BFFCC3ADB6FFF4CB72F42B58F029F01', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 1, NULL, NULL, 2, NULL, 0, 'Chop almonds into small pieces and toss with flour and salt. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9C0DECBAEADEB47A45DEDE9FC23574B', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 2, NULL, NULL, 2, NULL, 0, 'Heat butter, corn syrup, and sugar in a small saucepan over medium high heat until butter melts. Stir gently. Let it come to a boil. Stir in floured almonds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5234003A94F7824C9572E3A93B949A14', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 3, NULL, NULL, 2, NULL, 0, 'Pour onto a parchment paper lined baking sheet and spread thin. Set another piece of paper on top and use a rolling pin to roll it out as thin as possible. Remove top piece of paper then place baking pan in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E8357D52E59E849B6EB0BE928036161', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until the edges start to look set and the bubbling starts to subside.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7544B5027A383F4A8BE8F6C3C337D810', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 5, NULL, NULL, 2, NULL, 0, 'Butter the cutting edge of a round metal cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B42150B111FBD54886C81347BC3449EB', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven, and immediately use a buttered round metal cookie cutter to cut circles in the hot candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3119B332774F41478D9DDFADA1A39120', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 7, NULL, NULL, 2, NULL, 0, 'If needed, pop the pan back in the oven for a minute to reheat the nougatine so you can cut 12 circles out.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A871817259B0064380167BEAAA193B97', X'01890CC8A1B8774E9717E18E23862158', X'01890CC8A1B8774E9717E18E23862158', 8, NULL, NULL, 2, NULL, 0, 'Use a metal spatula or knife to pick up a cut piece of nougatine and place it over a mini tart mold, reheat in oven for a minute or two, then indent the center using a tart tamper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A8616204E8DFA46B59F9DE4FAFAE68E', X'9E38526191ACAC4F9D73004CAAA799DA', X'9E38526191ACAC4F9D73004CAAA799DA', 0, NULL, NULL, 2, NULL, 0, 'Heat 1/4 cup heavy whipping cream and white chocolate in microwave on high for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53B81229290CA040AFDE9F473F856140', X'9E38526191ACAC4F9D73004CAAA799DA', X'9E38526191ACAC4F9D73004CAAA799DA', 1, NULL, NULL, 2, NULL, 0, 'Whisk remaining 1 1/4 cups heavy whipping cream to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA4643838F83BC47AF011287C72F8A58', X'9E38526191ACAC4F9D73004CAAA799DA', X'9E38526191ACAC4F9D73004CAAA799DA', 2, NULL, NULL, 2, NULL, 0, 'Fold cream into chocolate in 3 increments.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'460EF7913BDE164DBCFC2F5AD5C9D17B', X'9E38526191ACAC4F9D73004CAAA799DA', X'9E38526191ACAC4F9D73004CAAA799DA', 3, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into Cocoa Almond Nougatine cups. Top with a maraschino cherry.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'861E38C3D1AB3648A9C4A8B2B1965229', X'699529905C985F44BF80710D496B2030', X'699529905C985F44BF80710D496B2030', 0, NULL, NULL, 2, NULL, 0, 'Cream butter, brown sugar, and granulated sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66DDE880EE5FC54FA1801E8343AEDEC9', X'699529905C985F44BF80710D496B2030', X'699529905C985F44BF80710D496B2030', 1, NULL, NULL, 2, NULL, 0, 'Add egg, egg yolk, and vanilla and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64B64585F9610E4B8400EC310A1D3AAB', X'699529905C985F44BF80710D496B2030', X'699529905C985F44BF80710D496B2030', 2, NULL, NULL, 2, NULL, 0, 'Combine salt and baking soda with 1 1/2 cups of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DB75D74EF7F69459A733A202283AAA5', X'699529905C985F44BF80710D496B2030', X'699529905C985F44BF80710D496B2030', 3, NULL, NULL, 2, NULL, 0, 'Add to wet ingredients and stir just to combine. If the dough is sticky add the remaining 1/4 cup flour, if not, then just leave it out.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCC1818D601BCB499EE7B8686BD428D0', X'699529905C985F44BF80710D496B2030', X'699529905C985F44BF80710D496B2030', 4, NULL, NULL, 2, NULL, 0, 'Stir in mini chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDAD331C9706494EBDA9FDAD79CEB143', X'699529905C985F44BF80710D496B2030', X'699529905C985F44BF80710D496B2030', 5, NULL, NULL, 2, NULL, 0, 'Line glass bowl with non-stick tin foil. Roll out dough and drape over bowl. Freeze for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55B42676B8D67946AD12E494BD03CE83', X'699529905C985F44BF80710D496B2030', X'699529905C985F44BF80710D496B2030', 6, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees. Bake for 30-36 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C52C04F48725F545BBA04AA8F273E823', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 0, NULL, NULL, 2, NULL, 0, 'Cream butter, brown sugar and granulated sugar and beat on medium speed for about a minute until well blended. Add egg, egg yolk, and vanilla and beat until fully incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF7CAC4B6A3B3A4E8FD5DF05F47DC906', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 1, NULL, NULL, 2, NULL, 0, 'Mix flour, salt, and baking soda together then incorporate it into the wet ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98B710EBC9CC4241965815C68DA89F2D', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 2, NULL, NULL, 2, NULL, 0, 'Fold chocolate chips into the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15EDD920EE46714DA7E38A992DA85455', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 3, NULL, NULL, 2, NULL, 0, 'Scoop out 16-18 cookies, about 1/4 cup of dough for each. Place cookies on parchment paper lined baking sheets. Refrigerate for 30-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'043DADAFCD0E4443A708480A9B9D0DFE', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 4, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0F8EF02AC24084D9201B0248ED9729C', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 5, NULL, NULL, 2, NULL, 0, 'Bake cookies one tray at a time, for 12-16 minutes. The cookies should begin to set around the edges, be light golden brown, yet still be soft in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02FA01533868F74DAC8FC7849C849E26', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 6, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59C24F6ED598304B86605B854308A8EB', X'EBF0F60F4F08884E814D832903BB55AA', X'EBF0F60F4F08884E814D832903BB55AA', 7, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes to each cookie using melted confectionery coating. Pour remaining coating into a squeeze bottle or small zip top bag. Pipe smiles onto cookies. Let candy coating harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30135A3B137D8F4B936E918F390773A1', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 0, NULL, NULL, 2, NULL, 0, 'Melt ice cream in microwave on high for 20 seconds. Remove and stir, heat for 20-30 more seconds until completely melted and it just comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C81BD553091CF247B47B10D7F4BC4B39', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 1, NULL, NULL, 2, NULL, 0, 'Measure out 1/2 cup of melted ice cream and stir into the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9A1C89185463C4F83D288BBCC4ADEA1', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 2, NULL, NULL, 2, NULL, 0, 'If all the chocolate doesn''t melt, heat at high power for 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12E6D7E5317CED42ADC9BD047F5EC17A', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 3, NULL, NULL, 2, NULL, 0, 'Let it sit in the microwave for a minute, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95428AE6DB78D946A62EAAFF8B5F52A3', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 4, NULL, NULL, 2, NULL, 0, 'Heat again if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B613D9A65E81E248AADB0157E3B9A469', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 5, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool and thicken at room temperature for about 6 hours or in the refrigerator for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'208C1E1F1358BC4CA247BD87B90275E1', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 6, NULL, NULL, 2, NULL, 0, 'Scoop out using a small ice cream scoop (2 teaspoon size) or a spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'717C6425B0C1DF49909411443945F495', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 7, NULL, NULL, 2, NULL, 0, 'Roll each scoop into a ball and immediately roll in the chocolate shavings.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B93EA242101A6348A58C81787A60692E', X'635EB139F2812F43B2738EE6C3192C05', X'635EB139F2812F43B2738EE6C3192C05', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container at room temp for up to 2 weeks. Refrigerate up to 3 weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1FC765D2E76B841BC185CC8D1D202F8', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 0, NULL, NULL, 2, NULL, 0, 'Heat blueberries and water in a small saucepan over medium low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4C2CD540DE1F34EA1303A772074A312', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 1, NULL, NULL, 2, NULL, 0, 'Cook blueberries until they can easily be smashed, about 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D5DF3840F28864E8CDC49DF1E2498F2', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 2, NULL, NULL, 2, NULL, 0, 'Set a fine mesh strainer over a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'460CD52FD1C8904AA849E29E0FC406D3', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 3, NULL, NULL, 2, NULL, 0, 'Pour blueberries into strainer, and press until all the liquid flows into the bowl. Discard the solids.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BDD1F9D677D5148B1AAF7C05C141E20', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 4, NULL, NULL, 2, NULL, 0, 'Pour eggs and egg yolks into a 3 or 4 quart saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD6C196E3F3B474C93C3DC8C965E2D06', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 5, NULL, NULL, 2, NULL, 0, 'Whisk lightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23865BAE1F1C12408610835165982604', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 6, NULL, NULL, 2, NULL, 0, 'Continue to whisk and slowly drizzle in the hot blueberry puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D8B2FD432CC5447909356C0F0274045', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 7, NULL, NULL, 2, NULL, 0, 'Add the sugar, butter, and a pinch of salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC38F784E12B8646BE982123DD5E4D2A', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 8, NULL, NULL, 2, NULL, 0, 'Return to medium low heat and stirring constantly with a heat proof rubber spatula or a wooden spoon, cook until thickened, between 10-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C67754ABD66594FA972911E226F5FEC', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 9, NULL, NULL, 2, NULL, 0, 'The blueberry curd should coat the back of your spatula. The curd will continue to thicken as it cools.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5F0767E6A82634C9546F1CF75E7032F', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 10, NULL, NULL, 2, NULL, 0, 'Remove curd from heat and pour into a glass bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87B1FAB5A096404DB2262DD4A84AB124', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 11, NULL, NULL, 2, NULL, 0, 'Stir in lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0E591F06F393B4ABB81126C29E9CF62', X'871FF6C7592001459D22810DD3E82670', X'871FF6C7592001459D22810DD3E82670', 12, NULL, NULL, 2, NULL, 0, 'Cool at room temperature for 20 minutes, then refrigerate for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BD237944B2A4A42A1F3110BAEAC3A53', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9510E8E1E31D14FA91182709D0633D1', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 1, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, whisk the egg whites until foamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E657B12770B7E2459A16BB68572A9DBE', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 2, NULL, NULL, 2, NULL, 0, 'Add the coconut and almonds and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AB1208B570A714B8A9988635BFA589A', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 3, NULL, NULL, 2, NULL, 0, 'Mix in melted butter, vanilla and almond extracts.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98137BC01CE26542AB482FA1249F7090', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 4, NULL, NULL, 2, NULL, 0, 'SPRAY your non-stick muffin tins with cooking spray. Don''t forget this, or your coconut cups wont come out of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'891806A52772E54DBF9E135DF67962E8', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 5, NULL, NULL, 2, NULL, 0, 'Equally divide the coconut mixture among the 12 muffin cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE921AE914452A47BAB96F718B9F0F04', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 6, NULL, NULL, 2, NULL, 0, 'Wet your fingers slightly and press the coconut mixture into the bottom and up the sides of each muffin cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82A47875D233D2418FF29F63E9590031', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 7, NULL, NULL, 2, NULL, 0, 'Bake for 20-24 minutes until the edges of your cups are really golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47A1FF46E60E6B43A2BC0A36E26068BE', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 8, NULL, NULL, 2, NULL, 0, 'Remove and cool for 10 minutes in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03EFEB8CD5B76242A613F0E844C3BD63', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 9, NULL, NULL, 2, NULL, 0, 'Carefully run a thin knife around the top edge of each coconut cup. Allow to cool for 10 more minutes, then remove the cups from the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C78B8F8C3D34E489A61F8BD1DDFD3FB', X'1311C62F68DAE74B95D19B4E1F75C33C', X'1311C62F68DAE74B95D19B4E1F75C33C', 10, NULL, NULL, 2, NULL, 0, 'Allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BDE2D6052BA374588D181DE441136A6', X'713C4E9BB3A6044FBDBA8EA31E303B28', X'713C4E9BB3A6044FBDBA8EA31E303B28', 0, NULL, NULL, 2, NULL, 0, 'Equally divide the blueberry curd among the coconut macaroon cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC031937C428B34FB79B6F4BF00B9D6E', X'713C4E9BB3A6044FBDBA8EA31E303B28', X'713C4E9BB3A6044FBDBA8EA31E303B28', 1, NULL, NULL, 2, NULL, 0, 'Chill the cups for 15-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E27C7DB06CF9B24091EA4B4D46A4857C', X'713C4E9BB3A6044FBDBA8EA31E303B28', X'713C4E9BB3A6044FBDBA8EA31E303B28', 2, NULL, NULL, 2, NULL, 0, 'Just before serving top with whipped cream and a few fresh blueberries, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5C5E69EB922B94B89EAE069AFCE502A', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 0, NULL, NULL, 2, NULL, 0, 'Set your cream cheese, sour cream, and egg out on the counter to come to room temperature at least one hour, but preferably 2 hours, before you plan to bake your cheesecakes. You need to use room temperature ingredients!');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B993767E9A47654FA256404D15535F67', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 1, NULL, NULL, 2, NULL, 0, 'Place a rimmed cookie sheet or baking pan filled with 1/2-inch to 1-inch of water on the lowest rack in your oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91F09F27A34F2D49B540A465A1530B93', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 2, NULL, NULL, 2, NULL, 0, 'Arrange another rack in the middle of the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19C0499301EFA14F9A4F401F21E2A04D', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 3, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 300 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CACA255D6452344B7D71F7C93ACFC54', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 4, NULL, NULL, 2, NULL, 0, 'Crush 12 OREO Cookies into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D4325825DCB5540B17F418C3C92F2C4', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 5, NULL, NULL, 2, NULL, 0, 'Stir 2 tablespoons of melted butter into the cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F0A8C32A9F4DB42B8AFBF15534EB333', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 6, NULL, NULL, 2, NULL, 0, 'Scoop out about one tablespoon of the cookie crumbs and drop into a paper-lined cupcake pan. Repeat, filling 12 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B969C3E29D2D564D8F16182D522A88E3', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 7, NULL, NULL, 2, NULL, 0, 'Press the crumbs down into an even layer in the bottom of the cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CA07E88FF652E4C98C84C731B2F8A1C', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 8, NULL, NULL, 2, NULL, 0, 'Place 8 ounces of softened cream cheese and 1/3 cup granulated sugar in a large mixing bowl and beat using an electric mixer on low speed just until it is creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D518AFE2B52014BA9A9DD61FFE5A5DB', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 9, NULL, NULL, 2, NULL, 0, 'Add the room temperature sour cream and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C99B1A9883F87E4C88F98729B231F1C8', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 10, NULL, NULL, 2, NULL, 0, 'Stir in 4 ounces of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56BF1394452D204A9C410D088CCD449B', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 11, NULL, NULL, 2, NULL, 0, 'Then pour a lightly beaten, room temperature, egg over the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9B246CD52E01A41B08B5569F4BF6837', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 12, NULL, NULL, 2, NULL, 0, 'Stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39E3A7A5EF18F14A853D5F30AF4BDF22', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 13, NULL, NULL, 2, NULL, 0, 'Scoop a little more than 2 tablespoons of the chocolate cheesecake filling over each cookie crust and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51E179AEB230CC43A36C5D3421BC7DD9', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 14, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter about 10 times to allow any air bubbles to rise to the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58815086FC7F064D9D2E0DD7AE2C7999', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 15, NULL, NULL, 2, NULL, 0, 'Place on the middle rack in the oven and bake for 24-28 minutes until the tops look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'899B25C809100841B25965C2D5AD773C', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 16, NULL, NULL, 2, NULL, 0, 'The cheesecakes should still jiggle when the pan is jostled and will be glossy but should not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05C4B4E570C1A349A3ABFEB532B53E33', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 17, NULL, NULL, 2, NULL, 0, 'Turn the oven off, leave the cheesecakes inside, but crack the door open.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2624FFEA234A2742B4D6C1AB018171D1', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 18, NULL, NULL, 2, NULL, 0, 'Allow the cheesecakes to rest in the warm oven for 45 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A11565A0BD20CB48982C40C9AC1A4EB0', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 19, NULL, NULL, 2, NULL, 0, 'Remove and allow the cheesecakes to cool on the counter for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2523C334988DF4284E00A3439C2CE8B', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 20, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFAD5E035E3BAF44A10005702A368E8E', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 21, NULL, NULL, 2, NULL, 0, 'Remove from the pan and peel off the paper before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'669B635301FDC1428B8D5E40268BB2FC', X'B956C2732220F641A18A11279F0C0FB2', X'B956C2732220F641A18A11279F0C0FB2', 22, NULL, NULL, 2, NULL, 0, 'Add toppings, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7553EB47FF6D3D4D9481D91DF121C32E', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D1ACE2BB1D7544CAA8CCC884AC880BD', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 1, NULL, NULL, 2, NULL, 0, 'Butter the cavities in your molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC660452DDA2BE4E9325B4794713954C', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 2, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81CA0CFD6BDD4941A5569A3900B4B42C', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 3, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B52EDAC4A317E4893D94D9F5F268FD3', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 4, NULL, NULL, 2, NULL, 0, 'Add heavy whipping cream, eggs, and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B6B292185BDA84D82FA7679D5BD854B', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 5, NULL, NULL, 2, NULL, 0, 'Set brownie pop molds on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F98DC7F4C6FF3A4E80B9C68EDB5D0F04', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 6, NULL, NULL, 2, NULL, 0, 'Fill 18-22 brownie pop cavities with cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAD2E1058F357848AD7F780380A4BEFB', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 7, NULL, NULL, 2, NULL, 0, 'Bake for 22-25 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'059E71F4C9C1944194C30D9C83354974', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 8, NULL, NULL, 2, NULL, 0, 'Allow them to cool at room temperature for one hour, then freeze them for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0C15818D16E8A4DB3D5A77136B9332E', X'8DF7C7A194841F48BA4F2270B6D17819', X'8DF7C7A194841F48BA4F2270B6D17819', 9, NULL, NULL, 2, NULL, 0, 'Un-mold them onto small dessert plates.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'150844ADA69AEC4FAE2D4F0F42A12A48', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 0, NULL, NULL, 2, NULL, 0, 'Combine graham cracker crumbs, cinnamon, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97CE1AEC70D40B4AAD80D6AA10766145', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 1, NULL, NULL, 2, NULL, 0, 'Press the mixture into the bottom and up the sides of the PushPan or spring-form pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90B684CEC5A5FC48B463DFDC51D92CC9', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 2, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges begin to brown. Allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F67B0309ED504A4EA74C3F3F667498DD', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 3, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese on medium speed until smooth, scraping down the sides of the bowl and the beater as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1223EA9749C52A4296734AF0016E1A46', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'794EA2097D6CED4187A162BB2AE41D01', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 5, NULL, NULL, 2, NULL, 0, 'Add eggs one at a time and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79E05BC2124039448148BA7AEA94A383', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 6, NULL, NULL, 2, NULL, 0, 'Add sour cream and vanilla and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D24D80DB8138B43BA9F76A80CE0B5C8', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 7, NULL, NULL, 2, NULL, 0, 'Pour 1/3 cup of cheesecake batter into a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71B9003F84C2104A80E7A37F1253D841', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 8, NULL, NULL, 2, NULL, 0, 'Color it red.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42DEDDA44BF81A45B1D809A3B59906F5', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 9, NULL, NULL, 2, NULL, 0, 'Pour 2 tablespoons of batter into another bowl and color it blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0C345B5115CE6499FF0C8CEEF48395C', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 10, NULL, NULL, 2, NULL, 0, 'Pour the colored batter into a squeeze bottle (or use a pastry bag or zip-top bag.)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36D7FB37E517D34283799DC17FF97410', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 11, NULL, NULL, 2, NULL, 0, 'Create the corner of the flag using blue. You can add small little stars or one big star.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'120BDFBFEF4C7C44B0927B34C17E0EA7', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 12, NULL, NULL, 2, NULL, 0, 'Pipe red stripes across the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'266E1FF7A4B394459FF632B16DDF77AC', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 13, NULL, NULL, 2, NULL, 0, 'Bake in a water bath for 45-50 minutes until the top looks set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60627C9C1E59B0428338B2BDF3CA974B', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 14, NULL, NULL, 2, NULL, 0, 'The center of the cake will jiggle but not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'879CF1781001294C88AE4187380885DC', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 15, NULL, NULL, 2, NULL, 0, 'If you think it might not be quite ready but are worried you will over bake it, you can open the oven door, turn off the oven and leave the cheesecake in the water bath for a while.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BA9739A0C69244C8F120ED6620F331C', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 16, NULL, NULL, 2, NULL, 0, 'I''ve done this a few times and it works great. I''ve never had an over baked cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'837A614E31C68B42A2532DFC3AB32103', X'F671B9CACFA14244A9A60E5EF66CC7AB', X'F671B9CACFA14244A9A60E5EF66CC7AB', 17, NULL, NULL, 2, NULL, 0, 'Allow the cheesecake to cool for one hour at room temperature then refrigerate for at least 4 hours, preferably overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2275ADD2FD7C0041BA84C3388C6E0EA6', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 0, NULL, NULL, 2, NULL, 0, 'Set your ingredients (cream cheese, eggs, sour cream) out on the counter to come to room temperature at least 1 hour, preferably 2 hours before you plan to bake your mini cheeseccakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7888F4311F874B4583C5DB2A9E75D1C6', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 1, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'956CAEBCD1410B44ADD46C129EB25093', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 2, NULL, NULL, 2, NULL, 0, 'Stir together the graham cracker crumbs, brown sugar, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AB76166B980284289F84FDBB2F2D011', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 3, NULL, NULL, 2, NULL, 0, 'Press about 2 tablespoons of the crumbs into 18 paper-lined muffin cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B4A46C586F3494CBC885233DD1250E0', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 4, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on low speed, scraping down the bowl, as needed, until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFB47169CAAAB54A96BCA03F1C65806A', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 5, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8392D20E52292C40948B4E465A3F8188', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 6, NULL, NULL, 2, NULL, 0, 'Add egg and vanilla just until combined. Do not overmix at this point.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'372DFE64E5AC924D8D61201F94485265', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 7, NULL, NULL, 2, NULL, 0, 'Stir in the sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C92A6D9C68EE3249937BE4956B662751', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 8, NULL, NULL, 2, NULL, 0, 'Equally divide cheesecake filling among the 18 muffin cups, spooning about 2 tablespoons of filling into each.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'424C5D688230B6489C56163AE8DF2779', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 9, NULL, NULL, 2, NULL, 0, 'Bake on the middle rack in the oven for 15-18 minutes until the cheesecakes no longer look wet, but the center still jiggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FDF7D3AF8414D41A1BFFB8616C034F8', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 10, NULL, NULL, 2, NULL, 0, 'Remove and set on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9245B0BB6FB65F40899E803EFD9DD911', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 11, NULL, NULL, 2, NULL, 0, 'Allow to cool for about 45 minutes then refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0C3CEEBCAFE6C4BB0E349A1D5DAD7A3', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 12, NULL, NULL, 2, NULL, 0, 'Before serving, pop the cheesecakes into the freezer for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'366A8533481F44488F49A276472A95BF', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 13, NULL, NULL, 2, NULL, 0, 'Remove and immediately peel away the paper liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DA03959D171D444A981CEB5B04AE688', X'63DA66176D11E24999D74E874ED803B3', X'63DA66176D11E24999D74E874ED803B3', 14, NULL, NULL, 2, NULL, 0, 'Set on a serving dish or individual dessert plates and serve plain or add your favorite topping.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F796AB65638894C99D22962F9FB8A36', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees. Butter the cavities in your pumpkin molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'773EAC7253CB6A428D4792B8C6BE1530', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 1, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, sugar, heavy whipping cream, eggs, and vanilla in the bowl of a food processor, pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23B92408F984F940A444359DC88B5700', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 2, NULL, NULL, 2, NULL, 0, 'Alternatively, mix cream cheese and sugar together until well blended. Add whipping cream, eggs, and vanilla and mix until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24E50F952DB7A246B05208D3775A4FC1', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 3, NULL, NULL, 2, NULL, 0, 'Remove about 2 tablespoons of filling and color it green.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C433D4FE1D6E74B9286CD41614C11DA', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 4, NULL, NULL, 2, NULL, 0, 'Color the remaining filling using orange food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF7BB3371BABA440941472E637CE9B93', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 5, NULL, NULL, 2, NULL, 0, 'Paint the green filling into the stems of 40 cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A9CDC57B1B17E41B23FF51A051611DA', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 6, NULL, NULL, 2, NULL, 0, 'The filling is pretty thick and should stay in place nicely, but just to be safe, pop these filled molds into the freezer for 5 minutes before filling with the orange cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1F7DA0921474840AB52B0E3C8C13182', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 7, NULL, NULL, 2, NULL, 0, 'Set silicone pumpkin mold on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C692B1808D09FA4C93E45DB14A5BD5C1', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 8, NULL, NULL, 2, NULL, 0, 'Fill the pumpkins with orange cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33FDAF4F5A70E64BA8DCFB93FACCDEF8', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 9, NULL, NULL, 2, NULL, 0, 'Leave a small amount of room in each mold for the filling to expand while baking (about a 16th of an inch.)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D8998199F8C814AB52C70AEBD908153', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 10, NULL, NULL, 2, NULL, 0, 'Bake 12-14 mins until the tops look set but the cheesecake pumpkins are still jiggly.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5773245C15A51440BFA01A62E05A9DBB', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 11, NULL, NULL, 2, NULL, 0, 'Allow to cool for 30 minutes at room temperature, then place in the freezer for at least 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F22D452FDF2D6344BC377FF65FAF52A0', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 12, NULL, NULL, 2, NULL, 0, 'Remove the molds from the freezer, turn the mold upside down and press the pumpkin cheesecakes out.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4690B953A824D94184AACE8C8F248C9F', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 13, NULL, NULL, 2, NULL, 0, 'Store the mini cheesecake pumpkins in the refrigerator for up to 5 days or keep frozen for a few weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D39DC785EC854140B3901C103C7C6F81', X'C742D5417BF1EF459569AB1DDCB813DA', X'C742D5417BF1EF459569AB1DDCB813DA', 14, NULL, NULL, 2, NULL, 0, 'Before serving, decorate with mini chocolate chips, if desired. Simply press the chocolate chips into the cheesecakes creating jack-o-lantern faces.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95426D667BDB5143BE85E33A1A85668A', X'D3010DD9B30EA2458BFDFD5664E4B173', X'D3010DD9B30EA2458BFDFD5664E4B173', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9F6F1E3FBB505479777183A1E6988E7', X'D3010DD9B30EA2458BFDFD5664E4B173', X'D3010DD9B30EA2458BFDFD5664E4B173', 1, NULL, NULL, 2, NULL, 0, 'Add powdered sugar and beat until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5C31E67C1EBEE4FA974AD306C75D208', X'D3010DD9B30EA2458BFDFD5664E4B173', X'D3010DD9B30EA2458BFDFD5664E4B173', 2, NULL, NULL, 2, NULL, 0, 'Add vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DBC548779A50A43B45EC94F2E91648F', X'D3010DD9B30EA2458BFDFD5664E4B173', X'D3010DD9B30EA2458BFDFD5664E4B173', 3, NULL, NULL, 2, NULL, 0, 'Spoon in whipped topping and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34DD5E6B24A9A9479D6CE45C6DDD5155', X'D3010DD9B30EA2458BFDFD5664E4B173', X'D3010DD9B30EA2458BFDFD5664E4B173', 4, NULL, NULL, 2, NULL, 0, 'To thin out the mousse stir in the heavy whipping cream until really smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A899D60CA4DD14285B1CA5A95BC43E1', X'D3010DD9B30EA2458BFDFD5664E4B173', X'D3010DD9B30EA2458BFDFD5664E4B173', 5, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag with the cheesecake mousse and pipe it into 6 skull shaped glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05E96DA5B1F9654DBE85949A905237CA', X'D3010DD9B30EA2458BFDFD5664E4B173', X'D3010DD9B30EA2458BFDFD5664E4B173', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for up to 5 days until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'315BEF4575C73E45AB9A12E68D19B7CD', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 0, NULL, NULL, 2, NULL, 0, 'Press the thawed raspberries through a fine mesh sieve to remove seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85ABD0A59414FC49849EC6F45DA5AE4A', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 1, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'296C96103D1EB64DB1746D2E858C7273', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 2, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA69671523D9E049B3AF2AA0ADB75740', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 3, NULL, NULL, 2, NULL, 0, 'Beat in eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADBE19B851C80248A7AAE4AA942ECDDC', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 4, NULL, NULL, 2, NULL, 0, 'Pour in raspberry puree and lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F004839A235134A8C788D85C9BA5833', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 5, NULL, NULL, 2, NULL, 0, 'Beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2A0934B96DCAB4EB4CADFFD816C7014', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 6, NULL, NULL, 2, NULL, 0, 'Fill one silicone rose mold half way with the raspberry cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'091764FE75FD0C478B7C8BEF100041A6', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 7, NULL, NULL, 2, NULL, 0, 'Use a food-use-only paint brush to push the filling into all the small grooves in the rose mold and to pop any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52D2926944E3024ABEA1F10158064CD7', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 8, NULL, NULL, 2, NULL, 0, 'Fill the mold with more cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69ADEDA597F1394F872260B7A54AF2EC', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 9, NULL, NULL, 2, NULL, 0, 'Repeat filling a total of 10 rose molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A63C2C54CC078F4687B5E9826E65DD40', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 10, NULL, NULL, 2, NULL, 0, 'Set molds on a rimmed baking sheet or in a roasting pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0DAA7906CBBBB48BC0C7D8D5148765A', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 11, NULL, NULL, 2, NULL, 0, 'Set pan in the oven and fill the pan with very hot water until the water comes half way up the sides of the rose mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CD3C714672A92408B93629A2E67D9A4', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 12, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until the tops of the cheesecakes no longer look wet but the center still jiggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29A19C59D7550941B531D8D4BFF52366', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 13, NULL, NULL, 2, NULL, 0, 'Remove from the oven and from the water bath.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'383DAE3534E77C46919AEC05865AE791', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 14, NULL, NULL, 2, NULL, 0, 'Let cool at room temperature for 20 minutes then refrigerate for 30 minutes. Cover with plastic wrap and pop in the freezer for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2ED0DA3051F3AE4B9E67FE7B67998349', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 15, NULL, NULL, 2, NULL, 0, 'Remove from freezer and push the rose shaped cheesecakes out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A63EDCB948D734C8895338A6EE1EDA8', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 16, NULL, NULL, 2, NULL, 0, 'Immediately set on a small dessert plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC5AEA1C86DB2A46840188E2AD7642B2', X'068FC7A61C36B440A459FBEC06C11E7F', X'068FC7A61C36B440A459FBEC06C11E7F', 17, NULL, NULL, 2, NULL, 0, 'Allow to thaw at room temperature for about 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'405181122F6D6E488DE6D27068A15213', X'A6CF4B7EA54FC84D8A4A6B50A184C54E', X'A6CF4B7EA54FC84D8A4A6B50A184C54E', 0, NULL, NULL, 2, NULL, 0, 'Pour chocolate and heavy whipping cream into a microwave safe bowl and heat on high power for 30 seconds. Let rest for about 3 minutes then stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A209FBFA561E0747B0D484870D577EBE', X'A6CF4B7EA54FC84D8A4A6B50A184C54E', X'A6CF4B7EA54FC84D8A4A6B50A184C54E', 1, NULL, NULL, 2, NULL, 0, 'Pour into a squeeze bottle and let cool until slightly thickened.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F75FCEB29B78D44D830B544DBB3C9770', X'A6CF4B7EA54FC84D8A4A6B50A184C54E', X'A6CF4B7EA54FC84D8A4A6B50A184C54E', 2, NULL, NULL, 2, NULL, 0, 'Pipe stems and leaves, dots, or squiggles onto the plate before adding the cheesecake rose.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2335979F8809714DAA68F464936407C2', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FCC0F1A2EDEEE429A7D516F7C3A104F', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 1, NULL, NULL, 2, NULL, 0, 'Stir together the graham cracker crumbs, brown sugar, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4B6B159A0EA2645A955E1021A3E1348', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 2, NULL, NULL, 2, NULL, 0, 'Press about 2 tablespoons of the crumbs into 12 paper-lined muffin cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C39876E67E34F4381F5649ABFE1CDDB', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 3, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on medium speed, scraping down the bowl, as needed, until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0886C8BAE5880A4DA490C9DC554C7FD4', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D33FB43ACEC61D46B51610C458C8B084', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 5, NULL, NULL, 2, NULL, 0, 'Mix in the Dulce de Leche then add the vanilla and egg and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87C278F82661E945987108894B0E1358', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 6, NULL, NULL, 2, NULL, 0, 'Stir in the sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B17346B56E1144DA46AF7BDB25FCA36', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 7, NULL, NULL, 2, NULL, 0, 'Equally divide cheesecake filling among the 12 muffin cups, spooning about 2 tablespoons of filling into each.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7168B9A9979BBA4A9D836B925F4DF4BD', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 8, NULL, NULL, 2, NULL, 0, 'Bake on the middle rack in the oven for 14-16 minutes until the cheesecakes no longer look wet, but the center still jiggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F07BD113E222C4BB17891B4629FE424', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 9, NULL, NULL, 2, NULL, 0, 'Remove and set on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71DBF9DEB5B8014BAA6DADBDC6B566FD', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 10, NULL, NULL, 2, NULL, 0, 'Allow to cool for about 45 minutes then refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47BB9529449BAB4185E800D889493232', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 11, NULL, NULL, 2, NULL, 0, 'Before serving, pop the cheesecakes into the freezer for 15 minutes (this will help the wrappers peel off easier).');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB1D36CFD195C1449F3925172530D8D2', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 12, NULL, NULL, 2, NULL, 0, 'Remove and immediately peel away the paper liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52BC32BD6A19A84E900B2DDC13C94CF4', X'2CBB6B0C40836F45B8BD3925897F93D5', X'2CBB6B0C40836F45B8BD3925897F93D5', 13, NULL, NULL, 2, NULL, 0, 'Set on a serving dish or individual dessert plates and serve plain or topped with a dollop of whipped cream, and drizzle of warmed Dulce de Leche, and a sprinkle of toffee bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E00E73A19B90394E8EE92C660E86CD66', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 Fahrenheit');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF052947A6B3A44886887439D03CBBB7', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 1, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on medium speed until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94879A30A3623C41B15A8777B77E0CCB', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 2, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA658301E55A5C4180A2E297D2A4D2D9', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 3, NULL, NULL, 2, NULL, 0, 'Add eggs one at a time and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3491DEA63B8BD84FB6DC13B1C92E6C96', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 4, NULL, NULL, 2, NULL, 0, 'Add sour cream and vanilla and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'614FC5271143884EA144BF606FE35064', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 5, NULL, NULL, 2, NULL, 0, 'Spoon 2 teaspoons of filling into 6 small bowls and 1 teaspoon of filling into 1 small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79E4E67A97534D48B382F25ED62E601A', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 6, NULL, NULL, 2, NULL, 0, 'Color one bowl of filling purple, one blue, one indigo, one green, one yellow, one orange, and one red.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'761F129CBDE2EE498A2DAD90277F54F2', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 7, NULL, NULL, 2, NULL, 0, 'Paint two letters using 6 of the colors and one letter using one of the colors (the bowl with just 1 teaspoon of filling).');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3702AC877E4EB845AA81755650EC6591', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 8, NULL, NULL, 2, NULL, 0, 'Use food use only paint brushes to paint the color into the letter cavities in the silicone mold. Fill the cavity completely so you get nice brightly colored letters.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD2DCDB2789BE146B73864EFDE4D30AE', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 9, NULL, NULL, 2, NULL, 0, 'Freeze the painted mold for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CD98D94BD2E7B45B293C850B9697571', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 10, NULL, NULL, 2, NULL, 0, 'Remove and pour the remaining cheesecake filling in the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B200A5183F8C7F438E404C05A0E5DADF', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 11, NULL, NULL, 2, NULL, 0, 'Bake your cheesecake in a water bath for 42-48 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DC40A524138824483E2D455E0278D87', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 12, NULL, NULL, 2, NULL, 0, 'The top will still jiggle, but look shiny and set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6D9FEE45E0A9C4EB7EC3F2266EB097B', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 13, NULL, NULL, 2, NULL, 0, 'Remove from oven and set on a cooling rack for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A54F2C5202780E4492AFE261D837D260', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 14, NULL, NULL, 2, NULL, 0, 'Wrap in plastic wrap and freeze for at least 6 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BC8B414C1A31844ADA3483A94612F73', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 15, NULL, NULL, 2, NULL, 0, 'Remove and peel the mold off the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B27AED439ED5647BACBFC0AEE3C23E9', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 16, NULL, NULL, 2, NULL, 0, 'Set on a serving platter or cake stand.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'415EBCE1C7CA3C418731A693D934E088', X'3F2C52D98713EE49A6DDA582FD75E6FD', X'3F2C52D98713EE49A6DDA582FD75E6FD', 17, NULL, NULL, 2, NULL, 0, 'The cheesecake will take 2-3 hours to thaw at room temperature or it can be refrigerated overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1296AEE9F7EEE149B2B2F49DCF19D7A8', X'43B32F9DC7A17C41864A0BEF3FA7C93D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1074D61BE36484FA12F2A5B95E190B5', X'43B32F9DC7A17C41864A0BEF3FA7C93D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', 1, NULL, NULL, 2, NULL, 0, 'Stir together graham cracker crumbs and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0581E310C27E4C4F8C182EE57AE46E4D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', 2, NULL, NULL, 2, NULL, 0, 'Spoon a heaping tablespoon of crumbs into each of 18 round cavities in 3 silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A889D766FF96CB4A8BCFFB227AF6381D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', 3, NULL, NULL, 2, NULL, 0, 'Press crumbs into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'114B95B887CA5040A4E28CA2D7909CD1', X'43B32F9DC7A17C41864A0BEF3FA7C93D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', 4, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until fragrant and golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF15411085A97C42A3C7AF9CCD5A9ADB', X'43B32F9DC7A17C41864A0BEF3FA7C93D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', 5, NULL, NULL, 2, NULL, 0, 'Remove and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C52A3DF9CAEB8D439F151AF3C485F12E', X'43B32F9DC7A17C41864A0BEF3FA7C93D', X'43B32F9DC7A17C41864A0BEF3FA7C93D', 6, NULL, NULL, 2, NULL, 0, 'Set molds on baking pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E754CF9D716CD4497B22F4122217E6D', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEB4F0BF4C287240B2B898FD8DF8F5A3', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 1, NULL, NULL, 2, NULL, 0, 'Add sugar, eggs, heavy whipping cream, and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D29F4E5CD1D364FB7BC8893FE07B4BB', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 2, NULL, NULL, 2, NULL, 0, 'Spoon 1/3 cup of filling into a bowl and color it blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A088D3438B6564DA468F2B232B7B356', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 3, NULL, NULL, 2, NULL, 0, 'Repeat, coloring 1/3 cup red, and another 1/3 cup yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A113FAB60ACED458D70F9D96507D26C', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 4, NULL, NULL, 2, NULL, 0, 'Color 3 tablespoons of filling orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D9F2A2C8B61434C8EA4A2C3B9A5BD2F', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 5, NULL, NULL, 2, NULL, 0, 'Tap the bowls on the counter several times to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'863E03E7CAB3F74CB55BB206338B6924', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 6, NULL, NULL, 2, NULL, 0, 'Pour individual colors of filling into squeeze bottles.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'653C81CFE15B844094F1066FB4F7D2A8', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 7, NULL, NULL, 2, NULL, 0, 'Spoon the plain filling over top of each of the crusts, diving filling equally.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6AFAA53AF3B6044BCB17F51D4744135', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 8, NULL, NULL, 2, NULL, 0, 'Pipe red, blue, and yellow beach ball panels onto the plain filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57A95C95A99B4641B7F3F3D821B06D78', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 9, NULL, NULL, 2, NULL, 0, 'Pipe an orange circle in the middle of the cheesecake beach balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'732B4E629D43324790D047F18054F9CD', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 10, NULL, NULL, 2, NULL, 0, 'Set pans in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABF107E8D6BF20409724872E9E5D6E70', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 11, NULL, NULL, 2, NULL, 0, 'Pour water into the pan, being careful not to pour any onto the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'620462BDF36C4E4DA654E1A85AE013F6', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 12, NULL, NULL, 2, NULL, 0, 'Bake for 16-22 minutes until the edges of the cheesecake look set. The center will still be jiggly.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7E0C7DB4F2856479750C48FF7344E21', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 13, NULL, NULL, 2, NULL, 0, 'Remove molds from water bath, set on a cooling rack, and cool for 30 minutes .');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00635E81265F3B40812FD8D4FF731532', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 14, NULL, NULL, 2, NULL, 0, 'Freeze for at least an hour until firm enough to remove from molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62D4E424EF905A4E9A5D3308C95361E7', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 15, NULL, NULL, 2, NULL, 0, 'Remove and push cheesecakes out of silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8C2D834E7003B4A9EB3E1939BFE1412', X'A20417CA79F50045ADE4247E81FF1FAF', X'A20417CA79F50045ADE4247E81FF1FAF', 16, NULL, NULL, 2, NULL, 0, 'Cover with paper towels and refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60229DCD4048D345BEF09B8EA317A6FF', X'A3E3E3C528184F4FB47E4F1BC75324A1', X'A3E3E3C528184F4FB47E4F1BC75324A1', 0, NULL, NULL, 2, NULL, 0, 'Place a roasting pan filled with enough water to reach half way up the side of a silicone Mickey Mouse mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E4A0F30F617CE4CBF6C4FE46604AFAA', X'A3E3E3C528184F4FB47E4F1BC75324A1', X'A3E3E3C528184F4FB47E4F1BC75324A1', 1, NULL, NULL, 2, NULL, 0, 'Place roasting pan in oven and heat to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C66897C9211333428CEC92CEBE7D5858', X'26B41D0834ECD84C9780FC1C5EFB9920', X'26B41D0834ECD84C9780FC1C5EFB9920', 0, NULL, NULL, 2, NULL, 0, 'Combine heavy whipping cream and finely chopped semi-sweet chocolate in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'554B7B53CEA2284F99F75D7AF3501A15', X'26B41D0834ECD84C9780FC1C5EFB9920', X'26B41D0834ECD84C9780FC1C5EFB9920', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6CCD0EF35F0384D9CE3BA4288BFAED4', X'26B41D0834ECD84C9780FC1C5EFB9920', X'26B41D0834ECD84C9780FC1C5EFB9920', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59B89BB8133079479B7C8F5DE5EAA92F', X'26B41D0834ECD84C9780FC1C5EFB9920', X'26B41D0834ECD84C9780FC1C5EFB9920', 3, NULL, NULL, 2, NULL, 0, 'Let sit for 3 minutes then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E29B6499F033F44800938FE5683ADC2', X'26B41D0834ECD84C9780FC1C5EFB9920', X'26B41D0834ECD84C9780FC1C5EFB9920', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12551AF735721C44A9DBEAE0AD44C1F2', X'26B41D0834ECD84C9780FC1C5EFB9920', X'26B41D0834ECD84C9780FC1C5EFB9920', 5, NULL, NULL, 2, NULL, 0, 'Set aside to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C752F71FCFC6C446AD9E8FF4ECA9D7CF', X'26B41D0834ECD84C9780FC1C5EFB9920', X'26B41D0834ECD84C9780FC1C5EFB9920', 6, NULL, NULL, 2, NULL, 0, 'Pour 2/3 cup ganache into a bowl and reserve for the crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D96D9207DDC3C04B91A31C74FD53E82D', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 0, NULL, NULL, 2, NULL, 0, 'Either use a food processor or an electric mixer and beat cream cheese until really light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B1F3F8AF8E38648803CEEADC2BCEEC4', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 1, NULL, NULL, 2, NULL, 0, 'Be sure all the lumps of cream cheese are gone.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'390A9ED56AB9EA4483D8E7299C6436BD', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 2, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6D6F4EF6B3DDF40B092C46D20174315', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 3, NULL, NULL, 2, NULL, 0, 'Add eggs, heavy whipping cream, and vanilla and beat just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A79AC8FDCE857468E29462B926BD217', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 4, NULL, NULL, 2, NULL, 0, 'Pour 1/4 cup of filling into one small bowl and color peach.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C678442B5C14134DAD20DE3B263EE0FA', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 5, NULL, NULL, 2, NULL, 0, 'Pour 3/4 teaspoon into another bowl and color bright red.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A27AB9B44A00D64B8099132E0081D19F', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 6, NULL, NULL, 2, NULL, 0, 'Pour 2 teaspoons of filling into a final bowl and leave white.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DA979E8BD2F0D4583EDB31E3D99B98D', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 7, NULL, NULL, 2, NULL, 0, 'Pour the cooled chocolate ganache into the remaining cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34EBE0722F3C41468613A00B7C85BCAB', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 8, NULL, NULL, 2, NULL, 0, 'Use some food use only paint brushes or squeeze-it mold painter (squeeze bottles) to paint or pipe chocolate cheesecake filling into a Mickey Mouse silicone mold. Paint the nose, pupils, and outlines around the eyes and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE57848D7C1F1549A7765F113D266864', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 9, NULL, NULL, 2, NULL, 0, 'Pop the mold in the freezer for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48229098A1A56C4CB6A6EA4D382775FA', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 10, NULL, NULL, 2, NULL, 0, 'Remove and paint the red tongue and white eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56E60FEA8354EA40A31D7D912105FE06', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E14069889606F44828C8AED0896FF40', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 12, NULL, NULL, 2, NULL, 0, 'Remove and paint the peach face being sure to make it thick.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32396177B7ECEA429F47C29533651D80', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 13, NULL, NULL, 2, NULL, 0, 'Freeze for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC7ED8F9EB638745A80F6892A6EE01EE', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 14, NULL, NULL, 2, NULL, 0, 'Remove and pipe or paint chocolate filling around the face then pour the remaining chocolate filling over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'691B4121108D9346B710B71069404C6B', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 15, NULL, NULL, 2, NULL, 0, 'Place the mold in a the hot water in the roasting pan in the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7D0B0A27047614698DD4FEC90A29DCF', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 16, NULL, NULL, 2, NULL, 0, 'Bake for 38-42 minutes until the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99554F56CBFA4C419E1E222787FB7041', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 17, NULL, NULL, 2, NULL, 0, 'The center will still be jiggly, but should not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FBE6D0F1B5E8342A998904E570636B5', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 18, NULL, NULL, 2, NULL, 0, 'Remove from oven and water bath and set on a cooling rack to cool for one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0515EDC370AE9C4DABBC4AE729D826D5', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 19, NULL, NULL, 2, NULL, 0, 'Reheat the reserved chocolate ganache in the microwave for 10-15 seconds until it''s fluid.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85953880783BC6499ACBF97CC4596DF6', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 20, NULL, NULL, 2, NULL, 0, 'Crush Oreo Cookies in food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECF8735810772545B641B961D25F5F39', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 21, NULL, NULL, 2, NULL, 0, 'Add 1 1/2 tablespoons of the chocolate ganache and pulse to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B9F21E598F0684A8517D185FF1AFF30', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 22, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate ganache over the cooled cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48624219357DE44EA0AADDE5705EA6B8', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 23, NULL, NULL, 2, NULL, 0, 'Sprinkle the cookie crumbs over the ganache and pat into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12EEF49BACBDC54CAE31DF89CBE1C781', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 24, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1BD572132D559408EBCED1DE09782D7', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 25, NULL, NULL, 2, NULL, 0, 'Remove and un-mold cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'953F1B064D328D4EBDC80C12F32C4615', X'F89FF2D78CFC684FA5C37A6AEBC26C71', X'F89FF2D78CFC684FA5C37A6AEBC26C71', 26, NULL, NULL, 2, NULL, 0, 'Allow to thaw before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8022FCFFDC661F4CA234554952A4FC35', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'987D8000BB3AD841951556B5427E2EC4', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 1, NULL, NULL, 2, NULL, 0, 'Stir together graham cracker crumbs and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22FF88CF91F11C40A9DD627BBC75D790', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 2, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoon of crumbs into each of 16 oval shaped cavities in 2 silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71E69349843C9C48AACEEBA3D12A86CA', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 3, NULL, NULL, 2, NULL, 0, 'Press crumbs into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'155C9B24F0AD7241A73A2C29000C5D8A', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 4, NULL, NULL, 2, NULL, 0, 'Bake for 8-12 minutes until fragrant and golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B57F1B5B0112B843A0F2076F27A750F6', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 5, NULL, NULL, 2, NULL, 0, 'Remove and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1F476C12DBCD74687B858B693BCEA51', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 6, NULL, NULL, 2, NULL, 0, 'Set molds on baking pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0822EC67A7108042984C6F5117A4CABD', X'93EAC7A0C04EE14E9773D9D8001159D8', X'93EAC7A0C04EE14E9773D9D8001159D8', 7, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4A819C1F8737742B25A312160CECB59', X'78CDC8E6D296284EBE91B782AB344B7A', X'78CDC8E6D296284EBE91B782AB344B7A', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AE9E90774B5BB4DBCD9A16E2EB8F12A', X'78CDC8E6D296284EBE91B782AB344B7A', X'78CDC8E6D296284EBE91B782AB344B7A', 1, NULL, NULL, 2, NULL, 0, 'Add sugar, eggs, heavy whipping cream, and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D14AFA4E65101349A772BDE86FDD977D', X'78CDC8E6D296284EBE91B782AB344B7A', X'78CDC8E6D296284EBE91B782AB344B7A', 2, NULL, NULL, 2, NULL, 0, 'Equally diving filling into 4 bowls.');
