INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF6C75FE0BF4B343803C6CB0CF914FC1', X'78CDC8E6D296284EBE91B782AB344B7A', X'78CDC8E6D296284EBE91B782AB344B7A', 4, NULL, NULL, 2, NULL, 0, 'Color one bowl pink, one blue, one yellow, and one purple using food coloring.');
