INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B138383B1BD5DE48B9B5BFB7223ADFC3', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 11, NULL, NULL, 2, NULL, 0, 'Add sour cream and the 3 flavorings and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC539A0842A62843AEA7EF6DB50BEEBF', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 12, NULL, NULL, 2, NULL, 0, 'Add desired color. (1 drop orange for light peach skin tone)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC23E800404E594EAB793EA983A88135', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 13, NULL, NULL, 2, NULL, 0, 'Scoop out 1 1/2 tablespoons of filing and place in a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53081F4308D40A4F98C5380C133E930C', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 14, NULL, NULL, 2, NULL, 0, 'Color using several drops of black coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B9C14EF2626D94CB41FC3E33114C626', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 15, NULL, NULL, 2, NULL, 0, 'Spoon into a small zip top bag or pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0544302470836B4F814712D018213A7B', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 16, NULL, NULL, 2, NULL, 0, 'If making a baby girl, also color 1 1/2 tablespoons of filling pink.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1228C9C00573954485E85E59CBC8A20E', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 17, NULL, NULL, 2, NULL, 0, 'Spoon into a bag also.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33A9B94645B76F4994DE39E8B4173F7C', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 18, NULL, NULL, 2, NULL, 0, 'Pour the remaining cheesecake filing over crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F98355514D1423498CF03C558C15F3F7', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 19, NULL, NULL, 2, NULL, 0, 'Pipe on two eyes, two eyebrows, a wisp of hair and a smile using the black filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A46FFDE9BEFBDF45A2CF5DD779D215F2', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 20, NULL, NULL, 2, NULL, 0, 'If making a girl, you can add rosy cheeks and a pink bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DB7D5637DFE114CB6E8D18FC9B1DA9E', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 21, NULL, NULL, 2, NULL, 0, 'Set cheesecake in roasting pan filled with water and bake for 45-50 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C66C2D2CEB36A54AA5FC1F1D004D224C', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 22, NULL, NULL, 2, NULL, 0, 'The top of the cheesecake should look set but still wiggle.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2D12AE2E40DCA49A18AA70C49F328D3', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 23, NULL, NULL, 2, NULL, 0, 'Turn off oven, crack open door, and let cheesecake sit for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'385C0DD0D151F74483D6E357C080F6C7', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 24, NULL, NULL, 2, NULL, 0, 'Remove cheesecake from water bath and allow to cool for 1 hour at room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA5C9D72828B274B8E5E0C377363D2F0', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 25, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 6 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'562B7B054827B442973D731CF33F237D', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 26, NULL, NULL, 2, NULL, 0, 'DECORATIONS:');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99304D7E46F3714A8AF8F59D94275062', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 27, NULL, NULL, 2, NULL, 0, 'Roll out blue or pink fondant and cut out one 1 1/2 inch round circle. Cut out a pacifier handle. Attach the handle to the circle using a dab of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F45AD36FFAC9EC46B5AF42120C4F9ED9', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 28, NULL, NULL, 2, NULL, 0, 'Roll out skin toned fondant and cut out two ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECE789C5359F3444A576DF140393F9D5', X'1100111AE55EA94EB722FDE705131EBC', X'1100111AE55EA94EB722FDE705131EBC', 29, NULL, NULL, 2, NULL, 0, 'Just before serving, attach the two ears to the side of the cheesecake and add a pacifier in the baby''s mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4787D2AD3CF62742BDE09D6978804772', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'554C7712B71BE94A90C468A63EB6BC0D', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 1, NULL, NULL, 2, NULL, 0, 'Spray the springform pan with non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EABF527D5DB6444CB5D04C848108481E', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 2, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EDB921177997A46B78FFEC1F6DC794A', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 3, NULL, NULL, 2, NULL, 0, 'Pour the Town House Pretzel Thins in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48C051006E62C54F94C79E9997D47D1B', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 4, NULL, NULL, 2, NULL, 0, 'Pulse to fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF71ED395C19A041B891C22B06927C31', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 5, NULL, NULL, 2, NULL, 0, 'Add Parmesan Cheese and butter and pulse until it resembles wet sand.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69E360DC990E4842809CB941287FA1B2', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 6, NULL, NULL, 2, NULL, 0, 'Press crumb mixture into the bottom of the springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B99453ADED46994188123457211CCCB0', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 7, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until the crust starts to brown around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF2044F9C2600C4D886569211CA9E04B', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 8, NULL, NULL, 2, NULL, 0, 'Cut two ounces of cream cheese off one block and reserve for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8714F7296AEA924D8F3E95AF57F1AFF9', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 9, NULL, NULL, 2, NULL, 0, 'Clean food processor bowl, and place remaining 14 ounces of cream cheese inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FE6C1EE72397A46B00855761220D009', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 10, NULL, NULL, 2, NULL, 0, 'Pulse until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E75BC3E90D9DA4589E13275D887E752', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 11, NULL, NULL, 2, NULL, 0, 'Add the sour cream, Parmesan cheese, roasted garlic, egg, egg yolk, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F89FEF794007DA45BE5B578E59C85806', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 12, NULL, NULL, 2, NULL, 0, 'Pulse until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'448962B176E4F24983D2E30BB1951F3F', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 13, NULL, NULL, 2, NULL, 0, 'Add artichokes, and pulse until chunky.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0D704685CACE445B850B407FE71A899', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 14, NULL, NULL, 2, NULL, 0, 'Add more salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32108531C55EE24F861736F166B29841', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 15, NULL, NULL, 2, NULL, 0, 'Pour cheesecake filling over crust and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2958D60C78CAB846AD5F64F419E7BCBE', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 16, NULL, NULL, 2, NULL, 0, 'Wrap bottom of springform pan in tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75D7C4A22EDE0C4CB848504437229FDF', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 17, NULL, NULL, 2, NULL, 0, 'Set in the 9x13 baking pan, set it in the hot oven, pour hot water into the baking pan, being careful to not spill any into the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B34DB7956CDE504290FBA48770CD3F70', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 18, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33B626A0339C384188409FB7C9F72303', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 19, NULL, NULL, 2, NULL, 0, 'Turn off oven, open oven door, and allow cheesecake to remain in oven for 15 more minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17E0E9DC930CFE4590ADA2B719C4DA87', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 20, NULL, NULL, 2, NULL, 0, 'Remove cheesecake from water bath and allow it to cool at room temperature for an hour. Refrigerate for at least 4 hours or up to several days.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F44A4CB37875FA469BFBDEEE8E99A6FB', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 21, NULL, NULL, 2, NULL, 0, 'Remove from springform pan and set on a serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A9B3F39C4D3D644AB0D07A9FF96A24F', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 22, NULL, NULL, 2, NULL, 0, 'Stir black food coloring into the reserved 2 ounces of cream cheese and put it in a pastry bag fitted with a #3 pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADA4DD0AE5A0B741A4B29B4C89D33C2E', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 23, NULL, NULL, 2, NULL, 0, 'Pipe on 1 through 12 onto the round cheesecake in a clock sequence.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEE27EADDB2AAD49BA449A042A276F02', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 24, NULL, NULL, 2, NULL, 0, 'Change the tips and put the #6 tip on the pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E96B93B3BD70445902DBA0E7B23F20D', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 25, NULL, NULL, 2, NULL, 0, 'Pipe on the hands of the clock.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'621E7A2FADE62B40A80BEA094C1A80CF', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 26, NULL, NULL, 2, NULL, 0, 'Refrigerate until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F34F6C1BD2F8641BBE16EF6B7D1B1A4', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 27, NULL, NULL, 2, NULL, 0, 'I popped my cheesecake in the freezer and will thaw it on New Year''s Eve before the party.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22D5CF82A5DD454D930873BF2AA9F395', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 28, NULL, NULL, 2, NULL, 0, 'You can serve the cheesecake all on it''s own, or with more Town House Sea Salt Pretzel Thins or some Town House Sea Salt and Olive Oil Flatbread Crisps.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B071198852F32D429EC62FD2D5EB221F', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 29, NULL, NULL, 2, NULL, 0, 'To make the chocolate ganache, heat 6 ounces of semi-sweet chocolate with 1/2 cup heavy whipping cream on high in the microwave for 30 seconds, then stir, and heat for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA17890ACBD2D047BB1641F8BB067C6F', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 30, NULL, NULL, 2, NULL, 0, 'Serve with Town House Sea Salt Pretzel Thins.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA610C1838C3DA4093B42522F34E30A3', X'BF2B57C64CB3914F8745DD23E762932A', X'BF2B57C64CB3914F8745DD23E762932A', 31, NULL, NULL, 2, NULL, 0, 'To make my cheese balls, I crushed 10 Town House Sea Salt and Olive Oil Flatbread Crisps to fine crumbs then blended 4 ounces cream cheese with 4 ounces garlic and herb Boursin cheese, 1/4 cup Parmesan cheese, and 4 ounces Italian blend shredded cheese. I scooped out a tablespoonful and wrapped it around a black stir stick and sprinkled on the crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5CE83D45B915145BAD86DC09E185125', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees. Butter the cavities in your molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFD5AA6D1CB94345BF8529133DFF0957', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 1, NULL, NULL, 2, NULL, 0, 'Heat heavy whipping cream and chocolate on high in the microwave for 20-second intervals, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE563304D9271D4FB22B02C6B94A7D06', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 2, NULL, NULL, 2, NULL, 0, 'Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0180151EBDDA145BEB93809AF25EEAA', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 3, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'606C5C0F958E3F4FB53E807D977618D9', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6FD0F0547B20E4F8AF690E6FE3935CF', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 5, NULL, NULL, 2, NULL, 0, 'Add Eggland''s Best Eggs, one at a time until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'355F876E5008494D8582AC6951899488', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 6, NULL, NULL, 2, NULL, 0, 'Add the vanilla then pour in the chocolate and beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'891232008228DE4DB9718B7C7B037FEA', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 7, NULL, NULL, 2, NULL, 0, 'Set the silicone molds on a baking sheet. Pour the chocolate cheesecake filling into the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A2213AB67C84043B1DF692D19369937', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 8, NULL, NULL, 2, NULL, 0, 'Bake for 22 minutes, then turn the oven off, open the oven door, and allow the cheesecake to remain in the oven for 10 minutes or until they look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2238BD4433330A449051CF92C6338CFD', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 9, NULL, NULL, 2, NULL, 0, 'Remove them from the oven and allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40E77FE4A2EC8147A3A945DE506FDE47', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 10, NULL, NULL, 2, NULL, 0, 'Pop them in the freezer for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D930BADB0D5E9F43A64A5F9061924CE4', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 11, NULL, NULL, 2, NULL, 0, 'Once frozen, un-mold them by pulling back on the silicone mold and pushing the cheesecake out.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CA8C58AB95E2747A9074F9E5BD568FE', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 12, NULL, NULL, 2, NULL, 0, 'Put them on small individual serving dishes and allow them to thaw.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5028080D699634CBE077488F4EB84BE', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 13, NULL, NULL, 2, NULL, 0, 'At this point, you can store the cheesecakes in the refrigerator for up to five days or put them back in the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D91C1135D930C141A6B5F42A1FEBAB5B', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 14, NULL, NULL, 2, NULL, 0, 'To make the turkey''s feathers, use a fluted biscuit cutter and simply press down onto the graham cracker and they will cut. Cut out about 3/4 of the circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE9B00B81C1C3849823252DC8A3B0687', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 15, NULL, NULL, 2, NULL, 0, 'When you are ready to decorate, press one tail feather onto the back of each cheesecake then add two candy eyes, one orange sunflower seed beak, and one red sunflower seed waddle. Do not decorate frozen cheesecakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A25FF354040F0547A8FD936D6400BB45', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 16, NULL, NULL, 2, NULL, 0, 'You can decorate your cheesecake turkeys a few hours before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C385B55A15AB241AB5FA9607F6087B3', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 17, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'188A1C66C1EEB74CA876982505D5875D', X'5291DD91A3BC854CA41C7DF53BD57B03', X'5291DD91A3BC854CA41C7DF53BD57B03', 18, NULL, NULL, 2, NULL, 0, 'Set them out at room temperature for at least 15 minutes before serving to intensify the chocolate flavor.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBFEFF02063F50419640B4A2F1886664', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8614559146FF924A80CF43DE037642E3', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 1, NULL, NULL, 2, NULL, 0, 'Wrap two layers of heavy duty tin foil around bottom and sides of a 9 inch springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03C4C09D4AC9454AB3CCE11E64595228', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 2, NULL, NULL, 2, NULL, 0, 'Combine vanilla wafer crumbs and butter. Press into bottom of springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC76898B13CC9A4F9BEB53C339E0E067', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 3, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on low for about a minute until smooth and free of lumps.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'657EE09D3F070B49B92DB47F1030FE10', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy, add eggs one at a time and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'220ECFB70998C742999AFFFAF5A82E7D', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 5, NULL, NULL, 2, NULL, 0, 'Add sour cream, orange juice, carrot juice, and orange zest. Mix until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BA4165BF540B748B18F0FBD63F6877B', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 6, NULL, NULL, 2, NULL, 0, 'Pour over cookie crust and bake in a water bath in a roasting pan for 70-75 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'799344141A5F974ABE7AF3904BFCAF6A', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 7, NULL, NULL, 2, NULL, 0, 'Turn oven heat off and leave cheesecake in oven for 10-25 minutes until cheesecake looks set but not wet and still jiggles just slightly in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43E5F3916585D04EB96003B8DD4ECB4B', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 8, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool for 30 minutes in the roasting pan, then remove and allow to cool on a rack for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F876F247CD14EC47BB5100FD6C6CB728', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 8 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EAC8DF8F926594BA21E2EDEA9174ADF', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 10, NULL, NULL, 2, NULL, 0, 'Pipe green candy coating carrot tops onto parchment paper lined baking sheets. Freeze for 3-4 minute until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B16B42F3127DE43931543EB943F3330', X'A2B5666D3356C64EB5E4E0E92A38E5B7', X'A2B5666D3356C64EB5E4E0E92A38E5B7', 11, NULL, NULL, 2, NULL, 0, 'Cut cheesecake into 12 slices. Add three carrot tops to each slice and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4DFA6773A858C45BA79438A976929AB', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 0, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, sugar, heavy whipping cream, eggs, and vanilla in the bowl of a food processor, pulse very smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'816A08B9E3A1CE4D99FC04401C28C4E8', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 1, NULL, NULL, 2, NULL, 0, 'Color the filling using a variety of food coloring. See notes in tutorial for details.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3454E1729E074740A28E1905C53280C6', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 2, NULL, NULL, 2, NULL, 0, 'Dip a paint brush into your colored cheesecake filling and use it to paint the indentations in the silicone Easter egg mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'152100A625D640488E5D34D6EE66D7E9', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 3, NULL, NULL, 2, NULL, 0, 'Once you have painted all of the indentations in 8 Easter eggs, freeze the molds for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77D65962ED41E249BDEBBB31471BFF3A', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 4, NULL, NULL, 2, NULL, 0, 'If using a water bath and you have a flat rack for inside your roasting pan, set your mold on the rack. If not using a water bath, set your Easter egg mold on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7E574D6A6997B4CBA57E5E3B63C719B', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 5, NULL, NULL, 2, NULL, 0, 'Pour colored cheesecake filling into the Easter egg mold, filling each egg shaped cavity to about 1/8th of an inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE1D76CA3C580942B4CFFEB2405E4CDA', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 6, NULL, NULL, 2, NULL, 0, 'Fill all 8 of the painted eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25285DCA3E5C094D95D98F2B034395FB', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 7, NULL, NULL, 2, NULL, 0, 'Pour any remaining cheesecake filling into an other egg mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39BF066243010548834E3739E5AC0019', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 8, NULL, NULL, 2, NULL, 0, 'Bake your cheesecakes in the water bath, if using, for 18-22 minutes until the top forms a smooth film and the center is still giggly but not wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FB2A745FA80854FBC2DA1A2B15068DC', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 9, NULL, NULL, 2, NULL, 0, 'Allow your cheesecakes to cool at room temperature for an hour then refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3471B484EF790448998C0A6CC406E05', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 10, NULL, NULL, 2, NULL, 0, 'Freeze for 1-2 hours before un-molding.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F91EA01FD5EAB1449E92547263B4D074', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 11, NULL, NULL, 2, NULL, 0, 'Remove from the mold and immediately set each cheesecake on a small dessert plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'258ADD80B93C6145A879EF7BD2D4F7B7', X'447804F2124F2B439D361090A1C01756', X'447804F2124F2B439D361090A1C01756', 12, NULL, NULL, 2, NULL, 0, 'Allow to thaw for about 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE681471A3B67A419748F89C665D9DC7', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E1F192BAD4B0F459053689999373B14', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 1, NULL, NULL, 2, NULL, 0, 'Butter leaf molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD5FA6ACBFB2894491E1B16AD2859175', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 2, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, sugar, heavy whipping cream, eggs, and vanilla in the bowl of a food processor, pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACE253DE2840EC48ABA9344F0F975255', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 3, NULL, NULL, 2, NULL, 0, 'Divide cheesecake mixture into three or more bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F735FC631DEEA941AFE3CC5FAE6C8B60', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 4, NULL, NULL, 2, NULL, 0, 'Color one bowl orange, red, and yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF84DDD6831AAB4299A0A201BE228B31', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 5, NULL, NULL, 2, NULL, 0, 'Fill each leaf mold with a swirl of orange, red, and yellow filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'991AC253AFC5B444B5961C1D00FD10D2', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 6, NULL, NULL, 2, NULL, 0, 'Tap the mold several times to remove excess air bubbles after each scoop of cheesecake filling you add.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A344B2AC59DFE45930C8FDA1894ADE5', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 7, NULL, NULL, 2, NULL, 0, 'Bake your cheesecakes for 20-24 minutes until the top forms a smooth film and the center is still giggly but not wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5172A5282BF7C04BA3AA3D063CC15207', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 8, NULL, NULL, 2, NULL, 0, 'Allow your cheesecakes to cool for an hour then freeze for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF2FF642053C8F4CBC936AA21B523105', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 9, NULL, NULL, 2, NULL, 0, 'Un-mold cheesecakes directly onto a serving dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC2435AC65EEC648AC58F209A539C362', X'A5211B06D362F04AA527FAD8FAAFE8CB', X'A5211B06D362F04AA527FAD8FAAFE8CB', 10, NULL, NULL, 2, NULL, 0, 'Allow to defrost in the refrigerator for a few hours before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90F595C6D213E34EA46ED8CEC5B83093', X'7858B8901ADBCB4EBE382A780A337F44', X'7858B8901ADBCB4EBE382A780A337F44', 0, NULL, NULL, 2, NULL, 0, 'Line rings with acetate.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DB50E16708B0049B01747A702DEA09F', X'7858B8901ADBCB4EBE382A780A337F44', X'7858B8901ADBCB4EBE382A780A337F44', 1, NULL, NULL, 2, NULL, 0, 'Set rings on a baking sheet that is lined with parchment paper. Otherwise, turn the bottom of your springform pan upside down and clamp it in (this makes for easier cutting of the cake.)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EC5DD388D270949917699CFF8FA7167', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', 0, NULL, NULL, 2, NULL, 0, 'Pulse vanilla wafers in food processor until you have fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A719D58628B72E48B20E7BC4CF701912', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', 1, NULL, NULL, 2, NULL, 0, 'Add blue sanding sugar (OR granulated sugar and some drops of blue food coloring).');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB46C4ED1450B2438992747F70A9DCF0', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', 2, NULL, NULL, 2, NULL, 0, 'Pulse to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68FFAE11462CFC4293F561211B3F3455', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', 3, NULL, NULL, 2, NULL, 0, 'Add more blue coloring, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67D82A7C5DEEA949AE317DABE72F6825', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', 4, NULL, NULL, 2, NULL, 0, 'Add the butter and pulse to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'777BD27FC5B24246A13FBB460CCB7104', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', 5, NULL, NULL, 2, NULL, 0, 'Divide crumbs evenly and press into the bottom of your cake rings or springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'801493C93D777B489F7E50E8DF29CB20', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', X'D9CA6219B9F69B49A98A3EFEB2ADE8F4', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate crusts until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA7C9962F27E26408142BE2F76686565', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 0, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat cream cheese and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5341C5BEE4253A46A5D8973D085DBDB9', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 1, NULL, NULL, 2, NULL, 0, 'Add vanilla and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8557D0913C43C14799F71DE09A0F2F3E', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 2, NULL, NULL, 2, NULL, 0, 'In another mixing bowl beat or whisk heavy whipping cream to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68D7C67AD5DEE44ABA70ABA80C49C428', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 3, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the cream cheese mixture. If using frozen whipped topping, like Cool Whip, you can beat it in using an electric mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7421350D53912E47903BF75B0C538E1B', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 4, NULL, NULL, 2, NULL, 0, 'Pour on top of blue crusts, evenly dividing among cake rings.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10A45E3AD004F94EB554056892550361', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 5, NULL, NULL, 2, NULL, 0, 'Spread filling out in an even layer, smoothing out surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'612AAC83BB33E4479C975B63793FDDE6', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 6, NULL, NULL, 2, NULL, 0, 'Clean any filling that stuck to the sides of your cake rings.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'358AECAA5060A344ACF67A36AA364669', X'70438D2A7A14D948AC956DB43FAC73E2', X'70438D2A7A14D948AC956DB43FAC73E2', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate until your gelée is ready.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F07F31356AA6C4388C456E7B33128F6', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over 1/3 cup water and allow to bloom for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F37056D43716434C927407FDA59CCE7B', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 1, NULL, NULL, 2, NULL, 0, 'Place frozen raspberries and sugar in a saucepan and bring to a boil over medium heat, stirring often.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'232F648F73943548AA37B46D62C685F0', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and pour into a fine mesh sieve set over a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2624B793B820344FA912C446A057FFA6', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 3, NULL, NULL, 2, NULL, 0, 'Press down on the raspberries allowing the juice to drain into the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F4447DD7FDA7B41A3A48B8FF7585905', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 4, NULL, NULL, 2, NULL, 0, 'Discard seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8742310977B86B4EA54F37F14A07BEE2', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 5, NULL, NULL, 2, NULL, 0, 'Add the bloomed gelatin to the bowl, stirring to dissolve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D7D2F06631288449DA316E7A6DA68D1', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 6, NULL, NULL, 2, NULL, 0, 'Stir in lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57D9D8B47D5396409F8F2B5792CBDB4B', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 7, NULL, NULL, 2, NULL, 0, 'Allow to cool until it is slightly thickened but still pourable.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D39E85A8363FA24789A5B38220F62179', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 8, NULL, NULL, 2, NULL, 0, 'Pour raspberry gelée on top of your cheesecakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E18DD261F7E9948889C01B3F637CF6D', X'4603C257109E03409D53C26BE6782F7E', X'4603C257109E03409D53C26BE6782F7E', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate until set, at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F03EA19270B1245878A3D9C365D16A7', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 0, NULL, NULL, 2, NULL, 0, 'Fill a roasting pan with enough water to come half way up the sides of the silicone heart molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29628C0284CB8F40AF4125534A5D2082', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 1, NULL, NULL, 2, NULL, 0, 'Set pan in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFC1EDA1C4ADA6459FB19BB30AB590C4', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 2, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degree Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD2A014C51470D46A0BC462579848776', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 3, NULL, NULL, 2, NULL, 0, 'Mix together the graham cracker crumbs, cinnamon, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E70A1345B725B64D9D4903F09362823C', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 4, NULL, NULL, 2, NULL, 0, 'Equally divide the crumbs among 18 heart shaped silicone mold cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4BC9D9377E92E40A8C9A06E72C24820', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 5, NULL, NULL, 2, NULL, 0, 'Press the crumbs into an even layer and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'051D8D113FD0954BAB73200784D61B08', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 6, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'430FA1284350C642BD62499A5FD5300C', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 7, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61687ACB36E43A428BA30AAFA96C85C1', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 8, NULL, NULL, 2, NULL, 0, 'Beat in eggs then add sour cream and vanilla and mix just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B02B33DC759AFC479560E3B9FCB40406', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 9, NULL, NULL, 2, NULL, 0, 'Equally divide batter into 6 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C11520CEB7D7DE4E845A8968DC88BD0D', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 10, NULL, NULL, 2, NULL, 0, 'Color one bowl of filling pink,another lavender, yellow, orange, green, and blue using food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3100B34E1C7A94EA55690A25559E2B1', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 11, NULL, NULL, 2, NULL, 0, 'Equally divide each color of filling among 3 heart cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DE577B7ACAC3946A5C88B76054AA1AF', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 12, NULL, NULL, 2, NULL, 0, 'Bake cheesecakes in a water bath for 18-24 minutes until the edges look set but the center is jiggly but not wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36188C26C85CA943B0FD2A2143C3C6F4', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 13, NULL, NULL, 2, NULL, 0, 'Remove from oven and water bath and allow to cool at room temperature for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B746293E5E1A249AE8BC67768BFB3ED', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 14, NULL, NULL, 2, NULL, 0, 'Freeze for at least two hours then remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F383E26636112D4998DED6A32A15D5E7', X'164D3229800347419393C71A6A66EA36', X'164D3229800347419393C71A6A66EA36', 15, NULL, NULL, 2, NULL, 0, 'Paint conversation heart phrases onto the tops of the cheesecakes using red food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71787D96A8D28D4AB0DC4F0327A41824', X'14B399F8220B2B4CA2B671992788A464', X'14B399F8220B2B4CA2B671992788A464', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'894E9ED453DB8A40B39F621AF3C02CFA', X'14B399F8220B2B4CA2B671992788A464', X'14B399F8220B2B4CA2B671992788A464', 1, NULL, NULL, 2, NULL, 0, 'Spread 1/3 cup pizza sauce in the middle of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DDB21FB586C3745919F00E3469248EC', X'14B399F8220B2B4CA2B671992788A464', X'14B399F8220B2B4CA2B671992788A464', 2, NULL, NULL, 2, NULL, 0, 'Blend together 8 ounces cream cheese, 1/2 cup shredded Mozzarella Cheese, 1/2 cup shredded Parmesan cheese, 1/2 cup sour cream, and a pinch of salt, then spread half of it over the pizza sauce in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3194AE7CE86D5C4D954E989AFBA4A435', X'14B399F8220B2B4CA2B671992788A464', X'14B399F8220B2B4CA2B671992788A464', 3, NULL, NULL, 2, NULL, 0, 'Top with 3/4 cup shredded Mozzarella cheese, then sprinkle on half of the pepperoni or your favorite pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AEA97D868AE3C4DB6205ECA6C11AA2B', X'14B399F8220B2B4CA2B671992788A464', X'14B399F8220B2B4CA2B671992788A464', 4, NULL, NULL, 2, NULL, 0, 'Layer on another 1/3 cup pizza sauce, the remaining cheese dip, 1/3 cup pizza sauce, another 3/4 cup Mozzarella cheese, and more pepperoni or pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBDF7B37FD806F4EB78D85B5C5C1DCCC', X'14B399F8220B2B4CA2B671992788A464', X'14B399F8220B2B4CA2B671992788A464', 5, NULL, NULL, 2, NULL, 0, 'Bake the dip for 28-30 minutes until the cheese is melted and the pizza crust is golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CCFC32682FB9348B9A9A172D69AA91E', X'14B399F8220B2B4CA2B671992788A464', X'14B399F8220B2B4CA2B671992788A464', 6, NULL, NULL, 2, NULL, 0, 'Serve while hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEF167FD578D3E45B8DC337C18773504', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 0, NULL, NULL, 2, NULL, 0, 'Pat chicken breasts dry with paper towels.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCFD3EBD3F80A8428756C0EF6699A070', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 1, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3225B334907BBD48AD084A9193CFFE83', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 2, NULL, NULL, 2, NULL, 0, 'Cover and set aside for 10 minutes to allow the salt to tenderize the chicken.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EBE413B697FC046BF8FA7F4BF34FC61', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 3, NULL, NULL, 2, NULL, 0, 'Pour Mazola Corn Oil into skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD2896698D8BDE4E995B89A669CF5351', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 4, NULL, NULL, 2, NULL, 0, 'Set over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CC4A415F778D54DB3923900DF2FDCF1', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 5, NULL, NULL, 2, NULL, 0, 'Heat until the oil begins to shimmer.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7916011E997F0148AEE3C56225782641', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 6, NULL, NULL, 2, NULL, 0, 'Pat the chicken dry again then place into the hot oil in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E2E5247328F3744A7683DAC14F11974', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 7, NULL, NULL, 2, NULL, 0, 'Cook 2-3 minutes on each side until the chicken reaches an internal temperature of 165 degrees Fahrenheit (74 degrees C).');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F4E3A065671F04FAA180C6978F5D435', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 8, NULL, NULL, 2, NULL, 0, 'Remove from the heat, tent with tin foil, and allow to rest for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0971D5F6449B824785D3CB5C813E1266', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 9, NULL, NULL, 2, NULL, 0, 'Cut each chicken breast crosswise into slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4D50BA02567A144A702667EF53FD2F6', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 10, NULL, NULL, 2, NULL, 0, 'Set one chicken breast onto each of your dinner plates.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9404C78E3F6E9241B23E0933566B016A', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 11, NULL, NULL, 2, NULL, 0, 'Arrange the diced tomatoes next to the chicken breast forming a heart shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B767023E4EFF7A4DB834D0F6CC84DD91', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 12, NULL, NULL, 2, NULL, 0, 'Sprinkle 1/2 of the feta cheese and 1/2 of the basil over top of the tomatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AF9BB6FEF36A848AEADF073BD9B8039', X'A98629A31D45A34E8F43CAFBEBDC25E8', X'A98629A31D45A34E8F43CAFBEBDC25E8', 13, NULL, NULL, 2, NULL, 0, 'Drizzle balsamic glaze over top of the chicken and tomatoes and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6B39E14959C994DBC616AB19A00E5A6', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B9A0F668558E341BB932AA5C0EBB52C', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 1, NULL, NULL, 2, NULL, 0, 'Combine 1 1/2 cups flour, undissolved yeast, sugar and salt in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0287505B7D3C643A9B115BF52ECBF3A', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 2, NULL, NULL, 2, NULL, 0, 'Add the very warm water (between 120° to 130°F) and corn oil then mix until well blended for about 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C08D351F776A94489D0C19A39F911C9', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 3, NULL, NULL, 2, NULL, 0, 'Knead the dough on a floured surface, adding additional flour if necessary, until smooth and elastic, about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'588C60DF7D22354392985F508BB361F3', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 4, NULL, NULL, 2, NULL, 0, 'Cover the dough with plastic wrap then let the dough rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3459046C21A3244DB80D34FB13C1D13F', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 5, NULL, NULL, 2, NULL, 0, 'Grease a 12-inch round pizza pan then pat the dough into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9ACBF6620041F499FC0EB3FF6E6CCF7', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 6, NULL, NULL, 2, NULL, 0, 'Pinch the edges to create a thicker crust around the perimeter of the crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CAC78C018AF3D45AF296888C43EA4CC', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 7, NULL, NULL, 2, NULL, 0, 'Brush the edges of the pizza crust lightly with corn oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6B2806302835C4C99C78B69148FC03C', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 8, NULL, NULL, 2, NULL, 0, 'Spread the pizza sauce over the center of the dough, leaving 1/2-inch to 1-inch of uncovered dough around the perimeter of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0DFFE45E5EC7D4289656305457AFF15', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle about 3/4 of the mozzarella cheese over the sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7A37BA68012AA42BC04B86BFFFC6F91', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 10, NULL, NULL, 2, NULL, 0, 'Add the roasted vegetables on top then add the marinated artichokes, the remaining mozzarella cheese, and the feta cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35B6926E61731246A7641BDE0C474B66', X'1216536430188A4AA863663FB4545099', X'1216536430188A4AA863663FB4545099', 11, NULL, NULL, 2, NULL, 0, 'Bake on lowest oven rack for 12 to 15 minutes, until cheese is bubbly and crust is browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38F4489355E9DB44B1065CACB8DE47A2', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon oil in a large, non-stick, 12-inch skillet over medium-high heat until the oil just begins to smoke.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7174BD059447F4598B930C6111224CC', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 1, NULL, NULL, 2, NULL, 0, 'Season the sirloin steak with salt and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DF1154F98CA6141A513948FEB756275', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 2, NULL, NULL, 2, NULL, 0, 'Place in the hot skillet and cook for 2-3 minutes per side until nicely browned and the internal temperature reaches at least 130 degrees for rare and up to 160 degrees for well done.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00E5621C0FFE444BA086D70120867072', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 3, NULL, NULL, 2, NULL, 0, 'Remove from skillet, set on a cutting board, and tent with foil. Set aside for at least 15 minutes');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA98035FF4BF1B4A847D75527A644042', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 4, NULL, NULL, 2, NULL, 0, 'Add one tablespoon olive oil to hot skillet and toss in onion and pepper slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FB9A55B5E731F4BA222D1BCCE8AE05E', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 5, NULL, NULL, 2, NULL, 0, 'Heat until tender then remove and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C038B3EB6E2AD74C82410B9BA559A95D', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 6, NULL, NULL, 2, NULL, 0, 'Add the remaining 1 tablespoon of olive oil to the skillet. Then pour in the water and the contents of the Knorr One Skillet Meals packet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FFBBAB5BC5B5B44B55999EAA90F7F17', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 7, NULL, NULL, 2, NULL, 0, 'Bring the liquid to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20F7F18F6C948C4CAE6BC807CF775400', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 8, NULL, NULL, 2, NULL, 0, 'Cover the skillet with a tight-fitting lid, reduce the heat to low, and simmer for about 22 minutes until the rice and quinoa are cooked through, and most of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05F3A2532635454EB41D5A68157C527A', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 9, NULL, NULL, 2, NULL, 0, 'Meanwhile, cut the steak into slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E2AF198B8828443AFECF837E9155903', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 10, NULL, NULL, 2, NULL, 0, 'Toss steak, peppers, and onions into the skillet with the rice and quinoa.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FED03BB5F3F4CF4A9EF6E5608BA0D51E', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 11, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat, cover, and let rest for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6223BE6B1F6C8468C9457B5C01DBC60', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', X'F1CE7A064CB5BC4AA9E55763FBB08AC9', 12, NULL, NULL, 2, NULL, 0, 'Once the steak and peppers have heated through, remove the lid, and serve directly from the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BC4DC638C60DC4488464BEDD6159FEE', X'5739CA1FD7DCB44E9FF9837023221154', X'5739CA1FD7DCB44E9FF9837023221154', 0, NULL, NULL, 2, NULL, 0, 'Spread Pure Blends Avocado Oil plant-based butter over toast.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0442B680273C74080F364DEB6C74A9D', X'5739CA1FD7DCB44E9FF9837023221154', X'5739CA1FD7DCB44E9FF9837023221154', 1, NULL, NULL, 2, NULL, 0, 'Cut avocado in half, remove pit, and scoop out flesh.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DB86C1C284B2D498B5AAD01A45DEA56', X'5739CA1FD7DCB44E9FF9837023221154', X'5739CA1FD7DCB44E9FF9837023221154', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle on lemon and salt and smash.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC30627B10137245AC629121A3067777', X'5739CA1FD7DCB44E9FF9837023221154', X'5739CA1FD7DCB44E9FF9837023221154', 3, NULL, NULL, 2, NULL, 0, 'Spread a thick layer over toast.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E0DCAD008BABF44BFA559C5E91578F5', X'5739CA1FD7DCB44E9FF9837023221154', X'5739CA1FD7DCB44E9FF9837023221154', 4, NULL, NULL, 2, NULL, 0, 'Add cucumber ribbons, cherry tomatoes, and a sprinkling of dill.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07AFE65B4635F1499E3E125B13F16053', X'5739CA1FD7DCB44E9FF9837023221154', X'5739CA1FD7DCB44E9FF9837023221154', 5, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACACA76883A206469A090E60C534041E', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', 0, NULL, NULL, 2, NULL, 0, 'Spread Pure Blends Coconut Oil Plant-Based Butter over toast.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E477446FDB3F4940858E39FF591E80DA', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', 1, NULL, NULL, 2, NULL, 0, 'Spread a thick layer of smashed banana over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1EDC1260BA5E645B13F75C2FC1BD3FE', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle on toasted coconut, chocolate, and almonds.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67E617431A027040889A65DC7A094D90', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', 3, NULL, NULL, 2, NULL, 0, 'Add slices of mango and papaya or cut the fruit into flower shapes and place on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B88BC3FD32FAFE4D879E9A935B8785AB', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', X'8911B66ECE4A9B42BA0BB8D7EF7AAE5F', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBA75DBA8CB4034D99FD3FBD35941311', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a 12 inch skillet set over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24CCE6DE49438140B6D5A8F1DF31560A', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 1, NULL, NULL, 2, NULL, 0, 'Season chicken with salt and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF6D157D958BA240946470A13D8DFF6E', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle flour over chicken and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B4A4930CDFE2B498F71944C65EC55B1', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 3, NULL, NULL, 2, NULL, 0, 'Brown the chicken in the skillet for about 4 minutes, in one or two batches.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B969068B9D6D347A3E38DC705586B90', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 4, NULL, NULL, 2, NULL, 0, 'Add the water, carrots, and the contents of the packet of Knorr® One Skillet Meal, and bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B07E36B35D7714393E6E86140FF43B3', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 5, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low, and cover the skillet. Simmer for 23 minutes, or until the barley is tender and most of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'866E90099650254D92E1A6028FA7560F', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 6, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and add the spinach. Cover for an additional 2 minutes until the spinach wilts.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E23E83E474CC4A4DA851D97054201662', X'756A79FC7A09624CADEE731A20240EAC', X'756A79FC7A09624CADEE731A20240EAC', 7, NULL, NULL, 2, NULL, 0, 'Stir in the spinach and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03F0BE13FD1B904FB9594D3DB266D6BC', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 0, NULL, NULL, 2, NULL, 0, 'Stir together the Hellmann''s® Real Mayonnaise with the garlic powder, mustard powder, white vinegar, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9201E54E9F4D944B44EC39F7653DBB2', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 1, NULL, NULL, 2, NULL, 0, 'Toss with the eggs, bacon, and tomatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3432C1C7E385BD4EB8773CE8EC946AE8', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25234BB79DAB614F8B738AADF3F2F79E', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 3, NULL, NULL, 2, NULL, 0, 'Pour oil into a 3 quart saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'083C67D1A0AB9F4ABC74F08A7AD978FF', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 4, NULL, NULL, 2, NULL, 0, 'Set the pot on the stove over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B98BB6E75D040F4690ED37D50CCE034B', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 5, NULL, NULL, 2, NULL, 0, 'Allow oil to come to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE6934DBBC9BDD4B924706AB2FCE3D8A', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 6, NULL, NULL, 2, NULL, 0, 'Cut a small hole out of the center of each biscuit using a 1 inch round cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F12B2F7057983D419410913C9425F72B', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 7, NULL, NULL, 2, NULL, 0, 'Gently lower 2 biscuits into the saucepan and fry, flipping them over as needed, until golden brown, 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A162ED0A2553294CA39A54FDF38EAB02', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 8, NULL, NULL, 2, NULL, 0, 'Remove from oil and set on paper towels to allow excess oil to drain off.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7989749DD69BA438EAFA58020ABFF89', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 9, NULL, NULL, 2, NULL, 0, 'If using, sprinkle the donuts with freshly grated Parmesan Cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB3EA33256425E4FB9DAAEA33D31DFA5', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 10, NULL, NULL, 2, NULL, 0, 'Allow donuts to cool before assembling sandwiches.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1305B33D4616E4B89E9C1960F4BF3B8', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 11, NULL, NULL, 2, NULL, 0, 'Cut open the donuts and set a leaf of lettuce on the bottom half of each.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48286DA9578BEF42917CC5E97BA01A13', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 12, NULL, NULL, 2, NULL, 0, 'Equally divide the egg salad among the bottom donut halves then place the other half on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9A0E42173B23144872000B6A2540C4F', X'B86AAC299934C8418CA8F9A3E4AD6749', X'B86AAC299934C8418CA8F9A3E4AD6749', 13, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE58B1B23877DA4BAE211449E4A8B501', X'49CA33FD5F25DF4BB990E556E8FE640E', X'49CA33FD5F25DF4BB990E556E8FE640E', 0, NULL, NULL, 2, NULL, 0, 'Puree roasted red pepper, roasted garlic, roasted almonds, lemon juice, and Parmesan cheese in a food processor, keeping puree somewhat chunky.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDE73C579525744EBBA12722BA033E37', X'49CA33FD5F25DF4BB990E556E8FE640E', X'49CA33FD5F25DF4BB990E556E8FE640E', 1, NULL, NULL, 2, NULL, 0, 'Add oil and puree just to incorporate the oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F34F78EBEE1DB6468827263B95046C4F', X'49CA33FD5F25DF4BB990E556E8FE640E', X'49CA33FD5F25DF4BB990E556E8FE640E', 2, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C09443283719E74488FF4A7D147D58E2', X'49CA33FD5F25DF4BB990E556E8FE640E', X'49CA33FD5F25DF4BB990E556E8FE640E', 3, NULL, NULL, 2, NULL, 0, 'Stir in chicken breast.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04BF93133D69CE488B5C6257C5D89576', X'49CA33FD5F25DF4BB990E556E8FE640E', X'49CA33FD5F25DF4BB990E556E8FE640E', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C04C24D31932744381190F98B5767B53', X'02FDA91F2CEF1541848361B78160EF14', X'02FDA91F2CEF1541848361B78160EF14', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB37F3BE016722428B444CE12269E063', X'02FDA91F2CEF1541848361B78160EF14', X'02FDA91F2CEF1541848361B78160EF14', 1, NULL, NULL, 2, NULL, 0, 'Cut twelve 4-inch round circles from tortillas.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA6154C4DCD46542974F51F325266C77', X'02FDA91F2CEF1541848361B78160EF14', X'02FDA91F2CEF1541848361B78160EF14', 2, NULL, NULL, 2, NULL, 0, 'Brush both sides of the rounds with olive oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D0B9F7AE92169449801E2E6678631BD', X'02FDA91F2CEF1541848361B78160EF14', X'02FDA91F2CEF1541848361B78160EF14', 3, NULL, NULL, 2, NULL, 0, 'Press into the bottom and up the sides of a muffin tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02BE3BC5D6EC284EBFDE3361E123220B', X'02FDA91F2CEF1541848361B78160EF14', X'02FDA91F2CEF1541848361B78160EF14', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges turn golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BF266CE1622D241BCA39FD771FA56E9', X'02FDA91F2CEF1541848361B78160EF14', X'02FDA91F2CEF1541848361B78160EF14', 5, NULL, NULL, 2, NULL, 0, 'Remove from muffin tin and allow to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A245F1339B95D148BE8F01EAB87BA6C9', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', 0, NULL, NULL, 2, NULL, 0, 'Spoon whipped cream cheese into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E9D5DE3251A294C9126B1DF2CBA08E4', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', 1, NULL, NULL, 2, NULL, 0, 'Pipe about a teaspoon of cream cheese into the bottom of each tortilla cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91159EF150C85447ABBD5661BFB3D2AB', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', 2, NULL, NULL, 2, NULL, 0, 'Equally divide the chicken salad among the 12 tortilla cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B298B1DFB38A24FAC3D410C2C4E3A54', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', 3, NULL, NULL, 2, NULL, 0, 'Pipe a swirl of cream cheese on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'841FB5FAA3B3C041B4150B543837DAF8', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', 4, NULL, NULL, 2, NULL, 0, 'Tear basil into tiny pieces and sprinkle over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CB2AAA9B231F943AEA7FB1829569A77', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', X'4A86A5F3FAF8DC4D8EB40C88587F5D71', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BAE6567EE8DD5489710EA2F7DE53894', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 0, NULL, NULL, 2, NULL, 0, 'Stir the French Onion Dip and buttermilk together in a shallow baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C541BC93664CAF46ADE9E8B046C49A1A', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken tenders in the dish, making sure they are covered with the dip mixture. Cover and refrigerate for 30-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F40D173406CB44BB0000116C8E5A34', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 2, NULL, NULL, 2, NULL, 0, 'Crush the potato chips into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2BA7DD980A1A34D9CFD9E7AE0E1869B', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CEBB9D926706041BE19C7B24BABA8BD', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 4, NULL, NULL, 2, NULL, 0, 'Spray a cooling rack with non-stick spray. Set it over a tin foil lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'901289EED2528B41A782AA5BE650FE76', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 5, NULL, NULL, 2, NULL, 0, 'Remove chicken tenders from the marinade. Allow excess liquid to drain off. Season with salt and pepper, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C15C1396408B3846906DE3EA961C0443', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 6, NULL, NULL, 2, NULL, 0, 'Skewer the chicken tenders, then coat them in the crushed potato chips. Set on the wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06B485786D2F084A928A65ED934886C8', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 7, NULL, NULL, 2, NULL, 0, 'Spray the chicken tenders with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98B1D5D17378434C9C9C7F47C0AD1E7A', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 8, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB4B5973B963D64197A1A43FAC643394', X'EADDFBC3D1A31C488AFC12F61C45378A', X'EADDFBC3D1A31C488AFC12F61C45378A', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with the remaining French onion dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA8EB0BB6342714E9134FB7CD7CD6638', X'B1F995F0789420499BDC1DB7062B5B89', X'B1F995F0789420499BDC1DB7062B5B89', 0, NULL, NULL, 2, NULL, 0, 'Wash, dry, and tear your lettuce into bite-size pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE58297672DD8846A4D8B5E794B04053', X'B1F995F0789420499BDC1DB7062B5B89', X'B1F995F0789420499BDC1DB7062B5B89', 1, NULL, NULL, 2, NULL, 0, 'Cut Kalamata olives into thin slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAA65C0246915D4881B9C0A54BEFAD6F', X'B1F995F0789420499BDC1DB7062B5B89', X'B1F995F0789420499BDC1DB7062B5B89', 2, NULL, NULL, 2, NULL, 0, 'Put a serving of lettuce on a salad plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07B738AC9671A745AF9F38927C9CD3D2', X'B1F995F0789420499BDC1DB7062B5B89', X'B1F995F0789420499BDC1DB7062B5B89', 3, NULL, NULL, 2, NULL, 0, 'Top with cucumbers, kalamata olives, and feta cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF6003C435CF6649A5596F0C0C5F073F', X'5777F6D8641906409D01B54275AF38C5', X'5777F6D8641906409D01B54275AF38C5', 0, NULL, NULL, 2, NULL, 0, 'Place grape tomato on the cutting board. Cut at a diagonal across the short side of the tomato.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AB6CDB7055E074385D2F020F2C6643A', X'5777F6D8641906409D01B54275AF38C5', X'5777F6D8641906409D01B54275AF38C5', 1, NULL, NULL, 2, NULL, 0, 'Separate the two halves of the tomato.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E512BDEF284D1347BAF93244FD3B3D10', X'5777F6D8641906409D01B54275AF38C5', X'5777F6D8641906409D01B54275AF38C5', 2, NULL, NULL, 2, NULL, 0, 'Pick up one half of the cut tomato.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10CE7021340BA34E8C78608E1EEFEAED', X'5777F6D8641906409D01B54275AF38C5', X'5777F6D8641906409D01B54275AF38C5', 3, NULL, NULL, 2, NULL, 0, 'Rotate one-half of the tomato 180 degrees (turn it upside down.)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0E55458AD03594BAB6DD6EB2DD376DD', X'5777F6D8641906409D01B54275AF38C5', X'5777F6D8641906409D01B54275AF38C5', 4, NULL, NULL, 2, NULL, 0, 'Press the two tomato halves together forming a heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55A95C07E424F84BAB126B34AEB88277', X'5777F6D8641906409D01B54275AF38C5', X'5777F6D8641906409D01B54275AF38C5', 5, NULL, NULL, 2, NULL, 0, 'Carefully pick up your heart-shaped tomatoes and artfully arrange them over the top of the salad.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD5A93329936254E82D813E73A077753', X'5777F6D8641906409D01B54275AF38C5', X'5777F6D8641906409D01B54275AF38C5', 6, NULL, NULL, 2, NULL, 0, 'Serve your festive salads with homemade Tomato Vinaigrette or your favorite salad dressing.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'703CDDDFE46E984CB1F141206E83F04B', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 0, NULL, NULL, 2, NULL, 0, 'Cut vine-ripened tomatoes into quarters.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABB05C89A1CE0543B0405F0ECEC38CA6', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle them with a pinch of salt and put into the bowl of a food processor (the food processor pictured at left is a mini one.)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10E606C28E0B7D4ABFD5EEAEFD8C3636', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 2, NULL, NULL, 2, NULL, 0, 'Puree the tomatoes until just slightly chunky.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A8D8B579EE3874E8F2609412EF0C0B6', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 3, NULL, NULL, 2, NULL, 0, 'Pour tomato puree into a fine-mesh sieve set over a measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15DD377740A811429333E94F9B039BF7', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 4, NULL, NULL, 2, NULL, 0, 'Allow the juice to drip down into the cup. Use a rubber spatula to press on the tomato solids to extract as much juice as possible.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE5DCD33D146C14F9D56EF7AFDA3C7FB', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 5, NULL, NULL, 2, NULL, 0, 'Discard the seeds and tomato solids.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DECC9642FF37B343A8A0B42A8A21F08C', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 6, NULL, NULL, 2, NULL, 0, 'You will need 1/2 cup of tomato juice for one recipe. Discard any extra juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AE9479B4A5AD24D8720DF690ECE0918', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 7, NULL, NULL, 2, NULL, 0, 'Pour tomato juice into a small saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F674FEC9F3115468DDBC1FA64195F82', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 8, NULL, NULL, 2, NULL, 0, 'Add red wine vinegar, oregano, and garlic.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3621470168F674DA610DFB21F004FC3', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 9, NULL, NULL, 2, NULL, 0, 'Heat over medium heat, simmering until liquid reduces to 3 tablespoons.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A850CD7DFD91C648929029A6A83A0963', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 10, NULL, NULL, 2, NULL, 0, 'Remove from heat and allow to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2434C67950DF664283F0279050C9FD74', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 11, NULL, NULL, 2, NULL, 0, 'Once cooled, slowly drizzle in the olive oil while continually whisking to create an emulsion (a good blending of a watery liquid with an oil.)');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0323087813676C409F068C6B03A49907', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 12, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings4 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05855FE6E12DD143A91EBFD0CC26027C', X'F8A2C211212B744A959BFF7FF08DE096', X'F8A2C211212B744A959BFF7FF08DE096', 13, NULL, NULL, 2, NULL, 0, 'Drizzle over salad.');
