INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'98DF62502A992843B46A7C8C0C9E75A8', X'5E648499E54E2C4F89A5D67FAE572B4C', X'5E648499E54E2C4F89A5D67FAE572B4C', 0, NULL, NULL, 2, NULL, 0, 'MIX almond butter, honey and vanilla in large bowl. Stir in oats, wheat germ, almonds, cranberries and cinnamon until evenly moistened. Shape into 1-inch balls. Chill 10 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'772783AB95DE8D42825D8916E4B81109', X'101E126A8ED22242B17826DC1A717F63', X'101E126A8ED22242B17826DC1A717F63', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on one slice of bread. Spread jam on remaining slice. Press together to form sandwich.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1FF17DF18273A748A4D19444516ECCB8', X'435F737A1A83444084AE34DF7D286017', X'435F737A1A83444084AE34DF7D286017', 0, NULL, NULL, 2, NULL, 0, 'COMBINE cinnamon and granulated sugar. Set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'68519EF5D596AC49AB14F295C9AFF8DE', X'435F737A1A83444084AE34DF7D286017', X'435F737A1A83444084AE34DF7D286017', 1, NULL, NULL, 2, NULL, 0, 'SPRAY one side of tortilla generously with butter cooking spray. Place spread side down in 10-inch skillet. Spread topside with peanut butter, top with banana slices, sprinkle with brown sugar and top with remaining tortilla. Spray top of tortilla generously with butter cooking spray and sprinkle with half of the cinnamon and sugar mixture.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9BBF17D7EA7AD44392E47AAEC07B10B5', X'435F737A1A83444084AE34DF7D286017', X'435F737A1A83444084AE34DF7D286017', 2, NULL, NULL, 2, NULL, 0, 'COVER skillet. Cook over medium-high heat until the bottom is golden brown. Turn tortilla with a wide spatula. Sprinkle top with remaining cinnamon and sugar. Continue cooking covered until bottom is golden brown. Remove to cutting board and slice into 8 wedges. Serve warm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'66DC2E276BB7DE4C8D17CA5051268033', X'17FD6232DED0D04CAC2DF51365DAC5CB', X'17FD6232DED0D04CAC2DF51365DAC5CB', 0, NULL, NULL, 2, NULL, 0, 'COMBINE milk, banana, peanut butter, honey and ice cubes in blender container. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9FED1E5415A6444D9405D2CFEB17B7E2', X'17FD6232DED0D04CAC2DF51365DAC5CB', X'17FD6232DED0D04CAC2DF51365DAC5CB', 1, NULL, NULL, 2, NULL, 0, 'POUR into serving glasses. Add whipped cream and sprinkles, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F96BCA767660DD4E82C0A5E661147CB5', X'87CB70697644B24DA443C565C8015E7E', X'87CB70697644B24DA443C565C8015E7E', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 300ºF. Coat 15 x 10-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3861CB8944F2D14DB5393CA7849E8E6E', X'87CB70697644B24DA443C565C8015E7E', X'87CB70697644B24DA443C565C8015E7E', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE cashew butter, honey and cinnamon in large microwave-safe bowl on HIGH for 30 seconds. Stir in vanilla. Add oats, raisins and cashews. Stir to coat. Spread on prepared baking pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63E558E2242A8048B46A282D84ABABF4', X'87CB70697644B24DA443C565C8015E7E', X'87CB70697644B24DA443C565C8015E7E', 2, NULL, NULL, 2, NULL, 0, 'BAKE 35 to 40 minutes, stirring occasionally. TURN OFF OVEN. Let dry 1 1/2 hours, stirring occasionally. Remove from oven to cool completely. Store in airtight container for up to 1 week.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C7C4930AB5D814DAA66F08186F6060D', X'3A478D2DEA34084BA88325B4F3B19575', X'3A478D2DEA34084BA88325B4F3B19575', 0, NULL, NULL, 2, NULL, 0, 'COMBINE frozen yogurt, soy milk, protein powder and peanut butter in blender container.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CA7BF1D7FCAB1A4AB75B84EB5245B223', X'3A478D2DEA34084BA88325B4F3B19575', X'3A478D2DEA34084BA88325B4F3B19575', 1, NULL, NULL, 2, NULL, 0, 'COVER and process until smooth. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E1624E5F838DE7469CCD8530CB8E7761', X'BB3D89BA1AF4F74F92CC6A835CE4BBE3', X'BB3D89BA1AF4F74F92CC6A835CE4BBE3', 0, NULL, NULL, 2, NULL, 0, 'HEAT French toast in toaster according to package directions until golden brown. Cut into sticks. Place on serving plate.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C8F60A26CF8B74199F6144FBD508C19', X'BB3D89BA1AF4F74F92CC6A835CE4BBE3', X'BB3D89BA1AF4F74F92CC6A835CE4BBE3', 1, NULL, NULL, 2, NULL, 0, 'SPRINKLE with cinnamon sugar. Spread with peanut butter and jam.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FD228AEE3D3D0642BE1E43261B79B3EE', X'825362D290AC2B479004452EF15D24D3', X'825362D290AC2B479004452EF15D24D3', 0, NULL, NULL, 2, NULL, 0, 'COMBINE almond butter and caramel syrup in small bowl. Spread on bagel crisps. Top each with apple ring.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'84222D315DD6404D9A457E216000B8D2', X'5D194DA81B4B61458D9183C4C507AF11', X'5D194DA81B4B61458D9183C4C507AF11', 0, NULL, NULL, 2, NULL, 0, 'WHISK syrup and peanut butter in a medium microwave-safe bowl until smooth. Microwave on HIGH about 1 minute to warm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FDFC77F5FEEA35439148F2095EA27281', X'C8929A6743580A4787D86EDB63637D54', X'C8929A6743580A4787D86EDB63637D54', 0, NULL, NULL, 2, NULL, 0, 'STIR pancake mix, milk, oil and eggs in large bowl until smooth. Let stand 3 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F2E8282E491CA243ADBF233F201BCCF9', X'C8929A6743580A4787D86EDB63637D54', X'C8929A6743580A4787D86EDB63637D54', 1, NULL, NULL, 2, NULL, 0, 'Coat griddle or large skillet with no-stick cooking spray. Heat over medium-high heat (375°). Make 7 large pancakes, in batches, by pouring a scant 1/2 cup batter for each pancake onto hot griddle to form 7-inch circle. Cook until bubbles appear and edges are dry, about 1 to 1 1/2 minutes. Turn and cook an additional 1 minute or until golden brown.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'342D33B9E8BEEB42A5CE4AC19ACF2F66', X'C8929A6743580A4787D86EDB63637D54', X'C8929A6743580A4787D86EDB63637D54', 2, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Place 1 pancake on oven-safe serving plate; spread with 1/3 cup peanut butter. Top with another pancake. Completely cover surface with banana slices. Top with third pancake; spread with 1/4 cup strawberry jam. Completely cover surface with 1 cup sliced strawberries, mounding strawberries near outside edge. Repeat all layers. Top with remaining pancake. Cover with foil.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'47B240CEB4FF1F459CE454FAEE20D26E', X'C8929A6743580A4787D86EDB63637D54', X'C8929A6743580A4787D86EDB63637D54', 3, NULL, NULL, 2, NULL, 0, 'BAKE 5 to 10 minutes or until heated through. Insert 6 decorative wooden skewers into stack-up, if desired, to secure each wedge. Cut into wedges. Garnish with whipped topping, if desired. Serve with syrup.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9EB13B107642664687CFE2B6595C7344', X'4C89FDEC7D358348A018ECE46C238DE5', X'4C89FDEC7D358348A018ECE46C238DE5', 0, NULL, NULL, 2, NULL, 0, 'WHISK peanut butter, honey, water and cinnamon in large bowl until smooth. Let stand 5 minutes to thicken.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2C5D1456F4389B4F888BFAFF0FDF6AF2', X'4C89FDEC7D358348A018ECE46C238DE5', X'4C89FDEC7D358348A018ECE46C238DE5', 1, NULL, NULL, 2, NULL, 0, 'ADD diced apple, banana slices, walnuts and raisins; stir to coat.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DCE2AA715E432C4B9A99B65094EE5039', X'4C89FDEC7D358348A018ECE46C238DE5', X'4C89FDEC7D358348A018ECE46C238DE5', 2, NULL, NULL, 2, NULL, 0, 'SPOON fruit mixture horizontally down center of each tortilla. Fold bottom edge over filling. Push filling against the fold to tighten filling in place. Fold in left and right edges of tortillas about 2 inches. Roll up from bottom to completely enclose filling. Cut wraps in half before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6ADD8853C66047428ACCCD91804A9352', X'A73687E8905A9B4DB8C1A8CD14B80985', X'A73687E8905A9B4DB8C1A8CD14B80985', 0, NULL, NULL, 2, NULL, 0, 'BREAK graham cracker in half to make 2 squares.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'25353F68797A684593F93F8CE7105285', X'A73687E8905A9B4DB8C1A8CD14B80985', X'A73687E8905A9B4DB8C1A8CD14B80985', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on one side of 1 piece of graham cracker. Spread jam on one side of remaining graham cracker. Press gently together to form a sandwich.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC5244224D30604CB0480FB4625F618A', X'38F704B888EA744395A553274683B268', X'38F704B888EA744395A553274683B268', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter and fruit spread on bread slices. Top with coconut and chia seeds, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'44A144F81D68654EA754FA74A9A14D17', X'CC1CF539F5A43940AE0C151D72D18C3B', X'CC1CF539F5A43940AE0C151D72D18C3B', 0, NULL, NULL, 2, NULL, 0, 'STIR cashew butter into oatmeal in small serving bowl until smooth. Drizzle with honey. Top with blueberries and cinnamon, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B133F098E646D42A39F20F9B4C55BC3', X'39F6707B826B1049A557FC934A468EE6', X'39F6707B826B1049A557FC934A468EE6', 0, NULL, NULL, 2, NULL, 0, 'WHISK water and 1/4 cup peanut butter in medium bowl until blended. Whisk in pancake mix and cinnamon just until evenly moistened. Stir in chocolate chips. Let stand 3 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E333DD7C85CDFB4B8578DE82C987E028', X'39F6707B826B1049A557FC934A468EE6', X'39F6707B826B1049A557FC934A468EE6', 1, NULL, NULL, 2, NULL, 0, 'HEAT skillet over medium-high heat or electric griddle to 375°F. Coat with no-stick cooking spray. Pour 2 tablespoons batter for each pancake onto hot skillet. Cook 1 to 1 /2 minutes on each side or until golden brown.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CEF12BA8D0427545B7AC86D51B6F6EF0', X'39F6707B826B1049A557FC934A468EE6', X'39F6707B826B1049A557FC934A468EE6', 2, NULL, NULL, 2, NULL, 0, 'COMBINE syrup with remaining 1/4 cup peanut butter in microwave-safe bowl. Microwave on HIGH 30 seconds. Serve over warm pancakes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF862369B3718C4AAC52870C9475F71C', X'D8E5AED06022CB46BA991BB1341C9731', X'D8E5AED06022CB46BA991BB1341C9731', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Spread 1 tablespoon peanut butter over each toasted English muffin half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B2434D75BA1D8441B891F9543E8670BE', X'D8E5AED06022CB46BA991BB1341C9731', X'D8E5AED06022CB46BA991BB1341C9731', 1, NULL, NULL, 2, NULL, 0, 'TOP 4 muffin halves each with 1 slice cheese, several apple slices and 2 bacon strips. Sprinkle with cinnamon, if desired. Top with remaining muffin halves to form sandwiches. Place on baking sheet. Bake 3 to 5 minutes or until cheese begins to melt.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3B20E34F4816F042ACD42A6B0D1146DC', X'14F2F3D148E2F744ACFCD9CD9BEEBA49', X'14F2F3D148E2F744ACFCD9CD9BEEBA49', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on cut side of 1 bagel half. Spread jam on cut side of remaining bagel half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D5435D232B7C9540953F81F4B431ADEC', X'14F2F3D148E2F744ACFCD9CD9BEEBA49', X'14F2F3D148E2F744ACFCD9CD9BEEBA49', 1, NULL, NULL, 2, NULL, 0, 'SPRINKLE peanut butter and jam with sunflower kernels.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'526F49B96ED50C4EA5510797A9ABB4E7', X'4AEAEB3C0B185F46B2CAB3F13EE7C389', X'4AEAEB3C0B185F46B2CAB3F13EE7C389', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter evenly on inside surface of hot dog roll.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C1DFFB25FD1934190988B7F52F19717', X'4AEAEB3C0B185F46B2CAB3F13EE7C389', X'4AEAEB3C0B185F46B2CAB3F13EE7C389', 1, NULL, NULL, 2, NULL, 0, 'PLACE banana in roll. Top with desired amount of fruit spread. Sprinkle with nuts or raisins.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C99897A5B95D3A4CB35A89844CFE74AE', X'7E83363F8821AC49A4388E1FA85D6838', X'7E83363F8821AC49A4388E1FA85D6838', 0, NULL, NULL, 2, NULL, 0, 'CUT granola bar in half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'869B33508F34F448AF8F41A2C464FCB8', X'7E83363F8821AC49A4388E1FA85D6838', X'7E83363F8821AC49A4388E1FA85D6838', 1, NULL, NULL, 2, NULL, 0, 'SPREAD one half of bar with peanut butter. Spread remaining half with jam.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'140E817A041AFB4AB712048DCA29C818', X'7E83363F8821AC49A4388E1FA85D6838', X'7E83363F8821AC49A4388E1FA85D6838', 2, NULL, NULL, 2, NULL, 0, 'PRESS together to form bite.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1EAD44A807514C469A3DAFCEA818ADBC', X'1A693180C442FC43AEC2C4CCC067C4CD', X'1A693180C442FC43AEC2C4CCC067C4CD', 0, NULL, NULL, 2, NULL, 0, 'DIVIDE peanut butter onto one side of 3 mini pancakes, spreading evenly.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0B6FF7A12CF2D146994D77B397D458D7', X'1A693180C442FC43AEC2C4CCC067C4CD', X'1A693180C442FC43AEC2C4CCC067C4CD', 1, NULL, NULL, 2, NULL, 0, 'DIVIDE jam onto one side of remaining 3 mini pancakes, spreading evenly. Place on top of peanut peanut layer, jam side down. Stack filled mini pancakes on serving plate.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FE1427E056CE6D4CBCBBE61FA4ED7BD2', X'A3DF5D1867353349BCC05D9B8DD19112', X'A3DF5D1867353349BCC05D9B8DD19112', 0, NULL, NULL, 2, NULL, 0, 'TRIM edges from bread slices using a serrated knife. Make 1 star in center of 3 bread slices by cutting with 1-inch star-shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5DE10BFAC4523746BA039D50BD8C025F', X'A3DF5D1867353349BCC05D9B8DD19112', X'A3DF5D1867353349BCC05D9B8DD19112', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter evenly onto 3 whole bread slices. Spoon jam evenly onto center of peanut butter, spreading gently into a 2-inch circle. Top with cut-out bread slices to make sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'30F56A66053D1E4583A1075900E84CB1', X'EB5318034A0B81439B59E18CC72E4EFC', X'EB5318034A0B81439B59E18CC72E4EFC', 0, NULL, NULL, 2, NULL, 0, 'SPLIT and toast English muffin. Spread peanut spread on both sides of muffin. Spread or squeeze fruit spread over peanut butter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6691303CBE352340A2A58EF9BD25AA4C', X'EB5318034A0B81439B59E18CC72E4EFC', X'EB5318034A0B81439B59E18CC72E4EFC', 1, NULL, NULL, 2, NULL, 0, 'TOP with banana slices. Drizzle with warmed fudge topping to taste. Sprinkle with coconut, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F27B442FB4177C4A962BB6902A31D0E8', X'8FA02371211540488458CFF2530F0414', X'8FA02371211540488458CFF2530F0414', 0, NULL, NULL, 2, NULL, 0, 'STIR peanut butter, honey and cinnamon in small bowl. Arrange apple rings on serving plates. Spread rings evenly with thin layer of peanut butter mixture.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'67AAB11A8A168145B1A7635FA5C6C507', X'8FA02371211540488458CFF2530F0414', X'8FA02371211540488458CFF2530F0414', 1, NULL, NULL, 2, NULL, 0, 'LIGHTLY PRESS granola cereal into peanut butter mixture. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2333505D18F2BC48937FEDEA0A862B6E', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Spray a 9 x 5 x 3-inch loaf pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2115EBE2DFB8904EBC9FDCDF9B2B11AA', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', 1, NULL, NULL, 2, NULL, 0, 'COMBINE flour, sugar, baking powder and salt in large bowl. Add banana, peanut spread, milk, oil and egg. Beat at medium speed of electric mixer, scraping sides and bottom of bowl. Mix just until blended. Pour into prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'47DA13CD328B3B41B67166E89089EFBC', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', 2, NULL, NULL, 2, NULL, 0, 'BAKE 60 to 65 minutes or until toothpick inserted in center comes out clean. (Cover top loosely with foil after 45 minutes to prevent over-browning.)');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8ABC72E4190D904EBC3A09559F7B2896', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', X'ED28BDBFBF090E4498F5BD2E2E9F3FB5', 3, NULL, NULL, 2, NULL, 0, 'COOL 10 minutes in pan. Remove to cooling rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4BB015F831070419008EB6072E4CF07', X'83AB449E129C7340A3055F9118416591', X'83AB449E129C7340A3055F9118416591', 0, NULL, NULL, 2, NULL, 0, 'COMBINE oats, water and salt in a medium saucepan. Bring to a boil over medium-high heat, stirring occasionally. Reduce heat and cook 5 minutes or until thick and creamy, stirring occasionally.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'415F79E0EA883543BAD2E72B4230B049', X'83AB449E129C7340A3055F9118416591', X'83AB449E129C7340A3055F9118416591', 1, NULL, NULL, 2, NULL, 0, 'ADD peanut butter, bananas and cinnamon; stir to combine. Serve immediately. Top with brown sugar and milk, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E72BDFC54B5F543B1FF097132FEA01A', X'3F11072144397846A403CA641052695E', X'3F11072144397846A403CA641052695E', 0, NULL, NULL, 2, NULL, 0, 'PLACE milk, banana, peanut butter and ice in blender container.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'382DC8EBB1BB144CA189AD9E897B942D', X'3F11072144397846A403CA641052695E', X'3F11072144397846A403CA641052695E', 1, NULL, NULL, 2, NULL, 0, 'COVER and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'861EE2D92261824788209C9D267F3ACD', X'388B9E101DEF1740932D7CE5247CA15E', X'388B9E101DEF1740932D7CE5247CA15E', 0, NULL, NULL, 2, NULL, 0, 'COAT 13 x 9-inch baking dish with no-stick cooking spray. Stir brown sugar, butter, peanut butter and corn syrup in small saucepan over medium heat, stirring constantly, until smooth and thickened. Pour peanut butter mixture into baking dish. Place bread slices over mixture, trimming to fit.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8F39F76EDB93DF489BF2990B2BD65F52', X'388B9E101DEF1740932D7CE5247CA15E', X'388B9E101DEF1740932D7CE5247CA15E', 1, NULL, NULL, 2, NULL, 0, 'WHISK together eggs, milk, salt, vanilla and almond extracts in medium bowl. Pour egg mixture over bread. Cover and chill 8 hours or overnight.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2F298C09604DDD4B89D023790BAE0919', X'388B9E101DEF1740932D7CE5247CA15E', X'388B9E101DEF1740932D7CE5247CA15E', 2, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Remove cover. Bake 45 minutes or until lightly browned. Invert onto large serving platter. Cut into servings. Serve with whipped cream.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6580981F9C41784D8BAAFAED464A63DE', X'EFDACC0559C18748B2D4B46B30DEEADF', X'EFDACC0559C18748B2D4B46B30DEEADF', 0, NULL, NULL, 2, NULL, 0, 'COMBINE pancake mix, milk, eggs and remaining 1/4 cup peanut butter in large bowl. Coarsely crush 1 cup granola cereal. Stir into batter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E3919DCE7B2F184BB5F9ACCC938011EA', X'EFDACC0559C18748B2D4B46B30DEEADF', X'EFDACC0559C18748B2D4B46B30DEEADF', 1, NULL, NULL, 2, NULL, 0, 'WHISK together syrup and 1/4 cup peanut butter in medium microwave-safe bowl until smooth. Microwave on HIGH 30 to 45 seconds to warm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AFCF60C85D9B664DA42788A1711A76D8', X'EFDACC0559C18748B2D4B46B30DEEADF', X'EFDACC0559C18748B2D4B46B30DEEADF', 2, NULL, NULL, 2, NULL, 0, 'COAT griddle with no-stick cooking spray. Heat over medium heat. Pour 1/8 cup (2 tablespoons) batter for each pancake onto griddle. Cook pancakes until bubbles begin to break on surface. Turn and cook until golden brown. Serve pancakes topped sliced banana and peanut butter syrup. Sprinkle evenly with remaining 1/2 cup granola cereal.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4332280A06644E48947490F35B5A42C4', X'F9AB355B77329445ABE5CA4793052C6D', X'F9AB355B77329445ABE5CA4793052C6D', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on 1 side of 1 slice of bread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D4152E0518BA445A40B7FFD5C7AE4D4', X'F9AB355B77329445ABE5CA4793052C6D', X'F9AB355B77329445ABE5CA4793052C6D', 1, NULL, NULL, 2, NULL, 0, 'PLACE pineapple on top of peanut butter layer. Spoon marshmallow creme over pineapple. Top with remaining slice of bread to form a sandwich. Cut with a serrated knife on a diagonal.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A37F1C693DF59541B3EBF5A5C08824F1', X'CE4B1CA9C7E1A945996B1D1589794CD6', X'CE4B1CA9C7E1A945996B1D1589794CD6', 0, NULL, NULL, 2, NULL, 0, 'HEAT frying pan or griddle to medium. Roll each slice of bread flat with rolling pin. Spread 1 tablespoon of peanut butter and 1 teaspoon apricot preserves on each slice of flattened bread. Take three apple slices and place them in single layer lengthwise along top of bread. Repeat with second slice. Sprinkle both slices with 1/2 teaspoon cinnamon sugar.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A73A5F2EC41EF740B87D59767C164879', X'CE4B1CA9C7E1A945996B1D1589794CD6', X'CE4B1CA9C7E1A945996B1D1589794CD6', 1, NULL, NULL, 2, NULL, 0, 'ROLL each slice of bread from apple end, gently but tightly. Lay seam side down. Add butter to griddle or pan to coat it. Mix egg, milk and vanilla in shallow bowl. Dip each roll in egg wash and place seam side down on pan. Lightly brown sandwich. Carefully turn until all sides are browned.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C68DB32F119E374886DAAA90BCE076B0', X'CE4B1CA9C7E1A945996B1D1589794CD6', X'CE4B1CA9C7E1A945996B1D1589794CD6', 2, NULL, NULL, 2, NULL, 0, 'REMOVE from pan and sprinkle or roll with remaining cinnamon sugar to coat. Combine remaining 2 tablespoons of peanut butter and 2 tablespoons maple syrup in small heatproof dish. Heat for 30 seconds in microwave and mix until combined. Serve as dipping sauce for roll-up.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B7CB5FC02C12484284E595529B224A75', X'DE39F8E224106F41B71DC56A1C218E2F', X'DE39F8E224106F41B71DC56A1C218E2F', 0, NULL, NULL, 2, NULL, 0, 'SPOON half of yogurt into two parfait glasses. Top with half of pineapple. Stir together peanut butter, water and honey until smooth. Drizzle half of peanut butter mixture over pineapple in parfait glasses. Top with half of granola cereal.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C7C6DE76E1183C4B91E8685FE71E1A8E', X'DE39F8E224106F41B71DC56A1C218E2F', X'DE39F8E224106F41B71DC56A1C218E2F', 1, NULL, NULL, 2, NULL, 0, 'REPEAT layers using remaining yogurt, pineapple, peanut butter mixture and granola. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A56B40F560CB5144B0E9C60463550140', X'24A0DCB578AD0047B7E093458CDAB369', X'24A0DCB578AD0047B7E093458CDAB369', 0, NULL, NULL, 2, NULL, 0, 'CUT pita bread in half crosswise. Separate cut edge gently to create two pita bread pockets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDB156405ABE684FAFE56E8CB62BEDC8', X'24A0DCB578AD0047B7E093458CDAB369', X'24A0DCB578AD0047B7E093458CDAB369', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on top, inside surface of pockets. Spread jam on bottom, inside surface of pockets. Fill pockets with banana and strawberries.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CEFF6B1398304941B0A5D7337A20D90B', X'6118295ADE15DE45987ED32492D3A31C', X'6118295ADE15DE45987ED32492D3A31C', 0, NULL, NULL, 2, NULL, 0, 'STIR peanut butter and dates in small bowl until blended. Spread evenly on rice cakes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F9FA2AA589BE7F4787B4CAB13877A232', X'6118295ADE15DE45987ED32492D3A31C', X'6118295ADE15DE45987ED32492D3A31C', 1, NULL, NULL, 2, NULL, 0, 'TOP with banana and pineapple. Sprinkle with coconut, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FEBC7DBC6DC77849A51C3AC8B795E2F8', X'18C725EAFD56364C96D51FBC0858DF8D', X'18C725EAFD56364C96D51FBC0858DF8D', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on one waffle. Spread jam on remaining waffle. Press together to form a sandwich. Cut into 4 servings.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9442BE5891F2B44C9D942C1FFF52A2E1', X'3D2E59B81E775E47A728F2118646E6BA', X'3D2E59B81E775E47A728F2118646E6BA', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 400ºF. Spray a 12-cup muffin pan with a no-stick cooking spray or line with paper cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'03DF673654AA2A4F871DE768367DF918', X'3D2E59B81E775E47A728F2118646E6BA', X'3D2E59B81E775E47A728F2118646E6BA', 1, NULL, NULL, 2, NULL, 0, 'STIR together flour, sugar, baking powder and salt in medium bowl. Combine egg, milk and oil in small bowl. Add liquids to flour mixture, stirring just until dry ingredients are moistened. Divide batter evenly among cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'344DA1D821E93C43BFCB0A759CA4FB1F', X'3D2E59B81E775E47A728F2118646E6BA', X'3D2E59B81E775E47A728F2118646E6BA', 2, NULL, NULL, 2, NULL, 0, 'COMBINE sugar, flour, peanut butter and salt in small bowl, mixing with fork until crumbly. Divide topping over muffins. Lightly stir into muffin with knife.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9180DD485986B049B8E55DADA880F890', X'3D2E59B81E775E47A728F2118646E6BA', X'3D2E59B81E775E47A728F2118646E6BA', 3, NULL, NULL, 2, NULL, 0, 'BAKE 18 to 23 minutes or until golden brown. Cool in pan on rack 5 minutes. Remove from pan to rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FB367B7209251A45B54208F32DB36529', X'C2924D436853C84D8424DCDC7017F318', X'C2924D436853C84D8424DCDC7017F318', 0, NULL, NULL, 2, NULL, 0, 'LAYER peanut butter, fruit spread and bacon on one side of 2 waffles. Place remaining waffles on top to form two sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B467F091A312049AEB50CFBD7D0CE20', X'C2924D436853C84D8424DCDC7017F318', X'C2924D436853C84D8424DCDC7017F318', 1, NULL, NULL, 2, NULL, 0, 'HEAT panini press or large skillet over medium heat. Brush tops of sandwiches with oil. Turn and brush bottoms with remaining oil.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E8F000478B80E74BA9427A627D393B14', X'C2924D436853C84D8424DCDC7017F318', X'C2924D436853C84D8424DCDC7017F318', 2, NULL, NULL, 2, NULL, 0, 'COOK sandwiches in skillet 2 to 3 minutes on each side or until golden brown. Remove from heat. Cut each sandwich in half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'77CC8CEDB69BAE498363B15E27D7A6ED', X'AE0F25B87188B24687031CF2ED6C2DF3', X'AE0F25B87188B24687031CF2ED6C2DF3', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Spray an 8 x 8-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C51D5AD8D697DF428ED0F3F6AA7FC581', X'AE0F25B87188B24687031CF2ED6C2DF3', X'AE0F25B87188B24687031CF2ED6C2DF3', 1, NULL, NULL, 2, NULL, 0, 'BREAK eggs into a large bowl; whisk until well combined. Add milk, sugar, vanilla and cinnamon; whisk until well combined. Add peanut butter; whisk until well mixed.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BCF4DC58A455F441AC527123F5AE194D', X'AE0F25B87188B24687031CF2ED6C2DF3', X'AE0F25B87188B24687031CF2ED6C2DF3', 2, NULL, NULL, 2, NULL, 0, 'CUT or tear bread into 1-inch squares and add to bowl. Add raisins and gently stir to mix well. Pour bread mixture into prepared baking pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC76BF755AF6C044846662B7B60C5F3B', X'AE0F25B87188B24687031CF2ED6C2DF3', X'AE0F25B87188B24687031CF2ED6C2DF3', 3, NULL, NULL, 2, NULL, 0, 'BAKE until the mixture is set and the top is golden brown (about 25 minutes). Remove from oven and place on a baking rack to cool.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B42CAAB7FD8B364F90AE9F14CF1E375C', X'AE0F25B87188B24687031CF2ED6C2DF3', X'AE0F25B87188B24687031CF2ED6C2DF3', 4, NULL, NULL, 2, NULL, 0, 'SERVE warm, spooned into bowls and topped with fruit yogurt.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA7F1B8D78FEFA498C7B805C4783EEF0', X'798DDB176CA38E41B5F64C2A5A2CD469', X'798DDB176CA38E41B5F64C2A5A2CD469', 0, NULL, NULL, 2, NULL, 0, 'STIR together flour, sugar and baking powder in large bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9BEF355B3BE294FA3B6C5E4195590CC', X'798DDB176CA38E41B5F64C2A5A2CD469', X'798DDB176CA38E41B5F64C2A5A2CD469', 1, NULL, NULL, 2, NULL, 0, 'BEAT egg with peanut butter in small bowl until blended. Stir in milk and oil. Add all at once to the dry ingredients, beating until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'87298FB6630D854DA6061984D17F481A', X'798DDB176CA38E41B5F64C2A5A2CD469', X'798DDB176CA38E41B5F64C2A5A2CD469', 2, NULL, NULL, 2, NULL, 0, 'HEAT skillet over medium-high heat or griddle to 375ºF. Spray with no-stick cooking spray. Pour 2 tablespoons batter for each pancake onto skillet. Cook until bubbles break the surface. Turn and brown the other side.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0D42FAEB648A6E47818DA42B776430D4', X'798DDB176CA38E41B5F64C2A5A2CD469', X'798DDB176CA38E41B5F64C2A5A2CD469', 3, NULL, NULL, 2, NULL, 0, 'SERVE with syrup, fresh fruit or powdered sugar.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C50A2097EA8C684CB2E0E589F1898575', X'E3E631503FE6064BA929994DDEE1FFFC', X'E3E631503FE6064BA929994DDEE1FFFC', 0, NULL, NULL, 2, NULL, 0, 'PLACE tortilla chips on serving plate. Top with apples.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8D7AB466EFEF0847A7B9DE0351F705B1', X'E3E631503FE6064BA929994DDEE1FFFC', X'E3E631503FE6064BA929994DDEE1FFFC', 1, NULL, NULL, 2, NULL, 0, 'STIR peanut butter, caramel topping and water in small bowl until smooth. Drizzle evenly over nachos. Top with small spoonfuls of jam. Sprinkle with cheese and peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'865CB00212858B49827D3D0E4E5E6E3A', X'4D50541719773A48A3150759F6A9B647', X'4D50541719773A48A3150759F6A9B647', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Coat 12 muffin cups with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7974A9E00800FF4283D907F45B5B5C0E', X'4D50541719773A48A3150759F6A9B647', X'4D50541719773A48A3150759F6A9B647', 1, NULL, NULL, 2, NULL, 0, 'STIR together flour, baking powder and salt in large bowl. Add 2/3 cup sugar, milk, peanut butter, oil and eggs. Beat with mixer on low speed just until moistened. Divide evenly into prepared muffin cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E93D5DBDA8678246A85FEE564CD03AE5', X'4D50541719773A48A3150759F6A9B647', X'4D50541719773A48A3150759F6A9B647', 2, NULL, NULL, 2, NULL, 0, 'MAKE small indentations in center of batter with back of spoon. Place 2 teaspoons preserves in center of each. Sprinkle with remaining 1/3 cup sugar.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E31266A641D4504EBF66DE91C7302F3C', X'4D50541719773A48A3150759F6A9B647', X'4D50541719773A48A3150759F6A9B647', 3, NULL, NULL, 2, NULL, 0, 'BAKE 22 to 24 minutes or until toothpick inserted into plain muffin batter comes out clean. Cool 5 minutes. Remove from pan to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0FF05C3EEC894429CD692F886FAF5E6', X'8868F854A18C9649BD96E20A5C74AA44', X'8868F854A18C9649BD96E20A5C74AA44', 0, NULL, NULL, 2, NULL, 0, 'BEAT eggs, milk, vanilla and salt in shallow bowl until well blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F920902D64763E498EEDDC05C6784A78', X'8868F854A18C9649BD96E20A5C74AA44', X'8868F854A18C9649BD96E20A5C74AA44', 1, NULL, NULL, 2, NULL, 0, 'MELT butter on griddle or in large skillet. Dip bread into egg mixture, coating both sides. Fry bread until golden brown on each side.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FCBB3E91356FA3458455C3B3FC5CD592', X'8868F854A18C9649BD96E20A5C74AA44', X'8868F854A18C9649BD96E20A5C74AA44', 2, NULL, NULL, 2, NULL, 0, 'COMBINE syrup and peanut butter in small microwave-safe bowl. Microwave for 30 seconds or until warm. Stir until blended. Serve french toast garnished with chopped peanuts and warm peanut butter syrup.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE74C2FF69EE6740A487471074D0DE19', X'E9648332D764E94EBE0034F0E9D1911E', X'E9648332D764E94EBE0034F0E9D1911E', 0, NULL, NULL, 2, NULL, 0, 'BRING water and salt to a boil in a medium saucepan. Add oats and boil one minute. Reduce heat, stir in milk and dried fruit and simmer for two minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'07F0BAC3DAB36F4689DB1AA84E5E5D15', X'E9648332D764E94EBE0034F0E9D1911E', X'E9648332D764E94EBE0034F0E9D1911E', 1, NULL, NULL, 2, NULL, 0, 'REMOVE from heat and stir in brown sugar, peanut butter and butter. Cover and let stand 5 minutes. Stir and serve.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5677F380358CDD4582B818E5EAAC9ED3', X'7F48BF09A7EE1D4C9D5D278C0987F3F7', X'7F48BF09A7EE1D4C9D5D278C0987F3F7', 0, NULL, NULL, 2, NULL, 0, 'PLACE prepared pancakes on serving plates.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3FCFCE94B7C6E49BF1251F4C6F3044E', X'7F48BF09A7EE1D4C9D5D278C0987F3F7', X'7F48BF09A7EE1D4C9D5D278C0987F3F7', 1, NULL, NULL, 2, NULL, 0, 'SPREAD about 2 tablespoons peanut butter on top of each serving of pancakes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1328D8B3DDC19846B3329618046D82BE', X'7F48BF09A7EE1D4C9D5D278C0987F3F7', X'7F48BF09A7EE1D4C9D5D278C0987F3F7', 2, NULL, NULL, 2, NULL, 0, 'SPRINKLE each serving with about 2 tablespoons granola. Top evenly with banana. Drizzle each serving with about 2 tablespoons honey.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B8FC3308E1F354AB6EBCC579C19DBAF', X'08F8BF8F3F27784FAA1686DD8542DB45', X'08F8BF8F3F27784FAA1686DD8542DB45', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Spray a a 9 x 3 x 5-inch loaf pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ACCA07329C81C049B716D7698EAB3722', X'08F8BF8F3F27784FAA1686DD8542DB45', X'08F8BF8F3F27784FAA1686DD8542DB45', 1, NULL, NULL, 2, NULL, 0, 'MIX flour, sugar, baking powder and salt in large bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'55957E1A6D8E184C90E283704D1CF8F2', X'08F8BF8F3F27784FAA1686DD8542DB45', X'08F8BF8F3F27784FAA1686DD8542DB45', 2, NULL, NULL, 2, NULL, 0, 'CUT in peanut butter with a fork. Add egg and milk; stir just enough to moisten dry ingredients. Pour into prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EA2ED3B2B60CCE4B9EE1CBD447958D9D', X'08F8BF8F3F27784FAA1686DD8542DB45', X'08F8BF8F3F27784FAA1686DD8542DB45', 3, NULL, NULL, 2, NULL, 0, 'BAKE 60 minutes or until wooden pick inserted near center comes out clean. Cool in pan 10 minutes. Invert and remove loaf to cooling rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6915FDCEC6B82947B2A391ADEDD3A5EE', X'D6E586BDF762244EABFB7FCB62FD4281', X'D6E586BDF762244EABFB7FCB62FD4281', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 300ºF. Combine peanut butter, honey and cinnamon in small saucepan; heat through. Remove from heat and stir in vanilla.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE753228B820F64EB3494B7AC0D531DC', X'D6E586BDF762244EABFB7FCB62FD4281', X'D6E586BDF762244EABFB7FCB62FD4281', 1, NULL, NULL, 2, NULL, 0, 'PLACE oats in a large, shallow roasting pan or a 15 x 10 x 1-inch baking pan. Pour warm peanut butter mixture over oats and stir gently until oats are coated. Spread evenly in pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'915F4BF352F5074CA523F928C3720F2E', X'D6E586BDF762244EABFB7FCB62FD4281', X'D6E586BDF762244EABFB7FCB62FD4281', 2, NULL, NULL, 2, NULL, 0, 'BAKE 35 to 40 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'036C27698BDE62439EB7720E0F9001C9', X'D6E586BDF762244EABFB7FCB62FD4281', X'D6E586BDF762244EABFB7FCB62FD4281', 3, NULL, NULL, 2, NULL, 0, 'REMOVE pan from oven. Stir raisins, nuts and figs into baked mixture. Return to oven and let dry 1 1/2 hours, stirring occasionally. Store in covered containers.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9CFFC80D3880DA41BB111EA71B23F4C6', X'6C57698B77F3D5459E1C4D2CFE9507A0', X'6C57698B77F3D5459E1C4D2CFE9507A0', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 400ºF. Spray a muffin pan with no-stick cooking spray. Stir together flour, sugar, baking powder and salt.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC02E3EFCFB6BB40A757D55F1FF3D975', X'6C57698B77F3D5459E1C4D2CFE9507A0', X'6C57698B77F3D5459E1C4D2CFE9507A0', 1, NULL, NULL, 2, NULL, 0, 'CUT in peanut butter and butter until mixture resembles coarse crumbs. Add milk and eggs all at once, stirring just until moistened. Fill prepared muffin cups 2/3 full.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DA85B6D1660D744FB098345E9087FA92', X'6C57698B77F3D5459E1C4D2CFE9507A0', X'6C57698B77F3D5459E1C4D2CFE9507A0', 2, NULL, NULL, 2, NULL, 0, 'BAKE 15 to 17 minutes. While puffs are baking, melt jelly in a small saucepan over medium heat. Remove puffs from oven. Immediately brush tops with melted jelly; dip in peanuts. Serve hot.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FAB30C1B2B378A42AC28183464F056AD', X'AA921FC9BB810348B24200316DBDD0B1', X'AA921FC9BB810348B24200316DBDD0B1', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter evenly on top of rice cakes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'058EEE192BD63F45B100C98FFEC56B83', X'AA921FC9BB810348B24200316DBDD0B1', X'AA921FC9BB810348B24200316DBDD0B1', 1, NULL, NULL, 2, NULL, 0, 'TOP evenly with dried fruit, banana and pineapple.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B91905702E87C74CA7CC5AC89AEDBE16', X'AA921FC9BB810348B24200316DBDD0B1', X'AA921FC9BB810348B24200316DBDD0B1', 2, NULL, NULL, 2, NULL, 0, 'SPRINKLE with coconut, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8578A82D74D5404395687AAFE9978DF0', X'C0728BD0A7E9504795CC707F89A5B446', X'C0728BD0A7E9504795CC707F89A5B446', 0, NULL, NULL, 2, NULL, 0, 'LINE 13 x 9-inch baking pan with foil, extending over edges of pan. Coat with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C54594CA7CC67845B63814AE6B1F1B81', X'C0728BD0A7E9504795CC707F89A5B446', X'C0728BD0A7E9504795CC707F89A5B446', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE butter and marshmallows in large microwave-safe bowl on HIGH 1 1/2 minutes. Stir in peanut butter until smooth. Add cereal; stir until evenly coated. Press into prepared pan. Cool completely. Cut out stars using 2-inch star cookie cutter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB56BBC16853F74FBEC4BA91F94BBF2D', X'C0728BD0A7E9504795CC707F89A5B446', X'C0728BD0A7E9504795CC707F89A5B446', 2, NULL, NULL, 2, NULL, 0, 'SPREAD small dollop of fruit spread on center of each star treat. Top with red raspberry, mini marshmallow and blueberry. Insert decorative skewer through top center. Repeat with remaining treats. Serve with whipped peanut butter for dipping if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FB638B9CE0CAE14E869A8A5046EDA86C', X'088F6FA02FD49C428225828FB309C075', X'088F6FA02FD49C428225828FB309C075', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 275°F. Coat 15 x 10-inch baking pan with no-stick cooking spray. Place popcorn in extra-large bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3C40AA77EB7F034E87A7F0F07D54B82C', X'088F6FA02FD49C428225828FB309C075', X'088F6FA02FD49C428225828FB309C075', 1, NULL, NULL, 2, NULL, 0, 'COMBINE corn syrup and sugar in medium microwave-safe bowl. Microwave on HIGH 2 minutes or until mixture boils. Remove from heat. Stir in butter, almond butter, vanilla, baking soda and salt until smooth. Pour over popcorn. Stir until evenly coated. Spread on prepared baking pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'82252BFDB87DB546A008BC431181425E', X'088F6FA02FD49C428225828FB309C075', X'088F6FA02FD49C428225828FB309C075', 2, NULL, NULL, 2, NULL, 0, 'BAKE 35 to 40 minutes, stirring every 10 minutes. Remove from oven. Cool completely. Break into pieces.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8BF57CD88E574C43BD4C2BB066A467BD', X'4F12A9C9BA4B8A478ADEF7415F1564CF', X'4F12A9C9BA4B8A478ADEF7415F1564CF', 0, NULL, NULL, 2, NULL, 0, 'COMBINE cinnamon and granulated sugar. Set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0EB9718309794A4FBA6DA087F38D0300', X'4F12A9C9BA4B8A478ADEF7415F1564CF', X'4F12A9C9BA4B8A478ADEF7415F1564CF', 1, NULL, NULL, 2, NULL, 0, 'SPRAY one side of tortilla generously with butter cooking spray. Place spread side down in 10-inch skillet. Spread topside with peanut butter, top with banana slices, sprinkle with brown sugar and top with remaining tortilla. Spray top of tortilla generously with butter cooking spray and sprinkle with half of the cinnamon and sugar mixture.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDA568602020314090C6C419DDDC0C5C', X'4F12A9C9BA4B8A478ADEF7415F1564CF', X'4F12A9C9BA4B8A478ADEF7415F1564CF', 2, NULL, NULL, 2, NULL, 0, 'COVER skillet. Cook over medium-high heat until the bottom is golden brown. Turn tortilla with a wide spatula. Sprinkle top with remaining cinnamon and sugar. Continue cooking covered until bottom is golden brown. Remove to cutting board and slice into 8 wedges. Serve warm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'23EFC95B9D320B409EAE1C863BBB7F56', X'A8B7EBE194493348BF04CC2EDD9061EC', X'A8B7EBE194493348BF04CC2EDD9061EC', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 325°F. Line 24 muffin cups with paper baking cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F096A7FBF18C54EA8832F803EE69389', X'A8B7EBE194493348BF04CC2EDD9061EC', X'A8B7EBE194493348BF04CC2EDD9061EC', 1, NULL, NULL, 2, NULL, 0, 'BEAT eggs, bananas, oil, water and vanilla in large bowl with electric mixer on medium speed until blended. Add pancake mix, sugar, cocoa and salt. Beat until smooth. Fill baking cups 2/3 full.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B4D529CE5F252944A63DF45F7D26EE1B', X'A8B7EBE194493348BF04CC2EDD9061EC', X'A8B7EBE194493348BF04CC2EDD9061EC', 2, NULL, NULL, 2, NULL, 0, 'BAKE 18 to 20 minutes or until toothpick inserted in center comes out clean. Cool in pans 5 minutes. Remove from pans and cool completely on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18E9F9F81D6BD948863394FBA67C680F', X'A8B7EBE194493348BF04CC2EDD9061EC', X'A8B7EBE194493348BF04CC2EDD9061EC', 3, NULL, NULL, 2, NULL, 0, 'BEAT frosting, peanut butter and milk in medium bowl with electric mixer until blended. Spread on cooled cupcakes. Place coconut in small bowl. Dip frosted top of each cupcake in coconut to cover. Make a rounded indentation on the top of each cupcake, using the back of a small spoon, to form a nest. Arrange 5 to 6 jelly beans in each nest.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'54737C33B1F9EA48A0D71197C4D56F3F', X'EFEA5A3D86B6D7429778F6954880021E', X'EFEA5A3D86B6D7429778F6954880021E', 0, NULL, NULL, 2, NULL, 0, 'PREPARE brownie mix according to package directions using oil, water and eggs. Bake in 13 x 9-inch pan according to package directions. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C84B5F4E17D8EF49B8E9024FEFBA8C25', X'EFEA5A3D86B6D7429778F6954880021E', X'EFEA5A3D86B6D7429778F6954880021E', 1, NULL, NULL, 2, NULL, 0, 'BEAT heavy cream, powdered sugar and vanilla in large bowl with electric mixer on medium speed until stiff.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1B9A5AFE172FA746885F097BA8844FCD', X'EFEA5A3D86B6D7429778F6954880021E', X'EFEA5A3D86B6D7429778F6954880021E', 2, NULL, NULL, 2, NULL, 0, 'BEAT cream cheese, peanut butter and honey in medium bowl with same beaters until smooth. Fold in 1 1/2 cups whipped cream mixture until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C299BE740B5D4541838C2E3A19D9AEC5', X'EFEA5A3D86B6D7429778F6954880021E', X'EFEA5A3D86B6D7429778F6954880021E', 3, NULL, NULL, 2, NULL, 0, 'CUT cooled brownies into 1-inch cubes. Layer 1/3 of brownie cubes in bottom of trifle dish or 2-quart glass bowl. Top with half of peanut butter mousse, half of the sliced banana, 1 1/4 cups whipped cream and 1 tablespoon chopped peanuts. Repeat all layers. Top with remaining brownie cubes. Spoon remaining whipped cream onto center. Sprinkle with remaining 2 tablespoons chopped peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A65AFACAE6F06745AE92FB7AF69A42F1', X'E03F1A34C69EB14184F0C75A9B5FF538', X'E03F1A34C69EB14184F0C75A9B5FF538', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 325°F. Line 8-inch square baking pan with foil, extending foil over edges of pan. Coat with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CE1421CA790C4D44ADC03B90C4265BBC', X'E03F1A34C69EB14184F0C75A9B5FF538', X'E03F1A34C69EB14184F0C75A9B5FF538', 1, NULL, NULL, 2, NULL, 0, 'BEAT butter and brown sugar in large bowl with electric mixer on medium speed until blended. Add cashew butter, egg and vanilla. Beat until smooth. Stir in flour and baking powder until blended. Stir in cashews. Spread evenly in prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D4281B0229431498D52FF27573EF6F5', X'E03F1A34C69EB14184F0C75A9B5FF538', X'E03F1A34C69EB14184F0C75A9B5FF538', 2, NULL, NULL, 2, NULL, 0, 'BAKE 35 to 38 minutes or until toothpick inserted in center comes out clean. Cool completely on wire rack. Remove from pan by lifting edges of foil. Remove foil. Cut into squares.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F237EB45C4E94D4C9F5DCE2BF71AF49D', X'41A74208C8355548819714F20E787D09', X'41A74208C8355548819714F20E787D09', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350°F. Spray 13x9-inch pan (dark pan not recommended) with no-stick cooking spray or line with nonstick foil. Evenly arrange cookie rounds in pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F6F387E32D5F99479F21A0B16C195429', X'41A74208C8355548819714F20E787D09', X'41A74208C8355548819714F20E787D09', 1, NULL, NULL, 2, NULL, 0, 'Bake 24 to 26 minutes or until light golden brown. Cool 15 minutes on cooling rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D2F152D4C471F547BBC5B9437C154097', X'41A74208C8355548819714F20E787D09', X'41A74208C8355548819714F20E787D09', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in 2-quart heavy saucepan, melt butter over medium heat. Stir in condensed milk, brown sugar and granulated sugar until blended. Add graham cracker crumbs; mix well (mixture will be thick). Bring to a boil, stirring constantly. Reduce heat to low; cook 5 minutes, stirring constantly, or until slightly thickened. Pour caramel mixture over warm cookie crust, spreading evenly.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'42A7507F895B8C4DAF2ED0DF28819E5A', X'41A74208C8355548819714F20E787D09', X'41A74208C8355548819714F20E787D09', 3, NULL, NULL, 2, NULL, 0, 'In medium microwavable bowl, microwave chocolate chips on High 1 minute to 1 minute 20 seconds, stirring every 30 seconds, until smooth. Stir in peanut butter until blended. Spread evenly over caramel layer. Sprinkle with chopped peanuts. Refrigerate 1 hour or until chocolate is set. For bars, cut into 6 rows by 6 rows. Store covered in refrigerator.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B1C54C4D3C01344888C17F43175AD1FC', X'CD589BF651AE3A45B534CEE70E14D0FF', X'CD589BF651AE3A45B534CEE70E14D0FF', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat 13 x 9-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A9A773A40F43E04D8F3E5B97A51A5BE5', X'CD589BF651AE3A45B534CEE70E14D0FF', X'CD589BF651AE3A45B534CEE70E14D0FF', 1, NULL, NULL, 2, NULL, 0, 'BEAT brown sugar, butter and vanilla in large bowl with mixer on medium speed until fluffy. Stir in oats, flour, baking soda and salt until dough forms. Reserve 1 cup dough for topping. Press remaining dough into prepared pan to form a crust.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0E2BBF0AB54EF479AEB0BDA5DDA61A5', X'CD589BF651AE3A45B534CEE70E14D0FF', X'CD589BF651AE3A45B534CEE70E14D0FF', 2, NULL, NULL, 2, NULL, 0, 'COMBINE sweetened condensed milk and peanut butter in medium bowl until smooth. Spread evenly over dough. Crumble reserved dough over top. Sprinkle evenly with chocolate pieces.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'756BBE635CEAF34D8798CFAF04F0EB4F', X'CD589BF651AE3A45B534CEE70E14D0FF', X'CD589BF651AE3A45B534CEE70E14D0FF', 3, NULL, NULL, 2, NULL, 0, 'BAKE 34 to 36 minutes or until golden brown. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D89853D4C7D1F34591C34E0FD4A890DA', X'5B55EFE37D24F942959B86575D6A695D', X'5B55EFE37D24F942959B86575D6A695D', 0, NULL, NULL, 2, NULL, 0, 'For cookies, HEAT oven to 350°F. Beat sugar, brown sugar, peanut butter and peanut in large bowl with mixer on medium speed until creamy. Beat in eggs, milk and vanilla. Mix well. Stir flour, baking powder and baking soda in medium bowl until blended. Stir in oats and coconut. Add to peanut butter mixture, beating on low speed until well blended. Cover and chill at least 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DEC42D4E69D63E45A8188B36544E9E40', X'5B55EFE37D24F942959B86575D6A695D', X'5B55EFE37D24F942959B86575D6A695D', 1, NULL, NULL, 2, NULL, 0, 'DROP by level tablespoonful 2 inches apart onto baking sheets to make 60 mounds of dough. Bake 10 to 12 minutes or until edges are lightly browned. Cool on baking sheet 2 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C15BEA832A4E424FAD2D9DF18497EA68', X'5B55EFE37D24F942959B86575D6A695D', X'5B55EFE37D24F942959B86575D6A695D', 2, NULL, NULL, 2, NULL, 0, 'For Filling, BEAT powdered sugar, peanut butter, milk and vanilla in medium bowl with mixer on low speed until moistened. Beat on high speed 2 minutes or until creamy, adding additional milk if necessary until desired consistency.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CF7B94582A7674FA741F3C3E51659E0', X'5B55EFE37D24F942959B86575D6A695D', X'5B55EFE37D24F942959B86575D6A695D', 3, NULL, NULL, 2, NULL, 0, 'PLACE 30 cookies flat side up. Pipe or spread each with filling. Top with remaining cookies, flat side down, to form sandwich cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4436B1648648F54A85FFCC21C05235D9', X'036629F0D715F747860C6B13CAD2B118', X'036629F0D715F747860C6B13CAD2B118', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'720E49F6A34AE547A7E500ED02B99A78', X'036629F0D715F747860C6B13CAD2B118', X'036629F0D715F747860C6B13CAD2B118', 1, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter and brown sugar in large bowl with mixer on medium speed until creamy. Beat in egg and water until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E222D835A1C5DA45BE59C70A65F50E3F', X'036629F0D715F747860C6B13CAD2B118', X'036629F0D715F747860C6B13CAD2B118', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, baking soda and salt in small bowl until blended. Beat into peanut butter mixture until evenly moistened. Stir in chocolate chips.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3873C05387103B48AD2FCA528A3E0786', X'036629F0D715F747860C6B13CAD2B118', X'036629F0D715F747860C6B13CAD2B118', 3, NULL, NULL, 2, NULL, 0, 'DROP by tablespoonfuls onto baking sheet. Press with fork to flatten slightly in criss-cross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F332E12D081F7D46837A3872C9CB3B0C', X'036629F0D715F747860C6B13CAD2B118', X'036629F0D715F747860C6B13CAD2B118', 4, NULL, NULL, 2, NULL, 0, 'BAKE 8 to 10 minutes or until golden brown. Cool 1 minute on baking sheet. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'05CA3FE4DD405E4BB17D20E628D61B2A', X'87F998385506D4479DAA238CDA1468B6', X'87F998385506D4479DAA238CDA1468B6', 0, NULL, NULL, 2, NULL, 0, 'FOLD cookies into peanut butter. Serve with fruit, graham crackers or pretzels.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D4C31BFDCAEBEC47B4D81E91968CB342', X'64140DAB921DBF488DA6CB546B87E92C', X'64140DAB921DBF488DA6CB546B87E92C', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Line 13 x 9-inch baking pan with foil, extending foil over edge of pan. Coat lightly with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9233F1B0B156A64CBC482715333FCCA0', X'64140DAB921DBF488DA6CB546B87E92C', X'64140DAB921DBF488DA6CB546B87E92C', 1, NULL, NULL, 2, NULL, 0, 'COMBINE graham cracker crumbs and butter in small bowl with fork. Press into bottom of prepared pan. Stir together sweetened condensed milk and 1/2 cup peanut butter chocolate spread in medium bowl. Pour evenly over crumb mixture. Layer with coconut, mini chocolate chips and peanuts. Press down firmly with fork.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FD0670DFD225114AB112C10A8D413789', X'64140DAB921DBF488DA6CB546B87E92C', X'64140DAB921DBF488DA6CB546B87E92C', 2, NULL, NULL, 2, NULL, 0, 'BAKE 25 to 30 minutes or until lightly browned. Place 1/4 cup peanut butter chocolate spread into corner of heavy-duty resealable plastic bag. Microwave on HIGH 10 seconds. Cut small corner off bag. Drizzle over bars. Cool completely on wire rack. Chill 20 minutes or until firm. Remove from pan by lifting edges of foil.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3DE4C265B8971044892B341821F862F6', X'B51078C66F4ABF4986B938994102CAA9', X'B51078C66F4ABF4986B938994102CAA9', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Microwave chocolate in medium microwave-safe bowl on HIGH 1 1/2 minutes. Stir until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C564A2679E272B45813C7E538DA10D1A', X'B51078C66F4ABF4986B938994102CAA9', X'B51078C66F4ABF4986B938994102CAA9', 1, NULL, NULL, 2, NULL, 0, 'DIP each cracker halfway into chocolate. Sprinkle immediately with toffee bits, if desired. Place on prepared baking sheet. Chill 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6644A0D562D60548BBDBC6EF13465DCA', X'B51078C66F4ABF4986B938994102CAA9', X'B51078C66F4ABF4986B938994102CAA9', 2, NULL, NULL, 2, NULL, 0, 'SPREAD 2 teaspoons peanut butter on inside of each cracker. Press 2 crackers together. Repeat with remaining crackers to make 24 snackwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6CC013E908FFE5468E5AA95E7A6A9CC5', X'75D97792C81BF2419C3B62D2A08D2F27', X'75D97792C81BF2419C3B62D2A08D2F27', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Line baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5261CF088330214689AB0BD1C7E75566', X'75D97792C81BF2419C3B62D2A08D2F27', X'75D97792C81BF2419C3B62D2A08D2F27', 1, NULL, NULL, 2, NULL, 0, 'BEAT cake mix, water, oil and eggs in large bowl with mixer on medium speed for 2 minutes, scraping bowl occasionally.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B5DD23D3EE3FE4469375C70BF0134EAD', X'75D97792C81BF2419C3B62D2A08D2F27', X'75D97792C81BF2419C3B62D2A08D2F27', 2, NULL, NULL, 2, NULL, 0, 'DROP by 2 tablespoonsful batter onto prepared baking sheets, placing 3 inches apart. Using the back of a spoon, spread batter into 2-inch diameter circles. Bake 8 to 9 minutes or until centers are set. Cool 1 minute. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ABE1FE821EFC624595EAF8945C913D6E', X'75D97792C81BF2419C3B62D2A08D2F27', X'75D97792C81BF2419C3B62D2A08D2F27', 3, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter and marshmallow creme in large bowl with mixer on medium speed until blended. Add powdered sugar and milk. Beat on low speed until blended. Beat on high speed 1 minute.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B9998D418EF95F4DB6775A8ECC2B3FCE', X'75D97792C81BF2419C3B62D2A08D2F27', X'75D97792C81BF2419C3B62D2A08D2F27', 4, NULL, NULL, 2, NULL, 0, 'PLACE half of pies flat side up. Pipe or spread each with about 2 tablespoons filling. Top with remaining pies, rounded side up, to form whoopie pies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'786C33E13BC3B245859DBA59F75626DD', X'AC64414B1436D44E86A8F171E8AA2DC0', X'AC64414B1436D44E86A8F171E8AA2DC0', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Spray 11 x 17 x 1 1/2-inch baking pan with no-stick cooking pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6721F7CA438D0044B5CA7AB53D68211E', X'AC64414B1436D44E86A8F171E8AA2DC0', X'AC64414B1436D44E86A8F171E8AA2DC0', 1, NULL, NULL, 2, NULL, 0, 'BEAT butter and sugar in medium bowl with an electric mixer on medium speed until fluffy. Add in peanut butter, eggs and vanilla one at a time until incorporated. In a separate bowl stir together flour, baking powder and salt. Add to creamed mixture. Beat just until mixed. Stir in coconut. Spread into prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D77CB55FF5BB34CA9574C13D31DEBB6', X'AC64414B1436D44E86A8F171E8AA2DC0', X'AC64414B1436D44E86A8F171E8AA2DC0', 2, NULL, NULL, 2, NULL, 0, 'BAKE 25 to 30 minutes. Cool. Cut into 2 dozen bars.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22812A8F9D1D704CBE068EC86497DA64', X'AC64414B1436D44E86A8F171E8AA2DC0', X'AC64414B1436D44E86A8F171E8AA2DC0', 3, NULL, NULL, 2, NULL, 0, 'MIX powdered sugar, salt, vanilla and enough light cream to make a frosting of drizzling consistency. Drizzle over cooled bars.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'19BA39A257341945BA77E6C76A906B34', X'28F42FC675DC914FB7DEFA3DB02D5F08', X'28F42FC675DC914FB7DEFA3DB02D5F08', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4FA788B2BCD8E84BBC742E40A70E8A13', X'28F42FC675DC914FB7DEFA3DB02D5F08', X'28F42FC675DC914FB7DEFA3DB02D5F08', 1, NULL, NULL, 2, NULL, 0, 'For Crust: COMBINE graham cracker crumbs, peanut butter, sugar and butter in medium bowl until well blended. Reserve 2 tablespoons for topping. Press remaining crumbs into bottom of 13 x 9-inch baking pan. Bake 5 to 7 minutes or until lightly browned. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'044BF0F1E3BB5541B1A69D850FD76CE9', X'28F42FC675DC914FB7DEFA3DB02D5F08', X'28F42FC675DC914FB7DEFA3DB02D5F08', 2, NULL, NULL, 2, NULL, 0, 'For Peanut Butter Layer: BEAT cream cheese and peanut butter in large bowl with electric mixer on medium speed until well blended. Beat in powdered sugar. Gently mix in 1 cup whipped topping. Spread evenly over cooled crumb crust.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BF436B0313BBF547BB5D037FCF321D09', X'28F42FC675DC914FB7DEFA3DB02D5F08', X'28F42FC675DC914FB7DEFA3DB02D5F08', 3, NULL, NULL, 2, NULL, 0, 'For Chocolate Layer: BEAT pudding mix and milk in medium bowl on low speed 2 minutes. Spread evenly over peanut butter layer. Chill 1 hour. Spread with remaining whipped topping. Sprinkle with reserved crumbs.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'54E7604844F87E40B01C5D450A7F53ED', X'3B6416EA0C237943ADB583A84C72152D', X'3B6416EA0C237943ADB583A84C72152D', 0, NULL, NULL, 2, NULL, 0, 'COMBINE almond butter and caramel syrup in small bowl. Spread on bagel crisps. Top each with apple ring.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC8C3887162E1C42AB86A0AC37D3EF42', X'324841425A14164C8ED1984A1B2C1F65', X'324841425A14164C8ED1984A1B2C1F65', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350° F. Mix egg, 1 cup peanut butter chocolate spread, sugar, chopped peanuts and almond extract in large bowl with wooden spoon until combined.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1DEBB4E3F763704CA4D7BEF959AC7F8A', X'324841425A14164C8ED1984A1B2C1F65', X'324841425A14164C8ED1984A1B2C1F65', 1, NULL, NULL, 2, NULL, 0, 'USING a heaping tablespoon of dough, form into 24 balls. Place dough, 2 inches apart, on ungreased baking sheets. Flatten dough with a fork using tines to make a criss-cross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA87962F433B224A8F8C6E3E54870FFE', X'324841425A14164C8ED1984A1B2C1F65', X'324841425A14164C8ED1984A1B2C1F65', 2, NULL, NULL, 2, NULL, 0, 'BAKE 9 to 12 minutes or until light-golden brown. Cool on baking sheet. Remove to cooling rack to cool completely. Frost cookies with remaining peanut butter chocolate spread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BF03B3868A3D9A489AB386CAE7B905C4', X'7039AADDC8169B418106CBEFBD54EF37', X'7039AADDC8169B418106CBEFBD54EF37', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE 2 tablespoons heavy cream in 1-cup glass measuring cup until just boiling, about 30 seconds. Add chopped chocolate. Let stand 5 minutes. Stir until smooth. Pour evenly into mini fillo shells. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC3418905AD3D24E84D6004E9388A722', X'7039AADDC8169B418106CBEFBD54EF37', X'7039AADDC8169B418106CBEFBD54EF37', 1, NULL, NULL, 2, NULL, 0, 'BEAT remaining 1/4 cup heavy cream in small deep bowl with electric mixer on high speed until stiff.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FF346CD7923BA143A5B5B7C92972B014', X'7039AADDC8169B418106CBEFBD54EF37', X'7039AADDC8169B418106CBEFBD54EF37', 2, NULL, NULL, 2, NULL, 0, 'BEAT cream cheese, peanut butter, powdered sugar, honey and vanilla in separate medium bowl with same beaters until smooth. Fold in whipped cream until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'59DD9443A4134E40B1BBD94583BA615E', X'7039AADDC8169B418106CBEFBD54EF37', X'7039AADDC8169B418106CBEFBD54EF37', 3, NULL, NULL, 2, NULL, 0, 'PIPE peanut butter mousse into fillo shells using a pastry bag fitted with a large star tip. Sprinkle with peanuts, if desired. Serve chilled. Best served same day.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'205CBEF379EA084AA4CEC2385F36A7DF', X'CB20BA8594A4474AB86A8722147850AC', X'CB20BA8594A4474AB86A8722147850AC', 0, NULL, NULL, 2, NULL, 0, 'WHISK pancake mix, sugar, water and eggs in a medium bowl until batter is smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'25A0FA230F706B429033C4C58AFA543A', X'CB20BA8594A4474AB86A8722147850AC', X'CB20BA8594A4474AB86A8722147850AC', 1, NULL, NULL, 2, NULL, 0, 'HEAT 8-inch skillet or crepe pan over medium heat. Coat with no-stick cooking spray between each crepe. Pour 2 tablespoons batter into preheated skillet, immediately tilting pan until batter covers bottom. Cook until edges start to dry and center is set. Turn to cook second side. Repeat, making 14 crepes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B834A121C9968045B28D1D4519D45F70', X'CB20BA8594A4474AB86A8722147850AC', X'CB20BA8594A4474AB86A8722147850AC', 2, NULL, NULL, 2, NULL, 0, 'PLACE first crepe onto center of presentation plate. Spread 2 tablespoons of peanut butter mixture evenly over crepe and all the way to the edges of the crepe. Repeat with remaining crepes, ending with peanut butter mixture on top.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3FB9C03D670CC742A069896E1A768C2C', X'CB20BA8594A4474AB86A8722147850AC', X'CB20BA8594A4474AB86A8722147850AC', 3, NULL, NULL, 2, NULL, 0, 'STARTING at outer edge of top of cake, place two rows of raspberries, turned upside down, all around cake. Fill center area with blueberries. Place any additional berries around bottom of cake. Sprinkle with powdered sugar just before serving. Garnish with mint leaves if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AB5EABEED4786D4EA36E6E2A28A0FF65', X'0E56E679A4F87C42BB2805CE8CF202A8', X'0E56E679A4F87C42BB2805CE8CF202A8', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat a 9-inch pie plate with no-stick cooking spray. Combine vanilla wafer crumbs and sugar in medium bowl. Add butter, stirring with a fork until evenly moistened. Press evenly onto bottom and sides of prepared pie plate to form a crust. Bake 8 to 10 minutes or until lightly browned. Place on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8BC239ABA17DB242B78828464E953D45', X'0E56E679A4F87C42BB2805CE8CF202A8', X'0E56E679A4F87C42BB2805CE8CF202A8', 1, NULL, NULL, 2, NULL, 0, 'COMBINE peanut butter and butter in medium bowl. Beat with electric mixer on medium speed until blended. Gradually add powdered sugar until blended. Crumble mixture evenly into bottom of pie crust. Sprinkle with 3 tablespoons chopped peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6ECEB681B1170845A196F5FF6B9A3951', X'0E56E679A4F87C42BB2805CE8CF202A8', X'0E56E679A4F87C42BB2805CE8CF202A8', 2, NULL, NULL, 2, NULL, 0, 'COMBINE sugar, cornstarch and salt in medium saucepan. Whisk in milk, stirring until cornstarch is dissolved. Cook over medium heat, stirring constantly, until mixture comes to a boil and thickens.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'55660B33D54B7A42B2E68DD5A578DE78', X'0E56E679A4F87C42BB2805CE8CF202A8', X'0E56E679A4F87C42BB2805CE8CF202A8', 3, NULL, NULL, 2, NULL, 0, 'WHISK egg yolks in medium bowl. Gradually whisk 1 cup hot filling mixture into yolks. Return mixture to saucepan. Cook over low heat, stirring constantly, until mixture just returns to a boil. Stir in peanut butter and vanilla until blended. Pour about half of filling mixture into pie crust. Sprinkle evenly with 2/3 of chopped peanut butter cups. Top evenly with remaining filling mixture. Cool completely. Chill at least 1 hour. Spread whipped topping over top of pie. Sprinkle with remaining chopped peanut butter cups and peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'23DBD136EB15F84E9544D67FD4CDA0CE', X'18D0465C55478344B89CB29988067D00', X'18D0465C55478344B89CB29988067D00', 0, NULL, NULL, 2, NULL, 0, 'WHISK syrup and peanut butter in a medium microwave-safe bowl until smooth. Microwave on HIGH about 1 minute to warm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BBBC734CB8B953429963CA4CDBF7EFEF', X'7063DF5F283AF44CA979CBE82A5D3A4E', X'7063DF5F283AF44CA979CBE82A5D3A4E', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 325°F. Line baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'522BABF91B32AD45A74D51CB72E06242', X'7063DF5F283AF44CA979CBE82A5D3A4E', X'7063DF5F283AF44CA979CBE82A5D3A4E', 1, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter, sugar, brown sugar, egg, cornmeal, baking powder, vanilla and salt in medium bowl with mixer on medium speed until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E991ED1CF295ED4999BD95030EE48E4E', X'7063DF5F283AF44CA979CBE82A5D3A4E', X'7063DF5F283AF44CA979CBE82A5D3A4E', 2, NULL, NULL, 2, NULL, 0, 'SHAPE into 1 1/4-inch balls. Place 2 inches apart on prepared baking sheet. Flatten with bottom of drinking glass to 1 3/4-inch diameter, then mark with fork in a criss-cross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7B411FD14BC17947A3D3479E483F253D', X'7063DF5F283AF44CA979CBE82A5D3A4E', X'7063DF5F283AF44CA979CBE82A5D3A4E', 3, NULL, NULL, 2, NULL, 0, 'BAKE 9 to 10 minutes or until edges are light golden brown. Cool on baking sheet 5 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC2FCC71CBDD774DA2406F68F1B6DC02', X'42326D206304D840BD8653D360D40238', X'42326D206304D840BD8653D360D40238', 0, NULL, NULL, 2, NULL, 0, 'STIR chocolate spread in large bowl until softened. Fold in whipped topping until evenly blended. Place in serving dish.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'91EBF98518CB7E49B08EACAE60DBF06F', X'42326D206304D840BD8653D360D40238', X'42326D206304D840BD8653D360D40238', 1, NULL, NULL, 2, NULL, 0, 'TOP with sprinkles or chocolate chips. Cover and chill. Serve as dip for cookies, pretzels and fruit.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'35A5CCBFABB42342AE2C36B205FB0E29', X'71CDA21938918D4C8F7FE7A61486BFAE', X'71CDA21938918D4C8F7FE7A61486BFAE', 0, NULL, NULL, 2, NULL, 0, 'LINE an 8-inch square baking pan with foil, extending foil over edge of pan. Coat with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5E32817AB4F3FB43AEFBEBA3751B370C', X'71CDA21938918D4C8F7FE7A61486BFAE', X'71CDA21938918D4C8F7FE7A61486BFAE', 1, NULL, NULL, 2, NULL, 0, 'MELT butter in a large saucepan (4 qt.) over low heat until melted. Add marshmallows stirring until completely melted. Remove from heat. Blend in 1 1/4 cups peanut butter chocolate spread mixing until combined. Add cereal. Stir until well coated. Press into prepared pan. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E1F3958C356EF14BBE17C0DB708D58CB', X'71CDA21938918D4C8F7FE7A61486BFAE', X'71CDA21938918D4C8F7FE7A61486BFAE', 2, NULL, NULL, 2, NULL, 0, 'FROST treats with remaining peanut butter chocolate spread. Remove treats from pan using edges of foil. Cut into 32 bars.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E5A42E71242EDF4197FBE7B66B45F5E8', X'767FA55E167EBC4DB90B9AFA3FD92F23', X'767FA55E167EBC4DB90B9AFA3FD92F23', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 400ºF. Spray a 12-cup muffin pan with a no-stick cooking spray or line with paper cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3180A2560EC99E48931D21732B143EA2', X'767FA55E167EBC4DB90B9AFA3FD92F23', X'767FA55E167EBC4DB90B9AFA3FD92F23', 1, NULL, NULL, 2, NULL, 0, 'STIR together flour, sugar, baking powder and salt in medium bowl. Combine egg, milk and oil in small bowl. Add liquids to flour mixture, stirring just until dry ingredients are moistened. Divide batter evenly among cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F57F85926552154E8B7DB1C0B502DA77', X'767FA55E167EBC4DB90B9AFA3FD92F23', X'767FA55E167EBC4DB90B9AFA3FD92F23', 2, NULL, NULL, 2, NULL, 0, 'COMBINE sugar, flour, peanut butter and salt in small bowl, mixing with fork until crumbly. Divide topping over muffins. Lightly stir into muffin with knife.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6453D3CD80464B409F4602DAA7BB8434', X'767FA55E167EBC4DB90B9AFA3FD92F23', X'767FA55E167EBC4DB90B9AFA3FD92F23', 3, NULL, NULL, 2, NULL, 0, 'BAKE 18 to 23 minutes or until golden brown. Cool in pan on rack 5 minutes. Remove from pan to rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD29AC7AEDE84F46B24C25FC0C818917', X'042AB4B356B83C43897470550BD0796A', X'042AB4B356B83C43897470550BD0796A', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Coat cookie sheets with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2F22296F878CFE4CAC566EC1A4B44862', X'042AB4B356B83C43897470550BD0796A', X'042AB4B356B83C43897470550BD0796A', 1, NULL, NULL, 2, NULL, 0, 'BEAT shortening, brown sugar, granulated sugar, peanut butter, eggs and vanilla in large bowl on medium speed of electric mixer until well blended. Combine oats, flour, baking soda, baking powder and salt in small bowl. Gradually mix into shortening mixture at low speed just until blended. Cover and chill 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8CD5D9C33BB88749807F3EF0120AD98E', X'042AB4B356B83C43897470550BD0796A', X'042AB4B356B83C43897470550BD0796A', 2, NULL, NULL, 2, NULL, 0, 'STIR in chocolate chips and candy bars. Drop by heaping teaspoonfuls 2 inches apart onto prepared cookie sheets. Bake 8 minutes or until lightly browned. Cool 2 minutes. Remove to wire racks to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D38306762BB3CE428117C8CC8B48E5C7', X'042AB4B356B83C43897470550BD0796A', X'042AB4B356B83C43897470550BD0796A', 3, NULL, NULL, 2, NULL, 0, '*Butterfinger candy bars were used in recipe testing.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1BC4AA3799CF104EB7DEAB4A0452B1E5', X'5E1DC5912563B54588EAC151B52B72D3', X'5E1DC5912563B54588EAC151B52B72D3', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Beat peanut butter, 1/4 cup shortening and sugar in large bowl with electric mixer on medium low speed until mixture comes together. Form heaping tablespoonfuls of mixture into egg shape. Place on prepared baking sheet. Repeat with remaining mixture. Freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9C7D07D09C8760459294C58396E169E5', X'5E1DC5912563B54588EAC151B52B72D3', X'5E1DC5912563B54588EAC151B52B72D3', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate and remaining shortening in microwave-safe bowl on HIGH 30 seconds. Stir. Microwave an addtional 30 seconds or until melted and smooth when stirred. Dip one end of lollipop stick into chocolate mixture. Insert stick into bottom center of each egg. Repeat with remaining eggs.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3C3F01363E190489001D86EEC4C82FC', X'5E1DC5912563B54588EAC151B52B72D3', X'5E1DC5912563B54588EAC151B52B72D3', 2, NULL, NULL, 2, NULL, 0, 'DIP each egg into chocolate to coat completely. Return to prepared baking sheet. Sprinkle immediately with decorator sprinkles or coconut, if desired. Chill 30 minutes or until chocolate is set. Store in air tight container in refrigerator.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D75695C5BDC5944BB959C8D0D782AA41', X'1D191C92CB29574C93C3196670D16712', X'1D191C92CB29574C93C3196670D16712', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat 13 x 9-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0ED18E8668D3AF4D91591EE8C5BF4431', X'1D191C92CB29574C93C3196670D16712', X'1D191C92CB29574C93C3196670D16712', 1, NULL, NULL, 2, NULL, 0, 'PREPARE brownie mix according to package directions using water, oil and eggs. Spread in prepared pan. Bake 28 to 31 minutes. Cool completely on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8BFC86194E67F14C92C7CB80EC6ABFD0', X'1D191C92CB29574C93C3196670D16712', X'1D191C92CB29574C93C3196670D16712', 2, NULL, NULL, 2, NULL, 0, 'FROST brownies with chocolate spread. Garnish with decorator sprinkles, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B70EE124163A44391D86ADFC70BC7D6', X'17D8AB3030F14148ADAA6CEDD799FC56', X'17D8AB3030F14148ADAA6CEDD799FC56', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Coat baking sheets with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C3A1982027D1E8458733A630F02DA773', X'17D8AB3030F14148ADAA6CEDD799FC56', X'17D8AB3030F14148ADAA6CEDD799FC56', 1, NULL, NULL, 2, NULL, 0, 'For Cookies: BEAT peanut butter, shortening, brown sugar, milk and vanilla in large bowl with electric mixer on medium speed until well blended. Add eggs. Beat just until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A7133E49F9579148B728007A4027F682', X'17D8AB3030F14148ADAA6CEDD799FC56', X'17D8AB3030F14148ADAA6CEDD799FC56', 2, NULL, NULL, 2, NULL, 0, 'COMBINE flour, baking soda and salt in medium bowl. Add to creamed mixture on low speed, mixing just until blended. Drop by level tablespoonfuls 2 inches apart onto prepared baking sheets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'060AD21FCDBE8544B46EBC5F021E2323', X'17D8AB3030F14148ADAA6CEDD799FC56', X'17D8AB3030F14148ADAA6CEDD799FC56', 3, NULL, NULL, 2, NULL, 0, 'BAKE 6 to 7 minutes or until set and just beginning to brown. Cool 2 minutes on baking sheet. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DCE132FF4452994C87B98D8D39BD82A5', X'17D8AB3030F14148ADAA6CEDD799FC56', X'17D8AB3030F14148ADAA6CEDD799FC56', 4, NULL, NULL, 2, NULL, 0, 'For Filling: MICROWAVE sweetened condensed milk and chocolate chips in medium microwave-safe bowl on HIGH for 1 1/2 minutes or until melted and smooth when stirred. Stir in vanilla. Let cool 15 minutes. Spread generously on flat side of half the cookies. Top with remaining cookies, flat side down, to form sandwich cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'97E3666E167F1745BE3A547E80EF44D1', X'18323AB5FAFB4241B1F037EF9D99A448', X'18323AB5FAFB4241B1F037EF9D99A448', 0, NULL, NULL, 2, NULL, 0, 'In large bowl, break up cookie dough. Mix in 1 cup of the peanuts. Cover; refrigerate about 30 minutes or until well chilled.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5647B6FAF7A1424A8E1F0DE8CFCC5AF9', X'18323AB5FAFB4241B1F037EF9D99A448', X'18323AB5FAFB4241B1F037EF9D99A448', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 375°F. Spray cookie sheets with CRISCO® Original No-Stick Cooking Spray, or line with cooking parchment paper. Shape dough into 36 (1 1/4-inch) balls. Place balls 2 inches apart on cookie sheets. Flatten to 1/2-inch thickness with lightly floured metal spatula or drinking glass. Bake 9 to 11 minutes or until edges are golden brown. Cool 5 minutes; remove from cookie sheets to cooling rack. Cool completely, about 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C80FA67ADD51CE49986A889F7F2B3460', X'18323AB5FAFB4241B1F037EF9D99A448', X'18323AB5FAFB4241B1F037EF9D99A448', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in 2-quart saucepan, melt butter and peanut butter over medium heat, stirring occasionally. Remove from heat; stir in vanilla. Cool 1 minute. Stir in powdered sugar. When cool enough to handle, knead filling several times until powdered sugar is thoroughly blended. Shape into log, about 9 inches long and 2 inches in diameter. Cut into 18 (1/2-inch) slices, reshaping slices into round shape if necessary. Cover with plastic wrap; set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3C0BE500AE69C64AB5F0E96F2F20333D', X'18323AB5FAFB4241B1F037EF9D99A448', X'18323AB5FAFB4241B1F037EF9D99A448', 3, NULL, NULL, 2, NULL, 0, 'When ready to assemble cookies, place 1 filling slice on bottom of 1 cookie; top with another cookie, bottom side down, and press together slightly. Repeat with remaining cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E5819FA0053A774A854C8CD64E40654C', X'18323AB5FAFB4241B1F037EF9D99A448', X'18323AB5FAFB4241B1F037EF9D99A448', 4, NULL, NULL, 2, NULL, 0, 'In medium microwavable bowl, microwave chocolate chips and whipping cream uncovered on High about 1 minute, stirring twice, until melted. Spoon heaping 1 teaspoonful on top of each cookie. Sprinkle with remaining ground peanuts. Let stand 10 minutes or until ganache is set. Store tightly covered in single layer at room temperature.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1A6591CB1247BB46BE98B114C7CB065B', X'18323AB5FAFB4241B1F037EF9D99A448', X'18323AB5FAFB4241B1F037EF9D99A448', 5, NULL, NULL, 2, NULL, 0, '*To grind peanuts, place in food processor bowl with metal blade. Cover; process with on-and-off pulses until coarsely ground.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3653681D9980C54DB48FE55D521EB65F', X'843A0549ED25CB4D856E2675A2A025F1', X'843A0549ED25CB4D856E2675A2A025F1', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D530AE20EE2A4D45A2421539C7D6DA80', X'843A0549ED25CB4D856E2675A2A025F1', X'843A0549ED25CB4D856E2675A2A025F1', 1, NULL, NULL, 2, NULL, 0, 'BEAT shortening, peanut butter, brown sugar and sugar in large bowl with mixer on high speed until light and fluffy, about 4 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CA04C4F2AB7199418C873F5AE1A964BA', X'843A0549ED25CB4D856E2675A2A025F1', X'843A0549ED25CB4D856E2675A2A025F1', 2, NULL, NULL, 2, NULL, 0, 'BEAT in egg, milk and vanilla until blended. Stir flour, baking soda and salt in medium bowl until blended. Add to creamed mixture. Beat on low speed until dough forms.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'31C186A2D439304DAB9927533093EF97', X'843A0549ED25CB4D856E2675A2A025F1', X'843A0549ED25CB4D856E2675A2A025F1', 3, NULL, NULL, 2, NULL, 0, 'PLACE each color of decorator sugar in small bowl. Roll dough into 1-inch balls. Roll in decorator sugar to coat. Place 2 inches apart on baking sheets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E77556E7B319684FBE81A838DFB3A510', X'843A0549ED25CB4D856E2675A2A025F1', X'843A0549ED25CB4D856E2675A2A025F1', 4, NULL, NULL, 2, NULL, 0, 'BAKE 10 to 11 minutes or until edges are golden brown. Cool on baking sheet 1 to 2 minutes. Top each cookie with chocolate kiss. Remove to wire rack to cool completely.');
