INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6343EBABD91BDD4EB4CE84DD4265D9B8', X'9240A55061389F48A5D88AF7D1F378C9', X'9240A55061389F48A5D88AF7D1F378C9', 1, NULL, NULL, 2, NULL, 0, 'BEAT shortening, peanut butter, brown sugar and sugar in large bowl with mixer on medium speed until well blended. Beat in egg until blended.');
