INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'73D0BC2A4D868F4DB2440DB7F5652534', X'9240A55061389F48A5D88AF7D1F378C9', X'9240A55061389F48A5D88AF7D1F378C9', 3, NULL, NULL, 2, NULL, 0, 'SHAPE dough into 1-inch balls. Roll in chopped nuts, pressing gently so nuts adhere to dough. Place 2 inches apart onto prepared baking sheets. Flatten to 1 1/2-inch diameter with bottom of drinking glass.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'45C69CBD9CE587499D2EB55E86CCDF33', X'9240A55061389F48A5D88AF7D1F378C9', X'9240A55061389F48A5D88AF7D1F378C9', 4, NULL, NULL, 2, NULL, 0, 'BAKE 9 to 11 minutes or until set. Cool 2 minutes on baking sheets. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5FE0F844AFA5674AB685F3E07F490ED4', X'87E734A2B32B8F4597C43E237B50F71D', X'87E734A2B32B8F4597C43E237B50F71D', 0, NULL, NULL, 2, NULL, 0, 'BEAT brown sugar, peanut butter, 1/2 cup shortening, milk and vanilla in bowl of electric mixer at medium speed until well blended. Beat in egg. Combine flour, baking soda and salt in a medium bowl. Add to shortening mixture. Beat at low speed just until blended. Refrigerate about 1 hour or until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2E709F46227CBE438441F55C3587AE03', X'87E734A2B32B8F4597C43E237B50F71D', X'87E734A2B32B8F4597C43E237B50F71D', 1, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Spray mini-muffin pans with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E087665C7EA914D83992DB40F7D8E69', X'87E734A2B32B8F4597C43E237B50F71D', X'87E734A2B32B8F4597C43E237B50F71D', 2, NULL, NULL, 2, NULL, 0, 'SHAPE dough into 1-inch balls. Roll in sugar. Place each ball in prepared mini-muffin cups. Press dough onto bottom and sides of cup to within 1/2-inch of top.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DCD1A074DE20D249B787E0573B9CFD42', X'87E734A2B32B8F4597C43E237B50F71D', X'87E734A2B32B8F4597C43E237B50F71D', 3, NULL, NULL, 2, NULL, 0, 'BAKE 7 to 8 minutes or until cookies are set and just beginning to brown. Cool 10 minutes on cooling racks. Remove cookie cups very carefully to cooling racks to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B9EA2FC90BE1854D92A143C3BC5E1BF7', X'87E734A2B32B8F4597C43E237B50F71D', X'87E734A2B32B8F4597C43E237B50F71D', 4, NULL, NULL, 2, NULL, 0, 'PLACE chocolate chips and 1 teaspoon shortening in microwave-safe bowl. Microwave at MEDIUM for 1 to 2 minutes or until chips are shiny and soft. Stir until smooth. Spoon about 1/2 teaspoon chocolate mixture into center of each cookie. Sprinkle with chopped peanuts. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD293201E2066847B0FBEDD872845E9C', X'189129F479FAD641984E019E61EF033C', X'189129F479FAD641984E019E61EF033C', 0, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter, sugar, butter, vanilla and cayenne pepper in large bowl with electric mixer until creamy. Shape into balls. Cover and refrigerate at least 3 hours.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C52085DF07DBDB4F946AE198CB6148C3', X'189129F479FAD641984E019E61EF033C', X'189129F479FAD641984E019E61EF033C', 1, NULL, NULL, 2, NULL, 0, 'MELT chocolate chips and shortening in top of double boiler, stirring until blended. Dip balls in chocolate mixture using toothpicks or skewers. Place on wax paper until chocolate has hardened. Refrigerate until serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC4F5C6E36128746B79B9076C0089BDF', X'821812ED3F60C7448510296BA5ADFFBA', X'821812ED3F60C7448510296BA5ADFFBA', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4EAF733E2A0D3740937703A4E3518571', X'821812ED3F60C7448510296BA5ADFFBA', X'821812ED3F60C7448510296BA5ADFFBA', 1, NULL, NULL, 2, NULL, 0, 'COMBINE shortening, brown sugar and granulated sugar in bowl of electric mixer. Beat at medium speed until well blended. Beat in eggs. Add peanut butter. Beat until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'488C78C712AAC34EA49C151F77F710EB', X'821812ED3F60C7448510296BA5ADFFBA', X'821812ED3F60C7448510296BA5ADFFBA', 2, NULL, NULL, 2, NULL, 0, 'COMBINE oats and baking soda in medium bowl. Stir into shortening mixture with spoon. Stir in chocolate chips, butterscotch chips and nuts. Drop by rounded teaspoonfuls 2 inches apart onto ungreased baking sheets.');
