INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF80E4535A7761488E002189711D2EAA', X'0F300D07C01FDE4B8DA969262146952C', X'0F300D07C01FDE4B8DA969262146952C', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375ºF. Coat baking sheet with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'94E01846BA789D49809B944F3105F536', X'0F300D07C01FDE4B8DA969262146952C', X'0F300D07C01FDE4B8DA969262146952C', 1, NULL, NULL, 2, NULL, 0, 'BEAT sugar and shortening in large bowl with mixer on high speed until well blended. Beat in peanut butter and eggs until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'960D84F8FC9687499B43F610CDD332FD', X'0F300D07C01FDE4B8DA969262146952C', X'0F300D07C01FDE4B8DA969262146952C', 2, NULL, NULL, 2, NULL, 0, 'COMBINE flour, cream of tartar, baking soda and salt in medium bowl. Gradually add to shortening mixture, beating on low speed until well blended.');
