INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A535B44240172B4EAD41F3D8F9AF9B0A', X'D1BF0AE53724CF45BAAA57346082D3D8', X'D1BF0AE53724CF45BAAA57346082D3D8', 0, NULL, NULL, 2, NULL, 0, 'LINE 2 trays with parchment or wax paper.');
