INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1AF92BA2082E15469B427CCD57DCDE0A', X'D1BF0AE53724CF45BAAA57346082D3D8', X'D1BF0AE53724CF45BAAA57346082D3D8', 2, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate chips and shortening in medium microwave-safe bowl on HIGH power 45 seconds. Stir until smooth. If necessary, microwave in additional 15-second intervals until completely melted and smooth when stirred.');
