INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CEE59FE5145036419C2CFE49F8735C49', X'4FF0FFCBD630974EB4C338874EEC4EFB', X'4FF0FFCBD630974EB4C338874EEC4EFB', 0, NULL, NULL, 2, NULL, 0, 'SPRAY two 9-inch cake pans with no-stick cooking spray.');
