INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'73C7FA7D656F2140AD343C8B1A9293F4', X'4FF0FFCBD630974EB4C338874EEC4EFB', X'4FF0FFCBD630974EB4C338874EEC4EFB', 2, NULL, NULL, 2, NULL, 0, 'REMOVE from pans. Cut ears and bow tie from one cake by cutting two football-shaped ears from the top and bottom of one cake layer, leaving a bow tie-shaped piece in the middle. Position two ears at top of whole round cake and place bow tie at bottom.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FB4D0D94A95BA94FADC49C3F4D00A022', X'4FF0FFCBD630974EB4C338874EEC4EFB', X'4FF0FFCBD630974EB4C338874EEC4EFB', 3, NULL, NULL, 2, NULL, 0, 'CUT jelly beans in half to use as bunny''s eyes and nose. Cut pieces of licorice for mouth and whiskers. Use cut jelly beans and licorice pieces to decorate bow tie.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7F75BD7FE9AB3478BCDA514581EAE4F', X'207173C0E472FC4BA4D4954CDD7436AC', X'207173C0E472FC4BA4D4954CDD7436AC', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375ºF.');
