INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'765663EECB600040A2B2D7F8A8330D0B', X'207173C0E472FC4BA4D4954CDD7436AC', X'207173C0E472FC4BA4D4954CDD7436AC', 3, NULL, NULL, 2, NULL, 0, 'BAKE 10 to 12 minutes, or until golden brown. Remove to wire rack to cool completely.');
