INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'520A1D59D74FD14592E654DC8EA5FA18', X'82134411EA9E4D40AE84270487941D24', X'82134411EA9E4D40AE84270487941D24', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Stir peanuts, sugar and cinnamon in small bowl until blended. Set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B4EDEF2651DA1F4794D77EC08EAEB8B7', X'82134411EA9E4D40AE84270487941D24', X'82134411EA9E4D40AE84270487941D24', 1, NULL, NULL, 2, NULL, 0, 'STIR peanut butter and powdered in small bowl sugar until completely blended. Shape mixture into 24 (1-inch) balls.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CF0E5EC8B93DAF46A3DC74B9047F2E05', X'82134411EA9E4D40AE84270487941D24', X'82134411EA9E4D40AE84270487941D24', 2, NULL, NULL, 2, NULL, 0, 'CUT cookie dough into 12 slices. Cut each slice in half to make 24 pieces. Flatten 1 piece of cookie dough and shape around 1 peanut butter ball to cover completely. Repeat with remaining dough and peanut butter balls.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B919D0EB6AF3084494D01A2D256ED9F9', X'82134411EA9E4D40AE84270487941D24', X'82134411EA9E4D40AE84270487941D24', 3, NULL, NULL, 2, NULL, 0, 'ROLL each ball in peanut mixture, pressing gently so that coating adheres to dough. Place 2 inches apart on baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'770863712F6041408E537D2DFD0E454E', X'82134411EA9E4D40AE84270487941D24', X'82134411EA9E4D40AE84270487941D24', 4, NULL, NULL, 2, NULL, 0, 'COAT bottom of drinking glass with no-stick cooking spray. Dip glass into remaining peanut mixture. Flatten balls of dough to 1/2-inch thickness with bottom of glass. Sprinkle any remaining peanut mixture evenly on tops of cookies. Gently press into dough.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'66EF86912C5C794082619DD4D63DEECB', X'82134411EA9E4D40AE84270487941D24', X'82134411EA9E4D40AE84270487941D24', 5, NULL, NULL, 2, NULL, 0, 'BAKE 8 to 12 minutes or until edges are golden brown. Cool 1 minute. Remove from baking sheet to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA132AE79D687243B6C4DA458AE76FD1', X'8D015D7CC2FE7742B66AF8D8A7113B80', X'8D015D7CC2FE7742B66AF8D8A7113B80', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 300°F. Coat two 10 x 15 x 1-inch baking sheets with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ACDAB6913E2F954CBE667B8ECDC3677D', X'8D015D7CC2FE7742B66AF8D8A7113B80', X'8D015D7CC2FE7742B66AF8D8A7113B80', 1, NULL, NULL, 2, NULL, 0, 'PLACE popped popcorn in a very large bowl or Dutch oven.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'31A87B9097B32242AF1E1113709FED83', X'8D015D7CC2FE7742B66AF8D8A7113B80', X'8D015D7CC2FE7742B66AF8D8A7113B80', 2, NULL, NULL, 2, NULL, 0, 'COMBINE peanut butter, corn syrup, butter and salt in a medium saucepan over medium heat, stirring constantly, until mixture begins to boil. Remove from heat, add vanilla and stir to combine.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'62118ED5D6CA6A46AC22DF7B17A82C68', X'8D015D7CC2FE7742B66AF8D8A7113B80', X'8D015D7CC2FE7742B66AF8D8A7113B80', 3, NULL, NULL, 2, NULL, 0, 'POUR warm peanut butter mixture over popcorn, stirring gently to combine. When all popcorn appears evenly coated with peanut butter mixture, spread evenly on prepared baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FBD58828BC650F47A4D5D499266FF1F1', X'8D015D7CC2FE7742B66AF8D8A7113B80', X'8D015D7CC2FE7742B66AF8D8A7113B80', 4, NULL, NULL, 2, NULL, 0, 'BAKE 20 to 25 minutes, stirring occasionally. Remove from oven and allow to cool. May be stored in airtight container for up to one week.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'85169A211EDB7445A3F5C06B953EEBDE', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375ºF. Coat baking sheets with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2C1A6F96ED72D84198B0D5DBEA1B219D', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', 1, NULL, NULL, 2, NULL, 0, 'COMBINE shortening, brown sugar, sugar and 1/2 cup peanut butter in large bowl with mixer on medium speed until well blended. Beat in egg and vanilla.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FAF56E306185BA4EA20DFBD3F2FFDAAC', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, baking soda and salt in medium bowl until blended. Beat into shortening mixture just until blended. Form 1 rounded teaspoonful dough around each peanut butter cup until completely enclosed. Place 2 inches apart on prepared baking sheets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E50770A0E7778C4488EF1DC784779481', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', 3, NULL, NULL, 2, NULL, 0, 'BAKE 8 to 10 minutes or until lightly browned. Remove immediately to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EF9473BB1F6E9846AB1EB22E249932F2', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', X'18E19DFA64F4CC4FBEC4273E4E14D7BC', 4, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate chips and 2 tablespoons peanut butter in small microwave-safe bowl on HIGH 30 seconds. Stir. Repeat until glaze is smooth when stirred. Spoon over tops on cookies, spreading with back of spoon. Let stand until set.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B6D65AE23E9D3645BF2015351FC1700F', X'A766D2CF97840644BABBAEED92DF8F6A', X'A766D2CF97840644BABBAEED92DF8F6A', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Line 24 muffin cups with paper baking cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0B43586D00EDD540A0D2AE519D481645', X'A766D2CF97840644BABBAEED92DF8F6A', X'A766D2CF97840644BABBAEED92DF8F6A', 1, NULL, NULL, 2, NULL, 0, 'PREPARE cake mix according to package directions using water, oil and eggs. Blend 3/4 cup graham cracker crumbs into cake batter. Divide evenly into prepared baking cups. Bake 19 to 23 minutes or until toothpick inserted in center comes out clean. Cool 5 minutes. Remove from pan to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F7AC414A1391E04787BC60BDAA88960E', X'A766D2CF97840644BABBAEED92DF8F6A', X'A766D2CF97840644BABBAEED92DF8F6A', 2, NULL, NULL, 2, NULL, 0, 'SPREAD or pipe peanut butter spread onto each cupcake. Top each with 3 mini marshmallows. Sprinkle with mini chocolate chips and remaining graham cracker crumbs.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A8E8A9111A97CF418FBC1BE6424911E5', X'B89716CFD4CF924BB141416A204146D7', X'B89716CFD4CF924BB141416A204146D7', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Coat baking sheet with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'517C3A7BB3C511449BD871991D706F35', X'B89716CFD4CF924BB141416A204146D7', X'B89716CFD4CF924BB141416A204146D7', 1, NULL, NULL, 2, NULL, 0, 'BEAT shortening, peanut butter and sugar in large bowl with mixer on medium speed until well blended. Beat in marmalade, eggs and vanilla.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'32CC324E7F8178459D383AA48C95EF73', X'B89716CFD4CF924BB141416A204146D7', X'B89716CFD4CF924BB141416A204146D7', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, baking powder and salt in medium bowl until blended. Mix into shortening mixture on low speed until just blended. Stir in baking chips. Drop by rounded tablespoonfuls 2 inches apart onto prepared baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8021B32C80DBCA41876D92B134648E7E', X'B89716CFD4CF924BB141416A204146D7', X'B89716CFD4CF924BB141416A204146D7', 3, NULL, NULL, 2, NULL, 0, 'BAKE 10 to 12 minutes or until lightly browned. Cool 2 minutes on baking sheet. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1868BDCE4278C54F8D584F5E905C4FBC', X'FE6035236DCE1541B9177E1D27CC61C1', X'FE6035236DCE1541B9177E1D27CC61C1', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A3E97F721F6F8E42A278DBE6675BF676', X'FE6035236DCE1541B9177E1D27CC61C1', X'FE6035236DCE1541B9177E1D27CC61C1', 1, NULL, NULL, 2, NULL, 0, 'BEAT brown sugar, peanut butter, shortening, milk and vanilla in large bowl with mixer on medium speed until well blended. Beat in egg just until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ABA71219BBF6AB49BD56C45570163CE8', X'FE6035236DCE1541B9177E1D27CC61C1', X'FE6035236DCE1541B9177E1D27CC61C1', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, baking soda and salt in medium bowl until blended. Beat into peanut butter mixture at low speed just until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FFBC9FEF98B18A48893619B62FF3036F', X'FE6035236DCE1541B9177E1D27CC61C1', X'FE6035236DCE1541B9177E1D27CC61C1', 3, NULL, NULL, 2, NULL, 0, 'PLACE sugar in small bowl. Shape dough into 48 (1-inch) balls. Roll in sugar to coat completely. Place 2 inches apart on baking sheets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A2AA3DE5626C9E448C627ADA14EDA7C1', X'FE6035236DCE1541B9177E1D27CC61C1', X'FE6035236DCE1541B9177E1D27CC61C1', 4, NULL, NULL, 2, NULL, 0, 'BAKE 5 minutes. Remove baking sheet from oven. Immediately press centers of cookies with back of measuring teaspoon to form an indentation. Bake an additional 2 to 4 minutes or until edges are just beginning to brown. Remove from oven. Immediately reshape indentations as necessary. Cool 2 minutes on baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'479F72D8C658AF4EBF0C2143FEEA6E58', X'FE6035236DCE1541B9177E1D27CC61C1', X'FE6035236DCE1541B9177E1D27CC61C1', 5, NULL, NULL, 2, NULL, 0, 'STIR jam until smooth. Spoon about 1/2 teaspoon jam into center of each warm cookie. Remove from baking sheet to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6273F8A7220344FB598BF70817C5EBB', X'4E3408EA61FB8F46B75B3DD5AF606159', X'4E3408EA61FB8F46B75B3DD5AF606159', 0, NULL, NULL, 2, NULL, 0, 'LINE 2 baking sheets with wax paper. Coat wax paper with cooking spray. Combine cereal squares, pretzels, cereal rings and peanuts in extra large bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0D67B56029F61D4FBD04430837A8083E', X'4E3408EA61FB8F46B75B3DD5AF606159', X'4E3408EA61FB8F46B75B3DD5AF606159', 1, NULL, NULL, 2, NULL, 0, 'COMBINE baking chips and shortening in large microwave-safe bowl. Microwave on HIGH 1 minute; stir. Microwave at additional 10 second intervals until completely melted and smooth when stirred. Stir in peanut butter until evenly blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DBE931D1DF73C74D8D72B16F0AA56F26', X'4E3408EA61FB8F46B75B3DD5AF606159', X'4E3408EA61FB8F46B75B3DD5AF606159', 2, NULL, NULL, 2, NULL, 0, 'POUR over cereal mixture. Stir until all of the cereal mixture is lightly coated. Stir in candy pieces. Spread evenly on prepared baking sheets. Chill 5 to 10 minutes or until firm. Break into small pieces. Store in airtight container.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'752A011CCA085240826593976B439362', X'66EEE4F47CAC85408C08D5CB8CAB7A55', X'66EEE4F47CAC85408C08D5CB8CAB7A55', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Coat 12-inch pizza pan with no-stick cooking spray. Press cookie dough evenly in prepared pan to form crust. Bake 10 to 14 minutes or until light golden brown. Cool completely on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E42936D520F7A64583C03B5ABF5A6C88', X'66EEE4F47CAC85408C08D5CB8CAB7A55', X'66EEE4F47CAC85408C08D5CB8CAB7A55', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter spread over cookie crust to within 1/2 inch of edge. Arrange raspberries in shape of star in center of pizza. Arrange blueberries around raspberries to cover surface of pizza.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B6747BD566779C429B94B3D3DA80D09D', X'66EEE4F47CAC85408C08D5CB8CAB7A55', X'66EEE4F47CAC85408C08D5CB8CAB7A55', 2, NULL, NULL, 2, NULL, 0, 'MICROWAVE baking chips in small heavy-duty plastic bag on HIGH 30 to 45 seconds, kneading every 10 seconds, until melted and smooth. Cut very small corner off bag. Drizzle over blueberries.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A349B8298786A4A98234904EC940C74', X'0A123C44AADB3D488ABA5429A5E160F7', X'0A123C44AADB3D488ABA5429A5E160F7', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C23ACD1D209A97448C2E4F3E4158123C', X'0A123C44AADB3D488ABA5429A5E160F7', X'0A123C44AADB3D488ABA5429A5E160F7', 1, NULL, NULL, 2, NULL, 0, 'COMBINE pumpkin, syrup, cashew butter and cinnamon in medium bowl until smooth. Place in 8inch square baking dish. Top with cashews.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FFA5DB691777A44D9763A8F458369898', X'0A123C44AADB3D488ABA5429A5E160F7', X'0A123C44AADB3D488ABA5429A5E160F7', 2, NULL, NULL, 2, NULL, 0, 'BAKE 30 to 35 minutes or until heated through. Serve warm with graham crackers, pretzels or apples.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D957F2D3AA3CA743BBE803BA7E237FD3', X'6A807CBDAC00714EA6A294540958C21D', X'6A807CBDAC00714EA6A294540958C21D', 0, NULL, NULL, 2, NULL, 0, 'PLACE milk and peanut butter in blender container. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A66FDC7C1FB63949895F6CB496A585E0', X'6A807CBDAC00714EA6A294540958C21D', X'6A807CBDAC00714EA6A294540958C21D', 1, NULL, NULL, 2, NULL, 0, 'ADD ice cream. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA06E62B1D94744E96CD747B394BB847', X'6A807CBDAC00714EA6A294540958C21D', X'6A807CBDAC00714EA6A294540958C21D', 2, NULL, NULL, 2, NULL, 0, 'POUR into serving glasses. Top with whipped cream and peanuts, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A6D5738EF6E0B4FB8DB883C4CF7FC86', X'6A807CBDAC00714EA6A294540958C21D', X'6A807CBDAC00714EA6A294540958C21D', 3, NULL, NULL, 2, NULL, 0, 'BANANA PEANUT BUTTER MILKSHAKE: Prepare recipe as directed above, adding 1 small sliced banana to the blender container with peanut butter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FE9E4B278BF03F40AB9D80AA68B1D485', X'80236C613C0E6B47BACBA956A07FE143', X'80236C613C0E6B47BACBA956A07FE143', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE milk and chocolate spread in 2-cup microwave-safe glass measuring cup or small microwave-safe bowl on HIGH 2 minutes or until hot but not boiling. Whisk until smooth. Add cocoa mix, whisking until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9BCCD1A32DDF364A83FF69512DA9FE98', X'80236C613C0E6B47BACBA956A07FE143', X'80236C613C0E6B47BACBA956A07FE143', 1, NULL, NULL, 2, NULL, 0, 'PLACE 2 tablespoons marshmallows into each of 2 (8 oz.) serving mugs. Pour hot cocoa mixture over marshmallows. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9EEB138929106E4BB8F14F48E60F1420', X'14B13BAEF490284EACFDCA75C04BDAD8', X'14B13BAEF490284EACFDCA75C04BDAD8', 0, NULL, NULL, 2, NULL, 0, 'BEAT honey and peanut butter in medium bowl with mixer on high speed until completely blended. Place in small serving bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'51CABE8D9C24A746A767E83631711040', X'14B13BAEF490284EACFDCA75C04BDAD8', X'14B13BAEF490284EACFDCA75C04BDAD8', 1, NULL, NULL, 2, NULL, 0, 'PLACE coatings in small bowls.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0DE783CFED13B40842E8FEFB9130266', X'14B13BAEF490284EACFDCA75C04BDAD8', X'14B13BAEF490284EACFDCA75C04BDAD8', 2, NULL, NULL, 2, NULL, 0, 'To enjoy, DIP apple slice or other dipper into dip, then into your favorite coating.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50D61366B6DCCF48AE8D54B4FB429693', X'33182BB5C8F04346B831AF1047273B82', X'33182BB5C8F04346B831AF1047273B82', 0, NULL, NULL, 2, NULL, 0, 'SPREAD 1 tablespoon whipped peanut butter on one brownie. Top with sliced strawberry. Add whipped cream then add banana slices. Add another layer of whipped cream. Spread remaining peanut butter on other brownie and place peanut butter face down on top. Drizzle with chocolate syrup.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08B257126FCB684CAD8605676D7C5CD9', X'11677F99BEA4C84BA09DA4646ED58DFC', X'11677F99BEA4C84BA09DA4646ED58DFC', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4DE03F0ACEA8D64B86F8D2E42B5BD9BF', X'11677F99BEA4C84BA09DA4646ED58DFC', X'11677F99BEA4C84BA09DA4646ED58DFC', 1, NULL, NULL, 2, NULL, 0, 'BEAT sugar, brown sugar, peanut butter and butter in large bowl with mixer on medium speed until creamy. Beat in eggs, milk and vanilla until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C7B685DFC112CE4C81D64658FBCD28F5', X'11677F99BEA4C84BA09DA4646ED58DFC', X'11677F99BEA4C84BA09DA4646ED58DFC', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, baking powder and baking soda in medium bowl until blended. Stir in oats and coconut. Add to peanut butter mixture. Beat on low speed until well blended. Cover and chill 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0CABD4AC5426964D9A68E6EFB47C8C5B', X'11677F99BEA4C84BA09DA4646ED58DFC', X'11677F99BEA4C84BA09DA4646ED58DFC', 3, NULL, NULL, 2, NULL, 0, 'DROP dough by heaping tablespoonful, 2 inches apart, onto baking sheets. Bake 10 to 12 minutes or until edges are lightly browned. Cool on baking sheet 2 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B69EF61DE00E8541BE921A6BA9163440', X'D71D890D82C6724699659CE2CD82A165', X'D71D890D82C6724699659CE2CD82A165', 0, NULL, NULL, 2, NULL, 0, 'MIX chocolate spread, cream cheese, whipped topping and vanilla in small bowl until smooth. Place in decorating bag fitted with a medium to large star tip.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7E4C911BBED1B4468FAE876F84EA3B32', X'D71D890D82C6724699659CE2CD82A165', X'D71D890D82C6724699659CE2CD82A165', 1, NULL, NULL, 2, NULL, 0, 'PIPE about 2 tablespoons filling into each end of cannoli.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1FFBEDEA66F59645BF4E4A56830918A8', X'D71D890D82C6724699659CE2CD82A165', X'D71D890D82C6724699659CE2CD82A165', 2, NULL, NULL, 2, NULL, 0, 'This recipe will fill 6 (5-inch) or 12 (3 1/2-inch) cannoli shells. For the smaller shells, pipe about 1 tablespoon filling into each end.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1BD31314CC70A947B25EFB41D7B165B3', X'C3B5F5D86A0B314992A9407F57B5A197', X'C3B5F5D86A0B314992A9407F57B5A197', 0, NULL, NULL, 2, NULL, 0, 'THAW frozen cream puffs 10 to 15 minutes. Cut in half horizontally with sharp serrated knife.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8672E8BA9E8D2F459DEF265D9D8CF335', X'C3B5F5D86A0B314992A9407F57B5A197', X'C3B5F5D86A0B314992A9407F57B5A197', 1, NULL, NULL, 2, NULL, 0, 'SPREAD or pipe about 1 teaspoon peanut putter spread over cream filling in bottom halves of cream puffs. Replace top halves of cream puffs over spread. Chill until ready to serve.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F543BB6D5969F14A93670E11FE65806F', X'C3B5F5D86A0B314992A9407F57B5A197', X'C3B5F5D86A0B314992A9407F57B5A197', 2, NULL, NULL, 2, NULL, 0, 'SPRINKLE cream puffs with powdered sugar and cocoa powder just before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'12A7FB40973F3C48B75F47CDEE656D2D', X'1721A658E405F8438E9EF7109DA317AF', X'1721A658E405F8438E9EF7109DA317AF', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Place rolled oats in a 15 x 10-inch baking pan. Bake 15 minutes or until toasted. Cool.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'967A51936650AC49830B5B6CD711F77F', X'1721A658E405F8438E9EF7109DA317AF', X'1721A658E405F8438E9EF7109DA317AF', 1, NULL, NULL, 2, NULL, 0, 'COMBINE peanut butter, chocolate chips, honey, butter and salt in medium saucepan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E0683BCFD3B9D541B87E8CD738DF1669', X'1721A658E405F8438E9EF7109DA317AF', X'1721A658E405F8438E9EF7109DA317AF', 2, NULL, NULL, 2, NULL, 0, 'HEAT over low heat, stirring occasionally, until peanut butter and chocolate are melted and mixture is well blended. Add toasted oats; mix thoroughly.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B0B8EF5373186148BE855A0D849E455C', X'1721A658E405F8438E9EF7109DA317AF', X'1721A658E405F8438E9EF7109DA317AF', 3, NULL, NULL, 2, NULL, 0, 'DROP from teaspoon onto waxed paper. Chill until firm. Store, tightly covered, in refrigerator.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6D350130B1FB7040A9E1BB82C2B5882F', X'45D697208EA1664EBEAB89DE368B10C0', X'45D697208EA1664EBEAB89DE368B10C0', 0, NULL, NULL, 2, NULL, 0, 'SPREAD 1 tablespoon chocolate spread on each pizzelle. Spoon 1/4 cup ice cream onto each of 4 pizzelles, using small spoonfuls. Top with remaining pizelles to make a sandwich, pressing carefully until ice cream is even around the edges.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FD7F60A1F8DB70489229CC32F0402CA8', X'45D697208EA1664EBEAB89DE368B10C0', X'45D697208EA1664EBEAB89DE368B10C0', 1, NULL, NULL, 2, NULL, 0, 'FREEZE 20 minutes or until ice cream is firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A3BF9547D63F1943AE2478D06EB865DE', X'A11DB662EA249F429C9C4ECA8EA32C1F', X'A11DB662EA249F429C9C4ECA8EA32C1F', 0, NULL, NULL, 2, NULL, 0, 'LINE 8-inch square pan with foil, extending foil over edges of pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2ED1A1D2F89A3747A62E74366F123B8E', X'A11DB662EA249F429C9C4ECA8EA32C1F', X'A11DB662EA249F429C9C4ECA8EA32C1F', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE sweetened condensed milk and chocolate chips in medium microwave-safe bowl on HIGH 1 minute. Stir until smooth. If necessary, microwave at additional 15-second intervals until completely melted and smooth when stirred.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'852676797C9B7E438C63248A694D7137', X'A11DB662EA249F429C9C4ECA8EA32C1F', X'A11DB662EA249F429C9C4ECA8EA32C1F', 2, NULL, NULL, 2, NULL, 0, 'ADD peanut butter and vanilla. Beat with hand mixer on medium speed until well blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E6D4F3DA7AD8724C88686B90ACCDF72A', X'A11DB662EA249F429C9C4ECA8EA32C1F', X'A11DB662EA249F429C9C4ECA8EA32C1F', 3, NULL, NULL, 2, NULL, 0, 'SPREAD in prepared pan. Chill at least 1 hour. Remove fudge from pan by lifting foil. Remove foil. Cut into pieces before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'004B27D801CDAA46ADF1BB5FF91E436B', X'6951BC956888FF4D9BD10FFA6A8152FB', X'6951BC956888FF4D9BD10FFA6A8152FB', 0, NULL, NULL, 2, NULL, 0, 'LINE 8-inch baking pan with foil, extending foil up over edges of pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D68C74D8425DF048BA3028E69C89AB85', X'6951BC956888FF4D9BD10FFA6A8152FB', X'6951BC956888FF4D9BD10FFA6A8152FB', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE 1/2 cup sweetened condensed milk, peanut butter and white baking chips in medium microwave-safe bowl on HIGH 45 seconds or until melted and smooth when stirred. Stir in 1/2 teaspoon vanilla until blended. Stir in 1/4 cup peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B17B4BA95B8D3740BA65060CB907259E', X'6951BC956888FF4D9BD10FFA6A8152FB', X'6951BC956888FF4D9BD10FFA6A8152FB', 2, NULL, NULL, 2, NULL, 0, 'MICROWAVE remaining sweetened condensed milk, chocolate chips and salt in medium microwave-safe bowl on HIGH 45 seconds or until melted and smooth when stirred. Stir in remaining 1/2 teaspoon vanilla until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'671D2595CD45FA4A95DA45F345BE1C46', X'6951BC956888FF4D9BD10FFA6A8152FB', X'6951BC956888FF4D9BD10FFA6A8152FB', 3, NULL, NULL, 2, NULL, 0, 'SPOON peanut butter and chocolate mixtures into prepared pan, alternating heaping tablespoonfuls of both mixtures to vary the colors. When complete, with table knife or metal spatula, swirl mixtures to create a marbled appearance. Pat top surface to flatten evenly. Sprinkle with remaining 2 tablespoons peanuts. Pat into top surface of fudge.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3C5460DB1B237D40B9B5FA62758F0FBA', X'6951BC956888FF4D9BD10FFA6A8152FB', X'6951BC956888FF4D9BD10FFA6A8152FB', 4, NULL, NULL, 2, NULL, 0, 'CHILL at least 1 hour. Remove fudge from pan by lifting edges of foil. Remove foil. Cut into squares.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'29200BA77B9F6446B3785EBF011060F6', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', 0, NULL, NULL, 2, NULL, 0, 'BEAT sweetened condensed milk, peanut butter, egg and vanilla in large bowl until smooth. Add biscuit mix; mix well. Chill at least 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8B6E700B10F0284B8E7FB41F71B637BA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', 1, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CBBBB4E8A9269B429FED27F3C07B2FF6', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', 2, NULL, NULL, 2, NULL, 0, 'SHAPE dough into 1-inch balls. Roll in sugar. Place 2 inches apart on ungreased baking sheets. Flatten with fork in criss-cross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0DF69F77E29C7B429F9797F18C60DB70', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', 3, NULL, NULL, 2, NULL, 0, 'BAKE 6 to 8 minutes or until lightly browned (do not overbake). Cool.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'608D15104B5C85408B40DF3C32E70D86', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', 4, NULL, NULL, 2, NULL, 0, 'PEANUT BLOSSOM COOKIES: PREPARE dough as directed above. Shape into 1-inch balls; roll in sugar. Do not flatten. Place 2 inches apart on ungreased baking sheet. Bake as directed above. Immediately after baking, press solid milk chocolate candy drop in center of each ball.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FB523707A1CED745AA70B042F742B906', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', 5, NULL, NULL, 2, NULL, 0, 'PEANUT BUTTER AND JELLY GEMS: PREPARE dough as directed above. Shape into 1-inch balls; roll in sugar. Do not flatten. Place 2 inches apart on ungreased cookie sheet. Press thumb in center of each ball of dough; fill with desired flavor of Smucker''s® Jelly, Jam or Preserves. Bake as directed above.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3B8485122342B64B8BFA49D9833E82BD', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', X'A89AF0E47F31F341B1ABE66E4A9EA5AA', 6, NULL, NULL, 2, NULL, 0, 'ANY WAY YOU LIKE ''EM COOKIES: STIR 1 cup semi-sweet chocolate chips, chopped peanuts, raisins or flaked coconut into dough. Proceed as directed above.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FBABD9A5C2588549A8EE072820661883', X'C0591B9CAD4D6640AE3435ABAB3D978D', X'C0591B9CAD4D6640AE3435ABAB3D978D', 0, NULL, NULL, 2, NULL, 0, 'HEAT fillo shells according to package directions. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'25618A3AED76AA4990F23CEDBA9902B7', X'C0591B9CAD4D6640AE3435ABAB3D978D', X'C0591B9CAD4D6640AE3435ABAB3D978D', 1, NULL, NULL, 2, NULL, 0, 'BEAT cream cheese, peanut butter and sugar in large bowl with mixer on medium speed until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B2DA4064A6E844C979C9B0D9AD444FD', X'C0591B9CAD4D6640AE3435ABAB3D978D', X'C0591B9CAD4D6640AE3435ABAB3D978D', 2, NULL, NULL, 2, NULL, 0, 'BEAT in whipped topping until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08634632E63FD14CAF97F9CCDD48DDDD', X'C0591B9CAD4D6640AE3435ABAB3D978D', X'C0591B9CAD4D6640AE3435ABAB3D978D', 3, NULL, NULL, 2, NULL, 0, 'PIPE or spoon into cooled fillo shells. Sprinkle with peanuts, sprinkles or other garnishes, as desired. Chill until ready to serve. Best served same day.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0C0A8D32DA4E34EB093A007758937CA', X'E414A7963A846B4AA015A8E1D567C299', X'E414A7963A846B4AA015A8E1D567C299', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE sweetened condensed milk in medium microwave-safe bowl on HIGH 1 minute or until warm. Stir in white chocolate until melted. Stir in peanut butter until smooth. Cover and chill 2 hours or until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7312A57C57F9C042B81A3B1811E584F5', X'E414A7963A846B4AA015A8E1D567C299', X'E414A7963A846B4AA015A8E1D567C299', 1, NULL, NULL, 2, NULL, 0, 'SHAPE into 3/4-inch balls. Roll 1/3 in pecans and remaining in decorator sprinkles or sugar. Chill 1 hour or until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'826F20706F56E8408240AD2F4EF75BFB', X'69B29CBD0FD3B5488B34C7A6243F8DE0', X'69B29CBD0FD3B5488B34C7A6243F8DE0', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375ºF. Combine peanut butter, shortening, brown sugar, milk and vanilla in large bowl. Beat at medium speed of electric mixer until well blended. Add egg. Beat just until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5464A420324E244B8107D6171DD00005', X'69B29CBD0FD3B5488B34C7A6243F8DE0', X'69B29CBD0FD3B5488B34C7A6243F8DE0', 1, NULL, NULL, 2, NULL, 0, 'COMBINE flour, baking soda and salt. Add to creamed mixture at low speed. Mix just until blended. Drop by rounded measuring tablespoonfuls of dough 2-inches apart onto greased baking sheet. Flatten slightly in a crisscross pattern with tines of fork.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'26E14EA9BEA49C44A215DD01EFB2D55F', X'69B29CBD0FD3B5488B34C7A6243F8DE0', X'69B29CBD0FD3B5488B34C7A6243F8DE0', 2, NULL, NULL, 2, NULL, 0, 'BAKE one baking sheet at a time for 7 to 8 minutes, or until set and just beginning to brown. Cool 2 minutes on baking sheet. Remove cookies to cooling rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A59ECAD98A50614D8E7EA94FF8F3EEB6', X'359AE7F7147BCC4D846B301E44EFA79B', X'359AE7F7147BCC4D846B301E44EFA79B', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Spray 13 x 9-inch pan with a no-stick cooking spray. Melt chocolate chips in dry, microwave-safe bowl on HIGH (100% power) 1 minute. Stir. Microwave at additional, 10- to 15-second intervals, stirring just until chips are melted. Set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C0D0F78D70B3D2448B865CDC34B4E87E', X'359AE7F7147BCC4D846B301E44EFA79B', X'359AE7F7147BCC4D846B301E44EFA79B', 1, NULL, NULL, 2, NULL, 0, 'BEAT together brown sugar, peanut butter and butter in large bowl with an electric mixer, until smooth and creamy. Add egg and vanilla. Beat until well blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0877DA652A95464D916B5C0CF5AAFE82', X'359AE7F7147BCC4D846B301E44EFA79B', X'359AE7F7147BCC4D846B301E44EFA79B', 2, NULL, NULL, 2, NULL, 0, 'STIR together flour, baking soda and salt. Add to peanut butter mixture. Beat just until combined. Stir in oats. Press 3/4 of dough into prepared baking pan. Spread evenly with melted chocolate. Dot chocolate layer with remaining dough.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4F537EAFA0F4964A8C29DD5FD431FF21', X'359AE7F7147BCC4D846B301E44EFA79B', X'359AE7F7147BCC4D846B301E44EFA79B', 3, NULL, NULL, 2, NULL, 0, 'BAKE 24 to 26 minutes. Cool. Cut into 24 bars.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A20DE2311D4E7B43B1CCDD1A0717F4DF', X'70D31B8E6BFF1F4789D8AC10199F3009', X'70D31B8E6BFF1F4789D8AC10199F3009', 0, NULL, NULL, 2, NULL, 0, 'SPREAD graham cracker square with peanut butter. Top with a square of chocolate and a marshmallow.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D02008543105C4280CF67981E7A75CA', X'70D31B8E6BFF1F4789D8AC10199F3009', X'70D31B8E6BFF1F4789D8AC10199F3009', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE on HIGH (100% power) 5 to 10 seconds or until marshmallow puffs. Top with another cracker square. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8DF4BEC4E3DA1D4FAFAA12D9827EE9E1', X'E064825EE790194EBFF9FC57169B7A84', X'E064825EE790194EBFF9FC57169B7A84', 0, NULL, NULL, 2, NULL, 0, 'PLACE 2 teaspoons chocolate cookies into 8 (2 oz.) parfait cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E2AF3AA9B16EF743B086CDA0811F6EC3', X'E064825EE790194EBFF9FC57169B7A84', X'E064825EE790194EBFF9FC57169B7A84', 1, NULL, NULL, 2, NULL, 0, 'ADD peanut butter to resealable plastic bag. Cut off corner of bag. Pipe about 1 tablespoon into each cup. Top with 1 teaspoon chocolate cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'64ECD03469CBBB46AFECCF3BA3B6EBBF', X'E064825EE790194EBFF9FC57169B7A84', X'E064825EE790194EBFF9FC57169B7A84', 2, NULL, NULL, 2, NULL, 0, 'CHILL until ready to serve. Top with whipped cream and cinnamon before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'629AA0822970314CBAF7B635B417617C', X'13FF55F7D6F0A248AD51C9ADF5E2AB13', X'13FF55F7D6F0A248AD51C9ADF5E2AB13', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE marshmallows and butter in medium microwave-safe bowl on HIGH for 30 seconds. Stir in graham crackers until completely coated. Press firmly into 24 mini muffin cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CFD0A273EFCF214ABCAB83F9D7A74E62', X'13FF55F7D6F0A248AD51C9ADF5E2AB13', X'13FF55F7D6F0A248AD51C9ADF5E2AB13', 1, NULL, NULL, 2, NULL, 0, 'CHILL 5 minutes. Gently remove from pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7F26A72754C0B345872990043E9BC53F', X'13FF55F7D6F0A248AD51C9ADF5E2AB13', X'13FF55F7D6F0A248AD51C9ADF5E2AB13', 2, NULL, NULL, 2, NULL, 0, 'PLACE peanut butter into resealable plastic bag. Cut small corner off bag. Pipe about 1 tablespoon onto each crust. Sprinkle with chocolate chips.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A85B776936921C48A471548CE894ABA6', X'8ED439B1BEBC7C47898DB7D0CD7D1910', X'8ED439B1BEBC7C47898DB7D0CD7D1910', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Stir flour and baking soda in small bowl until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9091B2636ACEED489C482DDE0ECB3061', X'8ED439B1BEBC7C47898DB7D0CD7D1910', X'8ED439B1BEBC7C47898DB7D0CD7D1910', 1, NULL, NULL, 2, NULL, 0, 'BEAT butter, sugar and brown sugar in large bowl with mixer on medium speed until creamy. Beat in peanut butter, egg and vanilla. Gradually beat in flour mixture. Stir in granola cereal, cranberries and pistachios until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EC879A133368DC449A9DFC6668F4B720', X'8ED439B1BEBC7C47898DB7D0CD7D1910', X'8ED439B1BEBC7C47898DB7D0CD7D1910', 2, NULL, NULL, 2, NULL, 0, 'DROP by rounded tablespoonfuls 2 inches apart onto baking sheet. Bake 8 to 9 minutes or until edges are lightly browned. Cool 3 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF29E281215074448CEDFCEAB11E0B18', X'B66E2593848A7245BA944F1C658F6F5B', X'B66E2593848A7245BA944F1C658F6F5B', 0, NULL, NULL, 2, NULL, 0, 'COMBINE milk, peanut powder, banana and honey in blender container. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A22F0D2957CCC4AA06514C4A8EBB5A6', X'B66E2593848A7245BA944F1C658F6F5B', X'B66E2593848A7245BA944F1C658F6F5B', 1, NULL, NULL, 2, NULL, 0, 'POUR into ice pop molds.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FC30FFF76EDFAD469D2BFF5263CBDB56', X'B66E2593848A7245BA944F1C658F6F5B', X'B66E2593848A7245BA944F1C658F6F5B', 2, NULL, NULL, 2, NULL, 0, 'FREEZE 4 hours or until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A85981EE6915E34AAB1D42DA162D99C5', X'B424E2780301994385A9925682D10F8A', X'B424E2780301994385A9925682D10F8A', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat 13 x 9-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C489ABC954326A44B9D888B3A1197EAF', X'B424E2780301994385A9925682D10F8A', X'B424E2780301994385A9925682D10F8A', 1, NULL, NULL, 2, NULL, 0, 'BEAT butter and brown sugar in large bowl with mixer on medium speed until fluffy, about 3 minutes. Add peanut butter, eggs and vanilla. Beat until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A4CC557F42B2474E9D46CB47D847E91B', X'B424E2780301994385A9925682D10F8A', X'B424E2780301994385A9925682D10F8A', 2, NULL, NULL, 2, NULL, 0, 'STIR flour and baking powder in small bowl until blended. Stir into peanut butter mixture. Spread evenly in prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B1AA44D28E5A974AA09D4675C82DC70C', X'B424E2780301994385A9925682D10F8A', X'B424E2780301994385A9925682D10F8A', 3, NULL, NULL, 2, NULL, 0, 'TOP surface evenly with teaspoonfuls of jam. Do not swirl. Sprinkle evenly with peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0454AC3EB088CD4FAB6B06CCD4FA9DBD', X'B424E2780301994385A9925682D10F8A', X'B424E2780301994385A9925682D10F8A', 4, NULL, NULL, 2, NULL, 0, 'BAKE 28 to 32 minutes or until toothpick inserted in center comes out clean. Cool completely on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ED86B9F0E361D1458201D9779FBA8364', X'9F439BD291124547BD161C0961094636', X'9F439BD291124547BD161C0961094636', 0, NULL, NULL, 2, NULL, 0, 'BEAT bananas and peanut butter in large bowl with electric mixer on medium speed until smooth. Spread evenly into pie crust.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0F40692CE7FB94AA4FDC1ED7D0C1682', X'9F439BD291124547BD161C0961094636', X'9F439BD291124547BD161C0961094636', 1, NULL, NULL, 2, NULL, 0, 'BEAT cream and preserves in large bowl on medium speed with electric mixer until soft peaks form. Spread over peanut butter mixture. Freeze 3 hours.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FE7617E2254E1A418BB9CA6053400F8A', X'9F439BD291124547BD161C0961094636', X'9F439BD291124547BD161C0961094636', 2, NULL, NULL, 2, NULL, 0, 'GARNISH with strawberries and peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'91859ADF804A32469F7407550CBF5406', X'569C64B7DC1E9D4283DE6E4C731F3CDC', X'569C64B7DC1E9D4283DE6E4C731F3CDC', 0, NULL, NULL, 2, NULL, 0, 'PLACE 3 tablespoons peanut butter chocolate spread into corner of resealable plastic bag. Reserve for garnish. Arrange bananas end to end, cutting to fit, in a circular pattern on the bottom of crust. Mix remaining peanut butter chocolate spread with almond extract. Spread evenly over bananas.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'417D551AB430AB4185821FE8D4D0EC2F', X'569C64B7DC1E9D4283DE6E4C731F3CDC', X'569C64B7DC1E9D4283DE6E4C731F3CDC', 1, NULL, NULL, 2, NULL, 0, 'BEAT pudding mixes and cold milk in large bowl with whisk for 2 minutes. Blend in 1 cup of whipped topping. Spread evenly over peanut butter mixture.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D602C7806BE7894781A34337443DE048', X'569C64B7DC1E9D4283DE6E4C731F3CDC', X'569C64B7DC1E9D4283DE6E4C731F3CDC', 2, NULL, NULL, 2, NULL, 0, 'TOP with remaining whipped topping. Starting at outside edge, and using a rounded edged knife, draw knife to center and pull up to create swirl at center. Repeat in a parallel fashion around pie. Cut small corner off reserved bag of peanut butter chocolate spread. Drizzle over pie. Sprinkle with chopped peanuts. Chill pie for 3 hours or until set.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C03B84DA73AB08449364B06A84A21B76', X'7BBF2917DE1A35428718301E380A2DD7', X'7BBF2917DE1A35428718301E380A2DD7', 0, NULL, NULL, 2, NULL, 0, 'WHISK together pudding mix and milk in medium microwave-safe bowl. Microwave on HIGH 5 minutes or until pudding comes to a boil, stirring every 2 minutes. Add 3/4 cup peanut butter, whisking until well blended. Pour into pie crust. Cover and chill at least 2 hours.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'497D16CC659F7C48809E3D19EA0B5DCB', X'7BBF2917DE1A35428718301E380A2DD7', X'7BBF2917DE1A35428718301E380A2DD7', 1, NULL, NULL, 2, NULL, 0, 'STIR preserves in a small bowl. Spoon onto pie, along inside edge of crust, creating a 1-inch wide border.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A3D4C97FC8580446A74C3429FEEE2C10', X'7BBF2917DE1A35428718301E380A2DD7', X'7BBF2917DE1A35428718301E380A2DD7', 2, NULL, NULL, 2, NULL, 0, 'PLACE remaining 1/4 cup peanut butter in small resealable plastic bag. Microwave on HIGH 10 seconds to warm. Knead peanut butter. Cut a very small hole in bottom corner of plastic bag. Drizzle peanut butter across pie. Chill until serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'21796D8F57F6014B8DBFD6D351B3BB30', X'28E6E220E6099F4AAB00BCE6115C1161', X'28E6E220E6099F4AAB00BCE6115C1161', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat 8-inch square baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E9C741F230C2274888440443A37B6BCD', X'28E6E220E6099F4AAB00BCE6115C1161', X'28E6E220E6099F4AAB00BCE6115C1161', 1, NULL, NULL, 2, NULL, 0, 'BEAT 1/2 cup peanut butter, butter and sugar in large bowl with electric mixer until creamy. Add eggs and vanilla. Beat until blended. Add milk and pancake mix alternately, beating after each addition until just combined. Pour into prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E00C3AE3843343448C58387D074EB184', X'28E6E220E6099F4AAB00BCE6115C1161', X'28E6E220E6099F4AAB00BCE6115C1161', 2, NULL, NULL, 2, NULL, 0, 'BAKE 30 to 35 minutes or until toothpick inserted in center comes out clean. Cool 10 minutes, then turn cake into a large bowl and set aside. Beat 1/2 cup peanut butter and frosting until smooth. Line two large baking sheets with wax paper.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A79EC71A7B147441B5097299901DCEF0', X'28E6E220E6099F4AAB00BCE6115C1161', X'28E6E220E6099F4AAB00BCE6115C1161', 3, NULL, NULL, 2, NULL, 0, 'CRUMBLE cake into fine crumbs with fork in bowl. Add frosting mixture. Mix thoroughly with spatula. Cover with plastic wrap and freeze 30 minutes. Roll mixture into 1 1/2-inch round cake bites using small cookie scoop (one tablespoon cake mixture). Place bites on one prepared baking sheet. Cover with plastic wrap and freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'07CBD975C8C58B4794FB31FB9BFB34FE', X'28E6E220E6099F4AAB00BCE6115C1161', X'28E6E220E6099F4AAB00BCE6115C1161', 4, NULL, NULL, 2, NULL, 0, 'PLACE chocolate chips and shortening in microwave-safe bowl. Microwave on HIGH 2 minutes. Stir. Microwave in additional 10 second intervals until completely melted and smooth when stirred.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'52F73C6F3977E74A90BF29FA3C3CC976', X'28E6E220E6099F4AAB00BCE6115C1161', X'28E6E220E6099F4AAB00BCE6115C1161', 5, NULL, NULL, 2, NULL, 0, 'REMOVE ten cake bites from freezer at a time, keeping the remaining ones frozen. Drop one cake bite at a time into melted chocolate. Spoon chocolate over each bite until coated. Lift cake bite out of chocolate with a fork, allowing the excess chocolate to drip into bowl. Using toothpick, gently push bite off fork onto other prepared baking sheet. Top each with chopped peanuts immediately. Repeat to coat remaining bites. Chill 5 minutes or until chocolate is set. Allow to come to room temperature before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'73A4280030F5334B96977851B4B3C5A8', X'28E6E220E6099F4AAB00BCE6115C1161', X'28E6E220E6099F4AAB00BCE6115C1161', 6, NULL, NULL, 2, NULL, 0, 'If chocolate becomes stiff while dipping bites, heat in microwave for 10 second intervals, stirring until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF8794DC7890394093709D310E14DA43', X'90A052CD34D3E245AA3C74BF62788DDF', X'90A052CD34D3E245AA3C74BF62788DDF', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate spread and butter in medium microwave-safe bowl on HIGH 30 seconds or until butter is melted. Stir until smooth. Cool 10 minutes. Stir in powdered sugar until blended. Form into 3/4-inch balls.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BCB2CC99F904A7448BB6D4B7D5D6323A', X'90A052CD34D3E245AA3C74BF62788DDF', X'90A052CD34D3E245AA3C74BF62788DDF', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE peanut butter chips and shortening in small microwave-safe bowl on HIGH 30 seconds; stir. Repeat as needed in 15 seconds intervals until melted and smooth when stirred. Dip truffles into melted peanut butter chips, allowing excess to drip off into bowl. Place on wax paper. Top immediately with decorator sprinkles.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D6E5E7F068A5F64AAA21C49E89A6F939', X'24789F1C7BBBF7459775E92593D26CB9', X'24789F1C7BBBF7459775E92593D26CB9', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 325°F. Beat peanut butter and 1/2 cup sugar in medium bowl with electric mixer on low until creamy. Mix in egg yolks, one at a time, until thoroughly combined. Add milk. Beat until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DFA58F2D13F9C44C915E775A393A599D', X'24789F1C7BBBF7459775E92593D26CB9', X'24789F1C7BBBF7459775E92593D26CB9', 1, NULL, NULL, 2, NULL, 0, 'POUR mixture into 8 oven-proof ramekins. Place ramekins in baking pan or casserole dish. Add 1/2 inch water to pan. Bake 40 to 45 mintues or until custard is set when shaken. Remove from oven. Chill 2 hours.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'411F9B8636F6574F8B27E6AB5E16BD72', X'24789F1C7BBBF7459775E92593D26CB9', X'24789F1C7BBBF7459775E92593D26CB9', 2, NULL, NULL, 2, NULL, 0, 'HEAT broiler. Sprinkle remaining sugar over tops of each custard. Place ramekins on top rack of broiler. Broil 4 to 6 minutes or until sugar bubbles and caramelizes. Serve warm or chilled.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50EFB690B5E92C42AE2AC87E936FD94F', X'22B62CB284B3A845AACDBEFB1B03B49C', X'22B62CB284B3A845AACDBEFB1B03B49C', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Cut donuts in half crosswise.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B02DEF4BF5925044ABE948C508794A11', X'22B62CB284B3A845AACDBEFB1B03B49C', X'22B62CB284B3A845AACDBEFB1B03B49C', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter spread between two halves. Press together to form sandwich. Repeat to make 12 sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'695320CDBE88824E9ECC0B39930BD52D', X'22B62CB284B3A845AACDBEFB1B03B49C', X'22B62CB284B3A845AACDBEFB1B03B49C', 2, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate chips in small microwave-safe bowl 1 minute. Stir until smooth. Dip each sandwich halfway into melted chocolate. Cool 10 minutes on prepared baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9E5D158C64C05E45A54C8664C1C324E4', X'0EC27CCCA9C3BE4C833DA5641AC80116', X'0EC27CCCA9C3BE4C833DA5641AC80116', 0, NULL, NULL, 2, NULL, 0, 'BREAK noodles into smaller pieces in large bowl, using your hands. Combine sugar and syrup in large saucepan. Cook over medium heat until sugar is melted and edges begin to bubble. Add peanut butter and stir until smooth. Pour mixture over noodles and toss to coat.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3B66F8A563F124DB986CABD658DF074', X'0EC27CCCA9C3BE4C833DA5641AC80116', X'0EC27CCCA9C3BE4C833DA5641AC80116', 1, NULL, NULL, 2, NULL, 0, 'TEST temperature carefully. With slightly buttered hands, form mixture into tight balls using approximately 1/4 cup mixture per ball. Make indention in center of each ball to resemble a birds nest. Place on waxed paper and allow to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'23DD35DCD978A447B3AD1AFC0B3D3F30', X'0EC27CCCA9C3BE4C833DA5641AC80116', X'0EC27CCCA9C3BE4C833DA5641AC80116', 2, NULL, NULL, 2, NULL, 0, 'PLACE two or three jelly beans or other egg-shaped candies into each nest.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4E51FF8A5B9E4443AB942249960CC27C', X'D3C72DFDD4F874489C86BD8610299331', X'D3C72DFDD4F874489C86BD8610299331', 0, NULL, NULL, 2, NULL, 0, 'LINE 13 x 9-inch pan with aluminum foil; spray with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'62617B738DAF004294B51DAF27FFB668', X'D3C72DFDD4F874489C86BD8610299331', X'D3C72DFDD4F874489C86BD8610299331', 1, NULL, NULL, 2, NULL, 0, 'COMBINE sugar, butter and milk in large saucepan, stirring constantly on medium heat, until mixture comes to a boil. Reduce heat and simmer 5 minutes, stirring constantly. Remove from heat.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B37EF0517841F341AEB7A3185002F9AA', X'D3C72DFDD4F874489C86BD8610299331', X'D3C72DFDD4F874489C86BD8610299331', 2, NULL, NULL, 2, NULL, 0, 'ADD peanut butter. Stir until well blended. Add marshmallow creme and vanilla. Beat until well blended. Spread in prepared pan. Cool. Cut into candy-sized pieces.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D38351AD8B784F4F9CCEA7401204EC4D', X'C046176B5DA8C5479015A7243859B497', X'C046176B5DA8C5479015A7243859B497', 0, NULL, NULL, 2, NULL, 0, 'COMBINE syrup and peanut butter in microwave-safe measuring cup. Microwave on MEDIUM 15 seconds. Stir. Repeat until smooth. Cool slightly.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B92F668CFC21C64F850F27348CCA3957', X'C046176B5DA8C5479015A7243859B497', X'C046176B5DA8C5479015A7243859B497', 1, NULL, NULL, 2, NULL, 0, 'POUR warm over frozen yogurt or ice cream.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B48E77B4D8D8954EAF6CBA1B55BDD089', X'C046176B5DA8C5479015A7243859B497', X'C046176B5DA8C5479015A7243859B497', 2, NULL, NULL, 2, NULL, 0, 'If topping becomes too thick, thin with a little additional syrup.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5888A97C207EE94EB814651A765D248C', X'7DBDCE7CACEEE54D8FF2DD8431D60CE9', X'7DBDCE7CACEEE54D8FF2DD8431D60CE9', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Beat peanut butter, brown sugar, sugar, butter and vanilla in large bowl with mixer on high speed until well blended. Beat in eggs.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F41D8353BEE2D94F9D6AF6D580C77430', X'7DBDCE7CACEEE54D8FF2DD8431D60CE9', X'7DBDCE7CACEEE54D8FF2DD8431D60CE9', 1, NULL, NULL, 2, NULL, 0, 'STIR oats, baking soda and salt in large bowl until blended. Gradually blend into peanut butter mixture. Stir in chocolate chips and chocolate pieces. Cover and chill at least 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F896A9D0EA0950408EE5F9C277E22624', X'7DBDCE7CACEEE54D8FF2DD8431D60CE9', X'7DBDCE7CACEEE54D8FF2DD8431D60CE9', 2, NULL, NULL, 2, NULL, 0, 'DROP by heaping tablespoonfuls 2 inches apart onto baking sheets. Press to flatten slightly. Bake 9 to 11 minutes or until edges are lightly browned. Cool 2 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A33A277BD48634FA5E3EF0ECC060009', X'F7B12774EC427140885B015FA8FC9931', X'F7B12774EC427140885B015FA8FC9931', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Line baking sheets with parchment paper, if desired. Beat brown sugar and butter in large bowl with electric mixer on medium speed until creamy. Add 1/2 cup peanut butter, egg and vanilla, beating until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB8A9B6161172849AF8279D57A717BA2', X'F7B12774EC427140885B015FA8FC9931', X'F7B12774EC427140885B015FA8FC9931', 1, NULL, NULL, 2, NULL, 0, 'COMBINE flour, baking soda, salt, cinnamon and baking powder in medium bowl, stirring until well blended. Gradually add to creamed mixture, beating just until blended. Mix in oats.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'057E50474889094F85DF90F34114A7AE', X'F7B12774EC427140885B015FA8FC9931', X'F7B12774EC427140885B015FA8FC9931', 2, NULL, NULL, 2, NULL, 0, 'DROP by 2 level teaspoonfuls, 2 inches apart, onto prepared baking sheets to make 48 mounds. Bake 9 to 11 minutes or until golden brown. Cool on baking sheet 3 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC5960FC9B92C3478D8601C215889DEE', X'F7B12774EC427140885B015FA8FC9931', X'F7B12774EC427140885B015FA8FC9931', 3, NULL, NULL, 2, NULL, 0, 'BEAT frosting and remaining 3/4 cup peanut butter in medium bowl with electric mixer on medium speed until blended. Spread in an even layer on the flat side of half the cookies. Place another cookie, flat side down, on top of filling mixture to make sandwich cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B6338F2DC066F4D9BA6B2962ACE6092', X'DF2C8DE568453C42841F3CC1DC7970EF', X'DF2C8DE568453C42841F3CC1DC7970EF', 0, NULL, NULL, 2, NULL, 0, 'PREPARE pudding mix according to package directions using 2 1/3 cups milk. Stir in peanut butter until completely blended. Divide into 4 (1-cup) serving dishes. Cool completely. Chill until ready to serve.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C9FFD17237F67D40927DDA3B5EA36090', X'DF2C8DE568453C42841F3CC1DC7970EF', X'DF2C8DE568453C42841F3CC1DC7970EF', 1, NULL, NULL, 2, NULL, 0, 'TOP evenly with granola, banana and jam just before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C5457AF3D1117B44B6497E51D91FD61B', X'79601DCB241FDA439316A1C906AAEC33', X'79601DCB241FDA439316A1C906AAEC33', 0, NULL, NULL, 2, NULL, 0, 'LINE 15 x 10 x 1-inch baking pan with wax paper. Place graham cracker crumbs in small bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4CCAB12140988D46BBFDD773FAAA2D93', X'79601DCB241FDA439316A1C906AAEC33', X'79601DCB241FDA439316A1C906AAEC33', 1, NULL, NULL, 2, NULL, 0, 'PLACE peanut butter spread in small bowl. Dip one end of each marshmallow into spread, one at a time, turning to coat bottom third of marshmallow. Dip coated area into graham cracker crumbs. Place on prepared pan, coated side down.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1285D7B220DAF4489B2656B1B882A37E', X'79601DCB241FDA439316A1C906AAEC33', X'79601DCB241FDA439316A1C906AAEC33', 2, NULL, NULL, 2, NULL, 0, 'COMBINE chocolate chips and shortening in 1-quart heavy-duty resealable plastic bag. Microwave on HIGH 30 to 40 seconds. Knead until chocolate is melted and smooth. Cut very small corner off bag. Drizzle over marshmallows. Insert lollipop sticks into marshmallows. Freeze 10 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'45DCF14CC0A9AD4E9406BB0D47B6B861', X'679F4D2C6438184DB51C4DBB17B58E90', X'679F4D2C6438184DB51C4DBB17B58E90', 0, NULL, NULL, 2, NULL, 0, 'HEAT over to 325ºF. Coat a 15 x 10-inch jelly roll pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6BC147EE411DE8419F798A017C0E003D', X'679F4D2C6438184DB51C4DBB17B58E90', X'679F4D2C6438184DB51C4DBB17B58E90', 1, NULL, NULL, 2, NULL, 0, 'PREPARE cake mix batter according to package directions using water, oil and eggs. Spread in prepared pan. Bake 16 to 20 minutes or until toothpick inserted in center comes out clean. Cool completely in pan on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'174737093282A947A54D23AEC57557F1', X'679F4D2C6438184DB51C4DBB17B58E90', X'679F4D2C6438184DB51C4DBB17B58E90', 2, NULL, NULL, 2, NULL, 0, 'BEAT milk and pudding mix in large bowl with electric mixer on medium speed until thickened, about 15 seconds. Add peanut butter; beat until smooth. Beat in whipped topping until smooth. Spread over cooled cake. Garnish with chopped peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B416864E4D81F46920B9B48BAD3CECF', X'90674C9EE5B5754A92F8116127A49EF7', X'90674C9EE5B5754A92F8116127A49EF7', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE sweetened condensed milk in medium microwave-safe bowl on HIGH 1 to 1 1/2 minutes or until very warm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'78C80DFCDB9AFF44AFFF8A127CAC788E', X'90674C9EE5B5754A92F8116127A49EF7', X'90674C9EE5B5754A92F8116127A49EF7', 1, NULL, NULL, 2, NULL, 0, 'STIR in white chocolate until melted. Stir in peanut butter until smooth. Cover and chill 2 hours or until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7AE9FA3D591A0544A69D85A91EBEA9BD', X'90674C9EE5B5754A92F8116127A49EF7', X'90674C9EE5B5754A92F8116127A49EF7', 2, NULL, NULL, 2, NULL, 0, 'SHAPE into 72 (3/4-inch) balls. Roll 2 dozen balls in peanuts, 2 dozen in cinnamon-sugar and remaining 2 dozen in sprinkles. Chill until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3F9F1092D1D1DE43B0A6ABDC07B27E4E', X'B69A7DA55897FD4EA557A9CDF081E7DB', X'B69A7DA55897FD4EA557A9CDF081E7DB', 0, NULL, NULL, 2, NULL, 0, 'COMBINE banana, milk, peanut butter and honey in blender container. Add ginger, if desired. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6956364C2193034CB7F853DD925B66ED', X'B69A7DA55897FD4EA557A9CDF081E7DB', X'B69A7DA55897FD4EA557A9CDF081E7DB', 1, NULL, NULL, 2, NULL, 0, 'DIVIDE evenly into 5 (5 oz.) waxed-coated paper cups or plastic cups. Cover with foil.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C4D5AC3D81E43479A448EC38B9F89F1', X'B69A7DA55897FD4EA557A9CDF081E7DB', X'B69A7DA55897FD4EA557A9CDF081E7DB', 2, NULL, NULL, 2, NULL, 0, 'FREEZE 6 hours or until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5E7FE0BA3CB6D6429002A491B045EE08', X'941E19A8CD38F646B049B0D619A043F8', X'941E19A8CD38F646B049B0D619A043F8', 0, NULL, NULL, 2, NULL, 0, 'UNWRAP ice cream sandwiches. Arrange enough sandwiches in bottom of 13 x 9-inch baking dish to form an even layer, cutting sandwiches as necessary to fit.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F4E9D7B2237F334F99AE6E55253D39E7', X'941E19A8CD38F646B049B0D619A043F8', X'941E19A8CD38F646B049B0D619A043F8', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE peanut butter in medium microwave-safe bowl on HIGH 45 to 60 seconds or until slightly melted. Whisk in powdered sugar until smooth. Drizzle over ice cream sandwich layer; spread evenly.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08AB67BD51AB1242A79CBF8A5EC93008', X'941E19A8CD38F646B049B0D619A043F8', X'941E19A8CD38F646B049B0D619A043F8', 2, NULL, NULL, 2, NULL, 0, 'TOP with additional ice cream sandwiches to form an even layer.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'748F2AC18EE4E64DB4E65356FC79E210', X'941E19A8CD38F646B049B0D619A043F8', X'941E19A8CD38F646B049B0D619A043F8', 3, NULL, NULL, 2, NULL, 0, 'MICROWAVE fudge topping according to label instructions. Stir. Spread over ice cream sandwich layer. Freeze until topping is set.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'96E6E029B946544195ABA14583AEF76F', X'941E19A8CD38F646B049B0D619A043F8', X'941E19A8CD38F646B049B0D619A043F8', 4, NULL, NULL, 2, NULL, 0, 'SPOON whipped topping over fudge layer; spread evenly. Sprinkle with peanuts. Cover tightly with foil and freeze until firm, at least 3 hours. Remove from freezer about 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1D2819C9A0C25C4DABE9C6FECB2B529C', X'AF2992EC7E8B2946854619CC0B7A96A7', X'AF2992EC7E8B2946854619CC0B7A96A7', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Coat an 8-inch square baking pan with no-stick cooking spray. Peel pears and cut in half lengthwise. Remove seeds with melon baller. Carefully trim ends. Place pears, cut side down, in prepared pan. Bake for 25 to 35 minutes or until fork tender.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3F128D0C8C8C8E49A5DE51C1F797E37B', X'AF2992EC7E8B2946854619CC0B7A96A7', X'AF2992EC7E8B2946854619CC0B7A96A7', 1, NULL, NULL, 2, NULL, 0, 'STIR peanut butter, caramel topping and water in small bowl until smooth. Let stand at least 10 minutes to thicken.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DA711BFCA83AA347A74F61E15FDC4C4F', X'AF2992EC7E8B2946854619CC0B7A96A7', X'AF2992EC7E8B2946854619CC0B7A96A7', 2, NULL, NULL, 2, NULL, 0, 'ARRANGE warm pears, cut side up, on individual serving plates. Top each with a scoop of frozen yogurt, if desired. Spoon sauce over yogurt and pears. Sprinkle with chopped peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'835EF698E618824980F7474AB9FEF169', X'CF8C6385040C1A448BB7EB4CA586D2F8', X'CF8C6385040C1A448BB7EB4CA586D2F8', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Microwave chocolate in medium microwave-safe bowl on HIGH 1 1/2 minutes. Stir until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E6A9E3F05BDCDA4C9802B47AE3BBF3A1', X'CF8C6385040C1A448BB7EB4CA586D2F8', X'CF8C6385040C1A448BB7EB4CA586D2F8', 1, NULL, NULL, 2, NULL, 0, 'DIP each cracker halfway into chocolate. Sprinkle immediately with toffee bits, if desired. Place on prepared baking sheet. Chill 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C7D238FF410E6741825B1F5C1E29D916', X'CF8C6385040C1A448BB7EB4CA586D2F8', X'CF8C6385040C1A448BB7EB4CA586D2F8', 2, NULL, NULL, 2, NULL, 0, 'SPREAD 2 teaspoons peanut butter on inside of each cracker. Press 2 crackers together. Repeat with remaining crackers to make 24 snackwiches. Freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C74617EEF37BC1438C59C300FABE3B77', X'25240B0D92977C41A514000DDE1A4E2B', X'25240B0D92977C41A514000DDE1A4E2B', 0, NULL, NULL, 2, NULL, 0, 'PLACE graham cracker crumbs on small plate.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'64F1AB5C9E69514394A16C0C717F071B', X'25240B0D92977C41A514000DDE1A4E2B', X'25240B0D92977C41A514000DDE1A4E2B', 1, NULL, NULL, 2, NULL, 0, 'SPOON marshmallow creme into bottom of waffle cone. Microwave on HIGH 5 seconds to warm, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4BED89AC149AF14F9BF70A70CCB85723', X'25240B0D92977C41A514000DDE1A4E2B', X'25240B0D92977C41A514000DDE1A4E2B', 2, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter spread evenly on inside and outside top edge of cone. Roll outside edge in graham cracker crumbs to coat. Scoop ice cream into cone. Garnish with whipped cream, sprinkle with any remaining graham cracker crumbs and top with a cherry. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ADBA5568D6F4CD4B8079385D37DFE94F', X'52CCE1F5EE322A4A957FAA472DFAF1CE', X'52CCE1F5EE322A4A957FAA472DFAF1CE', 0, NULL, NULL, 2, NULL, 0, 'LAYER 1 (9-inch) pie plate with peanut butter, pretzels, caramel topping, whipped topping, coconut, chocolate chips and peanuts. Serve with crackers.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D258992B5159B24FAA3C6B0C23985A07', X'9EADCE8B86503046AA09B0BD4AE97A8A', X'9EADCE8B86503046AA09B0BD4AE97A8A', 0, NULL, NULL, 2, NULL, 0, 'WHISK yogurt, peanut butter and fruit spread until mixed thoroughly. Chop fruit into small pieces.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'92CBFE5E760B074AAA1C4460C1CB8990', X'9EADCE8B86503046AA09B0BD4AE97A8A', X'9EADCE8B86503046AA09B0BD4AE97A8A', 1, NULL, NULL, 2, NULL, 0, 'PLACE 2 tablespoons yogurt mixture into bottom of each waffle cup, top with 1/2 cup of mixed fruit and a dollop of yogurt mixture.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A8A66D539B5DBD4EB8408AE324D87204', X'504A743718ADD54DA1947DD5FF1B87AC', X'504A743718ADD54DA1947DD5FF1B87AC', 0, NULL, NULL, 2, NULL, 0, 'PREPARE brownie mix according to package instructions using oil, water and eggs. Bake in 13 x 9-inch baking pan according to package instructions. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1A95B407442D3248A6ED3093B3BD233F', X'504A743718ADD54DA1947DD5FF1B87AC', X'504A743718ADD54DA1947DD5FF1B87AC', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter spread over brownies. Top with pretzels.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5462B8D2BB31A84CA794AF712AC262A9', X'506C078D248E674D86CA91A7F34EF343', X'506C078D248E674D86CA91A7F34EF343', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat tortillas with no-stick spray and cinnamon-sugar on each side. Turn a 12-cup muffin tin upside down. Press each tortilla in the space between 4 cups to form a bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA72C3BB8509E04E916C92A3E550FEAE', X'506C078D248E674D86CA91A7F34EF343', X'506C078D248E674D86CA91A7F34EF343', 1, NULL, NULL, 2, NULL, 0, 'BAKE 9 to 11 minutes or until lightly browned. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BF98D3AEA319FB49A7272E28D2AE6979', X'506C078D248E674D86CA91A7F34EF343', X'506C078D248E674D86CA91A7F34EF343', 2, NULL, NULL, 2, NULL, 0, 'SCOOP ice cream into tortilla cups. Top with peanut spread and hot fudge, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4E1DE360352FD749B35944FE1CFB6580', X'F0F8861DC8A16F468A068ED171A7B129', X'F0F8861DC8A16F468A068ED171A7B129', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat baking sheets with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F55528D9EE54424F809A21997F5F0EFB', X'F0F8861DC8A16F468A068ED171A7B129', X'F0F8861DC8A16F468A068ED171A7B129', 1, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter, shortening, sugar, brown sugar, egg, milk and vanilla in large bowl with mixer on high speed until well blended and fluffy, about 3 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'057645DF5C87FF4390098154A3CCCAFE', X'F0F8861DC8A16F468A068ED171A7B129', X'F0F8861DC8A16F468A068ED171A7B129', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, baking soda and salt in medium bowl until blended. Add to peanut butter mixture, mixing just until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'64E6ECB5434434479A2CA02ECBF72509', X'F0F8861DC8A16F468A068ED171A7B129', X'F0F8861DC8A16F468A068ED171A7B129', 3, NULL, NULL, 2, NULL, 0, 'SHAPE dough into 1-inch balls. Place on prepared baking sheets. Flatten with tines of fork in a crisscross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C9F0528F26E83449A47C32BCCA93974C', X'F0F8861DC8A16F468A068ED171A7B129', X'F0F8861DC8A16F468A068ED171A7B129', 4, NULL, NULL, 2, NULL, 0, 'BAKE 8 to 9 minutes or until edges are lightly browned and cookies are set. Cool 2 minutes. Remove from baking sheets to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D38829AC684C6544AC1143B5266B6EE0', X'95B4695B0790644B8A915E0FEA7B3FCE', X'95B4695B0790644B8A915E0FEA7B3FCE', 0, NULL, NULL, 2, NULL, 0, 'COMBINE banana, milk, peanut butter spread, honey and ginger in blender container. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A8EC87EB59A1744387692057B60C188A', X'95B4695B0790644B8A915E0FEA7B3FCE', X'95B4695B0790644B8A915E0FEA7B3FCE', 1, NULL, NULL, 2, NULL, 0, 'DIVIDE evenly into 5 (5 oz.) waxed-coated paper cups or plastic cups or other freezer-safe containers. Cover with foil.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3263FB012D4ACA498CCB2BC72512C70D', X'95B4695B0790644B8A915E0FEA7B3FCE', X'95B4695B0790644B8A915E0FEA7B3FCE', 2, NULL, NULL, 2, NULL, 0, 'FREEZE 6 hours or until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'78E28528E1B04B438566AA4F6B41FF9E', X'6B30FB8ABF41D94E8BB9AE297FAC9ABA', X'6B30FB8ABF41D94E8BB9AE297FAC9ABA', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on one slice of bread. Spread jam on remaining slice. Press together to form sandwich.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE04815749FBC74CB0972DD8A862A8B4', X'5FF1CFB69A18704FB7BF9181BE88A311', X'5FF1CFB69A18704FB7BF9181BE88A311', 0, NULL, NULL, 2, NULL, 0, 'COMBINE milk chocolate and heavy cream in small microwavable bowl. Microwave 15 seconds; stir until smooth. Set aside 1 teaspoon of mixture. Evenly spread remaining chocolate on flat side of 3 connected lady fingers. Sprinkle 1 tablespoon and 1 teaspoon of pretzels on chocolate and lightly press in. Place in refrigerator to set.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'206EDC10F1D8024AACC068E69411063F', X'5FF1CFB69A18704FB7BF9181BE88A311', X'5FF1CFB69A18704FB7BF9181BE88A311', 1, NULL, NULL, 2, NULL, 0, 'MIX peanut butter, mascarpone cheese and confectioners sugar in small bowl until blended and smooth. Spread over chocolate/pretzels. Spread dulce de leche on flat side of remaining 3 connected lady fingers. Place on top of peanut butter mixture with rounded side of lady fingers facing up.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'48FF0787C7A43D45A7B90F3C57ED765B', X'5FF1CFB69A18704FB7BF9181BE88A311', X'5FF1CFB69A18704FB7BF9181BE88A311', 2, NULL, NULL, 2, NULL, 0, 'HEAT 1 tablespoon of chocolate mixture that was set aside, if needed, and drizzle over top of lady fingers. Sprinkle with 1 teaspoon crushed pretzels. Store in refrigerator.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9FF644A15B77C942AA539CD290D30C11', X'1120949486F65544849A3D983467A533', X'1120949486F65544849A3D983467A533', 0, NULL, NULL, 2, NULL, 0, 'HEAT small nonstick skillet over medium-low heat.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DCCA33E77ECE93479FFD48659EAE194E', X'1120949486F65544849A3D983467A533', X'1120949486F65544849A3D983467A533', 1, NULL, NULL, 2, NULL, 0, 'SPREAD butter on one side of each slice of bread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F4A2B0BA3EAE744EB83901779535779E', X'1120949486F65544849A3D983467A533', X'1120949486F65544849A3D983467A533', 2, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on unbuttered side of 1 slice; spread jam on unbuttered side of other slice. Join bread slices together to make a sandwich.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E822B6151500784B99C4B23BC48A33E3', X'1120949486F65544849A3D983467A533', X'1120949486F65544849A3D983467A533', 3, NULL, NULL, 2, NULL, 0, 'PLACE in skillet. Cook 1 to 2 minutes per side or until golden brown.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DEC324D47D0D62478568625A6110442C', X'1120949486F65544849A3D983467A533', X'1120949486F65544849A3D983467A533', 4, NULL, NULL, 2, NULL, 0, 'For added variety, add crisp cooked bacon, sliced banana or sliced strawberries to the PB&J sandwich.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'51B356DF846B1248978F540EE2BEF18E', X'FCE0E1E8FE418E4090ABDBD2AB50BB59', X'FCE0E1E8FE418E4090ABDBD2AB50BB59', 0, NULL, NULL, 2, NULL, 0, 'SPREAD a thin layer of peanut butter over top surface of each sandwich wrap.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F9C6252BFCBAC942AF8EFD3582955D6A', X'FCE0E1E8FE418E4090ABDBD2AB50BB59', X'FCE0E1E8FE418E4090ABDBD2AB50BB59', 1, NULL, NULL, 2, NULL, 0, 'WHISK oil, vinegar and sugar in medium bowl until blended. Season with salt and pepper. Add salad mix and green onion; toss to coat. Divide evenly over peanut butter layer. Top with apple slices and cooked bacon.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA4393DFC4DC2646B0FAA095C952BA84', X'FCE0E1E8FE418E4090ABDBD2AB50BB59', X'FCE0E1E8FE418E4090ABDBD2AB50BB59', 2, NULL, NULL, 2, NULL, 0, 'ROLL up or fold to enclose filling. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3EAAA63035A7DA46B8E702F2FF9F0A6E', X'68F7045A1F71EA488E10F3642650A067', X'68F7045A1F71EA488E10F3642650A067', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Spread 1 tablespoon peanut butter over each toasted English muffin half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1B48425C5726164E8FA64809735745C5', X'68F7045A1F71EA488E10F3642650A067', X'68F7045A1F71EA488E10F3642650A067', 1, NULL, NULL, 2, NULL, 0, 'TOP 4 muffin halves each with 1 slice cheese, several apple slices and 2 bacon strips. Sprinkle with cinnamon, if desired. Top with remaining muffin halves to form sandwiches. Place on baking sheet. Bake 3 to 5 minutes or until cheese begins to melt.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'360C361FBFEF3542BB571526D3FA8224', X'690DC3E2BE18EE43816746BA9076EF09', X'690DC3E2BE18EE43816746BA9076EF09', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter evenly on inside surface of hot dog roll.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B20BC4B321061C488800D26B59326F2A', X'690DC3E2BE18EE43816746BA9076EF09', X'690DC3E2BE18EE43816746BA9076EF09', 1, NULL, NULL, 2, NULL, 0, 'PLACE banana in roll. Top with desired amount of fruit spread. Sprinkle with nuts or raisins.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80DE43286237934A91A8FB03B4B49D2C', X'A348581E68059E449D3176F7E577CB24', X'A348581E68059E449D3176F7E577CB24', 0, NULL, NULL, 2, NULL, 0, 'CUT 10 shapes from bread using 2-inch star-shaped cookie cutter. Spread peanut butter on half the stars and jam on remaining stars. Press together to make five small sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4B74B3880B50784D83D0FF9AE2E3CFAC', X'A348581E68059E449D3176F7E577CB24', X'A348581E68059E449D3176F7E577CB24', 1, NULL, NULL, 2, NULL, 0, 'THREAD sandwiches, strawberry halves and grapes alternately onto skewer. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2EE4A5EF90FCA84FB4CEAB4BDE550AAC', X'FD27ECD596EFF64F838DBB5400B9EBA2', X'FD27ECD596EFF64F838DBB5400B9EBA2', 0, NULL, NULL, 2, NULL, 0, 'TRIM edges from bread slices using a serrated knife. Make 1 star in center of 3 bread slices by cutting with 1-inch star-shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'311234DD82065A47B6728557440F86ED', X'FD27ECD596EFF64F838DBB5400B9EBA2', X'FD27ECD596EFF64F838DBB5400B9EBA2', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter evenly onto 3 whole bread slices. Spoon jam evenly onto center of peanut butter, spreading gently into a 2-inch circle. Top with cut-out bread slices to make sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0640B91E9B9C5B47A538E1253E23E43D', X'24E41127629E9947AD1B64D43E48D2BC', X'24E41127629E9947AD1B64D43E48D2BC', 0, NULL, NULL, 2, NULL, 0, 'SPLIT and toast English muffin. Spread peanut spread on both sides of muffin. Spread or squeeze fruit spread over peanut butter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D6F038D0AE567543B72D4BA0D226B01C', X'24E41127629E9947AD1B64D43E48D2BC', X'24E41127629E9947AD1B64D43E48D2BC', 1, NULL, NULL, 2, NULL, 0, 'TOP with banana slices. Drizzle with warmed fudge topping to taste. Sprinkle with coconut, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7107EE6F8DE93B4B941EC0F00695AF33', X'39973DD5602F674AAEC1AE7E793D50B2', X'39973DD5602F674AAEC1AE7E793D50B2', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on 1 side of 1 slice of bread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B647D2D91F2BA741B412DCE67A56C015', X'39973DD5602F674AAEC1AE7E793D50B2', X'39973DD5602F674AAEC1AE7E793D50B2', 1, NULL, NULL, 2, NULL, 0, 'PLACE pineapple on top of peanut butter layer. Spoon marshmallow creme over pineapple. Top with remaining slice of bread to form a sandwich. Cut with a serrated knife on a diagonal.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6EA6F2C343C4A84FBFE01FBA9609C9B1', X'15C58CCB6B5B9040B9F26CD138149FE6', X'15C58CCB6B5B9040B9F26CD138149FE6', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on top of tortilla to within 1/2 inch of edge.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8790ED654A7E6F468FB1BE650DF819AB', X'15C58CCB6B5B9040B9F26CD138149FE6', X'15C58CCB6B5B9040B9F26CD138149FE6', 1, NULL, NULL, 2, NULL, 0, 'ARRANGE slaw mix, cilantro and chicken lengthwise down center of tortilla. Drizzle evenly with chili sauce. Roll up tightly. Cut in half before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4DECF5D3A962BC40966F01917D69F035', X'6770601AFB1CBD4C9FBBFE971529F510', X'6770601AFB1CBD4C9FBBFE971529F510', 0, NULL, NULL, 2, NULL, 0, 'HEAT frying pan or griddle to medium. Roll each slice of bread flat with rolling pin. Spread 1 tablespoon of peanut butter and 1 teaspoon apricot preserves on each slice of flattened bread. Take three apple slices and place them in single layer lengthwise along top of bread. Repeat with second slice. Sprinkle both slices with 1/2 teaspoon cinnamon sugar.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AA796B824658D4428C3BA3A67A664D68', X'6770601AFB1CBD4C9FBBFE971529F510', X'6770601AFB1CBD4C9FBBFE971529F510', 1, NULL, NULL, 2, NULL, 0, 'ROLL each slice of bread from apple end, gently but tightly. Lay seam side down. Add butter to griddle or pan to coat it. Mix egg, milk and vanilla in shallow bowl. Dip each roll in egg wash and place seam side down on pan. Lightly brown sandwich. Carefully turn until all sides are browned.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C1CAF83197BB164FB2C560038CBB5D6A', X'6770601AFB1CBD4C9FBBFE971529F510', X'6770601AFB1CBD4C9FBBFE971529F510', 2, NULL, NULL, 2, NULL, 0, 'REMOVE from pan and sprinkle or roll with remaining cinnamon sugar to coat. Combine remaining 2 tablespoons of peanut butter and 2 tablespoons maple syrup in small heatproof dish. Heat for 30 seconds in microwave and mix until combined. Serve as dipping sauce for roll-up.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'95CBFA8584C52C4BA007FBEE9A7D9427', X'89820DEAEC23F74CA9D69820A49F085D', X'89820DEAEC23F74CA9D69820A49F085D', 0, NULL, NULL, 2, NULL, 0, 'CUT pita bread in half crosswise. Separate cut edge gently to create two pita bread pockets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F837DAD094B98141A69027A0479B3CDE', X'89820DEAEC23F74CA9D69820A49F085D', X'89820DEAEC23F74CA9D69820A49F085D', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on top, inside surface of pockets. Spread jam on bottom, inside surface of pockets. Fill pockets with banana and strawberries.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C42B796BF45A7B479FE0B3E54A0486FB', X'B2D9E57B23BDF5469B2B4487C0DDE35D', X'B2D9E57B23BDF5469B2B4487C0DDE35D', 0, NULL, NULL, 2, NULL, 0, 'HEAT oil in 4-quart stock pot. Saute garlic and gingerroot in oil. Whisk in water, peanut butter, soy sauce, brown sugar, cornstarch, sesame oil, red pepper and seasoning packets from noodle packages until blended. Bring to a boil. Reduce to simmer.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4E93557FD435744A8FDF227A0344628', X'B2D9E57B23BDF5469B2B4487C0DDE35D', X'B2D9E57B23BDF5469B2B4487C0DDE35D', 1, NULL, NULL, 2, NULL, 0, 'ADD noodles, chicken and stir-fry vegetables. Stir to combine. Return to a boil. Reduce heat. Cover and simmer 5 to 8 minutes or until vegetables are tender. Top with green onions before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9F64095CF8BE304CA1CF43F26E3DCA5E', X'B2D9E57B23BDF5469B2B4487C0DDE35D', X'B2D9E57B23BDF5469B2B4487C0DDE35D', 2, NULL, NULL, 2, NULL, 0, 'Broccoli stir fry contains broccoli, carrots, onions, red pepper, water chestnuts, mushrooms and celery.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D88D60210DF0664DB08371B906BC778F', X'971F5514FCA1624C8546077D454EBB91', X'971F5514FCA1624C8546077D454EBB91', 0, NULL, NULL, 2, NULL, 0, 'SPREAD cashew butter down center of lettuce leaf. Top with cucumber, carrots, chili sauce and cilantro. Overlap edges of lettuce leaf to create a wrap.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF92217878442849A95805B05EE516D1', X'54B0E2821F18904197648F4139FEC7F7', X'54B0E2821F18904197648F4139FEC7F7', 0, NULL, NULL, 2, NULL, 0, 'PRESS Saute button to heat programmable electric pressure cooker. Add oil; heat 2 to 3 minutes. Cook chicken thighs in hot oil, a few at a time, until browned on both sides. Remove from pot; set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5310FAA4A2DAE949BE74F6945FD5FA1F', X'54B0E2821F18904197648F4139FEC7F7', X'54B0E2821F18904197648F4139FEC7F7', 1, NULL, NULL, 2, NULL, 0, 'ADD chicken broth, peanut butter, soy sauce, Sriracha sauce, honey, ginger and garlic to pressure cooker. Stir until blended, scraping up any browned bits. Return chicken to pot, turning to coat.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C7C1A0EFF118674F97923DBED126B673', X'54B0E2821F18904197648F4139FEC7F7', X'54B0E2821F18904197648F4139FEC7F7', 2, NULL, NULL, 2, NULL, 0, 'LOCK lid on pressure cooker. Place exhaust valve in the Sealing position. Press Manual button and set timer for 12 minutes. When cooking is complete, turn off pot and use Quick Pressure Release method.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E06D46F7ECE35045AED8F069149BC74F', X'54B0E2821F18904197648F4139FEC7F7', X'54B0E2821F18904197648F4139FEC7F7', 3, NULL, NULL, 2, NULL, 0, 'REMOVE lid. Remove chicken to serving platter, leaving sauce in pot. Stir in cilantro and lime juice. Serve sauce over chicken. Sprinkle with peanuts and green onions. Serve with lime wedges.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'51577D778CEA2B4E84710E3676DB40BD', X'E477AA15D9FA594994680BE017484CDA', X'E477AA15D9FA594994680BE017484CDA', 0, NULL, NULL, 2, NULL, 0, 'COOK pasta in salted water, according to package directions; drain well. Place in large bowl and toss with 1 tablespoon canola oil. Set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4ADB92BA83C65745B95953D76E395F62', X'E477AA15D9FA594994680BE017484CDA', X'E477AA15D9FA594994680BE017484CDA', 1, NULL, NULL, 2, NULL, 0, 'WHISK peanut butter, orange juice, lemon juice, soy sauce and sugar in medium bowl until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6254DF160323344BA2F3186469A77C43', X'E477AA15D9FA594994680BE017484CDA', X'E477AA15D9FA594994680BE017484CDA', 2, NULL, NULL, 2, NULL, 0, 'HEAT 1/4 cup oil in a large, deep skillet over medium high heat. Add onion, peppers, garlic, jalapeno pepper and broccoli, cooking and stirring about 3-5 minutes, until broccoli is crisp-tender. Add pasta and citrus sauce, tossing until well combined. Serve at room temperature.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'741703A75C34984B8A3718FB586E1A64', X'031D4643A8454C4B9F0B4069686AD03E', X'031D4643A8454C4B9F0B4069686AD03E', 0, NULL, NULL, 2, NULL, 0, 'HEAT butter in a 4-quart saucepot. Sauté carrot, onion and celery until tender. Remove from heat and blend in flour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FD0E37FC4D8859448CC0E8DC36D7E13A', X'031D4643A8454C4B9F0B4069686AD03E', X'031D4643A8454C4B9F0B4069686AD03E', 1, NULL, NULL, 2, NULL, 0, 'ADD curry powder, salt, chicken broth, Worcestershire sauce, peanut butter and evaporated milk. Puree mixture in batches using a blender or food processor.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E0966D243F964F4DAA887E7FEC772960', X'031D4643A8454C4B9F0B4069686AD03E', X'031D4643A8454C4B9F0B4069686AD03E', 2, NULL, NULL, 2, NULL, 0, 'RETURN soup to pot; bring to boil. Simmer until thickened. Stir in fresh parsley. Season to taste with salt and pepper. Garnish with additional parsley.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6CC54AD2DE0EB4468A20A8D9E31A64F7', X'E481FF7FF06AC34BB7AE84C72479F0AD', X'E481FF7FF06AC34BB7AE84C72479F0AD', 0, NULL, NULL, 2, NULL, 0, 'PLACE tortilla chips on serving plate. Top with apples.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9317E79CB046E84F93F47752CA29B991', X'E481FF7FF06AC34BB7AE84C72479F0AD', X'E481FF7FF06AC34BB7AE84C72479F0AD', 1, NULL, NULL, 2, NULL, 0, 'STIR peanut butter, caramel topping and water in small bowl until smooth. Drizzle evenly over nachos. Top with small spoonfuls of jam. Sprinkle with cheese and peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'438BA0F8FB0DE945B309A77E8F9182A2', X'3CF60472EB3C57448853BBC83754FA22', X'3CF60472EB3C57448853BBC83754FA22', 0, NULL, NULL, 2, NULL, 0, 'COAT inside of waffle cone with peanut butter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5271D47D431A7C4FBFDF37433E780DF1', X'3CF60472EB3C57448853BBC83754FA22', X'3CF60472EB3C57448853BBC83754FA22', 1, NULL, NULL, 2, NULL, 0, 'STIR fruit and jam in small bowl to coat. Spoon into cone. Top with yogurt. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3B80B6E5706BEF4F81CC0B1A161EDF7B', X'13610AF967082F4FB60A4602EFACD10D', X'13610AF967082F4FB60A4602EFACD10D', 0, NULL, NULL, 2, NULL, 0, 'REMOVE crusts from bread. With a rolling pin or large soup can, completely flatten bread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ED9EAAAC82E0104AB85AA75CF6E41C7F', X'13610AF967082F4FB60A4602EFACD10D', X'13610AF967082F4FB60A4602EFACD10D', 1, NULL, NULL, 2, NULL, 0, 'SPREAD 1 tablespoon peanut butter and 1 tablespoon jelly on each slice of bread');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D27D843A32B99E4496AC5528E9C87B75', X'13610AF967082F4FB60A4602EFACD10D', X'13610AF967082F4FB60A4602EFACD10D', 2, NULL, NULL, 2, NULL, 0, 'ROLL each slice into a tight spiral. Cut each spiral into 4 pieces.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF474BA860EED946AF7904408FDC8182', X'05BE4A0BDCB6CA4DA57B5BCB28B9F3D7', X'05BE4A0BDCB6CA4DA57B5BCB28B9F3D7', 0, NULL, NULL, 2, NULL, 0, 'SPREAD mayonnaise on one side of one slice of toasted bread. Spread peanut butter on one side of remaining slice of bread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E09194509366B84CAD2AE3D8B0D6E859', X'05BE4A0BDCB6CA4DA57B5BCB28B9F3D7', X'05BE4A0BDCB6CA4DA57B5BCB28B9F3D7', 1, NULL, NULL, 2, NULL, 0, 'TOP mayonnaise with lettuce, tomato, bacon and remaining slice of bread, peanut butter side down. Cut in half. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CC21A45C4B6B14CB8412CD577ADD9A6', X'2C6C786A914ABB43A92EFA0F92ED1ED5', X'2C6C786A914ABB43A92EFA0F92ED1ED5', 0, NULL, NULL, 2, NULL, 0, 'LINE 13 x 9-inch baking pan with foil, extending over edges of pan. Coat with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BED780BBC4105D4EBD9E98D11DF080AC', X'2C6C786A914ABB43A92EFA0F92ED1ED5', X'2C6C786A914ABB43A92EFA0F92ED1ED5', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE butter and marshmallows in large microwave-safe bowl on HIGH 1 1/2 minutes. Stir in peanut butter until smooth. Add cereal; stir until evenly coated. Press into prepared pan. Cool completely. Cut out stars using 2-inch star cookie cutter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'84310E8D7E42D54B93BCB6D34AAE94FB', X'2C6C786A914ABB43A92EFA0F92ED1ED5', X'2C6C786A914ABB43A92EFA0F92ED1ED5', 2, NULL, NULL, 2, NULL, 0, 'SPREAD small dollop of fruit spread on center of each star treat. Top with red raspberry, mini marshmallow and blueberry. Insert decorative skewer through top center. Repeat with remaining treats. Serve with whipped peanut butter for dipping if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E35F33277BF6243B8E07FB5837A409F', X'0F3E4458330F3044BF6D40F0F70AB8E6', X'0F3E4458330F3044BF6D40F0F70AB8E6', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 275°F. Coat 15 x 10-inch baking pan with no-stick cooking spray. Place popcorn in extra-large bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4A6851DBA10D114A8BAF1159139C322A', X'0F3E4458330F3044BF6D40F0F70AB8E6', X'0F3E4458330F3044BF6D40F0F70AB8E6', 1, NULL, NULL, 2, NULL, 0, 'COMBINE corn syrup and sugar in medium microwave-safe bowl. Microwave on HIGH 2 minutes or until mixture boils. Remove from heat. Stir in butter, almond butter, vanilla, baking soda and salt until smooth. Pour over popcorn. Stir until evenly coated. Spread on prepared baking pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB605B04A463C84492B047C1114E1E1E', X'0F3E4458330F3044BF6D40F0F70AB8E6', X'0F3E4458330F3044BF6D40F0F70AB8E6', 2, NULL, NULL, 2, NULL, 0, 'BAKE 35 to 40 minutes, stirring every 10 minutes. Remove from oven. Cool completely. Break into pieces.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AFC7F0DCC2958E48B24EECED146E93EF', X'43411E796F7B7C4282B79463736368AD', X'43411E796F7B7C4282B79463736368AD', 0, NULL, NULL, 2, NULL, 0, 'MIX almond butter, honey and vanilla in large bowl. Stir in oats, wheat germ, almonds, cranberries and cinnamon until evenly moistened. Shape into 1-inch balls. Chill 10 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0B24040B6754D84095726515011C1C30', X'2887FD66C1498C4F959C4514BF02096C', X'2887FD66C1498C4F959C4514BF02096C', 0, NULL, NULL, 2, NULL, 0, 'COMBINE brown sugar, water and syrup in small saucepan. Bring to a boil over medium heat. Reduce heat to low and simmer 5 minutes or until sugar is completely dissolved. Pour into medium bowl. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C580BD95C08384F89EFA67F26826C60', X'2887FD66C1498C4F959C4514BF02096C', X'2887FD66C1498C4F959C4514BF02096C', 1, NULL, NULL, 2, NULL, 0, 'ADD peanut butter, marshmallow creme and maple flavoring, if desired, to syrup mixture. Beat with electric mixer on medium speed until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1635BD37C5A2A74BBF04EA8D442A954F', X'2887FD66C1498C4F959C4514BF02096C', X'2887FD66C1498C4F959C4514BF02096C', 2, NULL, NULL, 2, NULL, 0, 'SPOON spread into serving dish. Sprinkle with peanuts. Spread on apple slices or sliced bread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6BBD0B38DB820542A7FA29B16D1B0B17', X'0350A67C586C2B46BCAB2A7E355F851F', X'0350A67C586C2B46BCAB2A7E355F851F', 0, NULL, NULL, 2, NULL, 0, 'STIR peanut butter, honey, soy sauce, vinegar, Sriracha, green onion and ginger in medium bowl until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7D4DDEBBF33D7142A0331D16D9C41408', X'0350A67C586C2B46BCAB2A7E355F851F', X'0350A67C586C2B46BCAB2A7E355F851F', 1, NULL, NULL, 2, NULL, 0, 'SERVE with fresh cut vegetables such as carrots, broccoli, celery, peppers, cauliflower, mushrooms and grape tomatoes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'47C0D3C84884C1408F4B8C9BC94056EB', X'43C92DAB518E0A4B8A2823EDC4046A64', X'43C92DAB518E0A4B8A2823EDC4046A64', 0, NULL, NULL, 2, NULL, 0, 'COMBINE cinnamon and granulated sugar. Set aside.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5133EAFB5A81244EB8AB97FCA62E7E03', X'43C92DAB518E0A4B8A2823EDC4046A64', X'43C92DAB518E0A4B8A2823EDC4046A64', 1, NULL, NULL, 2, NULL, 0, 'SPRAY one side of tortilla generously with butter cooking spray. Place spread side down in 10-inch skillet. Spread topside with peanut butter, top with banana slices, sprinkle with brown sugar and top with remaining tortilla. Spray top of tortilla generously with butter cooking spray and sprinkle with half of the cinnamon and sugar mixture.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'54D48D4FE3A2BB4985EA4FD1D3CB8142', X'43C92DAB518E0A4B8A2823EDC4046A64', X'43C92DAB518E0A4B8A2823EDC4046A64', 2, NULL, NULL, 2, NULL, 0, 'COVER skillet. Cook over medium-high heat until the bottom is golden brown. Turn tortilla with a wide spatula. Sprinkle top with remaining cinnamon and sugar. Continue cooking covered until bottom is golden brown. Remove to cutting board and slice into 8 wedges. Serve warm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'996C90C7CE68D147A22AFA5FE016753E', X'B4ED7519506AE34EA13EADA864825208', X'B4ED7519506AE34EA13EADA864825208', 0, NULL, NULL, 2, NULL, 0, 'COMBINE milk, banana, peanut butter, honey and ice cubes in blender container. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BB305BBB67E36C4EBC0B33A26B1496A6', X'B4ED7519506AE34EA13EADA864825208', X'B4ED7519506AE34EA13EADA864825208', 1, NULL, NULL, 2, NULL, 0, 'POUR into serving glasses. Add whipped cream and sprinkles, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E63739E8C1F84547BE97BF751ADD3229', X'BE2BA102100CEB4E8B02797920A043E0', X'BE2BA102100CEB4E8B02797920A043E0', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 325°F. Line 24 muffin cups with paper baking cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'999A007BB94EF74C93F7F7D6590C2C75', X'BE2BA102100CEB4E8B02797920A043E0', X'BE2BA102100CEB4E8B02797920A043E0', 1, NULL, NULL, 2, NULL, 0, 'BEAT eggs, bananas, oil, water and vanilla in large bowl with electric mixer on medium speed until blended. Add pancake mix, sugar, cocoa and salt. Beat until smooth. Fill baking cups 2/3 full.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC6CFBFBF6642B4892D43A05C0277288', X'BE2BA102100CEB4E8B02797920A043E0', X'BE2BA102100CEB4E8B02797920A043E0', 2, NULL, NULL, 2, NULL, 0, 'BAKE 18 to 20 minutes or until toothpick inserted in center comes out clean. Cool in pans 5 minutes. Remove from pans and cool completely on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C94B8A3F32A58546B355F87D184A79C9', X'BE2BA102100CEB4E8B02797920A043E0', X'BE2BA102100CEB4E8B02797920A043E0', 3, NULL, NULL, 2, NULL, 0, 'BEAT frosting, peanut butter and milk in medium bowl with electric mixer until blended. Spread on cooled cupcakes. Place coconut in small bowl. Dip frosted top of each cupcake in coconut to cover. Make a rounded indentation on the top of each cupcake, using the back of a small spoon, to form a nest. Arrange 5 to 6 jelly beans in each nest.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E9D23B18B8C884B9647080C5DD19967', X'B1B9AC0DD866174BB4E7FDAF16DD5A79', X'B1B9AC0DD866174BB4E7FDAF16DD5A79', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 300ºF. Coat 15 x 10-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'39E9D8B749B4B841B9E04C3C5E9F217E', X'B1B9AC0DD866174BB4E7FDAF16DD5A79', X'B1B9AC0DD866174BB4E7FDAF16DD5A79', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE cashew butter, honey and cinnamon in large microwave-safe bowl on HIGH for 30 seconds. Stir in vanilla. Add oats, raisins and cashews. Stir to coat. Spread on prepared baking pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3AF80873BE30404A817E161E0C8DC309', X'B1B9AC0DD866174BB4E7FDAF16DD5A79', X'B1B9AC0DD866174BB4E7FDAF16DD5A79', 2, NULL, NULL, 2, NULL, 0, 'BAKE 35 to 40 minutes, stirring occasionally. TURN OFF OVEN. Let dry 1 1/2 hours, stirring occasionally. Remove from oven to cool completely. Store in airtight container for up to 1 week.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6EB5D7F891FC7C438CD61240B00EB9AE', X'BD79E09D277CE44CAA5232CE619E1A49', X'BD79E09D277CE44CAA5232CE619E1A49', 0, NULL, NULL, 2, NULL, 0, 'For cookies, HEAT oven to 350°F. Beat sugar, brown sugar, peanut butter and peanut in large bowl with mixer on medium speed until creamy. Beat in eggs, milk and vanilla. Mix well. Stir flour, baking powder and baking soda in medium bowl until blended. Stir in oats and coconut. Add to peanut butter mixture, beating on low speed until well blended. Cover and chill at least 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0C756C30E046DD478ADB8288E847A6EE', X'BD79E09D277CE44CAA5232CE619E1A49', X'BD79E09D277CE44CAA5232CE619E1A49', 1, NULL, NULL, 2, NULL, 0, 'DROP by level tablespoonful 2 inches apart onto baking sheets to make 60 mounds of dough. Bake 10 to 12 minutes or until edges are lightly browned. Cool on baking sheet 2 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'438F21F439FC444BBD07847846047D5D', X'BD79E09D277CE44CAA5232CE619E1A49', X'BD79E09D277CE44CAA5232CE619E1A49', 2, NULL, NULL, 2, NULL, 0, 'For Filling, BEAT powdered sugar, peanut butter, milk and vanilla in medium bowl with mixer on low speed until moistened. Beat on high speed 2 minutes or until creamy, adding additional milk if necessary until desired consistency.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8DCD08F9F795E7499863B3C48C46C4D5', X'BD79E09D277CE44CAA5232CE619E1A49', X'BD79E09D277CE44CAA5232CE619E1A49', 3, NULL, NULL, 2, NULL, 0, 'PLACE 30 cookies flat side up. Pipe or spread each with filling. Top with remaining cookies, flat side down, to form sandwich cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4468A58B36948D4CA3665297EE24D9A9', X'9EBAD7790A27464CBAEE1B41DD32C0EA', X'9EBAD7790A27464CBAEE1B41DD32C0EA', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'583C24375149E54095D3F5D734DC6326', X'9EBAD7790A27464CBAEE1B41DD32C0EA', X'9EBAD7790A27464CBAEE1B41DD32C0EA', 1, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter and brown sugar in large bowl with mixer on medium speed until creamy. Beat in egg and water until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D11401BE31DB904A9FAC258326621B67', X'9EBAD7790A27464CBAEE1B41DD32C0EA', X'9EBAD7790A27464CBAEE1B41DD32C0EA', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, baking soda and salt in small bowl until blended. Beat into peanut butter mixture until evenly moistened. Stir in chocolate chips.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63F673109E525A4EA2FCEC533CA9EE33', X'9EBAD7790A27464CBAEE1B41DD32C0EA', X'9EBAD7790A27464CBAEE1B41DD32C0EA', 3, NULL, NULL, 2, NULL, 0, 'DROP by tablespoonfuls onto baking sheet. Press with fork to flatten slightly in criss-cross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'44FB7A39CEA13E4586F55146C5BFEC8A', X'9EBAD7790A27464CBAEE1B41DD32C0EA', X'9EBAD7790A27464CBAEE1B41DD32C0EA', 4, NULL, NULL, 2, NULL, 0, 'BAKE 8 to 10 minutes or until golden brown. Cool 1 minute on baking sheet. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'56AFAB87087B7E40A3D5693C37D7F7F4', X'9C87FEFCD8BD8A40B741EE066852855D', X'9C87FEFCD8BD8A40B741EE066852855D', 0, NULL, NULL, 2, NULL, 0, 'FOLD cookies into peanut butter. Serve with fruit, graham crackers or pretzels.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DA034B92B2A8DB4EBCF4A760BB2292E8', X'086451CF09010C49B2F3A5BAC1CDA859', X'086451CF09010C49B2F3A5BAC1CDA859', 0, NULL, NULL, 2, NULL, 0, 'MIX chocolate spread, cream cheese, whipped topping and vanilla in small bowl until smooth. Place in decorating bag fitted with a medium to large star tip.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'05F05B4ED3F26244B7587ADA27B6B69F', X'086451CF09010C49B2F3A5BAC1CDA859', X'086451CF09010C49B2F3A5BAC1CDA859', 1, NULL, NULL, 2, NULL, 0, 'PIPE about 2 tablespoons filling into each end of cannoli.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0A41FF237A62CD47A351A511A3E487D7', X'086451CF09010C49B2F3A5BAC1CDA859', X'086451CF09010C49B2F3A5BAC1CDA859', 2, NULL, NULL, 2, NULL, 0, 'This recipe will fill 6 (5-inch) or 12 (3 1/2-inch) cannoli shells. For the smaller shells, pipe about 1 tablespoon filling into each end.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'479EAFFCA7BC634F8F48A24A762643A0', X'53BD5287E55B8B4C9C30FCCED6C8D8D8', X'53BD5287E55B8B4C9C30FCCED6C8D8D8', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Line 9-inch round cake pan with foil, extending foil over edge of pan. Coat with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'92044005372E0F40A52B231A5B7F5B10', X'53BD5287E55B8B4C9C30FCCED6C8D8D8', X'53BD5287E55B8B4C9C30FCCED6C8D8D8', 1, NULL, NULL, 2, NULL, 0, 'PREPARE brownie mix according to package directions using oil, 3 tablespoons water and 1 egg. Spread into prepared pan. Prepare filling mix according to package directions using 2 tablespoons water and 1 egg. Stir in 1/4 cup chocolate mint spread. Spoon on top of brownies. Swirl with tip of knife. Bake 30 to 35 minutes or until set in center. Cool completely on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B16C5054ECDED44FB236B0C4C5141A71', X'53BD5287E55B8B4C9C30FCCED6C8D8D8', X'53BD5287E55B8B4C9C30FCCED6C8D8D8', 2, NULL, NULL, 2, NULL, 0, 'REMOVE from pan by lifting edges of foil. Remove foil. Place on serving plate. Place 1/4 cup chocolate spread into decorating bag fitted with a medium star tip. Make 12 rosettes around outside edge of brownie cake. Chill 30 minutes. Cut into wedges.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7BD6921206BA4F488C6D0F176A6F2892', X'3CE2D02129BCA54CAE5B3A92277B4214', X'3CE2D02129BCA54CAE5B3A92277B4214', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Beat peanut butter, 1/4 cup shortening and sugar in large bowl with electric mixer on medium low speed until mixture comes together. Form heaping tablespoonfuls of mixture into egg shape. Place on prepared baking sheet. Repeat with remaining mixture. Freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4250AA2BEF7DAC4C959384831563D537', X'3CE2D02129BCA54CAE5B3A92277B4214', X'3CE2D02129BCA54CAE5B3A92277B4214', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate and remaining shortening in microwave-safe bowl on HIGH 30 seconds. Stir. Microwave an addtional 30 seconds or until melted and smooth when stirred. Dip one end of lollipop stick into chocolate mixture. Insert stick into bottom center of each egg. Repeat with remaining eggs.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'12204D55C0C8A144854FDA1F55CD5FF5', X'3CE2D02129BCA54CAE5B3A92277B4214', X'3CE2D02129BCA54CAE5B3A92277B4214', 2, NULL, NULL, 2, NULL, 0, 'DIP each egg into chocolate to coat completely. Return to prepared baking sheet. Sprinkle immediately with decorator sprinkles or coconut, if desired. Chill 30 minutes or until chocolate is set. Store in air tight container in refrigerator.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'48385B82D347C44395D9BB50CF4D68FE', X'A2B48DA3DE867443B06565CC21D9DA9C', X'A2B48DA3DE867443B06565CC21D9DA9C', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat 13 x 9-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CF3B8DB74443B54FA569E3083117A8B1', X'A2B48DA3DE867443B06565CC21D9DA9C', X'A2B48DA3DE867443B06565CC21D9DA9C', 1, NULL, NULL, 2, NULL, 0, 'PREPARE brownie mix according to package directions using water, oil and eggs. Spread in prepared pan. Bake 28 to 31 minutes. Cool completely on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A829336313CC4B4491C64C293738DC2F', X'A2B48DA3DE867443B06565CC21D9DA9C', X'A2B48DA3DE867443B06565CC21D9DA9C', 2, NULL, NULL, 2, NULL, 0, 'FROST brownies with chocolate spread. Garnish with decorator sprinkles, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CCC48711A48A1846B515A9B2A028C0EE', X'EBE348452A01DB4D8FBDC7AD4979388E', X'EBE348452A01DB4D8FBDC7AD4979388E', 0, NULL, NULL, 2, NULL, 0, 'LINE 8-inch square pan with foil, extending foil over edges of pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E818CC881C8D414293CFA5B24E332932', X'EBE348452A01DB4D8FBDC7AD4979388E', X'EBE348452A01DB4D8FBDC7AD4979388E', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE sweetened condensed milk and chocolate chips in medium microwave-safe bowl on HIGH 1 minute. Stir until smooth. If necessary, microwave at additional 15-second intervals until completely melted and smooth when stirred.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B76AC19D4E22143BCEB490FACE6CE8C', X'EBE348452A01DB4D8FBDC7AD4979388E', X'EBE348452A01DB4D8FBDC7AD4979388E', 2, NULL, NULL, 2, NULL, 0, 'ADD peanut butter and vanilla. Beat with hand mixer on medium speed until well blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'28F441CB02B5C24D8C780540157224E9', X'EBE348452A01DB4D8FBDC7AD4979388E', X'EBE348452A01DB4D8FBDC7AD4979388E', 3, NULL, NULL, 2, NULL, 0, 'SPREAD in prepared pan. Chill at least 1 hour. Remove fudge from pan by lifting foil. Remove foil. Cut into pieces before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1C276A232CBC7349B3714B52AC75A06B', X'F9A39636BA346A4D8AB21817E84BFFBD', X'F9A39636BA346A4D8AB21817E84BFFBD', 0, NULL, NULL, 2, NULL, 0, 'COMBINE frozen yogurt, soy milk, protein powder and peanut butter in blender container.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'716F6C0936E3754896B0A369E653643B', X'F9A39636BA346A4D8AB21817E84BFFBD', X'F9A39636BA346A4D8AB21817E84BFFBD', 1, NULL, NULL, 2, NULL, 0, 'COVER and process until smooth. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'147967218048654DBAEC8E1EB1B652BE', X'A77F903204F3DE44A26653E789D0A3A6', X'A77F903204F3DE44A26653E789D0A3A6', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Microwave chocolate in medium microwave-safe bowl on HIGH 1 1/2 minutes. Stir until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5D5CD3E6AB8DD843BCFC33DC10675F8E', X'A77F903204F3DE44A26653E789D0A3A6', X'A77F903204F3DE44A26653E789D0A3A6', 1, NULL, NULL, 2, NULL, 0, 'DIP each cracker halfway into chocolate. Sprinkle immediately with toffee bits, if desired. Place on prepared baking sheet. Chill 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'65D11771219265439FC3398887272F1A', X'A77F903204F3DE44A26653E789D0A3A6', X'A77F903204F3DE44A26653E789D0A3A6', 2, NULL, NULL, 2, NULL, 0, 'SPREAD 2 teaspoons peanut butter on inside of each cracker. Press 2 crackers together. Repeat with remaining crackers to make 24 snackwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD8BBBDB66553E41B98531D7A9DB8905', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Line baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DC3C6834D413A14C94ECEB8F768E32FF', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', 1, NULL, NULL, 2, NULL, 0, 'BEAT cake mix, water, oil and eggs in large bowl with mixer on medium speed for 2 minutes, scraping bowl occasionally.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8C57A1B97A21574DA7ACB1BA1AD88401', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', 2, NULL, NULL, 2, NULL, 0, 'DROP by 2 tablespoonsful batter onto prepared baking sheets, placing 3 inches apart. Using the back of a spoon, spread batter into 2-inch diameter circles. Bake 8 to 9 minutes or until centers are set. Cool 1 minute. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'68D2C0AAE8FE3845941181577A6D18C7', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', 3, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter and marshmallow creme in large bowl with mixer on medium speed until blended. Add powdered sugar and milk. Beat on low speed until blended. Beat on high speed 1 minute.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'95867AA2EDA5C44DBCF36A7CFFDB91E4', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', X'8CBBB3EB9AB0F6458772D1E1E7182DAD', 4, NULL, NULL, 2, NULL, 0, 'PLACE half of pies flat side up. Pipe or spread each with about 2 tablespoons filling. Top with remaining pies, rounded side up, to form whoopie pies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'60B128B82671C747B6D05D56F1009276', X'70A6626CC2EF15408C9223D8AD86DF5C', X'70A6626CC2EF15408C9223D8AD86DF5C', 0, NULL, NULL, 2, NULL, 0, 'HEAT French toast in toaster according to package directions until golden brown. Cut into sticks. Place on serving plate.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B731463A12006A4995A20D2CFF7428BA', X'70A6626CC2EF15408C9223D8AD86DF5C', X'70A6626CC2EF15408C9223D8AD86DF5C', 1, NULL, NULL, 2, NULL, 0, 'SPRINKLE with cinnamon sugar. Spread with peanut butter and jam.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A357AC1C06C4C74EB361EF6E5F1F3EF9', X'50B09595FA05BB4C9225C41F4B21FFC1', X'50B09595FA05BB4C9225C41F4B21FFC1', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Spray 11 x 17 x 1 1/2-inch baking pan with no-stick cooking pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F37B04C53B07F48954D4A26949AD498', X'50B09595FA05BB4C9225C41F4B21FFC1', X'50B09595FA05BB4C9225C41F4B21FFC1', 1, NULL, NULL, 2, NULL, 0, 'BEAT butter and sugar in medium bowl with an electric mixer on medium speed until fluffy. Add in peanut butter, eggs and vanilla one at a time until incorporated. In a separate bowl stir together flour, baking powder and salt. Add to creamed mixture. Beat just until mixed. Stir in coconut. Spread into prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'17C7655B9731774DA3314F96E558982E', X'50B09595FA05BB4C9225C41F4B21FFC1', X'50B09595FA05BB4C9225C41F4B21FFC1', 2, NULL, NULL, 2, NULL, 0, 'BAKE 25 to 30 minutes. Cool. Cut into 2 dozen bars.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'772ED0E8FCD75F4C831A3D0C08BA8525', X'50B09595FA05BB4C9225C41F4B21FFC1', X'50B09595FA05BB4C9225C41F4B21FFC1', 3, NULL, NULL, 2, NULL, 0, 'MIX powdered sugar, salt, vanilla and enough light cream to make a frosting of drizzling consistency. Drizzle over cooled bars.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'89C20B3661A5BA44AF83605DD539FB77', X'C87C705BE81FE849AD4F92C2BFF9EB6E', X'C87C705BE81FE849AD4F92C2BFF9EB6E', 0, NULL, NULL, 2, NULL, 0, 'MIX peanut butter, orange marmalade, whipped topping and cinnamon in large bowl until combined. Place into serving bowl. Arrange pretzels, cookies and fresh fruit on serving platter. Serve with dip.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'81F2BF86489D884891258F91CB8FBA84', X'74E204BD3599AE49A13FE1401AE66F24', X'74E204BD3599AE49A13FE1401AE66F24', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'31E4D99416C5E34BA1EE6DF81139C28C', X'74E204BD3599AE49A13FE1401AE66F24', X'74E204BD3599AE49A13FE1401AE66F24', 1, NULL, NULL, 2, NULL, 0, 'For Crust: COMBINE graham cracker crumbs, peanut butter, sugar and butter in medium bowl until well blended. Reserve 2 tablespoons for topping. Press remaining crumbs into bottom of 13 x 9-inch baking pan. Bake 5 to 7 minutes or until lightly browned. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B34320D602D3B4AAB16855C03598C8A', X'74E204BD3599AE49A13FE1401AE66F24', X'74E204BD3599AE49A13FE1401AE66F24', 2, NULL, NULL, 2, NULL, 0, 'For Peanut Butter Layer: BEAT cream cheese and peanut butter in large bowl with electric mixer on medium speed until well blended. Beat in powdered sugar. Gently mix in 1 cup whipped topping. Spread evenly over cooled crumb crust.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7C6BC45CD352CF40936C82778CD451EC', X'74E204BD3599AE49A13FE1401AE66F24', X'74E204BD3599AE49A13FE1401AE66F24', 3, NULL, NULL, 2, NULL, 0, 'For Chocolate Layer: BEAT pudding mix and milk in medium bowl on low speed 2 minutes. Spread evenly over peanut butter layer. Chill 1 hour. Spread with remaining whipped topping. Sprinkle with reserved crumbs.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4BB5FDABF5AFD04A918C482EFA38890C', X'49264B0177A1B74984E891B3D9321EF1', X'49264B0177A1B74984E891B3D9321EF1', 0, NULL, NULL, 2, NULL, 0, 'BEAT cheese cheese, peanut butter, 1/3 cup caramel topping, cinnamon and almond extract in medium bowl with electric mixer on medium speed until smooth. Stir in marshmallows.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A5B8722C9A7CB243956D4B0492FA5A4A', X'49264B0177A1B74984E891B3D9321EF1', X'49264B0177A1B74984E891B3D9321EF1', 1, NULL, NULL, 2, NULL, 0, 'SPOON into serving bowl. Drizzle with remaining 1/3 cup caramel topping. Sprinkle with peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F514365D40050D4C9C50969A860BD433', X'49264B0177A1B74984E891B3D9321EF1', X'49264B0177A1B74984E891B3D9321EF1', 2, NULL, NULL, 2, NULL, 0, 'TOSS sliced apples with lemon juice to prevent browning. Serve immediately with dip.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FB094CCB0B7A3E4580ECA5198C35BBC9', X'481E5A7792B19849B8613BA8E00685DC', X'481E5A7792B19849B8613BA8E00685DC', 0, NULL, NULL, 2, NULL, 0, 'COMBINE almond butter and caramel syrup in small bowl. Spread on bagel crisps. Top each with apple ring.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE7B27B0DD59D6458C698FCE6179A0AD', X'C73838CA0AFF31459207FF5239922C91', X'C73838CA0AFF31459207FF5239922C91', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350° F. Mix egg, 1 cup peanut butter chocolate spread, sugar, chopped peanuts and almond extract in large bowl with wooden spoon until combined.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A63ED914FF620749854D57BAEA007E2D', X'C73838CA0AFF31459207FF5239922C91', X'C73838CA0AFF31459207FF5239922C91', 1, NULL, NULL, 2, NULL, 0, 'USING a heaping tablespoon of dough, form into 24 balls. Place dough, 2 inches apart, on ungreased baking sheets. Flatten dough with a fork using tines to make a criss-cross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6B7E74BE3B69048BADC2BFED3DE5EF3', X'C73838CA0AFF31459207FF5239922C91', X'C73838CA0AFF31459207FF5239922C91', 2, NULL, NULL, 2, NULL, 0, 'BAKE 9 to 12 minutes or until light-golden brown. Cool on baking sheet. Remove to cooling rack to cool completely. Frost cookies with remaining peanut butter chocolate spread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'42A2B91E7BE3F548A605B526485E7763', X'4046B9DDA59A784196BD9056B65F16CC', X'4046B9DDA59A784196BD9056B65F16CC', 0, NULL, NULL, 2, NULL, 0, 'COMBINE milk chocolate and heavy cream in small microwavable bowl. Microwave 15 seconds; stir until smooth. Set aside 1 teaspoon of mixture. Evenly spread remaining chocolate on flat side of 3 connected lady fingers. Sprinkle 1 tablespoon and 1 teaspoon of pretzels on chocolate and lightly press in. Place in refrigerator to set.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD489E4BC75E0D46A9F61A75159D10B4', X'4046B9DDA59A784196BD9056B65F16CC', X'4046B9DDA59A784196BD9056B65F16CC', 1, NULL, NULL, 2, NULL, 0, 'MIX peanut butter, mascarpone cheese and confectioners sugar in small bowl until blended and smooth. Spread over chocolate/pretzels. Spread dulce de leche on flat side of remaining 3 connected lady fingers. Place on top of peanut butter mixture with rounded side of lady fingers facing up.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F8DB9238751A954CA264358BB0BB1529', X'4046B9DDA59A784196BD9056B65F16CC', X'4046B9DDA59A784196BD9056B65F16CC', 2, NULL, NULL, 2, NULL, 0, 'HEAT 1 tablespoon of chocolate mixture that was set aside, if needed, and drizzle over top of lady fingers. Sprinkle with 1 teaspoon crushed pretzels. Store in refrigerator.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7D21CAA62FCBB8439B09EC07C90BC366', X'83E2D484E68ACF4DB0A8CD4BF1FE4C03', X'83E2D484E68ACF4DB0A8CD4BF1FE4C03', 0, NULL, NULL, 2, NULL, 0, 'COMBINE applesauce, peanut butter and cinnamon in small bowl. Stir until blended. Stir in sunflower kernels and raisins.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D0FF23936A6EDC4386AF4254474B4057', X'83E2D484E68ACF4DB0A8CD4BF1FE4C03', X'83E2D484E68ACF4DB0A8CD4BF1FE4C03', 1, NULL, NULL, 2, NULL, 0, 'SERVE as a dip or spread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D6D8A5366B1D8E418C4B1D9E8D491B54', X'08FF3C272CE0E84C8E7FCBB92A0BA033', X'08FF3C272CE0E84C8E7FCBB92A0BA033', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 325°F. Line baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A9183C4697391144BF236473B260E544', X'08FF3C272CE0E84C8E7FCBB92A0BA033', X'08FF3C272CE0E84C8E7FCBB92A0BA033', 1, NULL, NULL, 2, NULL, 0, 'BEAT peanut butter, sugar, brown sugar, egg, cornmeal, baking powder, vanilla and salt in medium bowl with mixer on medium speed until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'68A60275BE5BDD48A27843572375ADAB', X'08FF3C272CE0E84C8E7FCBB92A0BA033', X'08FF3C272CE0E84C8E7FCBB92A0BA033', 2, NULL, NULL, 2, NULL, 0, 'SHAPE into 1 1/4-inch balls. Place 2 inches apart on prepared baking sheet. Flatten with bottom of drinking glass to 1 3/4-inch diameter, then mark with fork in a criss-cross pattern.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7A0D948BA195164889CB71DDCC2BDA95', X'08FF3C272CE0E84C8E7FCBB92A0BA033', X'08FF3C272CE0E84C8E7FCBB92A0BA033', 3, NULL, NULL, 2, NULL, 0, 'BAKE 9 to 10 minutes or until edges are light golden brown. Cool on baking sheet 5 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1664A36CB6A894458AC6316E445CBC7E', X'A96834638A742247A9FD9EEB779223D8', X'A96834638A742247A9FD9EEB779223D8', 0, NULL, NULL, 2, NULL, 0, 'STIR chocolate spread in large bowl until softened. Fold in whipped topping until evenly blended. Place in serving dish.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0B0ADACF3370D647B01E00425E9C07FB', X'A96834638A742247A9FD9EEB779223D8', X'A96834638A742247A9FD9EEB779223D8', 1, NULL, NULL, 2, NULL, 0, 'TOP with sprinkles or chocolate chips. Cover and chill. Serve as dip for cookies, pretzels and fruit.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1B0125F4233FA3488A8C26166D216B35', X'9CFF55874FF03E498E7A1128C5F58D0A', X'9CFF55874FF03E498E7A1128C5F58D0A', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter and preserves on bread. Top with white chocolate.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A3A4A09F5FDCB40AA2B8483C4809BF5', X'6129D40A75B8F2489FDB141DC549B193', X'6129D40A75B8F2489FDB141DC549B193', 0, NULL, NULL, 2, NULL, 0, 'LINE an 8-inch square baking pan with foil, extending foil over edge of pan. Coat with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'113DC9D46CB1234DBBCE14F4200F28FA', X'6129D40A75B8F2489FDB141DC549B193', X'6129D40A75B8F2489FDB141DC549B193', 1, NULL, NULL, 2, NULL, 0, 'MELT butter in a large saucepan (4 qt.) over low heat until melted. Add marshmallows stirring until completely melted. Remove from heat. Blend in 1 1/4 cups peanut butter chocolate spread mixing until combined. Add cereal. Stir until well coated. Press into prepared pan. Cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7E75CD2B5CFE34585C17F8E7B3D3AD8', X'6129D40A75B8F2489FDB141DC549B193', X'6129D40A75B8F2489FDB141DC549B193', 2, NULL, NULL, 2, NULL, 0, 'FROST treats with remaining peanut butter chocolate spread. Remove treats from pan using edges of foil. Cut into 32 bars.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'255D43163F98D045A0B56D75A78913C5', X'063266025BC3B04887DDECBD0864FD6B', X'063266025BC3B04887DDECBD0864FD6B', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Spread 1 teaspoon peanut butter on 20 banana slices. Top with remaining slices. Insert party forks into each bite. Place on prepared baking sheet. Freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'35542FBDBF9BAF4E88E233760983B06E', X'063266025BC3B04887DDECBD0864FD6B', X'063266025BC3B04887DDECBD0864FD6B', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate chips in medium microwave-safe bowl on HIGH 1 1/2 minutes. Stir until smooth. Dip each bite into chocolate, coating completely. Sprinkle immediately with coconut, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63BCAB7B5FB7684B9D55408D29EE5E4A', X'063266025BC3B04887DDECBD0864FD6B', X'063266025BC3B04887DDECBD0864FD6B', 2, NULL, NULL, 2, NULL, 0, 'FREEZE 30 minutes or until ready to serve.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EDA7B258DC21934C843F554DD8D8E514', X'BE873CF5EC6B5E40B5E6AE34DFAC9888', X'BE873CF5EC6B5E40B5E6AE34DFAC9888', 0, NULL, NULL, 2, NULL, 0, 'WHISK peanut butter, honey, water and cinnamon in large bowl until smooth. Let stand 5 minutes to thicken.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E98E16A063BF644B5C77BF6E4DD5F51', X'BE873CF5EC6B5E40B5E6AE34DFAC9888', X'BE873CF5EC6B5E40B5E6AE34DFAC9888', 1, NULL, NULL, 2, NULL, 0, 'ADD diced apple, banana slices, walnuts and raisins; stir to coat.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E920208BDDCF13489AB9765D32DE9AD3', X'BE873CF5EC6B5E40B5E6AE34DFAC9888', X'BE873CF5EC6B5E40B5E6AE34DFAC9888', 2, NULL, NULL, 2, NULL, 0, 'SPOON fruit mixture horizontally down center of each tortilla. Fold bottom edge over filling. Push filling against the fold to tighten filling in place. Fold in left and right edges of tortillas about 2 inches. Roll up from bottom to completely enclose filling. Cut wraps in half before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EDDA7D1D735EC949BDF96B8FED2A71E3', X'C96D69DDDA537E438EA2C4FDFC87F7B2', X'C96D69DDDA537E438EA2C4FDFC87F7B2', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Coat baking sheets with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'881347D0FAF5BB47B2E2E768C0B1ED11', X'C96D69DDDA537E438EA2C4FDFC87F7B2', X'C96D69DDDA537E438EA2C4FDFC87F7B2', 1, NULL, NULL, 2, NULL, 0, 'For Cookies: BEAT peanut butter, shortening, brown sugar, milk and vanilla in large bowl with electric mixer on medium speed until well blended. Add eggs. Beat just until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF13DC957ADA6F439868EDFB0B54501A', X'C96D69DDDA537E438EA2C4FDFC87F7B2', X'C96D69DDDA537E438EA2C4FDFC87F7B2', 2, NULL, NULL, 2, NULL, 0, 'COMBINE flour, baking soda and salt in medium bowl. Add to creamed mixture on low speed, mixing just until blended. Drop by level tablespoonfuls 2 inches apart onto prepared baking sheets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18DF6DAB6B4FDE4BB611055E43A10418', X'C96D69DDDA537E438EA2C4FDFC87F7B2', X'C96D69DDDA537E438EA2C4FDFC87F7B2', 3, NULL, NULL, 2, NULL, 0, 'BAKE 6 to 7 minutes or until set and just beginning to brown. Cool 2 minutes on baking sheet. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C0EC60E56C04C240AD7F9D2BB6CA690B', X'C96D69DDDA537E438EA2C4FDFC87F7B2', X'C96D69DDDA537E438EA2C4FDFC87F7B2', 4, NULL, NULL, 2, NULL, 0, 'For Filling: MICROWAVE sweetened condensed milk and chocolate chips in medium microwave-safe bowl on HIGH for 1 1/2 minutes or until melted and smooth when stirred. Stir in vanilla. Let cool 15 minutes. Spread generously on flat side of half the cookies. Top with remaining cookies, flat side down, to form sandwich cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'47AF60A1F5E71645B78D891620E2FCAF', X'913E82C7D8A70346B25A4A6CA633CD5F', X'913E82C7D8A70346B25A4A6CA633CD5F', 0, NULL, NULL, 2, NULL, 0, 'BREAK graham cracker in half to make 2 squares.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9E13E32A3D43A44A553EC14CDA85BC2', X'913E82C7D8A70346B25A4A6CA633CD5F', X'913E82C7D8A70346B25A4A6CA633CD5F', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on one side of 1 piece of graham cracker. Spread jam on one side of remaining graham cracker. Press gently together to form a sandwich.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B782562F5A7C0C4AA0BCB7328BC1DA2B', X'18CAC32DAAF99841BDABA48F3DEBC93E', X'18CAC32DAAF99841BDABA48F3DEBC93E', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter and fruit spread on bread slices. Top with coconut and chia seeds, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B722363BA483B145AB70013B95461466', X'3ED39574D96D474CA3304957D7EAE439', X'3ED39574D96D474CA3304957D7EAE439', 0, NULL, NULL, 2, NULL, 0, 'CUT bacon crosswise into 1/2-inch pieces. Cook in large skillet over medium heat until crisp, stirring occasionally. Remove from skillet and drain on paper towels.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EDE836A76AC55C48ABC954E67CABCA8E', X'3ED39574D96D474CA3304957D7EAE439', X'3ED39574D96D474CA3304957D7EAE439', 1, NULL, NULL, 2, NULL, 0, 'SPREAD or pipe peanut butter lengthwise, down center of celery pieces.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'757CA3E12791194AADEC2BEC83320DEF', X'3ED39574D96D474CA3304957D7EAE439', X'3ED39574D96D474CA3304957D7EAE439', 2, NULL, NULL, 2, NULL, 0, 'DOT surface of peanut butter with raisins and cooked bacon. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6840B7D6150C4B408F9586C1FCE93101', X'7A8A74E3A028774399C10BBB703DC6E0', X'7A8A74E3A028774399C10BBB703DC6E0', 0, NULL, NULL, 2, NULL, 0, 'FOLD peanut butter, honey and cinnamon together just until blended. Place into serving bowl. Serve with pretzels, cookies and fresh fruit.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B40FECA65BBE334E99D2EB468807C8A8', X'CE0C9012452666448487E1D5AE8636EB', X'CE0C9012452666448487E1D5AE8636EB', 0, NULL, NULL, 2, NULL, 0, 'SPREAD graham cracker square with peanut butter. Top with a square of chocolate and a marshmallow.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C436525667DFC46B28BB031728BAAB8', X'CE0C9012452666448487E1D5AE8636EB', X'CE0C9012452666448487E1D5AE8636EB', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE on HIGH (100% power) 5 to 10 seconds or until marshmallow puffs. Top with another cracker square. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CA60B7FC790CC4983662A08EFF4D5F2', X'4A9A52450C65364695575A973DEFF890', X'4A9A52450C65364695575A973DEFF890', 0, NULL, NULL, 2, NULL, 0, 'PLACE 2 teaspoons chocolate cookies into 8 (2 oz.) parfait cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE5B7F5762A9C848A0E59A252464075D', X'4A9A52450C65364695575A973DEFF890', X'4A9A52450C65364695575A973DEFF890', 1, NULL, NULL, 2, NULL, 0, 'ADD peanut butter to resealable plastic bag. Cut off corner of bag. Pipe about 1 tablespoon into each cup. Top with 1 teaspoon chocolate cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B2BB0C1FDD7E5749A772F93F1F2FF832', X'4A9A52450C65364695575A973DEFF890', X'4A9A52450C65364695575A973DEFF890', 2, NULL, NULL, 2, NULL, 0, 'CHILL until ready to serve. Top with whipped cream and cinnamon before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E47D513ADC2E0541A8EF9798C1380FBE', X'BE5F36F2F7939042A65F88BD77F71374', X'BE5F36F2F7939042A65F88BD77F71374', 0, NULL, NULL, 2, NULL, 0, 'WHISK water and 1/4 cup peanut butter in medium bowl until blended. Whisk in pancake mix and cinnamon just until evenly moistened. Stir in chocolate chips. Let stand 3 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2830BE051FCE4B44BDF170344903AE76', X'BE5F36F2F7939042A65F88BD77F71374', X'BE5F36F2F7939042A65F88BD77F71374', 1, NULL, NULL, 2, NULL, 0, 'HEAT skillet over medium-high heat or electric griddle to 375°F. Coat with no-stick cooking spray. Pour 2 tablespoons batter for each pancake onto hot skillet. Cook 1 to 1 /2 minutes on each side or until golden brown.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2E44054B0F194F44B19F8A0F935D08B1', X'BE5F36F2F7939042A65F88BD77F71374', X'BE5F36F2F7939042A65F88BD77F71374', 2, NULL, NULL, 2, NULL, 0, 'COMBINE syrup with remaining 1/4 cup peanut butter in microwave-safe bowl. Microwave on HIGH 30 seconds. Serve over warm pancakes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A76B9EC3202E174192FE9ACD292B466E', X'EE796C16CAA7B04C904F208AF87EFE37', X'EE796C16CAA7B04C904F208AF87EFE37', 0, NULL, NULL, 2, NULL, 0, 'SPREAD a thin layer of peanut butter over top surface of each sandwich wrap.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'920EC9359915A44FA867E4D1D282F3BF', X'EE796C16CAA7B04C904F208AF87EFE37', X'EE796C16CAA7B04C904F208AF87EFE37', 1, NULL, NULL, 2, NULL, 0, 'WHISK oil, vinegar and sugar in medium bowl until blended. Season with salt and pepper. Add salad mix and green onion; toss to coat. Divide evenly over peanut butter layer. Top with apple slices and cooked bacon.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E34ACF8D0CAE75429C93AA1FC89EB4DF', X'EE796C16CAA7B04C904F208AF87EFE37', X'EE796C16CAA7B04C904F208AF87EFE37', 2, NULL, NULL, 2, NULL, 0, 'ROLL up or fold to enclose filling. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'26A680A72E53A741824FF910E1B28ADF', X'3327C5A300C24E4F8BC0B397363B6737', X'3327C5A300C24E4F8BC0B397363B6737', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375°F. Spread 1 tablespoon peanut butter over each toasted English muffin half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9871696A33B74849815006B168E121A6', X'3327C5A300C24E4F8BC0B397363B6737', X'3327C5A300C24E4F8BC0B397363B6737', 1, NULL, NULL, 2, NULL, 0, 'TOP 4 muffin halves each with 1 slice cheese, several apple slices and 2 bacon strips. Sprinkle with cinnamon, if desired. Top with remaining muffin halves to form sandwiches. Place on baking sheet. Bake 3 to 5 minutes or until cheese begins to melt.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DBD2067A1E0E8E4480566CBAECA9175F', X'34545EF503C4E942838993810F191D68', X'34545EF503C4E942838993810F191D68', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on cut side of 1 bagel half. Spread jam on cut side of remaining bagel half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC8097941770104B99329E2EF10D75D9', X'34545EF503C4E942838993810F191D68', X'34545EF503C4E942838993810F191D68', 1, NULL, NULL, 2, NULL, 0, 'SPRINKLE peanut butter and jam with sunflower kernels.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6BC814809C4D9C4294290E3917634955', X'9F159CF986D0B14F900550BDF33FD147', X'9F159CF986D0B14F900550BDF33FD147', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter evenly on inside surface of hot dog roll.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FC57BA3E4A3A5045B1A58EBDAE7EA00C', X'9F159CF986D0B14F900550BDF33FD147', X'9F159CF986D0B14F900550BDF33FD147', 1, NULL, NULL, 2, NULL, 0, 'PLACE banana in roll. Top with desired amount of fruit spread. Sprinkle with nuts or raisins.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2ADE6CE0EC85FA43B5DF086B429D8037', X'36AA84812F5B6C42AD45ADB0F9775B86', X'36AA84812F5B6C42AD45ADB0F9775B86', 0, NULL, NULL, 2, NULL, 0, 'CUT 10 shapes from bread using 2-inch star-shaped cookie cutter. Spread peanut butter on half the stars and jam on remaining stars. Press together to make five small sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1EE914D2F1C05A498C0B49FB317DDFBB', X'36AA84812F5B6C42AD45ADB0F9775B86', X'36AA84812F5B6C42AD45ADB0F9775B86', 1, NULL, NULL, 2, NULL, 0, 'THREAD sandwiches, strawberry halves and grapes alternately onto skewer. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A4369D8E8AE01C49B1F28A289D45092C', X'60551E05A69F0A4583EEB3633F27EDF0', X'60551E05A69F0A4583EEB3633F27EDF0', 0, NULL, NULL, 2, NULL, 0, 'CUT granola bar in half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7A802D740CE02440A8AC25241BF1F7AF', X'60551E05A69F0A4583EEB3633F27EDF0', X'60551E05A69F0A4583EEB3633F27EDF0', 1, NULL, NULL, 2, NULL, 0, 'SPREAD one half of bar with peanut butter. Spread remaining half with jam.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6816D84A53E25428A085D500CFBF9E3', X'60551E05A69F0A4583EEB3633F27EDF0', X'60551E05A69F0A4583EEB3633F27EDF0', 2, NULL, NULL, 2, NULL, 0, 'PRESS together to form bite.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A957E83F68F06C49916DCE4F1F0FEB88', X'DA80AD3DF5C8424085518040FFC68CD9', X'DA80AD3DF5C8424085518040FFC68CD9', 0, NULL, NULL, 2, NULL, 0, 'DIVIDE peanut butter onto one side of 3 mini pancakes, spreading evenly.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D9897DC75CED745841187B32A40287B', X'DA80AD3DF5C8424085518040FFC68CD9', X'DA80AD3DF5C8424085518040FFC68CD9', 1, NULL, NULL, 2, NULL, 0, 'DIVIDE jam onto one side of remaining 3 mini pancakes, spreading evenly. Place on top of peanut peanut layer, jam side down. Stack filled mini pancakes on serving plate.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD624F69F4502E41B3275DE75AE5020B', X'43DB2421D7B74545A89EBCF5495AE4F7', X'43DB2421D7B74545A89EBCF5495AE4F7', 0, NULL, NULL, 2, NULL, 0, 'TRIM edges from bread slices using a serrated knife. Make 1 star in center of 3 bread slices by cutting with 1-inch star-shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'762A2CC48F100C48BAE4D399B5A4B222', X'43DB2421D7B74545A89EBCF5495AE4F7', X'43DB2421D7B74545A89EBCF5495AE4F7', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter evenly onto 3 whole bread slices. Spoon jam evenly onto center of peanut butter, spreading gently into a 2-inch circle. Top with cut-out bread slices to make sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'10D2927E2D95BD438AEFE229B670BDFE', X'13F609A928AF73459EDDBD41193B1D64', X'13F609A928AF73459EDDBD41193B1D64', 0, NULL, NULL, 2, NULL, 0, 'PLACE jam, peanut butter, frozen yogurt, milk and frozen berries in a blender container. Blend until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C10AC12CCCBE634F83BB928B2D4FF5A1', X'804174FCE761F748A693BF76404DD409', X'804174FCE761F748A693BF76404DD409', 0, NULL, NULL, 2, NULL, 0, 'SPLIT and toast English muffin. Spread peanut spread on both sides of muffin. Spread or squeeze fruit spread over peanut butter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3A8E7EDB6572DB4AA5910DEFF47A28A6', X'804174FCE761F748A693BF76404DD409', X'804174FCE761F748A693BF76404DD409', 1, NULL, NULL, 2, NULL, 0, 'TOP with banana slices. Drizzle with warmed fudge topping to taste. Sprinkle with coconut, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6139BDFDEC471446BA51447318E5C390', X'B2F2D94D0858C5499B737C420D2A4608', X'B2F2D94D0858C5499B737C420D2A4608', 0, NULL, NULL, 2, NULL, 0, 'STIR peanut butter, honey and cinnamon in small bowl. Arrange apple rings on serving plates. Spread rings evenly with thin layer of peanut butter mixture.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1D63DE9BA7DA3342BE6A2DEA46815ECA', X'B2F2D94D0858C5499B737C420D2A4608', X'B2F2D94D0858C5499B737C420D2A4608', 1, NULL, NULL, 2, NULL, 0, 'LIGHTLY PRESS granola cereal into peanut butter mixture. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BE3A6BCE0BC0C949837C74528A65C183', X'8652546DB97ED14888857D4F1989912A', X'8652546DB97ED14888857D4F1989912A', 0, NULL, NULL, 2, NULL, 0, 'PLACE milk, banana, peanut butter and ice in blender container.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE483AAA32734C4A94D9E7850BF79E78', X'8652546DB97ED14888857D4F1989912A', X'8652546DB97ED14888857D4F1989912A', 1, NULL, NULL, 2, NULL, 0, 'COVER and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDA960F78798644A8E4EF13101C9912F', X'71351064D082AD419403F515EFCE25BF', X'71351064D082AD419403F515EFCE25BF', 0, NULL, NULL, 2, NULL, 0, 'SPRAY two 9-inch cake pans with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D1DF62A39D4A4C4E9AA154314B926D29', X'71351064D082AD419403F515EFCE25BF', X'71351064D082AD419403F515EFCE25BF', 1, NULL, NULL, 2, NULL, 0, 'MELT butter over low heat in large saucepan. Add marshmallows. Stir until completely melted and remove from heat. Stir in peanut butter mixing well. Add cereal and stir until well coated. Divide evenly between prepared pans. Press evenly into pans. Cool.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9138CB5848442F459616FBE29F0993D3', X'71351064D082AD419403F515EFCE25BF', X'71351064D082AD419403F515EFCE25BF', 2, NULL, NULL, 2, NULL, 0, 'REMOVE from pans. Cut ears and bow tie from one cake by cutting two football-shaped ears from the top and bottom of one cake layer, leaving a bow tie-shaped piece in the middle. Position two ears at top of whole round cake and place bow tie at bottom.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC5343840585B14D8569B23D3FE848AB', X'71351064D082AD419403F515EFCE25BF', X'71351064D082AD419403F515EFCE25BF', 3, NULL, NULL, 2, NULL, 0, 'CUT jelly beans in half to use as bunny''s eyes and nose. Cut pieces of licorice for mouth and whiskers. Use cut jelly beans and licorice pieces to decorate bow tie.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2139FC5866BB7D4D8BDC64D466977358', X'A78318330ACD834CBF79D14F8E7D9705', X'A78318330ACD834CBF79D14F8E7D9705', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat 8-inch square baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4754C4AE61CF614FA1C2C7DC86FF745D', X'A78318330ACD834CBF79D14F8E7D9705', X'A78318330ACD834CBF79D14F8E7D9705', 1, NULL, NULL, 2, NULL, 0, 'BEAT 1/2 cup peanut butter, butter and sugar in large bowl with electric mixer until creamy. Add eggs and vanilla. Beat until blended. Add milk and pancake mix alternately, beating after each addition until just combined. Pour into prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'442BDE4905B30A429C8031B5DCCD3B99', X'A78318330ACD834CBF79D14F8E7D9705', X'A78318330ACD834CBF79D14F8E7D9705', 2, NULL, NULL, 2, NULL, 0, 'BAKE 30 to 35 minutes or until toothpick inserted in center comes out clean. Cool 10 minutes, then turn cake into a large bowl and set aside. Beat 1/2 cup peanut butter and frosting until smooth. Line two large baking sheets with wax paper.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'479C3212A17F2240875B7946F472F8FA', X'A78318330ACD834CBF79D14F8E7D9705', X'A78318330ACD834CBF79D14F8E7D9705', 3, NULL, NULL, 2, NULL, 0, 'CRUMBLE cake into fine crumbs with fork in bowl. Add frosting mixture. Mix thoroughly with spatula. Cover with plastic wrap and freeze 30 minutes. Roll mixture into 1 1/2-inch round cake bites using small cookie scoop (one tablespoon cake mixture). Place bites on one prepared baking sheet. Cover with plastic wrap and freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A7C2273420E21240A81FDDD709539A67', X'A78318330ACD834CBF79D14F8E7D9705', X'A78318330ACD834CBF79D14F8E7D9705', 4, NULL, NULL, 2, NULL, 0, 'PLACE chocolate chips and shortening in microwave-safe bowl. Microwave on HIGH 2 minutes. Stir. Microwave in additional 10 second intervals until completely melted and smooth when stirred.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'601BB7BF6D578B4085B4D688F22C2C74', X'A78318330ACD834CBF79D14F8E7D9705', X'A78318330ACD834CBF79D14F8E7D9705', 5, NULL, NULL, 2, NULL, 0, 'REMOVE ten cake bites from freezer at a time, keeping the remaining ones frozen. Drop one cake bite at a time into melted chocolate. Spoon chocolate over each bite until coated. Lift cake bite out of chocolate with a fork, allowing the excess chocolate to drip into bowl. Using toothpick, gently push bite off fork onto other prepared baking sheet. Top each with chopped peanuts immediately. Repeat to coat remaining bites. Chill 5 minutes or until chocolate is set. Allow to come to room temperature before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BAA8DB733057584EAA930543AB35855A', X'A78318330ACD834CBF79D14F8E7D9705', X'A78318330ACD834CBF79D14F8E7D9705', 6, NULL, NULL, 2, NULL, 0, 'If chocolate becomes stiff while dipping bites, heat in microwave for 10 second intervals, stirring until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'871F0AD11A3D86468A8A0C5B5F38114B', X'0254A4088798C7439F6010E4A96B6BE1', X'0254A4088798C7439F6010E4A96B6BE1', 0, NULL, NULL, 2, NULL, 0, 'BREAK noodles into smaller pieces in large bowl, using your hands. Combine sugar and syrup in large saucepan. Cook over medium heat until sugar is melted and edges begin to bubble. Add peanut butter and stir until smooth. Pour mixture over noodles and toss to coat.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2BA429B96F641A45BFF0799DB590DBFD', X'0254A4088798C7439F6010E4A96B6BE1', X'0254A4088798C7439F6010E4A96B6BE1', 1, NULL, NULL, 2, NULL, 0, 'TEST temperature carefully. With slightly buttered hands, form mixture into tight balls using approximately 1/4 cup mixture per ball. Make indention in center of each ball to resemble a birds nest. Place on waxed paper and allow to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E1BA9445DD236C42964DDB905183CAB1', X'0254A4088798C7439F6010E4A96B6BE1', X'0254A4088798C7439F6010E4A96B6BE1', 2, NULL, NULL, 2, NULL, 0, 'PLACE two or three jelly beans or other egg-shaped candies into each nest.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5650E6DAD727424187D43C7A6FF7584C', X'F56BE9FA6E3DE141AE44F24D61726231', X'F56BE9FA6E3DE141AE44F24D61726231', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 300°F. Coat two 10 x 15 x 1-inch baking sheets with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9FA1295AA7A77B4E82976F8D71FB6D16', X'F56BE9FA6E3DE141AE44F24D61726231', X'F56BE9FA6E3DE141AE44F24D61726231', 1, NULL, NULL, 2, NULL, 0, 'PLACE popped popcorn in a very large bowl or Dutch oven.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6137F82ACEDDC04AA3C1084DF1EC2773', X'F56BE9FA6E3DE141AE44F24D61726231', X'F56BE9FA6E3DE141AE44F24D61726231', 2, NULL, NULL, 2, NULL, 0, 'COMBINE peanut butter, corn syrup, butter and salt in a medium saucepan over medium heat, stirring constantly, until mixture begins to boil. Remove from heat, add vanilla and stir to combine.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5E52297CF9E171428A3B93F5EA21B233', X'F56BE9FA6E3DE141AE44F24D61726231', X'F56BE9FA6E3DE141AE44F24D61726231', 3, NULL, NULL, 2, NULL, 0, 'POUR warm peanut butter mixture over popcorn, stirring gently to combine. When all popcorn appears evenly coated with peanut butter mixture, spread evenly on prepared baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3C02229C5528040A69A340B05DCCC14', X'F56BE9FA6E3DE141AE44F24D61726231', X'F56BE9FA6E3DE141AE44F24D61726231', 4, NULL, NULL, 2, NULL, 0, 'BAKE 20 to 25 minutes, stirring occasionally. Remove from oven and allow to cool. May be stored in airtight container for up to one week.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ECD5D38A0D5280499390C740602E8E2E', X'D1007CC529B1084AB53AD715EC0BCB74', X'D1007CC529B1084AB53AD715EC0BCB74', 0, NULL, NULL, 2, NULL, 0, 'COMBINE syrup and peanut butter in microwave-safe measuring cup. Microwave on MEDIUM 15 seconds. Stir. Repeat until smooth. Cool slightly.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CF9AB948C77D4E48BBD348576C035BA6', X'D1007CC529B1084AB53AD715EC0BCB74', X'D1007CC529B1084AB53AD715EC0BCB74', 1, NULL, NULL, 2, NULL, 0, 'POUR warm over frozen yogurt or ice cream.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50C68D542E79324798F74D19F3F78833', X'D1007CC529B1084AB53AD715EC0BCB74', X'D1007CC529B1084AB53AD715EC0BCB74', 2, NULL, NULL, 2, NULL, 0, 'If topping becomes too thick, thin with a little additional syrup.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7F5CA29B81C6E14D87F294A990A9A870', X'5E5B0EBBE0C2D24C831A82EE791C2EE8', X'5E5B0EBBE0C2D24C831A82EE791C2EE8', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Beat peanut butter, brown sugar, sugar, butter and vanilla in large bowl with mixer on high speed until well blended. Beat in eggs.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1061D3DD5CD2E646AC318A355E8371FD', X'5E5B0EBBE0C2D24C831A82EE791C2EE8', X'5E5B0EBBE0C2D24C831A82EE791C2EE8', 1, NULL, NULL, 2, NULL, 0, 'STIR oats, baking soda and salt in large bowl until blended. Gradually blend into peanut butter mixture. Stir in chocolate chips and chocolate pieces. Cover and chill at least 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D30DCECF758AAA4EA3327F9A3CFD7BF0', X'5E5B0EBBE0C2D24C831A82EE791C2EE8', X'5E5B0EBBE0C2D24C831A82EE791C2EE8', 2, NULL, NULL, 2, NULL, 0, 'DROP by heaping tablespoonfuls 2 inches apart onto baking sheets. Press to flatten slightly. Bake 9 to 11 minutes or until edges are lightly browned. Cool 2 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5F284D30F54351488AE0FB99E6E1D8F2', X'F07E582A5B32DB4C99A4C0BD0674D3EC', X'F07E582A5B32DB4C99A4C0BD0674D3EC', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Line baking sheets with parchment paper, if desired. Beat brown sugar and butter in large bowl with electric mixer on medium speed until creamy. Add 1/2 cup peanut butter, egg and vanilla, beating until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B998D7F9EF5DB342AC186A8B114DF246', X'F07E582A5B32DB4C99A4C0BD0674D3EC', X'F07E582A5B32DB4C99A4C0BD0674D3EC', 1, NULL, NULL, 2, NULL, 0, 'COMBINE flour, baking soda, salt, cinnamon and baking powder in medium bowl, stirring until well blended. Gradually add to creamed mixture, beating just until blended. Mix in oats.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'28B9AEB653367645BF801B75B7CFB42F', X'F07E582A5B32DB4C99A4C0BD0674D3EC', X'F07E582A5B32DB4C99A4C0BD0674D3EC', 2, NULL, NULL, 2, NULL, 0, 'DROP by 2 level teaspoonfuls, 2 inches apart, onto prepared baking sheets to make 48 mounds. Bake 9 to 11 minutes or until golden brown. Cool on baking sheet 3 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1978BA8F1394814B9F677C3D0A78F7B3', X'F07E582A5B32DB4C99A4C0BD0674D3EC', X'F07E582A5B32DB4C99A4C0BD0674D3EC', 3, NULL, NULL, 2, NULL, 0, 'BEAT frosting and remaining 3/4 cup peanut butter in medium bowl with electric mixer on medium speed until blended. Spread in an even layer on the flat side of half the cookies. Place another cookie, flat side down, on top of filling mixture to make sandwich cookies.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A2A02DC8D9485A4FA1E8C3E57CD013A1', X'7A99BB342D7F8948ABA31456CE109C0D', X'7A99BB342D7F8948ABA31456CE109C0D', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on 1 side of 1 slice of bread.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'768C623D82DA924299AEC82807FC8EF5', X'7A99BB342D7F8948ABA31456CE109C0D', X'7A99BB342D7F8948ABA31456CE109C0D', 1, NULL, NULL, 2, NULL, 0, 'PLACE pineapple on top of peanut butter layer. Spoon marshmallow creme over pineapple. Top with remaining slice of bread to form a sandwich. Cut with a serrated knife on a diagonal.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3146773F1B14CE4CB338C664DF65C23C', X'B5186F267A59A54E90BF5C76FA46E34A', X'B5186F267A59A54E90BF5C76FA46E34A', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Stir flour and baking soda in small bowl until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A7F9E8C15236045B723855031AD333C', X'B5186F267A59A54E90BF5C76FA46E34A', X'B5186F267A59A54E90BF5C76FA46E34A', 1, NULL, NULL, 2, NULL, 0, 'BEAT butter, sugar and brown sugar in large bowl with mixer on medium speed until creamy. Beat in peanut butter, egg and vanilla. Gradually beat in flour mixture. Stir in granola cereal, cranberries and pistachios until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A6AEC0AB6F25F45BD4E7B7403C16E9F', X'B5186F267A59A54E90BF5C76FA46E34A', X'B5186F267A59A54E90BF5C76FA46E34A', 2, NULL, NULL, 2, NULL, 0, 'DROP by rounded tablespoonfuls 2 inches apart onto baking sheet. Bake 8 to 9 minutes or until edges are lightly browned. Cool 3 minutes. Remove to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'58638DC4C569E04B896B67ADDF70BA7D', X'67AF9EF21D525B498420ED9D8CFF1735', X'67AF9EF21D525B498420ED9D8CFF1735', 0, NULL, NULL, 2, NULL, 0, 'PREPARE pudding mix according to package directions using 2 1/3 cups milk. Stir in peanut butter until completely blended. Divide into 4 (1-cup) serving dishes. Cool completely. Chill until ready to serve.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D76AB4311FF2C4781652A48DA6DA7ED', X'67AF9EF21D525B498420ED9D8CFF1735', X'67AF9EF21D525B498420ED9D8CFF1735', 1, NULL, NULL, 2, NULL, 0, 'TOP evenly with granola, banana and jam just before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6897F8C3A303BC4E953BA1AE44DAE098', X'4A9EB29DC0E8E949897EBCF3326FB0C9', X'4A9EB29DC0E8E949897EBCF3326FB0C9', 0, NULL, NULL, 2, NULL, 0, 'PLACE milk and peanut butter in blender container. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D473DD05D38AA49B732566F77EDF983', X'4A9EB29DC0E8E949897EBCF3326FB0C9', X'4A9EB29DC0E8E949897EBCF3326FB0C9', 1, NULL, NULL, 2, NULL, 0, 'ADD ice cream. Cover and process until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'312A6F786AAF124A917C1C8E8B7FB3D9', X'4A9EB29DC0E8E949897EBCF3326FB0C9', X'4A9EB29DC0E8E949897EBCF3326FB0C9', 2, NULL, NULL, 2, NULL, 0, 'POUR into serving glasses. Top with whipped cream and peanuts, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9105E6F3E448464BA6F54DDF3B05253F', X'4A9EB29DC0E8E949897EBCF3326FB0C9', X'4A9EB29DC0E8E949897EBCF3326FB0C9', 3, NULL, NULL, 2, NULL, 0, 'BANANA PEANUT BUTTER MILKSHAKE: Prepare recipe as directed above, adding 1 small sliced banana to the blender container with peanut butter.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'42B8CAA9A64C8C4CB7A7F530EFE0BA5F', X'87B0422152DB2944B23477CEEDC032ED', X'87B0422152DB2944B23477CEEDC032ED', 0, NULL, NULL, 2, NULL, 0, 'LINE 15 x 10 x 1-inch baking pan with wax paper. Place graham cracker crumbs in small bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5F09CC8109015A4EA1FD16AABA11166E', X'87B0422152DB2944B23477CEEDC032ED', X'87B0422152DB2944B23477CEEDC032ED', 1, NULL, NULL, 2, NULL, 0, 'PLACE peanut butter spread in small bowl. Dip one end of each marshmallow into spread, one at a time, turning to coat bottom third of marshmallow. Dip coated area into graham cracker crumbs. Place on prepared pan, coated side down.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'403A7C48C5724F46BF26ADF0DC52B3F2', X'87B0422152DB2944B23477CEEDC032ED', X'87B0422152DB2944B23477CEEDC032ED', 2, NULL, NULL, 2, NULL, 0, 'COMBINE chocolate chips and shortening in 1-quart heavy-duty resealable plastic bag. Microwave on HIGH 30 to 40 seconds. Knead until chocolate is melted and smooth. Cut very small corner off bag. Drizzle over marshmallows. Insert lollipop sticks into marshmallows. Freeze 10 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A846518156880447B5A3B05405F8827D', X'8823635436FC4E4895ACB1EAA18D5EAC', X'8823635436FC4E4895ACB1EAA18D5EAC', 0, NULL, NULL, 2, NULL, 0, 'HEAT over to 325ºF. Coat a 15 x 10-inch jelly roll pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5FA31621AF3BA74294359E2F48327C09', X'8823635436FC4E4895ACB1EAA18D5EAC', X'8823635436FC4E4895ACB1EAA18D5EAC', 1, NULL, NULL, 2, NULL, 0, 'PREPARE cake mix batter according to package directions using water, oil and eggs. Spread in prepared pan. Bake 16 to 20 minutes or until toothpick inserted in center comes out clean. Cool completely in pan on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C99AC671B88F69489F174372CF1AB89F', X'8823635436FC4E4895ACB1EAA18D5EAC', X'8823635436FC4E4895ACB1EAA18D5EAC', 2, NULL, NULL, 2, NULL, 0, 'BEAT milk and pudding mix in large bowl with electric mixer on medium speed until thickened, about 15 seconds. Add peanut butter; beat until smooth. Beat in whipped topping until smooth. Spread over cooled cake. Garnish with chopped peanuts.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'06C647B5A3F2AD42B74513E02189EB20', X'6A004A0B3CCE884399859C0998A0433F', X'6A004A0B3CCE884399859C0998A0433F', 0, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on top of tortilla to within 1/2 inch of edge.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7959DFF95E00B748A40F3F2E36E627C2', X'6A004A0B3CCE884399859C0998A0433F', X'6A004A0B3CCE884399859C0998A0433F', 1, NULL, NULL, 2, NULL, 0, 'ARRANGE slaw mix, cilantro and chicken lengthwise down center of tortilla. Drizzle evenly with chili sauce. Roll up tightly. Cut in half before serving.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'367BDBAFBEE40546A2C7AEC861FFC5F3', X'D3804F763FDF2149BA38843E69158EFB', X'D3804F763FDF2149BA38843E69158EFB', 0, NULL, NULL, 2, NULL, 0, 'HEAT frying pan or griddle to medium. Roll each slice of bread flat with rolling pin. Spread 1 tablespoon of peanut butter and 1 teaspoon apricot preserves on each slice of flattened bread. Take three apple slices and place them in single layer lengthwise along top of bread. Repeat with second slice. Sprinkle both slices with 1/2 teaspoon cinnamon sugar.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'936E458B1596BC49B83DF06DF3F6B6C8', X'D3804F763FDF2149BA38843E69158EFB', X'D3804F763FDF2149BA38843E69158EFB', 1, NULL, NULL, 2, NULL, 0, 'ROLL each slice of bread from apple end, gently but tightly. Lay seam side down. Add butter to griddle or pan to coat it. Mix egg, milk and vanilla in shallow bowl. Dip each roll in egg wash and place seam side down on pan. Lightly brown sandwich. Carefully turn until all sides are browned.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0D08C4B081C4DF4C8E50F2D1318F3407', X'D3804F763FDF2149BA38843E69158EFB', X'D3804F763FDF2149BA38843E69158EFB', 2, NULL, NULL, 2, NULL, 0, 'REMOVE from pan and sprinkle or roll with remaining cinnamon sugar to coat. Combine remaining 2 tablespoons of peanut butter and 2 tablespoons maple syrup in small heatproof dish. Heat for 30 seconds in microwave and mix until combined. Serve as dipping sauce for roll-up.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C85EB30873F5D8438A744AC99A035C7C', X'4F0321B8E0C1574597D183E88F965A44', X'4F0321B8E0C1574597D183E88F965A44', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE milk and chocolate spread in 2-cup microwave-safe glass measuring cup or small microwave-safe bowl on HIGH 2 minutes or until hot but not boiling. Whisk until smooth. Add cocoa mix, whisking until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E95D35C3585C7F409E1517A157B8DF34', X'4F0321B8E0C1574597D183E88F965A44', X'4F0321B8E0C1574597D183E88F965A44', 1, NULL, NULL, 2, NULL, 0, 'PLACE 2 tablespoons marshmallows into each of 2 (8 oz.) serving mugs. Pour hot cocoa mixture over marshmallows. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'98A21CF338DF1B4D882F7EFC13774B80', X'0E2BDAE3E6055A41AE9DFB509921392F', X'0E2BDAE3E6055A41AE9DFB509921392F', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Line rimmed baking sheet with foil.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AAEAEFE3BC472D48AA874D3C7E0E59CA', X'0E2BDAE3E6055A41AE9DFB509921392F', X'0E2BDAE3E6055A41AE9DFB509921392F', 1, NULL, NULL, 2, NULL, 0, 'COMBINE butter, peanut butter, vanilla and cinnamon in a microwave-safe bowl. Microwave on HIGH (100% power) 35 to 45 seconds, stirring until well blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B7B3E01D44EA1042A6D67A07F6451145', X'0E2BDAE3E6055A41AE9DFB509921392F', X'0E2BDAE3E6055A41AE9DFB509921392F', 2, NULL, NULL, 2, NULL, 0, 'COMBINE cereal, mixed nuts and pretzels in large bowl. Pour butter mixture over cereal mixture. Toss well to coat. Spread mixture evenly on foiled baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A51EC89ED2AEC45ABA6DA3FE5FB7A1F', X'0E2BDAE3E6055A41AE9DFB509921392F', X'0E2BDAE3E6055A41AE9DFB509921392F', 3, NULL, NULL, 2, NULL, 0, 'BAKE 10 to 12 minutes, stirring occasionally. Cool. Sprinkle lightly with cayenne pepper, if desired. Toss. Store in resealable food storage bag.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F48928B8643FF8478AAF2BE58A9F4949', X'20705F3BF4EEB049AEF165175B6ECACD', X'20705F3BF4EEB049AEF165175B6ECACD', 0, NULL, NULL, 2, NULL, 0, 'LINE 2 baking sheets with wax paper. Coat wax paper with cooking spray. Combine cereal squares, pretzels, cereal rings and peanuts in extra large bowl.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CEA7CD2E1CA6FF449A01D612BB912059', X'20705F3BF4EEB049AEF165175B6ECACD', X'20705F3BF4EEB049AEF165175B6ECACD', 1, NULL, NULL, 2, NULL, 0, 'COMBINE baking chips and shortening in large microwave-safe bowl. Microwave on HIGH 1 minute; stir. Microwave at additional 10 second intervals until completely melted and smooth when stirred. Stir in peanut butter until evenly blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2BF4F1032D3EAC44A89A725FB007F069', X'20705F3BF4EEB049AEF165175B6ECACD', X'20705F3BF4EEB049AEF165175B6ECACD', 2, NULL, NULL, 2, NULL, 0, 'POUR over cereal mixture. Stir until all of the cereal mixture is lightly coated. Stir in candy pieces. Spread evenly on prepared baking sheets. Chill 5 to 10 minutes or until firm. Break into small pieces. Store in airtight container.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6B8769633003FB4D82173B3764602DE6', X'331ADE1EA8709A4786850977FD359C6E', X'331ADE1EA8709A4786850977FD359C6E', 0, NULL, NULL, 2, NULL, 0, 'SPOON half of yogurt into two parfait glasses. Top with half of pineapple. Stir together peanut butter, water and honey until smooth. Drizzle half of peanut butter mixture over pineapple in parfait glasses. Top with half of granola cereal.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0A3C1FBB5A74C64A8B231259E6EF654F', X'331ADE1EA8709A4786850977FD359C6E', X'331ADE1EA8709A4786850977FD359C6E', 1, NULL, NULL, 2, NULL, 0, 'REPEAT layers using remaining yogurt, pineapple, peanut butter mixture and granola. Serve immediately.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4B53A9F839B28E4CB8AEDE8DE1201153', X'C7638D006AB3E14AAB99EC481D604904', X'C7638D006AB3E14AAB99EC481D604904', 0, NULL, NULL, 2, NULL, 0, 'CUT pita bread in half crosswise. Separate cut edge gently to create two pita bread pockets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'81333BEB682B5F45B568751B64ACC94F', X'C7638D006AB3E14AAB99EC481D604904', X'C7638D006AB3E14AAB99EC481D604904', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter on top, inside surface of pockets. Spread jam on bottom, inside surface of pockets. Fill pockets with banana and strawberries.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'04DD18B1D4C10E4084A41F0D787771CF', X'3F202485CA334C4A916B8EFF0607DEDB', X'3F202485CA334C4A916B8EFF0607DEDB', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D50BC1CFD903AD4FA10D1178E6268719', X'3F202485CA334C4A916B8EFF0607DEDB', X'3F202485CA334C4A916B8EFF0607DEDB', 1, NULL, NULL, 2, NULL, 0, 'COMBINE pumpkin, syrup, cashew butter and cinnamon in medium bowl until smooth. Place in 8inch square baking dish. Top with cashews.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0C4FD83491464546BA8D4C489500E4D6', X'3F202485CA334C4A916B8EFF0607DEDB', X'3F202485CA334C4A916B8EFF0607DEDB', 2, NULL, NULL, 2, NULL, 0, 'BAKE 30 to 35 minutes or until heated through. Serve warm with graham crackers, pretzels or apples.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9BCD64E4EE6E8E44AA1EA49DC6A263D7', X'A2992D9B2E3F824AA50A9D5351315840', X'A2992D9B2E3F824AA50A9D5351315840', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Cut donuts in half crosswise.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C284393B6793A459CDC8EC225E786FA', X'A2992D9B2E3F824AA50A9D5351315840', X'A2992D9B2E3F824AA50A9D5351315840', 1, NULL, NULL, 2, NULL, 0, 'SPREAD peanut butter spread between two halves. Press together to form sandwich. Repeat to make 12 sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D6F13E54F18DE44D87BE30ECE5D6B02A', X'A2992D9B2E3F824AA50A9D5351315840', X'A2992D9B2E3F824AA50A9D5351315840', 2, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate chips in small microwave-safe bowl 1 minute. Stir until smooth. Dip each sandwich halfway into melted chocolate. Cool 10 minutes on prepared baking sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EEBC9A3AB49CB8408B34DA9305EA8E19', X'F4FB690AB0AA7B4B9ED42952B1D5F86D', X'F4FB690AB0AA7B4B9ED42952B1D5F86D', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Microwave chocolate in medium microwave-safe bowl on HIGH 1 1/2 minutes. Stir until smooth.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7ED6AD38368FC143B02155BCB49C924F', X'F4FB690AB0AA7B4B9ED42952B1D5F86D', X'F4FB690AB0AA7B4B9ED42952B1D5F86D', 1, NULL, NULL, 2, NULL, 0, 'DIP each cracker halfway into chocolate. Sprinkle immediately with toffee bits, if desired. Place on prepared baking sheet. Chill 30 minutes.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0122971412207A4CA00C5DD970CC1AF1', X'F4FB690AB0AA7B4B9ED42952B1D5F86D', X'F4FB690AB0AA7B4B9ED42952B1D5F86D', 2, NULL, NULL, 2, NULL, 0, 'SPREAD 2 teaspoons peanut butter on inside of each cracker. Press 2 crackers together. Repeat with remaining crackers to make 24 snackwiches. Freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4828039F3123D449A19AE98FF8148649', X'AAE17BB6D5273E488879674A806BBF8F', X'AAE17BB6D5273E488879674A806BBF8F', 0, NULL, NULL, 2, NULL, 0, 'PLACE peanut butter gently into shallow bowl or platter. Make well in center of peanut butter. Place sriracha in center. Garnish with cilantro and peanuts. Serve with cucumbers, carrots, snap peas, crackers, pretzels or chips.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5223C84E84A4ED41B68E081CAC9D208E', X'846C6E453BC84544A6B8700BCA5DB0F1', X'846C6E453BC84544A6B8700BCA5DB0F1', 0, NULL, NULL, 2, NULL, 0, 'LAYER 1 (9-inch) pie plate with peanut butter, pretzels, caramel topping, whipped topping, coconut, chocolate chips and peanuts. Serve with crackers.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'799521D77E89994DADEB8F3F4D235338', X'E3E07DDE382EB14099C31D2E14EB6F70', X'E3E07DDE382EB14099C31D2E14EB6F70', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 450° F. Place crust on cookie sheet.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BEBC713FCA545146BCD859AD497D3E85', X'E3E07DDE382EB14099C31D2E14EB6F70', X'E3E07DDE382EB14099C31D2E14EB6F70', 1, NULL, NULL, 2, NULL, 0, 'MIX peanut butter, peanut satay sauce, sesame oil and red pepper flakes in small bowl until blended. Combine 1/4 cup peanut mixture with chicken in a medium bowl. Spread remaining peanut mixture over pizza crust. Top with chicken mixture and 1 1/2 cups cheese.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9CEF9ED16E20DF47BABF98EEA0204E2D', X'E3E07DDE382EB14099C31D2E14EB6F70', X'E3E07DDE382EB14099C31D2E14EB6F70', 2, NULL, NULL, 2, NULL, 0, 'BAKE 10 to 12 minutes or until cheese is bubbly and golden. Remove pizza from oven. Top with shredded carrots, green onion, cilantro and remaining 1/2 cup cheese. Cut and serve.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C4778C6ED6A4B44AD7D906BCBBF929F', X'78A2410F80543241AB6FC5726A8152D4', X'78A2410F80543241AB6FC5726A8152D4', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 400ºF. Spray a 12-cup muffin pan with a no-stick cooking spray or line with paper cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'74ADA6FC6CE3364D9C5A2A7897F22DF7', X'78A2410F80543241AB6FC5726A8152D4', X'78A2410F80543241AB6FC5726A8152D4', 1, NULL, NULL, 2, NULL, 0, 'STIR together flour, sugar, baking powder and salt in medium bowl. Combine egg, milk and oil in small bowl. Add liquids to flour mixture, stirring just until dry ingredients are moistened. Divide batter evenly among cups.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'60BC3B281CCC2449A6B5ED11A1F3999B', X'78A2410F80543241AB6FC5726A8152D4', X'78A2410F80543241AB6FC5726A8152D4', 2, NULL, NULL, 2, NULL, 0, 'COMBINE sugar, flour, peanut butter and salt in small bowl, mixing with fork until crumbly. Divide topping over muffins. Lightly stir into muffin with knife.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0841596C325074A972D9520C27331F6', X'78A2410F80543241AB6FC5726A8152D4', X'78A2410F80543241AB6FC5726A8152D4', 3, NULL, NULL, 2, NULL, 0, 'BAKE 18 to 23 minutes or until golden brown. Cool in pan on rack 5 minutes. Remove from pan to rack to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'58DA598C24C7E94889ABFE9D9E616690', X'F83A7CAA0C24F44DA0A1A8AA4BB5F32B', X'F83A7CAA0C24F44DA0A1A8AA4BB5F32B', 0, NULL, NULL, 2, NULL, 0, 'LAYER peanut butter, fruit spread and bacon on one side of 2 waffles. Place remaining waffles on top to form two sandwiches.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DB8CED339356FA48BF07005B3139711F', X'F83A7CAA0C24F44DA0A1A8AA4BB5F32B', X'F83A7CAA0C24F44DA0A1A8AA4BB5F32B', 1, NULL, NULL, 2, NULL, 0, 'HEAT panini press or large skillet over medium heat. Brush tops of sandwiches with oil. Turn and brush bottoms with remaining oil.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1F0C49C2F045284A9DCED814BA5F8503', X'F83A7CAA0C24F44DA0A1A8AA4BB5F32B', X'F83A7CAA0C24F44DA0A1A8AA4BB5F32B', 2, NULL, NULL, 2, NULL, 0, 'COOK sandwiches in skillet 2 to 3 minutes on each side or until golden brown. Remove from heat. Cut each sandwich in half.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'64C0551244AA104C967265FB82A8E8C7', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375ºF. Coat 12 x 8-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FC7EC9455DFBE54E9D1D0CDBE430D73D', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', 1, NULL, NULL, 2, NULL, 0, 'For crumb topping, BEAT 1/2 cup flour, 1/2 cup brown sugar, 1/2 cup peanut butter and 2 tablespoons softened butter in medium bowl with electric mixer at medium speed until crumbly. Stir in 1/2 cup miniature chocolate chips.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CBCCC420D671D045815914DB93318ACA', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', 2, NULL, NULL, 2, NULL, 0, 'BEAT remaining 1/2 cup peanut butter and 1/4 cup butter in large bowl at medium speed until blended. Beat in remaining 1 cup brown sugar. Add eggs, one a time, beating until fluffy after each addiiton.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4CA27932C830DD438A017154D7CC3C81', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', 3, NULL, NULL, 2, NULL, 0, 'COMBINE remaining 2 cups flour, baking soda and salt. Add alternately with milk and almond extract to creamed mixture. Stir in remaining 1/2 cup chocolate chips. Spread evenly in prepared pan. Sprinkle evenly with crumb topping.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DB8702FDD19D764F9999904A14DD4BF3', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', X'12FEF3CB3F4D5C4E8DA78B3F595BFEEA', 4, NULL, NULL, 2, NULL, 0, 'BAKE 30 to 35 minutes or until toothpick inserted in center comes out clean. Cool completely in pan on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C44B0F42D260C94DAB7715ADC9287D72', X'932C9411EBA24F4ABE5587E980C34435', X'932C9411EBA24F4ABE5587E980C34435', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F. Coat baking sheets with no-stick cooking spray.');
