INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7EC7E503943C004891153CB232649C9F', X'70DBEA47418949499CA44E6FBFEAE7E9', X'70DBEA47418949499CA44E6FBFEAE7E9', 0, NULL, NULL, 2, NULL, 0, 'COMBINE sugar, corn syrup and salt in large glass bowl. Microwave about 3 minutes or until sugar is dissolved.');
