INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F5160517AF6D2C4BB11758A35B2385F2', X'70DBEA47418949499CA44E6FBFEAE7E9', X'70DBEA47418949499CA44E6FBFEAE7E9', 2, NULL, NULL, 2, NULL, 0, 'MICROWAVE butter and brown sugar in small glass bowl 45 seconds or until melted. Add milk and vanilla. Stir in powdered sugar; beat until smooth.');
