INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F30D65EE7BE97D4481CC6DD92F40AC3C', X'94BF8F927ADE5C49BECD257BE70EA29B', X'94BF8F927ADE5C49BECD257BE70EA29B', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Combine cereal and peanuts in large bowl.');
