INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F308E3AE681BB44A80135CCD5F0E89E9', X'C7C4EB305A58F74FA267F5B6997A0BB2', X'C7C4EB305A58F74FA267F5B6997A0BB2', 1, NULL, NULL, 2, NULL, 0, 'COMBINE brown sugar, peanut butter, shortening, banana, milk, vanilla and almond extracts in bowl of electric mixer; beat at medium speed until well blended. Add egg; beat just until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'346BF72DE1F8C447BBEB1BFE6F95F222', X'C7C4EB305A58F74FA267F5B6997A0BB2', X'C7C4EB305A58F74FA267F5B6997A0BB2', 2, NULL, NULL, 2, NULL, 0, 'COMBINE flour, baking soda and salt in medium bowl. Add to shortening mixture; beat at low speed just until blended. Stir in chocolate chunks and nuts, if desired. Drop dough by rounded measuring tablespoonfuls 2 inches apart onto ungreased baking sheets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'68D66C85CDAA9F4CB4AA7EE57CE07717', X'C7C4EB305A58F74FA267F5B6997A0BB2', X'C7C4EB305A58F74FA267F5B6997A0BB2', 3, NULL, NULL, 2, NULL, 0, 'BAKE 11 to 13 minutes or until cookies are light brown around edges. Cool 2 minutes on baking sheet; transfer to cooling racks to cool completely.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C9DCCB5FB6FFD444916424C8DD63571B', X'C7C4EB305A58F74FA267F5B6997A0BB2', X'C7C4EB305A58F74FA267F5B6997A0BB2', 4, NULL, NULL, 2, NULL, 0, 'A combination of milk chocolate and semi-sweet chocolate chunks can be used.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0EB5832E716DA47B6A07EA07760F593', X'5877D789DFA55E4BB961DB051424FA42', X'5877D789DFA55E4BB961DB051424FA42', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 375ºF. Coat 12 x 8-inch baking pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D8D2A6E7F84DDE478A4F3F894AE4369B', X'5877D789DFA55E4BB961DB051424FA42', X'5877D789DFA55E4BB961DB051424FA42', 1, NULL, NULL, 2, NULL, 0, 'For crumb topping, BEAT 1/2 cup flour, 1/2 cup brown sugar, 1/2 cup peanut butter and 2 tablespoons softened butter in medium bowl with electric mixer at medium speed until crumbly. Stir in 1/2 cup miniature chocolate chips.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E9FBB8BA27A81E4C8FBDBF59532CA6ED', X'5877D789DFA55E4BB961DB051424FA42', X'5877D789DFA55E4BB961DB051424FA42', 2, NULL, NULL, 2, NULL, 0, 'BEAT remaining 1/2 cup peanut butter and 1/4 cup butter in large bowl at medium speed until blended. Beat in remaining 1 cup brown sugar. Add eggs, one a time, beating until fluffy after each addiiton.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0ACC59EACB745B43B575A6A58BC60544', X'5877D789DFA55E4BB961DB051424FA42', X'5877D789DFA55E4BB961DB051424FA42', 3, NULL, NULL, 2, NULL, 0, 'COMBINE remaining 2 cups flour, baking soda and salt. Add alternately with milk and almond extract to creamed mixture. Stir in remaining 1/2 cup chocolate chips. Spread evenly in prepared pan. Sprinkle evenly with crumb topping.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'25879FE61138F445989F12E313BEBD33', X'5877D789DFA55E4BB961DB051424FA42', X'5877D789DFA55E4BB961DB051424FA42', 4, NULL, NULL, 2, NULL, 0, 'BAKE 30 to 35 minutes or until toothpick inserted in center comes out clean. Cool completely in pan on wire rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'24C50897D573CA4C9A31F147784241B5', X'C14EF5074A06E349992D676366913A60', X'C14EF5074A06E349992D676366913A60', 0, NULL, NULL, 2, NULL, 0, 'MICROWAVE butterscotch chips, peanut butter and shortening in large microwave-safe bowl 1 minute. Stir. Microwave at addiitonal 15-second intervals until completely melted and smooth when stirred.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'31D51B756231984FB9392EADE9D94C34', X'C14EF5074A06E349992D676366913A60', X'C14EF5074A06E349992D676366913A60', 1, NULL, NULL, 2, NULL, 0, 'STIR in corn flakes until evenly coated. Stir in chocolate chips.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F6DDA3835CCEC74F86D72D4936CC9FCD', X'C14EF5074A06E349992D676366913A60', X'C14EF5074A06E349992D676366913A60', 2, NULL, NULL, 2, NULL, 0, 'DROP by heaping tablespoonful onto wax paper-lined baking sheets. Sprinkle with peanuts. Chill until firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3EADA6935AAB6A438014152FCD07573B', X'6B8C18911B00874188F6A606577448F6', X'6B8C18911B00874188F6A606577448F6', 0, NULL, NULL, 2, NULL, 0, 'LINE baking sheet with wax paper. Spread 1 teaspoon peanut butter on 20 banana slices. Top with remaining slices. Insert party forks into each bite. Place on prepared baking sheet. Freeze 1 hour.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE8DEEB7096FCE44AE4CF864DB3C0B91', X'6B8C18911B00874188F6A606577448F6', X'6B8C18911B00874188F6A606577448F6', 1, NULL, NULL, 2, NULL, 0, 'MICROWAVE chocolate chips in medium microwave-safe bowl on HIGH 1 1/2 minutes. Stir until smooth. Dip each bite into chocolate, coating completely. Sprinkle immediately with coconut, if desired.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8BD3844A8BEDE94E987A3323FDA7506B', X'6B8C18911B00874188F6A606577448F6', X'6B8C18911B00874188F6A606577448F6', 2, NULL, NULL, 2, NULL, 0, 'FREEZE 30 minutes or until ready to serve.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5AFDBA3E1ED0334E9C1ACFFF409C3F0D', X'C6D61FA69F935C47BB1C7BA7E94635E4', X'C6D61FA69F935C47BB1C7BA7E94635E4', 0, NULL, NULL, 2, NULL, 0, 'HEAT French toast in toaster according to package directions until golden brown. Cut into sticks. Place on serving plate.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BB0420C48967CB4CA3613B7644878E07', X'C6D61FA69F935C47BB1C7BA7E94635E4', X'C6D61FA69F935C47BB1C7BA7E94635E4', 1, NULL, NULL, 2, NULL, 0, 'SPRINKLE with cinnamon sugar. Spread with peanut butter and jam.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'902747759FA4A144809E7B2A357D1BD6', X'5B0986EC9845104DA1CCB73F1578AA65', X'5B0986EC9845104DA1CCB73F1578AA65', 0, NULL, NULL, 2, NULL, 0, 'SPREAD 1 tablespoon chocolate spread on each pizzelle. Spoon 1/4 cup ice cream onto each of 4 pizzelles, using small spoonfuls. Top with remaining pizelles to make a sandwich, pressing carefully until ice cream is even around the edges.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E53DBECCEE11E54EA89A2D46F0627D1E', X'5B0986EC9845104DA1CCB73F1578AA65', X'5B0986EC9845104DA1CCB73F1578AA65', 1, NULL, NULL, 2, NULL, 0, 'FREEZE 20 minutes or until ice cream is firm.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3C40F9C4C7348B4EAE12F434FCDBB8AD', X'3E9977C534DE184EAB264A2A73419F35', X'3E9977C534DE184EAB264A2A73419F35', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350ºF. Spray a 9 x 5 x 3-inch loaf pan with no-stick cooking spray.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6BAAF76AE197DE4FA08BE42813ECF456', X'3E9977C534DE184EAB264A2A73419F35', X'3E9977C534DE184EAB264A2A73419F35', 1, NULL, NULL, 2, NULL, 0, 'COMBINE flour, sugar, baking powder and salt in large bowl. Add banana, peanut spread, milk, oil and egg. Beat at medium speed of electric mixer, scraping sides and bottom of bowl. Mix just until blended. Pour into prepared pan.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4A6E9AE9C9540945AFD16CAE1F79E3FD', X'3E9977C534DE184EAB264A2A73419F35', X'3E9977C534DE184EAB264A2A73419F35', 2, NULL, NULL, 2, NULL, 0, 'BAKE 60 to 65 minutes or until toothpick inserted in center comes out clean. (Cover top loosely with foil after 45 minutes to prevent over-browning.)');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C9BF9C569039F74BB808EDF6C8970B55', X'3E9977C534DE184EAB264A2A73419F35', X'3E9977C534DE184EAB264A2A73419F35', 3, NULL, NULL, 2, NULL, 0, 'COOL 10 minutes in pan. Remove to cooling rack.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F062C2F00A70524BA787224A6A68610B', X'E1F1E713D9A6FF4186D002D7553E5289', X'E1F1E713D9A6FF4186D002D7553E5289', 0, NULL, NULL, 2, NULL, 0, 'HEAT oven to 350°F.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'65481C647D550A4B934F80129986598C', X'E1F1E713D9A6FF4186D002D7553E5289', X'E1F1E713D9A6FF4186D002D7553E5289', 1, NULL, NULL, 2, NULL, 0, 'BEAT shortening, peanut butter, brown sugar and sugar in large bowl with mixer on medium speed until well blended. Beat in egg and vanilla until blended.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'61553AC2FDD57E49A5DD46B7D97FA331', X'E1F1E713D9A6FF4186D002D7553E5289', X'E1F1E713D9A6FF4186D002D7553E5289', 2, NULL, NULL, 2, NULL, 0, 'STIR flour, cocoa, baking powder and baking soda in medium bowl until blended. Add to shortening mixture, beating at low speed until blended. Stir in chocolate chips. Drop by rounded tablespoonfuls 2 inches apart onto baking sheets.');
INSERT INTO `VWR INTERNAL Jif recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0664A8DD5856943BC15EE81A8CCCBAF', X'E1F1E713D9A6FF4186D002D7553E5289', X'E1F1E713D9A6FF4186D002D7553E5289', 3, NULL, NULL, 2, NULL, 0, 'BAKE 10 to 12 minutes or until edges are lightly browned. Cool 2 minutes on baking sheets. Remove to wire rack to cool completely.');
