INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB7395E25185A24E92D51B5E65F91D2B', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 0, NULL, NULL, 2, NULL, 0, 'Prepare crust by adding pecans, dates, and a pinch of salt to a food processor. Pulse until the mixture becomes sticky and cumbled. If your dates are very dry feel free to add up to a tablespoon of water. Press the crust into a 9 inch pie dish and set in fridge.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C8A129DFDB23947B3420D2739EC1256', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 1, NULL, NULL, 2, NULL, 0, 'Make VFR''s healthy caramel dip (double it!). It will make 1 cup. Spread 3/4 of a cup on the bottom of the crust.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C06D3FA91DFDC40B4046A823A8F4D2E', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 2, NULL, NULL, 2, NULL, 0, 'Clean out the food processor and add all chocolate filling ingredients to it. Pulse the soaked cashews, dates, coconut oil, and cacao until smooth. The mixture will be thick. Spread chocolate filling over the Caramel layer.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6930D6DABADA0A449E268CBD49F876B2', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 3, NULL, NULL, 2, NULL, 0, 'Heat remaining 1/4 of a cup caramel dip in a water bath until warm and soft. Add to a piping bag with a small round tip and drizzle across the top of the pie.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'716CEE8610B1F64EA4E8A952D4FFB30F', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 4, NULL, NULL, 2, NULL, 0, 'Decorate Pie with pecans.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B118E9493658749822397C9EC487035', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 5, NULL, NULL, 2, NULL, 0, 'Whip coconut cream with an electric mixer. until thick and creamy. Add to a piping bag and pipe decorative whipped coconut cream around the pie.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D15FC62C1EE9DE4786846253A765C5A9', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 6, NULL, NULL, 2, NULL, 0, 'Cool pie in fridge for at least an hour before slicing and serving.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FCEDFCE7C3F954782F9B57A4A0F085C', X'D45CFA14ECD2EE42887B5B24976D6A25', X'D45CFA14ECD2EE42887B5B24976D6A25', 7, NULL, NULL, 2, NULL, 0, 'Pie will keep covered in fridge for up to 5 days.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FE0576ABE44A347A182068F491E8588', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 0, NULL, NULL, 2, NULL, 0, 'Make your own homemade pie crust recipe, make VFR''swhole wheat pie crust, or use a store bought crust.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B15A52BD273AA479A29422448308BE1', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 410F (210C).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A44A2D20C721843B3B32BD97744863B', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 2, NULL, NULL, 2, NULL, 0, 'Mix cranberries, blueberries, flour, sugar, salt, lemon juice, and cinnamon together in a bowl and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2667CC78933B9E4ABD29599D8B57168C', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 3, NULL, NULL, 2, NULL, 0, 'Roll out pie crust to fit a pie pan. Fill with berry mixture.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'773DB642737BEC45BF16429F0F277C4E', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 4, NULL, NULL, 2, NULL, 0, 'Use remaining pie crust to make a top crust. Weave a lattice top or cut slits into the top crust for the steam to escape.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AD043E690B4C643B01370EF6728E549', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 5, NULL, NULL, 2, NULL, 0, 'Bake pie for 15 minutes at 410F (210C), then lightly cover with aluminum foil and cook for another 50 minutes at 375F (190C). Remove blueberry cranberry pie from oven. If pie is not golden brown yet, return it to the oven and bake (without aluminum foil) for an additional 5 - 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B5F7A1BE8007A4CA3400ED824C0CF4B', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 6, NULL, NULL, 2, NULL, 0, 'Allow blueberry cranberry pie to fully cool before cutting and serving.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49735A439793A744867F87A66C9024A8', X'BD36440D02B9BB42BD7A7071077D2096', X'BD36440D02B9BB42BD7A7071077D2096', 7, NULL, NULL, 2, NULL, 0, 'Pie will keep in fridge for up to 3 days.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'200FDCA316BA314EADAF612F64B62406', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 0, NULL, NULL, 2, NULL, 0, 'Cut butter into small chunks and place in a bowl in the freezer.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D102CF426CC0624492D14C3C8BD6B196', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 1, NULL, NULL, 2, NULL, 0, 'Mix together sifted flour, sugar, and salt.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47C4B3727A1C52499D991D0A7CAA73A3', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 2, NULL, NULL, 2, NULL, 0, 'Use a mixer with a beater attachment or pastry cutter to beat the butter, and flour mixture together until very crumbly.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A17822A6465904F899B75F6F478FF68', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 3, NULL, NULL, 2, NULL, 0, 'Add a few tablespoons of cold water to the mixture and mix with dough hooks. Add more water as needed until the dough comes together nicely.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F3982CC93944F4B8381B8E0ACAE2BE4', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 4, NULL, NULL, 2, NULL, 0, 'On a lightly floured, flat surface, roll dough into a ball and press flat into a disk. Wrap in plastic wrap and place in fridge for at least two hours. Alternatively, if you are in a time crunch, place dough in freezer for 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'087637DC7BD06447AC765CB90597712B', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 5, NULL, NULL, 2, NULL, 0, 'Remove dough from fridge and cut in half. Roll out 1 half of the dough to fit a 9 inch pie plate. Work quickly to avoid the streaks of butter going through the dough from melting. Put other half back into the fridge. Place rolled out pie dough into pie form and press into the bottom and the sides evenly.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9B4092E695168439235DCDE7A9F5A62', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 6, NULL, NULL, 2, NULL, 0, 'Put back into the fridge.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F92BEE0B1E41FB43A9A9F6F07C735470', X'95C2D3D9BB56884084F6885595B9A93F', X'95C2D3D9BB56884084F6885595B9A93F', 7, NULL, NULL, 2, NULL, 0, 'Continue here to make the pie filling and lattice.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A578A2E6A46284AAD1E42BAD9BD2FEA', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 0, NULL, NULL, 2, NULL, 0, 'Prepare whole wheat pie crust according to instructions or use your own.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2CEC761574D4C499ECAD0E4FD6610F7', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 410F (210C).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5112130436CEE1488678571A0F69742F', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 2, NULL, NULL, 2, NULL, 0, 'Mix apples together with flour, sugar, cinnamon, all spice, and salt.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BC62EACCB3D854FAE45253F6B69789E', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 3, NULL, NULL, 2, NULL, 0, 'Fill pie with apple mixture. Place in fridge.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D89ED87331BA24B947C30075D017624', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 4, NULL, NULL, 2, NULL, 0, 'Take remaining half of the previously made pie crust and roll it out thinly on a lightly floured, flat surface. Try not to touch the dough very much as you don''t want the butter to melt.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36908DEF57040A4E9F78F20B6C2FAECD', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 5, NULL, NULL, 2, NULL, 0, 'Cut the dough into 1 inch long slices. Weave a lattice crust with the slices. Cutting off the edges of the dough when they are too long.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56ABE35291C7FA4F8F264FCA930EF315', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 6, NULL, NULL, 2, NULL, 0, 'Bake pie for 15 minutes at 410F (210C), then lightly cover with aluminum foil and cook for another 50 minutes at 375F (190C). Remove apple pie from oven. If pie is not golden brown yet, return it to the oven and bake (without aluminum foil) for an additional 5 - 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'373837759397B04B9480B9CF312EE5BC', X'56CDA4BF202BB54D9EA54CB5253156BD', X'56CDA4BF202BB54D9EA54CB5253156BD', 7, NULL, NULL, 2, NULL, 0, 'Allow apple pie to fully cool before cutting and serving.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6002C28C00F1C7478E0A2A789E980C34', X'E6E1FCD2029C1F4D945281B82D045DC5', X'E6E1FCD2029C1F4D945281B82D045DC5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F (180C)');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'882546DA3C6D274DA3225B353545E31A', X'E6E1FCD2029C1F4D945281B82D045DC5', X'E6E1FCD2029C1F4D945281B82D045DC5', 1, NULL, NULL, 2, NULL, 0, 'Dice pear and mix together with cranberries. Divide between two ramekins until each is roughly 2/3 full.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E850B9B28721FB4987012E5389757C98', X'E6E1FCD2029C1F4D945281B82D045DC5', X'E6E1FCD2029C1F4D945281B82D045DC5', 2, NULL, NULL, 2, NULL, 0, 'Mix together remaining ingredients. This is best done with your hands. Crumble oat mixture over each pear filled ramekin.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4719D405C59FA84899B76C3843B5D43B', X'E6E1FCD2029C1F4D945281B82D045DC5', X'E6E1FCD2029C1F4D945281B82D045DC5', 3, NULL, NULL, 2, NULL, 0, 'Bake on lowest rack in oven for roughly 30 minutes. Cranberry pear crisp is done when fruit juices are bubbling, oats are crisp and browned.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35B96F9C7EE8AF409F4FCEB7A844F89F', X'E6E1FCD2029C1F4D945281B82D045DC5', X'E6E1FCD2029C1F4D945281B82D045DC5', 4, NULL, NULL, 2, NULL, 0, 'Remove ramekins from oven, let cool for a few minutes, and eat warm.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'331765A3F545B84B82464ED1E16C5310', X'91CB6B8FFB8E594A97F5D9DB930AE400', X'91CB6B8FFB8E594A97F5D9DB930AE400', 0, NULL, NULL, 2, NULL, 0, 'Place a 7 inch (18cm) Springform on a sheet of parchment paper. Trace a circle around it and cut it out. Place the parchment paper circle and springform aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'614DB5E906B57C4BB4F2C331A324585A', X'91CB6B8FFB8E594A97F5D9DB930AE400', X'91CB6B8FFB8E594A97F5D9DB930AE400', 1, NULL, NULL, 2, NULL, 0, 'Prepare crust by pulsing dates and almonds in a food processor until a sticky mass forms. Press this into the base of the springform.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35FF02E7147F324D9D3F0819FCB799A6', X'91CB6B8FFB8E594A97F5D9DB930AE400', X'91CB6B8FFB8E594A97F5D9DB930AE400', 2, NULL, NULL, 2, NULL, 0, 'Clean out food processor and add coconut cream and frozen strawberries. Pulse until smooth. Add up to 1(3 of a cup coconut water if food processor is having difficulties. Taste filling and add a sweetener of choice (I prefer maple syrup) if it is not sweet enough to your liking. This all depends on the sweetness of the strawberries to begin with. Spread the strawberry ice cream filling over the crust in the spring form, and place prepared parchment paper circle on the top. Place in freezer for 2-3 hours.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'499F3EA850F28C40B9B8D3685AC0EF87', X'91CB6B8FFB8E594A97F5D9DB930AE400', X'91CB6B8FFB8E594A97F5D9DB930AE400', 3, NULL, NULL, 2, NULL, 0, 'Once the strawberry ice is frozen remove the parchment paper and prepare the strawberry topping by pulsing 1 date with 6 strawberries in a food processor. If the strawberries are very small add 2 more. Spread strawberry-date topping over the ice cream layer.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A37F3F16038CC41856E3F9AE9C96F07', X'91CB6B8FFB8E594A97F5D9DB930AE400', X'91CB6B8FFB8E594A97F5D9DB930AE400', 4, NULL, NULL, 2, NULL, 0, 'Garnish with 12 small strawberries or more if desired.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A539771A682094EA01676A0F11006DC', X'91CB6B8FFB8E594A97F5D9DB930AE400', X'91CB6B8FFB8E594A97F5D9DB930AE400', 5, NULL, NULL, 2, NULL, 0, 'Serve cold and slightly thawed. Cake will keep in freezer for up to two weeks.');
