INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B918EA3050224945AF9363C510B0B5E3', X'174A262679702646838A8C76AD08437E', X'174A262679702646838A8C76AD08437E', 4, NULL, NULL, 2, NULL, 0, 'Drop cookie dough (tablespoon size) on cookie sheet. Bake for 10-12 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'228B95EC1C554946AD0E37D871779FB6', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400F (200C)');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C1C3E3AE9049A4091F1F8C4099D98FE', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 1, NULL, NULL, 2, NULL, 0, 'Peel, deseed and dice Pumpkin into 1 to 2 inch cubes. Toss together with other filling ingredients and place in a baking dish or on a sheet pan. Bake in oven for 20 - 30 minutes. Depending on the size of your cubes the faster they will cook.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A153E99D46157845B208932E27B28034', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, place all ingredients listed under hazelnut topping in a medium sized saucepan. Stir frequently over low-medium heat. Do not let the hazelnuts burn. Remove saucepan from heat once hazelnuts have absorbed all of the liquid and are golden brown. This should take about 15-20 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD7DC5EBED80C746B533AE5AFD3AA8FE', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 3, NULL, NULL, 2, NULL, 0, 'Remove Pumpkin from Oven when it is soft. Peel of skin and mash pumpkin flesh with a fork. If Pumpkin is too hard, return to oven for another 5 minutes. Allow pumpkin to slightly cool.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65EC0178B88A3B45A428405F04CA3AD8', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 4, NULL, NULL, 2, NULL, 0, 'Spread pumpkin mixture on the puff pastry sheet with a 1 inch border and top with 2/3 of hazelnut topping. Working quickly and touching the puff pastry as little as possible.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A954569EC4AFA45AF53C9C5F032A16F', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 5, NULL, NULL, 2, NULL, 0, 'Fold dough over (like a brochure) and seal the ends. Brush with vegetable oil and bake in oven for 20 - 30 minutes until puff pastry is golden brown.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36406CF577AE3A4287D22292472E297E', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 6, NULL, NULL, 2, NULL, 0, 'While strudel in baking, melt the chocolate in a double boiler or microwave.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7138A2BD073BD6459AE7F2E1E97616A4', X'0456F493A639B54DA893258B068F7633', X'0456F493A639B54DA893258B068F7633', 7, NULL, NULL, 2, NULL, 0, 'Remove strudel from oven. Drizzle with melted chocolate and top with remaining hazelnut topping. Serve hot.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4DEA7E6D160E54AA747A2661BF8C97C', X'2EBBB07AD9B9EC45B029875572A3C130', X'2EBBB07AD9B9EC45B029875572A3C130', 0, NULL, NULL, 2, NULL, 0, 'Cream together butter, sugar, and vanilla.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AB9008B5F8B74428ACEC29A4C559239', X'2EBBB07AD9B9EC45B029875572A3C130', X'2EBBB07AD9B9EC45B029875572A3C130', 1, NULL, NULL, 2, NULL, 0, 'Melt chocolate in a double boiler or microwave. Add to butter mixture and continue to beat on high for 2 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6C2BE0D5FDEEE4EBAF1451A1E90C65A', X'2EBBB07AD9B9EC45B029875572A3C130', X'2EBBB07AD9B9EC45B029875572A3C130', 2, NULL, NULL, 2, NULL, 0, 'Fill into piping bag and refrigerate for 10 minutes before use.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D84DDD2FEE52B42AF2B1BA1BC5A7B30', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 355F (180C). Grease and flour muffin pan or line with muffin cups.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE5DC9C4F90DA24B858D934B8FA8C5F8', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', 1, NULL, NULL, 2, NULL, 0, 'Cream together sugar, mashed bananas, oil and milk in a medium sized mixing bowl. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A678BA2084268B45AE566614FC8E294D', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', 2, NULL, NULL, 2, NULL, 0, 'Mix flour, baking powder and salt together. Add to bananas mixture with a wooden spoon. Fold in cranberries and chocolate.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FAE2C67A5F73D408A61EA9D069D4F55', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', 3, NULL, NULL, 2, NULL, 0, 'Fill each muffin cup 2/3 full with mixture and bake in oven for 20 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2073D06F8631C4A8C500249EB0295A5', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', X'ED4B9F6FD0DB1046BD2F52C07CF17B1C', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool completely before decorating with frosting.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E0C1F769EF08E4E825016FE532D22C3', X'383F9944BE36A24CA810CC3D2F7BC9D5', X'383F9944BE36A24CA810CC3D2F7BC9D5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 355F (180C) and line a cookie sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6DFEEC341EDE442AC3A42B2C9E7CAF9', X'383F9944BE36A24CA810CC3D2F7BC9D5', X'383F9944BE36A24CA810CC3D2F7BC9D5', 1, NULL, NULL, 2, NULL, 0, 'Combine flours, sugar, butter, nuts, vanilla, and salt in a medium mixing bowl. Mix for 4 minutes using a dough hook or knead with your hands until the mixture is well combined and there are no large chunks of butter.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'739E5FF7D0C2B44BA8AECF786CDEDF1D', X'383F9944BE36A24CA810CC3D2F7BC9D5', X'383F9944BE36A24CA810CC3D2F7BC9D5', 2, NULL, NULL, 2, NULL, 0, 'Using your hands, form 3 different sized balls ( 1 3/4 inch, 1 1/4 inch, 3/4 inch). Place the large balls at the back of the cookie sheet and the smaller towards the front when placing in the oven. Bake for 11-14 minutes. The cookies should gain a slight amount of brownish color.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1771BF0EE3B755458B10439DC695F669', X'383F9944BE36A24CA810CC3D2F7BC9D5', X'383F9944BE36A24CA810CC3D2F7BC9D5', 3, NULL, NULL, 2, NULL, 0, 'Remove cookies from oven and allow them to cool completely on a cookie rack.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E1A71D26C559B49BEB857222548A59F', X'383F9944BE36A24CA810CC3D2F7BC9D5', X'383F9944BE36A24CA810CC3D2F7BC9D5', 4, NULL, NULL, 2, NULL, 0, 'Once cookies have cooled. Melt chocolate over a double boiler or microwave.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51DD127FA8127B44AE8055330A512F4F', X'383F9944BE36A24CA810CC3D2F7BC9D5', X'383F9944BE36A24CA810CC3D2F7BC9D5', 5, NULL, NULL, 2, NULL, 0, 'Fill melted chocolate into a pastry bag with a medium sized round tip and place a small amount of chocolate on the top of a large ball. Quickly place a medium sized ball on top, add chocolate and finish with the smallest ball. Continue this until you have used up all the cookie balls.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A9A861A599ECF478A1946402EF8B304', X'383F9944BE36A24CA810CC3D2F7BC9D5', X'383F9944BE36A24CA810CC3D2F7BC9D5', 6, NULL, NULL, 2, NULL, 0, 'Allow for Chocolate to melt completely, then cover in powdered sugar. Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE9E2F08BD480A4095F67018D65347B5', X'254E954ADB6D42448558D0F39BA27706', X'254E954ADB6D42448558D0F39BA27706', 0, NULL, NULL, 2, NULL, 0, 'Line a 10 inch square baking pan with aluminum foil.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9F3CABBAB876341BF07FA3F560F10A6', X'254E954ADB6D42448558D0F39BA27706', X'254E954ADB6D42448558D0F39BA27706', 1, NULL, NULL, 2, NULL, 0, 'Combine butter, sugar and maple syrup in a medium saucepan. Bring to a boil over medium-high heat then lower temperature and simmer. Continue to simmer until the sugar mixture reaches a temperature of 305F(150C). This is the hard-crack stage. This will take about 30 minutes. It is best to use a candy thermometer. If you don''t have one see my notes on the bottom.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'390958A239015840AC479E70D6FD7F54', X'254E954ADB6D42448558D0F39BA27706', X'254E954ADB6D42448558D0F39BA27706', 2, NULL, NULL, 2, NULL, 0, 'Once sugar has reached the correct temperature stir in salt and quickly pour it into the lined baking pan and let cool.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE8319D4789E474088CC644BB9F83556', X'254E954ADB6D42448558D0F39BA27706', X'254E954ADB6D42448558D0F39BA27706', 3, NULL, NULL, 2, NULL, 0, 'Melt chocolate using a double boiler or carefully in the microwave. Pour Chocolate over slightly cooled toffee. Top with coconut and hazelnuts.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'075246D8CD5E8440BA8DC491C0B19292', X'254E954ADB6D42448558D0F39BA27706', X'254E954ADB6D42448558D0F39BA27706', 4, NULL, NULL, 2, NULL, 0, 'Let cool completely until chocolate sets and becomes hard. This is will take several hours.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'387D984CC4B9104C87E2D8B495CA2A6E', X'254E954ADB6D42448558D0F39BA27706', X'254E954ADB6D42448558D0F39BA27706', 5, NULL, NULL, 2, NULL, 0, 'Remove from pan and cut into squares using a large sharp knife. Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'729EA6D1782B554F996CF9604E193B3C', X'21F3545D63DE454DA50155161D44D6D5', X'21F3545D63DE454DA50155161D44D6D5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F (190C). Grease and flour a 9x9 inch pan.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38450D2091944346879C869D2A4C8838', X'21F3545D63DE454DA50155161D44D6D5', X'21F3545D63DE454DA50155161D44D6D5', 1, NULL, NULL, 2, NULL, 0, 'Over medium-low heat, while constantly stirring, melt 7 ounces of chocolate, butter, sugar and coffee. Be careful not to burn the chocolate. The melted mixture will look a bit grainy due to the coffee.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EF18DA4E554304380168D933019ACA1', X'21F3545D63DE454DA50155161D44D6D5', X'21F3545D63DE454DA50155161D44D6D5', 2, NULL, NULL, 2, NULL, 0, 'Allow melted mixture to cool slightly then add remaining ingredients (other 7 oz of chocolate, applesauce, flours, salt, and baking soda) and mix together with a wooden spoon or spatula.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA8B4B060D920B4C966C2F06D681481B', X'21F3545D63DE454DA50155161D44D6D5', X'21F3545D63DE454DA50155161D44D6D5', 3, NULL, NULL, 2, NULL, 0, 'Pour mixture into square pan and bake in oven for 30-35 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F70A91374A5B542985CB7E447050465', X'21F3545D63DE454DA50155161D44D6D5', X'21F3545D63DE454DA50155161D44D6D5', 4, NULL, NULL, 2, NULL, 0, 'Allow brownies to cool, cut into squares and enjoy.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6ADDF7FECB801447A094042AA9B2AE7A', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F (190C). Line baking sheet with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4C272BAC6BA174AA96580B5A0A2FD28', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 1, NULL, NULL, 2, NULL, 0, 'Make vegan `buttermilk` by whisking any plant-based milk with apple cider vinegar. Let stand for 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A75849503CFAF84C8E236532D36EBF75', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 2, NULL, NULL, 2, NULL, 0, 'Mix all dry ingredients in a large bowl. Add small chunks of margarine/vegan butter to the bowl. Use your hands to rub butter into flour mixture until crumbs form.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC1E5E82BBE12340B2ED711E22124FFB', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 3, NULL, NULL, 2, NULL, 0, 'Mix the `vegan buttermilk`, pureed banana and maple syrup. Add it to the dry ingredients and combine with a wooden spoon. Don''t overdo it!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06CBAB4A4A405243BAB8085572556B2E', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 4, NULL, NULL, 2, NULL, 0, 'Carefully mix in chocolate, almonds, and frozen raspberries. Again, be careful not to overmix the dough.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EAC4C711F345543BA373A7F91FD94FD', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 5, NULL, NULL, 2, NULL, 0, 'The dough should be sticky, see notes if it is too wet. Place entire dough onto a floured counter top. Form a circle out of the dough about 10 Inches in diameter.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4093410BC6AC74293C4486AA0DF0BB3', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 6, NULL, NULL, 2, NULL, 0, 'Cut the circle into and frozen even slices (the way you would cut a pizza). Place the scones 2 inches apart on the prepared baking sheet and dust the top of each scone with sugar (optional).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18841B3C5AA2E24E94C93B68F42169B3', X'A0A64A6383CD7A418694331BC616F9DD', X'A0A64A6383CD7A418694331BC616F9DD', 7, NULL, NULL, 2, NULL, 0, 'Bake for 16-18 minutes. until a toothpick inserted in the middle of a scone comes out clean. Remove the scones from the oven and allow them to cool for 10 minutes on a cooling rack before eating. Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11A4D38CB4770244A98DC57BFC86680B', X'2A7D3CE8FAA7354F979319EB0F0F6098', X'2A7D3CE8FAA7354F979319EB0F0F6098', 0, NULL, NULL, 2, NULL, 0, 'Blend together all ice cream ingredients until smooth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2279160BF17B6E4F96B8E65249CA3557', X'2A7D3CE8FAA7354F979319EB0F0F6098', X'2A7D3CE8FAA7354F979319EB0F0F6098', 1, NULL, NULL, 2, NULL, 0, 'Add to ice cream maker and make according to instructions, or place in a stainless steel bowl. Place in a freezer and using a spoon scrape sides and mix every 30 - 45 minutes. Continue to do this until mixture arrives at an ice cream consistency (about 3-4 hours).');
