INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C84B4C653D6AF546864A4E559B447A09', X'E0D694059DD53B41B3BE081686FCDAFE', X'E0D694059DD53B41B3BE081686FCDAFE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F (190C). Grease and flour 4 ramekins.');
