INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A181F2A91F08F149977A7E55D71B3BCA', X'E0D694059DD53B41B3BE081686FCDAFE', X'E0D694059DD53B41B3BE081686FCDAFE', 3, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of the chocolate batter to 4 ramekins (can be anywhere between 6 - 8 ounces in size)');
