INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44246D0BC1C5F54AA5EC4DA470040A27', X'B638C12D4B4DCE49B225490E317D5F58', X'B638C12D4B4DCE49B225490E317D5F58', 0, NULL, NULL, 2, NULL, 0, 'Line a 7 inch springform with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5243898A951E84DBAC30C80BD7989F1', X'B638C12D4B4DCE49B225490E317D5F58', X'B638C12D4B4DCE49B225490E317D5F58', 1, NULL, NULL, 2, NULL, 0, 'Place macadamia nuts, 4 dates and pinch of salt in food processor and pulse until a sticky mass forms.');
