INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A943ED85C9A4C448F881A22719B1B4C', X'B638C12D4B4DCE49B225490E317D5F58', X'B638C12D4B4DCE49B225490E317D5F58', 3, NULL, NULL, 2, NULL, 0, 'Clean out food processor and place coconut cream, cashews, lime juice, and maple syrup in it. Pulse and process until mixture is smooth.');
