INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7881935EE13A024C9086EFF45203B4B5', X'B638C12D4B4DCE49B225490E317D5F58', X'B638C12D4B4DCE49B225490E317D5F58', 5, NULL, NULL, 2, NULL, 0, 'Clean out food processor again and place blueberries and 2 dates in it. Pulse again until smooth (blueberry skin will still be present, this is OK). Carefully spread blueberry topping over filling in the springform.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A944A12BA6321A4B94386C8EBCF697B9', X'B638C12D4B4DCE49B225490E317D5F58', X'B638C12D4B4DCE49B225490E317D5F58', 6, NULL, NULL, 2, NULL, 0, 'Place in refrigerator for at least 4 hours or overnight until filling is firm.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77CF44E5671026448A6EA2B1E2EE5BFF', X'B638C12D4B4DCE49B225490E317D5F58', X'B638C12D4B4DCE49B225490E317D5F58', 7, NULL, NULL, 2, NULL, 0, 'When cheesecake is firm and ready to serve garnish with fresh blueberries and lime zest.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'459359FAEBB0BA47A0D08F26B113D379', X'1C2A9AC8B13DB7438DE964072DF15234', X'1C2A9AC8B13DB7438DE964072DF15234', 0, NULL, NULL, 2, NULL, 0, 'Heat diced peaches (with skin on) in a small saucepan over medium heat. Once they start to bubble, reduce to low heat. Stir occasionally (every 2 minutes) for 15 minutes.');
