INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A63E9A4323F034A91E849B036065F5F', X'9EBD5672E58208418CA352C62ADD7F34', X'9EBD5672E58208418CA352C62ADD7F34', 6, NULL, NULL, 2, NULL, 0, 'Store in airtight container in fridge for up to 5 days.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F40D06B6387EF149AA48ECBBACC071DE', X'95572F9745EDD5409F35FFBDDE6A35A6', X'95572F9745EDD5409F35FFBDDE6A35A6', 0, NULL, NULL, 2, NULL, 0, 'Prepare guacamole (see notes) and taco sauce first.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'691975736A50804A9B14F127BF38805F', X'95572F9745EDD5409F35FFBDDE6A35A6', X'95572F9745EDD5409F35FFBDDE6A35A6', 1, NULL, NULL, 2, NULL, 0, 'Add olive oil to a large fry pan and heat over medium heat. Add garlic and salt and sautee over low-medium heat until garlic becomes fragrant. (about 2-3 minutes) Then add sliced mushroom, tomatoes, and cumin to fry pan and cook until mushrooms are browned and soft. (about 6-8 minutes) Add chopped cilantro to the mushrooms and cook for 1 more minute. Remove pan from heat.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DB67B8ABBA17340B9E9A289A5EC54AF', X'95572F9745EDD5409F35FFBDDE6A35A6', X'95572F9745EDD5409F35FFBDDE6A35A6', 2, NULL, NULL, 2, NULL, 0, 'While mushrooms are cooking chop lettuce and dice the red onion. Heat taco shells or tortillas in oven.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BFF7E491706E24790FCDDFE1A6CCC69', X'95572F9745EDD5409F35FFBDDE6A35A6', X'95572F9745EDD5409F35FFBDDE6A35A6', 3, NULL, NULL, 2, NULL, 0, 'Assemble tacos and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'337A464C4AE09942AE865F67FCCB1368', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 395F (200C)with a baking sheet inside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36067D09278FDC4FA236FBFBA9866523', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', 1, NULL, NULL, 2, NULL, 0, 'Remove your puff pastry from the refridgerator and score a 1 1/2 inch border. (See notes)');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29F1A9787092D943957FB59860B4DB28', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', 2, NULL, NULL, 2, NULL, 0, 'Wash the pumpkin and slice in half. Scoop out the seeds and toss. Slice your pumpkin as thinly as you can. Place the sliced pumpkin on the puff pastry, keeping it within the border. Add sliced red onion on top of the pumpkin and fresh rosemary. Brush with olive oil.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7A589BA23D9EC4AB0FF6CED98C1B921', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes on lower rack. After 20 minutes, increase heat to 450F (230C) and bake for an additional 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CA9A9A47434B34C8826A66388B2680E', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', 4, NULL, NULL, 2, NULL, 0, 'While the tart is in the oven make the garlic-cashew sauce by blending all ingredients together in a food processor, blender, or with an immersion blender until smooth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A4C336FC3C989418BEBF68FBEEC118A', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', X'7EAFA9EA8F2E98439AA4F971AEC6DE36', 5, NULL, NULL, 2, NULL, 0, 'Remove pumpkin tart from oven, drizzle with sauce, top with fresh cracked pepper (optional), and serve hot.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2979A52FF0161458FEF78475F8DC8B3', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 0, NULL, NULL, 2, NULL, 0, 'Combine Yeast,  Cup (125ml) warm Water, and 2 tablespoons of the flour. Let stand for 10 minutes. Mix with a dough hook or knead with your hands while slowly adding salt and the rest of the flour. Knead until the dough is no longer sticking to the side of the bowl. The dough should not be sticking to your hands but still be soft and moist. (See notes if it is not) Cover dough with plastic wrap and let rise in a warm place for 20-40 minutes until it has doubled in size.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD3687E71F88B747BC3D44E5B80E857D', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450F.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEF432FCCDC5CA45932D5B2981EE3C78', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 2, NULL, NULL, 2, NULL, 0, 'Prepare chipotle sauce by pureeing peppers and coconut milk until smooth in a food processor or with an immersion blender.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEDBB4CACDE7CC4B9D66EF4FF360EBCF', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 3, NULL, NULL, 2, NULL, 0, 'Make tomato sauce by stirring together all ingredients. Set aside');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C38062270147B4DA7BBC044283F596B', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 4, NULL, NULL, 2, NULL, 0, 'Take half of the risen dough and ,using a rolling pin, roll each half into a pizza base/crust. Using your thumb and index finger fold small sections of dough over to form a raised crust.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B357A526146414494345D1287D93563', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 5, NULL, NULL, 2, NULL, 0, 'Lay on parchment paper lined sheet pan or pizza stone and cover to let rise for another 10 minutes. Repeat with remaining dough to make second pizza base.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95D220E1F653D14287D9D94D80158964', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 6, NULL, NULL, 2, NULL, 0, 'Assemble pizza by spreading 1/4 of a cup tomato sauce on each pizza base, top with vegetables, and drizzle with a bit of chipotle sauce.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF3D506A11CDAE479B687408831513F4', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 7, NULL, NULL, 2, NULL, 0, 'Bake in oven for 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6860F0FE3280948ACB99E130055F7A2', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', X'BE5EFDC2BB37BF4BB513BA53D99FF02A', 8, NULL, NULL, 2, NULL, 0, 'Serve hot and drizzle with more chipotle sauce and sprinkle with fresh cilantro if desired.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7358E2F28289BC48961CC8A47ECD3F1C', X'42CDBCAF21D2C44A9D154782337B8F29', X'42CDBCAF21D2C44A9D154782337B8F29', 0, NULL, NULL, 2, NULL, 0, 'Prepare asparagus by snapping off the ends at the normal breaking point. Toss ends. Using a peeler, peel the first layer of skin off the asparagus. Cut asparagus into 2 inch pieces and blanch for 2 minutes. Cool asparagus under cold water to stop cooking. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'802C6365EC56794AACFF2186A9450C12', X'42CDBCAF21D2C44A9D154782337B8F29', X'42CDBCAF21D2C44A9D154782337B8F29', 1, NULL, NULL, 2, NULL, 0, 'In a large stock pot or saut pan, heat olive oil over medium heat. Saut shallots for 2 minutes. Add garlic and risotto rice. Saut until edges of rice turn slightly translucent (roughly 4 minutes).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EB13891562F38419CA3683DCC22C03E', X'42CDBCAF21D2C44A9D154782337B8F29', X'42CDBCAF21D2C44A9D154782337B8F29', 2, NULL, NULL, 2, NULL, 0, 'Stir in white wine until absorbed by rice. Then add broth 3/4 of a cup at a time, stirring occasionally, adding more once absorbed by rice. Stir in chopped parsley and lemon zest before adding last 3/4 of a cup broth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EBC7F5385B9804186E8EDD79E5F3794', X'42CDBCAF21D2C44A9D154782337B8F29', X'42CDBCAF21D2C44A9D154782337B8F29', 3, NULL, NULL, 2, NULL, 0, 'Stir asparagus into risotto until hot.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F6D0673CF9ADF40ABDE9FC7F983C867', X'42CDBCAF21D2C44A9D154782337B8F29', X'42CDBCAF21D2C44A9D154782337B8F29', 4, NULL, NULL, 2, NULL, 0, 'Remove pan from heat and garnish risotto with more parsley, fresh cracked pepper and lemon juice to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EA322CCF91DD34E9A1BCCF0E2FE57E9', X'9CBE892DAFD5FB478BCB97D5E9989595', X'9CBE892DAFD5FB478BCB97D5E9989595', 0, NULL, NULL, 2, NULL, 0, 'Prepare green beans by trimming both ends, and cutting them into 1 inch slices. Steam or blanch green beans for roughly 2 minutes. Remove from steamer or stock pot and run cooked green beans under cold water. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83F1B60FA9FE62499479AF03D05B83BB', X'9CBE892DAFD5FB478BCB97D5E9989595', X'9CBE892DAFD5FB478BCB97D5E9989595', 1, NULL, NULL, 2, NULL, 0, 'Heat olive oil over medium heat in a saute pan. Add minced garlic and salt and saute for 2 minutes. Add sliced bell peppers and cook covered for 5 minutes, stirring occasionally, until bell peppers have softened. Add cooked green beans and cook for another 2 minutes. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4ACA4E5FACA55F4D9AA0B11A549AACD7', X'9CBE892DAFD5FB478BCB97D5E9989595', X'9CBE892DAFD5FB478BCB97D5E9989595', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, chop tomatoes, lettuce and onion. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFEC105325C9D3489CA73B3F36002198', X'9CBE892DAFD5FB478BCB97D5E9989595', X'9CBE892DAFD5FB478BCB97D5E9989595', 3, NULL, NULL, 2, NULL, 0, 'Prepare easy guacamole. Mash avocado in a bowl with a fork. Mix in salt, cilantro, lime juice, and red pepper flakes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'994A0BA78ACA5C49AD2E8FC43FEB12B0', X'9CBE892DAFD5FB478BCB97D5E9989595', X'9CBE892DAFD5FB478BCB97D5E9989595', 4, NULL, NULL, 2, NULL, 0, 'Assemble tacos by spreading VFR''s Taco Sauce on tortilla or on bottom of taco shell, top with lettuce, tomato, onion, then add Green bean-bell pepper mixture, and top with easy guacamole.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52284C00C178C346BF6256A8111993BC', X'9CBE892DAFD5FB478BCB97D5E9989595', X'9CBE892DAFD5FB478BCB97D5E9989595', 5, NULL, NULL, 2, NULL, 0, 'Eat immediately.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9FA745CC071D4499E5B9F30B296BFBE', X'5497A59BAF05D74C8E512F5A0C5C5DC5', X'5497A59BAF05D74C8E512F5A0C5C5DC5', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a food processor or high powdered blender. Blend until smooth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD92BAB030239D4C960ACC2BA7EBBF87', X'5497A59BAF05D74C8E512F5A0C5C5DC5', X'5497A59BAF05D74C8E512F5A0C5C5DC5', 1, NULL, NULL, 2, NULL, 0, 'Add a few drops of hot sauce to make this a spicy vegan mayo.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'568EB912F6A2354EBBCAB42C26E7ACAE', X'5497A59BAF05D74C8E512F5A0C5C5DC5', X'5497A59BAF05D74C8E512F5A0C5C5DC5', 2, NULL, NULL, 2, NULL, 0, 'Store in refrigerator, covered, for up to a week.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E1A7955750B47488F705AAF2184EF0A', X'16E229E179692C499CDD3CC0A0680447', X'16E229E179692C499CDD3CC0A0680447', 0, NULL, NULL, 2, NULL, 0, 'Slice the eggplant into 1/2` slices. Rub each slice with salt. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01F89BD6EA6DE544B3BF4829966A087B', X'16E229E179692C499CDD3CC0A0680447', X'16E229E179692C499CDD3CC0A0680447', 1, NULL, NULL, 2, NULL, 0, 'In a medium sized bowl, mix together chia seeds, coconut flour, and garlic powder. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF40CC350C7D8B4CB9AE7A05170ED275', X'16E229E179692C499CDD3CC0A0680447', X'16E229E179692C499CDD3CC0A0680447', 2, NULL, NULL, 2, NULL, 0, 'Rinse eggplant with cold water and pat dry with a paper towel.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54838405DE40CA4DA4022C8C4111EA2D', X'16E229E179692C499CDD3CC0A0680447', X'16E229E179692C499CDD3CC0A0680447', 3, NULL, NULL, 2, NULL, 0, 'Coat each eggplant slice by dipping it in the flour mixture. For a ticker coating, dip in flour mixture then in almond milk, and again in the flour mixture.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C2B1585A01A484AA34281615892F2ED', X'16E229E179692C499CDD3CC0A0680447', X'16E229E179692C499CDD3CC0A0680447', 4, NULL, NULL, 2, NULL, 0, 'Fry coated eggplant in a pan over medium-high heat for 5 minutes. Remove from pan and let rest on a paper towel for 5 minutes.Repeat until all eggplant slices are cooked.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93F83332B4844D4DB56A6EF4525FEE94', X'16E229E179692C499CDD3CC0A0680447', X'16E229E179692C499CDD3CC0A0680447', 5, NULL, NULL, 2, NULL, 0, 'Assemble burgers by spreading smashed avocado on the bottom burger bun and the vegan mayo on the top bun. Add two slices of cooked eggplant on top the avocado and top with tomato, onion, and lettuce. Place top bun on top and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD442AD415C71E4FB334586E56D3F762', X'BEF925351740704482622C85C97AFE14', X'BEF925351740704482622C85C97AFE14', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 390 F (200 C) with a baking sheet inside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'908CE44F8D1D944AA2376227C3C46296', X'BEF925351740704482622C85C97AFE14', X'BEF925351740704482622C85C97AFE14', 1, NULL, NULL, 2, NULL, 0, 'Add chopped mushrooms and olive oil to a fry pan and cook for roughly 3-4 minutes over medium heat until mushrooms have softened. Add salt, garlic and scallions and cook for 2 more minutes. Remove pan from heat and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30E41C2852D3F64AA2E54C50A7FC0988', X'BEF925351740704482622C85C97AFE14', X'BEF925351740704482622C85C97AFE14', 2, NULL, NULL, 2, NULL, 0, 'Roll out your puff pastry sheet on a piece of parchment paper and score a 1 inch border. (See notes)');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'369387FA26FA18469732EB3B3AFC4065', X'BEF925351740704482622C85C97AFE14', X'BEF925351740704482622C85C97AFE14', 3, NULL, NULL, 2, NULL, 0, 'Place your mushrooms evenly within the border of your puff pastry sheet. Try to not add to much of the extra mushroom liquid in the pan. Top with 3 rosemary springs and brush with small amount of olive oil.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CE77984F54D214F841C14C09A96D94C', X'BEF925351740704482622C85C97AFE14', X'BEF925351740704482622C85C97AFE14', 4, NULL, NULL, 2, NULL, 0, 'Carefully slide puff pastry onto the now hot baking sheet in the oven. Cook for 20 minutes or until puff pastry is golden brown.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF6E91C4B2F8E0408FAA837CD4F5B0D0', X'13BE9DBE2921F94282D586847CC74CDC', X'13BE9DBE2921F94282D586847CC74CDC', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a medium sized stockpot over medium heat. Add diced onions and saute for 2 minutes. Add diced tempeh, garlic, and rosemary. Saute for about 3 minutes until tempeh is lightly browned and garlic becomes fragrant.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4104D95032DC8045BA4162F93AD192ED', X'13BE9DBE2921F94282D586847CC74CDC', X'13BE9DBE2921F94282D586847CC74CDC', 1, NULL, NULL, 2, NULL, 0, 'Toss tomatoes, 1/4 teaspoon salt, and pepper into stockpot with tempeh. Cover stockpot and cook for 10 minutes while stirring occasionally.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'731EFADB56A8504FB62070460BAB9723', X'13BE9DBE2921F94282D586847CC74CDC', X'13BE9DBE2921F94282D586847CC74CDC', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 475 F (245C).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E62EFBBA4E1D94992D51EF6EA7487A2', X'13BE9DBE2921F94282D586847CC74CDC', X'13BE9DBE2921F94282D586847CC74CDC', 3, NULL, NULL, 2, NULL, 0, 'Prepare cashew cheese by pulsing cashews, 1/4 teaspoon salt, and oregano in a food processor until it become thick and creamy. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D110711D200A746BB5B7B198E7747D9', X'13BE9DBE2921F94282D586847CC74CDC', X'13BE9DBE2921F94282D586847CC74CDC', 4, NULL, NULL, 2, NULL, 0, 'Add almond or oat milk to tomatoes, bring to a quick boil. Reduce heat and simmer for 3-5 minutes until mixture slightly thickens.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE776581DA9AAF499C39384C54AE0528', X'13BE9DBE2921F94282D586847CC74CDC', X'13BE9DBE2921F94282D586847CC74CDC', 5, NULL, NULL, 2, NULL, 0, 'Stir in cooked pasta shells and white wine vinegar to tomato-tempeh mixture.Pour into a large baking dish or casserole and sprinkle with crumbled cashew cheese. Drizzle with olive oil and bake on middle rack of oven for 5 minutes until bubbling and cashew cheese has browned.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22B2003FB6E6AA429646BAE610700680', X'13BE9DBE2921F94282D586847CC74CDC', X'13BE9DBE2921F94282D586847CC74CDC', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and serve hot.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CAE2C523AC23C4BB1DEAC85C3BB651A', X'90060FEDAC61EE4285129DE614B41B75', X'90060FEDAC61EE4285129DE614B41B75', 0, NULL, NULL, 2, NULL, 0, 'Cook Beluga Lentils according to packaging.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE0782FD6581834391912DC1A8210029', X'90060FEDAC61EE4285129DE614B41B75', X'90060FEDAC61EE4285129DE614B41B75', 1, NULL, NULL, 2, NULL, 0, 'While Lentils are cooking, prep remaining ingredients. Quarter tomatoes, dice onion, wash and tear romaine lettuce, slice avocado and wash cilantro.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E6E6BE807D0C64B974D1B207A936C63', X'90060FEDAC61EE4285129DE614B41B75', X'90060FEDAC61EE4285129DE614B41B75', 2, NULL, NULL, 2, NULL, 0, 'Prepare Jalapeno cream by placing coconut cream, jalapeno, and lime juice in a food processor. Pulse until smooth. Refrigerate jalapeno cream until ready to serve.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD68BDE41B41CD488E92988E2E1576A4', X'90060FEDAC61EE4285129DE614B41B75', X'90060FEDAC61EE4285129DE614B41B75', 3, NULL, NULL, 2, NULL, 0, 'Heat tacos according to instructions on packaging.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB000EA9927371489B20761C44130FE5', X'90060FEDAC61EE4285129DE614B41B75', X'90060FEDAC61EE4285129DE614B41B75', 4, NULL, NULL, 2, NULL, 0, 'Once Lentils are fully cooked stir in salt, smoked paprika, cumin, and lime juice.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44BA9020B0203846866AF2A7C0D489CA', X'90060FEDAC61EE4285129DE614B41B75', X'90060FEDAC61EE4285129DE614B41B75', 5, NULL, NULL, 2, NULL, 0, 'Assemble lentil tacos by first placing one romaine lettuce leaf into a taco, top with spiced beluga lentils, tomatoes, onion, and avocado. Finish with a dollop of jalapeno cream and fresh cilantro. Repeat with remaining taco shells and serve immediately.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2669D97FC25EC445A9474F2A95633FE0', X'C3F0BEF7509ED64AA06CFE9937407C64', X'C3F0BEF7509ED64AA06CFE9937407C64', 0, NULL, NULL, 2, NULL, 0, 'Heat Olive oil in a large deep fry pan and add onion with dash of salt. Saute until translucent, then add garlic and jalapeno. Saute for 1-2 more minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8C39358DCDFFB49B152D8993EF89EA4', X'C3F0BEF7509ED64AA06CFE9937407C64', X'C3F0BEF7509ED64AA06CFE9937407C64', 1, NULL, NULL, 2, NULL, 0, 'Add sweet potato, zucchini, corn, and tomatoes. Saute for a further 2 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DEC4246754CDF42B7B261CE243B0F81', X'C3F0BEF7509ED64AA06CFE9937407C64', X'C3F0BEF7509ED64AA06CFE9937407C64', 2, NULL, NULL, 2, NULL, 0, 'Stir in rice and add tomato puree. Slowly add vegetable broth without stirring, adding just enough to cover all ingredients. Again, DON''T STIR!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA1321B5CBCC8F4194E634B42E732903', X'C3F0BEF7509ED64AA06CFE9937407C64', X'C3F0BEF7509ED64AA06CFE9937407C64', 3, NULL, NULL, 2, NULL, 0, 'Top with herbs, and add a bit of pepper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A506B085EDBB41418EC5BA32EA7CB923', X'C3F0BEF7509ED64AA06CFE9937407C64', X'C3F0BEF7509ED64AA06CFE9937407C64', 4, NULL, NULL, 2, NULL, 0, 'Cover and let simmer on low heat for 20 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E03F3390BBE0B5498890DC8DA7D577CB', X'C3F0BEF7509ED64AA06CFE9937407C64', X'C3F0BEF7509ED64AA06CFE9937407C64', 5, NULL, NULL, 2, NULL, 0, 'Paella will be done when the rice has absorbed all the liquid, yet it should look moist and juicy.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE98B2A7E48EA84CA3B2CEE7A9DBF2EE', X'C3F0BEF7509ED64AA06CFE9937407C64', X'C3F0BEF7509ED64AA06CFE9937407C64', 6, NULL, NULL, 2, NULL, 0, 'Spritz lemon juice over finished paella and serve hot.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'123F0DF82415C24FB4762997AFD50F1F', X'457D8098F7D0ED41ADB13D67FA3A7B6A', X'457D8098F7D0ED41ADB13D67FA3A7B6A', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 395F (200C) and line sheet pan with baking paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBF6A1D8037F8C40972F08203D8B745E', X'457D8098F7D0ED41ADB13D67FA3A7B6A', X'457D8098F7D0ED41ADB13D67FA3A7B6A', 1, NULL, NULL, 2, NULL, 0, 'Cut squash in half, remove seeds, and brush olive oil on cut side. Place squash (cut side down) on baking pan. Cook for 40-50 minutes until squash is tender.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEDD36A46762B04F8AC64C7B01476416', X'457D8098F7D0ED41ADB13D67FA3A7B6A', X'457D8098F7D0ED41ADB13D67FA3A7B6A', 2, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a fry pan. Add tempeh and onion and saute until onions become translucent and tempeh gains some color. Don''t let onions burn. if they start to stick, add more oil.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DEA480C25A9974CA7F341E27E46A1B4', X'457D8098F7D0ED41ADB13D67FA3A7B6A', X'457D8098F7D0ED41ADB13D67FA3A7B6A', 3, NULL, NULL, 2, NULL, 0, 'Add bell pepper, tomato, tomato puree, and maple syrup to tempeh/onion mixture. Season with salt, pepper, and cayenne pepper. Keep warm over low heat.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69E146648399FF4BB2C0ADC397199F88', X'457D8098F7D0ED41ADB13D67FA3A7B6A', X'457D8098F7D0ED41ADB13D67FA3A7B6A', 4, NULL, NULL, 2, NULL, 0, 'Take butternut squash out of oven and further hallow out the squash. Leaving about 1/4 to 1/2 inch squash around the edges. Add squash flesh to tempeh mixture.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95CD0AEFB2AB404991044D89E68C672B', X'457D8098F7D0ED41ADB13D67FA3A7B6A', X'457D8098F7D0ED41ADB13D67FA3A7B6A', 5, NULL, NULL, 2, NULL, 0, 'Fill tempeh mixture into squash, drizzle with olive oil and cook in oven (lower rack) for another 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E8ED4B10F681442808378FA9246F2B8', X'457D8098F7D0ED41ADB13D67FA3A7B6A', X'457D8098F7D0ED41ADB13D67FA3A7B6A', 6, NULL, NULL, 2, NULL, 0, 'Remove squash from oven and top with fresh parsley. 1 stuffed squash half can be served as a meal or they can be cut into 3 or 4 pieces to be served as a side dish. Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A60837C7B2757B41A4F1AF04C30FCB67', X'57F0B0F50366BD428851516960559CE4', X'57F0B0F50366BD428851516960559CE4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 435F (225C) and line a baking pan with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'013EC669C7FBA24FBF434E5558844EFC', X'57F0B0F50366BD428851516960559CE4', X'57F0B0F50366BD428851516960559CE4', 1, NULL, NULL, 2, NULL, 0, 'Heat Olive Oil in a fry pan on medium heat. Add onion, sweet potato, and garlic and saute for 3 minutes. Cover and cook over low heat for 10 minutes until sweet potatoes are soft.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12DA5ADDB0629140BACDFE2AFCCE8E54', X'57F0B0F50366BD428851516960559CE4', X'57F0B0F50366BD428851516960559CE4', 2, NULL, NULL, 2, NULL, 0, 'Remove pan from heat and place sweet potato,onion,and garlic into a medium sized bowl. Add spices and mash ingredients together. Set aside for 10 minutes until cool enough to handle.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'589BF203D47B9941A32AB644B7BD1FF0', X'57F0B0F50366BD428851516960559CE4', X'57F0B0F50366BD428851516960559CE4', 3, NULL, NULL, 2, NULL, 0, 'Add corn, beans, and breadcrumbs to somewhat cooled sweet potato mixture. Mix well with hands and form into 4 large or 6 medium sized patties.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01C4D1B35B3EC44EB8312C2300403C93', X'57F0B0F50366BD428851516960559CE4', X'57F0B0F50366BD428851516960559CE4', 4, NULL, NULL, 2, NULL, 0, 'Place on parchment paper lined baking pan and bake for 15 minutes on one side, flip, and bake for an additional 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE8BEDDF6004724E9EA998C7E8DE9BFE', X'57F0B0F50366BD428851516960559CE4', X'57F0B0F50366BD428851516960559CE4', 5, NULL, NULL, 2, NULL, 0, 'While burgers are in the oven make your yogurt sauce by combining yogurt with spices to your taste. Start with a little of each spice and slowly add more until sauce is to your liking.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0B5182F05DF2D49B49E92D3D84B3F79', X'57F0B0F50366BD428851516960559CE4', X'57F0B0F50366BD428851516960559CE4', 6, NULL, NULL, 2, NULL, 0, 'Remove burgers from oven, serve on buns with onions, tomato, lettuce and sauce, and enjoy.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31C8442E44D5F841B7CEAEC5A1A0C873', X'0C793DBCCAFF1F4486E9B97991727467', X'0C793DBCCAFF1F4486E9B97991727467', 0, NULL, NULL, 2, NULL, 0, 'Combine Yeast, 1/2 Cup (125ml) warm Water, and 2 tablespoons of the flour. Let stand for 10 minutes. Mix with a dough hook or knead with your hands while slowly adding salt and the rest of the flour. Knead until the dough is no longer sticking to the side of the bowl. The dough should not be sticking to your hands but still be soft and moist. (See notes if it is not) Cover dough with plastic wrap and let rise in a warm place for 20-40 minutes until it has doubled in size.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10EF3C86A6FF9144A6594EEFC3C21933', X'0C793DBCCAFF1F4486E9B97991727467', X'0C793DBCCAFF1F4486E9B97991727467', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450F.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'838881094BF20E47A82B835D53ECEFDA', X'0C793DBCCAFF1F4486E9B97991727467', X'0C793DBCCAFF1F4486E9B97991727467', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile get started on the Tomato sauce. Simply add all ingredients together and put aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A82ACF24E406D04CA332AB3258AD6894', X'0C793DBCCAFF1F4486E9B97991727467', X'0C793DBCCAFF1F4486E9B97991727467', 3, NULL, NULL, 2, NULL, 0, 'Take half of the risen dough and ,using a rolling pin, roll each half into a pizza base/crust. Lay on parchment paper lined sheet pan and cover to let rise for another 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EA4D3896643774283775D90B3B3C165', X'0C793DBCCAFF1F4486E9B97991727467', X'0C793DBCCAFF1F4486E9B97991727467', 4, NULL, NULL, 2, NULL, 0, 'Cover each base with tomato sauce, top onion or shallots, spinach, pine nuts, and salt. Drizzle with Olive Oil and bake on lowest rack in oven for 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56EA599E52C8F644B24D57ECAF5815BE', X'0C793DBCCAFF1F4486E9B97991727467', X'0C793DBCCAFF1F4486E9B97991727467', 5, NULL, NULL, 2, NULL, 0, 'Remove Pizza from oven. Adults will enjoy this pizza topped with fresh cracked pepper. Serve hot and Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFCBB19865AFB84CBC9AFEEA907BE77D', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 440F (225C). Line a baking sheet with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08475E9117B59C4E9AC3F83E50303323', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 1, NULL, NULL, 2, NULL, 0, 'Steam sweet potato until you can easily pierce it with a fork. Set aside and let cool.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BD126CE8F9BEC458C2FF329BABE8677', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 2, NULL, NULL, 2, NULL, 0, 'Bring lentils to a boil in double the amount of water, then simmer for 20-30 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDE1570CA8D8D74EAAD438814DD3509F', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile grate carrots, crush garlic and chop parsley.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9A6E3FC1055AC4D91D74D88FB932B2C', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 4, NULL, NULL, 2, NULL, 0, 'Place somewhat cooled sweet potatoes into a large mixing bowl and mash with a fork. Mix in carrots, garlic, parsley, and spices.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFAAD5DEF4FF0543887EF46271D1D16A', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 5, NULL, NULL, 2, NULL, 0, 'Add cooked beluga lentils and bread crumbs.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6F1329FDDEAFE4CB773006557947239', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 6, NULL, NULL, 2, NULL, 0, 'Form roughly 12 patties, 3` to 4` in size. These won''t all fit on one baking sheet. Brush or drizzle with olive oil and bake for 15 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'978526577245254CA44297E8D926468D', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 7, NULL, NULL, 2, NULL, 0, 'Remove patties quickly from oven, flip patties, brush top side with olive oil again and bake for an additional 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6705803707B7F04FB4BAFF66EF334FA5', X'1FB9CF768532A142982F0013C656087A', X'1FB9CF768532A142982F0013C656087A', 8, NULL, NULL, 2, NULL, 0, 'Remove patties from oven when edges are slightly crisp. Using a spatula, carefully lift each patty off of baking sheet and place onto burger buns. Serve with avocado, tomatoes, and onion.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8E1A61C8A90DE45A8F292DA20457CEF', X'DA98868230D5684A981BD7BD4EDD6BF8', X'DA98868230D5684A981BD7BD4EDD6BF8', 0, NULL, NULL, 2, NULL, 0, 'Peel and steam beet for 35-50 minutes until you are easily able to pierce the beet with a knife or fork to the center. Set beet aside and allow to cool to touch.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55FDD1E7C1D12842A3D19ACE1B87E830', X'DA98868230D5684A981BD7BD4EDD6BF8', X'DA98868230D5684A981BD7BD4EDD6BF8', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile mix coconut flakes, bread crumbs, salt and garlic powder together on a plate.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA43FC709E2D5E42BBE71FFDAA10FE08', X'DA98868230D5684A981BD7BD4EDD6BF8', X'DA98868230D5684A981BD7BD4EDD6BF8', 2, NULL, NULL, 2, NULL, 0, 'On a seperate plate mix flour with water until a thick paste forms.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3D905677065AA46B9C2FD6127170864', X'DA98868230D5684A981BD7BD4EDD6BF8', X'DA98868230D5684A981BD7BD4EDD6BF8', 3, NULL, NULL, 2, NULL, 0, 'Slice steamed beet into half inch slices.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'561A0AA33107AA46BE9E4BBD905F2F6B', X'DA98868230D5684A981BD7BD4EDD6BF8', X'DA98868230D5684A981BD7BD4EDD6BF8', 4, NULL, NULL, 2, NULL, 0, 'Take a beet slice dip in flour mixture then in coconut-breadcrumbs. Continue this with all beet slices.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8B204D63C6CED40998745B6C26AF2A3', X'DA98868230D5684A981BD7BD4EDD6BF8', X'DA98868230D5684A981BD7BD4EDD6BF8', 5, NULL, NULL, 2, NULL, 0, 'Heat skillet with 1 tablespoon of Olive oil and fry beet patties over medium heat for 2 minutes on each side until golden-brown.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72435F3B265FE042BF44B67D77A9DE84', X'DA98868230D5684A981BD7BD4EDD6BF8', X'DA98868230D5684A981BD7BD4EDD6BF8', 6, NULL, NULL, 2, NULL, 0, 'Serve while still hot with Mango Vinaigrette or as a burger and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90B51CAED91D984EB0D05E95D5079742', X'7F5903BC2656B044914675806A079FC7', X'7F5903BC2656B044914675806A079FC7', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil and 1/4 teaspoon of Salt in a fry pan over med-high heat. Once hot, add diced onions, and crumbled tempeh. Saute for 3-5 minutes adding a bit more oil or water if tempeh starts to stick.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABE3D02FC7B6D94484867DA9B5174C71', X'7F5903BC2656B044914675806A079FC7', X'7F5903BC2656B044914675806A079FC7', 1, NULL, NULL, 2, NULL, 0, 'Once onions have become glossy and tempeh is slightly browned add bell pepper and carrots. Saute for an additional 3 minutes. Prepare pasta of choice according to package directions.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E577B3C9D57174285686F435AA439E5', X'7F5903BC2656B044914675806A079FC7', X'7F5903BC2656B044914675806A079FC7', 2, NULL, NULL, 2, NULL, 0, 'Stir in tomato puree, tomato paste, dried herbs and red wine vinegar. Add salt and pepper to taste. Lower heat and simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C31D4338283C8429FFF8AA05DC81A0D', X'7F5903BC2656B044914675806A079FC7', X'7F5903BC2656B044914675806A079FC7', 3, NULL, NULL, 2, NULL, 0, 'Serve hot over pasta and garnish with fresh basil.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAF329221AF70644818832F1B7916C6C', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 0, NULL, NULL, 2, NULL, 0, 'Combine Yeast, 1/2 Cup (125ml) warm Water, and 2 tablespoons of the flour. Let stand for 10 minutes. Mix with a dough hook or knead with your hands while slowly adding salt and the rest of the flour. Knead until the dough is no longer sticking to the side of the bowl. The dough should not be sticking to your hands but still be soft and moist. (See notes if it is not) Cover dough with plastic wrap and let rise in a warm place for 20-40 minutes until it has doubled in size.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CF579388BA6DF4182393E6A6A179941', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450F.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B80449952B8D1341AB6D184DDC1E923A', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile get started on the coconut garlic sauce. Heat olive oil in a saucepan. Add garlic and saute over medium-low heat until garlic becomes fragrant.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88EB7963A4CF7C429458B4F14D1CAB1E', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 3, NULL, NULL, 2, NULL, 0, 'Add coconut milk and salt to saucepan and bring to a boil. Reduce heat and simmer for 3-5 minutes. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'931F9CFD24B674458E09768769646321', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 4, NULL, NULL, 2, NULL, 0, 'Take half of the risen dough and ,using a rolling pin, roll each half into a pizza base/crust. Using your thumb and index finger fold small sections of dough over to form a raised crust.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B0ABBC09DB84441908AD9588763917B', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 5, NULL, NULL, 2, NULL, 0, 'Lay on parchment paper lined sheet pan or pizza stone and cover to let rise for another 10 minutes. Repeat with remaining dough to make second pizza base.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D1C59F58D5E6042A219C91D4679D582', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 6, NULL, NULL, 2, NULL, 0, 'Slowly add half of the coconut garlic sauce to the base being careful not to have it overflow.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D21F9819FE2ABA4B968463B8B311B78F', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 7, NULL, NULL, 2, NULL, 0, 'Top with bell peppers, shallots and oregano.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0681572E3616945A6372D7B11B8B78C', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 8, NULL, NULL, 2, NULL, 0, 'Bake in oven for 10 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE76D4E9058AB3468133D0240DB58EC3', X'DA3EB319E73245479A0AB86ABDCD7720', X'DA3EB319E73245479A0AB86ABDCD7720', 9, NULL, NULL, 2, NULL, 0, 'Serve hot and top with fresh cracked pepper (optional).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6BC40540D260C43B8B4F5003610A97C', X'3FF6DD16A553B145AA2D0397946A2D44', X'3FF6DD16A553B145AA2D0397946A2D44', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large deep fry pan. Add onion and saute until they begin to soften.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97A1D5A464B7484390197E618CD7184E', X'3FF6DD16A553B145AA2D0397946A2D44', X'3FF6DD16A553B145AA2D0397946A2D44', 1, NULL, NULL, 2, NULL, 0, 'Add zucchini and bell pepper and sautee for an additional 2 minutes. (If you want to use jalapeno, add it now or add to top as garnish once pilaf is done)');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13853B7F5C585847885F7FCE6BFCB78E', X'3FF6DD16A553B145AA2D0397946A2D44', X'3FF6DD16A553B145AA2D0397946A2D44', 2, NULL, NULL, 2, NULL, 0, 'Add corn, beans, tomato, freekeh, spices, herbs and half the fresh cilantro. Stir ingredients in pan to mix well then add broth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CA2176195DB9E4C9E48E2428B56DE60', X'3FF6DD16A553B145AA2D0397946A2D44', X'3FF6DD16A553B145AA2D0397946A2D44', 3, NULL, NULL, 2, NULL, 0, 'Bring to boil.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4B07462E57784469002C0429A673AFA', X'3FF6DD16A553B145AA2D0397946A2D44', X'3FF6DD16A553B145AA2D0397946A2D44', 4, NULL, NULL, 2, NULL, 0, 'Reduce heat, cover and simmer for 40 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'695CF22855A78846885B915B7632CFC7', X'3FF6DD16A553B145AA2D0397946A2D44', X'3FF6DD16A553B145AA2D0397946A2D44', 5, NULL, NULL, 2, NULL, 0, 'Serve hot, garnish will remaining cilantro and jalapeno(optional) and a spritz of fresh lime juice (optional). Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1735F656CA21AD4C8516CC3C4FF529AB', X'3D5FD9FFEE536A498F4DA56090E2ACFD', X'3D5FD9FFEE536A498F4DA56090E2ACFD', 0, NULL, NULL, 2, NULL, 0, 'Prepare rice of choice according to instructions on packaging.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA75B71048BF32428D8DB5F643070C68', X'3D5FD9FFEE536A498F4DA56090E2ACFD', X'3D5FD9FFEE536A498F4DA56090E2ACFD', 1, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon olive oil in small fry pan. Fry diced tempeh over medium-high heat on each side until browned. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B940B91DD8D6324D83848B10271C1CA7', X'3D5FD9FFEE536A498F4DA56090E2ACFD', X'3D5FD9FFEE536A498F4DA56090E2ACFD', 2, NULL, NULL, 2, NULL, 0, 'Make peanut sauce by adding vegetable broth, peanut butter, soy sauce, and maple syrup to a bowl. Whisk together. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98F0A1B645891049B824FEBBEB24FB2E', X'3D5FD9FFEE536A498F4DA56090E2ACFD', X'3D5FD9FFEE536A498F4DA56090E2ACFD', 3, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon olive oil in a wok or large saute pan. Add onion, bell pepper, garlic, ginger, and carrots (and red pepper flakes if using). Stir fry vegetables over medium heat for 10 minutes. Add bamboo shoots, tempeh and peanut sauce.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8320F51BBCF2B6419B717FB76D9E7109', X'3D5FD9FFEE536A498F4DA56090E2ACFD', X'3D5FD9FFEE536A498F4DA56090E2ACFD', 4, NULL, NULL, 2, NULL, 0, 'Continue to stir fry vegetables in peanut sauce until sauce thickens about 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'420FEEADD45C944BB12F5CD2BE321848', X'3D5FD9FFEE536A498F4DA56090E2ACFD', X'3D5FD9FFEE536A498F4DA56090E2ACFD', 5, NULL, NULL, 2, NULL, 0, 'Serve peanut sauce stir fry with rice and top with chopped peanuts(optional).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46C27F27D0E8C04E8B1E43C8701C2DC4', X'F6B535310B1EAA4BA7CFA42E831025BE', X'F6B535310B1EAA4BA7CFA42E831025BE', 0, NULL, NULL, 2, NULL, 0, 'Melt coconut oil in over medium-high heat in a medium sized stock pot. Add onion, garlic, and ginger to pot and cook for 5 minutes. Add carrots and cook for 3 more minutes. Stir broth and cilantro into pot and bring to a boil. Once boiling, lower heat, cover pot and simmer until carrots can easily be pierced with a fork. This will take anywhere between 15 and 40 minutes depending on the size of the diced carrots.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17E33CABAD2EE24FBD4894418CC3B22B', X'F6B535310B1EAA4BA7CFA42E831025BE', X'F6B535310B1EAA4BA7CFA42E831025BE', 1, NULL, NULL, 2, NULL, 0, 'When carrots are soft, puree using an immersion blender or add to a blender to puree. Return soup back to stock pot and add coconut milk and more water if too thick.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'595BCFE7E4019D45A085B85EB4D1B6BA', X'F6B535310B1EAA4BA7CFA42E831025BE', X'F6B535310B1EAA4BA7CFA42E831025BE', 2, NULL, NULL, 2, NULL, 0, 'Add lime juice and more salt to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71D4A9D35C29F748B3D561A293578F84', X'F6B535310B1EAA4BA7CFA42E831025BE', X'F6B535310B1EAA4BA7CFA42E831025BE', 3, NULL, NULL, 2, NULL, 0, 'Garnish soup with cayenne pepper and fresh cilantro leaves. Serve hot.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B913E2B370E0024D8285EE3082F34D1C', X'BEC8B6B9596E4C4FB9B5804F895D6369', X'BEC8B6B9596E4C4FB9B5804F895D6369', 0, NULL, NULL, 2, NULL, 0, 'Chop Cauliflower head into florets and remove tough stems from kale.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43BF3E5D469F8A40A3D1F511148CEEC8', X'BEC8B6B9596E4C4FB9B5804F895D6369', X'BEC8B6B9596E4C4FB9B5804F895D6369', 1, NULL, NULL, 2, NULL, 0, 'In a large stock pot, heat olive oil and add diced onions.Cook for 5 to 7 minutes until onions become translucent. Add garlic and cook for 2 minutes or until garlic becomes fragrant.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADA2F4F050EA3E4FB971C8FB14A43B95', X'BEC8B6B9596E4C4FB9B5804F895D6369', X'BEC8B6B9596E4C4FB9B5804F895D6369', 2, NULL, NULL, 2, NULL, 0, 'Add cauliflower, kale and broth to stock pot. Bring to a boil, reduce heat and simmer for 10 to 15 minutes until cauliflower and kale has softened.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A469A4DFBF882840AC45E18B1CB2B860', X'BEC8B6B9596E4C4FB9B5804F895D6369', X'BEC8B6B9596E4C4FB9B5804F895D6369', 3, NULL, NULL, 2, NULL, 0, 'Blend soup with an immersion blender or in a blender until smooth. Return to stock pot and simmer for an additional 10 minutes.');
