INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C329244B2AFE6468F03C2A39640202E', X'CF826457AB62F04DB17E0971DFD6B59F', X'CF826457AB62F04DB17E0971DFD6B59F', 1, NULL, NULL, 2, NULL, 0, 'Bring broth to a boil, remove from heat and let sit for 15 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCD2D98CFC3B37409BFE3EB8123DD721', X'CF826457AB62F04DB17E0971DFD6B59F', X'CF826457AB62F04DB17E0971DFD6B59F', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile julienne carrots, slice bell peppers and scallions.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFDDADC8F9A44B47BAB99444332C9226', X'CF826457AB62F04DB17E0971DFD6B59F', X'CF826457AB62F04DB17E0971DFD6B59F', 3, NULL, NULL, 2, NULL, 0, 'Prepare cellophane noodles according to package directions. This is usually done by placing noodles in warm water for 10 to 15 minutes. Then drain and rinse noodles.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3673BF3007F0248B59CDE078DE34561', X'CF826457AB62F04DB17E0971DFD6B59F', X'CF826457AB62F04DB17E0971DFD6B59F', 4, NULL, NULL, 2, NULL, 0, 'After allowing broth to sit for 15 minutes, remove lemongrass and add sugar snap peas. Bring broth to a boil again and then simmer for 3-5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3F7739204CBBB4F9E6F6B1C1DC5AD04', X'CF826457AB62F04DB17E0971DFD6B59F', X'CF826457AB62F04DB17E0971DFD6B59F', 5, NULL, NULL, 2, NULL, 0, 'Dice tofu and fry in olive oil for 2-3 minutes. Season tofu with salt and pepper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A09DA30214E7C47821D3118736A2D63', X'CF826457AB62F04DB17E0971DFD6B59F', X'CF826457AB62F04DB17E0971DFD6B59F', 6, NULL, NULL, 2, NULL, 0, 'Add bell peppers, carrots, scallions, tofu, and noodles to broth. Simmer for 2 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8237C9C67F5DB842A180C21989009946', X'CF826457AB62F04DB17E0971DFD6B59F', X'CF826457AB62F04DB17E0971DFD6B59F', 7, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEDD08FBED8AF443B58897A0111A015E', X'7B0A1F2F8621D440AEB49ADE68A6C54E', X'7B0A1F2F8621D440AEB49ADE68A6C54E', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a medium sized stock pot. Saute onions until glossy. Add garlic and continue cooking, stirring often, until garlic becomes fragrant ( about 3 minutes).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB575F6A6D77A740BA4A4AB012DDD663', X'7B0A1F2F8621D440AEB49ADE68A6C54E', X'7B0A1F2F8621D440AEB49ADE68A6C54E', 1, NULL, NULL, 2, NULL, 0, 'Add sweet potato and cauliflower, cook for 1 - 2 minutes, and then add broth. Cover stock pot Reduce to low heat, uncover, and let simmer for 15 to 20 minutes until sweet potato can easily be pierced with a fork.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E80A79BA924E742A63EC949DAD4921B', X'7B0A1F2F8621D440AEB49ADE68A6C54E', X'7B0A1F2F8621D440AEB49ADE68A6C54E', 2, NULL, NULL, 2, NULL, 0, 'Puree with an immersion blender until smooth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19EB6E0EA2A91C4C87070D06146BE135', X'7B0A1F2F8621D440AEB49ADE68A6C54E', X'7B0A1F2F8621D440AEB49ADE68A6C54E', 3, NULL, NULL, 2, NULL, 0, 'Stir in 2 tablespoons of apple cider vinegar, bring to another quick boil, and again simmer for an additional 3 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59393DE83BFDC445B5E1970FD7E3BC06', X'7B0A1F2F8621D440AEB49ADE68A6C54E', X'7B0A1F2F8621D440AEB49ADE68A6C54E', 4, NULL, NULL, 2, NULL, 0, 'Serve hot and garnish with favorite toppings such as pumpkin seeds, chives, pine nuts, parsley, croutons, vegan sour cream or yogurt.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2960A870DD5ED94A8D92DACF017975A1', X'EDA85595F6C1C04B91834CD3960C3E1E', X'EDA85595F6C1C04B91834CD3960C3E1E', 0, NULL, NULL, 2, NULL, 0, 'Place mixed greens in a large salad bowl and top with remaining ingredients. Add cilantro, pepper and drizzle with lime juice if desired.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F46DF9C64B54342A60844E7CC5D21BA', X'EDA85595F6C1C04B91834CD3960C3E1E', X'EDA85595F6C1C04B91834CD3960C3E1E', 1, NULL, NULL, 2, NULL, 0, 'Eat immediately or store in an air tight container in the fridge and eat within 24 hours.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40458ECFE1CE9D4BBA43BD397E87131F', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', 0, NULL, NULL, 2, NULL, 0, 'Cook bulgur according to instructions on packaging or check notes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E817239B57D5C74DB382073F4B7B4336', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', 1, NULL, NULL, 2, NULL, 0, 'Prepare bell pepper sauce by adding bell pepper, lemon juice, and salt to a food processor. Pulse until no chunks of bell pepper are left. Stir bell pepper sauce into warm, cooked bulgur.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13ECD91146BF164F8630CB3A4D5EE2BB', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', 2, NULL, NULL, 2, NULL, 0, 'Add tomatoes, onion, and parsley to bulgur.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33F587F687F2E34F8BD748327D24A665', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', 3, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98864E404015454CAB748526A1DCB3F4', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', 4, NULL, NULL, 2, NULL, 0, 'Serve bulgur salad warm or cold.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F84BE73D0123D4409A4EBB9C3D3DB11E', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', X'31B12FCFDAF0DB4E94037F5BA1B4E9ED', 5, NULL, NULL, 2, NULL, 0, 'Bulgur salad will keep covered in fridge for up to 5 days.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2881CFB1881627479ADCFEA2D99A920F', X'EF2EE02245841343BB3382FBCB984BCF', X'EF2EE02245841343BB3382FBCB984BCF', 0, NULL, NULL, 2, NULL, 0, 'Add heirloom tomatoes, watermelon, red onion, cucumber, and dill to medium sized salad bowl.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F56AF8F02793C44280B721B848FED724', X'EF2EE02245841343BB3382FBCB984BCF', X'EF2EE02245841343BB3382FBCB984BCF', 1, NULL, NULL, 2, NULL, 0, 'Cut lemon in half and squeeze juice from one or both lemon halves over salad.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45F2B538D2ACFF4CB59171A581EEEE6B', X'EF2EE02245841343BB3382FBCB984BCF', X'EF2EE02245841343BB3382FBCB984BCF', 2, NULL, NULL, 2, NULL, 0, 'Serve salad with salt and fresh cracked pepper to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE44136707C3CC49A9396016865B58DD', X'8113E2AAC1A7DA47BF18BF06EA0DAE7C', X'8113E2AAC1A7DA47BF18BF06EA0DAE7C', 0, NULL, NULL, 2, NULL, 0, 'Cook pasta as directed on packaging. Drain well and add olive oil, lime juice, cilantro, broth, and vinegar while still warm. Mix well and let pasta cool.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED4585239B6B15448C2431D9A7247F64', X'8113E2AAC1A7DA47BF18BF06EA0DAE7C', X'8113E2AAC1A7DA47BF18BF06EA0DAE7C', 1, NULL, NULL, 2, NULL, 0, 'Once pasta has cooled mix in diced onions, corn, tomatoes and cucumber.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF498D105F7DF84A9D8595522595CD2A', X'8113E2AAC1A7DA47BF18BF06EA0DAE7C', X'8113E2AAC1A7DA47BF18BF06EA0DAE7C', 2, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to taste and garnish with a few fresh cilantro leaves.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84E7F60E734CCA4DA77580AE60633A22', X'3449B4724E92F147BDB4A9C9C9533A1D', X'3449B4724E92F147BDB4A9C9C9533A1D', 0, NULL, NULL, 2, NULL, 0, 'Crush garlic clove and place in a small bowl.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'242E6D4A0328234AB41BD645D4111CA5', X'3449B4724E92F147BDB4A9C9C9533A1D', X'3449B4724E92F147BDB4A9C9C9533A1D', 1, NULL, NULL, 2, NULL, 0, 'Add olive oil, paprika, marjoram, cumin, coriander, cayenne pepper and lemon juice to bowl and whisk together. Set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E372DE8A5720449B7E2ABBF3E360CDC', X'3449B4724E92F147BDB4A9C9C9533A1D', X'3449B4724E92F147BDB4A9C9C9533A1D', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl add chickpeas, bell peppers, scallions. Pour sauce made in previous step over chickpeas. Mix well.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAF02A54ACAD3C45ACDC90BE73FAFCA4', X'3449B4724E92F147BDB4A9C9C9533A1D', X'3449B4724E92F147BDB4A9C9C9533A1D', 3, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42E3C9CF7EB0194A9A87751A3EDFF38E', X'3449B4724E92F147BDB4A9C9C9533A1D', X'3449B4724E92F147BDB4A9C9C9533A1D', 4, NULL, NULL, 2, NULL, 0, 'Garnish with fresh parsley leaves and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04D6A95859AFCF4A9F9CB5BB21ED60BB', X'277077C4164BE747A9B308BF5DF02DFA', X'277077C4164BE747A9B308BF5DF02DFA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 390F (200C) and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D980448005D7643BC271B67E0AF95B2', X'277077C4164BE747A9B308BF5DF02DFA', X'277077C4164BE747A9B308BF5DF02DFA', 1, NULL, NULL, 2, NULL, 0, 'Toss sweet potato chunks with 1 tablespoon of olive oil and optional spices. Spread on lined baking sheet and bake in oven for 25-35 minutes until roasted.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5093E171538814CBAA7EBC8B25CD86F', X'277077C4164BE747A9B308BF5DF02DFA', X'277077C4164BE747A9B308BF5DF02DFA', 2, NULL, NULL, 2, NULL, 0, 'Remove sweet potatoes from oven and cool at room temperature.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4DE6A20688C2148ACF0FB9966339580', X'277077C4164BE747A9B308BF5DF02DFA', X'277077C4164BE747A9B308BF5DF02DFA', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare dressing by mixing shallots, scallions, chives, vinegar, olive oil, and maple syrup.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88673227E920024FA5C0208469473A9D', X'277077C4164BE747A9B308BF5DF02DFA', X'277077C4164BE747A9B308BF5DF02DFA', 4, NULL, NULL, 2, NULL, 0, 'Toss sweet potatoes with dressing, garnish with chives and serve. I suggest refrigerating salad for an hour before serving to allow for all the flavor to seep into the potatoes. Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74E04FB3814F43438E93AADC609B0225', X'EF3B0E211DEDAE46AB6C79359AC1326E', X'EF3B0E211DEDAE46AB6C79359AC1326E', 0, NULL, NULL, 2, NULL, 0, 'Rinse millet under running water. Add millet with 2 cups cold water to a mediums sized stockpot. Bring to a boil over high heat and continue to boil for 5 minutes. Remove from heat and cover stockpot and let steam for 10 minutes. After 10 minutes add millet to a large mixing bowl and fluff with a fork.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3306360CF0429E4792DD25115F587471', X'EF3B0E211DEDAE46AB6C79359AC1326E', X'EF3B0E211DEDAE46AB6C79359AC1326E', 1, NULL, NULL, 2, NULL, 0, 'Allow millet to cool for 5 and then add sambal oelek, olive oil, tomato paste and lemon juice. Mix well with a fork or gently with a spoon until all of the millet is covered in sauce.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EEEBBE2C4C5B748B0DC2629EEF84701', X'EF3B0E211DEDAE46AB6C79359AC1326E', X'EF3B0E211DEDAE46AB6C79359AC1326E', 2, NULL, NULL, 2, NULL, 0, 'Add scallions, bell pepper and parsley to millet and mix again.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'186B6EF9D0DEBA43A36178FE63EF9E5B', X'EF3B0E211DEDAE46AB6C79359AC1326E', X'EF3B0E211DEDAE46AB6C79359AC1326E', 3, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper if needed and garnish with a few parsley leaves.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'364C5C2791582F4E9393438CD9913DB6', X'EF3B0E211DEDAE46AB6C79359AC1326E', X'EF3B0E211DEDAE46AB6C79359AC1326E', 4, NULL, NULL, 2, NULL, 0, 'Allow to cool completely and serve.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89B8A8A80204AC41A9877F3D715BFE9F', X'6C911D2E39124945BED18900429179B5', X'6C911D2E39124945BED18900429179B5', 0, NULL, NULL, 2, NULL, 0, 'Rinse Quinoa to remove any bitterness.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09F2AFB31CBCED419A65625B88E3C956', X'6C911D2E39124945BED18900429179B5', X'6C911D2E39124945BED18900429179B5', 1, NULL, NULL, 2, NULL, 0, 'Place uncooked Quinoa in 2 1/2 times the amount of boiling water (2 1/2 cups) and simmer for 20 minutes. Remove from heat. Cover and let rest for 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98609DD5F194A840B420428BEF41E277', X'6C911D2E39124945BED18900429179B5', X'6C911D2E39124945BED18900429179B5', 2, NULL, NULL, 2, NULL, 0, 'Fluff Quinoa with fork. Add vinegar and olive oil. Let cool for 15 minutes.');
