INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'923085008C034C4185A1392D811D68A9', X'C98AB603CAF9FF449C945968C503A6C9', X'C98AB603CAF9FF449C945968C503A6C9', 0, NULL, NULL, 2, NULL, 0, 'Wash mint leaves and add to food processor along with all other ingredients listed for salad dressing. Blend until smooth.');
