INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'864103F6F0E79C41918BB104BB775F12', X'C98AB603CAF9FF449C945968C503A6C9', X'C98AB603CAF9FF449C945968C503A6C9', 6, NULL, NULL, 2, NULL, 0, 'Drizzle Salad with Tahini Mint Dressing and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83FDB9CFD1B9CB4BABFC5FD6751317F7', X'8DA96DA45F92AB45A62093D20689010C', X'8DA96DA45F92AB45A62093D20689010C', 0, NULL, NULL, 2, NULL, 0, 'Cook pasta according to instructions on packaging. Drain pasta and add olive oil. Allow pasta to cool for 15 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2F75FD180CDAC4986AFD881C66152EC', X'8DA96DA45F92AB45A62093D20689010C', X'8DA96DA45F92AB45A62093D20689010C', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, dice bell peppers and onion, halve tomatoes, and chop herbs.');
