INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'452682CB09759A43874F46B582851D23', X'8DA96DA45F92AB45A62093D20689010C', X'8DA96DA45F92AB45A62093D20689010C', 3, NULL, NULL, 2, NULL, 0, 'Serve and Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19FADF80A08B614EA4B57370B8BC45EB', X'E163AC24AE056146AC0A91E4B211A662', X'E163AC24AE056146AC0A91E4B211A662', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F (190C) and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C4A1920266FA8438CAE16DF2C23B178', X'E163AC24AE056146AC0A91E4B211A662', X'E163AC24AE056146AC0A91E4B211A662', 1, NULL, NULL, 2, NULL, 0, 'Coat diced sweet potatoes with olive oil, salt and paprika. Roast in oven for 20 - 25 minutes, turning after 15 minutes. Remove from oven once edges have browned.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5573744E7111B4428241EE515424E4F1', X'E163AC24AE056146AC0A91E4B211A662', X'E163AC24AE056146AC0A91E4B211A662', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, rinse quinoa and cook as per instructions on packaging or add to a saucepan with 1 3/4 cup boiling water. Simmer for 20 minutes then remove from heat and add juice of half a lemon. Cover saucepan and allow to rest for 5 minutes. Fluff with a fork and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21A1BEA5F5248D40A0C3C54FC1F2AD13', X'E163AC24AE056146AC0A91E4B211A662', X'E163AC24AE056146AC0A91E4B211A662', 3, NULL, NULL, 2, NULL, 0, 'Cut cauliflower into florets and steam for 3-5 minutes. Alternatively blanch for 3 minutes in boiling water. Remove from water and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21F8F032DDED7A4193457211FCE00C4F', X'E163AC24AE056146AC0A91E4B211A662', X'E163AC24AE056146AC0A91E4B211A662', 4, NULL, NULL, 2, NULL, 0, 'Heat a small fry pan over medium heat. Add pine nuts (don''t use any oil!) toast for 45 seconds, toss and toast for an additional 30 seconds. Repeat this until they start turning brown.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0197F2514C093645BF40F3662FB68ADC', X'E163AC24AE056146AC0A91E4B211A662', X'E163AC24AE056146AC0A91E4B211A662', 5, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine sweet potatoes, quinoa, cauliflower, quartered tomatoes, chopped parsley (leave a few leaves for garnish), and pine nuts.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9376A64C0CB42C4DB4D1D3813163793E', X'E163AC24AE056146AC0A91E4B211A662', X'E163AC24AE056146AC0A91E4B211A662', 6, NULL, NULL, 2, NULL, 0, 'Finish off with remaining lemon juice and add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B9217658DC3AC4CAF3E1B9EDCB2CB59', X'A7D2E18FF95FB348B45B9D231EA91584', X'A7D2E18FF95FB348B45B9D231EA91584', 0, NULL, NULL, 2, NULL, 0, 'Wash and dry the basil leaves. Put aside 6 leaves for garnish. Place the remaining basil leaves together with the garlic, cashews, salt, pepper, and 3 tablespoons olive oil in a food processor and blend.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00A27C433B07B946A4235E236DDB065B', X'A7D2E18FF95FB348B45B9D231EA91584', X'A7D2E18FF95FB348B45B9D231EA91584', 1, NULL, NULL, 2, NULL, 0, 'Cut out tofu block using a circle cookie cutter or a knife. Slice tofu into 1/4 inch slices.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92385F9FF692444B985257F3D6E69105', X'A7D2E18FF95FB348B45B9D231EA91584', X'A7D2E18FF95FB348B45B9D231EA91584', 2, NULL, NULL, 2, NULL, 0, 'Marinate tofu slices in 1 tablespoon olive oil and a pinch of salt and pepper for about 3 - 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19561C6C03C6714580466CFA2947CF99', X'A7D2E18FF95FB348B45B9D231EA91584', X'A7D2E18FF95FB348B45B9D231EA91584', 3, NULL, NULL, 2, NULL, 0, 'Wash and cut tomatoes into 1/4 inch slices.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4134F025FABE7B43A2681321FB28400F', X'A7D2E18FF95FB348B45B9D231EA91584', X'A7D2E18FF95FB348B45B9D231EA91584', 4, NULL, NULL, 2, NULL, 0, 'Alternate placing tomato and tofu slices on a large plate. Add a large teaspoon of basil pesto on each tofu slice.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F480AA8025BE489BBEBEEF8C99C118', X'A7D2E18FF95FB348B45B9D231EA91584', X'A7D2E18FF95FB348B45B9D231EA91584', 5, NULL, NULL, 2, NULL, 0, 'Garnish with basil leaves and drizzle with balsamic reduction sauce.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DA881E4FCA0C04E861FD03B887ADD82', X'639759B6B85C8148A9FA05DD6B51C1C6', X'639759B6B85C8148A9FA05DD6B51C1C6', 0, NULL, NULL, 2, NULL, 0, 'Rinse lentils under cold running water to remove any debris. Add lentils and 6 cups of water (no salt added) to a large stock pot and bring to a boil. Once boiling, reduce heat and simmer for 10 minutes. Drain lentils, run under cold water to cool, and drain again.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D9E42CBF8612441880994C892F6D19B', X'639759B6B85C8148A9FA05DD6B51C1C6', X'639759B6B85C8148A9FA05DD6B51C1C6', 1, NULL, NULL, 2, NULL, 0, 'Add lentils to a large serving bowl and stir in olive oil, lemon juice, and cumin.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'731FF689C5236349B3BCE9281A87AF41', X'639759B6B85C8148A9FA05DD6B51C1C6', X'639759B6B85C8148A9FA05DD6B51C1C6', 2, NULL, NULL, 2, NULL, 0, 'Dice bell peppers and scallions, quarter tomatoes and chop parsley. Add to lentils and add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'468FC1A3A96CB34B9ED581F2E72DD04F', X'639759B6B85C8148A9FA05DD6B51C1C6', X'639759B6B85C8148A9FA05DD6B51C1C6', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B22D0D8674B2A40AA92CA4439ACD7BF', X'D3D52B4A7BC4924B866B54C729576304', X'D3D52B4A7BC4924B866B54C729576304', 0, NULL, NULL, 2, NULL, 0, 'Wash cherries and blueberries. Cut cherries in half and remove pits then add to a medium sized bowl with the blueberries.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DA97083984A9F49BAA36A1237351913', X'D3D52B4A7BC4924B866B54C729576304', X'D3D52B4A7BC4924B866B54C729576304', 1, NULL, NULL, 2, NULL, 0, 'Cut off ends of Kiwi and peel off skin using a peeler. Cut kiwi in half length-wise then cut into slices.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDB6EAF2541842439C1D98E2826A9097', X'D3D52B4A7BC4924B866B54C729576304', X'D3D52B4A7BC4924B866B54C729576304', 2, NULL, NULL, 2, NULL, 0, 'Mix Kiwi together with cherries and blueberries in a bowl. Sprinkle with chopped pistachios.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD428FE7D9D55C468BD3620E14A11783', X'D3D52B4A7BC4924B866B54C729576304', X'D3D52B4A7BC4924B866B54C729576304', 3, NULL, NULL, 2, NULL, 0, 'Eat fresh and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DC16DD96C8F084395E19627F2B13367', X'56B51FEFBA9B0844B1BADA709591F522', X'56B51FEFBA9B0844B1BADA709591F522', 0, NULL, NULL, 2, NULL, 0, 'Cook rice and lentils according to instructions on packaging.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AAE4D4CE1429E44B31F2BF0EE0DF94E', X'56B51FEFBA9B0844B1BADA709591F522', X'56B51FEFBA9B0844B1BADA709591F522', 1, NULL, NULL, 2, NULL, 0, 'Place spices and herbs into a small bowl and whisk together with olive oil and lemon juice. Pour over cooked and drained lentils and rice.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B850FF1E39875C49AE82A7831E6CB847', X'56B51FEFBA9B0844B1BADA709591F522', X'56B51FEFBA9B0844B1BADA709591F522', 2, NULL, NULL, 2, NULL, 0, 'Add chopped tomatoes and scallions and mix all ingredients together.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AB566942766E14DA9B031E4312A2D44', X'56B51FEFBA9B0844B1BADA709591F522', X'56B51FEFBA9B0844B1BADA709591F522', 3, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6B48794D917664289C7147A4B4451BC', X'0C3E85EA74F36048BC130960056A69EB', X'0C3E85EA74F36048BC130960056A69EB', 0, NULL, NULL, 2, NULL, 0, 'Soak one rich paper (or 2 on top of each other for easier rolling) with lukewarm water in a shallow dish or plate. Soak for roughly 30 seconds until rice paper becomes slimy. Carefully place on a clean plate or cutting board.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B1F78883EA0384DA3600BCB21D34890', X'0C3E85EA74F36048BC130960056A69EB', X'0C3E85EA74F36048BC130960056A69EB', 1, NULL, NULL, 2, NULL, 0, 'Add roughly 2 heaping tablespoons of red lentils, 5 carrot slices, 5 bell pepper slices, and a small handful of lettuce to the bottom edge of the rice paper circle. Gently but tightly roll up the spring rolls up the way you would a burrito. (Check step by step rolling directions above.) Repeat steps until all ingredients or rice papers are used up.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41203064C3939944937712A4B687DD5F', X'0C3E85EA74F36048BC130960056A69EB', X'0C3E85EA74F36048BC130960056A69EB', 2, NULL, NULL, 2, NULL, 0, 'Serve immediately with your favorite dipping sauces.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'493B327372B35B4C970092C658136BF1', X'FFBEC749DC39A04294AF9EB617814FE4', X'FFBEC749DC39A04294AF9EB617814FE4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F and line a cookie sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD2B6C5C932F0E4782BF96579D9CCD59', X'FFBEC749DC39A04294AF9EB617814FE4', X'FFBEC749DC39A04294AF9EB617814FE4', 1, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a food processor and pulse until nuts are slightly ground. If too much for food processor, process ingredients in batches. and mix together in a bowl.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58B9AEECDC5F4C48902FD01D476354FA', X'FFBEC749DC39A04294AF9EB617814FE4', X'FFBEC749DC39A04294AF9EB617814FE4', 2, NULL, NULL, 2, NULL, 0, 'Scoop tablespoon sized balls of the spinach mixture out of the food processor or bowl and place on cookie sheet.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'639649F5BA505548A6C3C52EC64BF3C1', X'FFBEC749DC39A04294AF9EB617814FE4', X'FFBEC749DC39A04294AF9EB617814FE4', 3, NULL, NULL, 2, NULL, 0, 'Bake in oven for 15-20 minutes until bottoms of the spinach balls have browned.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F28ED021F6BF974795DF8C927FA7D2BF', X'FFBEC749DC39A04294AF9EB617814FE4', X'FFBEC749DC39A04294AF9EB617814FE4', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool for a minute on a cookie rack. Serve warm.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F68B59C17BD8364BA8DEB36CC6CD781E', X'0A85F2C765C29246875CCE16D2B2E2CD', X'0A85F2C765C29246875CCE16D2B2E2CD', 0, NULL, NULL, 2, NULL, 0, 'Clean mushrooms and remove stems.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77D0C114D2EDBF4C90F832806CC68B73', X'0A85F2C765C29246875CCE16D2B2E2CD', X'0A85F2C765C29246875CCE16D2B2E2CD', 1, NULL, NULL, 2, NULL, 0, 'Hollow out mushrooms carefully with a small spoon, and dice what has been hollowed out. (stems will not be used)');
