INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DA5BD74866FDF489DEDD10D4C4B3A3E', X'4040C49B25EAED49811E56DF29AA7A80', X'4040C49B25EAED49811E56DF29AA7A80', 5, NULL, NULL, 2, NULL, 0, 'They cook best without much oil.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F70C849D7BD8A4A8FBC141FB9320305', X'4040C49B25EAED49811E56DF29AA7A80', X'4040C49B25EAED49811E56DF29AA7A80', 6, NULL, NULL, 2, NULL, 0, 'Serve folded over, filled with strawberry or blueberry jam. A chocolate hazelnut spread is great, too.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C86F0DBE798A14098764A1B4CDA4D71', X'A931A8CA9112DB4DBF8A7C6A6A820CB1', X'A931A8CA9112DB4DBF8A7C6A6A820CB1', 0, NULL, NULL, 2, NULL, 0, 'Rinse quinoa with hot water to remove any dirt and bitter taste.');
