INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8F31B22C24DAD408A3127613EC39451', X'972C7620E3236A4EA298F620CB5A99AA', X'972C7620E3236A4EA298F620CB5A99AA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F (190C). Line cookie sheet with baking paper and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C935661825E6C44ABD7FAD1B3D55FDE', X'972C7620E3236A4EA298F620CB5A99AA', X'972C7620E3236A4EA298F620CB5A99AA', 1, NULL, NULL, 2, NULL, 0, 'Toast walnuts in a saucepan. Be careful not to burn them! Chop into large chunks , let cool, mix with chocolate and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C503CCAA02EFD499128768CEA0695F7', X'972C7620E3236A4EA298F620CB5A99AA', X'972C7620E3236A4EA298F620CB5A99AA', 2, NULL, NULL, 2, NULL, 0, 'Make vegan buttermilk by mixing soy milk with lemon juice. Let stand for 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D7D93B7322D8247B38981606BCEC2DE', X'972C7620E3236A4EA298F620CB5A99AA', X'972C7620E3236A4EA298F620CB5A99AA', 3, NULL, NULL, 2, NULL, 0, 'Mix together all dry ingredients in a large bowl. Add small chunks of margarine to bowl. Use your hands to rub butter into flour mixture, until crumbs form.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'512980342FAC544D867DF31B1A2E7750', X'972C7620E3236A4EA298F620CB5A99AA', X'972C7620E3236A4EA298F620CB5A99AA', 4, NULL, NULL, 2, NULL, 0, 'Mix `soy buttermilk`, banana, maple syrup and add to flour mixture. Combine with a wooden spoon. Don''t overdo it!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08714B9ECAF7054CBA0FCC8354A24C75', X'972C7620E3236A4EA298F620CB5A99AA', X'972C7620E3236A4EA298F620CB5A99AA', 5, NULL, NULL, 2, NULL, 0, 'Add chocolate-walnut mixture and place entire dough onto lightly floured counter top. Form a circle out of the dough about 8 Inches in diameter and slightly less than an inch thick. Cut the circle into 8 even slices (the way you would cut a pizza).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE134B475AAAC0479A51901FBE5B9846', X'972C7620E3236A4EA298F620CB5A99AA', X'972C7620E3236A4EA298F620CB5A99AA', 6, NULL, NULL, 2, NULL, 0, 'Bake for 16-18 minutes. Let cool for 10 minutes and enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADAC6753F9846D4BABED29906229137C', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F (190C). Line baking sheet with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAF1F1C2912061418EEA20889A67D821', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 1, NULL, NULL, 2, NULL, 0, 'Make vegan `buttermilk` by whisking any plant-based milk with apple cider vinegar. Let stand for 5 minutes.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'090321C120AAD24EB2D4949A63C9CC8C', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 2, NULL, NULL, 2, NULL, 0, 'Mix all dry ingredients in a large bowl. Add small chunks of margarine/vegan butter to the bowl. Use your hands to rub butter into flour mixture until crumbs form.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2D72D7A2541C7448B467F1C6FF36761', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 3, NULL, NULL, 2, NULL, 0, 'Mix the `vegan buttermilk`, pureed banana and maple syrup. Add it to the dry ingredients and combine with a wooden spoon. Don''t overdo it!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF558815C67B564F9A748818424521D4', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 4, NULL, NULL, 2, NULL, 0, 'Carefully mix in chocolate, almonds, and frozen raspberries. Again, be careful not to overmix the dough.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CB43D351D387B4DBE93B12E7772A4D5', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 5, NULL, NULL, 2, NULL, 0, 'The dough should be sticky, see notes if it is too wet. Place entire dough onto a floured counter top. Form a circle out of the dough about 10 Inches in diameter.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'036BE1F9A3FD784C904C65B6E49E3CDE', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 6, NULL, NULL, 2, NULL, 0, 'Cut the circle into and frozen even slices (the way you would cut a pizza). Place the scones 2 inches apart on the prepared baking sheet and dust the top of each scone with sugar (optional).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22D400E85C1976409C06E0641BDE9DD2', X'5E1EE90A911413429006AE509216FAA4', X'5E1EE90A911413429006AE509216FAA4', 7, NULL, NULL, 2, NULL, 0, 'Bake for 16-18 minutes. until a toothpick inserted in the middle of a scone comes out clean. Remove the scones from the oven and allow them to cool for 10 minutes on a cooling rack before eating. Enjoy!');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AA3A995703E9E49B4E9CC002D7A75F5', X'AAABEAE16B478E4497EFDD2C79152DD3', X'AAABEAE16B478E4497EFDD2C79152DD3', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F. Grease and flour a 12 cup muffin tray or line with muffin cups.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1B19EE3C2AF9244994E0E0C6878CCBA', X'AAABEAE16B478E4497EFDD2C79152DD3', X'AAABEAE16B478E4497EFDD2C79152DD3', 1, NULL, NULL, 2, NULL, 0, 'Blend together bananas, milk, and olive oil until smooth using a blender, food processor or an immersion blender.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F16CF9AE861B92488AABE0F6A5AE3362', X'AAABEAE16B478E4497EFDD2C79152DD3', X'AAABEAE16B478E4497EFDD2C79152DD3', 2, NULL, NULL, 2, NULL, 0, 'In a separate large bowl mix together flours, 1/3 cup sugar, baking powder, baking soda, salt, cinnamon, and nutmeg with a wooden spoon.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A24E3295A614B488441FBD2D59B9319', X'AAABEAE16B478E4497EFDD2C79152DD3', X'AAABEAE16B478E4497EFDD2C79152DD3', 3, NULL, NULL, 2, NULL, 0, 'Add the blended ingredients to the bowl and mix again mix with a wooden spoon.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3425BDC0328F0C4289C0F5BFB7B71DE3', X'AAABEAE16B478E4497EFDD2C79152DD3', X'AAABEAE16B478E4497EFDD2C79152DD3', 4, NULL, NULL, 2, NULL, 0, 'Stir in diced apple and finely chopped walnuts. Evenly distribute muffin dough into muffin cups.');
