INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CDF4678D14A1243B387064AB9C7FCDF', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', 1, NULL, NULL, 2, NULL, 0, 'Prepare crust by pulsing dates and almonds in a food processor until a sticky mass forms. Press this into the base of the springform.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1298311AFD5D3647A0813FCF67ABBDB5', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', 2, NULL, NULL, 2, NULL, 0, 'Clean out food processor and add coconut cream and frozen strawberries. Pulse until smooth. Add up to 1(3 of a cup coconut water if food processor is having difficulties. Taste filling and add a sweetener of choice (I prefer maple syrup) if it is not sweet enough to your liking. This all depends on the sweetness of the strawberries to begin with. Spread the strawberry ice cream filling over the crust in the spring form, and place prepared parchment paper circle on the top. Place in freezer for 2-3 hours.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B05AD412E62184F890505F3D38F9B97', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', 3, NULL, NULL, 2, NULL, 0, 'Once the strawberry ice is frozen remove the parchment paper and prepare the strawberry topping by pulsing 1 date with 6 strawberries in a food processor. If the strawberries are very small add 2 more. Spread strawberry-date topping over the ice cream layer.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'324DF25B6967FE4AB6ADDD3495649EEE', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', 4, NULL, NULL, 2, NULL, 0, 'Garnish with 12 small strawberries or more if desired.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AFDBA215C4A2146B0CC60C8B753139C', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', X'62BF0F1DF6EDDD43B2A5D6A63CC5552B', 5, NULL, NULL, 2, NULL, 0, 'Serve cold and slightly thawed. Cake will keep in freezer for up to two weeks.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1564E88DA11813458FB25C45D88BD1B5', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F (180C). Grease and flour a springform or line with parchment paper (See notes).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50810A855768614A951AC112596BC00C', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', 1, NULL, NULL, 2, NULL, 0, 'Stir together dry ingredients in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6C32CDE9D4CD3419A08D2B3BD454E43', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', 2, NULL, NULL, 2, NULL, 0, 'Stir in apple cider vinegar, milk, and water. Slowly add olive oil until just mixed.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A6CA5513321604F9D2B5982AA878EE2', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', 3, NULL, NULL, 2, NULL, 0, 'Pour cake batter into prepared springform and bake in preheated oven for 30 minutes. Cake is finished when the cake bounces back after being lightly pressed on or when a toothpick inserted into the center comes out clean.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'052303294456BD48BA0C8C45B5203F85', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', 4, NULL, NULL, 2, NULL, 0, 'Let cake cool before removing it from the springform.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0F5A150584A2C46B1FC5B1484E69B77', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', 5, NULL, NULL, 2, NULL, 0, 'Melt chocolate chips and drizzle over chocolate olive oil cake.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA71608CC136B5489CCDE8A4178386FA', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', X'9759ADDA21DE4D4EBC6CA159EB9AC6F0', 6, NULL, NULL, 2, NULL, 0, 'Serve with whipped coconut cream.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'360326C608965A4A8D761C76F8FC79EC', X'C5F7EB8446B906408B9E6109F198A106', X'C5F7EB8446B906408B9E6109F198A106', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 355F (180C). Grease and flour a 10 inch (26cm) loaf pan.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E243EEDD4177914ABEE3AFC76311D852', X'C5F7EB8446B906408B9E6109F198A106', X'C5F7EB8446B906408B9E6109F198A106', 1, NULL, NULL, 2, NULL, 0, 'Using a wooden spool, mix together flours, sugar, and baking powder in a medium sized mixing bowl.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC5A7C3F2F34364893C58BA030299AEE', X'C5F7EB8446B906408B9E6109F198A106', X'C5F7EB8446B906408B9E6109F198A106', 2, NULL, NULL, 2, NULL, 0, 'Add melted butter or coconut oil, milk, and lemon juice to flour mixture and stir together.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A615953A0053E24F9FF310C0827BA8A7', X'C5F7EB8446B906408B9E6109F198A106', X'C5F7EB8446B906408B9E6109F198A106', 3, NULL, NULL, 2, NULL, 0, 'Pour into prepared loaf pan and bake on lower rack for 50 to 60 minutes or until skewer comes out clean.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4C13DB2FEE0484BA7E6EDA9C1326512', X'C5F7EB8446B906408B9E6109F198A106', X'C5F7EB8446B906408B9E6109F198A106', 4, NULL, NULL, 2, NULL, 0, 'Remove pan from oven and allow to cool for 10 minutes before removing cake from pan. Allow cake to fully cool on a cooling rack.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9E853A4D789BC4FB82CCC7A7D28A014', X'46D678397695C54581EF2F83C186496D', X'46D678397695C54581EF2F83C186496D', 0, NULL, NULL, 2, NULL, 0, 'Cream together coconut oil, lemon juice, and lemon zest.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A958C1C69B03D54DAAF3EE454F1F1DC7', X'46D678397695C54581EF2F83C186496D', X'46D678397695C54581EF2F83C186496D', 1, NULL, NULL, 2, NULL, 0, 'Add salt and 1/2 cup powdered sugar and beat together on medium speed, slowly adding more and more powdered sugar until desired consistency is achieved.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BC8A68B1AE70C4190E2F4C82E85F05F', X'46D678397695C54581EF2F83C186496D', X'46D678397695C54581EF2F83C186496D', 2, NULL, NULL, 2, NULL, 0, 'Spread lemon frosting over cooled lemon loaf.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02230DCDD95C3B41A0D30D2F5E02785D', X'46D678397695C54581EF2F83C186496D', X'46D678397695C54581EF2F83C186496D', 3, NULL, NULL, 2, NULL, 0, 'Keep lemon loaf in fridge and eat within 5 days.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CCD2BA6A40B8441AB3A776A84D729E2', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 0, NULL, NULL, 2, NULL, 0, 'Line a 7 inch springform with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92FB94E3F20548479177181750BBBA33', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 1, NULL, NULL, 2, NULL, 0, 'Prepare crust by pulsing dates, walnuts, and salt in a food processor until a sticky mass forms. Press the walnut-date mixture into the bottom of your springform .');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F931B1F4CB09094BB3B2549AABA850A6', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 2, NULL, NULL, 2, NULL, 0, 'Make the caramel cheesecake filling by pulsing coconut cream, cashews, and 1/4 cup caramel dip in a food processor. Process until smooth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4A4BB97DB6E39478E05195B914D99F2', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 3, NULL, NULL, 2, NULL, 0, 'Spread filling evenly into springform. Place in the freezer for about 15-30 minutes until the top hardens a bit.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0647814D6D170A43B7113A139AFDBE14', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 4, NULL, NULL, 2, NULL, 0, 'Spread remaining 1/2 cup caramel dip over filling.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E3CF2131083D644998CFBFF08B98BF3', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 5, NULL, NULL, 2, NULL, 0, 'Garnish with chocolate chips (optional).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56F55BAE44DF244BBF683822AD8FBB90', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 6, NULL, NULL, 2, NULL, 0, 'Place caramel cheesecake in refrigerator for at least 4 hours until it sets.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD27CFD98B843943B14300204F7ED94F', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 7, NULL, NULL, 2, NULL, 0, 'Carefully remove cheesecake from springform and serve.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E29348214C9B9349BAD6C9866639B745', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', X'A8E874D58B1DC7438D5B7AE5B6D7C3F9', 8, NULL, NULL, 2, NULL, 0, 'Keep in fridge for up to 5 days.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'803477BAFFA59444BF06455701AA96A0', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 0, NULL, NULL, 2, NULL, 0, 'Line a 7 inch springform with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'014101BE1E68864B92AC9F610BFCCEE4', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 1, NULL, NULL, 2, NULL, 0, 'Place Almonds, 5 dates and a pinch of salt in food processor and pulse until a sticky mass forms.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'064ED68F62D8AC4C90F9B8593D68813E', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 2, NULL, NULL, 2, NULL, 0, 'Press almond crust into bottom of lined springform and place in freezer while you prepare the filling.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C54CEE75BF2F0B47BAE4D6DEBEAC7090', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 3, NULL, NULL, 2, NULL, 0, 'Clean out food processor and place coconut cream, cashews, kiwi, lime juice, and powdered sugar in it. Pulse and process until mixture is smooth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7ED12B91CC582547B19663AC57CA17D4', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 4, NULL, NULL, 2, NULL, 0, 'Remove springform from freezer and pour kiwi cheesecake filling into the springform. Place springform in freezer again until filling in slightly firm (about 10 minutes).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F38E9F2D3D20AB44B9AB4E4596D6D6FA', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 5, NULL, NULL, 2, NULL, 0, 'Clean out food processor again and place mango and 2 dates in it. Pulse again until smooth. Carefully spread mango topping over filling in the springform.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FDEA3654164E440A8A811B511C80C66', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 6, NULL, NULL, 2, NULL, 0, 'Place in refrigerator for at least 4 hours or overnight until filling is firm.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'406C82AAED65DB4B8F8EEE943818158A', X'1E6167BB0C26B447825E794F465BD018', X'1E6167BB0C26B447825E794F465BD018', 7, NULL, NULL, 2, NULL, 0, 'When cheesecake is firm and ready to serve garnish with fresh kiwi slices and mint leaves. (optional)');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C2853C4323F4C4C8347BEB872A0FD5F', X'4EB9F6819A09A24397EECB977564E0E6', X'4EB9F6819A09A24397EECB977564E0E6', 0, NULL, NULL, 2, NULL, 0, 'Line 2 8oz ramekins with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3FA01C1423291439B9C3D82F5CC0498', X'4EB9F6819A09A24397EECB977564E0E6', X'4EB9F6819A09A24397EECB977564E0E6', 1, NULL, NULL, 2, NULL, 0, 'Place hazelnuts, dates and pinch of salt in food processor and pulse until a sticky mass forms.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45D04EA9EFEF3F4CB89019115F57E410', X'4EB9F6819A09A24397EECB977564E0E6', X'4EB9F6819A09A24397EECB977564E0E6', 2, NULL, NULL, 2, NULL, 0, 'Press half of hazelnut crust into bottom of each ramekin and up the sides using fingers.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53AF1798B498D34B86B83960D2CD0C7B', X'4EB9F6819A09A24397EECB977564E0E6', X'4EB9F6819A09A24397EECB977564E0E6', 3, NULL, NULL, 2, NULL, 0, 'Clean out food processor and add Raspberries and powdered sugar to it. Pulse again until smooth (raspberry seeds will remain whole and this is OK). Pour Raspberry glaze into a small bowl.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7533A6A10625444DB339FF67F5B71AF0', X'4EB9F6819A09A24397EECB977564E0E6', X'4EB9F6819A09A24397EECB977564E0E6', 4, NULL, NULL, 2, NULL, 0, 'Clean out food processor again and place cashews, maple syrup, water, dates, coconut oil, and cocoa powder in it. Pulse and process until mixture is smooth.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EB7CC3D1B4025498C8A2C22D9F296BB', X'4EB9F6819A09A24397EECB977564E0E6', X'4EB9F6819A09A24397EECB977564E0E6', 5, NULL, NULL, 2, NULL, 0, 'Drop 1 tablespoon raspberry glaze into each ramekin. Top with half of the chocolate filling and push it out to the sides. Top with another tablespoon of raspberry glaze.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13DF91F47A22B8498F3F86AE719CF491', X'4EB9F6819A09A24397EECB977564E0E6', X'4EB9F6819A09A24397EECB977564E0E6', 6, NULL, NULL, 2, NULL, 0, 'Place in refrigerator for at least 4 hours or overnight until filling is firm.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F68423256B51499CFCC1919465F110', X'31AF151B845DBD44BD95628A4507BF8C', X'31AF151B845DBD44BD95628A4507BF8C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F (190C). Grease and flour 4 ramekins.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0151A5B8D210C649A19EA0288ED5BB22', X'31AF151B845DBD44BD95628A4507BF8C', X'31AF151B845DBD44BD95628A4507BF8C', 1, NULL, NULL, 2, NULL, 0, 'Over medium-low heat, while constantly stirring, melt chocolate, butter, and sugar. Be careful not to burn the chocolate.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABC52798942D7A4F9588F60BFBABD5C5', X'31AF151B845DBD44BD95628A4507BF8C', X'31AF151B845DBD44BD95628A4507BF8C', 2, NULL, NULL, 2, NULL, 0, 'Allow melted mixture to cool slightly then add remaining ingredients and mix together with a wooden spoon or spatula.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B388FAB6497E16478E03287957D5BD63', X'31AF151B845DBD44BD95628A4507BF8C', X'31AF151B845DBD44BD95628A4507BF8C', 3, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of the chocolate batter to 4 ramekins (can be anywhere between 6 - 8 ounces in size)');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'055350716D55B6498087B89F6A4D6464', X'31AF151B845DBD44BD95628A4507BF8C', X'31AF151B845DBD44BD95628A4507BF8C', 4, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon Peanut Butter to each and top with remaining chocolate batter (about 1 tablespoon for each ramekin) on top. Spread dough using finger or spoon to outer edge.');
