INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E577CBA8AABB734BA3B7EABD315ECD62', X'31AF151B845DBD44BD95628A4507BF8C', X'31AF151B845DBD44BD95628A4507BF8C', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven, allow to cool and enjoy!');
