INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7535EC22C26EC34D820AB0B63C45D447', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', 4, NULL, NULL, 2, NULL, 0, 'Remove springform from freezer and pour lemon cheesecake filling into the springform. Place springform in freezer again until filling in slightly firm (about 10 minutes).');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95ECCF3A839BD54BAC5A6F744D11CA9C', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', 5, NULL, NULL, 2, NULL, 0, 'Clean out food processor again and place blueberries and 2 dates in it. Pulse again until smooth (blueberry skin will still be present, this is OK). Carefully spread blueberry topping over filling in the springform.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23F318D23C0CF64F8FEC297673067564', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', 6, NULL, NULL, 2, NULL, 0, 'Place in refrigerator for at least 4 hours or overnight until filling is firm.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C08C3E22F4ED874A81DAE5AA0B568C00', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', X'EC410E1E5A26CA498E1C7F4BDCE7C0A4', 7, NULL, NULL, 2, NULL, 0, 'When cheesecake is firm and ready to serve garnish with fresh blueberries and lime zest.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F4FDFB1F60C984994C123BC896794D8', X'990FE76D6A1861468C2A1C4280868B28', X'990FE76D6A1861468C2A1C4280868B28', 0, NULL, NULL, 2, NULL, 0, 'Line a 7 inch springform with parchment paper.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC7D28A7BBD5954B84EB497A459F05FF', X'990FE76D6A1861468C2A1C4280868B28', X'990FE76D6A1861468C2A1C4280868B28', 1, NULL, NULL, 2, NULL, 0, 'Prepare crust by pulsing peanuts and coconut oil in food processor. Pulse until crumbly. Press peanut crust firmly into bottom of springform.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'301E9FFDBE38314DABDCC58ADCE1819C', X'990FE76D6A1861468C2A1C4280868B28', X'990FE76D6A1861468C2A1C4280868B28', 2, NULL, NULL, 2, NULL, 0, 'Rinse out food processor and pulse coconut cream, banana, and maple syrup together until creamy and smooth. Spread coconut-banana filling over peanut crust.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D94B2DFFF659249A5DA40A023F319A6', X'990FE76D6A1861468C2A1C4280868B28', X'990FE76D6A1861468C2A1C4280868B28', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle chocolate chips on top of filling.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'694A12A7BCEDE442BAD1A726D850CB82', X'990FE76D6A1861468C2A1C4280868B28', X'990FE76D6A1861468C2A1C4280868B28', 4, NULL, NULL, 2, NULL, 0, 'Place in freezer for at least 10 minutes until firm then remove from cheesecake from springform and serve.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F76C8048CA0B7643970B3F4D694B24FB', X'E18505BDB37A0344B8BCF77D09484F34', X'E18505BDB37A0344B8BCF77D09484F34', 0, NULL, NULL, 2, NULL, 0, 'Puree Banana and mix together with melted chocolate chips, vanilla extract, melted butter, and peppermint extract.');
INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'154EF93C83C3E6458CC6D321D57B3113', X'E18505BDB37A0344B8BCF77D09484F34', X'E18505BDB37A0344B8BCF77D09484F34', 1, NULL, NULL, 2, NULL, 0, 'Sift together flour, sugar, salt, and baking powder. Using a wooden spoon, stir together with chocolate banana mixture. Mix in remaining (not melted!) chocolate chips, wrap in plastic wrap and chill in fridge for at least an hour.');
