INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A3856C452A9A74F9F60696BFBA69A72', X'E18505BDB37A0344B8BCF77D09484F34', X'E18505BDB37A0344B8BCF77D09484F34', 5, NULL, NULL, 2, NULL, 0, 'Remove cookies from the oven and allow them to cool for 5 minutes on the cookie sheet before transferring them to a cooling rack.');
