INSERT INTO `VWR INTERNAL Veganfamilyrecipes howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD4AD38BB545914A84A968C081344705', X'A24697FF69CEBD42859B50A4A6F8F8BF', X'A24697FF69CEBD42859B50A4A6F8F8BF', 4, NULL, NULL, 2, NULL, 0, 'Add sugar mixture and chia eggs to flour. Carefully mix together with a wooden spoon and add chocolate chips.');
